INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55086,'Bunga melur dalam jambangan,','Di tepi kolam menjadi hiasan.','Kucipta pantun buat renungan,','Semarak Nilam empunya wawasan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55087,'Mekar sejambak bunga gubahan,','Jatuh sekuntum di atas peti,','Membaca itu suruhan Tuhan,','Al-alaq diturun menjadi bukti.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55088,'Cerana kesumba indah melati,','Cantik tersemat dambaan puteri,','Ilmu ditimba sepenuh hati,','Kekuatan umat minda bestari.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55089,'Harum sungguh bunga kemboja,','Bunga karangan cantik tersemat,','Janganlah membaca kulit sahaja,','Telitilah kandungan sehingga tamat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55090,'Mekar angsana pandang tak jemu,','Bunganya luruh ke dalam raga,','Buku laksana lautan ilmu,','Dasarnya penuh mutiara berharga.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55091,'Daun nipah kajangnya rapat,','Hidangan tetamu di Kuala Maran,','Buku menyimpan berjuta maklumat,','Fakta, ilmu, cereka hiburan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55092,'Anak muda pulang ke desa,','Ibu dan ayah lama menanti,','Warga membaca tak buang masa,','Khutub khanah teman sejati.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55093,'Pohon pedada di dalam taman,','Jadi idaman si anak rusa,','Marilah anakanda marilah teman,','Kita membaca sepanjang masa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55094,'Meniti senja biasan mentari,','Merkah setitis menjelang kabut,','Bacalah baca setiap hari,','Tak akan habis ilmu dituntut.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55095,'Cantik sungguh si bunga kejora,','Buat hiasan di hari raya,','Marilah sahut seruan negara,','Bangsa membaca bangsa berjaya.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55096,'Ikat jerami muat ke kandar,','Selepas makan mandi di telaga;','Ucap difahami wasangka terhindar,','Bahasa kebangsaan penyatu warga.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55097,'Batang halban dibawa ke huma,','Hendak dirikan sebuah taman;','Bahasa kebangsaan milik bersama,','Kita ungkapkan sepanjang zaman.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55098,'Terbang tempua ke semak berduri,','Singgah sekali di pohon rumbia;','Bahasa kita lambang jati diri,','Tidak rugi berbahasa Malaysia.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55099,'Berbanjar cemara di tanah rata,','Tempat teduhan sekumpulan murai;','Segar dan mesra bahasa kita,','Anugerah warisan usah terburai.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55100,'Riuh nelayan memunggah pelata,','Beli seraga dibawa pulang;','Bahasa kebangsaan jiwa kita,','Jati diri warga gampak cemerlang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55101,'Busut di hutan sarang kelekatu,','Angsana tumbang terkejut tempua;','Berbahasa kebangsaan saban waktu,','Makin berkembang ke serata benua.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55102,'Dari Benta ke Kota Gelanggi,','Singgah di Jengka membeli toman;','Bahasa kita martabatnya tinggi,','Lingua franca zaman-berzaman.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55103,'Sepohon celagi lanjut usia,','Tempat istirahat ayam jantan;','Tidak rugi berbahasa Malaysia,','Warga erat sejahtera watan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55104,'Tegak rumah kerana sendi,','Runtuh sendi rumah binasa;','Tegak bangsa kerana budi,','Runtuh budi hilanglah bangsa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55105,'Bunga melati bunga di darat,','Bunga seroja di tepi kali;','Hina besi kerana karat,','Hina manusia tidak berbudi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55106,'Limau manis dimakan manis,','Manis sekali rasa isinya;','Dilihat manis dipandang manis,','Manis sekali hati budinya.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55107,'Di sana padi di sini padi,','Baru bernama sawah dan bendang;','Di sana budi di sini budi,','Baru sempurna bernama orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55108,'Awal pertama orang berbangsa,','Kedua banyak ribu dan laksa;','Ketiga majlis bermanis muka,','Keempat banyak berbudi bahasa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55109,'Orang Jawa mencari benang,','Mencari benang di atas bukit;','Orang jauh jangan dikenang,','Lama-lama jadi penyakit.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55110,'Orang berkain corak berbelang,','Mengayuh rakit mencari duku,','Jangan bermain kekasih orang,','Nyawa tersangkut di hujung kuku.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55111,'Patah jarum dalam peti,','Buat menjahit kain bertimbun;','Nasihat sepatah kurang dimengerti,','Rupanya penawar beribu tahun.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55112,'Padi muda jangan dilurut,','Kalau dilurut patah batangnya;','Hati muda jangan diturut,','Kalau diturut susah datangnya.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55113,'Bawa mari ke kedai Cina,','Kerana itu buah dagangan;','Hidup kita biar sempurna,','Kerana dunia ini adalah tumpangan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55114,'Beras kisar mudik ke hulu,','Tanak pulut santan durian;','Tak ada orang menyesal dahulu,','Banyak orang menyesal kemudian.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55115,'Bukan tidak saya katakan,','Merbuk biasa makan di papan;','Bukan tidak saya katakan,','Buah mabuk jangan dimakan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55116,'Bukit Jertih berhutan buluh,','Tempat raja pergi memburu;','Minta selisih malaikat empat puluh,','Janganlah saya diberi malu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55117,'Orang hulu memalu nobat,','Nobat dipalu kayu berangan;','Fikir dahulu sebelum buat,','Kalau dibuat menyesal jangan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55118,'Angin teluk menyisir pantai,','Hanyut rumpai di bawah titi;','Biarlah buruk kain dipakai,','Asal pandai mengambil hati.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55119,'Buah pelaga makan dikikir,','Dibawa orang dari hulu;','Sebarang kerja hendak difikir,','Supaya jangan mendapat malu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55120,'Hendak belayar ke Teluk Betong,','Sambil mencuba labuhkan pukat;','Bulat air kerana pembetung,','Bulat manusia kerana muafakat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55121,'Pakai baju warna biru,','Pergi ke sekolah pukul satu;','Murid sentiasa hormatkan guru,','Kerana guru pembekal ilmu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55122,'Jangan pergi mandi di lombong,','Emak dan kakak sedang mencuci;','Jangan suka bercakap bohong,','Semua kawan akan membenci.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55123,'Jikalau tuan mengangkat peti,','Tolong masukkan segala barang;','Jikalau anak-anak bersatu hati.','Kerja yang susah menjadi senang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55124,'Asam kandis mari dihiris,','Manis sekali rasa isinya;','Dilihat manis dipandang manis,','Lebih manis hati budinya.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55125,'Selasih tumbuh di tepi telaga,','Selasih dimakan si anak kuda;','Kasih ibu membaa ke syurga,','Kasih saudara masa berada.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55126,'Masuk hutan pakai sepatu,','Takut kena gigitan pacat;','Kalau kita selalu bersatu,','Apa kerja mudah dibuat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55127,'Orang haji dari Jeddah','Buah kurma berlambak-lambak','Pekerjaan guru bukanlah mudah','Bagai kerja menolak ombak', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55128,'Pinang muda dibelah dua','Anak burung mati diranggah','Dari muda sampai ke tua','Ajaran baik jangan diubah', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55129,'Asal kapas menjadi benang','Dari benang dibuat kain','Barang yang lepas jangan dikenang','Sudah menjadi hak orang lain', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55130,'Kapal Anjiman disangka hantu','Nampak dari Kuala Acheh','Rosak iman kerana nafsu','Rosak hati kerana kasih', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55131,'Tingkap papan kayu bersegi','Sampan sakat di Pulau Angsa','Indah tampan kerana budi','Tinggi darjat kerana bahasa', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55132,'Bintang tujuh sinar berseri','Bulan purnama datang menerpa','Ajaran guru hendak ditaati','Mana yang dapat jangan dilupa', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55133,'Parang tajam tidak berhulu','Buat menetak si pokok Ru','Bila belajar tekun selalu','Jangan ingkar nasihat guru', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55134,'Hari malam gelap-gelita','Pasang lilin jalan ke taman','Sopan santun budaya kita','Jadi kebanggaan zaman berzaman', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55135,'Pergi berburu sampai ke sempadan','Dapat Kancil badan berjalur','Biar carik baju di badan','Asalkan hati bersih dan jujur', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55136,'Dalam semak ada duri','Ayam kuning buat sarang','Orang tamak selalu rugi','Macam anjing dengan bayang', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55137,'Baik-baik mengirai padi','Takut mercik ke muka orang','Biar pandai menjaga diri','Takut nanti diejek orang', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55138,'Ke hulu membuat pagar','Jangan terpotong batang durian','Cari guru tempat belajar','Supaya jangan sesal kemudian', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55139,'Orang Daik memacu kuda','Kuda dipacu deras sekali','Buat baik berpada-pada','Buat jahat jangan sekali', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55140,'Dayung perahu tuju haluan','Membawa rokok bersama rempah','Kalau ilmu tidak diamalkan','Ibarat pokok tidak berbuah', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55141,'Kalau kita menebang jati','Biar serpih tumbangnya jangan','Kalau kita mencari ganti','Biar lebih kurang jangan', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55142,'Adik ke kedai membeli halia','Emak memesan membeli laksa','Jadilah insan berhati mulia','Baik hati berbudi bahasa', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55143,'Pantai Mersing kuala Johor','Pantainya bersih sangat mashyur','Pohonkan doa kita bersyukur','Negara kita aman dan makmur', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55144,'Tuan Mahmud bermain tombak,','Dang Kasuma menangkap tupai;','Kalau takut dilambung ombak,','Jangan berumah di tepi pantai.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55145,'Sayang Musalmah memakai tudung,','Tudung dipakai sebelah kiri;','Apa dikenang kepada untung,','Untung tak untung diri sendiri.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55146,'Ambil bertih dari hulu,','Isi mari di dalam balang;','Bersihkan laman kita dahulu,','Baru bersihkan halaman orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55147,'Anak tiung anak ketitir,','Anak balau terlompat-lompat;','Barang dikendong habis tercicir,','Barang dikejar haram tak dapat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55148,'Selangor menjual : Dihujung selat bertiup bayu,','Teratai di kolam tampak serinya;','Dikandung adat budaya Melayu,','Dicemar susila mana manisnya?', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55149,'Melaka membeli : Sinis surya di persada meriah,','Tatkala camar memainkan rebab;','Manis budaya pada maruah,','Susila yang cemar dipulihkan adap.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55150,'Melaka menjual : Rejang menuding belukar nan kelam,','Pelepah bidara bergalang malap;','Bijak berunding luar dan dalam,','Indah bicara di mana silap?', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55151,'Selangor membeli : Buah bidara di dalam kaca,','Serai seikat di pangkal bertemu;','Salah bicara bukanlah punca,','Hanya mufakat belum ditemu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55152,'Anak katak terlompat-lompat,','Terlompat-lompat di tepi paya,','Janganlah kita suka mengumpat,','Kelak hilang semua pahala.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55153,'Enak rasanya ikan gelama,','Jika dimasak secukup rasa,','Solat itu tiang agama,','Perlu dijaga setiap masa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55154,'Lemak kubis santan kelapa,','Ikan haruan dimasak cuka,','Taatlah kepada ibu dan bapa,','Jangan menjadi anak derhaka.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55155,'Pohon sena tumbuh menjulang,','Pandan semerbak tepian mempelas,','Bila Ramadhan mulai menjelang,','Puasalah dengan hati yang ikhlas.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55156,'Pak tani membeli baja,','Baja ditabur di sawah padi,','Hidup ini sementara sahaja,','Akhirat di sana kekal abadi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55157,'Melati kuntum tumbuh melata,','Sayang merbah di pohon cemara;','Assalamualaikum mulanya kata,','Saya sembah pembuka bicara.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55158,'Ingin rasa memakan kari,','Kari cendawan batang keladi;','Girang rasa tidak terperi,','Bertemu tuan yang baik budi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55159,'Mencari timba si anak dara,','Di bawah sarang burung tempua;','Salam sembah pembuka bicara,','Selamat datang untuk semua.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55160,'Sayang kumbang mencari makan,','Terbang seiring di tepi kali;','Selamat datang kami ucapkan,','Moga diiring restu Ilahi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55161,'Ke Pekan Kuala membeli bingka,','Sayang pesanan terlupa sudah;','Majlis bermula tirai dibuka,','Dengan alunan madah yang indah.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55162,'Indah berbalam si awan petang,','Berarak di celah pepohon ara;','Pemanis kalam selamat datang,','Awal bismillah pembuka bicara.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55163,'Mega berarak indah berbalam,','Dipuput bayu ke pohon ara;','Pemanis kalam selamat malam,','Awal bismillah pembuka bicara.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55164,'Kalau pergi Tanjung Keramat,','Anak manis jangan diangkat;','Bersama kita memohon rahmat,','Moga majlis mendapat berkat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55165,'Tetak buluh kajang sepuluh,','Laksana dititing kias ibarat;','Angkat tangan jemari sepuluh,','Doa di pohon biar selamat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55166,'Garam ada kicap pun ada,','Sayang lada terlupa bagi;','Pantun ada ucapan ada,','Sayang tiada berdoa lagi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55167,'Lebat kemiri pohonnya rendah,','Dahan terikat tali perkasa;','Sepuluh jari kami menadah,','Mohon berkat yang Maha Esa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55168,'Tetak buluh kajang sepuluh,','Mari jolok sarang penyengat;','Angkat tangan jemari sepuluh,','Doa di pohon biar selamat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55169,'Lebat rumbia di Sungai Kedah,','Sayang senduduk di tepi muara;','Penganan mulia terletak sudah,','Samalah duduk menjamu selera.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55170,'Terbang sekawan si burung merbuk,','Batang selasih di Tanjung Dara;','Padamu tuan kami persembah,','Santapan kasih juadah mesra.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55171,'Kalau tuan pikat cemara,','Buatlah bara bakar selasih;','Sudilah tuan jamu selera,','Hidangan mesra pengikat kasih.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55172,'Sungguh indah bunga kemboja,','Di bawah atap tepi halaman;','Juadah sudah letak di meja,','Sudilah santap tuan budiman.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55173,'Bukan rumput sebarang rumput,','Rumput penghias tepi halaman;','Bukan jemput sebarang jemput,','Jemput memohon kata aluan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55174,'Duduk sekawan gadis jelita,','Buat santapan untuk pahlawan;','Tampillah tuan kami meminta,','Madah ucapan kata aluan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55175,'Sudilah tuan jalan berlapan,','Jalan berlapan di malam kelam;','Sudilah tuan beri ucapan,','Kata aluan pemanis kalam.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55176,'Emas tempawan buatmu puteri,','Busana indah buat rupawan;','Sudilah tuan tampilkan diri,','Bersama madah kata aluan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55177,'Terbang di awan burung jentayu,','Di atas papan batang selisih;','Tampillah tuan kami merayu,','Bersama ucapan pengikat kasih.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55178,'Besar langit di tepi busut,','Besar tak muat di dalam peti;','Besar hajat kami menjemput,','Besar niat di dalam hati.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55179,'Duduk sekawan si burung unta,','Tepi keramat di waktu malam;','Pada mu tuan kami meminta,','Kata azimat penutup kalam.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55180,'Terbang di awan burung jentayu,','Kerana penat gugur ke bumi;','Tampillah tuan kami merayu,','Bersama amanat bekalan kami.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55181,'Pergi ke kota beli selasih,','Sayang si dara buat halwa;','Berilah kata simpulan kasih,','Sulamkan mesra satukan jiwa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55182,'Terbang di awan burung jentayu,','Di atas papan batang jerami;','Tampillah tuan kami merayu,','Bersama ucapan kata perasmi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55183,'Daun semulur di pekan sari,','Batang jerami rebah ke bumi;','Madah dihulur sembah diberi,','Kata perasmi hajatnya kami.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55184,'Jangan tertawan alpa duniawi,','Hanya indah serupa mimpi;','Duhai pahlawan ksatria pertiwi,','Hulurkan madah kata perasmi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55185,'Kalau tuan pikat kenari,','Jangan patahkan batang jerami;','Kepada tuan sembah diberi,','Mohon rasmikan majlis kami.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55186,'Banyak keluk ke penarik,','Keluk tumbuh pohon kuini;','Nan elok bawalah balik,','Nan tak elok tinggallah di sini.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55187,'Bunga dedap di atas para,','Anak dusun pasang pelita;','Kalau tersilap tutur bicara,','Jemari disusun maaf dipinta.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55188,'Pohon berangan tempat bertemu,','Girangnya rasa si anak dara;','Baliklah tuan membawa ilmu,','Binalah bangsa bangunkan negara.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55189,'Di atas dahan burung tempua,','Melihat rusa tepi perigi;','Salam perpisahan untuk semua,','Lain masa bersua lagi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55190,'Bunga seroja di atas para,','Jatuh ditimpa buah berangan;','Andai kata tersilap bicara,','Kemaafan jua kami pohonkan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55191,'Dari Rokan ke Indragiri,','Membawa tinta ke Kuala Linggi;','Baliklah tuan rehatkan diri,','Esok kita bersua lagi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55192,'Dari Kedah ke pekan sari,','Beli suasa di Kota Tinggi;','Selesai sudah tugas diberi,','Di lain masa bersua lagi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55193,'Bulan purnama ke pekan Kuah,','Di tengah hari membeli ragi;','Dirimu umpama nyiur bertuah,','Padat sari harum mewangi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55194,'Curah serbat di atas lantai,','Basah tikar lupa disidai;','Dikau ibarat karang di pantai,','Tak pernah gentar dipukul badai.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55195,'Pohon sena tepi perigi,','Tepi paya si pohon jambu;','Hadirmu laksana suria pagi,','Menyinar maya tak pernah jemu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55196,'Anak dara ke Tanjung Batu,','Janji bertemu di Teluk Bayu;','Walau usia dimamah waktu,','Cekal hatimu tak pernah layu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55197,'Bukan gua sebarang gua,','Gua penuh pusaka bonda;','Bukan tua sebarang tua,','Tua penuh ilmu di dada.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55198,'Pergi ke Nilai membeli jamu,','Jamu rasa dari tenggara;','Tidak ternilai jasa baktimu,','Kepada bangsa juga negara.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55199,'Tidak goyah pancang seribu,','Tika masih badai menderu;','Engkau ayah engkaulah ibu,','Menumpang kasih mengubat rindu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55200,'Pohon beringin tepi keramat,','Dengar siulan diufuk barat;','Kalau ingin hidup selamat,','Buat bekalan untuk akhirat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55201,'Beli keladi di Pekan Lidup,','Sayang rasa lemaknya kurang;','Taburlah budi ketika hidup,','Kelak jasa dikenang orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55202,'Pintal kapas menjadi benang,','Tenun benang menjadi sari;','Barang yang lepas jangan dikenang,','Kalau dikenang meracun diri.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55203,'Pohon senduduk baru di tanam,','Sayang mati di pijak pak tani;','Dari duduk baik bersenam,','Senang hati sihat jasmani.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55204,'Apa guna siparang panjang,','Kalau tidak dengan tujuan;','Apa guna berumur panjang,','Kalau tidak dengan amalan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55205,'Timba suasa ditepi kali,','Buat menyimbah si tuan puteri;','Berputus asa jangan sekali,','Jadikan tabah pakaian diri.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (55206,'Betik muda buat halwa,','Kelapa muda santan dirasa;','Biar muda di dalam jiwa,','Dari tua sebelum masa.',4, 4);