INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50000,'Burung serindit terbang melayang,','Terhenti hinggap di ranting mati,','Bukan ringgit dipandang orang,','Tetapi memandang bahasa dan budi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50001,'Tegak rumah kerana sendi,','Runtuh sendi rumah binasa,','Tegak bangsa kerana budi,','Runtuh budi hilanglah bangsa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50002,'Yang kurik itu kundi,','Yang merah itu saga,','Yang baik itu budi,','Yang indah itu bahasa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50003,'Sirih kuning gagang menguning,','Mari tanam belukar sana,','Seri sirih naik ke kening,','Seri pinang naik ke muka.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50004,'Payah kami menabur padi,','Nenas juga ditanam orang,','Payah kami menabur budi,','Emas juga dipandang orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50005,'Banyaklah rajut perkara rajut,','Rajut bertali bunga bercanggah,','Banyaklah sujud perkara sujud,','Sujud sejati kepada Allah.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50006,'Kalau merenda supaya indah,','Tekat tenun beri berjambul,','Kalau meminta kepada Allah,','Lambat laun pasti terkabul.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50007,'Apa tanda orang mulia,','Berbaik sangka sesama manusia,','Siapa hidup berbaik sangka,','Dunia akhirat hidup sentosa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50008,'Bila todak melanda Singapura,','Habis dikerat dicincang lumat,','Bila khianat pada manusia,','Dunia akhirat takkan selamat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50009,'Habis dikerat dicincang lumat,','Patinya diaduk dijadikan ubat,','Dunia akhirat takkan selamat,','Kecuali minta ampun nasuha taubat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50010,'Baik-baik mengirai padi,','Takut mercik ke muka orang,','Baik-baik menjaga diri,','Takut nanti diejek orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50011,'Apa guna berkain batik,','Kalau tidak dengan sucinya?','Apa guna beristeri cantik,','Kalau tidak dengan budinya.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50012,'Bakul penuh berisi limau,','Sangat berat tak boleh dijinjing,','Biar mati ditangkap harimau,','Jangan mati digigit anjing.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50013,'Hendak gugur gugurlah nangka,','Jangan ditimpa si ranting pauh,','Hendak tidur tidurlah mata,','Jangan dikenang orang yang jauh.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50014,'Buah cempedak di luar pagar,','Ambil galah tolong jolokkan,','Saya budak baru belajar,','Kalau salah tolong tunjukkan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50015,'Bila orang memakan bubur,','Tentulah ada santan kelapa;','Apabila abang segan menegur,','Tentulah hamba segan menyapa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50016,'Beras bukan sebarang beras,','Beras ditumbuk membuang antah;','Panas bukan sebarang panas,','Panas menengok abang menikah.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50017,'Apa gunanya bertih direndang,','Bila tidak dapat dimakan,','Apa guna nya berkasih sayang,','Bila tegak tiada sepadan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50018,'Apa guna pergi ke ladang,','Bila padi tidak bertuai;','Apa guna menanti abang,','Bila adik tidak sesuai.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50019,'Air pasang singgahlah dulu,','Dapatlah berhenti di pulau karang;','Hatiku bimbang bertambah pilu,','Ingat kekasih di rantau orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50020,'Kata tua kata bermanfaat,','Banyak manfaat dapat dirasa;','Hina bangsa tiada muafakat,','Tidak muafakat umat binasa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50021,'Kata tua kata bertuah,','Tuah melekat seisi alam;','Hina bangsa kerana lemah,','Lemah akibat tidak sefaham.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50022,'Bukan mudah memasang jerat,','Salah pasang orang yang kena,','Bukan mudah memegang adat,','Salah pegang orang menghina.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50023,'Adat hidup Melayu terpuji,','Sesama makhluk bersuci hati,','Kepada orang bertanam budi,','Memberi tidak mengharap ganti.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50024,'Rumah bertiang dinding papan,','Tanam selasih di tengah laman,','Biar hilang segala harapan,','Namun kasih hamba turutkan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50025,'Bila selasih di dalam peti,','Sampai masanya dijerang jua;','Sekali kasih didalam hati,','Sampailah tua kukenang jua.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50026,'Biji selasih rasanya lazat,','Di makan serasi membuang panas,','Sekali kasih sudah melekat,','Sampai mati takkan kulepaskan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50027,'Gulai umbut pucuk paku,','Nasi terletak di muka pintu,','Lemah lembut puteri Melayu,','Bijak memasak menjahit baju.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50028,'Kalau hendak membeli cermin,','Belilah cermin tembus cahaya,','Kalau awak menjadi pemimpin,','Jadilah pemimpin lurus bicara.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50029,'Kajang Pak Malau kajang berlipat,','Kajang hamba mengkuang layu,','Dagang Pak Malau dagang bertempat,','Dagang hamba musafir lalu.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50030,'Pergi ke hutan memburu rusa,','Dapat saekor di bagi rata;','Eratkan perpaduan tak kira bangsa,','Menjaga keamanan bumi tercinta.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50031,'Emas perak intan suasa,','Cantik bersinar cahaya berkilauan;','Biar berbeza agama dan bangsa,','Mimpi bersama kukuhkan perpaduan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50032,'Tumbuh melata buah bidara,','Didalam acuan berbagai warna;','Elakkan sengketa demi negara,','Barulah perpaduan dapat dibina.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50033,'Putik kemboja baru mengembang,','Wangi mengharum di tengah laman;','Erat perpaduan bangsa berbilang,','Kemakmuran bersama satu tujuan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50034,'Burung kedidi dipohon nangka,','Anak bayan hinggap di dahan;','Orang berbudi kita berbahasa,','Mesra ikatan erat perpaduan.',4, 4);