INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50035,'Burung merpati di dalam sangkar,','Baru sepurnama dapat dijerat;','Kalau kita rajin belajar,','Ilmu berguna banyak didapat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50036,'Burung pipit burung kedidi,','Hinggap di pokok di tepi sawah;','Jika hidup tidak berbudi,','Ibarat pokok tidak berbuah.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50037,'Dari hulu turun ke pekan,','Sampannya rosak di tengah hari;','Kalau selalu ingatkan tuhan,','Apa dipinta pasti diberi.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50038,'Delima batu dipenggal-penggal,','Bawa galah ke tanah merah;','Lima waktu kalau ditinggal,','Allah Taala memang marah.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50039,'Gulai lemak sayur cendawan,','Anak udang goreng berlada;','Kasih emak sepanjang jalan,','Sayang saudara sama berada.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50040,'Halia ini tanam-tanaman,','Ke barat juga akan condongnya;','Dunia ini pinjam-pinjaman,','Akhirat juga akan sudahnya.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50041,'Hancur-hancur daun keladi,','Buat makanan sarapan pagi;','Hendak dikesal tak guna lagi,','Carilah lain sebagai pengganti.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50042,'Ikan belanak dalam tapisan,','Orang membubu di Kuala Dungun;','Ayuhai anak ingat pesanan,','Kalau jatuh cepatlah bangun.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50043,'Jangan biasa duduk bertentang,','Tengok orang dari belakang;','Cari pagi makan petang,','Asal hidup jangan berhutang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50044,'Jangan suka menganyam nyiru,','Nyiru dianyam di tepi tasik,','Jangan suka menanggung rindu,','Rindu datang bawa penyakit.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50045,'Kalau ada menanam tebu,','Boleh dijual di pasar desa;','Kalau kita jadi tetamu,','Biarlah pandai berbudi bahasa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50046,'Kalau tuan merendang telur,','Jangan terlupa telur yang masin;','Kalau tuan sudah mahsyur,','Jangan lupakan keluarga yang miskin.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50047,'Kembang indah menawan hati,','Sayang terkerat di tengah hari;','Cinta itu laksana api,','Tersalah hemat membawa diri.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50048,'Laksamana Bentam datang menyembah,','Merendah diri walaupun gagah;','Raja adil raja disembah,','Raja zalim raja disanggah.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50049,'Limau manis tiga serangkai,','Taruh mari di kebun cina;','Mulut manis jangan di pakai,','Banyak orang sudah terkena.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50050,'Marilah kita menanam padi,','Padi ditanam di dalam bendang;','Marilah kita menabur budi,','Supaya kita dikenang orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50051,'Medang sila buahnya cantik,','Jatuh menimpa ke pasu bunga;','Dengan sebab nila setitik,','Habis rosak susu sebelanga.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50052,'Menjual lidi ke Batu Bara,','Menjual lokan ke Batu Pahat;','Kerana dengki punya angkara,','Orang baik disangka jahat.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50053,'Naik bukit pergi Asahan,','Bayar niat mudah-mudahan;','Sungguhpun songket kain pilihan,','Tapi tak boleh buat basahan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50054,'Nyiur manis baru gugur,','Dibawa nyonya keluar pagar;','Jasad menangis di pintu kubur,','Menyesal di dunia tidak belajar.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50055,'Orang berniaga semalam-malaman,','Apa jualan pulut berinti,','Hidup di dunia buat amalan,','Untuk bekalan akhirat nanti.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50056,'Orang Daik pergi menuba,','Dapat seekor ikan haruan;','Perkara tak baik jangan dicuba,','Takut kelak merana badan.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50057,'Orang mengail di Lubuk Dulang,','Umpan mari si kulit duku;','Kalau bermain tunangan orang,','Nyawa bergantung di hujung kuku.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50058,'Pagi-pagi kapal berlabuh,','Singgah di Bentan membeli kopi;','Bangun pagi sembahyang Subuh,','Minta Tuhan tambahkan rezeki.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50059,'Pokok cempedak di tepi tangga,','Buah sebiji gugur terbuang;','Janganlah mudah merasa bangga,','Bila diri dipuji orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50060,'Rendang sungguh pohon ara,','Tumbuh subur tepi telaga;','Tidak salah berkenal mesra,','Tatasusila perlulah dijaga.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50061,'Sayang laksamana menggulung benang,','Benang digulung perahu Bugis;','Orang jauh jangan dikenang,','Kalau dikenang tunduk menangis.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50062,'Ambil bertih dari hulu,','Isi mari di dalam balang;','Bersihkan laman kita dahulu,','Baru bersihkan halaman orang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50063,'Anak ayam di atas pagar,','Turun ke tanah memakan padi;','Kalau hutang wajib dibayar,','Kalau janji wajib ditepati.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50064,'Cedok air dengan baldi,','Cedok nasi dengan senduk;','Baik dibawa resmi padi,','Semakin berisi semakin tunduk.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50065,'Kalau ke bukit sama mendaki,','Kalau ke laut sama berenang;','Kalau kita bersatu hati,','Kerja yang berat menjadi senang.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50066,'Kelapa muda rumah Megat,','Mari makan bulan puasa;','Badan muda biarlah ingat,','Sementara belum rosak binasa.', 4, 4);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (50067,'Kelapa muda, muda juga,','Kelapa tua dibuat inti;','Suka, suka juga,','Sembahyang kita jangan di henti.',4, 4);