INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100002,"Dari Bangi ke Johor Bahru,","Sembilan jam sesak di jalan,","Walaupun hidup terus memburu,","Sejahtera di akhirat jadi tujuan", 4, 6);
