INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100004,"Suka dan duka sering dikongsi,","Supaya derita tidak menyiksa,","Perjalanan sukar menuju destinasi,","Ujian bahagia kembali ke desa", 4, 6);
