INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100006,"Hadiah tradisi berupa telur,","Simbol kegembiraan dengan kehadiran,","Salam Eidul Fitr tangan dihulur,","Ibadah Ramadhan mendidik keperibadian", 4, 6);
