INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100008,"Berjalan-jalan di bazaar Ramadhan,","Beli sedikit juadah berbuka,","Hidup ini bukanlah mainan,","Perjuangan panjang yang terus dibawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100009,"Membakar daging dengan arang,","Menambah selera bercampur asap,","Usah diperkecil peranan orang,","Semua pihak ada tanggungjawab", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100010,"Ramadan menghampiri penghujung jalan,","Semakin bersemangat untuk beribadah,","Teguhkan hati hadapi cabaran,","Mudah menyerah menempah kalah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100011,"Kisah percintaan ke hujung nyawa,","Tema drama yang sering ditayang,","Pengurusan berintegriti dambaan semua,","Organisasi kukuh suasana merangsang", 4, 6);
