INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100013,"Menanti keputusan penuh debaran,","Akan mencorak maruah dan wibawa,","Membesarkan anak banyak cabaran,","Kesabaran tinggi membentuk jiwa", 4, 6);
