INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100015,"Mengupas mangga berhati-hati,","Jangan sampai terluka tangan,","Ramadhan bulan mengasuh hati,","Amanah usahlah dipandang ringan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100016,"Setiap pagi menghadiri kuliah,","Tanyalah Ustaz program pilihan,","Sentiasa berdoa memperoleh hidayah,","Agar kehidupan tiada penyesalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100017,"Perlu melompat bermain teng-teng,","Suara teriakan mengganggu lena,","Undangan sahabat usah dienteng,","Tanda kita dalam ingatannya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100018,"Hidup bererti dengan berbudi,","Menjadi amal dibawa ke akhirat,","Pemergian ibu saudara ke alam abadi,","Semoga rohnya dicucuri rahmat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100019,"Majlis dirasmikan memukul gong,","Diikuti pula tepukan tangan,","Nuzul AlQuran sejarah yang agung,","Untuk penghayatan panduan kehidupan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100020,"Berbuka puasa bersama rakan,","Rakan yang lama pergi melata,","Ramadhan sememangnya sebuah pendidikan,","Menguji keikhlasan kepada yang meminta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100021,"Berterbangan riang burung merpati,","Hinggap sebentar di Batu Layar,","Pertolongan orang kita hargai,","Penat lelahnya tidak terbayar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100022,"Cuaca panas berpeluh di dahi,","Air diteguk dingin meluru,","Undangan ikhlas harus dipenuhi,","Pererat persahabatan lama dan baru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100023,"Sebarkan kebaikan tak kira siapa,","Harus disantuni semua insan,","Apa pun karenah ibu dan bapa,","Layani dengan penuh keikhlasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100024,"Sampaikan kebaikan banyaklah cara,","Cara terbaik terus dirancang,","Berlemah lembut bila berbicara,","Tentu dihormati mulia dipandang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100025,"Di hari raya berbaju kurung,","Berkain batik bunga kiambang,","Bakat kanak-kanak harus didorong,","Berikan semangat supaya berkembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100026,"Mencari langgai dapatnya bawal,","Setelah makan terus terlena,","Menyemai nilai sedari awal,","Melentur buluh dari rebungnya", 4, 6);
