INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100040,"Musim pilihanraya banyaklah kempen,","Banyak pula janji bertaburan,","Berilah ruang yang muda memimpin,","Kalau terkhilaf berilah panduan", 4, 6);
