INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100053,"Angin berhembus cuaca pun redup,","Segerakan apa yang perlu dibuat;","Kesakitan menunjukkan kita masih hidup,","Permasalahan tandanya kita masih kuat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100054,"Diri terhina dengan panas baran,","Orang melihat terasa rimas;","Didiklah diri dengan kesabaran,","Jiwa yang berkembang usah ditindas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100055,"Walaupun lesu tetap digagahi,","Ke puncak gunung jadi sasaran;","Adakalanya harapan tidak terpenuhi,","Ada pendidikan Tuhan sampaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100056,"Jalan berliku ke Sungai Koyan,","Singgah sebentar di pekan Mentakab;","Kemenangan dilihat sebagai ujian,","Penuhi janji dengan bertanggungjawab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100057,"Masam manis rasa delima,","Beli sekilo bawa ke Sabah;","Keputusan pilihan raya sudah pun diterima,","Usah pertikai lakukanlah muhasabah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100058,"Adakalanya hidup dengan kesukaran,","Harus ditempuh segala badai;","Pilih pemimpin dengan kesedaran,","Masa depan kita usah digadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100059,"Tikar diperbuat daripada rotan,","Bergotong-royong sambil berlawak;","Patuhilah arahan tata kesihatan,","Demi kebaikan semua pihak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100060,"Di kawasan tertentu pandu perlahan,","Hormati pengguna yang kian bertambah;","Usah disesali ujian Tuhan,","Pasti berhikmah di sebalik musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100061,"Naik rakit milik nelayan,","Mudik ke hilir membawa jala;","Hati yang sakit usah dilayan,","Jadikan ujian peningkat takwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100062,"Kapal padat meriam dan bedil,","Pergi belayar ke tanah asing;","Berhujah hebat perihal yang batil,","Di depan Munkar Nakir mulut terkancing.", 4, 6);
