INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100064,"Gelang emas gelang suasa,","Tak semua boleh jadi gadaian;","Hidup berjiran berbudi bahasa,","Hormat menghormati elak persengketaan.", 4, 6);
