INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100066,"Universiti rakyat universiti watan,","Tempat membina generasi matang;","Pemandangan indah dari perbukitan,","Kota Kinabalu terus pesat berkembang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100067,"Jadual harian semakin padat,","Terus berfikir mengasah minda;","Pertama kali menjejaki Kudat,","Penghujung utara kini disapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100068,"Kuih tradisi kuih bahulu,","Dibakar dengan himpunan sabut;","Setiap kali ke Kota Kinabalu,","Gembiranya hati sukar disebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100069,"Kuih tradisi kuih bahulu,","Sentiasa ada dimusim perayaan,","Apabila malang tidak berbau,","Keselamatan diri sering diabaikan", 4, 6);
