INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100071,"Bersarapan pagi di warung sebelah,","Nasi lemak kuih keria;","Berpolitik boleh menjadi ibadah,","Harus didahului niat yang mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100072,"Kerbau menguak di tengah lembah,","Terdengar jauh merentas desa;","Cabaran diharungi dengan tabah,","Janganlah mudah berputus asa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100073,"Kalau melancong ke Sulawesi,","Belilah batik barang sehelai;","Janganlah ada perasaan benci,","Kalaulah ada segeralah dilerai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100074,"Perjalanan indah kembali ke Selatan,","Berehat seketika di Hentian Renggam;","Mengunjungi keluarga perkukuh ikatan,","Kejap bagaikan tangan menggenggam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100075,"Di pohon mangga menumpang dedalu,","Jika dibiar pasti merosakkan;","Usah dikesal masa yang lalu,","Untuk dijadikan asas kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100076,"Segenggam kapas memanglah ringan,","Berbanding dengan segenggam lada;","Nafkah keluarga dilupa jangan,","Berikan lebih pahala berganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100077,"Bunga cempaka berbau harum,","Bulan mengambang bercahaya indah;","Senyum terukir cantik menguntum,","Berpuluh tahun jasad terpisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100078,"Awan mendung berarak perlahan,","Cuaca redup hiba di hati;","Prinsip perjuangan harus dipertahan,","Didakap erat sampai kemati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100079,"Kisah lama masih diulang,","Kenangan indah tak boleh dilupa;","Musibah datang bukanlah malang,","Untuk menguji kekuatan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100080,"Pantai putih pasirnya gebu,","Tempat bermain diwaktu petang;","Bersihkan hati daripada berdebu,","Supaya hubungan tidak terhalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100081,"Kereta baru dipandu laju,","Tidak dihirau walau disaman;","Usia meningkat dipohon restu,","Keberkatan hidup jadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100082,"Bersama sahabat semua bercerita,","Cerita lama kembali diputar;","Isu politik tidak habis dibicara,","Perbincangan sengit cabar mencabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100083,"Usah merasakan dirimu kuat,","Di alam maya hanyalah sekuman;","Apabila berkesempatan pergilah melawat,","Untuk peroleh ilmu dan pengalaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100084,"Luka kecil bengkak bernanah,","Sedari awal tiada rawatan;","Harapkan pemimpin bersifat amanah,","Seakan sukar jadi sandaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100085,"Burung terbang dipipiskan lada,","Itulah bidalan lama dahulu;","Kasih saudara masa berada,","Usah terbudaya di dalam kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100086,"Adakala diperdaya Sang Kancil,","Bergerak tangkas akalnya cerdas;","Rawatan tradisional usah diperkecil,","Ilmu Ilahi amatlah luas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100087,"Tekak sakit tersekat tulang,","Apatah lagi tulang berduri;","Kampung tradisi kian menghilang,","Kenangan tetap kekal abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100088,"Kelapa muda banyak khasiat,","Airnya lazat isinya lembut,","Kalau hidup tidak bermanfaat;","Bila mati nama pun tak disebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100089,"Anak muda suka berkelana,","Berkelana jauh mencari rezeki;","Walimahtul urus lancar terlaksana,","Himpunan keluarga berlangsung lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100090,"Jangan biasa lebihkan gula,","Sentiasa jaga lestari kesihatan;","Perniagaan sahabat baru bermula,","Mari bersama berikan sokongan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100091,"Hampir habis air segelas,","Hilangkan pedas tempoyak patin;","Kalaulah hati tidak lagi ikhlas,","Berilah laluan orang lain berprihatin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100092,"Banyak kebaikan mengingati mati,","Jadikan kita penuh kesedaran;","Usia emas yang sedang dinikmati,","Ingatilah sahabat yang sedang keuzuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100093,"Kekurangan diri perlu diperhati,","Jangan sampai orang mengata;","Saat pertemuan sangat dinanti,","Sahabat lama berkongsi cerita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100094,"Usah dibiar yang terpendam dihati,","Kongsikan dengan orang dipercaya;","Riadah harian usah berhenti,","Di usia emas kesihatan dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100095,"Tetamu datang bertali arus,","Riuh rendah dengan bersembang;","Rumah sahabat terbiar tak terurus,","Urusan kehidupan tidak terancang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100096,"Badan letih tidur pun lena,","Rehat yang panjang tiada igauan;","Dasar Pandang ke Timur lama terlaksana,","Nilai yang murni dijadikan pegangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100097,"Kopi panas di dalam gelas,","Gelas tebal berdaya tahan;","Amanah diberi harus digalas,","Kejujuran tulus akan menyempurnakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100098,"Dipagi hari berangkat pergi,","Kereta dipandu dengan kemas;","Persahabatan lama perlu dihargai,","Meriahkan kehidupan diusia emas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100099,"Berbondong-bondong rombongan Cik Kiah,","Tua dan muda pelbagai rupa,","Majlis walimah sungguh meriah,","Golongan miskin tidak dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100100,"Tahun Baru Cina sungguh meriah","Dentuman mercun tidak ketinggalan;","Kehidupan hina mengamal rasuah","Tidak terfikir hidup perlu keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100101,"Berenang cantik ikan pelaga","Meliuk lintuk lembut melambai;","Terus berprihatin terhadap keluarga","Ibu bapa yang tua jangan diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100102,"Menumbuk padi di dalam lesung","Lesung besar daripada kayu;","Apabila perkahwinan sudah berlangsung","Dua keluarga kini bersatu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100103,"Majlis perkahwinan di Pekan Ceras","Saudara mara datang menyertai;","Untuk berjaya usahlah bermalas","Sampai bila pun hanya bermimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100104,"Angin menderu terasa dingin","Perubahan musim memang dinanti;","Tetapkan azam apa yang diingin","Usaha berterusan usah berhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100105,"Membakar ikan gunakan arang","Rasanya enak sungguh mengasyikkan;","Usah dibiarkan kesusahan orang","Bantulah segera ringankan beban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100106,"Jarang didengar penyakit kusta","Pelbagai wabak perlu dihindar;","Jiwa resah sentiasa berdusta","Keuntungan dunia terus dikejar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100107,"Awan berarak ke ufuk timur","Langit cerah panas terasa;","Penghormatan tidak berasaskan umur","Kebaikan yang tulus lebih berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100108,"Telah pun layu bunga melati","Harus disiram usah berlebihan;","Hati yang rosak perlu diubati","Dengan berzikir mengingati Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100109,"Peralatan dapur di atas para","Mudah dicapai untuk masakan","Protes rakyat diambilkira","Masa depan negara perlu diutamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100110,"Masak menguning sibuah mangga","Makan berlebihan sampai terbatuk;","Sampailah masa berumahtangga","Kematangan berfikir seharusnya terbentuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100111,"Perhatikan jalan jangan tersasar","Agar segera sampai ke tujuan;","Perkahwinan adalah amanah besar","Dipikul bersama dengan penuh kesabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100112,"Ke Inderapura memanglah jauh","Berhenti seketika di warung nasi;","Bagai bahtera kita bersauh","Tiba masanya sampai di destinasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100113,"Tanaman bunga menjadi hobi","Untuk pesara mengisi masa;","Kenaikan pangkat janganlah dilobi","Maruah diri pelihara sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100114,"Teruja pesara menanti pencen","Akaun bank sering diintai;","Usah bersedih peristiwa kelmarin","Perjalanan hidup jangan dirantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100115,"Sungguh lazat kelapa muda","Isinya lembut mudah ditelan;","Persoalan agama usah dipersenda","Prinsip sebenar perlu ditegakkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100116,"Kopi terenak dari Kedah","Di waktu petang menjadi hidangan;","Mencari kebenaran tidaklah mudah","Fakta yang jelas perlu disediakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100117,"Pergi ke surau berkain sarung","Di pinggir jalan membeli nangka;","Janganlah mudah memandang serong","Bersihkan hati berbaik sangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100118,"Tengkujuh reda masa pemulihan","Rumah yang musnah dibaiki semula;","Apabila keadilan tidak dilaksanakan","Bermulalah keruntuhan organisasi yang terbina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100119,"Terengganu Darul Iman kini ditinggal","Negeri indah penduduknya ramah;","Biarpun berkhidmat hanya sepenggal","Jasanya terkesan khidmatnya berhemah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100120,"Keropok dan batik di Pasar Payang","Antara keistimewaan negeri Terengganu;","Maruah diri usahlah diperdagang","Perlu disuburkan rasa malu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100121,"Setelah lama tidak mengunjungi","Negeri Terengganu penuh keindahan;","Usahlah bertingkah pelbagai kontroversi","Banyak kenyataan banyaklah persoalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100122,"Biarpun diuji berkali-kali","Tetap ditempuhi dengan ketabahan;","Musibah menimpa usah disesali","Pasti berhikmah penuh pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100123,"Di tanah lapang menanam halia","Hasil dikutip memang lumayan;","Berkata benar sifat mulia","Sekali dinodai hilang penghormatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100124,"Ibu tua menanti kepulangan","Musim perayaan kini kembali;","Ulangtahun ketujuh cucu kesayangan","Semoga kehidupan diberkati Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100125,"Sangat digalakkan pintar memanah","Memanah melatih keputusan tepat;","Pertanggungjawaban adalah amanah","Setiap tindakan diperhatikan rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100126,"Pelbagai haiwan hidup melata","Banyak terdapat di hutan Lundu;","Sebagai pemimpin usah berdusta","Setiap dusta mudah dihidu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100127,"Sungguh indah sirama-rama","Riang mencari bunga-bungaan;","Peluang yang baik dikongsi bersama","Kemakmuran sahabat menjadi kebanggaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100128,"Buah dikeluarkan dengan memulas","Kulit pulasan memanglah liat;","Jika menolong hendaklah berikhlas","Jangan sampai rakyat meluat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100129,"Dalam hutan unggas bernyanyi","Selepas hujan riang dan ria;","Selama ini banyak yang tersembunyi","Banjir besar memecah rahsia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100130,"Mari selesaikan perkara yang kusut","Barulah hidup ceria kembali;","Meskipun banjir sudah menyusut","Aktiviti pemulihan banyak sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100131,"Hadapi ujian dengan tabah","Segala kesukaran dipikul bersama","Sahabat lama semuanya berubah","Hampir tidak dikenali meskipun nama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100132,"Usah biasa melebihkan gula","Punca penyakit jejaskan kesihatan","Azam yang lama dinilai semula","Segala kekurangan lakukan penambahbaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100133,"Jualan murah memilih sepatu","Belikan juga adik beradik","Hari terakhir 2021","Bermuhasabah diri pilihan terbaik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100134,"Usahlah sudu melebihi kuah","Bidalan lama untuk panduan","Bantuan dihulur melimpah ruah","Jangan sampai berlaku pembaziran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100135,"Berbau wangi buah kuini","Manisnya tidak seperti madu","Berhati-hati cuaca begini","Segala kemungkinan boleh berlaku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100136,"Matahari bersinar wajah ceria","Jiwa gelisah langit yang mendung","Pemimpin sejati disanjung mulia","Pemimpin palsu minta disanjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100137,"Menabur jagung makanan merpati","Diserbu merpati jumlahnya ratusan","Pemimpin bertingkah sentiasa diperhati","Terasa mual dengan kepura-puraan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100138,"Setiap makhluk ada peranan","Supaya kehidupan terus seimbang","Bencana melanda bersama pesanan","Usah musnahkan alam yang terbentang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100139,"Cuba longgarkan ikatan terpintal","Untuk menambat sampan di pengkalan","Semoga berbahagia di Hari Natal","Kukuhkan kekeluargaan asas kebahagiaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100140,"Jangan abaikan anjuran Tuhan","Barulah peroleh tenang dan damai","Biar pun hidup tanpa kemewahan","Budaya memberi terus disemai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100141,"Berteduh hujan di pohon ara","Tidak pun basah sama sekali","Tanpa disangka bertemu saudara","Sedari kecil belum pun dikenali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100142,"Adakalanya perlu beri amaran","Untuk penyelesaian yang perlu dicari","Semasa kecemasan banyak cabaran","Keputusan dibuat harus berhati-hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100143,"Apabila berbicara mesti berhati-hati","Jangan ada hati yang terluka","Perilaku pemimpin kini diperhati","Disaat sukar apa tindakan mereka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100144,"Bagaimana berat mata memandang","Berat lagi bahu memikul","Hanya bersabar musibah yang datang","Ujian Tuhan untuk kita menakkul", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100145,"Tebing pecah di Sungai Tekali","Air deras mengalir laju","Musibah banjir berat sekali","Semua bersatu datang membantu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100146,"Berwaspada gunting dalam lipatan","Agar tidak ditimpa musibah","Walimahtul urus adalah kesempatan","Kukuhkan kembali pertalian ukhuwah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100147,"Menampung berat dengan bertongkat","Berhati-hati apabila berjalan","Biar pun usia sudah meningkat","Kesihatan diri berikan perhatian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100148,"Ada udang di sebalik batu","Batu karang banyak rahsia","Kesusahan orang harus dibantu","Tuhan gantikan ganjaran berganda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100149,"Undangan diterima majlis makan","Lauk pauk semuanya lazat","Cadangan diberi memang menjanjikan","Berhati-hati keputusan yang dibuat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100150,"Banyak perkara ingin dihindari","Fobia tercipta harus disingkir","Idea yang terzahir kurnia Ilahi","Usahlah angkuh keupayaan berfikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100151,"Mendayung sampan merapat ke tepi","Sampai ke tebing terus melompat","Nasihat yang baik harus disyukuri","Masih ada yang mengambil berat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100152,"Ramai orang yang kaya raya","Kaya harta indah keperibadian","Kehilangan sahabat yang berbudi mulia","Semoga damai dalam keredhaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100153,"Semakin dibuat perkara dilarang","Tidak terfikir perkara yang merugi","Penganiayaan dilakukan kepada orang","Akan berbalik ke diri sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100154,"Kita hanyalah insan yang lemah","Hidup ini ibaratnya roda","Apabila di atas","mungkin esok akan ke bawah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100155,"Ekspedisi menarik ke Hutan Belum","Tasik membiru pemandangannya indah","Dalam era ekonomi merudum","Rancang perbelanjaan dengan berhemah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100156,"Bermacam inti di dalam pau","Semua dirasai untuk perbandingan","Karenah birokrasi yang sangat melampau","Hanya jejaskan reputasi kerajaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100157,"Ada orang menyebutnya cabai","Umum sebutan adalah cili","Nasihat sahabat jangan diabai","Itu tanda dia mengasihi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100158,"Lakukan sesuatu dengan terarah","Barulah nanti peroleh kepuasan","Cerita rakyat adalah sejarah","Perlu didokumentasi sebagai warisan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100159,"Memang berkhasiat kelapa dara","Boleh menjadikan badan pun kurus","Peluang bertemu sanak saudara","Hadirilah undangan walimahtul urus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100160,"Kambing berlari di atas titi","Dikejar oleh sekumpulan lebah","Pemimpin organisasi silih berganti","Matlamat asal usahlah berubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100161,"Sudah ditarah Bukit Pelali","Di lereng bukit dibina kediaman","Setelah ditangguh beberapa kali","Makan Malam PAMAJA berjaya diadakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100162,"Faktor dalaman faktor luaran","Supaya tiada yang rasa tersisih","Semasa pandemik banyak cabaran","Penyebaran berita perlulah sohih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100163,"Bila malam turunkan kelambu","Nyamuk menyerang pasti tersekat","Janganlah lokek berkongsi ilmu","Banyak memberi banyak yang dapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100164,"Jangan dikata otak berkarat","Semua bermula dihati nurani","Persahabatan hakiki tanpa bersyarat","Di saat susah sentiasa menemani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100165,"Berhenti rehat di Hentian Macap","Beli sebiji buah mempelam","Sifat manusia suka bercakap","Kalau tak tahu lebih baik berdiam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100166,"Menaiki basikal pergi ke lahan","Lahan dimiliki panjang dan lebar","Dalam hidup ada permasalahan","Hadapi ia dengan tenang dan sabar", 4, 6);
