INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100251,"Karya ilmu dan pelbagai hikayat,","Mercu tanda tamadun bermartabat;","Tuntutlah ilmu sepanjang hayat,","Cemerlang di dunia gemilang di akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100252,"Mahkota raja bertatah intan,","Anggun dipakai di atas takhta;","Warisan dipulihara kemodenan dipesatkan,","Universiti watan lestarikan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100253,"Sayyidul ayyam hari Jumaat,","Penuh barakah kurniaan Ilahi;","Penubuhan UKM mandat rakyat,","Akan dipenuhi amanah abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100254,"Dirgahayu UKM telah berlalu pelbagai peristiwa,","Semuanya bermanfaat menjadi teladan;","Roh UKM tersemat dijiwa,","Universiti keramat universiti kebanggaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100255,"Terbang beralun sirama-rama,","Bagai ombak tali temali;","Hormat menghormati prinsip bersama,","Mengapa dilanggar berkali-kali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100256,"Hati tenang melihat ikan,","Berenang-renang riang dan ria;","Kedaulatan undang-undang sering dilaungkan,","Rakyat memerhati ia dilaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100257,"Sentiasa memelihara kesihatan gigi,","Ikuti jadual rawatan diatur;","Meraikan sahabat yang akan pergi,","Sehingga bertemu dipanjangkan umur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100258,"Tak hilang bisa ular diakar,","Mengesan mangsa deria bau;","Membina keikhlasan memanglah sukar,","Amal ibadah tak perlulah beritahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100259,"Buah berkhasiat itulah kurma,","Dibulan Ramadan paling dicari;","Pandangan pakar harus diterima,","Usah bergantung berita tak pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100260,"Angin semilir bertiup lembut,","Pohon nyiur melambai-lambai;","Redakan keadaan yang kelam kabut,","Supaya kestabilan dapat dicapai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100261,"Sorong papan hingga ke depan,","Untuk diletakkan daun selasih;","Keadilan adalah asas kehidupan,","Harus dilaksana tanpa pilih kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100262,"Jalanraya lengang kini di bandar,","Berhati-hati wabak yang bertebaran;","Kini rakyat semakin sedar,","Apa yang berlaku jadi perdebatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100263,"Musim durian hampir berakhir,","Muncul pula buah rambai;","Berlapang dada perkembangan mutakhir,","Kepentingan rakyat jangan terabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100264,"Pagi-pagi sudah berangan,","Sambil berbaring fikiran melayang;","Hendak melantik perlu pertimbangan,","Supaya tidak dipersendakan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100265,"Terbang unggas di langit biru,","Bermain-main di celah awan;","Politik Malaysia semakin bercelaru,","Kalau lompat sikatak lompat dibiarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100266,"Terasa bosan lama menanti,","Tidak pun muncul lintas kelibat;","Buat keputusan biar berhati-hati,","Usah timbulkan amarah rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100267,"Antara kegemaran kuih ketayap,","Aroma pandan tambah kelazatan;","Mendapatkan jawatan dengan tanggungjawab,","Kepentingan masyarakat menjadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100268,"Air yang dingin di dalam kendil,","Sungguh menyegarkan hilang dahaga;","Pemergian ilmuwan Dr Siddiq Fadzil,","Bangsa kehilangan permata berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100269,"Pagi nan indah udaranya nyaman,","Unggas terbang bersiulan riang;","Mari raikan Hari Kemerdekaan,","Hasil pengorbanan para pejuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100270,"Gerai Pak Su menjual peneram,","Kini hidupnya senang dulunya susah;","Orang khianat tidak kan tenteram,","Jiwa derita resah gelisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100271,"Jangan biarkan menjadi muflis,","Perlakuan bercanggah hilanglah pahala;","Jika takdir sudah tertulis,","Jodoh tertangguh berlangsung jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100272,"Selepas hujan terasa kesepian,","Mengenang diri tidak sempurna;","Pemergian Tun Ahmad satu kehilangan,","Jasa dan bakti dikenang selamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100273,"Tidak semua berkebolehan mengarang,","Ilham datang di malam sunyi;","Usah dipersenda kebijaksanaan orang,","Tidak semua yang kita ketahui.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100274,"Di pagi raya berjuadah ketupat,","Rendang dan lodeh jadi hidangan;","Apabila keputusan sudah dibuat,","Bertawakallah melaksana dengan kesungguhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100275,"Tempat berwacana tersedia di persada,","Perdebatan ilmiah sering dianjurkan;","Bukakan pemikiran luaskan dada,","Demi rakyat lupakanlah perselisihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100276,"Burung berterbangan merah di dada,","Terbang berkawan sambil bersiulan;","Usah dipersulitkan keadaan yang ada,","Gembleng keupayaan membina kesejahteraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100277,"Cuaca kering menyebabkan kebakaran,","Hasil pertanian banyak binasa;","Melihat sesuatu dengan pemikiran,","Dapat menginsafkan kebesaran Sang Pencipta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100278,"Burung camar terbang berkawan,","Pulang ke sarang menjelang malam;","Kematian ilmuwan sangat dirasakan,","Bagai obor yang telah padam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100279,"Raja Udang burung yang cantik,","Hinggap seketika di pohon ara;","Perdana Menteri baru telah pun dilantik,","Tumpukan perhatian mengurus negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100280,"Sentiasa berhemah bila berkata,","Peliharalah hati apabila berteman;","Universiti watan naratif kita,","Terus diperjuangkan zaman berzaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100281,"Kenangan pahit terus terpahat,","Peristiwa hitam tidak terhurai;","Dalam politik banyak muslihat,","Kejujuran hilang, maruah tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100282,"Awal malam sudah terlena,","Perut kenyang nikmatnya makan;","Senario politik menguasai suasana,","Semoga perubahan membawa kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100283,"Rempah ratus di Pulau Maluku,","Hasil penjajahan kerajaan Belanda;","Bersabar menanti perubahan berlaku,","Harapan rakyat sedang membara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100284,"Suasana resah semakin kepanasan,","Hiruk pikuk kata mengata;","Apabila bersahabat tiada keikhlasan,","Saling mengaibkan rahsia terbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100285,"Nasi lemak di atas meja,","Disapu kaya sekeping roti;","Berbudi bahasa jadikan budaya,","Di persada dunia bangsa dihormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100286,"Dusun meriah dikunjung tetamu,","Buah yang ranum dipohon yang rendah;","Jika sampai rakyat pun jemu,","Pemimpin mungkar kan terima padah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100287,"Buah manggis terhimpun di nyiru,","Buah durian semakin berkurang;","Pemimpin bertelagah rakyat keliru,","Letakkan keutamaan apa yang diperjuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100288,"Memberi kebahagiaan jiwa pun girang,","Janji dimeterai mesti ditepati;","Bersedekah tanpa diketahui orang,","Bina keikhlasan menyuci hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100289,"Dalam air terbayang wajah,","Wajah hilang air berkocak;","Tegakkan benang basah dengan berhelah,","Hilang penghormatan orang dah muak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100290,"Usah bersedih berputus tunang,","Ada hikmah di sebalik peristiwa;","Orang berbudi sentiasa dikenang,","Terasa kehadirannya walaupun telah tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100291,"Mendengar suara berbisik perlahan,","Dicelah pohon kukuh berakar;","Pemergian Sarah amat dirasakan,","Semoga yang ditinggalkan tabah dan sabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100292,"Timbul kerisauan senja yang merah,","Bagaikan petanda kepada wabak","Budaya memfitnah kian membarah,","Harus dibendung agar tidak merebak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100293,"Media sosial kini menjadi alat,","Harus berhati-hati memberi pandangan;","Angka kematian semakin meningkat,","Wabak ini usah diambil ringan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100294,"Angin panas terasa dipipi,","Monsun Barat Daya memberi kesan;","Kenyataan disiar kenyataan dinafi,","Rakyat tak peduli tanda dah bosan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100295,"Negeri Terengganu ramai pengukir,","Ukiran kayu dibuat bekas;","Selagi kemelut belum berakhir,","Tangani wabak tidak terfokus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100296,"Pemandangan indah di Lurah Bilut,","Tempat berehat tenangkan fikiran;","Konflik politik semakin kalut,","Rakyat keliru semakin bosan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100297,"Sedang berlangsung musim buah-buahan,","Kuranglah dicari buah rambai;","Krisis kepemimpinan jadi perhatian,","Pembelaan rakyat usah diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100298,"Berkayuh sampan mudik ke hulu,","Arus diredah dengan berani;","Mengimbau perjuangan tokoh terdahulu,","Legasi tinggalan wajar diteladani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100299,"Dibuat jeruk buah berangan,","Apabila diperam tidaklah lama;","Hilangkan sengketa harmonikan hubungan,","Masalah negara hadapi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100300,"Sentiasa dambakan berita terkini,","Yang benar lagi berwibawa;","Pemimpin tulin harus berani,","Usah berdalih apa lagi berdusta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100301,"Jangan sampai diberi amaran,","Ikutlah peraturan yang disenarai;","Amalan agama perlu ada sandaran,","Sunnah Nabi usahlah diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100302,"Perlakuan janganlah menjolok mata,","Hanya timbulkan benci dihati;","Di saat rakyat sedang derita,","Para pemimpin tunjukkanlah empati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100303,"Bersilang kaki duduk dikerusi,","Merenung alam ciptaan Tuhan;","Bagi yang berlebih mari berkongsi,","Mereka yang kurang usah dibiarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100304,"Enak sekali rendang serati,","Juadah dihidang di atas meja;","Jadi pemimpin sentiasa diperhati,","Agar dihormati akhlak dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100305,"Pergi ke pasar beli tenggiri,","Harga murah tak perlu ditawar;","Lihatlah kepada diri sendiri,","Jika hubungan menjadi hambar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100306,"Tempat bertemu di satu lingkaran,","Kenderaan beratur berbaris- baris;","Setelah bersara aktifkan pemikiran,","Banyakkan membaca dan juga menulis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100307,"Tumpang berteduh di pohon sena,","Tumbuh subur di pinggir jalan;","Perasaan gembira melihat suasana,","Fakir dan miskin beraya korban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100308,"Resah sekali di dalam gerabak,","Penumpang bersesak semua kepanasan;","Kematian sahabat kerana wabak,","Perginya tiba-tiba amat mengejutkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100309,"Seperti terdengar bunyi tembakan,","Burung bernyanyian terhenti tiba-tiba;","Kelihatan wabak semakin membimbangkan,","Berusaha hindari bertawakal sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100310,"Memang berkhasiat durian Belanda,","Hijau kulitnya lembut berduri;","Usah dibiar tekanan melanda,","Kelak merosak diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100311,"Sambil bercerita lidi diraut,","Menghimbau kembali kisah yang lama;","Jasad terpisah jiwa bertaut,","Eidul Adha diraikan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100312,"Hujan turun mendingin hawa,","Selepas hujan udara pun segar;","Berpuasa Arafah besar manfaatnya,","Semangat berkorban terus dibugar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100313,"Menyusuri pagi di pantai yang indah,","Sambil menatap awan gemawan;","Dalam persahabatan jagalah lidah,","Tanpa sedar tersinggung perasaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100314,"Berjalan kaki berbatu-batu,","Di dalam hutan meredah sengsara;","Berganding bahu kita membantu,","Sewaktu berada janganlah berkira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100315,"Terik mentari mata berpinar,","Keringat berbintik membasahi dahi;","Pandemik yang ada memang lah benar,","Norma baharu haruslah dipatuhi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100316,"Ingin melancong ke Balik Papan,","Tempatnya indah lagi berbudaya;","Kalau pun berlaku sebarang kesilapan,","Usahlah diaib dan dipersenda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100317,"Cuaca harian sentiasa diramal,","Aktiviti direncana untuk keluarga;","Usahlah berkira apabila beramal,","Tuhan mengganjari berlipat ganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100318,"Seorang sarjana bicaranya indah,","Banyak tersirat disebalik kata;","Semasa pandemik sentiasalah bertabah,","Ujian Tuhan di alam semesta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100319,"Disenangi orang berbicara ramah,","Tetamu berkunjung dari Darau;","Bila berbelanja sentiasa berhemah,","Tidaklah sesak dimusim kemarau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100320,"Burung berkicau riang menari,","Angin berhembus terasa dingin;","Jaga kesejahteraan jagalah diri,","Kesihatan keluarga harus dipimpin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100321,"Bayangkan esok kita kan mati,","Apa dilakukan hari kelmarin;","Laksanakan amanah sepenuh hati,","Usah dirampas hak orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100322,"Memetik bunga dalam belukar,","Janganlah sampai tertusuk duri;","Dalam suasana susah dan sukar,","Janganlah pentingkan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100323,"Apabila datang suatu bencana,","Sabar dan bersatu menjadi tuntutan;","Jika kepimpinan tidak sempurna,","Pasti negara tidak berhaluan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100324,"Tempat bersejarah di Kota Linggi,","Turut dirungkai catatan lama;","Berpulang saudara ke pangkuan Ilahi,","Iringkan doa berbahagialah di sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100325,"Anak itik berenang-renang,","Berenang-renang sampai ke tepi;","Derita orang harus dikenang,","Kesenangan peribadi harus dikongsi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100326,"Bertitik keringat kelihatan di dahi,","Mengejar keindahan si rama-rama;","Sahabat pulang ke pangkuan Ilahi,","Tinggalkan kenangan ketika bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100327,"Masak ranum sijambu batu,","Pohonnya rendang jauh di sana;","Bergabung hati bersama membantu,","Keyakinan sahabat disempurnakan sebaiknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100328,"Melakukan kebaikan tidak kan berakhir,","Itulah bekalan yang dibawa ke sana;","Hati yang mulia nampak terzahir,","Tanpa menunjuk-nunjuk kepada sesiapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100329,"Jarang diguna si gula batu,","Amalan biasa generasi lama;","Usah dihalang yang datang membantu,","Masa sukar dihadapi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100330,"Walaupun harganya tidaklah seberapa,","Amatlah bermakna menjadi kenangan;","Selagi memiliki ibu dan bapa,","Harus dilayani serta dimuliakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100331,"Berpada-padalah memilih hiburan,","Biarlah yang dapat mendidik kalbu;","Berhati-hatilah lakukan pelaburan,","Supaya tidak diperangkap penipu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100332,"Awan mendung meredup suasana,","Matahari terlindung cahaya tersekat;","Persahabatan lama terus terbina,","Hargailah ia sampai ke akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100333,"Pergi berdagang di Pasar Ahad,","Bawa barangan khazanah hutan;","Biar pun orang terus berhasad,","Sentiasa waspada doakan keselamatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100334,"Makan sireh bersama pinang,","Untuk tetamu datang berkunjung;","Kenangan nan lalu usah dikenang,","Nasib yang malang tidak beruntung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100335,"Harus bersabar sifat amarah,","Musnahkan jiwa personaliti berkembang;","Amat bergembira hari bersejarah,","Temui semula tulisan lama yang hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100336,"Laluan pembangunan terpaksalah berpindah,","Hasil negara perlulah dijana;","Penulisan kreatif tidaklah mudah,","Jagalah bersungguh jangan kehilangannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100337,"Tanam keledek di atas batas,","Dijaga selalu cepatlah bercambah;","Semakin pandemik ramai yang terjejas,","Bilangan yang miskin semakin bertambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100338,"Berayun perlahan buai-buaian,","Biarkan sahaja didorong angin;","Saat menikmati kelazatan durian,","Ingatlah derita sifakir dan miskin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100339,"Bila bersendirian usahlah berhiba,","Cari ketenangan di dalam dusun;","Musim buah-buahan sudah pun tiba,","Durian berguguran harga pun turun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100340,"Di usia begini carilah rakan,","Sesekali diaturkan dapat bersua;","Perkara mudah mengapa disukarkan,","Bantulah urusan bahagiakan manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100341,"Digigit kenyal isinya lokan,","Masakan tradisi jadi pusaka;","Kebaikan lalu telah dilakukan,","Ganjaran diterima tanpa disangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100342,"Harus melancong ke kota Wuhan,","Kota moden lagi bestari;","Apabila sakit diuji Tuhan,","Rawatan dan doa harus selari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100343,"Sumbangan orang di sebalik tabir,","Jarang disedari kerana tersorok;","Ungkapan buruk terlahir dibibir,","Dari hati yang sedang membusuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100344,"Burung terbang riang di angkasa,","Burung helang mencari makan;","Tidak bersyukur dengan yang ada,","Jiwa gelisah dengan ketamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100345,"Masih ramai yang hidup melarat,","Negara pesat dengan pembangunan;","Persahabatan sejati tanpa bersyarat,","Sentiasa ada saat diperlukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100346,"Kuda berlari atas pelana,","Hati sayu ingin menangis;","Amanah orang harus disempurna,","Semoga kepercayaan tidak terhakis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100347,"Kuda berlari atas pelana,","Hati sayu ingin menangis;","Amanah orang harus disempurna,","Semoga kepercayaan tidak terhakis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100348,"Rasanya manis buah tembikai,","Musim panas meningkatkan rasa;","Walaupun banyak yang dipertikai,","Dos kedua sudah pun disempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100349,"Lebah bersarang lebah tebuan,","Usah diganggu usah dilibas;","Apabila datang rayuan bantuan,","Usah bertangguh hulur dengan ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100350,"Elakkan wabak duduklah di rumah,","Aktiviti kekeluargaan dapat dilaksana;","Covid19 menzahir banyak hikmah,","Peniaga kecil meningkat bilangannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100351,"Menegak kebenaran bersuara lantang,","Dengan ilmu asas perdebatan;","Biarpun banyak cemuhan yang datang,","Perubahan kebaikan perlu diperjuangkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100352,"Hasad dengki diamalkan jangan,","Tanyalah sendiri apa yang dicari;","Kalau hidup penuh kepura-puraan,","Kegelisahan hati yang kuasai diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100353,"Indah memutih kambing biri-biri,","Padang menghijau sungguh mempesona;","Hati berbahagia dapat memberi,","Amanah dan kepercayaan telah disempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100354,"Kenangan lama bersama rakan,","Bergurau senda berselisih berbalah;","Bukankah Nabi telah mengajarkan,","Lembut tutur kata disukai Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100355,"Emosi terganggu jejaskan kesihatan,","Orang merajuk eloklah dipujuk;","Pelbagai pendapat telah dikemukakan,","Pandangan pakar haruslah dirujuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100356,"Adakalanya perlu berimaginasi,","Untuk jelmakan segala hasrat;","Peningkatan usia menghampiri destinasi,","Perkampungan abadi di alam akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100357,"Fikirkan dahulu sebelum bertindak,","Tidaklah nanti mudah diperdaya;","Sama ada konspirasi atau tidak,","Hadapi wabak dengan sepenuh upaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100358,"Juadah istimewa terubuk masin,","Hadiah dari seorang kawan;","Segeralah dapatkan suntikan vaksin,","Wabak melanda dapat dilawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100359,"Cahaya matahari di celah awan,","Awan tebal berarak tiada;","Pandemik melanda suatu ujian,","Patuhi semua SOP yang disedia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100360,"Sungguh harum buah kuini,","Wangi baunya rasanya lazat;","Mari hargai sumbangan yang murni,","Pengorbanan rasa, peluh dan keringat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100361,"Kalau menebang sipohon jati,","Pohon di Jawa dijadikan papan;","Biarpun terluka di dalam hati,","Lebihlah mulia berikan kemaafan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100362,"Sesekali berlaukkan ikan kering,","Hasil laut tak pernah pupus;","Berita yang tiba harus disaring,","Fitnah disebar dapat dihapus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100363,"Wabak melanda di setiap negeri,","Jangan mudah diambil ringan;","Pemergian ulama menghadapi Ilahi,","Kehilangan besar amat dirasakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100364,"Emosi terkesan peristiwa kelmarin,","Berhari-hari tidak berselera;","Dengan merampas hak orang lain,","Apakah wujud ketenangan di jiwa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100365,"Lemak manis kuih keria,","Asal Melaka juadah harian;","Mari raikan Jumaat yang mulia,","Banyakkan solawat ke atas Junjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100366,"Jiwa peduli harus diimarah,","Susah saudara dapat dirasa;","Sangat perlu mempelajari sejarah,","Untuk perkukuh jati diri bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100367,"Sudah terhidang sebuku roti,","Secawan kopi untuk bersarapan;","Setiap kematian adalah pasti,","Usaha dan ikhtiar jadi kewajipan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100368,"Di air cetek sampan disorong,","Sambil menangkap ikan belida;","Usah ada pandangan yang serong,","Kepada yang dijangkiti virus melanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100369,"Langit gelap diliputi awan,","Awan mendung di tengahari;","Pengambilan vaksin usah diringankan,","Pandemik menular membimbangkan sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100370,"Mi goreng dengan perencah kerang,","Antara juadah di meja makan;","Apabila menerima kebaikan orang,","Hargai dengan memberi kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100371,"Dari Bangi hendak ke Bangsar,","Tersekat pula di Seri Petaling;","Majlis dihadiri hasratnya besar,","Bunga yang mekar hendak disunting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100372,"Untuk berjaya hindari kemalasan,","Tekadkan azam tingkatkan usaha;","Sudah hilangkah rasa kemanusiaan,","Nyawa manusia seperti tiada harga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100373,"Duduk sendiri di pinggir kali,","Angin malam berhembus perlahan;","Yang pergi menghadap Ilahi,","Relakan saja takdir ketentuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100374,"Tujuan hidup lakukan kebaikan,","Bermula keikhlasan di dalam hati;","Jiwa kemanusiaan harus disuburkan,","Hak orang lain perlu dihormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100375,"Berjalan-jalan di pinggir sungai,","Udara segar fikiran pun tenang;","Hati terkilan melihat perangai,","Suka membazir makanan terbuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100376,"Ke dalam hutan pergi berburu,","Kepada sasaran senjata membedek;","Ucapkanlah Selamat Hari Guru,","Kepada insan yang berjasa mendidik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100377,"Isikan hati janganlah kosong,","Sepanjang masa terus belajar;","Mengapa pencabulan terus berlangsung,","Nilai kemanusiaan semakin pudar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100378,"Berwarna warni bunga di lembah,","Embun menitis di awal pagi;","Suasana raya sudah berubah,","Ucapan dizahir menerusi teknologi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100379,"Malam cerah bulan purnama,","Langit membiru tidak berawan;","Eidul Fitri telah pun menjelma,","Kembali fitrah sebaik-baik insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100380,"Keris pusaka indah berukir,","Jadi kebanggaan para pahlawan;","Meski hari ini semakin berakhir,","Pendidikan Ramadhan harus diteruskan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100381,"Semasa pandemik tiada pengunjung,","Ramai peniaga menanggung rugi;","Ramadhan semakin berada di penghujung,","Hati sayu dapatkah temuinya lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100382,"Setiap hari mencipta pantun,","Pantun dicipta melestari tradisi;","Berbicara ramah bersopan santun,","Tentulah pelanggan berkunjung lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100383,"Mencari juadah di Kampung Medan,","Juadah berbuka keluarga di rumah;","Memperbanyak ibadah di bulan Ramadhan,","Semoga seterusnya lebih beristiqomah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100384,"Panas terik sungguh menyengat,","Menjelang petang berawan mendung;","Hidup hendaklah sentiasa beringat,","Hindari sifat suka berbohong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100385,"Buah mengkudu banyak khasiat,","Saintis mengkaji mencari bukti;","Kes Covid19 terus meningkat,","Tiap pergerakan terus berhati-hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100386,"Beratur kereta di tengah kota,","Jalan sesak di panas terik;","Mari maafkan padamlah sengketa,","Rapatkan kembali hubungan tercarik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100387,"Bulan Ramadhan semakin berakhir,","Hati sedih bagai kehilangan;","Lakukan kebaikan pahala mengalir,","Kehidupan kemudian beroleh kebahagiaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100388,"Rasa teruja makan pepahat,","Memanglah enak menambah selera;","Keputusan kerajaan telah pun dibuat,","Kita redha untuk kemaslahatan semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100389,"Hujan turun setempat setempat,","Rumput segar belalang berlompatan;","Petugas dan awam bekerja rapat,","Wabak melanda dapat dilawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100390,"Senja hari terasa dingin,","Hujan turun seketika sahaja;","Puasa melatih diri memimpin,","Ketulusan hati segalanya terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100391,"Menanti dapat merentas negeri,","Kalaulah boleh menjelang raya;","Sumbangan pekerja kita hargai,","Amanah dan bakti membina negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100392,"Berhati-hati berada di sempadan,","Buruk dan baik ada di sana;","Santuni generasi muda dengan teladan,","Dapat dimanfaat di hari muka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100393,"Hujan lebat menjelang petang,","Kasihan melihat peniaga di bazar;","Jangan diabai jika berhutang,","Sampai masanya wajib dibayar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100394,"Bulan mengambang indah sekali,","Burung pungguk tenang di dahan;","Hidupkan budaya saling peduli,","Tiadalah sesiapa akan dipersiakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100395,"Bulan penuh pertengahan Ramadhan,","Keindahan tercipta alam semesta;","Jiwa miskin mengejar keduniaan,","Tidak bersyukur apa yang ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100396,"Ramadhan bulan penuh barokah,","Ibadah dijalani dengan kesyukuran;","Usah diturut nafsu serakah,","Didiklah hati penuh ketundukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100397,"Walaupun jauh bertanyalah khabar,","Semoga diperteguh tali persaudaraan;","Ramadhan mendidik kita bersabar,","Janganlah mudah timbul kemarahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100398,"Berbuka dengan buah dan nasi,","Cukuplah untuk keperluan kesihatan;","Santuni Pondok Unais sungguh menginspirasi,","Budaya berwaqaf mengukuh pendidikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100399,"Rotan disiat dibuat lukah,","Menahan lukah di hujung saliran;","Tunaikan zakat, infaq dan sedekah,","Pilihan terbaik di bulan Ramadhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100400,"Burung camar terbang melayah,","Sambil berterbangan siul bersahutan;","Pada sahabat dikirim takziah,","Semoga tabah menghadapi ujian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100401,"Sentiasa segak berpakaian kemas,","Ramah bersapa sering melambai;","Pada usia berwarga emas,","Kesihatan gigi usah diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100402,"Sukar mencantas kelapa setandan,","Kelapa yang muda enak isinya;","Saling memberi di bulan Ramadhan,","Fakir dan miskin sentiasa diutama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100403,"Seluruh penduduk berketurunan Jawa,","Lembut dan santun dalam pertuturan;","Laksanakan tanggungjawab sepenuh jiwa,","Asas kejayaan dalam pekerjaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100404,"Rasa terhibur mendengar cerita,","Cerita lama kembali diingatan;","Nasihat menasihati budaya kita,","Usah tersentap apabila diberi teguran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100405,"Niat yang baik datangkan keberkatan,","Tuhan tidak persia segala bakti;","Kesakitan menginsafkan tentang kesihatan,","Permasalahan membentuk kekuatan peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100406,"Kaya negara dengan flora dan fauna,","Di rantau masyhur di dunia terkenal;","Asasnya kebangsaan martabatnya mendunia,","Berakal nasional, berakal internasional.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100407,"Tanjung Puteri tempat bersejarah,","Asal disitu pusat pemerintahan;","Setengah abad kegemilangan terserlah,","UKM dipacu menggapai harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100408,"Pujangga Melayu melakar madah,","Madah penuh berhias kiasan;","Jubli Emas UKM berlalu sudah,","Sambutan 51 tahun kini diraikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100409,"Buah langsat buah rambai,","Buahnya kuning berbiji-biji;","Nikmat kesihatan usah diabai,","Apabila kehilangan amatlah rugi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100410,"Banyak haiwan dalam belukar,","Kala jengking, ular dan lipan;","Di saat kehidupan semakin sukar,","Bantulah membeli produk yang diniagakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100411,"Menjelang pagi udaranya nyaman,","Sambil menatap embun di titi;","Syukur Ilahi yang beri kesempatan,","Menikmati Ramadhan mengabdi diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100412,"Masih tersekat hendak berperantauan,","Rancangan mengembara lagi tertunda;","Walaupun berselisih jagalah kesantunan,","Apatah lagi hormati yang lebih tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100413,"Dapat kelihatan tangan yang luka,","Luka dihati dirawat sendirian;","Takdir diterima dengan terbuka,","Kehidupan yang tinggal usah disiakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100414,"Pergi berburu di dalam hutan,","Berhati-hati siap siaga;","Patuhi sahaja prosedur kesihatan,","Diri dan orang lain semoga terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100415,"Ikan haruan menyambar umpan,","Sangkut kail di tepi sungai;","Nak diri jangan berjangkitan,","Ikutlah nasihat jarakkan. diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100416,"Daun terbang melayang layang,","Jatuh bertabur di atas jeti;","Kalau dah sayang mengapa nak di buang,","Betulkan niat perbaiki diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100417,"Burung serindit di dalam perahu,","Bunga bayam di dalam dulang;","Hati adik merasa rindu,","Kerana berjauhan dengan DJ Aniesma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100418,"Membelah buah kepayang muda,","Dimakan mabuk dibuang kepalang;","Hendak ditelan mati ayahanda,","Mau diluah bonda menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100419,"Harum bau nya bunga melati,","Dibawa orang ketanah jawa;","Lain dimulut lain dihati,","Aku ditipu dalam ketawa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100420,"Burung layang-layang,","Hinggap rumpun padi;","Dihati kata sayang,","Dimulut kata benci.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100421,"Burung serindit di atas dahan,","Dahan pokok buah pulasan;","Andai tindakan tiada pilihan,","Sukar memilih membuat keputusan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100422,"Beras ditumbuk si padi layang,","Habis lencun hujannya lebat;","Dimakan mabuk dicampak sayang,","Dialah racun, dialah pengubat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100423,"Memahat papan jadi almari,","Hati-hati cedera tangan;","Gantung harapan pada Ilahi,","Terang jalan jelas pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100424,"Senjakala bermain layang-layang,","Jatuh pula dalam perigi;","Nak buang terasa sayang,","Bagilah peluang sekali lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100425,"Ke pulau meniti memetik laici,","Bersama pauh hiris dimayang;","Walau di hati berdetik benci,","Bila menjauh terasa sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100426,"Sengkuang calit dipercik kicap,","Piring berhabuk sipi berbayang;","Terlalu sulit untuk ku ucap,","Ditelan mabuk dibuang sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100427,"Ke rawa ceri petik seraga,","Bawa ke rumah jamu tetamu;","Benci terperi tidak terhingga,","Namun berhikmah jodoh bertemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100428,"Masam-masam si buah menia,","Dicicah orang secubit garam;","Beginilah adat hidup di dunia,","Ujian bertamu bermacam ragam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100429,"Terasa janggal bawa pedati,","Naik berdua janganlah lalai;","Hendak kutinggal tak sampai hati,","Hendak kubawa sayap terkulai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100430,"Radio klasik menggamit kenangan,","Sajian pagi menghibur diri;","Gunakan akal untuk pertimbangan,","Membuat keputusan bijak bestari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100431,"Alang-alang pekasam diseluk,","Biarlah sampai kepangkal lengan;","Alang-alang bantal ku peluk,","Biar lena dlam pelukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100432,"Kepekan baru membeli ragi,","Singgah berehat dipohon kepayang;","Kasih tidak berbelah bagi,","Tua dan mudah sama disayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100433,"Mencari rata pohon pedada,","Kayu meranti dikerat-kerat;","Sayang dan cinta berpada-pada,","Berhati-hati elak mudarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100434,"Sarapan pagi goreng sukun,","Dihidangkan secawan kopi;","Lagu termahal gadis dusun,","Wanita tercantik ramai meminati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100435,"Senja kala menarah kayu,","Meraut rotan buat ikatan;","Apa disayang benda dah layu,","Galang ganti yang berkekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100436,"Pagi hari pergi menjala,","Nasi ulam bekal dimakan;","Lupa diri lupa segala,","Rasa gelora usah turutkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100437,"Masuk belukar bawa bekalan,","Sayur betik dan kuih bahulu;","Kalau sukar mencari jalan,","Buruk dan baik fikirkan dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100438,"Hasil tanaman padi melinja,","Padi menguning luasnya nampak;","Resam dunia memang bersaja,","Ditelan mati mak diluah mati bapak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100439,"Jika makan buah nangka,","Getah dikulit terkena pasti;","Usah suka berburuk sangka,","Biar sentiasa bersih di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100440,"Merbuk terbang terkena jerat,","Dalam sangkar diatas para;","Mabuk Kepayang tetap ku minat,","Ahmad Jais empunya suara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100441,"Ambil lada mari tumbukkan,","Lada ditumbuk bersama pala;","kepayang muda usah dimakan,","kelak mabuk pening kepala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100442,"Sayang-sayang si buah kepayang,","Buah kepayang hendak dimakan;","Manusia hanya boleh merancang,","Kuasa Allah S.W.T jua menentukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100443,"Layang-layang terbang melayang,","Terbang melangit tak nampak bayang;","Sayang-sayang buah kepayang,","Ditelan pahit dibuang sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100444,"Waktu pagi makan bahulu,","Sedap diminum bersama kopi;","Di sana menanti di sini menunggu,","Serah kepada takdir Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100445,"Anak seluang disambar helang,","Jatuh ke jalan terkena duri;","Hendak kubuang terasa sayang,","Andai kutelan mabuk sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100446,"Sayang-sayang buah kepayang,","Disimpan Ibu dalam tempayan;","Kita manusia hanya merancang,","Allah jua yang menentukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100447,"Terbang tinggi burung helang,","Lalu hinggap dahan jati;","Perlu kita berkasih sayang,","Bagai ikatan bersimpul mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100448,"Dari pulau pinang merantau ke kedah,","Untung nasib si anak dagang;","Orang tak sayang fikir yg mudah,","Semua tak kekal andai dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100449,"Daun kering jatuh melayang,","Jatuh pula atas titi;","Bila dah rasa sayang,","Kasih setia dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100450,"Tanam bunga di tepi halaman,","Mawar merah kembang mewangi;","Memang susah mencari teman,","Yang setia dan sayang menyayangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100451,"Mengutip lokan apa pun tak nampak,","Di pokok sena seorang diri;","Usah dimakan usah dicampak,","mungkin berguna kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100452,"Buah kepayang sembunyi di laci,","Diambil buang tidak dimakan;","Masih sayang melebihi benci,","Berilah ruang sama betulkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100453,"Terbang nuri kejar ditangkap,","Rasa gemuruh dihati anih;","Insaflah diri perbaiki silap,","Buang keruh amibil yang jernih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100454,"Cuaca cerah tak berawan,","Indah sungguh waktu pagi;","Duduk memikirkan jawapan,","Semua urusan tidak menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100455,"Sayang sayang buah kepayang,","Sayang lagi si buah delima;","Hati sudah terpaut sayang,","Harap kasih kan kekal lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100456,"Daun habuk terbang melayang,","Sembunyi balam di tepi kuari;","Dimakan mabuk dibuang sayang,","Simpan semalam fikirlah sehari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100457,"Masak di dahan buah berangan,","Tempat merpati menyepi diri;","Usah dimakan dibuang jangan,","Simpan di hati jadi memori.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100458,"Buah kepayang buah rotan,","Bawa ke lubuk bersama nyoya;","Walaupun sayang jangan dimakan,","Kelak mabuk tiada ubatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100459,"Berehat sambil menganyam ketupat,","Daun yang lama tiada berguna;","Buat pilihan biarlah tepat,","Menyesal kemudian tiada gunanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100460,"Bunga selasih daun mengkuang,","Buah kepayang pohon mengkudu;","Kemana kasih hendak ku buang,","Bila sayang bercampur rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100461,"Hujung dahan si bunga sena,","Tumbuh melata berbunga indah;","Patuhi arahan dengan sempurna,","Jangan kita ambil mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100462,"Wajah dikenang mata berbayang,","Di dekat malu dikata perasan;","Hendak dibuang terasa sayang,","Berfikiir dulu buat keputusan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100463,"Burung merbah terbang melayang,","Terbang dikejar si burung helang;","Sayang-sayang semakin Sayang,","Dimakan mabuk dibuang sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100464,"Bunga kudup di hujung dahan,","Di tepi semak buruk kelapa;","Susah hidup membuat pilihan,","Ditelan mati emak diluah mati bapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100465,"Turun ke sawah hari Jumaat,","Cangkul di sandang sambil mengaduh;","Jika buah tidak bermanfaat,","Pokoknya rendang buat berteduh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100466,"Turun ke sawah bergurau senda,","Beras ditumbuk si padi layang;","Membelah buah kepayang muda,","Dimakan mabuk dicampak sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100467,"Burung tempua terbang tinggi,","Hinggap di dahan pokok mati;","PPE dipakai setiap hari,","Hindari covid pembunuh tersembunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100468,"Cenderai putih di makan kuda,","Dalam lautan jangan diduga;","Bercerai kasih talak tiada,","Seribu tahun teringat juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100469,"Bunga selasih di bendang huma,","Pohon mengkuang sipohon mengkudu;","Umpama kasih semaian sukma,","Nostalgia sayang mengulit rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100470,"Kilauan teratai cantek ditasik,","Harum bunga disukai lebah;","Apakala pkp diri terjarak,","Merindu jauh hati gelisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100471,"Terbang sejoli burung merpati,","Cantik berseri ditaman indah;","Biar rindu berpaut lagi,","Walau tahun berganti sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100472,"Bila dekat kurasa sayang,","Bila jauh ku rasa rindu;","Kasihmu umpama bayang-bayang,","Ingin ku gapai tapi tak mampu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100473,"Tika memetik daun pegaga,","Jangan di petik daun sembilu;","Bila jauh rindu membara,","Sudah di sisi kelu membisu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100474,"Hinggap rerama di atas riba,","Diberi madu di atas kain;","Kutunggu lama tak kunjung tiba,","Rupanya rindu pada yang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100475,"Burung melayang burung nuri,","Hingga sebentar di pohon kenari;","Kasih sayangku amat murni,","Seperti embun di pagi hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100476,"Masaknya padi elok rupanya,","Daunnya melepai akan layu;","Sudah menjadi resam dunia,","Rinduku sampai melalui lagu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100477,"Makan nasi ulam mengkudu,","Cicah sambal rebus tenggiri;","Tak tahan dah menanggung rindu,","Bila lah boleh merentas negeri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100478,"Mudik seberang Tanjung Jati,","Nampak hutan pergi segera;","Rindu bersarang lama menanti,","Tamat sekatan berjumpa mesra.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100479,"Air selasih bawa kedepan,","Masak kerang buat hidangan;","Mesra kasih ramuan kehidupan,","Rindu bersarang kejauhan pandangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100480,"Bunga rambutan mencelah sekuntum,","Anak ayam cantik berseri;","Bertaut ingatan mesra bercantum,","Rindu dianyam dalam nubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100481,"Buah duku buah rambutan,","Mari dijual dipasar tani;","Rindu saya pada puan,","Sentiasa menanti setiap hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100482,"Buah Kandih si asam jawa,","Buah Kundang dicicah gula;","Ikatan kasih gurindamnya jiwa,","Terlalu dikenang boleh gila.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100483,"Burung tempua terbangnya rendah,","Makan padi berkawan-kawan;","Kasih sayang ikatan ukhwah,","Semurni kasih Nabi junjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100484,"Rahimah kasihnya tanpa Rahim,","Sayang pautan sendu di jiwa;","Ingatan kasih jalinan silaturrahim,","Kenangan ingatan selagi bernyawa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100485,"Kain batik kain pelikat,","Dara manis buat selendang;","Bila rindu sudah tersemat,","Semakin jauh semakin sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100486,"Bujang dara tertawa girang,","Tumbuh subur si buah rambai;","Bukan hamba tak rindu sayang,","Apakan daya tangan tak sampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100487,"Beringin di hujung pulau,","Lautan Sayang tedung kumbang;","Ingin hati memandang pulau,","Ada sampan pengayuh tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100488,"Anak haruan tidak terluang,","Benang sutera di dalam buluh;","Hendak buang tidak terbuang,","Sudah mesra di dalam tubuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100489,"Sungguh indah bunga Cempaka,","Indahnya hidup ciptaan Ilahi;","Apa guna nya kita dilupa,","Tetap ingat sampai berhari-hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100490,"Jala ditebar menangkap haruan,","Tebar ditasik atas jambatan;","Bertanya khabar walaupun berjauhan,","Tandanya kasih tetap bertautan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100491,"Sigai bersisih dijalin kemas,","Buat pelindung panas di beranda;","Bagai kasih rantaiannya emas,","Dihias kalung menghias dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100492,"Cicah asam jambu batu,","Daun gugur jatuh melayang;","Walau jauh beribu batu,","Dalam hati tetap sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100493,"Sungguh kuat dahan meranti,","Tidak sama dahan mengkudu;","Sejenak tak nampak resah di hati,","Lama tak jumpa menahan rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100494,"Mudik sampan hanyut kehulu,","Bersaksikan pula sarang tempua;","Ku titipkan doa diangin lalu,","Moga rindu terubat jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100495,"Rindu rinduan kenang kenangan,","Diulit mimpi dibuai rindu;","Walaupun jauh sentiasa terkenang,","Itulah jasamu wahai ibu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100496,"Pergi memburu di dalam hutan,","Dapat seekor rusa betina;","Kasih dirindu sentiasa dalam ingatan,","Makan tak kenyang tidur tak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100497,"Mekar indah si bunga selasih,","Tanam di pasu dilaman mamu;","Sungguhpun jauh merindu kasih,","Titipkan doa pengganti rindumu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100498,"Bercucuk tanam dibumi Allah,","Hasil dipungut untuk santapan;","Disimpan rindu biar ku mengalah,","Memori yang tinggal buat ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100499,"Mekar bunga dipagi hari,","banyak kumbang terbang datang;","Rasa rindu simpan dihati,","Mengenang si anak dirantau orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100500,"Waktu petang duduk berehat,","Duduk berehat dalam perahu;","Dipandang mata tidak terlihat,","Sedih dihati siapa yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100501,"Apa disuka bunga dihutan,","Bunga ditaman hampir layu;","Lama berkasih hanya ingatan,","Pergi hembus bersama bayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100502,"Warna terang jalan di pandu,","Berhati hati di jalan raya;","Walaupun jauh beribu batu,","Kenangan lama terbayang dimata.", 4, 6);
