INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100589,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Doa dipohon kepadamu,","Tuhan Semoga kehidupan kembali gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100590,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Rindu padamu tidak keruan,","Bila la wabak ini akan hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100591,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Jujur dan setia bila berkawan,","Jangan diundang marahnya orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100592,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Jika gemar belanja kawan,","Ikhlas teruja bukan kepalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100593,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Berhati hati senda dalam gurauan,","Risau nanti kawannya hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100594,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Dimanakah duhai kawan kawan,","Lama sudah awak menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100595,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Telah ku lamar anak Tok Wan,","Persis kemboja cantik terbilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100596,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Makanan kugemar kulat cendawan,","Goreng saja-saja enak terbilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100597,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Teruskan melamar dalam kedermawan,","Hati teruja dalam kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100598,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Rendahkan diri bila berkawan,","Rajin berusaha ringankan tulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100599,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Hati gembira bersama kawan,","Rasa bosan pun hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100600,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Bagaikan pungguk merindukan bulan,","Mengharapkan kasih yang tak kunjung datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100601,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Hilang rezeki hilang kawan,","Teruskan usaha jangan kecundang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100602,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Hati-hati mencari kawan,","Bimbang terkena si hidung belang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100603,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Setia lah kita bila berkawan,","Budi bahasa jangan hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100604,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Kalau melamar aduhai Tuan,","Hantaran belanja dibawa berdulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100605,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Pilu hati tidak tertahan,","Terkenang dinda jauh seberang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100606,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Ramai sahabat teman dan kawan,","Kasih ku tetap padamu sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100607,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Ingin kulamar dirimu puan,","Apakan daya nasibku malang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100608,"Terbang camar tinggi di Awan,","Kicau senja mengajak pulang;","Luhur hati ikhlas kemaafan,","Itu tandanya ingatan tidak hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100609,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Kasihmu samar hatiku rawan,","Mungkinkah cinta dihatimu hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100610,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Usah dicemar kasihku tuan,","Setelah dipuja mengapa menghilang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100611,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Hendak melamar sunti rupawan,","Apakan daya banyak menghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100612,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Setia dan jujur itu namanya kawan,","Biar jauh tetap kawannya takkan hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100613,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Hendakku lamar tidak terlawan,","Tengoklah saja didalam walang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100614,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Hati gemar mengajak kawan,","Bergurau senda bosan hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100615,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Ramai sahabat ramai kawan,","Istri dirumah tetap abang sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100616,"Terbang camar tinggi diawan,","Kicau senja mengajak pulang;","Ingin ku lamar duhai perawan,","Bilang sahaja hantaran dulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100617,"Terbang camar tinggi di awan,","Kicau senja mengajak pulang;","Rindu pada mu hati ku rawan,","Hingga jasad ku tinggal tulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100618,"Akademi Pengajian Melayu di gelar APM,","Bertahta di perbukitan Universiti Malaya;","Selamat berjuang anak-anak SPM,","Setelah tekun berusaha semoga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100619,"Pagi minggu pergi pekan,","Untuk mencari buah kurma;","Rajin dan usaha tangga kejayaan,","Doa dan tawakkal itu yg utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100620,"Tanam halia ditepi paya,","Sahabat berdua hidup sepakat;","Tekun dan usaha untuk berjaya,","Diiring doa memohon berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100621,"Teratak kayu mahligai batu,","Kaki melangkah jangan teragak;","Akal dan hati berpadu satu,","Jawapan padu jangan teragak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100622,"Berenang bebas si ikan kelah,","Di bawah titi ikan tengalan;","Tariklah nafas baca basmallah,","Tenangkan hati buka soalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100623,"Rasa hati rawan timbul kehibaan,","Tanggung dugaan sedih teramat;","Di sini Dewan di sana pekerjaan,","Hidup diteruskan hinggalah tamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100624,"Mulakan langkah dengan bismillah,","Ke dewan periksa dengan hati yang cekal;","Kepada Allah kita berserah,","Penuh yakin serta tawakkal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100625,"Malu itu permainan perasaan,","Virus covid mudah tersebar;","Hadapi pula musim peperiksaan,","Dalam yakin penuh berdebar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100626,"Kalau pergi ke tanjung batu,","Kirimkan saya sebatang lilin;","Kita bangsa bersatu padu,","Siraturahmi sentiasa terjalin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100627,"Hati-hati masuk ke dewan,","Kaki kanan melangka dahulu;","Hari pertama menduduki peperiksaan,","Jangan bimbang doa ibu bersama mu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100628,"Air kolah melalui terusan,","Dengan ihsan kendalian cik nyonya;","Semoga Allah permudahkan urusan,","Menjawab peperiksaan dengan jayanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100629,"Pergi ke laut memancing ikan,","Ikan dibawa lalu paya;","Kami disini sentiasa mendoakan,","Semoga adik-adik terus berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100630,"Emas berkarat indah ketara,","Pakai di tangan ramai terpikat;","Ilmu ibarat kilau mutiara,","Tuntutlah dengan penuh semangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100631,"Buah markisa di Pekan Baru,","Elok dibelah jus disukat;","Hadir periksa di norma baru,","Mulakan bismillah serta selawat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100632,"Pergi ke sungai mengail seluang,","Dapat seekor beri ke Nyonya;","Jangan kalah sebelum berjuang,","Semoga kalian berjaya semuanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100633,"Vaksin ditempah bukannya murah,","Dai jauh dibawa ke sini;","Jangan degil jangan membantah,","Ikut sahaja suara majoriti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100634,"Anak murai di atas dahan,","Sambil diintai anak si Leha;","Anak- anak mengambil peperiksaan,","Moga dipermudahkan segala usaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100635,"Saban malam tidurnya lewat,","Buku, komputer dan ilmu melekat;","Semangat juang bertambah kuat,","Doa ibunda mendapat berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100636,"Kemana hinggap burung tempua,","Singgah bertapa ditepi lalang;","Selamat berjuang adikku semua,","Semoga berjaya dengan ccemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100637,"Dari minda ke mata pena,","Luahkan rasa yang tersirat;","Semuga berjaya adik di sana,","Capai impian yang di hasrat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100638,"Nampak Cikgu jangan lari,","Buat seperti adik beradik;","Doa tersemat didalam hati,","Setiap usaha pencapaian terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100639,"Menghulur galah di dalam marah,","Letih meramu pucuk peria;","Kepada Allah kita berserah,","Senjata ilmu siap sedia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100640,"Gadis ayu sungguh pemalu,","Bunga di kepala jadi hiasan;","Terkenang aku satu waktu dahulu,","Gigih berjuang berhabis-habisan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100641,"Hitam sungguh buah senduduk,","Masak ranum tepi gua;","Jalani peperiksaan di hujung tanduk,","Didoakan kejayaan dimiliki jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100642,"Sarapan pagi nasi lemak hijau,","Makan dengan sambal hitam Pahang;","Jawab soalan jangan gelojoh menggagau,","Tenangkan hati jawab dengan tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100643,"Sarang Tempua bergayut rendah,","Bergayutan dicelah pohonnya mangga;","Belajar sudah menghafalnya pun sudah,","Doa dan tawakal semoga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100644,"Nangka mengkal dibuat pengat,","Makan bersama roti sekaya;","Segala persiapan telah dibuat,","Iringi doa semoga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100645,"Pergi muara kayuh sampan,","Tengah Kuala dayung patah;","Jawap gopoh-gapah jawap soalan,","Tenang hati berserta doa kepada Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100646,"Tali di ikat biar tersimpol,","Semoga ia tak lagi terhurai;","Doa selalu semoga di makbul,","Malaysiaku rakyatnya hidup sejahtera dan damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100647,"Buah kelubi buah perah,","Mana sama si asam paya;","Berdoa dulu baru berserah,","Andai berusaha pasti berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100648,"Pergi sekolah berlatih sukan,","Sampah dibuang padang dibersihkan;","Mohon agar Allah permudahkan,","Selamat berjuang dalam peperiksaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100649,"Pokok delima di kebun peria,","Akar tuba pokok gelenggang;","Sudah lama belajar sedia,","SPM tiba masuk gelanggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100650,"Kura kura jalan perlahan lahan,","Akhirnya sampai walau pun sukar;","Tangan ditadah mengadap tuhan,","Moga segalanya berjalan lancar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100651,"Jambu batu di atas dahan,","Di buat sekaya untuk sarapan;","Doa restu kami titipkan,","Semoga berjaya dalam peperiksaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100652,"Tun perak bijak berakal,","Bendahara terbilang sepanjang zaman;","Sudah berusaha harus lah tawakal,","Agar kejayaan dalam genggaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100653,"Bunga dihias atas meja,","Rumah cantik beseri seri;","Rajin usaha pasti berjaya,","RezekI datang dari illahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100654,"Leka berjalan di atas titi,","Lantainya papan baru dibelah;","Jawab soalan berhati-hati,","Serah jawapan kepada Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100655,"Malu itu permainan perasaan,","Itu virus mudah tersebar;","Hadapi pula musim peperiksaan,","Dalam yakin penuh berdebar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100656,"Wajah nan manis sungguh berseri,","Berbudi bahasa sopan orangnya;","Tenang menjawap soalan,","diberi Doa diiring semoga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100657,"Di tepi kolah dilonggok pala,","Petik di desa di Pulau Pinang;","Semoga Allah permudah segala,","Masuk periksa hatinya tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100658,"Tumbuh cendawan merata rata,","Di rumput hijau di tepi jalan;","Ilmu di cari bagai permata,","Berusaha gigih membawa kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100659,"Buah durian penuh berduri,","Bagai raja ia di puja;","Tuah ayam nampak di taji,","Tuah manusia kena berusaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100660,"Buah markisa di ujana orang,","Dipetik datuk dari Kangar;","Musim periksa sekolah tenang,","Suara batuk berbatu pun dengar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100661,"Daun ciku daun selasih,","Ubi merah dipotong dimakan;","Untuk cikgu terima kasih,","Ibu ayah tolong doakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100662,"Carik rotan terluka ditangan,","Batang rotan dikerat kerat;","Teruskan usaha dan perjuangan,","Semoga berjaya dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100663,"Kuih keria enak dimakan,","Bersama mi dan roti kaya;","Pelbagai cara telah diusahakan,","Harapan kami semoga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100664,"Buah kuini atas bangku,","Pohonnya rendang buahnya berguguran;","Tahun ini penuh liku,","Moga tenang hadapi peperiksaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100665,"Air mengalir sampai kuala,","Banyak batu berhati hati,","Tabah kan hati menempuh segala,","Cekal kuat tahan di uji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100666,"Dari desa menuja ke kota,","Mencari rezeki ubah kehidupan;","Semoga perjuangan diringi doa,","Setiap usaha ada kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100667,"Daun palas dijeling jangan,","Seraga papaya letak di dulang;","Doa ikhlas mengiring perjuangan,","Semoga berjaya dengan cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100668,"Hujan petang basahan diperah,","Atap bertebar biarkan sahaja;","Dugaan datang jangan menyerah,","Tetap sabar teruskan usaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100669,"Sukan di padang berlari kuat,","Rakan bersorak bersuka ria;","Segala persediaan telah dibuat,","Doa dan tawakal selepas berusaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100670,"Dari minda ke mata pena,","Segaris doa kami rangkakan;","Semoga berjaya adik di sana,","Masa depan dalam perjuangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100671,"Sampan didayung di tengahari,","Terasa penat bercampur malas;","Sopan berbudi pakaian diri,","Berjasa jangan diharap balas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100672,"Kalau kita berkata-kata,","Jangan sampai orang terasa;","Amat indah andainya kita,","Punya harta dan berbudi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100673,"Kain sari buatan tangan,","Dipakai gadis bawa bersiar;","Mudah mencari gadis rupawan,","Bersopan santun itu yang sukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100674,"Banyak lada bermacam lada,","Sungguh pedas si lada cili;","Atur kata sebelum bicara,","Cantik bicara bersinar lah budi.", 4, 6);
