INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100759,"Kain lusuh untuk derma,","Anak dara makan mi;","Musnah musuh tanggungjawab bersama,","Kembalikan negara tingkat ekonomi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100760,"Tanam herba hidup senang,","Usah berkira rezeki berkat;","Musuh dituba negara tenang,","Rakyat gembira ekonomi meningkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100761,"Mohon doa umat yang solih,","Kuatkan semangat berdaya tahan;","Usah kita berdolakdalih,","Patuhi teguran turuti arahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100762,"Putih-putih melati,","Merah-merah delima;","Senyum anisma manis sekali,","Bersuara ceria berkumandang diudara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100763,"Ikan dikolam hendak dituba,","Bawa bersama kuih bahulu Vaksin;","Ditunggu sudah pun tiba,","Pulihkan kehidupan seperti dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100764,"Di muara Padang banyak buaya,","Buaya timbul manusia lari;","Virus Corona sungguh berbahaya,","Mari bersama menjaga diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100765,"Hendak membuat kuih bauhulu,","Pukul telur jangan melimpah;","Semoga kita seperti dahulu,","Hidup ceria dan meriah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100766,"Ke rumah kak Leha makan juadah,","Sambil bersanding di pelamin berbunga;","Semoga usaha kerajaan dipermudah,","Kita berganding bahu membenterasnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100767,"Bau harum si bunga kantan,","Rasa masin kuahnya laksa;","Kalau jarum jadi igauan,","Kenapa vaksin menjadi mangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100768,"Kayu saberkas jadi saikat,","Buat memasak beras perang;","Suntik vaksin ada menafaat,","Virus lenyap negara girang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100769,"Penambah selera si telur masin,","Makan berulam daun pegaga;","Teruskan usaha mempromosi vaksin,","Sehingga beroleh kepercayaan warga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100770,"Menyusun bata lenguhnya tangan,","Tertindih papan tercucuk semalu,","Bersama kita memberi sokongan,","Pulihkan kehidupan seperti dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100771,"Bunga di taman hendak dipetik,","Ada penyengat jadi penghalang;","Akur saranan vaksin disuntik,","Agar rakyat kembali tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100772,"Buah berangan petik di tangkai,","Tidak berduri bertahan lama;","Beri sokongan usah pertikai,","Vaksin diberi secara percuma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100773,"Tinggi lampai pokok bidara,","Kolam sepat di kebun nyonya;","Vaksin sampai daftar segera,","Supaya cepat dapat manfaatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100774,"Vaksin ditempah bukannya murah,","Ikut lah nasihat kerajaan kita;","Berusaha dulu jangan menyerah,","Demi kebaikan kita semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100775,"Manis betik nikmat disyukur,","Diri diurus indah dilihat;","Vaksin disuntik sokonglah akur,","Virus dihapus kembali sihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100776,"Singgah ke pekan beli bahulu,","Bahulu Semarak paling diminat;","Kita rindukan hidup dahulu,","Bebas bergerak sihat walafiat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100777,"Angan nak ke Melaka,","Banyak pula bawa muatan;","Hari ni jgn berduka;","Cucuk vaksin untuk jaga kesihatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100778,"Pohon berduri di hujung gua,","Ramunia jati di tepi sumur;","Lindung diri lindung semua,","Dunia pasti kembali makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100779,"Bebatu ampuh letak di hutan,","Kukuh berdahan meranti paya;","Bersatu teguh elak jangkitan,","Patuh arahan pasti berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100780,"Jalan licin ditempuh gagah,","Berbatu kecil campak di hutan;","Diberi vaksin untuk mencegah,","Perlu diambil elak jangkitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100781,"Pokok kemangi di tanah rendah,","Pokok bidara si pokok kuini;","Vaksin ditemui Allah permudah,","Semoga negara kembali harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100782,"Telur asin nasik kerabu,","Campur bersama ulam ulaman;","Vaksin tiba rasa terharu,","Perihatin untuk rakyat dari kerajaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100783,"Pergi ke bandar berjalan-jalan,","Dapat membeli sehelai baju;","Norma baharu jadi amalan,","Kebiasaan yang lalu tetap dirindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100784,"Zakat dan infak diutamakan,","Ibadat fardu dan sunat ditingkatkan;","Ukhuwah dipelihara perselisihan dihapuskan,","Mendapat redha dan ihsan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100785,"Ramadan bulan berkat,","Penuhi dengan amal ibadat;","Elakkan keji dan umpat,","Kasihi diri kasihi umat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100786,"Gulai tempoyak ikan patin,","Memilih ikan bukan sebarangan;","Terimalah arahan dan aturan kuarantin,","Kesihatan diri dan orang diutamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100787,"Hati gembira bertemu rakan,","Saling meluahkan tersimpan dihati;","Majlis ilmu perlu disuburkan,","Itulah tradisi di sebuah universiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100788,"Usah terperangkap dendam kesumat,","Hanya merosakkan hati sanubari;","Tidak disangka banyaknya maklumat,","Semuanya bermanfaat mencetus inspirasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100789,"Duku langsat di Pulau Kati,","Di tebing sungai subur bertumbuh;","Usah dicurigai yang dijangkiti,","Berikan semangat agar segera menyembuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100790,"Malam cerah gemerlap bintang,","Meresap ke jiwa duka terubat;","Meskipun banyak cabaran mendatang,","Ujian Tuhan mengangkat martabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100791,"Awan terukir berbaris-baris,","Di langit cerah biru menila;","Biarpun hati telah terguris,","Dapat memaafkan itu yang termulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100792,"Semakin berusia banyaklah perkiraan,","Hidup kita bukan main-main;","Semua orang nantikan persaraan,","Untuk berbakti dengan bentuk yang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100793,"Ikan yang lazat namanya ketutu,","Ambil seekor di dalam raga;","Anak yatim yang dahulu dibantu,","Kini dewasa dan berumahtangga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100794,"Dalam pertikaian usah menyebelahi,","Damaikan hati yang sedang berbalah;","Rezeki melimpah Allah anugerahi,","Syukuri dengan terus bersedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100795,"Sejarah manusia terus berputaran,","Membentang ibrah bukan kepalang;","Menjejaki semula tanah kelahiran,","Sekian lama telah terhalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100796,"Berbau harum buah kuini,","Tidak tumbuh dimana-mana;","Yang lebih tua terus menyantuni,","Generasi muda usah malu bertanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100797,"Ayam di hutan ingin dicari,","Carilah dengan segala daya;","Pelbagai ilmu belum dipelajari,","Apabila berpeluang rebutlah ia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100798,"Dalam perancangan ke Pulau Bentan,","Singgah nanti di Pulau Rawa;","Nisfu Syaaban adalah kesempatan,","Meningkatkan lagi iman dan taqwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100799,"Harus dicoba sukan yang lasak,","Ketahanan diri barulah diketahui;","Kalau hati sudah merosak,","Kelebihan orang sentiasa dicemburui.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100800,"Tinggi nilainya sikayu jati,","Banyak terdapat di tanah seberang;","Apabila bertindak berhati-hati,","Jangan sampai menyinggung perasaan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100801,"Masam manis buah delima,","Manis lagi buah mempelam;","Bertemu kembali sahabat yang lama,","Mengenang semula keindahan semalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100802,"Bersimpati sahabat yang putus tunang,","Punah seketika hasrat naik pelamin;","Tempat jatuh lagi kan dikenang,","Ini kan pula tempat bermain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100803,"Punya rumah di pinggir kali,","Sungguh indah walau tak mewah;","Bukan jumlah rezeki dicari,","Yang penting halal dan penuh berkah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100804,"Datang hinggap si burung pipit,","Mencari makanan disiang hari;","Masalah duit yang menghimpit,","Dengan sabar rezeki dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100805,"Berniaga berjual penuh muatan,","Berpenat lelah usaha sendiri;","Rezeki yang halal membawa keberkatan,","Nikmat dari Allah yang selalu memberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100806,"Setandan buah di tepi perigi,","Suasa dan emas ditimbang kati;","Ramadan sudah tak lama lagi,","Puasa yang lepas anda dah ganti?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100807,"Kalau rezeki secupak,","Haruskah jadi segantang;","Allah sudah siapkan awak,","Mengemis jangan usaha digalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100808,"Pergi ke pejabat berbaju kemeja,","Cuci tangan dan menutup muka;","Sudah terlambat nak pergi kerja,","Kunci pula tak tahu letaknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100809,"Budak-budak pergi sekolah,","Burung Tempua buat sarang;","Biar kerja sampai lelah,","Agar tidak nyusahkan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100810,"Jangan jadi seperti Pak Pandir,","Pergi berbasikal balik jalan kaki;","Usah kerap menyalahkan takdir,","Yakinlah Allah pemberi rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100811,"Usah dibiar pangkin berdaki,","Kayu lapuk melentur lentur;","Berpagian keluar mencari rezeki,","Semangat dipupuk usah luntur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100812,"Masak kari ikan selayang,","Buat sedekah untuk pak Jalal;","Rezeki dicari buat tersayang,","Pastinya berkah dan juga halal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100813,"Leka memberus sebatang galah,","Buang daki hati pun lega;","Usaha terus usah mengalah,","Demi rezeki buat keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100814,"Kalau rezeki secupak,","Haruskah jadi segantang;","Ingat jangan jadi pak pacak,","Siapkan diri sedia berparang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100815,"Mencari kurma di pekan sehari,","Untuk juadah berbuka puasa;","Setiap orang punya rezeki,","Hanya usaha bezakan ganjarannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100816,"Kalau hendak ke air molek,","Laksa asam dilupa jangan;","Rezeki tidak datang bergolek,","Usaha itu tangga kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100817,"Pohon kelapa tidak berdahan,","Pohon Pinang di tanam rapat;","Memang rezeki urusan Tuhan,","Kalau tak berusaha tak kan dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100818,"Sambal lada sangat berguna,","Masak senang enak di rasa,","Rezeki ada di mana mana,","Dengan tenang rajin berusaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100819,"Rintik hujan disulami pelangi,","Cuaca suram hilangnya bayang;","Onak dan duri diharungi,","Demi sesuap untuk tersayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100820,"Tarik upih kawan berpegang,","Ruang sempit bentuk persegi;","Yang pipih juga tak datang melayang,","Malahan Pipit menjauh pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100821,"Punya rumah di pinggir kali,","Sungguh indah walau tak mewah;","Bukan jumlah rezeki dicari,","Penting halal dan penuh berkah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100822,"Pagi hari kita beriadah,","Sambil membawa si anak kecil;","Mencari rezeki itu ibadah,","Usaha keras pastinya berhasil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100823,"Daun pandan pilihan terbaik,","Pengat disediakan berbau wangi,","Semasa hujan mentari terik,","Rupanya Tuhan nak kurniakan pelangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100824,"Hari lebaran rasa teruja,","Hidangan lemang dijamu teman;","Ada cabaran ketika kerja,","Hakikat memang menguji iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100825,"Kepulau langkawi membeli hadiah,","Untuk di beri pada sahabat,","Mencari rezeki memang tak mudah,","Usaha dan tawakal pasti mendapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100826,"Bola ditendang bergolek,","Golek jatuh bergolek sampai kepaya;","Rezeki tidak datang bergolek,","Rajin usaha pasti berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100827,"Beranda berseri berselang solek,","Ketara putih di celah ensil;","Rezeki tidak datang bergolek,","Usaha gigih membuah hasil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100828,"Sungguh cantik balairung seri,","Dihias dengan bunga cempaka;","Dalam kita mencari rezeki,","Kepada Allah jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100829,"Jangan dituan padi huma,","Biar buahnya lerai sendiri;","Rezeki ada dimana mana,","Berusaha lah untuk mencari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100830,"Simpan neraca di bawah rumah,","Senang tetamu hendak menimbang;","Walau cuaca kelihatan cerah,","Siapa tahu bencana kan datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100831,"Petik meranti membawa raga,","Buah pauh jatuh bertaburan;","Cari rezeki menyara keluarga,","Hujan dan panas satu cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100832,"Pisanglah kaki siap dibelah,","Makan berdua elok sepakat;","Cari rezeki di bumi Allah,","Di mana jua asalkan berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100833,"Ke Paya Jaras berjalan kaki,","Membawa bekal pucuk betik;","Kerja keras mencari rezeki,","Usaha dan tawakkal itu yang baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100834,"Berjalan kaki menuju pulang,","Kerja di bendang sehingga senja;","Cari rezeki membanting tulang,","Tidak didendang teruskan kerja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100835,"Perahu kecil dipukul gelombang,","Itu petanda badai melanda;","Rezeki secupak jadi segantang,","Andai usaha dilipat ganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100836,"Bukit didaki tuju ke huma,","Dua pemuda berjalan cepat;","Ada rezeki kongsi bersama,","Rezeki berganda pahala pun dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100837,"Dari permatang meniti cermat,","Naik kedarat menjamah hidangan;","Rezeki datang disusuli rahmat,","Berkat doa usaha berpanjangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100838,"Terbang penyengat lalu menghilang,","Turun kenari tepi telaga;","Bermandi keringat membanting tulang,","Rezeki dicari buat keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100839,"Terik sungguh sinaran mentari,","Minum air sebagai bekal;","Mencari Rezeki sepanjang hari,","Dengan usaha dan juga tawakal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100840,"Datang hinggap burung pipit,","Mencari makanan disiang hari;","Masalah duit yang menghimpit,","Dengan sabar rezeki dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100841,"Pergi pekan kekota kiri,","Singgah berehat dipohon sena;","Dengan berkat rezeki diberi,","Kehidupan harian menjadi sempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100842,"Keris sakti ditempa molek,","Taming sari tajam bercahaya;","Rezeki tidak datang bergolek,","Rajin usaha rahsia berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100843,"Mudik ke hulu mendayung kolek,","Penat mendayung sampai ke seberang;","Yang bulat takkan datang bergolek,","Yang pipih takkan datang melayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100844,"Mentari berseri tertampan,","Indah terpana Sang Surya;","Rezeki kurniaan Tuhan,","Doa usaha jaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100845,"Buah delima masak merekah,","Masak sebijik diatas meja;","Rezeki itu memang dari Allah,","Tidak berpangku tangan sahaja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100846,"Beli gula di pekan Serting,","Singgah santap di kedai buah;","Rajin dan usaha itu yang penting,","Rezeki kan datang melimpah ruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100847,"Masak kari resepi bonda,","Campur santan kuahnya pekat,","Rezeki dicari usaha diganda,","Mohon keberkatan supaya berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100848,"Aku Dia Lagu,","Diradio klasik diwaktu petang;","Pecah rumbia dapat sagu,","Rezeki secupak jadi segantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100849,"Pergi kemuara menaik kolek,","Pokok nipah tumbuhnya rapat;","Takkan rezeki datang bergolek,","Kalau tak usaha manakan dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100850,"Carikan pandan harum mewangi,","Berjalan kaki tidaklah dekat;","Berpanas hujan terbias pelangi,","Agar rezeki mendapat berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100851,"Rasa teruja tidak terkata,","Dianjur niaga nasihat bebekal;","Carilah kerja di mana merata,","Buat keluarga pastikan halal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100852,"Kalau hendak ke air molek,","Belikan saya si-ikan duri,","Rezeki pasti tak datang bergolek,","Usaha yakin diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100853,"Pokok celagi sebelahan teja,","Jarak sekali tepian kolah;","Cerah pagi semua bekerja,","Cari rezeki dijanji Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100854,"Subur teja tepian tasik,","Banyak di desa akar seruntun;","Selamat bekerja sahabat-sahabat klasik,","Ada masa marilah berpantun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100855,"Indah tercipta alam yang luas,","Cuaca panas langit membiru;","Andai usaha bersulam ikhlas,","Rezeki bertunas di batas waktu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100856,"Jalan kaki ke kuala kedah,","Terserempak bilal memetik bunga,","Cari rezeki bukannya mudah,","Pastikan halal untuk keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100857,"Usah mengeluh jika mendaki,","Redah segala ujian menduga;","Titisan peluh cari rezeki,","Manik pahala sara keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100858,"Penuh berhurung pacat di kaki,","Berjalan di lata selepas subuh;","Sedang burung mencari rezeki,","Patutkah kita berpeluk tubuh?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100859,"Ayam jantan si-ayam jalak,","Ayam jaguh nama diberi;","Rezeki datang tak ditolak,","Musuh pula tidak dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100860,"Pisang sekampit diberi Jalal,","Daun cekur sedikit diikat;","Rezeki sedikit didapat halal,","Nikmat disyukur doa dipanjat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100861,"Bunga melati atas meja,","Disiram air setiap pagi;","Ingin berjaya rajin usaha,","Dimana mana ada rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100862,"Kelah dicari di tepi paya,","Cuaca kabur suluh pandangan;","Kaislah rezeki selagi terdaya,","Sentiasa syukur tempuh rintangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100863,"Berlari deras pusing keliling,","Alur lintang mengalir ke huma;","Usaha keras jangan berpaling,","Rezeki yang datang redha terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100864,"Segar terasa lepas subuh,","Mencengkam jiwa pada diri;","Rezeki tak datang berpeluk tubuh,","Berusaha kuat mendapat rezeki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100865,"Pengat dimasak di pagi hari,","Buat hidangan anak menantu,","Keringat membasah rezeki dicari,","Berkt dan halal dari Yang Satu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100866,"Celah almari banyak pepijat,","Gigit di tangan berbintik-bintik;","Rezeki dicari doa dipanjat,","Bersinsing lengan peluh bertitik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100867,"Kalau hendak ke Ayer Molek,","Carikan pandan harum mewangi;","Rezeki tidak datang bergolek,","Berpanas berhujan terbias pelangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100868,"Lembut rusuk mudahlah patah,","Elok dibelai lemah lembut;","Hanya pria bijak berpetah,","Dapat membimbing wanita tersebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100869,"Wanita solehah idaman kalbu,","Penyejuk mata ketika derita;","Tidak ternilai kasihnya ibu,","Tidak terbalas dek wang dan harta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100870,"Awal pagi menoreh getah,","Di separuh jalan hujan lebat;","Jangan dibiarkan rusuk patah,","Cepat cepat cari ubat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100871,"Berkerudung baldu mata bercelak,","Tahan ketawa riuhnya tenggelam;","Isteri itu bukan milik mutlak,","Hanya Hawa dari rusuk Nabi Adam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100872,"Rusuk yang bengkok penyokong organ,","Tisyu-tisyu lembut di tubuh kita,","Hari ini seronok Tahniah diucapkan,","Selamat Menyambut Hari Wanita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100873,"Rusuk yang bengkok usah di lurus,","Kalau di lurus rusukkan Patah,","Hati wanita cinta nya tulus,","Gelora melanda jiwa tak betah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100874,"Pakai baju kebaya nyonya,","Berbunga cantik sulaman tangan;","Wanita sekarang bukan sembarangan,","Hormati lah jangan sakiti hatinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100875,"Pagi hening sinar suria,","Kembang mawar rasa gembira;","Wanita pendamping setia,","Tetap tersenyum walaupun duka lara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100876,"Pasang pelita di atas para,","Terangi khemah untuk dipunggah;","Kita wanita tonggak negara,","Kelihatan lemah tapi gagah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100877,"Tongkat dahan guna buluh,","Takut sekah menimpa diri;","Sudah tahu lagi bersuluh,","Siapkan diri tenangkan hat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100878,"Pasang pelita di tepi reban,","Tak kala bumi berembun pagi;","Hari wanita kena lah bekorban,","Izinkan suami kahwin lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100879,"Waktu dinihari pelita dipadam,","Perkuat amalan teguhkan hati;","Diciptanya dari tulang rusuk Adam,","Fitrah kecekalan wanita sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100880,"Di atas batu air dijirus,","Banyak melata si daun kailan;","Perlu dibantu diri diurus,","Biar wanita lancar berjalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100881,"Enak sajian cucurnya pisang,","Ambillah satu berikan kakak;","Puji pujian kata perangsang,","Dapat membantu susutnya bengkak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100882,"Bunga raya berwarna merah,","Letak di meja buat hiasan;","Cantik wanita nampak terserlah,","Setia cintanya bawa kebahagiaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100883,"Pasang pelita usah melirik,","Jalan mana berduri semalu;","Manis wanita senyum melirik,","Terpikat teruna mengoda selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100884,"Kain digosok usah melulu,","Beras disukat pilih antah;","Bengkuk rusuk disepuh selalu,","Guna tongkat elak patah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100885,"Jalan ke lata burung bersiulan,","Helang berlegar di kebun halia;","Tangan wanita menghayun buaian,","Bisa menggegar goncang dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100886,"Merajuk kata merahlah mata,","Ku pujuk hati jangan bersedih;","Penyejuk mata ketika derita,","Kasih isteri penyambung kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100887,"Bertanam peria di kebun nangka,","Benih di Pagoh bersamanya bendi;","Hebat jaya megahnya jejaka,","Di belakangnya teguh seorang srikandi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100888,"Segak bersongkok jalan sekata,","Dalam pesta mengenang budi;","Rusuk bengkok resam dikata,","Namun wanita hebat peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100889,"Bicara hati seorang serikandi,","Perjudi nasib di medan sahara;","Teruskan memberi menabur budi,","Demi menggapai hikmat kembara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100890,"Berkain batik memetik kangkung,","Cuaca berkabus awan berarak;","Ibarat menarik rambut ditepung,","Rambut tak putus tepung jangan berselerak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100891,"Banyak sungguh pohon rumbia,","Tumbuh melata tepi telaga;","Lembut wanita mengoncang dunia,","Tetap anggun dimata keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100892,"Sungguh manis si gadis desa,","Wajah yang ayu cantik sekali;","Pelengkap rusuk setiap jejaka,","Wanita tetap akan disayangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100893,"Wanita solehah idaman kalbu,","Penyejuk mata ketika derita;","Tidak ternilai kasihnya ibu,","Tidak terbalas dek wang dan harta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100894,"Sinar mentari hangat sekata,","Terukir pelangi dapat dilihat,","Di rusuk kiri wanita tercipta,","untuk disayangi ke akhir hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100895,"Tenang menempuh musibah yang datang,","Setiap suratan terselit hikmah;","Tegakkan keadilan bersuara lantang,","Muslimah sejati pembela ummah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100896,"Apa saja boleh dikata,","Andai tak fikir akan mudaratnya;","Apa tak jadi tanpa wanita,","Bukan nak puji akan hebatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100897,"Maruah ibarat mahkota peribadi,","Jangan dicalari usah dicemarkan;","Berakhlak mulia santun berbudi,","Sifat mahmudah terus diamalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100898,"Baruh dan padang tempat si puyu,","Kolam bertiti di kebun pinang;","Cantik dipandang berwajah ayu,","Wanita berbakti jasa dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100899,"Tidak berubah lokasi paya,","Berterbangan penyengat dan santai beruang;","Wanita tabah wanita berjaya,","Pemangkin semangat sentiasa berjuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100900,"Indah terpana alam semesta,","Terbang bersama sekawan merpati;","Cantik pesona senyum wanita,","Memikat sukma menggetar hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100901,"Sinar mentari terpancar di pangkin,","Dihujung kali tepian paya;","Hargailah isteri sebaik mungkin,","Jangan sekali disakiti hatinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100902,"Wanita solehah idaman kalbu,","Penyejuk mata masa derita;","Kasih wanita wahai ibu,","Tak ternilai wang dan harta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100903,"Bunga mawar bunga berduri,","Ditanam dengan pokok mangga;","Seorang wanita dipanggil isteri,","Buat penyejuk dalam rumahtangga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100904,"Wangi menyusuk si dara jelita,","Cantik terserlah bila berdandan;","Dari rusuk tercipta wanita,","Lembut tabah dalam cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100905,"Indah nyaman dipinggir kota,","Terasa sejuk duduk ditaman;","Sungguh besar pengorbanan mu wanita,","Tetap kekal sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100906,"Jaga perjarakkan kita sekata,","Jangan melatah selalu cekal;","Manis tarikan wanita dimata,","Tidak ngelabah berjiwa kental.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100907,"Banyak ulam di tepi telaga,","Baik diambil makan bercicah;","Peribadi diri perlu dijaga,","Jangan sampai dipandang rendah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100908,"Tangan berkeru berwajah sayu,","Anak terjaga di malam masih;","Wanita selembut airmata ibu,","Memastikan keluarga dipayungi kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100909,"Burung kenek-kenek,","Terbang ke pucuk tebu;","Pesan datuk nenek,","Jangan derhaka pada ibu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100910,"Meraut lidi sehari suntuk,","Daun selasih ditepian titi;","Wanita serikandi mudah dibentuk,","Curahkan kasih Sayang sepenuh hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100911,"Berkerudung elok dipakai terus,","Molek kiranya naik bertatah;","Rusuk yang bengkok tidakkan lurus,","Usah pula dibiar patah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100912,"Burung merak burung tempua,","Turun ke lembah mencari kawan;","Salam Israk Mikraj buat semua,","Semoga bertambah keimanan dan ketaqwaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100913,"Musafir sendiri rela tempuhi,","Niat yang satu tiada bernoktah;","Perjalanan Nabi bertemu Ilahi,","Solat lima waktu diturun perintah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100914,"Mega merah mengocak rasa,","Kala kelam bangkit bertawajuh;","Dari Mekah ke Masjidil Aqsa,","Merentas malam ke langit ketujuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100915,"Indahnya mahraj bacaan surah,","Lantunan suara seiring iman;","Israk dan Mikraj tercipta sejarah,","Jibrail setia menjadi teman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100916,"Letak bungkusan bersilih-silih,","Untuk pasaran serta tempahan;","Nabi utusan insan terpilih,","Saksi kebesaran kuasaNYA Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100917,"Indah sawah kerana padi,","Di sinar mentari di waktu pagi;","Perjalanan nabi bertemu Ilahi,","Tanda kekuasaan yang maha tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100918,"Nabi Muhammad junjungan Ummat,","Membawa rahmat membawa berkat;","Marilah kita banyakkan solawat,","Untuk syafaat di hari akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100919,"Cantik Senyuman menyentap Jiwa,","Diatas pangkin duduk berlima;","Pesan Allah Dibawa Baginda,","Untuk semua umat manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100920,"Selalu lah membaca kitab Kalammullah,","Berzikir bertasbih jangan henti;","Selawat dan salam kepada Rasulullah,","Bekalan kita di akhirat nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100921,"Beli cempedak si Anak Nyonya,","Ke pekan Setiu di hari sabtu;","Nabi tidak membeban umat-Nya,","Lima puluh waktu ke lima waktu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100922,"Langit pertama bertemu Adam,","Langit keenam bertemu Musa;","Sejarah ini takkan terpadam,","Akan diingat sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100923,"Israk Mikraj perjalanan teragung,","Kisah Nabi bertemu Allah;","Buraq diguna perintah dijunjung,","Kepada Allah Baginda berserah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100924,"Asakan sengit hujjah hakikat,","Bagaikan guruh tetap bertahan;","Naik ke langit waktu yang singkat,","Nabi disuruh mengadap Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100925,"Dari duduk berangan- angan,","Bentang sejadah jangan dihalang;","Antara mukjizat Rasul junjungan,","Israk Mikraj peristiwa gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100926,"Pergi sungai mencari lokan,","Lokan diletak dalam sampan;","Perintah solat telah diwajibkan,","Jadikan amalan juga kewajipan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100927,"Hendak berbicara lidah ku kelu,","Takut pulak dikata perasan;","Amanat Nabi diingat selalu,","Terpelihara sentiasa dari landasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100928,"Mari diracik ditabur halia,","Campak sedikit si asam jawa;","Isra Mi'raj bulan mulia,","Sirah nabi penuh peristiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100929,"Dihias cantik pedati lembu,","Bermain gasing di negeri Kelantan;","Sembahyang wajib lima waktu,","Menjadi tiang orang beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100930,"Kuah durian dimasak pekat,","Cicah roti makan bersama;","Malam nan syahdu nabi berangkat,","Solat lima waktu amalan utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100931,"Di dalam garaj letak mempelam,","Banyak teramat dibelah-belah;","Israq dan mikraj kembara malam,","Nabi Muhammad mengadap Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100932,"Pusing keliling tepi lurah,","Bawa sebakul benih kelapa;","Israj mikraj tersemadi sejarah,","Solat lima waktu jangan lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100933,"Pergi kedai mengirim surat,","Sambil mengirim membeli ubat;","Tujuan utama Israq Mikraj,","Menyeru umat supaya beribadat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100934,"Israk mikraj sejarah nabi,","Perjalanannya membawa alamat;","Ajaran yang dibawa kita patuhi,","Semoga selamat didunia dan akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100935,"Lubuk terpencil di pasang belat,","Dapat ikan punggah ke talam;","Sejak kecil di latih solat,","Lima waktu sehari semalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100936,"Ikan kelisa ramai berkenan,","Tidaklah ralat beri percuma;","Besarnya jasa dan pengorbanan,","Perintah solat Nabi terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100937,"Setiap isnin bukan Selasa,","Layan Pantun Jadi Budaya;","Israk makraj di ingat sentiasa,","Kebesaran Allah Tiada Taranya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100938,"Senjata purba pahlawan silam,","Silat bertingkah gaya perkasa;","Perjalanan hamba di suatu malam,","Dari Makkah ke Masjidil Aqsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100939,"Anyam ketupat bawah serambi,","Menggait kelapa gunalah galah;","Isra' Mikraj tanda kasihnya nabi,","Tunaikn 5 waktu itu jgn lelah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100940,"Dari Kangar ke pekan Juru,","Singgah minum di Kampung Seriab;","Solat yang wajib lima waktu,","Itulah amalan utama dihisab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100941,"Jalan ke lata banyak kelkatu,","Di pokok sena pokok delima;","Solat kita lima waktu,","Wajib laksana tiang agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100942,"Tanam ubi diatas bukit,","Selang seli pohon rambutan;","Sunnah Nabi bukan sedikit,","Mari bersama kita amalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100943,"Waktu subuh nampak malam,","Siang hari cahaya benderang;","Nabi menerangi seluruh alam,","Umat islam biak berkembang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100944,"Pohon selasih ditepi perigi,","Indah berseri sang suria menjelma;","Israk dan mikraj mukjizat nabi,","Marilah kita sama sama menghayatinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100945,"Malam hari merentas angkasa,","Melaluai langit hingga ketujuh;","Menemuai tuhan yang maha esa;","Pulang kembali hingga ka subuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100946,"Pakai sepatu mengharung denai,","Cuaca cerah di pagi hari;","Lima waktu wajib ditunai,","Kebesaran Allah usah didustai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100947,"Kain pelikat dari Jawa,","Hendak di jinjing terlalu berat;","Rasulullah melihat berbagai peristiwa,","Gambaran balasan hari akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100948,"Waktu malam angin mendayu,","Sejuk terasa sekeliling alam;","Nabi Muhamad pembawa wahyu,","Segala rukun fahamilah mendalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100949,"Di puncak mahligai kota anggerik,","Cantik hiasannya anjung di barat;","Israk mikraj peristiwa menarik,","Gambaran balasan hari akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100950,"Hantar dobi sebelah rumah,","Kain berkilat miliknya mama;","Perjalanan Nabi penuh hikmah,","Terima solat amalan utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100951,"Ladang jagung habis tenggelam,","Habis bertempiaran segala tikusnya;","Peristiwa agung umat islam,","Tanda kebesaran Allah terhadap umatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100952,"Dimuka pintu jangan berlelama,","Tidak selamat nanti dianiaya;","Lima waktu tiang utama,","Setiap umat diarah mengerjakannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100953,"Bunga raya warnanya merah,","Jatuh sekuntum di dalam kolam;","Israk mekraj perjalanan rasulullah,","Membawa keberkatan ummat islam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100954,"Ukir kolaj usah mengalah,","Cuci luka hampas halia;","Israk mikraj anugerah Allah,","Pengubat duka nabi mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100955,"Kait kurma guna galah,","Jalan denai usah berangan;","Solat lima waktu perintah Allah,","Wajib ditunai tanpa halangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100956,"Bawa bekalan untuk berhijab,","Jauh berjalan penat terasa;","Kekalkan amalan dibulan rejab,","Solat sunat dan berpuasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100957,"Naik perahu muatan sarat,","Sayang kemudi tersangkut keman;","Gambaran balasan hari akhirat,","Jadi pengajaran orang beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100958,"Di hinggap lalat busuk kotoran,","Jaga kebersihan kena sentiasa;","Kefarduan solat jadi tuntutan,","Moga suruhan lakukan segera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100959,"Di meja bulat jeruk kelubi,","Gula lumat sedia di sana;","Wajibnya solat diterima Nabi,","Kita umat patuh laksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100960,"Burung kedidi mencari makan,","Menyusur lalu ditepi perigi;","Salam israk mikraj hamba ucapkan,","Semoga hidup sentiasa dirahmati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100961,"Dalam sejarah kenangan ini,","sangat berkah kongsi amanah;","lakar sejarah perjalanan nabi,","dari Mekah ke kota Madinah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100962,"27 Rejab megimbau semula,","Nabi junjungan sentiasa diingat;","Israk Mikraj tercatat peristiwa,","Rakaat ditentukan seluruh umat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100963,"Cahaya bersinar dari barat;","Duduk bersantai dalam taman;","Buat ibadah dunia akhirat,","Israk Mikraj buat orang beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100964,"Anggun dan manis si dara jelita,","Hiaskan bunga ditengah dada;","Istimewa nya solat kepada kita,","Diterima terus oleh Baginda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100965,"Disusun getah satu persatu,","Getah keping lapis berlapis;","Tiang agama lima waktu,","Tabah sabar dengan hati bersih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100966,"Kata kunci kuat di hati,","Kan terbit kukuh asas beragama;","Kota suci tempat berhenti,","Ke langit tujuh tugas diterima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100967,"Dalam hadapi dugaan berat,","Jarak ditempuh penangan cinta;","Malam yang sepi berteman berkat,","Gerak sejauh pandangan mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100968,"Rendang bidara gugus kejora,","Lembah nan gelap muncul sinaran;","Tenang kendera terus kembara,","Kisah menyingkap Rasul berjalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100969,"Cahaya malam sungguh kelam,","Terbang pula burung kedidi;","Perjalanan nabi suatu malam,","Israk mikraj bukti abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100970,"Hamparan berkilat jadi hiasan,","Atas serambi hujung beranda;","Akhirnya solat menjadi bingkisan,","Kepada Nabi dan umat baginda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100971,"Insan istidraj hatinya mati,","Merasa megah berlagak sombong;","Israk dan Mikraj menjadi bukti,","Kebesaran Allah Yang Maha Agung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100972,"Bawah serambi di kala kelam,","Menyusun getah satu persatu;","Perjalanan nabi di suatu malam,","Membawa perintah lima waktu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100973,"Tambak seberang makin menaik,","Tinggi separuh sebelah kiri;","Masa sekarang peluang terbaik,","Sikap bertangguh merugikan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100974,"Bayang kelibat berkembang dada,","Silap disangka dirinya jantan;","Peluang taubat tetaplah ada,","Selagi nyawa dikandung badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100975,"Ambil galah mengait berangan,","Buahnya ranum jangan di sisa;","Ada baiknya memberi bimbingan,","Dari peluang yang mungkin tersia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100976,"Enak berlada masakan umbut,","Tadi dikupas basuh di lata;","Peluang ada perlu direbut,","Andai terlepas berputih mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100977,"Bermain lelayang di batas permatang,","Pelbagai warna ternampak indah;","Usah diabai peluang mendatang,","Bimbang nanti kesal tak sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100978,"Ikan tenggiri dimasak lada,","Daun selom dibuat salad;","Tidak menghargai peluang yang ada,","Ruginya hidup sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100979,"Pergi ke Daik dari Sedili,","Naik jambatan di sungai Kempas;","Peluang terbaik datang sekali,","Ambil kesempatan sebelum terlepas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100980,"Rewang petang memotong cabai,","Makin meriah sambil perhati;","Peluang datang jangan di abai,","Mungkin ada tuah menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100981,"Melambai lambai pohon kelapa,","Buah nye banyak bertandan tandan;","Peluang kedua pasti ada,","Jika nyawa masih di badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100982,"Anak cina mencari lokan Lokan,","Dibakul dibawa pulang;","Peluang kedua jarang kedapatan,","Mungkin juga tidak berulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100983,"Ramai orang ke Pasaria,","Sambil membeli buah dan kain;","Jangan berharap peluang kedua,","Bimbang disambar dek orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100984,"Menungkat dagu melepas hela,","Terasa letih membanting tulang;","Peluang kedua jangan dipersia,","Hilang percaya hilang yang tersayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100985,"Anak seluang berenang-renang,","Seronoknya berkejaran di batu bata,","Peluang biasanya tidak berulang,","Andai terlepas rugilah kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100986,"Isi kelapa di dalam pedati,","Lalu lalang tepi gua;","Usaha segigih setulus hati;","Peluang kedua ditemui jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100987,"Sabut di pagar ringan belaka,","Buang dilarang dengan kerambil;","Berebut biar dengan etika,","Peluang orang jangan diambil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100988,"Ribut kencang semua lari,","Pokok tumbang menimpa kolah;","Rebut peluang jika di beri,","Rezeki datang dari Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100989,"Seluang kelisa berenang bebas,","Belut lari terkena tuba;","Peluang pertama usah dilepas,","Takut yang kedua tak kunjung tiba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100990,"Pergi kekuala menaik kolek;","Tempat berteduh dipohon berangan,","Rezeki tidak datang bergolek,","Peluang yang ada dilepas jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100991,"Ke paya menangguk seluang,","Pulang menugal makan pulasan;","Orang berjaya melihat peluang,","Malang si gagal mencari alasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100992,"Rendah nya sarang tempua,","Bergaut gaut diranting mati;","Apa dinanti peluang kedua,","Asal hidup banyak berbakti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100993,"Seluang tidak berenang ke hulu,","Lubuk di air si ikan limbat;","Peluang tidak datang selalu,","Kejarlah ia sebelum terlambat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100994,"Kota nan kukuh di Inderagiri,","Jadi bualan jadi sebutan;","Sekian lama menyepi diri,","Rupanya mimpi bersama bulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100995,"Mengutip lokan dalam kegembiraan,","Diluar kawasan sehingga petang,","Jika dilepaskan mungkin kerugian,","Peluang keemasan bukan selalu datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100996,"Sengkuang dimakan buat rojak,","Santai di bawah pohon buluh;","Peluang pasti kan berganjak,","Andai hanya berpeluk tubuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100997,"Mentari naik hilanglah durja,","Tepian kali mengelamun buntu;","Yang terbaik sekali sahaja,","Kedua kali belum tentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100998,"Adun bingka tengah ruang,","Hutang dibayar setiap selasa;","Jangan leka tunggu peluang,","Usaha ikhtiar perlu diperkasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (100999,"Menjelang petang pancing udang,","Anak kera makan lada;","Peluang datang tanpa diundang,","Rebut segera terlanjur ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101000,"Anak seluang berdabik dada,","Duduk selesa atas titi,","Peluang pasti senantiasa ada,","Setia usaha punya jati diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101001,"Ulat sutera dibuat benang,","Benang disimpan dalam laci;","Hidup didunia jangan bimbang,","Rajin usaha pasti ada rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101002,"Bulan Ramadhan kian mendekat,","Mari perbanyak amal ibadat;","Ayuh saling memberi nasihat,","Semoga selamat dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101003,"Kota Tinggi Johor Indah nyaman,","Duduk di beranda Sambil duduk berangan angan;","Rebutlah peluang dimana ada,","Jangan biarkan terlepas ditangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101004,"Dari sumatera ke seri menanti,","Indah sungguh di pagar alam;","Peluang kedua sedia menanti,","Usaha dan doa siang malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101005,"Tanam lada di sulami cekur,","Tepi ampaian di bawah sena;","Nikmat segala panjatkan syukur,","Kesal kemudian tiada berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101006,"Setiap insan rezekinya tersedia,","Usah difikir peluang kedua;","Sentiasa bersyukur setiap masa,","Jangan alpa tamak haloba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101007,"Sarang tempua cantik terukir,","Diwaktu pagi hinggap rerama;","Peluang kedua suratan takdir,","Sekali lagi kita bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101008,"Sinar mentari dihalang kabut,","Di hujung bangsal hinggap kenari;","Peluang diberi mesti direbut,","Bimbang sesal mengintai diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101009,"Rindu tersemat sebak di dada,","Budak botak di tepi bangsal","Hargai nikmat apa yang ada,","Agar tidak bertamu kesal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101010,"Berenang semua sang ikan ke pantai,","Si polan mengail resah menanti;","Peluang kedua seakan berkecai,","Lantaran terkucil langkah terhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101011,"Suram mentari diufuk senja,","Sampan dibawa ombak diredah,","Perlulah hargai peluang yang ada,","Tabah dan doa moga dipermudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101012,"Bunga cempaka di pukul ribut,","Jatuh sekuntum di atas karang;","Di mulut naga akan ku rebut,","Ini kan pula di tangan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101013,"Pandai mertua cerita Pak Pandir,","Walau berusia ceritanya idola;","Peluang kedua mungkin hadir,","Jangan persia perbetulkan segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101014,"Terbang tinggi burung Geroda,","Hinggap di dahan pohon meranti;","Rebut lah peluang masa yang ada,","Agar tidak menyesal nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101015,"Andai singgah di bandar Melaka,","Jangan lupa asam pedas;","Jika selalu hidup leka,","Peluang kita pasti terlepas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101016,"Dari pagi hingga ke petang,","Duduk termenung seorang diri;","Peluang kedua akan menjelang,","Sabar menanti takdir Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101017,"Menguning seri bunga akasia,","Gugur merelai di atas pangkin;","Peluang diberi usah dipersia,","Perbaiki perangai sebaik mungkin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101018,"Berjalan kak Leha menuju Pahi,","Bersama Pak Mat untuk menjerat;","Yakin berusaha doa Ilahi,","Semoga selamat dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101019,"Rembulan Syaaban menyapa kini,","Bererti Ramadan semakin hampir;","Tingkatkan amalan suci murni,","Pasti peluang sentiasa mampir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101020,"Kabut berlalu nampaklah bintang,","Bintang kejora di hujung benua;","Rebut selalu kesempatan datang,","Bimbang tiada peluang kedua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101021,"Anak ayam tepi reban,","Terbang rendah burung merpati;","Bantuan diberi ringankan beban,","Jangan belanja sesuka hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101022,"Asam tempoyak gulai patin,","Bau semerbak sanggup menanti;","Besyukur negara masih prihatin,","Guna perbelanjaan sebaik pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101023,"Di dalam bejana batu suasa,","Dijolok kayu diambil mari;","Malaysia Penjana, Prihatin, Pemerkasa,","Ayuh bersatu majukan ekonomi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101024,"Segak pahlawan pakai tanjak,","Keris berhulu selit dipinggang;","Yang sampai hanyalah dedak,","Lembu bertali mereka pegang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101025,"Pergi berburu dipinggir hutan,","Jerat dipasang pelanduk yang kena;","Perkasa ekonomi jana pendapatan,","Pemimpin perihatin rakyat sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101026,"Tinggi-tinggi pohon kelapa,","Berhati-hati jika mahu memanjat;","Jasa bakti jangan dilupa,","Biar berjasa penuh taat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101027,"Masak lemak daun turi,","Buat kerabu daun pegaga;","Imbuhan diterima rakyat syukuri,","Meringankan beban ekonomi keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101028,"Terbang tinggi burung kenari,","Hinggap didahan pokok jati;","Bermacam-macam bantuan diberi,","Kenapa masih ada yang tak puas hati?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101029,"Ayam kampung masak kari,","Campur dengan serbuk jintan;","Perhatin kerajaan patut di hargai,","Membantu rakyat di dalam kesempitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101030,"Bersama jiran duduk di pangkin,","Sambil menikmati kelapa bali;","Manfaatkan skim sebaik mungkin,","Moga ekonomi pulih kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101031,"Bunga mawar bunga siantan,","Bunga pilihan bunga kemboja;","Ekonomi dipacu jana pendapatan,","Kesihatan rakyat yang paling utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101032,"Beratus tahun negara dijajah,","Portugis Belanda Inggeris dan Jepun;","Kini Malaysia berdiri megah,","Pelbagai langkah telah disusun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101033,"Masak kari ikan tenggiri,","Campur tomato dibelah dua;","Terima kasih bantuan yang diberi,","Syukur alhamduillah padaNya jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101034,"Batu dicari buat hiasan,","Dilihat cantik diatas meja;","Bantuan diberi bukan berterusan,","Beringatlah kita bila berbelanja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101035,"Makan berlauk ikan patin,","Duduk bersandar ditepi tingkap;","Batuan diberi tanda prihatin,","Rakyat bersemangat ekonominya tegap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101036,"Masak lemak ikan patin,","Cili padi dimasukkan bersama;","Banyak bantuan kerajaan perihatin,","Kesetabilan rakyat paling utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101037,"Layu jerami tepian huma,","Segar meranti subur di kali;","Pacu ekonomi peranan bersama,","Negara pasti makmur kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101038,"Lari sana lari sini,","Nampak pulak si Melati;","Berita semua memang terkini,","Dapat bantuan kemain suka senang hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101039,"Kepayang dijemur dibalik-balikkan,","Cendawan putih sedap dimakan;","Yang buta huruf dicelik-celikkan,","Bantuan pelajaran Kerajaan sediakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101040,"Ekonomi meriah disana sini,","Insentif dihulur ekonomi dijana;","Alangkah bertuah dibumi ini,","Pemimpin prihatin lagilah cakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101041,"Masakan patin terasa lazat,","Aroma kari enak kepalang;","Sikap perihatin membantu rakyat,","Jana ekonomi Malaysia gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101042,"Jalur gemilang nama diberi,","Lambang perpaduan kukuhkan ikatan;","Berbagai bantuan telah diberi,","Kukuh kan ekonomi rakyat sejahtera aman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101043,"Hidangan enak membuka selera,","Dihidang juga air kelapa;","Ekonomi dipacu negara sejahtera,","Kebajikan rakyat tak pernah dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101044,"Ceruk mukin terbang merpati,","Tekukur terbang turun ketanah;","Kerajaan perihatin banyak memberi,","Syukur kita kepada allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101045,"Ikan patin ikan ketutu,","Masak kicap bercampur peria;","Kerajaan prihatin banyak membantu,","Supaya rakyat kembali ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101046,"Pergi kepekan dipagi hari,","Bertemu pulak mak Dara;","Pelbagai bantuan sudah diberi,","Pacu ekonomi bantu negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101047,"Pohon angsana jangan ditebang,","Takut menimpa pohon pulasan;","Ekonomi membangun politik berkembang,","Malaysia maju jadi wawasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101048,"Ikan patin ikan tenggiri,","Daun cekur dibelah belah;","Pemimpin prihatin bantuan diberi,","Ucapkanlah Shukur Dan Alhamdulillah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101049,"Nampak redup pohon akasia,","Dari sini terlihat gayat;","Bertuahnya hidup dalam Malaysia,","Pemimpinnya harmoni berjiwa rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101050,"Sarang tebuan di pokok bidara,","Di pangkal teja tertimbun jerami;","Segala bantuan sambut mesra,","Mantapkan kerja pulih ekonomi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101051,"Tumbuh subur tanpa baja,","Hasil diperoleh boleh dijual;","Rakyat mengharapkan bantuan saja,","Harap harap semuanya halal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101052,"Dibina bangsal majlis pengantin,","Santai berehat kerusi dijemur;","Malaysia terkenal Negara perihatin,","Serumpun sepakat Malaysia makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101053,"Menumis kari dihujung sana,","Ikan kelah selera tak tahan,","Bantuan diberi begitu bermakna,","Meleraikan masalah semasa kesusahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101054,"Ceruk mukim terbang merpati,","Singgah mari pohon beringin;","Berbagai skim bantuan diberi,","Pacu ekonomi Malaysia prihatin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101055,"Buah pauh masak didahan,","Pisang sesikat bawa kekota;","Mengapa yang jauh jadi pilihan,","Orang yang dekat ayu dan jelita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101056,"Balik ke kampung jalannya lurus,","Cerita rakyat kisah Pak Pandir;","Apa di buat menongkah arus,","Ibarat hidup melawan takdir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101057,"Jambatan siap untuk dilalu,","Jembatan lama jadi kenangan;","Biar bersakit kita dalu,","Di akhir nanti dapat kesenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101058,"Panjang sungguh Sungai Perak,","Banyak ikannya menjadi rezeki;","Hadapi setiap cabaran dengan bijak,","Agar tidak memakan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101059,"Gadis pemalu berambut lurus,","Sedangkan kehilir mengalir tenang;","Usahkan berjanji kepada yang tidak tulus,","Bila dikecewakan kerana kamu yang terkenang kenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101060,"Jauh berjalan Luas pandangan,","Itulah percakapan orang duluan;","Hingga kekini jadi pegangan,","Agar tidak tersedar jalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101061,"Berkolek kehulu menongkah arus,","Sedangkan ke hilir mengalir tenang;","Yang mana perlu yang mana harus,","Salah satu pasti akan menang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101062,"Buahnya Lemba menghilang haus,","Boleh dihirup dengan tenang;","Jangan cuba menongkah arus,","Kelak hidup tidak kan tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101063,"Burung helang pulang ke sarang,","Melihat itik leka berenang;","Jangan dikejar tempatnya orang,","Tempat sendiri sediakan tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101064,"Katak melompat ke kolam sebarang,","Menyangka diri sudah selamat;","Kalau diri bersifat pemberang,","Ke hulu ke hilirpun berkat terhambat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101065,"Mawar melur didalam paha,","Hias pula diatas sepatu;","Rezeki dihadapan perbaiki usaha,","Jangan mengejar belum tentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101066,"Sampan kolek tepian seberang,","Teruna asyik mengalun lagu;","Mengapa mengharap kasih orang,","Depan mata setia menunggu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101067,"Menyapa dahulu sebelum mencelah,","Bercerita kisah seram dan misteri;","Hanya di hulu diamnya Kelah,","Sanggup bersusah dek kerenah isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101068,"Tali ditarik terus menerus,","Tidak sedar terluka jari;","Sesekali kita menongkah arus,","Untuk menguji ketahanan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101069,"Berpurdah dahulu susah diberus,","Benang disulam jahit bergilir,","Bersusah dahulu menempah arus;","Bersenang kemudian mudik ke hilir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101070,"Gadis melayu indah bahasa,","Ilmu dan budi penuh di dada;","Ikut hati ikut rasa,","Nafsu manusia terkawal tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101071,"Burung Tempua di atas papan,","Sungai lurus di Kuala Deli;","Dipesan tok tua ikut peraturan,","Menongkah arus pilihan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101072,"Gadis pemalu pakai selendang,","Cantik tersenyum memukau mata;","Sekian lama tak bertandang,","Kerana kasih ku tanam setia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101073,"Di kala pagi bunga di jirus,","Berkudup melati nampak berseri;","Relaku pergi menongkah arus,","Rajuk hati membawa diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101074,"Beruang memanjat nampak kurus,","Bersolek ketitir tanpa bersuara;","Pantang ajal menempuh arus,","Berkolek ke hilir sampai muara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101075,"Pokok semalu durinya lurus,","Tumbuh di semaian menutup permukaan,","Berkolek ke hulu menongkah arus,","Memburu impian mengejar kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101076,"Melangkah terus meniti balak,","Kaki meredah rumpun dedalu;","Menongkah arus hanyut ditolak,","Solusi mudah fikir selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101077,"Ke dalam baran meredah belukar,","Daun diselak pokok meranti;","Bukan cabaran jika tak sukar,","Hasilnya kelak pasti menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101078,"Andai berakit mesti waspada,","Jangan sampai karam disungai;","Pandai sedikit tak mengapa,","Jangan pula memandai mandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101079,"Hati2 menyeberang ke titi,","Kayu nya lapuk tidak tahan;","Jangan di kejar yg belum pasti,","Di depan mata banyak kelebehan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101080,"Lilau si Kelah menongkah arus,","Di celah batu menyorok diri;","Banyak helah terus menerus,","Sungguh begitu akal dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101081,"Burung helang di pohon meranti,","Burung kuang jadi tumpuan;","Masa dirancang dengan teliti,","Tidak terbuang tanpa tujuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101082,"Harum merbak bunga melati,","Jatuh terdampar diatas batu;","Mudik kahulu sudahlah pasti,","Menghilir tenang belum tentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101083,"Ubi keledek penuh seperahu,","Direbus sedap digoreng pun mudah;","Sekali terkena dua kali baru tahu,","Sudah terhantuk baru nak terngadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101084,"Usah bimbang hendak kelaut,","Andai cuaca nampak terang;","Buat sesuatu usah kalut,","Fikir dahulu biar tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101085,"Burung merah dipohon jati,","Mengintai burung nampak lah bayang;","Kuatkan azam kentalkan hati,","Aruh ditongkah bukan halangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101086,"Gadis pemalu berambut lurus,","Duduk dilaman makan mempelam;","Mengapa mahu menongkah arus,","Diair tenang lagi tenggelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101087,"Hijau terbentang luasnya sawah;","Rehat sebentar minum dan makan,","Jangan buat perkara yang susah,","Hanya akan meletihkan badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101088,"Pergi ke Dengkil ke rumah nyonya,","Rumah di seberang hujung permatang;","Lagak sidegil begitu gayanya,","Cakap orang semua ditentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101089,"Menanggal sepatu meniti dahan,","Tersangkut benang kain terbelah;","Melakukan kerja ikutlah arahan,","Hati tenang dan diberkati Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101090,"Menuju reban ambil berus,","Ayam serama dipegang si jelita;","Sedia berkorban menongkah arus,","Demi agama negara tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101091,"Berenang ke kuala serta ke hulu,","Bertemu buaya di tepi paya;","Berkerja keras tanpa ilmu;","Hasilnya bertambah wangnya tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101092,"Nampak basah daun sekiat,","Embun berbenang berkilat di padang;","Kerja susah jangan dibuat,","Perkara senang banyak terhidang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101093,"Pokok semalu batang melurus,","Daun berkilat disimbah mentari;","Jangan selalu menongkah arus,","Sekali tersilap lemas sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101094,"Duduk bersila menopang tangan,","Gulai berkuah sedia terhidang;","Sudah selesa dengan kesenangan,","Datang musibah janganlah kecundang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101095,"Nyaman redup bayu berhembus,","Indah rembulan tidak terperi;","Andai hidup menongkah arus,","Bimbang sesalan merantai diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101096,"Kalau benar mahu berenang,","Berkolek dulu sampai ke hulu;","Kalau benar hajat mahu meminang,","Jangan lah buat orang tertunggu-tunggu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101097,"Disisi ayah jalan terhendap,","Menuju ke hulu dirintik hujan;","Sukar berpayah perlu dihadap,","Bersusah dulu bersenang kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101098,"Baju tradisi cantik kebaya,","Tambah indah bercorak batik;","Air tenang kadang berbahaya,","Rupanya dalam disangka cetek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101099,"Ikan tersangkut dihujung joran,","Sebesar peha tidak bermaya;","Tidak takut hadapi cabaran,","Tetap berusaha hingga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101100,"Usah berkira makan bersantai,","Air berarus menuju tambak;","Ganas gelora menghempas pantai,","Kelok terus membandai ombak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101101,"Minum selera susu cair,","Jalan kaki cari lokan;","Tenang gelora piantan air,","Mencari rezeki tetap diteruskan.", 4, 6);
