INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101147,"Pasukan polis bersedia berkhidmat,","Bersiap siaga setiap Masa;","Menangani jenayah begitu cermat,","Agar negara Dan rakyat aman sentiasa.", 4, 6);
