INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101149,"Terbang rerama di pohon ara,","Terasa tenang suasana laman;","Polis umpama wira negara,","Jasa dikenang sepanjang zaman.", 4, 6);
