INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101151,"Pergi ke Lumut beli ikan bilis,","Beza harganya tidak serupa;","Selamat menyambut peringatan hari Polis,","Jasa Orang Lama Jangan Di Lupa.", 4, 6);
