INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101166,"Diatas batu nama ditulis,","Dikerat rotan panjang senada;","Keselamatan dan keamanan tugasnya polis","Rakyat dibantu berpisahnya tiada.", 4, 6);
