INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101178,"Tinggi si gunung Daik,","Burung terbang melayang;","Selalu berbuat baik,","Tetap di kenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101179,"Cantik sungguh sikunang kunang,","Terbang tinggi berwarna merah;","Andai jasa tidak dikenang,","Redhakan saja itulah fitrah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101180,"Walau dah tinggi diharap sabar,","Pangkat yang baru bebanan terkena;","Tagih bau setanggi racik dibakar,","Rupanya Kayu Gaharu bukan Cendana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101181,"Tempua bersarang rendah,","Julai nampak labuh;","Cendana dibakar sudah,","Wangi hingga ke subuh.", 4, 6);
