INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101186,"Lewat petang pelepah di perun,","Sambil minum kopi;","Si Setanggi masih di dusun,","Si Cendana sahaja yang kerja di konti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101187,"Ramuan China Melaka,","Enggang terbang tinggi;","Makan tepian lurah,","Tiada rembang pagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101188,"Rebana sudah ditukar,","Berat diangkat susah;","Cendana belum terbakar,","kayu apiku basah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101189,"Kemboja warna putih,","Telang warna ungu;","Memuja sudah letih,","Kecewa lama menunggu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101190,"Malam terasa sunyi,","Pungguk di pohon berangan;","Tepuk tidak berbunyi,","rupanya sebelah tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101191,"Cekur dalam cupak,","Masak sayur keladi;","Buat baik jangan biar nampak,","Jangan pulak menyombong diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101192,"Harum mewangi kemangi,","Dibuat masak juadah;","Bau setangi mewangi,","Terselindung dibalik wadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101193,"Siput berjalan perlahan,","Katak melompat-lompat;","Didunia banyak godaan,","Berbuat baik setiap saat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101194,"Pergi lama sekali,","Kerana mencari bawang;","Setanggi aroma asli,","Cendana semerbak ruang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101195,"Buat kenduri jangan tak jemput,","Sambil makan sambil berjalan;","Walau berjalan umpama siput","Kancil tetap ketinggalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101196,"Kembang bunga waktu pagi,","Hanya berbau tak boleh rasa","Cendana harum memawangi","Pasti gunakan setiap masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101197,"Harum bau setanggi,","Tanam tepi rumbia;","Hidup biar disayangi,","Kasih tetap setia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101198,"Enggang terbang tinggi,","Tempua bersarang rendah;","Kepalang menabur janji,","Tak tunai menjadi padah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101199,"Masuk dalam bakul,","Bawak pusing keliling;","Berat sama dipikul,","Ringan sama dijinjing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101200,"Enggang terbang tinggi,","Sayap mengembang megah;","Hidup pastikan rugi,","Andai tak ikut perintah allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101201,"Nampak bayang bukit,","Sebalik kabus tebal;","Hati rasa sakit,","Kalbu rasa tebal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101202,"Bersih taman kepayang,","Dihias rumpun kekwa;","Kasih pada yang sayang,","Baru tidak kecewa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101203,"Sakit kena santau,","Berubat pakai tepung;","Walau jauh merantau,","Hati tetap dikampung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101204,"Burung kedidi hinggap di dahan,","Burung tempua bersarang rendah;","Melayang diri tiada tertahan,","Menanggung rindu tiada bernoktah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101205,"Bangun pagi kesawah padi,","Burong kenari berkiauan;","Dimana mana pasti ada rezeki,","Sudah berjaya dilupa jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101206,"Melor putih mewangi,","Berjambangan menyubur indah;","hidup saling menyanyangi,","rukun bahagia berukhuwah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101207,"Tinggi tinggi pohon keranji,","Tempat punai membuat sarang;","Bila setanggi mungkir janji,","Tinggal cendana terbakar seorang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101208,"Terasa pahit ulam mengkudu,","Khasiatnya jua yang nanti;","Usah serapi dikulit buku,","Isinya yang harus diamati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101209,"Enggang terbang tinggi,","Tempua bersarang rendah;","Pegang pada janji,","Semua jadi indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101210,"Tanam padi huma,","Tabur baja asli;","Sentiasa kerjasama,","Yang keruh dijernihi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101211,"Wajah cantik berseri,","Baju di hias labuci;","Cinta telah terpatri,","Mengapa mungkir janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101212,"Enggang terbang tinggi,","Tempua bersarang rendah;","Hidup hanya sekali,","Sempurnakan yang indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101213,"Meniti atas beluti,","Sambil bawa galah;","Hidup penuh bakti,","Pahala kurniaan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101214,"Pipit melayang tinggi,","Jatuh dalam belukar;","Seranggi cendana wanggi,","Enak bau dibakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101215,"Sejuk mandi air di telaga,","Bawa bersama baju basuhan;","Ilmu orang usah diduga,","Tingginya mungkin tiada bandingan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101216,"Sungguh harum bau nya setanggi,","Semerbak di dalam ruangan;","Jadilah orang yang berbudi,","Jasa orang jangan sesekali dilupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101217,"Tempua bersarang rendah,","Bijaknya tidak di sangkal;","Mesra menjalin ukhuwah,","Muga selamanya kekal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101218,"Kait buah pelam,","Di simpan dalam bakul;","manis senyum bersulam,","Berat sama dipikul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101219,"Bulan puasa anyam ketupat,","Ada rendang ulam pegaga;","Setinggi mana tupai melompat,","Akhir nya jatuh ke tanah juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101220,"Burung terbang tinggi,","Hinggap dihujung dahan;","Ceria setiap pagi,","Moga diberkati Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101221,"Petik buah keranji,","Letak bersama gambir;","Mana perginya janji?,","Cuma bertebu di bibir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101222,"Seloka pantun lama,","Didengar terasa indah;","Berbaiksangka kita sesama,","Kehidupan selalu dipermudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101223,"Enggang terbang tinggi,","tempua bersarang rendah;","Hidup sentiasa menyayangi,","kemesraan bertaut sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101224,"Bunga melati si bunga raya,","Di dalam taman mekar sekuntum;","Moga hari ini sihat dan ceria,","Selamat pagi Assalamualaikum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101225,"Enggang terbang tinggi,","Tempua bersarang rendah;","Mana Bau Setanggi,","Cendana dibakar Sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101226,"Duduk berkumpul mendengar cerita,","Dengar teliti seperti enggan balik;","Landasan keharmonian tutur dan kata,","Jagalah lidah ucapkan yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101227,"Pergi ke telaga membawa kendil,","Sambil berjalan kaki menjengkit;","Semakin dipelajari terasa kerdil,","Ilmu dimiliki terlalu sedikit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101228,"Produk berkualiti keluaran Brahim,","Sangat diperlukan apabila berpergian;","Dari jauh mempererat silaturrahim,","Hargailah dengan balasan kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101229,"Sesekali teringin merebus kerang,","Rebus di bawah sipohon nangka;","Sering selesaikan kesusahan orang,","Rezeki kan datang tanpa disangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101230,"Menghadiri latihan di Felda Trolak,","Mengenali peserta menjadi rakan;","Undangan ikhlas usah ditolak,","Kukuh persahabatan dengan makan-makan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101231,"Segala yang di alam Tuhan mencipta,","Termasuk matahari panas membakar;","Apabila mendengar sesuatu berita,","Usah mudah terima sebagai benar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101232,"Makanlah sayur dan juga ikan,","Supaya kesihatan lebih terjaga;","Apabila diamanah belajarlah menyempurnakan,","Apabila ditegur terimalah dengan terbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101233,"Pelanggaran hukum tentu dimurkai,","Mungkin terlepas ketika di dunia;","Susah dan payah harus dihargai,","Pemikiran rasional dapat menilainya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101234,"Amalkan pengambilan makan berprotein,","Sistem imunisasi harus diperkuat;","Disaat susah tunjukkan prihatin,","Derita orang dapat diangkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101235,"Hari cerah langit membiru,","Kerbau berkubang sambil menguak;","Pergolakan politik semakin mengeliru,","Jangan sampai rakyat semakin muak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101236,"Bangkit tidur perasaan terbuai,","Pergilah mandi kembalikan kesegaran;","Kebaikan lalu kini hasilnya dituai,","Tanpa disangka urusan dipermudahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101237,"Menumpang bas ke Hentian Larkin,","Bersesak-sesak penumpang yang sarat;","Harus bersyukur didatangi si miskin,","Itulah ladang keuntungan di akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101238,"Di malam hari pasang pelita,","Unggun api mencerahkan yang gelap;","Berhati-hati menerima berita,","Jangan mudah terjerumus ke perangkap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101239,"Burung berkicau matahari bersinar,","Suasana pagi indah sekali;","Persahabatan yang tulus sentiasa segar,","Dirasakan meresap ke dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101240,"Alangkah indah di alam maya,","Hidup berteraskan belas dan ehsan;","Hari Wanita disambut se dunia,","Hargai segala jasa yang telah dicurahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101241,"Tuntutan agama berakhlak mulia,","Setiap ketika ada cabaran;","Keberkatan hidup bukan penilaian manusia,","Peningkatan usia mengukuh kesabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101242,"Di dalam dusun rumah makan,","Juadah pelbagai sentiasa tersaji;","Keikhlasan hati harus dibuktikan,","Semoga persahabatan kekal abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101243,"Gelang tembaga gelang suasa,","Tidak kan ada yang berasa iri;","Berbudi bahasa identiti bangsa,","Penghormatan orang kan datang sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101244,"Undangan kenduri wajar dihadiri,","Berbesar hati majlis empunya;","Usahlah sengaja hinakan diri,","Perbuatan memfitnah terlalu besar dosanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101245,"Pengunjung datang beramai-ramai,","Melihat peristiwa menjadi sejarah;","Alangkah bahagia suasana yang damai,","Tiada hari berlalu dengan amarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101246,"Sesekali bersama pergi berburu,","Sentiasa memandang kanan dan kiri;","Sebelum berjanji fikir dahulu,","Jangan menyesal dikemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101247,"Musim kemarau ternakan pun kurus,","Tanah kering permukaan merekah;","Jiwa kanak-kanak sangatlah halus,","Usah diguris dengan herdik amarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101248,"Di pasar malam orang berjualan,","Pengunjung bersesak perempuan lelaki;","Santuni peniaga kecil di pinggir jalan,","Usah berkira kongsikan rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101249,"Darilah benang dibuat kain,","Kain dicorak mawar berduri;","Memang mudah salahkan orang lain,","Lihat dahulu kelemahan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101250,"Tempat baharu memang menarik,","Boleh ditinjau banyak peluang;","Walaupun tidak sempat berbuat baik,","Jangan menimbulkan masalah kepada orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101251,"Tidak kelihatan sehalus hama,","Berhati-hati sangatlah wajar;","Keberkesanan organisasi paling utama,","Hubungan dan komunikasi hendaklah lancar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101252,"Tali pancing gunakan tangsi,","Teguh bertahan renggutan ikan;","Hasrat yang baik usah disangsi,","Doakan sahaja peroleh kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101253,"Jangan terperangkap syak dan wahan,","Didik hati jadilah yang terbaik;","Kesihatan adalah anugerah Tuhan,","Hargai sepenuhnya sebelum ditarik balik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101254,"Suasana dingin hujan pun lebat,","Marilah minum kopi segelas;","Hati gembira bertemu sahabat,","Sahabat sejati berhati ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101255,"Sekumpulan burung terbang melayah,","Di hari baru penuh keindahan;","Budayakan tradisi memberi hadiah,","Terutama kepada yang kurang kemampuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101256,"Walaupun berat untuk dibawa,","Tetap dipikul tanpa jemu;","Amalan bersabar menenangkan jiwa,","Biarpun orang tidak menyenangimu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101257,"Digoreng berlada isinya kerang,","Bersama ulam-ulaman sambal belacan;","Usahlah dipersenda kekurangan orang,","Muhasabah diri banyak kelompongan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101258,"Nyawa dicabut jasad di tanah,","Masa berdukacita hanyalah seketika;","Memang terluka apabila difitnah,","Mengadulah kepada Tuhan Maha Berkuasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101259,"Suasana meriah di bulan Syawal,","Umat Islam meraikan kemenangan;","Fitnah orang sukar dikawal,","Memberi maaf itulah kemuliaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101260,"Perjalanan lancar menuju kota,","Pagi hening menenang fikiran;","Apabila sifat cenderung berdusta,","Semakin Tuhan jauhkan dari kebenaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101261,"Lakukanlah kebaikan setiap hari,","Tidakkan miskin dengan bersedekah;","Apabila kesombongan menguasai diri,","Kehinaan peribadi akan terserlah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101262,"Menerokai kepelbagaian di Pulau Jawa,","Penuh mitos, legenda dan misteri;","Lembut berbicara menenangkan jiwa,","Menyerlah keperibadian terukir di sanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101263,"Penting untuk menjadi kaya,","Salurkan kekayaan membela kemiskinan;","Jadilah insan yang paling mulia,","Usah musnahkan reputasi keperibadian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101264,"Indah berlengguk ikan pelaga,","Bagai menurut rentaknya lagu;","Semoga berbahagia bersama keluarga,","Gembira menyambut tahun yang baharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101265,"Berhati-hati tersebar penyakit,","Apatah lagi di angin lalu;","Pemberian ikhlas usah diungkit,","Kelak hilang bagaikan debu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101266,"Sedang menanti roti ditebar,","Pembeli beratur sambil bersembang;","Mendidik anak perlulah bersabar,","Jiwa matangnya sedang berkembang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101267,"Menggunakan dayung menggerakkan sampan,","Basah baju keringat meleleh,","Apa yang ada usahlah disimpan,","Berilah seikhlasnya apa yang berlebih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101268,"Suasana kecoh semasa kebakaran,","Semua berperanan memadamkan api;","Berhati perut semasa kesukaran,","Pentingkan orang lain itu yang terpuji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101269,"Kuih peneram enak rasanya,","Rangup dan manis diminati ramai;","Berusaha berazam semasa di dunia,","Lakukan kebaikan usah diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101270,"Duduk berbicara di atas pangkin,","Berjuadah jeruk mangga dan pala;","Hargai kehadiran orang yang miskin,","Allah cipta untuk kita jana pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101271,"Inginkan berjaya usah bermalasan,","Nanti jauh ditinggal kereta api;","Walaupun semasa PKP banyak keterbatasan,","Bermunculan pula budaya kreativiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101272,"Dinding roboh digantikan papan,","Kerja bertukang hingga ke subuh;","Alangkah indah dan damai kehidupan,","Hubungan kemanusiaan terus diperkukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101273,"Di tebing sungai sarang tempua,","Burung tempua suka menyanyi;","Kesalahan orang mudah didakwa,","Salah sendiri tak pernah mengakui.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101274,"Malu tersipu merah di pipi,","Apabila sapaan sukar dijawab;","Usah gulana tambahan PKP,","Demi kesihatan walaupun ekonomi lembap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101275,"Burung terbang dipipiskan lada,","Ulam ulaman daun kemangi;","Walaupun banyak kekurangan yang ada,","Ikatan persahabatan tetap disantuni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101276,"Memang teringin ke Pulau Sipadan,","Pantainya memutih lagi melandai;","Sebagai pemimpin tunjukkan teladan,","Setiap tutur kata pasti dinilai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101277,"Keadaan dan masa boleh mengubah,","Perubahan yang baik harus dituju;","Menyantuni orang yang ditimpa musibah,","Jagalah tutur kata dan perilaku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101278,"Insan mulia ringankan tangan,","Tidak berkira sentiasa berbakti;","Muzik dan alam memberi ketenangan,","Ciptaan Tuhan yang harus dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101279,"Berduyun berarak kambing biri-biri,","Diwaktu pagi pasti kelaparan;","Jangan diperkecil idea diberi,","Fikirkan bersama bagaimana melaksanakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101280,"Mengheret kaki yang tidak larat,","Bermalam berehat di dalam gua;","Walaupun diikat digenggam erat,","Kalau bukan rezeki terlepaslah jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101281,"Usah berhenti menulis dan mengarang,","Walaupun berat untuk digalas;","Berasa gembira dengan kejayaan orang,","Itu tandanya hati yang ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101282,"Senda gurau Rosmin kita nantikan,","Teman penghibur lara dihati;","Selain berikhtiar untuk kesembuhan,","Doa ikhlas sahabat Allah makbuli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101283,"Paling enak si ikan parang,","Dimasak asam pedas sangat digemari;","Usah cemari kepercayaan orang,","Sentiasa laksanakan amanah yang diberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101284,"Bermain-main di tengah laman,","Sambil berlari belajar memanah;","Segala yang dimiliki hanyalah pinjaman,","Harta dan kesihatan adalah amanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101285,"Walaupun resah dengungan lebah,","Harus diterima dengan redha;","Apabila sahabat ditimpa musibah,","Segeralah dibantu diiringi doa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101286,"Pergi mengembara ke semua pelosok,","Berhenti seketika melepaskan lelah;","Alhamdulillah pencen sudah pun masuk,","Jangan lupa banyakkan bersedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101287,"Hari ini hari yang bersejarah,","Penciptaan baru memudahkan kehidupan;","Kalau terus berdegil terimalah padah,","Tinggal di hospital masa dihabiskan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101288,"Kehidupan di dunia banyak lakonan,","Nabi pesan sering mati diingati;","Semasa sihat lazimilah kebaikan,","Di penghujung usia Allah terus berkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101289,"Menentukan sempadan dengan mengukur,","Banyak karenah berkaitan tanah;","Sebarang tindakan mestilah jujur,","Supaya tidak menimbulkan fitnah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101290,"Banjir besar dipanggil bah,","Banyak tempat ditimpa bencana;","Apabila sahabat ditimpa musibah,","Bersama-sama kita atasi kesukarannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101291,"Pergi ke pasar menjelang petang,","Hendak membeli seekor tongkol;","Betapa berat mata yang memandang,","Berat lagilah bahu yang memikul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101292,"Berjalan di pantai berhati-hati,","Jangan terpijak benda yang tajam;","Berpulangnya ulama kehadhirat Ilahi,","Cahaya hilang dunia pun kelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101293,"Semasa wabak tidaklah bebas,","Pesanan berkali-kali kepada rakyat;","Sahabat dikesan positif Covid-19,","Semoga sembuh kembali walafiat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101294,"Tapai tidak menjadi tanpa ragi,","Penyediaannya perlu penuh penelitian;","Fenomena banjir kini datang lagi,","Empati dan simpati dari kejauhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101295,"Pergi beriadah di Bukit Rupa,","Nun jauh indahnya pemandangan;","Selagi mempunyai ibu dan bapa,","Santuni setulusnya dengan kemuliaan.", 4, 6);
