INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101298,"Pahlawan gagah bagai Hang Lekir,","Segala cabaran tabah diharungi;","Meskipun perjalanan lalu tidak berakhir,","Banyak pelajaran telah dipelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101299,"Langit mendung tanda kan hujan,","Segera ke destinasi memandu laju;","Pemimpin diangkat kerana kewibawaan,","Berikan sokongan melakar arah tuju.", 4, 6);
