INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101338,"Minum kopi barang secangkir,","Sambil mengira pendapatan hibah;","Tahun 2020 di ambang akhir,","Cabaran mendatang hadapi dengan tabah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101339,"Sering disebut ayam tambatan,","Pasukannya menang diatur rapi;","Mari hargai huluran persahabatan,","Tanpa sahabat hidup kan sepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101340,"Terasa dingin di dalam gua,","Banyak rahsia yang boleh diterokai;","Menziarahi kenalan yang sudah tua,","Amalan pekerti yang diredhai Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101341,"Jangan hanya dekati golongan atasan,","Belum tentu membantu apabila dipinta;","Persahabatan harus berlandas keikhlasan,","Sentiasa membela bila ada yang menista.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101342,"Buah cempedak buahlah nangka,","Disimpan elok di dalam peti;","Janganlah mudah kita berprasangka,","Kesusahan orang harus dimengerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101343,"Tenang dihati menempuh kehijauan,","Hutan Simpan dan rumput rampai;","Menziarahi ibu bapa adalah kebahagiaan,","Kesempatan yang ada usah diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101344,"Yang berkocak bukan kerana buaya,","Yang tenang tempat bermeditasi;","Selamat Hari Natal dan berbahagia,","Buat sahabat yang sentiasa dihormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101345,"Walaupun wabak sedang melanda,","Janganlah risau jagalah diri;","Meskipun lama tidak bersua,","Tidakkan lupa di sudut sanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101346,"Kelihatan memutih sekumpulan biri-biri,","Rumput menghijau di tanah yang lembab;","Apabila kepercayaan telah diberi,","Sepenuh hati laksanakan tanggungjawab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101347,"Jarang ditemui buah rambai,","Tangkainya panjang tergantung berjuntaian;","Nilai persahabatan usah diabai,","Bila kehilangannya akan kerugian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101348,"Pegawai Daerah digelar Bupati,","Nilai kejujuran harus dipraktik;","Janji diucap harus ditepati,","Tidaklah nanti digelar munafik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101349,"Semua laporan harus diperinci,","Jangan ada fakta ketinggalan;","Ikatan perkahwinan ikatan suci,","Usah dicemari dengan pengkhianatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101350,"Dalam perjalanan melintasi Sitiawan,","Singgah seketika rehatkan kaki;","Jika datang orang minta bantuan,","Bantu segera kerana itu adalah rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101351,"Hati bersih wajah bercahaya,","Terasa tenang di dalam hati;","Merendah diri sifat yang mulia,","Segala dimiliki kepunyaan Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101352,"Orang berbicara Yajuj dan Majud,","Tanda kiamat akan menjelma;","Masalah kehidupan tetap kan wujud,","Hadapi dengan tenang dan bijaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101353,"Awan mendung suasana suram,","Burung terbang singgah di dahan;","Hilang bintang di langit malam,","Bagai pelayar ketiadaan pedoman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101354,"Diwaktu senja duduk sendirian,","Menilai semula amalan sehari;","Tidak tunduk kepada godaan,","Itulah insan yang dikasihi Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101355,"Diwaktu senja duduk sendirian,","Menilai semula amalan sehari;","Tidak tunduk kepada godaan,","Itulah insan yang dikasihi Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101356,"Terasa sakit apabila terluka,","Entah kan bila ia kan kebah;","Keaiban peribadi usah dibuka,","Keaiban orang jangan dihebah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101357,"Menabur bijirin makanan merpati,","Melihat gelagatnya memberi ketenangan;","Keputusan dibuat wajib dihormati,","Usah berhelah daripada melaksanakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101358,"Atas permukaan panas berwap,","Tengahari berbahang tidak tertahan;","Apabila dipikulkan dengan tanggungjawab,","Laksanakan dengan penuh kesungguhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101359,"Genting Highland tidaklah dingin,","Tetapi nyaman angin menderu;","Tidak semua diperoleh apa diingin,","Menguji kesabaran di dalam kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101360,"Ikan di kolam kejar mengejar,","Suasana harmoni penuh ketenangan;","Sepanjang hidup sentiasa belajar,","Sedari diri yang sentiasa kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101361,"Berilah masa untuk pulang,","Ke kampung halaman sekali sekala;","Kemiskinan bukan suatu yang malang,","Inilah kesempatan menjana pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101362,"Jangan terminum air bersemut,","Orang tua kata menjadi pelupa;","Menjejaki perjalanan hingga ke Lumut,","Apabila rentas negeri telah dibuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101363,"Membina kemuliaan memelihara aurat,","Kehormatan diri menjadi sanjungan,","Persahabatan hakiki tanpa bersyarat,","Kemesraan lestari hingga berpanjangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101364,"Airnya jernih di Tasik Kesuma,","Jelas terlihat bayangan wajah;","Lagu lama kenangan bersama,","Mengimbau masa lalu yang indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101365,"Harus dibudayakan mencari kesahihan,","Berita yang datang perlu diteliti;","Semasa pandemik semua orang terkesan,","Kelaziman baharu seringlah memberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101366,"Harimau tidur menyimpan kuku,","Apabila sedar mengaum gagah;","Hati yang keras terserlah perilaku,","Sedarlah Tuhan menarik hidayah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101367,"Kenduri kendara masak memasak,","Tetamu datang disediakan hidangan;","Dendam kesumat hati yang rosak,","Tiada kesudahan tiada ketenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101368,"Makanan enak dibakar arang,","Udang, ketam, kupang dan ikan;","Bila bertemu keaiban orang,","Lindungi dan simpan usah dihebahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101369,"Usahlah dibiar tamak dan wahan,","Dimana mungkin hidup yang barokah;","Bersyukur dengan nikmat Tuhan,","Banyakkanlah berinfaq dan bersedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101370,"Bunga matahari menghiasi pagi,","Burung murai riang berterbangan;","Perginya guruku mengadap Ilahi,","Jasa dan amalnya jadi bekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101371,"Menaiki perahu hingga ke tepi,","Menebar jala mencari pendapatan;","Kenangan baik wajar diingati,","Kenangan pahit dijadikan sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101372,"Ulang tahun kelahiran bersama dirai,","Semoga bertambah rezeki dan keberkahan;","Keikhlasan orang dapat dirasai,","Daripada perilaku yang ia zahirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101373,"Berhati-hati di pinggir kali,","Bimbang muncul seekor buaya;","Usah berkira ketika membeli,","Bantu orang yang keras berusaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101374,"Banyak manfaat perubatan tradisi,","Sungguh berkhasiat hasilan buah pala;","Kemiskinan orang bermakna tersendiri,","Anugerah Allah menjana pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101375,"Hujah kukuh semua ternganga,","Bukti yang ada dapat dipertahan;","Usah diperkecil orang berdoa,","Setelah berikhtiar serahkan kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101376,"Tiada gigi makanan lembik,","Banyak diperbuat daripada kanji;","Semua terkesan dengan pandemik,","Biasakan memberi Tuhan sedang menguji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101377,"Sekian lama tidak bertemu,","Cerita silam terungkap kembali;","Jangan berasa sudah berilmu,","Cari kesempatan terus mendalami.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101378,"Berpada-pada belanja berlebihan,","Usah diabaikan yatim piatu;","Semasa pandemik ramai yang terkesan,","Suburkan empati segeralah membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101379,"Berhati-hati melangkah di lantai,","Paku yang karat terpijak jangan;","Sesekali bertemu sahabat handai,","Butir bicara meningkat kematangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101380,"Berjalan kaki berbatu-batu,","Menjelang senja sampai di destinasi;","Usah berkira untuk membantu,","Ramai yang susah pada masa ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101381,"Memancing ikan di atas jambatan,","Mendapat hasil hati ceria;","Harus peka gejala kesihatan,","Dapatkan rawatan dengan segera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101382,"Rasa diraikan disambut ramah,","Pelbagai juadah terhidang lama;","Semasa pandemik tinggallah di rumah,","Keselamatan semua tanggungjawab bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101383,"Kenangan abadi cincin suasa,","Dihadiahkan dengan hasrat yang murni;","Nasihatilah dengan berbudi bahasa,","Semoga menyentuh hati nurani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101384,"Untuk berjaya usahlah lambat,","Semua berpegang nilai bersama;","Gembira dengan kejayaan sahabat,","Sememangnya layak pengiktirafan diterima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101385,"Dari puncak gunung dituruni,","Denai ditempuh penuh tumbuhan;","Ketamakan nafsu terserlah kini,","Muslihat meliputi dalam segala perbuatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101386,"Permintaan orang segera dipenuhi,","Melepas kesempitan terasa kebahagiaan;","Disaat menikmati rezeki anugerah Ilahi,","Usah dilupa jasa petani dan nelayan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101387,"Terasa kesihatan tidak stabil,","Rehatkan diri di pinggir kali;","Hak orang janganlah diambil,","Di akhirat kelak harus dikembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101388,"Usahlah berbelanja bermewah-mewah,","Salurkan bantuan kepada fakir dan miskin;","Disaat kehidupan sukar dan payah,","Orang memerhati akhlak pemimpin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101389,"Perselisihan kecil menjadi kelahi,","Di dalam diri suburkan kesabaran;","Kemakmuran rezeki anugerah Ilahi,","Ujian besar bagaimana dimanfaatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101390,"Nama lain cili ialah cabai,","Usah dirisau banyak dijual;","Kesihatan diri usah diabai,","Bertemu doktor secara berjadual.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101391,"Hari ini Isnin esok Selasa,","Beramal baik hidupmu berkat;","Bahasa itu jiwa bangsa,","Perjuangkannya hingga akhir hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101392,"Gadis melintas berpayung merah,","Jalan melenggang di tengah pekan;","Hujan lebat turun mencurah,","Ada pesan yang disampaikan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101393,"Harum mewangi bunga melati,","Berkembang mekar di pinggir jalan;","Ujian datang silih berganti,","Kasih Tuhan kepada manusia pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101394,"Bekerja keras badan berpeluh,","Rezeki yang halal itulah keutamaan;","Jangan menangguk di air yang keruh,","Bersimpati dengan orang yang kesempitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101395,"Orang berlari ke sana ke mari,","Suasana hingar jeritan suara;","Amalan berhutang perlu dihindari,","Telah berhutang lunaskan segera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101396,"Antara daging lembu dengan ikan,","Mana satu yang berisiko tinggi;","Gejala wabak semakin membimbangkan,","Peranan semua bersama memerangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101397,"Berliku-liku berenang haruan,","Jinak mendekat di hujung jari;","Jangan hidup penuh penipuan,","Nanti menyesal di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101398,"Lambat lagi untuk berhari raya,","Kini suasana pasti berlainan;","Hargai persahabatan dengan sebaiknya,","Beri masa walau dalam kesibukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101399,"Jiwa yang lembut mudah terharu,","Peristiwa lama berulang semula;","Berhati-hati dengan ajaran baharu,","Supaya aqidah tetap terpelihara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101400,"Daripada awal belajar mengeja,","Bergurau senda ketawa mengilai;","Saat menikmati hidangan di meja,","Jasa petani nelayan tidak ternilai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101401,"Berenang-renang ikan pelaga,","Di dalam kolam tenang sekali;","Nikmat kesihatan sangat berharga,","Usah dipersia anugerah Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101402,"Sungguh berharga kayu gaharu,","Wangian tersebar di waktu malam;","Lahir Nabi Muhammad lembaran baharu,","Membawa rahmat ke seluruh alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101403,"Penginapan sementara di pangsapuri,","Walaupun sederhana tetap selesa;","Pengiktirafan orang jangan dicari,","Tiba masanya datanglah ia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101404,"Terasa segar mandi ditelaga,","Dingin mencengkam seluruh badan;","Orang asing usahlah dicuriga,","Sumbangan besarnya usah dinafikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101405,"Buah cempedak buah nangka,","Rasanya manis tetapi berlainan;","Janganlah mudah kita berprasangka,","Anggap saja kesilapan tidak disengajakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101406,"Jangan hanya duduk terperap,","Banyak aktiviti memberi faedah;","Telah selesai laksana tanggungjawab,","Terasa beban terangkatlah sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101407,"Kalau enggan banyaklah alasan,","Kalau ikhlas tidaklah dimungkiri;","Ucapan selamat dengan keikhlasan,","Persahabatan sejati dapat dirasai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101408,"Usah berhasad kepada yang lebih,","Andainya ditegur usahlah marah;","Bersihkan hati dengan bertasbih,","Ingati Tuhan yang Maha Pemurah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101409,"Nikmati keenakan sambal kerang,","Turut terhidang gulai tenggiri;","Usahlah dicari kekurangan orang,","Lebih baik kita bermuhasabah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101410,"Belajar menulis di atas kertas,","Pena dipegang berhati-hati;","Walaupun pergerakan kian terbatas,","Aktiviti riadah usah dihenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101411,"Bunga berkembang berkuntum-kuntum,","Rama-rama riang tidak terkira;","Disaat ekonomi terus merudum,","Berhemah dan bersyukur dengan yang ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101412,"Menghadiri majlis berpakaian kemas,","Majlis disusun begitu indah;","Usia menjelang warga emas,","Berhati-hati pilih aktiviti riadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101413,"Berhati-hati dengan wabak melanda,","Sentiasa utamakan sejahtera kesihatan;","Tidak bersyukur apa yang ada,","Hidup resah tanpa keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101414,"Bersiar-siar di Putrajaya,","Gembirakan hati yang sedang walang;","Senyuman ikhlas bagaikan cahaya,","Menuju destinasi menyingkir yang menghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101415,"Membina kolek menggunakan papan,","Kemahiran bertukang sudah diwarisi;","Menjadi terbaik sememangnya harapan,","Menjadi unik keistimewaan tersendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101416,"Berhati-hati apabila berkawan,","Sukar memilih kawan sejati;","Usah dinista apa yang dilakukan,","Niat di hatinya Tuhan lebih mengerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101417,"Di malam purnama harum mewangi,","Terasa dingin bayu berhembusan;","Kebenaran fakta usah dilindungi,","Sebagai asas menegakkan keadilan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101418,"Untuk keselamatan dipasangkan pagar,","Jari sakit tertusuk paku;","Pujuklah hati dengan bersabar,","Peringatan Tuhan di sebalik yang berlaku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101419,"Lumrah kanak-kanak gemar bermain,","Perlulah juga diperhatikan selalu;","Usahlah menuding kepada orang lain,","Muhasabahlah diri terlebih dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101420,"Melepas lelah di Bukit Rupa,","Kabus meliputi menghirup kesegaran;","Kebaikan selama ini tidak dilupa,","Sentiasa berada dalam kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101421,"Ikan kembung digoreng berlada,","Kayu api dibakar tinggalkan asap;","Dalam keadaan pandemik sedang melanda,","Kesejahteraan masyarakat semua bertanggungjawab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101422,"Keadaan berjerebu pemandangan kelam,","Batalkan hasrat ke Tanjung Malim;","Mengapa diungkit kisah semalam,","Jika merosakkan hubungan silaturrahim.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101423,"Kehilangan arah usah diredah,","Lebih elok pergilah bertanya;","Laksana tanggungjawab dengan beramanah,","Jangan sampai ada yang teraniaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101424,"Ke pantai Port Dickson ayuh berkunjung,","Cari ketenangan dengarkan ombak;","Dengan manusia usahlah sombong,","Segala amal bakal tertolak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101425,"Pasar Terapung di negeri Kelantan,","Riuh rendah pelbagai bunyi;","Usah dipersia ikatan persahabatan,","Mengubati jiwa dilanda sunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101426,"Sangat berkhasiat simadu lebah,","Bersama herba ia diadun;","Wabak melanda adalah musibah,","Menguji manusia kembali bertamadun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101427,"Semakin dibuat apabila dilarang,","Nasihat berhikmah lebih berkesan;","Usah dipersenda kebaikan orang,","Diri sendiri belum tentu ada kebaktian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101428,"Banjir besar penduduk berpindah,","Penempatan sementara banyak kemudahan;","Telusuri Sungai Melaka yang diperindah,","Mengimbau sejarah mencetus keinsafan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101429,"Menziarahi sahabat di Pekan Selama,","Musim buah-buahan rezeki melimpah;","Ulang tahun perkahwinan dirai bersama,","Semoga kekal bahagia ke jannah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101430,"Ciri tersendiri bunga bakawali,","Di permukaan air ia bertakhta;","Harus bertekad ambil peduli,","Wabak melanda tanggungjawab kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101431,"Jarang dimakan sibuah rokan,","Pahit manis perlulah dirasa;","Janji dilafaz untuk ditunaikan,","Jangan mudah memungkirinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101432,"Kalau berjanji usahlah dimungkiri,","Kalau berbicara biarlah berpada;","Hanya doa yang dapat mengiringi,","Hargai perkahwinan sepenuh jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101433,"Segala dilakukan ingatlah Tuhan,","Bukakan hati apabila dikritik;","Perebutan kuasa tanda keruntuhan,","Harus dinilai semula matlamat berpolitik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101434,"Doakan orang di dalam hati,","Itu tandanya penuh keikhlasan;","Sahabat yang tulus sentiasa mengingati,","Undangannya haruslah kita raikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101435,"Elakkan memasuki restoran yang kotor,","Janganlah sampai kita terlupa;","Lembutkan suara apabila bertutur,","Apatah lagi dengan ibu bapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101436,"Mari melancong ke Tanjung Karang,","Jangan lupa bawalah kail;","Apakah berbahagia menindas orang,","Jiwa kosong diri pun jahil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101437,"Pagi segar disinari matahari,","Terasa riang burung berkicauan;","Bersyukurlah dengan anugerahi Ilahi,","Kehidupan bahagia dengan kesederhanaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101438,"Tumbuh lalang kalau bukannya padi,","Musim tengkujuh tenggelamkan tanaman;","Utamakan kepentingan umum daripada peribadi,","Di saat masyarakat sedang kesempitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101439,"Segak dan kemas cara berpakaian,","Kelihatan dirinya semakin matang;","Sesekali perlu hanya bersendirian,","Bermuhasabah perkara lalu dan yang mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101440,"Banyak manfaat berlaukkan kailan,","Disertai bersama ikan parang;","Orang berilmu menunjukkan jalan,","Bukan diperalat memanipulasi orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101441,"Mahal harganya udang kara,","Belilah sedikit penuhi hasrat;","Walaupun dunia hanya sementara,","Jadikannya faktor bahagia di akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101442,"Memang berbisa digigit lipan,","Sakitnya mencengkam hingga ke pagi;","Usah kecewa teruskan kehidupan,","Lihat ke hadapan dengan berstrategi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101443,"Banyak dilihat di Pasar Karat,","Barangan antik ramai yang mencari;","Mari bersahabat tanpa bersyarat,","Keikhlasan perhubungan dapat dirasai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101444,"Harganya mahal buah delima,","Bukan sahaja enak malah berkhasiat;","Apabila ajal datang menjelma,","Tidak boleh ditangguh walau sesaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101445,"Bunga dahlia bunga melati,","Disatukan jambangan jiwa bahagia;","Hari Malaysia menyatukan hati,","Utamakan negara hapuskan curiga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101446,"Di bulan puasa bermain meriam,","Meriam buluh untuk bersuka ria;","Persaraan tidak bererti berdiam,","Manfaatkan ilmu dan pengalaman di dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101447,"Manis sekali apabila berkata,","Semoga tidak diliputi kepalsuan;","Berhati-hati menerima berita,","Usah terperangkap dengan penipuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101448,"Hanya termenung perbuatan sia-sia,","Tiada faedah dapat dimanfaatkan;","Merajuk adalah fitrah manusia,","Hanya seketika teruskan kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101449,"Menyusur cahaya kunang-kunang,","Pohon rendang meruntun hati;","Sifat tamak budi tidak dikenang,","Kerugian diri sedang menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101450,"Negara ini kaya bijih timah,","Kini jadi kenangan belaka;","Dengan ikhlas bela golongan lemah,","Allah anugerahi rezeki tanpa disangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101451,"Manfaatkan kesempatan untuk bercuti,","Ketenangan jiwa perlu dijana;","Peruntukkan masa yang berkualiti,","Bina kekeluargaan lebih bermakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101452,"Hidup susah disebabkan kemalasan,","Bagaimanakah dapat kecap bahagia;","Apabila keikhlasan jadi landasan,","Allah turunkan rezeki tanpa diduga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101453,"Ahli sukan badminton memang handalan,","Menjulang nama negara dihormati;","Sistem perundangan mencari keadilan,","Kebijaksanaan Hakim sangatlah dinanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101454,"Susah senang bersama rakan,","Jerih perih dilalui bersama;","Sebelum melakukan sebarang tindakan,","Fikirkan kesan yang bakal menimpa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101455,"Jika berkelahi usah membenci,","Ada masanya jadi kenangan;","Perkahwinan adalah institusi suci,","Usah dimanipulasi mengaut keuntungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101456,"Mengenang kembali malang menimpa,","Ambillah pengajaran punca terjadi;","Mengapa manusia sering terlupa,","Musibah menimpa berulang kali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101457,"Kumpulan itik jalan beratur,","Hati riang terbitnya fajar;","Ketamakan manifestasi jiwa yang kotor,","Keutamaan dunia yang dikejar-kejar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101458,"Hilangkan mengantuk mengunyah kuaci,","Kulitnya dikumpul dalam kertas;","Hubungan kemanusiaan hubungan suci,","Jika meminta usah melampaui batas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101459,"Kusut masai rambut disikat,","Kemas dan ceria tenang di wajah;","Usah diabai pendidikan masyarakat,","Masyarakat berilmu negara bermaruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101460,"Anak rusa berkejar-kejaran,","Di hutan belukar riang dan ria;","Kehidupan adalah proses pembelajaran,","Sentiasa berubah tiada hentinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101461,"Tanah air bebas daripada dijarah,","Penjajah diusir dengan berdiplomasi;","Hari Kemerdekaan hari bersejarah,","Merdekakan jiwa dengan berintegriti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101462,"Burung nuri di atas dahan,","Berehat seketika melepas lelah;","Negara sendiri penuh keindahan,","Terokai warisannya dengan berhemah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101463,"Buah delima buah pauh,","Mari dihidang di atas pangkin;","Sahabat bertandang dari jauh,","Santunilah dengan sebaik mungkin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101464,"Buah mangga di hujung tanjung,","Berselang seli buah betik;","Guru berjasa sentiasa disanjung,","Pemergian ditangisi ribuan anak didik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101465,"Silap langkah terimalah padah,","Telitilah dahulu sebelum tindakan;","Usah tinggalkan aktiviti beriadah,","Di usia emas lebih lagi diperlukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101466,"Kampung bernama Kedai Lalat,","Tentu namanya ada sejarah;","Bangun pagi teringatkan sahabat,","Semoga terus sihat dianugerah barokah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101467,"Malam cerah langit bercahaya,","Jadi kegembiraan ahli falak;","Janji ditepati sifat yang mulia,","Sengaja engkari hinanya akhlak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101468,"Meningkat tua banyak yang terlupa,","Terus membaca menguatkan ingatan;","Sentiasa menjaga hati ibu bapa,","Kelak hidupmu peroleh keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101469,"Meskipun berusia masih berbisa,","Bagai pohon yang banyak dahan;","Walaupun fahaman terus berbeza,","Usah dirungkai tali persahabatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101470,"Pelancaran syiling tanda peringatan,","Bersama menyambut Jubli Emas;","Suburkan rasa kasihkan watan,","Sejarah perjuangan terakam kemas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101471,"Rasa bersyukur mendapat kurnia,","Janganlah pula ada yang hasad;","Penghijrahan sebenar luas ertinya,","Bukan sekadar berpindah jasad.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101472,"Nelayan pulang berwajah ceria,","Sarat dengan udang dan ikan;","Kehidupan persaraan usah dipersia,","Ada tanggungjawab perlu dilaksanakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101473,"Mulakan pengembaraan ke Lahad Datu,","Kenderaan yang baik amat diperlukan;","Walaupun keburukan sedang berlaku,","Memaafkan adalah tingkah teladan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101474,"Banyak kepala banyak disaran,","Bila berbahas haruslah bersabar;","Drama ditonton banyak pengajaran,","Mengapa tidak diambil iktibar?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101475,"Elakkan dari bercakap kotor,","Hubungan kemanusiaan jangan dibuang;","Berlemah lembut dalam bertutur,","Manusia hormat Allah pun sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101476,"Kampung halaman sentiasa dirindui,","Kenangan lalu datang berulang;","Undangan walimah harus dipenuhi,","Kita diingati bila diundang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101477,"Senja indah sentiasa dinanti,","Bagan Lalang pantainya putih;","Hindarkan hasrat beriri hati,","Berusaha menjadikan hati yang bersih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101478,"Cuba mengingati cerita lama,","Jadi kenangan menghibur hati;","Menyantuni orang tua ajaran agama,","Refleksi diri dihari tua nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101479,"Letih bekerja malam berdengkur,","Disorong bantal lena sekali;","Walaupun mimpi mainan tidur,","Telah menjadi ilham memotivasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101480,"Berwarna merah air sirap Bandung,","Nyaman terasa mengalir di kerongkongan;","Adakalanya marah sukar dibendung,","Sabar pesanan Rasul junjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101481,"Wajah muram berduka nestapa,","Peristiwa silam janganlah diserlah;","Usah abaikan kebajikan ibu bapa,","Santunilah semoga memperoleh berkah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101482,"Tiada salahnya duduk berangan,","Bermula berangan menjadi realiti;","Usahlah malu pohon pertolongan,","Itulah ertinya persahabatan abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101483,"Hilangkan mengantuk mengunyah kuaci,","Barulah mata terbuka luas;","Ikatan perkahwinan ikatan suci,","Harus bermula hati yang ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101484,"Bersahut-sahutan suara angsa,","Berenang di kolam berkawan-kawan;","Bahasa Melayu jiwa bangsa,","Hargai dan hayati sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101485,"Berterbangan indah sirama-rama,","Menghisap madu bunga-bungaan;","Kesedihan sahabat dikongsi bersama,","Semoga Tuhan menganugerahi ketabahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101486,"Indah berkembangan bunga melati,","Di pinggir taman elok terjaga;","Kalau pun ada ketidakpuasan hati,","Adab dan sopan harus dipelihara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101487,"Pelbagai cara capai matlamat,","Jangan lakukan sebarang penganiayaan;","Jasa perintis universiti keramat,","Pelajarilah pengorbanan yang telah ditunjukkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101488,"Diasah selalu parang berkarat,","Manfaat diperoleh apabila dijaga;","Persahabatan abadi tidak bersyarat,","Saling prihatin akhlak mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101489,"Tempat dituju di Sungai Mati,","Bandar Maharani Bandar DiRaja;","Pengorbanan sebenar harus dihayati,","Barulah ibadah menepati maksudnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101490,"Indah terbentuk burung berterbangan,","Riang dan ria melayang bebas;","Sayang anak berikan perhatian,","Jangan dimanjakan melampaui batas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101491,"Cahaya mentari menimpa dedaunan,","Percikannya menyilau memukau sukma;","Rasa bahagia beribadah korban,","Gembirakan fakir miskin hadir bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101492,"Manis rasanya sipisang kaki,","Di Brastagi subur berkembangan;","Gigih berusaha mencari rezeki,","Dengan kudrat dikurniakan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101493,"Berjalan-jalan lapangkan fikiran,","Selama ini otak penuh sarat;","Hati tenang menempuh persaraan,","Terus berbakti kepada masyarakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101494,"Mencari rezeki di hutan belukar,","Menjelang senja hidupkan bara;","Mengetahui sahabat hidupnya sukar,","Usah dibiarkan sendirian sengsara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101495,"Gunung berbalam di tanah seberang,","Diliputi awan cahaya melantun;","Budayakan menghargai budi orang,","Amalan mulia masyarakat bertamadun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101496,"Tenangkan hati usahlah gundah,","Nyanyian seniman jadi pengubat;","Kenangan semalam sungguhlah indah,","Silaturrahim persahabatan kukuh terpahat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101497,"Ke tebing sungai menyejukkan diri,","Tanpa merosakkan nilai kejernihan;","Sahabat handai berhati suci,","Pertemuan semula mendapat keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101498,"Enak didengar buluh perindu,","Angin berhembus terasa nyaman;","Tiada lagi yang lebih dirindu,","Selain pulang ke kampung halaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101499,"Bertahun lama tidak ketahuan,","Pergi merantau ke pelbagai negeri;","Persahabatan tulus terus berpanjangan,","Di usia begini sahabat yang dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101500,"Rahmat yang turun bersama hujan,","Tanah yang kering menjadi basah;","Mengira hari tarikh pertemuan,","Menanti sahabat yang lama terpisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101501,"Berkilau-kilauan gemerlapnya bintang,","Burung pungguk rindukan bulan;","Tempat jatuh lagi kan dikenang,","Ini kan pula kampung halaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101502,"Bila berkawan biarlah beribu,","Teman sejati sukar diperolehi;","Lipatan kenangan meresap di kalbu,","Membina kedewasaan keperibadian sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101503,"Mulakan perjalanan ke Kampung Lepar,","Hendak membeli langsat dan duku;","Kemuliaan hati jelas terpapar,","Daripada tutur kata dan tingkah laku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101504,"Berarak panjang kambing biri-biri,","Bergerak memutih ke suatu arah;","Alangkah indah permulaan hari,","Tanpa herdikan penuh amarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101505,"Beralah bukannya bererti lemah,","Berdiam bukannya bererti bungkam;","Bertuturlah dengan lembut dan hemah,","Hubungan mesra sebarkan salam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101506,"Gadis manis senyum tersipu,","Ditegur jejaka gagah terbilang;","Sejarah tidak pernah menipu,","Pelajari daripadanya melangkah cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101507,"Laluan bukit sama-sama dituruni,","Sambil menghirup hembusan bayu;","Usia emas harus dihargai,","Tenangkan fikiran beriadah selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101508,"Padang terbentang indah pemandangan,","Tempat kuda belajar berlari;","Kutiplah daripada orang berpengalaman,","Banyak ibrah boleh dipelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101509,"Wajah bersih diconteng arang,","Arang dikutip dari halaman;","Usah dilupakan jasa orang,","Kelak jiwamu tiada kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101510,"Bukakan hati melihat dunia,","Cabaran dan peluang harus disedari;","Ada orang hilang kemanusiaannya,","Tamak haloba menguasai diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101511,"Lembu diikat dengan tali,","Tali diikat di pohon sena;","Sampai masanya kita kembali,","Kampung halaman abadi di alam sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101512,"Mari beriadah diwaktu petang,","Demi kesihatan bertahan lama;","Pemimpin pergi pemimpin datang,","Ilmu dan pengalaman dikongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101513,"Menjelang senja dirikan khemah,","Ikan di sungai terperangkap di bubu;","Erti persahabatan perlu diterjemah,","Dapat dirasai di dalam kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101514,"Terbang indah sirama-rama,","Kuntum melati tempat persinggahan;","Hari jadi sahabat dirai bersama,","Umur meningkat perkukuh persahabatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101515,"Sentiasa berfikir berpakaian kemas,","Tanpa disedari baju terbalik;","Mudah merajuk siwarga emas,","Pandangan peribadi dianggap terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101516,"Buah betik di atas papan,","Keenakannya dinikmati terasa puas;","Politik adalah sebahagian kehidupan,","Kejujuran perlu dijadikan asas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101517,"Dihujung tahun pelbagai tawaran,","Walaupun teruja harus berhati-hati;","Banyakkan berzikir menenang fikiran,","Terasa diri Tuhan mengasihi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101518,"Makanan tradisi masih disanjung,","Berkhasiat dan lazat ciri utama;","Keluhuran budi sentiasa dijunjung,","Mengenang budi dituntut agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101519,"Belayar jauh kapal pesiaran,","Singgah berlabuh di Pulau Tahiti;","Tenangkan hati kuatkan kesabaran,","Masalah yang datang ujian Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101520,"Usia tua kurang pendengaran,","Pemeriksaan doktor jadual dituruti;","Setelah bersara peroleh ketenangan,","Membaca menulis usahlah dihenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101521,"Pedas enak sambal kerang,","Sedikit berbeza rasanya lokan;","Usah biar kepala dipijak orang,","Maruah diri perlu dipertahankan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101522,"Hari-hari berlaukkan ikan,","Kepala nelayan berikatkan kain;","Aktiviti riadah perlu diteruskan,","Kesihatan diutamakan persahabatan terus terjalin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101523,"Berhati-hati terpijak paku,","Ada orang ingin menganiaya;","Usah difikirkan yang belum berlaku,","Hadapi hari ini dengan sebaiknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101524,"Siakap enak seperti senohong,","Gelama juga menjadi kesukaan;","Orang yang hidup suka berbohong,","Keutamaan hatinya adalah keduniaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101525,"Setelah bercuti marilah pulang,","Keadaan di rumah semoga selamat;","Jangan dilupa jika berhutang,","Langsaikan hutang hidup pun berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101526,"Sudah lama tidak ke Melaka,","Hari ini dikunjungi lagi;","Janganlah kita sampai terleka,","Jika terleka kitalah yang rugi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101527,"Bunga musnah dikerjakan kera,","Sayang sungguh bunga melati;","Apabila orang terlalu berkira,","Tiadalah lagi keikhlasan di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101528,"Desa yang tinggal hanyalah Lepau,","Semuanya hilang tidak berganti;","Apabila meminta usahlah melampau,","Hanya mendedahkan buruknya pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101529,"Anak bertanya usah disekat,","Rangsang mindanya untuk berfikir;","Usah berhenti mendengar nasihat,","Mengelak jiwa menjadi fakir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101530,"Dalam hidup bukan sekadar makan,","Banyak tanggungjawab perlu dilaksana;","Apabila berlaku perkara yang melukakan,","Berhenti seketika fikir tindakan sewajarnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101531,"Dunia yang aman kita amati,","Kenikmatan hidup dalam keharmonian;","Pemandangan indah yang dinikmati,","Bersyukurlah dengan mata yang dianugerahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101532,"Keindahan taman bunga melati,","Menyembuh resah hati yang gundah;","Kerana kesantunan orang mengingati,","Apabila di puncak teruslah merendah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101533,"Ilmu digali mencipta peralatan,","Budaya inovasi keperluan terkini;","Semasa nikmati kelazatan makanan,","Usah lupakan nelayan dan petani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101534,"Tumbuh subur daun pegaga,","Ulam-ulaman asas kesihatan;","Persahabatan tulus sangat berharga,","Usah ambil kesempatan atas kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101535,"Janganlah menunjuk-nunjuk dengan kuasa,","Bila-bila masa sahaja boleh terburai;","Walaupun usia di penghujung masa,","Persahabatan terbina usah dilerai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101536,"Sungguh manis sibuah nangka,","Rasanya lazat bila dimakan;","Rezeki yang datang tidak disangka,","Allah janjikan balasan kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101537,"Sudah terkenal nama Kemunting,","Tempat jalani hukum bernegara;","Sediakan wasiat sangatlah penting,","Untuk yang tinggal terus sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101538,"Melayang-layang siburung camar,","Ada ketikanya terbang rendah;","Apabila reputasi sudah tercemar,","Meyakinkan kembali tidaklah mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101539,"Awan mendung suasana redup,","Keadaan sepi terasa pilu,","Teruskan pembelajaran sepanjang hidup,","Segala aspek dilalui dengan ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101540,"Lauk yang ringkas dicampur kicap,","Lebih enak gulai itik serati;","Nilai persahabatan tidak terucap,","Biarpun orang lain tidak mengerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101541,"Terbang tinggi siburung helang,","Matanya pantas mengintai rezeki;","Setelah berpurnama tidak pulang,","Kampung halaman dirindu kini dijejaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101542,"Orang sombong cakap berdegar-degar,","Letih mendengar jiwa merana;","Walaupun kawalan pergerakan dilonggar,","Kawalan kendiri harus terlaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101543,"Marilah berdoa dicucuri rahmat,","Semoga kehidupan terus sejahtera;","Syukur kepada Tuhan segala nikmat,","Yang diberikan tidaklah terhingga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101544,"Bergotong royong tenaga dikerah,","Masa sedikit tidaklah rugi;","Syukur kepada Tuhan Maha Pemurah,","Musim buah-buahan kini tiba lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101545,"Banyak manfaat pohon gaharu,","Kulitnya dikikis daunnya diramas;","Bermulalah detik kehidupan baharu,","Sebagai pesara dan berwarga emas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101546,"Berbalam-balam banjaran pulau,","Indahnya disirami cahaya purnama;","Meski zahirnya tidak terjangkau,","Dihati nurani tetap bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101547,"Yang bermula akan berakhir,","Keputusan dicapai dipertimbang adil;","Sanjungan dan pujian yang dizahir,","Menjadikan berasa teramat kerdil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101548,"Banyak yang bersarang di dalam dada,","Semuanya perlu untuk diuruskan;","Dengan sumber yang kita ada,","Berhati-hati menentukan pelaburan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101549,"Mari ubati hati yang pilu,","Semoga jiwa peroleh ketenangan;","Walaupun pedih kisah nan lalu,","Usah diungkit hanya melukakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101550,"Sungguh wangi buah kuini,","Pohonnya tumbuh di dalam kebun;","Usah ambil mudah perkembangan terkini,","Walaupun kes positif kian menurun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101551,"Lautan dalam usah dijengkal,","Bahtera usang kini berlabuh;","Kian difikir tak tercapai akal,","Disangka baik berperangai hodoh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101552,"Lama tidak ke Pulau Sentosa,","Tampak dari tanah seberang;","Mengapa mudah lupakan jasa,","Jadilah insan yang hargai orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101553,"Jalan terhalang banyaknya duri,","Duri berceracak sepanjang dahan;","Jika nafsu menghantui diri,","Hilang pertimbangan orang berpendidikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101554,"Tiupan angin seperti berirama,","Bagai sarat membawa khabar;","Jangan diungkit cerita lama,","Jadikan pengajaran ambillah iktibar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101555,"Rabu 27 Bicara politik tidak pernah berakhir,","Pandangan berbeza jadi perbalahan;","Walau tidak bertemu secara zahir,","Ucapan selamat sudah menceriakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101556,"Api yang kecil menjadi kebakaran,","Menyusahkan kehidupan yang sedang harmoni;","Disaat semua orang hadapi kesukaran,","Jangan ada yang pentingkan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101557,"Di pagi hari mulakan perjalanan,","Menuju desa yang sudah berubah;","Eidul Fitri kini diraikan berlainan,","Banyak hikmah di sebalik musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101558,"Salam Eidul Fitr saya ucapkan,","Buat sahabat sentiasa dihati;","Berhari raya tak sekadar makan,","Silaturrahim diperkukuh sehingga mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101559,"Dengar arahan yang mutakhir,","Demi keselamatan dan kesihatan;","Menjelang Ramadhan kian berakhir,","Maafkan orang lain sifat kemuliaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101560,"Rasanya enak ikan kerisi,","Enaklah lagi ikan aya;","Pemergian sahabat semua menangisi,","Perginya dihari dan bulan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101561,"Sering berbicara bersama rakan,","Pelbagai isu terus didiskusi;","Peraturan ditetapkan bukan menyusahkan,","Demi kesejahteraan marilah dipatuhi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101562,"Malam likur dihiasi lampu,","Berwarna-warni cahaya tersebar;","Doa dan bantuan yang termampu,","Semoga ujian dihadapi dengan sabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101563,"Terbang indah sirama-rama,","Menyapa bunga sedang berkembangan;","Musibah kepada sahabat dikongsi bersama,","Ujian Tuhan menguji erti persahabatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101564,"Tingginya terbang siburung helang,","Ke sarang jua menjelang senja;","UKM menjadi lebih gemilang,","Warganya pertaruh jiwa dan raga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101565,"Resah menanti waktu ketibaan,","Terasa lenguh lama berdiri;","Usah ambil kesempatan pada kebaikan,","Kelak diketahui durjana sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101566,"Ke dalam hutan mari berburu,","Kalau bertuah dapatlah rusa;","16 Mei adalah Hari Guru,","Hargai mereka yang amat berjasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101567,"Sering bersedekah kepada yang fakir,","Amalan hidup dipandang mulia;","Kalau dendam tidak berakhir,","Hati yang tenang rosak binasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101568,"Udara bersih tiada jerebu,","Fenomena berlaku boleh dijangka;","Beruntunglah kepada yang punya ibu,","Kasih sayang melimpah tidak terhingga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101569,"Bernas juga pandangan Basiran,","Sahabat lama semasa remaja;","Mengambil mudah segala peraturan,","Hanya dedahkan diri kepada bahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101570,"Mari singgah di gerai makan,","Mi goreng mamak ditambah taugeh;","Walaupun kes semakin berkurangan,","Langkah keselamatan usah diremeh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101571,"Hormati peraturan untuk melawat,","Tanggungjawab hospital memberi rawatan;","Jangan pertikai ketetapan yang dibuat,","Keselamatan rakyat jadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101572,"Menuruti perkembangan turun dan naik,","Bimbang sekitaran ekonomi yang kelam;","Nuzul al-Quran detik yang terbaik,","Mencipta tamadun semesta alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101573,"Hati terbuka terima kekalahan,","Peluang terbaik buat introspeksi;","Dalam keadaan kita berjauhan,","Hanya doa restu dapat mengiringi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101574,"Sewaktu kecil bermain rakit,","Ternanti-nanti air yang bah;","Bila mendengar sahabat yang sakit,","Usah ditangguh untuk berziarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101575,"Sayup di seberang Gunung Daik,","Tanahnya subur ditanam lada;","Kelaziman baharu kesan yang baik,","Di sebalik musibah yang datang melanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101576,"Pembesar berangkat terus diiring,","Di bilik menunggu berehat dahulu;","Berita yang sampai perlu disaring,","Mengelirukan orang keadaan tak menentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101577,"Pendapatan diperoleh untuk diagihkan,","Golongan memerlukan tidak dilupa;","Kelebihan mengingati tarikh kelahiran,","Semakin dekat kembali kepada Pencipta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101578,"Mendengar berita petugas dilanggar,","Semangat berbakti usah terkulai;","Walaupun sekatan sudah dilonggar,","Kawal diri sendiri usah dilalai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101579,"Awan berarak di langit biru,","Angin bertiup terasa nyaman;","Jangan buat keputusan terburu-buru,","Nanti menyesal tidak berkesudahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101580,"Mengisi air dalam tempayan,","Tradisi lama kita warisi;","Ramai terjejas pendapatan harian,","Yang ada kelebihan marilah memberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101581,"Sawah bertingkat padi huma,","Kawasan berbukit tidaklah mudah;","Hari Pekerja diraikan bersama,","Ubah perspektif, bekerja itu ibadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101582,"Tinggi sayup terbangnya helang,","Mangsa dicari sebagai makanan;","Walaupun wabak semakin menghilang,","Berbudaya bersih jadikan amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101583,"Berhati-hati melangkahi lopak,","Berenang-renang pelbagai ikan;","Usah biarkan rayuan berinfaq,","Amatlah rugi sengaja diabaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101584,"Di kuala sungai mengutip kerang,","Nikmati keindahan di Kuala Ibai;","Walaupun kes positif semakin berkurang,","Amalan terbaik usahlah diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101585,"Minyak sapu buah pala,","Hasil rimba khasiatnya banyak;","Bulan Ramadhan ladang pahala,","Amalkan bersedekah kepada yang berhak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101586,"Pemandangan indah di Indrapuri,","Padang hijau indah pemandangan;","Jasa orang haruslah disyukuri,","Usah meminta yang bukan-bukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101587,"Budaya orang muda pergi berkelana,","Cari pengalaman membina kehidupan;","Kalau ingin harmonikan suasana,","Banyakkan sabar nada suara direndahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101588,"Kejujuran orang usah diragui,","Apatah lagi untuk menganiaya;","Ramadhan Kareem yang sentiasa dirindui,","Manfaat segala kelebihan dan kemuliaannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101589,"Hidup terkurung jiwa pun resah,","Namun banyak boleh dilaksana;","Semakin ramai hidupnya susah,","Mari berinfaq, bersedekah dan menderma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101590,"Kambing terbaik hidup di gurun,","Enak dimakan khasiat berganda;","Meskipun kes Covid kian menurun,","Jangan pula kita berleka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101591,"Semasa kecil bermain guli,","Bersama kawan pelbagai bangsa;","Golongan miskin yang tidak dipeduli,","Tanggungjawab kita usahlah berkira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101592,"Terbang di awan siburung helang,","Mencari mangsa di tengah sunyi;","Usah dihitung apa yang hilang,","Lihat kebaikan banyak tersembunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101593,"Masakan enak ditambah lada,","Pedas sedikit menjadi kelaziman;","Biarpun kebiasaan sudah berbeza,","Kepentingan umum letak di hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101594,"Jadikan panduan ajaran Al-Quran,","Pesanan Tuhan buat manusia;","Dalam hidup perlukan kejujuran,","Barulah sentosa, damai dan bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101595,"Di dalam belukar susah diteka,","Sebarang kemungkinan boleh bermunculan;","Disaat sukar usahlah bersengketa,","Cintakan negara harus jadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101596,"Mi rebus enak di Tanjung Agas,","Tempat singgah sambil berehat;","Hormati tanggungjawab para petugas,","Laksana amanah sejahterakan rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101597,"Banyak khasiat durian Belanda,","Boleh dimakan bersama bubur;","Ramai terkesan wabak melanda,","Bantu membantu semakin menyubur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101598,"Wataknya lucu memang kelakar,","Suasana ceria terhiburlah hati;","Walaupun kehidupan semakin sukar,","Banyak hikmah yang boleh diperolehi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101599,"Terus dipelihara keutuhan ikatan,","Tali silaturrahim yang telah dirangkai;","Usah diremeh tanda kesakitan,","Disaat wabak yang sukar dirungkai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101600,"Sesekali keluar bertemu rakan,","Usah disekat usah dilarang;","Kesahihan berita harus dipastikan,","Supaya tidak menggusarkan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101601,"Hujan lebat bersama petir,","Cari perlindungan di bawah bangunan;","Disaat kehidupan menjadi getir,","Berhati-hati dengan yang ambil kesempatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101602,"Marilah belajar disepanjang hayat,","Ilmu bertambah hadapi cabaran;","Demi kesihatan dan kesejahteraan rakyat,","Sambungan PKP tidak dapat dielakkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101603,"Usah dimakan ikan yang mentah,","Bimbang jejaskan tahap kesihatan;","Patuhi segala arahan pemerintah,","Kesejahteraan rakyat menjadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101604,"Subur tegak berdiri pohon sena,","Bermanfaat sentiasa jadi teduhan;","Virus Corona menyerang memberi makna,","Kesedaran manusia tentang kuasa Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101605,"Jarang dijumpai buah rokan,","Bila ditemui cubalah dirasa;","Sahabat yang berkesempitan usah dibiarkan,","Ini ujian buat kita semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101606,"Hidup terpenjara dilingkungi kawat,","Pengajaran diperolehi meresap dijiwa;","Dalam keadaan hidup yang gawat,","Masih ada yang tidak bertimbang rasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101607,"Melihat keanehan mulut ternganga,","Sambil memuji kebesaran Tuhan;","Selain berikhtiar teruslah berdoa,","Semoga segera dianugerahi kesembuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101608,"Panggilan tidak dikenali jangan dilayan,","Tipu daya haruslah diwaspadai;","Segala yang berlaku adalah ujian,","Bukan kebetulan malah aturan Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101609,"Setelah kemarau hujan pun lebat,","Panaskan badan di tepi bara;","Doakan kebaikan kepada sahabat,","Diberi tanggungjawab mentadbir negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101610,"Makan dan minum jangan dibimbang,","Disusun teratur di atas kain;","Kalau pun tidak mampu menyumbang,","Usahlah menyusahkan tugas orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101611,"Pergi Sekincan nikmati mentarang,","Hasrat merasainya sekian lama;","Usah dipermainkan kesedihan orang,","Bayangkan berada di situasi yang sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101612,"Memanglah banyak kedai makan,","Bukan sekadar layanan yang ramah;","Hidup perlu nasihat menasihatkan,","Dengan sabar dan penuh berhikmah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101613,"Durian bergantungan sepanjang dahan,","Isinya manis kulitnya berduri;","Hubungan kemanusiaan mencipta keindahan,","Tidak mungkin muncul jika bersendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101614,"Pokok mawar batangnya berduri,","Kalau tercucuk memang berbisa;","Usah pentingkan diri sendiri,","Orang lain yang tidak selesa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101615,"Terbang damai sirama-rama,","Taman bunga indah berseri,","Dengan ikhtiar dan doa bersama,","Bencana dihadapi dapat diatasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101616,"Di sebelah pagi sesi berkawat,","Disiplin pasukan perlu dipatuhi;","Semakin dilarang itu yang dibuat,","Hanya pentingkan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101617,"Pakaian malam berenda-renda,","Diperbuat dengan sulam tangan;","Wabak Corona belum lagi reda,","Kerjasama warga amat diperlukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101618,"Masa yang ada perlu diisi,","Pergi ke sungai memancing ikan;","Pemergian sahabat usah ditangisi,","Tuhan menyayangi doa dipanjatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101619,"Cendul dinikmati di tepi lembah,","Lebih enak dicampurkan tapai;","Memang sukar untuk berubah,","Melainkan jelas yang perlu dicapai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101620,"Usah berperangai seperti ular,","Lidah bercabang liciknya helahan;","Banyak berita diragukan yang tular,","Terima dengan penuh pertimbangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101621,"Cahaya pagi zahirkan kedamaian,","Angin lembut hingga terlena;","Dukacita dengan berita kematian,","Semoga berbahagia di alam sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101622,"Banyak khasiat daun pegaga,","Tumbuh melata di kaki bukit;","Kasihi diri sendiri serta keluarga,","Patuh arahan bendung wabak berjangkit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101623,"Budayakan membalas jasa dan budi,","Bangsa berjiwa hargai pahlawan;","Usah diutama kepentingan peribadi,","Kepentingan ramai perlu didahulukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101624,"Usah bertengkar elak sengketa,","Cari persamaan daripada perbezaan;","Bertawakal memanglah pegangan kita,","Elak mara bahaya suruhan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101625,"Usah bertengkar elak sengketa,","Cari persamaan daripada perbezaan;","Bertawakal memanglah pegangan kita,","Elak mara bahaya suruhan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101626,"Apabila janji sering dimungkir,","Jauhkan diri supaya berjarak;","Dendam kesumat tidak berakhir,","Terjemahan hati yang telah rosak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101627,"Belajarlah daripada Imam Shafie,","Ilmunya menjadikan kehidupan yang berkat;","Orang bijaksana sentiasa dihargai,","Pertimbangan mendalam kebaikan masyarakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101628,"Bunga orkid bunga melati,","Pelbagai warna menyegarkan udara;","Orang yang sakit harus dilawati,","Bahagiakan hatinya berasa gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101629,"Usah diprasangka orang yang kaya,","Usaha yang gigih rezeki digali;","Pulang ke desa berasa bahagia,","Segala kenangan terungkap kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101630,"Kepada ibu bapa haruslah taat,","Supaya menjadi anak yang berguna;","Nasihat diberi harus dimanfaat,","Supaya sejahtera kehidupan semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101631,"Sahabat lama bernama Raai,","Kamera disandang merakam kejadian;","Tiada bahasa yang lebih dikuasai,","Melainkan senyuman mencetus kemesraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101632,"Rehatkan diri barang seketika,","Merenung semalam dengan teliti;","Penjawat awam harus beretika,","Usah berbuat sesuka hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101633,"Terbang jauh siburung camar,","Pulang ke labuhan berkali-kali;","Kalau hati sudah tercemar,","Saranan perubahan tidak dipeduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101634,"Sebarkan ilmu usahlah kikir,","Siddiq, amanah, tabligh, fatonah;","Segala penantian sudah berakhir,","Mulakan tanggungjawab laksana amanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101635,"Kuat bekerja tanpa letih,","Usah amalkan tabiat bertangguh;","Telekomunikasi kini semakin canggih,","Biar perhubungan pun semakin kukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101636,"Peribadi dikenali menerusi perlakuan,","Ingin belajar seringlah bertanya;","Ikatan kekeluargaan harus diperkuatkan,","Biar diwarisi generasi seterusnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101637,"Bila berbicara biar berdiplomasi,","Maksud sampai tiada gurisan;","Segala bakti jadi legasi,","Segala yang baik jadikan ikutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101638,"Biarpun mahal penuhi kepuasan,","Kuih pilihan kuih keria;","Nilai merit menjadi landasan,","Pilih calon menepati kriteria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101639,"Walaupun letih terus digagahi,","Mencapai puncak tidaklah lama;","Kerjasama semua sentiasa dihargai,","Matlamat organisasi dijayakan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101640,"Menjadi baik lama kelamaan,","Didikan berterusan selagi berkudrat;","Amalan berwakaf jadikan keutamaan,","Ganjaran terbaik menanti di akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101641,"Marilah mengembara ke tanah Samarkand,","Tamadun gemilang di era dahulu;","Kehidupan bernegara harus diteruskan,","Pertikaian semalam sudah berlalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101642,"Merah menyala isi tembikai,","Untuk hilangkan rasa kedahagaan;","Pemimpin berganti usah dipertikai,","Keputusan dibuat dengan berperlembagaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101643,"Semasa kecil bermain guli,","Bermain guli di laman Nyonya;","Sudah lama mentarang dikenali,","Hari ini dapat rasai keenakannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101644,"Cuaca panas ais pun mencair,","Dahaga terasa semenjak pagi;","Kemelut politik bilakah berakhir,","Agenda kesejahteraan terganggu lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101645,"Yang sakit harus diziarahi,","Yang susah dihulurkan bantuan;","Sahabat sejati sentiasa diingati,","Walaupun jauh sering berhubungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101646,"Jika ditegur usah terasa,","Baiki diri yang sering berjerebu;","Penggubah lama masih berbisa,","Alunan lagunya menusuk kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101647,"Kemudahan lengkap sudah tersedia,","Bantulah rakan berkaki kudung;","Meskipun cuaca cerah ceria,","Politik negara masih diselaputi mendung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101648,"Langit cerah berwarna biru,","Pemandangan indah cetuskan ketenangan;","Setiap dilalui pengalaman yang baru,","Hargai peluang dijadikan pengetahuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101649,"Burung terbang dipipiskan lada,","Bidalan lama banyak pengajaran;","Beriadah perlu berpada-pada,","Jangan keterlaluan jejaskan kesihatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101650,"Bergurau senda monyet dan kera,","Makanan enak buah dan daunan;","Walimahtul urus anak saudara,","Dirai bersama keluarga dan taulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101651,"Usah diminum darah ular,","Amalan pelik yang perlu dihindari;","Wabak penyakit yang sedang menular,","Ada peringatan yang perlu diteliti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101652,"Dengar panduan dari penyelia,","Penyelidikan berhasil menjadi solusi;","Hormati yang tua adat mulia,","Jangan disusahkan kelangsungan institusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101653,"Kadangkala terasa diri teraniaya,","Sabar sahaja kuatkan peribadi;","Usah tamak dengan dunia,","Akhirnya rugi diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101654,"Lihat dahulu kanan dan kiri,","Sentiasa berfikir keselamatan jiwa;","Kalaupun ada pemikiran sendiri,","Hormatilah orang yang lebih tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101655,"Bersama meredah dalamnya lurah,","Sukar dilalui akar melilit;","Kepandaian sendiri adalah anugerah,","Usah jadi kacang lupakan kulit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101656,"Jalan berliku ke Kalabakan,","Hujan panas pemandangan berkaca;","Keikhlasan orang dapat dirasakan,","Daripada perilaku dapat dibaca.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101657,"Harus dijaga kesihatan gigi,","Senyuman manis jelas terpampang;","Keindahan Kundasang dijejaki lagi,","Pembangunan berterusan harus terancang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101658,"Burung merpati terbang berkawan,","Kongsi bersama rezeki terjana;","Menyantuni sahabat yang tidak berkemampuan,","Tanggungjawab sosial harus dilaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101659,"Tunjukkan prestasi terus diperhebat,","Pencapaian cemerlang jadi sasaran;","Beri semangat kepada sahabat,","Supaya tidak berasa keseorangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101660,"Tidak lama lagi kita berpuasa,","Bulan mulia banyakkan amalan;","Janganlah mudah berputus asa,","Berusaha terus Tuhan tentukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101661,"Seperti ada sesuatu yang melekat,","Hendak berjalan terasa berhalangan;","Isu semasa yang meresah masyarakat,","Universiti berperanan merungkai permasalahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101662,"Musim menuai di sawah bendang,","Padi dituai bersama dipukul;","Berat sungguh mata memandang,","Berat lagi bahu yang memikul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101663,"Bersungguh bukan bersambil lewa,","Pasti peroleh banyak kejayaan;","Teruslah berusaha tanpa kecewa,","Apa ketentuan pasrah kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101664,"Berehat seketika di Pujangga Puri,","Menuju destinasi di daerah Pekan;","Meskipun perjalanan separuh hari,","Demi silaturrahim undangan diraikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101665,"Semakin merosot bilangan sibadak,","Rasa tamak menguasai diri;","Fikirkan kepentingan orang bila bertindak,","Barulah kehidupan penuh harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101666,"indah perbukitan di Inderapuri,","Waktu pagi diselimuti kabut;","Tokoh ilmuwan harus dihargai,","Kebijaksanaan dan hikmahnya perlu direbut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101667,"Kalau sakit dapatkan ubat,","Tidak semudah seperti andaian;","Usah berputus asa wahai sahabat,","Teruslah berusaha Allah tentukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101668,"Sampai masanya harus bertemu,","Kekusutan terhurai tiada lagi terluka;","Ulama Nusantara berkongsi ilmu,","Hadir dan dengar dengan hati terbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101669,"Sempatkan masa bersarapan roti,","Kopi yang panas terhidang sama;","Sudah bertekad bulatkan hati,","Semoga yang direncana dapat dilaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101670,"Terasa halusnya daging landak,","Tidak dicuba tidakkan kenal;","Berikhlaslah sentiasa dalam bertindak,","Usah tunduk emosi tidak terkawal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101671,"Usah dilayan perasaan tertekan,","Kelak terlepas semua yang dimiliki;","Doa yang tulus sentiasa diharapkan,","Itulah erti persahabatan hakiki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101672,"Berhati-hati apabila makan,","Walaupun dihidang madu lebah;","Bila tanggungjawab tidak dilaksanakan,","Hilang amanah muncullah rasuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101673,"Manis sekali sibuah betik,","Semasa sarapan dijadikan hidangan;","Marilah kita berbaju batik,","Hari Khamis jadikan kebiasaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101674,"Manis sekali sibuah betik,","Semasa sarapan dijadikan hidangan;","Marilah kita berbaju batik,","Hari Khamis jadikan kebiasaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101675,"Menjadi batu disumpah Sang Kelembai,","Peristiwa berlaku di Daerah Marang;","Penderitaan orang usah diabai,","Segalanya ujian buat semua orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101676,"Golongan yang lemah perlu diperkasa,","Supaya kehidupan kembali terang;","Usahlah mudah berputus asa,","Idea dikemuka tidak diterima orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101677,"Janganlah segan untuk bertanya,","Sekiranya sesat kembali ke pangkalan;","Kesakitan ditanggung terasa beratnya,","Itulah ujian melatih kesabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101678,"Kalau berburu di dalam hutan,","Pantang larang ada yang empunya;","Pada saat menghadapi kesukaran,","Hulur bantuan dan berdoa setulusnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101679,"Sesekali memakan beras huma,","Baunya wangi menambah keenakan;","Tahun Baru Cina dirai bersama,","Simbol tikus lambang kebijaksanaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101680,"Kenangan silam tidak berakhir,","Akan diingati sehingga mati;","Rasa cinta kepada tanahair,","Ciri alami yang perlu dihormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101681,"Mari bermusafir bersama rakan,","Barulah persahabatan kukuh terbina;","Menziarahi yang sakit satu kemuliaan,","Doakan kesembuhan dan ketabahan jiwanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101682,"Manfaat diperoleh dengan berzikir,","Jiwa tenang mendekati Tuhan;","Berkat usaha dan cerdas berfikir,","Impian lama kini jadi kenyataan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101683,"Rasa gembira bertemu rakan,","Sambil menikmati minuman koko;","Perancangan rapi perlu dilakukan,","Apatah lagi aktiviti yang berisiko.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101684,"Membakar ikan gunakan arang,","Ikan dihidang di atas peti;","Jangan ambil kesempatan kebaikan orang,","Keburukan sendiri terbuka nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101685,"Kehidupan manusia ditentukan Tuhan,","Akhirnya semua kembali ke rahmatullah;","Usah bermegah dengan kelebihan,","Semua pinjaman oleh Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101686,"Langit indah berwarna biru,","Awan berarak alam pun teduh;","Perkahwinan membina keluarga yang baru,","Silaturrahim diperkukuh persaudaraan diperteguh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101687,"Memang berbangga jadi yang pertama,","Namun usah dilanggar perkara terlarang;","Menyedari kekurangan diri lebih utama,","Daripada hanya melihat kelemahan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101688,"Kalau bermula dengan murung,","Masa depan akan jadi kelabu;","Keazaman yang kuat menjadi pendorong,","Capai cita-cita terpahat di kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101689,"Air dingin di dalam gelas,","Minum berhati-hati agar selamat;","Tanggungjawab diberi sedia digalas,","Kesempurnaan amanah jadikan matlamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101690,"Yakin dengan Yang Maha Berkuasa,","Semai subur di dalam hati;","Pengiktirafan diberi kerana berjasa,","Contoh tauladan yang perlu diikuti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101691,"Tolong ambilkan daun bidara,","Banyak manfaat lagi berkah;","Lembutkan lidah ketika berbicara,","Kasih sayang Allah datang melimpah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101692,"Fikiran yang kosong perlulah diisi,","Ide yang bernas apabila ditanya;","Balaslah budi kepada institusi,","Ilmu diperoleh tak ternilai harganya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101693,"Usahlah diceroboh lubuk ikan,","Cara yang ganas usah ditiru;","Sampai jodoh sudah ditakdirkan,","Tabah hati tempuh kehidupan baharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101694,"Berbaring malas dengan angan-angan,","Hanya mengenang nasibnya malang;","Kuat keyakinan dengan perancangan,","Kejayaan menanti dengan gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101695,"Dilayan mesra bagai sang puteri,","Kasih sayang diberi dengan melimpah;","Hari ulang tahun harus disyukuri,","Semoga menjadi seorang yang solehah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101696,"Pergi ziarah bawalah cenderamata,","Tuan rumah terima senangnya hati;","Sumbang kembali ke alma mater,","Itu tandanya membalas budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101697,"Mendengar titisan hujan menitik,","Semakin mengocak hati yang nanar;","Memanglah mudah memberi kritik,","Tanpa mengetahui permasalahan sebenar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101698,"Ke Tanjung Balai menaiki feri,","Di tengah laut nyamannya hawa;","Menilai pencapaian tugasan diberi,","Ada kalanya menekan jiwa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101699,"Tinggallah ranting sebatang pohon,","Di padang pasir sepi dan sunyi;","Setelah berpisah berpuluh tahun,","Kenangan lalu amat dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101700,"Suasana hingar bingar di pasar,","Dari pagi hingga ke malam;","Nasihat menasihati usah digusar,","Tanda kasih yang sangat mendalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101701,"Ingin menjejaki tanah Pekalongan,","Tempat asal keturunan keluarga;","Usah diabai rayuan pertolongan,","Atasi kesempitan orang sifat mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101702,"Mulakan tahun baru dengan tekad,","Satukan azam warga universiti;","Mengenal pasti setiap bakat,","Tentukan matlamat digapai nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101703,"Itik salai di Pekan Rembau,","Terkenal lazatnya jadi kegemaran;","Di penghujung tahun masa mengimbau,","Kekuatan kelemahan dijadikan panduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101704,"Kalau pergi ke Tanjung Rokan,","Singgah sebentar rumah Encik Bihie;","Bermula dengan keikhlasan sesuatu dilakukan,","Natijah yang baik akan diperolehi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101705,"Banyak khasiat memakan badam,","Nilai perubatan ada di pucuk pauh;","Persahabatan sejati tidak terpadam,","Walaupun berzaman berpisah jauh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101706,"Bila berjauhan berbau wangi,","Bila berdekatan sering bersengketa;","Guru berjasa ayuh kunjungi,","Menjadikan kita insan berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101707,"Sahabat lama berkunjung ke mari,","Menyambung silaturrahim lama berpisah;","Janji yang dimeterai jika dimungkiri,","Sesal kemudian menjadi padah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101708,"Destinasi esok ke Alor Gajah,","Guru berjasa sesekali dilawati;","Personaliti mulia terpancar di wajah,","Tutur kata tulus muncul dari hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101709,"Merenung purnama penuh keindahan,","Puisi tertulis lantunan jiwa;","Hormati perbezaan raikan kepelbagaian,","Kehidupan bernegara menjadi bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101710,"Kalau tersasar rujuklah peta,","Jika tidak bertambahlah lelah;","Antara diperkata dengan dikota,","Orang jujur tidakkan bercanggah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101711,"Jarang ditemui buah rambai,","Cuba mencari bila ke Kedah;","Kesihatan diri usah diabai,","Supaya selesa terus beribadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101712,"Mencari dingin di Sungai Tekali,","Serabut fikiran menjadi kosong;","Tanpa mendekati tidaklah dikenali,","Lebih mengerti dengan berhubung langsung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101713,"Suasana misteri di dalam gua,","Air menitis tidak berhenti;","Tanjung Balai dikunjungi jua,","Dulu dengar nama kini dijejaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101714,"Ke Kota Palembang ayuh berkunjungan,","Tamadun bangsa harus ditekuni;","Kebijaksanaan, kesabaran dan juga keimanan,","Adalah asas kebahagiaan yang abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101715,"Berfikir serius berkerut dahi,","Idea diutara ada yang tentang;","Usah disesali segala yang ditempuhi,","Semuanya jadikan kita lebih matang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101716,"Kesihatan dijaga berjalan kaki,","Memperkuat tenaga tingkatkan stamina;","Kota Palembang kini dijejaki,","Harus dipelajari sejarah tamadunnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101717,"Berdikit-dikit langkah kura-kura,","Arnab berlari sambil berlagu;","Menziarahi yang sakit menghibur lara,","Menyuntik semangat tabahkan kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101718,"Sebelum tidur mari bercerita,","Cucu gembira tidak terperi;","Kebahagiaan sendiri boleh dicipta,","Jangan diharap orang yang memberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101719,"Ramai berkunjung ke Pasar Karat,","Mencari barang lama dan baharu;","Sahabat sejati tiada bersyarat,","Kemesraan tidak berubah sejak dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101720,"Memohon kerja mengisi borang,","Borang diisi dengan teliti;","Usah didengar hasutan orang,","Hanya merosakkan jiwa sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101721,"Pergi ke pasar membeli kembung,","Banyak membiak di Selat Melaka;","Apalah ertinya bersikap sombong,","Segala yang dimiliki pinjaman belaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101722,"Lembah yang dalam sanggup dituruni,","Misi yang besar digalas semua;","Masyarakat terpinggir harus disantuni,","Barulah dirasai kemakmuran bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101723,"Berbau harum bunga melati,","Angin bertiup segar di taman;","Kenalilah sahabat yang sejati,","Usah diperdaya kepura-puraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101724,"Mulakan perjalanan diwaktu pagi,","Singgah di Nilai mencari hidangan;","Negeri Di Bawah Bayu dikunjungi lagi,","Menyambung muhibbah dan rasa persaudaraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101725,"Mulakan perjalanan diwaktu pagi,","Singgah di Nilai mencari hidangan;","Negeri Di Bawah Bayu dikunjungi lagi,","Menyambung muhibbah dan rasa persaudaraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101726,"Jiwa yang kotor sentiasa gelisah,","Suka runtuhkan kebahagiaan orang;","Diawal hidup memanglah susah,","Susun strategi dengan terancang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101727,"Sedang merancang melancong ke Balkan,","Ahli keluarga bawa bersama;","Undangan walimah harus diraikan,","Peluang bertemu kenalan lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101728,"Melintasi kehijauan di kawasan Behrang,","Rasa ngeri dimalam hari;","Mengapa mudah dihasut orang,","Manfaatkan akal dan kematangan sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101729,"Lidah kelu mendengar hujahan,","Semua penipuan dapat disekat;","Orang tamak tiada perasaan,","Mesti dapatkan walau tidak berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101730,"Menyentuh jiwa lagu Kehilangan,","Suara merdu menghela nafas;","Kampung halaman hanyalah kenangan,","Hiba di hati tidak dapat dilafaz.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101731,"Menadah air di dalam baldi,","Untuk bersedia hadapi kekeringan;","Sentiasa fikirkan kepulangan yang abadi,","Apa bekal persiapan telah disediakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101732,"Usahlah terfikir untuk menganiaya,","Hindarkan nafsu suka membuli;","Apabila hati diracuni dunia,","Kebaikan orang tidak dipeduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101733,"Ingin berkunjung ke Kota Karachi,","Kota sibuk di negara Pakistan;","Perkahwinan adalah ikatan suci,","Harus dipelihara dengan penghormatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101734,"Alangkah bahagia hidup yang aman,","Hormat menghormati saling membantu;","Amatlah pedih melihat kezaliman,","Tanyakan diri bagaimana dapat membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101735,"Bangun pagi lihatlah rerumput,","Ada mesej dan juga peringatan;","Hak peribadi harus dituntut,","Namun fikirkan juga soal keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101736,"Ziarah orang sakit usahlah lama,","Berikan masa untuk kerehatan;","Pergi bermusafir galakan agama,","Syukuri ciptaan Tuhan jadi pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101737,"Menanam kacang menanam lada,","Usahakan sendiri untuk dimakan;","Hargai sumber kehidupan yang ada,","Air setitik pun jangan dibazirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101738,"Batik dan tenun warisan bangsa,","Jadikan lambang kehalusan budi;","Usahlah terfikir mencari mangsa,","Menolong orang mulialah peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101739,"Mengunjungi sahabat di Menjalara,","Akan melewati Kampung Selayang;","Dunia ini biarpun sementara,","Indahkan dengan kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101740,"Badan yang letih mudah terlena,","Apatah lagi ada sandaran;","Manusia yang rakus dipandang hina,","Sering lupa pesanan kesyukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101741,"Ada orang memakan lipan,","Hidup-hidup dikunyah selamba;","Biar pun digugat dalam kehidupan,","Teruskan perjalanan dengan waspada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101742,"Usah bersedih dengan kekalahan,","Banyak pengajaran boleh diperhati;","Ambil kesempatan atas kesusahan,","Amalan buruk peribadi sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101743,"Waktu petang berjuadahkan bubur,","Bubur dihidang di dalam buluh;","Tanah Sabah yang sangat subur,","Perhubungan pelbagai etnik harus diperkukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101744,"Bumi Sabah penuh keindahan,","Destinasi utama pelancongan negara;","Tamak haloba sifat keterlaluan,","Keberkatan langsung tidak diperkira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101745,"Indah memutih kambing biri-biri,","Sama seperti pemandangan kelmarin;","Orang tamak usah dihampiri,","Tiada belas kepada yang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101746,"Ingin sekali jelajahi Samarkand,","Sedang pertimbang beberapa destinasi;","Kota Hatyaai hari ini ditinggalkan,","Banyak pengajaran dapat diperhalusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101747,"Di pesisir pantai menunggang kuda,","Kuda dipelihara berpasang-pasangan;","Hargai segala sumber yang ada,","Dipelihara diperkembang produk pelancongan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101748,"Membakar arang menjadi bara,","Manfaatkan kepanasan untuk kebaikan;","Ke bumi Siam kini mengembara,","Memahami budaya menghormati perbezaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101749,"Usahlah gemar bermain belati,","Silap bermain tangan terluka;","Segala pertelingkahan boleh dihadapi,","Asalkan dengan hati yang terbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101750,"Simpan mempelam di dalam peti,","Di dalam peti sekati beras;","Hapuskan dendam di dalam hati,","Kelak merosak fikiran yang waras.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101751,"Pengunjung dikalungi bunga malai,","Permaidani merah turut dibentang;","Hala tuju organisasi perlu dinilai,","Supaya bersama perubahan yang datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101752,"Berhati-hati hadapi bencana,","Supaya keselamatan sentiasa diperhati;","Pengalaman lalu amat berguna,","Generasi muda harus mempelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101753,"Tanda hormat semua berdiri,","Raja bersemayam di atas takhta;","Orang berilmu merendah diri,","Sanjungan diterima tanpa dipinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101754,"Pergi berkelah di Sungai Tekali,","Walaupun jauh tetap diredah;","Setelah hati diluka berkali-kali,","Untuk kesembuhan tidaklah mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101755,"Berterbangan indah sirama-rama,","Mencari girang bunga-bungaan;","Maulidur Rasul disambut bersama,","Contohi Rasulullah jadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101756,"Bersedekah jangan sesudah kaya,","Kekayaan dimiliki hanyalah pinjaman;","Santuni anak yatim amalan mulia,","Dikasihi Rasul sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101757,"Ramai pelancong di dalam kota,","Setelah lapar segeralah makan;","Bahasa kebangsaan jiwa kita,","Perkasakannya siapa lagi diharapkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101758,"Yang pergi usah dikenang,","Teruskan hidup dengan berusaha;","Segala kebaikan jangan dibilang,","Nanti pengorbanan jadi sia-sia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101759,"Meski ibu bapa tiada di sisi,","Bersama sahabat hilanglah pilu;","Berhati mulia sejak azali,","Tanpa dipinta disanjung selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101760,"Mohon kerja isilah borang,","Lengkapkan kemahiran dalam berkomunikasi;","Gembira melihat kejayaan orang,","Hasil didikan penuh berdedikasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101761,"Penempatan gajah di Kuala Gandah,","Tidaklah jauh Pekan Maran;","Proses pembelajaran tidaklah mudah,","Kesukaran ditempuh penuh pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101762,"Merah kemerahan buah delima,","Masih bergantungan kukuh di dahan;","Impian dan ikhtiar dibawa bersama,","Kejayaan diidam jadi kenyataan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101763,"Untuk belayar angkatlah sauh,","Kini tinggallah tempat bermain;","Sahabat yang datang dari jauh,","Ladeni dengan sebaik yang mungkin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101764,"Pandang ke barat indahnya suria,","Waktu petang hati merawan;","Tanggungjawab berat dipikul bersama,","Keikhlasan hati membawa kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101765,"Sambil menunggang pijak pelana,","Larian laju memberi kepuasan;","Rasa gembira tanggungjawab terlaksana,","Kesempurnaan diperoleh dengan kebiasaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101766,"Melatih ketepatan dengan memanah,","Menunggang kuda melatih ketahanan;","Usah diikut rasa amarah,","Kelak menyesal tiada kesudahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101767,"Ekspedisi mendaki di Bukit Broga,","Sambil menikmati indahnya pemandangan;","Sahabat berpulang tanpa terduga,","Segala kebaikannya menjadi kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101768,"Singgah seketika di Kampung Lohan,","Dihidang nasi berlaukkan sepat;","Kesejahteraan dan kebahagiaan adalah pilihan,","Tentukan matlamat bertindak yang tepat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101769,"Usah dilupakan kaum kerabat,","Majlis kenduri dapat bersua;","Mari penuhi undangan sahabat,","Perkukuh persahabatan lama terbina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101770,"Bermandi manda di dalam kolah,","Air bertakung hingga ke pinggang;","Keperibadian sebenar telah terserlah,","Kebendaan juga dikejar orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101771,"Ukur baju biarlah padan,","Tidaklah nanti terasa sendat;","Kebebasan pasti ada sempadan,","Tanpa sempadan boleh tersesat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101772,"Hasil kerja sulam menyulam,","Setelah siap janganlah disimpan;","Usah terlalu mengenang silam,","Ambil pengajaran binalah masa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101773,"Hendakkan kejayaan usahlah malas,","Atasi kesulitan berganding bahu;","Tanda bersahabat yang tulus ikhlas,","Doakan kebaikan tanpa dia tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101774,"Udara bersih kini berjelaga,","Industri berkembang terburu-buru;","Pengalaman lalu sangat berharga,","Manfaatkan untuk generasi baharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101775,"Pergi memancing di pinggir kali,","Memancing ikan gunakan joran;","Perkara yang berlaku usah dikesali,","Kesilapan sendiri jadikan pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101776,"Asam pedas ikan ketutu,","Kampung nelayan di Kuala Pitas;","Meskipun jauh beribu batu,","Komunikasi berterusan usah terjejas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101777,"Pergi membeli pisang sesikat,","Jangan dilupa sebekas lada;","Pada usia semakin meningkat,","Hargai dan perkukuh persahabatan yang ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101778,"Nyaman menghidu bau yang wangi,","Bunga kesidang di pinggir jalan;","Kesilapan lalu usahlah diulangi,","Pelajaran berharga buat bekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101779,"Usah terpesona dengan wajah rupawan,","Tidaklah diketahui keadaan hakiki;","Hargai kehadiran seorang ilmuwan,","Pelajari pengetahuan dan pengalaman dimiliki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101780,"Puteri rupawan dari Majapahit,","Lautan diredah demi kecintaan;","Nyatakan kebenaran meskipun pahit,","Masa yang lalu penuh pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101781,"Kulitnya halus mukanya bujur,","Bila menari lentiknya jari;","Bicarakan isu secara jujur,","Penyelesaian terbaik itulah yang dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101782,"Orang berada berumah di Kuningan,","Orang miskin memerah kudrat;","Menang dan kalah adat pertandingan,","Susun strategi kuatkan tekad.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101783,"Erat bagai isi dengan kuku,","Persahabatan dan persaudaraan biar berpanjangan;","Bersabar dengan segala yang berlaku,","Redha dan rancang langkah ke hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101784,"Berhati-hati merawat luka,","Lukanya kecil terus merengek;","Terima takdir redha dan terbuka,","Allah gantikan dengan yang lebih baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101785,"Hari yang berlalu usia dimamah,","Kemerosotan kesihatan sudah suratan;","Setiap yang berlaku sentiasa berhikmah,","Pengajaran terbaik Tuhan anugerahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101786,"Dimasak bening silabu kundur,","Lauk sihat kesihatan bertahan lama;","Terlajak perahu boleh diundur,","Terlajak kata besar mudharatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101787,"Apabila Temasik dilanggar todak,","Semua berfikir kepala pun pening;","Fikirkan kesan apabila bertindak,","Turut dirasakan orang disekeliling.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101788,"Tuntutan pelik menjadi modal,","Untuk huru harakan kehidupan orang;","Bila bersedia melangkah dengan cekal,","Harungi segala cabaran yang merintang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101789,"Biar papa asalkan bergaya,","Habiskan wang tanpa bertakat;","Berubah pendirian sifat manusia,","Meskipun janji sudah pun terpahat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101790,"Hati terguris sukar disembuh,","Akan berpanjangan sedih dan duka;","Kasih yang subur pasti kan tumbuh,","Tingkah dan kata yang tidak meluka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101791,"Fikiran licik mencari helah,","Masalah ada tidak terselesaikan;","Melangkah masa depan dengan gagah,","Perancangan digaris peroleh kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101792,"Memanaskan badan dibakar arang,","Setelah berehat barulah pergi;","Hati yang rosak menyusahkan orang,","Kebaikan seringkali tidak dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101793,"Dimasak lemak bercili padi,","Udang, ketam, kerang dan ikan;","Bila bersama barulah mengenali,","Keperibadian sebenar yang disembunyikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101794,"Rezeki pagi mendapat belukang,","Di Kuala Johor memetik lada;","Elok sesekali toleh ke belakang,","Melihat kekuatan kelemahan yang ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101795,"Manis sekali buah ciku,","Masak sebiji di atas para;","Usah disesali telah yang berlaku,","Lihat ke hadapan positifkan minda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101796,"Musim duku langsat di Pulau Kati,","Di Kampung Losong orang berniaga;","Pagi yang indah lebih bererti,","Keluarga disantuni kebahagiaan terbina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101797,"Kuih bingka di dalam loyang,","Adunan pandan, tepung dan gula;","Kehidupan yang dilandasi kasih sayang,","Sangat indah dan begitu bermakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101798,"Mari bersarapan kopi secawan,","Teringin minum air tebu;","Bila berpergian sering kerinduan,","Cucu dikasihi jenuh menunggu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101799,"Banyak teka-teki dalam kehidupan,","Suratan lalu sesekali dibelek;","Perubahan tidak dapat dielakkan,","Uruskannya dengan cara yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101800,"Barang berkualiti sukar diperolehi,","Setelah diperolehi harus digunakan;","Usah dipersoal ketentuan Ilahi,","Muhasabah diri melangkah ke hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101801,"Enak nyaman air kelapa,","Mari dinikmati bersama teman;","Jasa orang usah dilupa,","Harus dihargai sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101802,"Memang terasa pedih dan pilu,","Perpisahan terjadi disebabkan alpa;","Usahlah diungkit kekurangan yang lalu,","Kebaikan diterima jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101803,"Di kawasan sesak pandulah perlahan,","Ramai berbelanja di Pasar Karat;","Nilai persahabatan harus dipertahan,","Perhubungan yang tulus tanpa bersyarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101804,"Sambil memandu makan kuaci,","Jadi bekalan pemanduan yang lama;","Ikatan pernikahan ikatan yang suci,","Ikhlaskan niat untuk hidup bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101805,"Buah rambutan diikat tali,","Dibeli oleh Baba dan Nyonya;","Keburukan diulang berkali-kali,","Sukar dipercayai ada kekesalannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101806,"Kalaupun ada ruang kembali,","Peristiwa lalu serik ditempuh;","Anak yatim dan miskin terus disantuni,","Masa depan mereka menjadi ampuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101807,"Usahlah mudah berasa kecewa,","Kalau perhubungan tidak berpanjangan;","Sejarah Tapak Tuan sungguh istimewa,","Meskipun legenda perlu terus dibuktikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101808,"Tiada persefahaman ikatan pun dibubar,","Pastinya berhikmah dan ada pengajaran;","Biar pun perjalanan sungguh mencabar,","Di penghujungnya menanti keindahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101809,"Ke Aceh Selatan perjalanannya lama,","Lutut tua terasa lenguh;","Hari Malaysia diraikan bersama,","Kesatuan bangsa terus diperkukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101810,"Segala yang berlaku sudah ketentuan,","Usah diungkap keaiban sendiri;","Sampai juga hajat ke Tapak Tuan,","Destinasi jauh yang banyak potensi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101811,"Usah diharap semua menyukai,","Itulah adat hidup di dunia;","Biarpun hati sering dilukai,","Sabar dan tenang hadapi segalanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101812,"Di pasir sungai terkumpul lokan,","Lokan lama rasanya pahit;","Hargailah pengorbanan yang dilakukan,","Usah jadi kacang lupakan kulit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101813,"Akhlak terbentuk bermula di rumah,","Suasana kekeluargaan terpaut erat;","Sahabat yang tulus sentiasa istiqamah,","Persahabatan sejati tanpa bersyarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101814,"Tidaklah pernah menjejaki Selama,","Masih boleh dicari unsur keunikan;","Melakukan kebaikan aspirasi semua,","Tidaklah sia-sia anugerah kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101815,"Memerangkap ikan gunakan bubu,","Ikan sungai dimasak kari;","Kalau bersahabat biar beribu,","Untuk bermusuh usahlah dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101816,"Manfaatkan bersama masa yang ada,","Mari berkelah di Sungai Sungkai;","Meskipun ikatan akad telah tiada,","Tali persaudaraan usahlah dirungkai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101817,"Pelanggan yang datang harus dilayan,","Untuk membuka luas pintu rezeki;","Berbudi bahasa lambang keperibadian,","Tanpanya hilang nilai diri hakiki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101818,"Masa yang berlalu jadi kenangan,","Sesekali datang emosi pun berselirat;","Perkahwinan mensucikan tali perhubungan,","Kekeluargaan dipererat tanpa bersyarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101819,"Usahlah ketawa berdekah-dekah,","Ketawa sekadarnya pun tidak berkurang;","Memberi senyuman adalah sedekah,","Kerana dengannya membahagiakan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101820,"Merah gelap kulit pulasan,","Ada juga rasanya masam;","Meskipun sukar buat keputusan,","Demi masa depan dibulatkan azam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101821,"Mendayung sampan ke Pulau Nasi,","Asalkan sampai biarpun lambat;","Pererat silaturrahim melalui telekomunikasi,","Gunakan berhemah dan bersahabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101822,"Sungguh jauh Pulau Maluku,","Tidak nampak dari Gunung Daik;","Usah disesali segala yang berlaku,","Muhasabah diri itu yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101823,"Membakar ikan gunakan arang,","Enak terasa dipanggang sebati;","Bertolak ansur kesusahan orang,","Barulah muncul rasa berempati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101824,"Singgah sebentar di Bandar Muazam,","Minum kopi di ruang tamu;","Awal tahun Hijrah kita berazam,","Menjadi lebih baik daripada tahun berlalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101825,"Dalam kegelapan memasang pelita,","Barulah muncul cahaya bersuluhan;","Hargai kemerdekaan nilai kita,","Tanpanya kita hilang keindahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101826,"Memang istimewa berlaukkan belangkas,","Ditambah dengan ikan bakar;","Hati yang luka tetap berbekas,","Boleh memaafkan dilupakan sukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101827,"Sambil memandu mengunyah kacang,","Boleh dikunyah yang sihat gigi;","Di ambang persaraan perlu merancang,","Supaya masa depan tidak kan rugi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101828,"Setelah menoleh ke kanan ke kiri,","Barulah melintas ke seberang sana;","Keindahan alam negara sendiri,","Kongsikan untuk dipromosi ke dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101829,"Kasih sayang terbina kian terungkai,","Hati sedih jiwa pun rawan;","Pesan orang tua harus dipakai,","Pengalaman dijana jadi pegangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101830,"Amat gembira dapat melawat,","Tempat bersejarah lama diimpikan;","Keputusan besar sukar dibuat,","Berhati-hati dan penuh penelitian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101831,"Semua dikata harus dikotakan,","Tidak beramanah harus disisih;","Usah menyindir lukakan perasaan,","Hanya melonggarkan ikatan kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101832,"Berlaukkan gulai ikan tenggiri,","Sambal kelapa ulam-ulaman;","Usah dibuka keaiban sendiri,","Kelak merugi mencalar keperibadian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101833,"Terbang indah sikunang-kunang,","Cahaya bertebaran menjadi hiburan;","Yang berlalu usahlah dikenang,","Penyesalan kemudian jadikan pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101834,"Merpati merah makan ditangan,","Makanan dicampur dengan kelapa;","Orang berjasa dilupakan jangan,","Bangsa besar wiranya tidak dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101835,"Pelbagai kisah dari Arakan,","Isu kemanusiaan semakin gundah;","Memang kesalahan boleh dimaafkan,","Untuk melupakan tidaklah mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101836,"Pelbagai pengalaman cerita dunia,","Dibuat pengajaran bagi yang mahu;","Luka ditangan nampak darahnya,","Luka dihati siapalah yang tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101837,"Masa berlalu berganti masa,","Pantai berubah resam alami;","Kalau dihati tiada lagi getar rasa,","Khabarkan setulusnya agar dimaklumi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101838,"Menatap keindahan di Indrapura,","Diserikan dengan taman melati;","Lunakkan suara bila berbicara,","Berbicara kasar pancaran budi pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101839,"Bukan mudah menangkap napuh,","Ilmu hutan yang belum matang;","Kesabaran adalah senjata terampuh,","Dalam menghadapi cabaran yang datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101840,"Sesekali teringin makan roti,","Roti dimakan dengan kari ikan;","Tidak semua harapan jadi realiti,","Segala berlaku ujian daripada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101841,"Sering dimomokkan dengan hantu,","Menyebabkan jiwa tiada keyakinan;","Usahlah mudah percayakan sesuatu,","Tanpa sandaran fakta kebenaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101842,"Matahari bersinar gemerlap cahaya,","Indahnya menjadi sumber inspirasi;","Jalani hidup sedaya upaya,","Meriahkan kehidupan teruslah berbakti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101843,"Selepas makan membasuh tangan,","Memakan buah di kedai Nyonya;","Meskipun ditemui ada kekurangan,","Terimalah dengan redha seadanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101844,"Mata mengantuk nikmat terlena,","Terlena pula dalam perjalanan;","Pertemuan keluarga besar akhirnya terlaksana,","Menjalin silaturrahim memperkukuh kekeluargaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101845,"Eidul Adha mari diraikan,","Solat di masjid dua rakaat,","Pengorbanan hidup membawa kebahagiaan,","Kepada kehidupan memberi manfaat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101846,"Bingka ubi tinggalan tradisi,","Enak juga kuih apam balik;","Usah dipersia hidup singkat ini,","Berbakti dan memberi amalan terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101847,"Menziarahi saudara di Kota Medini,","Perumahan teratur udaranya nyaman;","Apa pun kedudukan pada hari ini,","Usah dilupakan asal keturunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101848,"Berayun tinggi lambungan buaian,","Tidak terjejak sentuhan kaki;","Tidak semua diharap jadi kenyataan,","Syukuri sahaja segala yang dimiliki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101849,"Mertabak Raja terkenal di Pekan,","Bila dimakan berulang-ulang;","Lupakan masa silam yang melukakan,","Letak harapan masa depan gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101850,"Mendirikan solat menghadap kaabah,","Tumpu beribadah sepenuh sabar;","Menziarahi sahabat yang dilanda musibah,","Inilah erti persahabatan sebenar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101851,"Mari melihat keindahan dunia,","Lakukan apa jua hindari kecurangan;","Santuni orang lain dengan sebaiknya,","Usah dikira segala kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101852,"Terbang bebas sirama-rama,","Sayap beralun lembut dan tenang;","Sahabat yang ikhlas sentiasa bersama,","Meski sewaktu susah dan senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101853,"Bangun bersujud didini hari,","Rasa tenang menggenap ruang;","Sentiasa menjaga kesihatan diri,","Supaya tidak menyusahkan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101854,"Makan nasi berlauk halia,","Memang elok untuk kesihatan;","Maaf memaafkan amalan mulia,","Hanya berjiwa besar mampu melakukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101855,"Meliuk lintuk pohonan ru,","Tepi pantai nyiur melambai;","Bermulalah pola hidup yang baharu,","Semoga lebih tenang dan damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101856,"Di tempat orang jaga kelakuan,","Hormat menghormati yang muda yang tua;","Biarpun ada yang mendukacitakan,","Positifkan pemikiran hargai dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101857,"Pergi berjalan hingga ke Cangkat,","Berhenti seketika rawat keletihan;","Setinggi mana pun kedudukan dan pangkat,","Hilang penghormatan dengan keangkuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101858,"Makanan klasik tempe dan tapai,","Perlu dibuat dengan teliti;","Tidak semua hasrat segera tercapai,","Berusaha dan berdoa usah berhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101859,"Setiap saat usahlah leka,","Jika terleka kita yang rugi;","Walaupun seketika tetap berbahagia,","Masa yang ada amat dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101860,"Melihat ikan berkejar-kejaran,","Sambil minum kopi secangkir;","Masa lalu ada pengajaran,","Masa hadapan bersama diukir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101861,"Melihat keindahan waktu senja,","Menatap laut di Tanjung Tuan;","Hidup ini seperti roda,","Berpegang teguh takdir ketentuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101862,"Untuk menyambung gunakan tali,","Tali terbentuk berserat-serat;","Segala yang berlaku usah dikesali,","Ada hikmah yang sudah tersurat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101863,"Wajah yang manis bermuka bujur,","Akhlaknya baik jiwanya murni;","Lakukan tanggungjawab biarlah jujur,","Perkara yang benar usah diselindungi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101864,"Matahari senja indah di Matang,","Di sebalik bukit ia bersembunyi;","Pemimpin pergi pemimpin datang,","Segalanya sudah ditentukan Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101865,"Terbang tinggi siburung helang,","Melihat sekeliling mencari mangsa,","Usah digundah apa yang hilang,","Bersyukur sahaja apa yang ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101866,"Makanan tradisi sudah ternama,","Ketupat, lepat, lemang dan lontong;","Nasihat dan peringatan harus diterima,","Jadikan kita orang yang beruntung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101867,"Sesekali pergi memancing ikan,","Sambil berehat di tepi pantai;","Hujung minggu harus dimanfaatkan,","Perkukuh kekeluargaan pergilah bersantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101868,"Biar berlangsung secara alami,","Tidak mengapa bergerak perlahan;","Dunia ini indah untuk didiami,","Asalkan subur dengan kemanusiaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101869,"Menjelang malam memasang kandil,","Cahaya memancar hingga ke tepian;","Bila memilih hendaklah adil,","Walaupun sukar melawan perasaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101870,"Marak memerah luahan lahar,","Gunung berapi hangat memanah;","Bila melapor katakan yang benar,","Elakkan menjadi bahan memfitnah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101871,"Memotong lada dengan belati,","Memotong ranting dengan parang;","Bila berbicara berhati-hati,","Usah dihiris perasaan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101872,"Bila berbicara usahlah kasar,","Aib diri pada pandangan orang;","Perkara remeh usah diperbesar,","Kelak mencair kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101873,"Elakkan diri daripada menganiaya,","Perbuatan keji dibenci Tuhan;","Usahlah malu untuk bertanya,","Perkara yang masih menjadi persoalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101874,"Berkelip cahaya kunang-kunang,","Tidaklah perlu pasang pelita;","Jasa guru sentiasa dikenang,","Yang mencorakkan kehidupan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101875,"Merasa kelainan padi huma,","Enak juga menjamah ubi;","Santuni anak yatim ajaran agama,","Menyebar kasih sayang amalan Nabi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101876,"Enak sekali rasanya sup tulang,","Juadah istimewa sarapan pagi;","Tidak diketahui bila kita berpulang,","Taburlah bakti usah ditangguh lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101877,"Usah disentuh bunga berputik,","Kelak tangkainya terkulai patah;","Latihlah hati bersangka baik,","Barulah kehidupan menjadi indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101878,"Setelah di bawah pergilah naik,","Fitrah hidup sentiasa berputaran;","Untuk memilih pemimpin yang baik,","Harus jujur pilih yang paling berkelayakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101879,"Anak yang manja ingin dilayan,","Anak kecil dipakaikan lampin;","Kepimpinan sering dilanda ujian,","Untuk mengukur keutuhan pemimpin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101880,"Bunga indah bunga melati,","Rama-rama terbang indahnya warna;","Kembali ke rahmatullah adalah pasti,","Iringi doa kesejahteraannya di sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101881,"Cara lama kelapa dikukur,","Pengalaman baharu santan diperah;","Suburkan dijiwa rasa bersyukur,","Barulah kehidupan menjadi indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101882,"Kota Melaka berdiri teguh,","Sejarah lama untuk dikenang;","Kalau pun berilmu usahlah angkuh,","Kelak hilang penghormatan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101883,"Dalam kesukaran hulurlah pertolongan,","Semoga hidup terus diberkati;","Walaupun mesyuarat larut berpanjangan,","Keputusan terbaik yang mesti disepakati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101884,"Tenangkan hati berjalan di pekan,","Jangan mudah kita diperdaya;","Jati diri murni harus dipertahankan,","Walaupun berlainan adat dan budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101885,"Berhati-hati ular mematuk,","Terkena patuk meninggalkan kesan;","Pemimpin yang baik boleh dibentuk,","Asal sedar tanggungjawab kepimpinan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101886,"Dalam majlis orang bermadah,","Semua yang diperkata banyak pengajaran;","Dunia adalah tempat yang indah,","Andainya disuburkan dengan kemanusiaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101887,"Sering juga rasa terkilan,","Kepastian tiada teraba-raba;","Kerana bimbang akan kegagalan,","Tiada keinginan untuk mencuba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101888,"Indah sungguh pantai di Rompin,","Tidak jauh Bandar Muazam;","Bawalah diri ke daerah lain,","Perbaharui suasana segarkan azam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101889,"Sesekali pergi menonton wayang,","Tonton bersama taulan dan rakan;","Wajah riang jelas terbayang,","Majlis Eidul Fitri masih dirayakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101890,"Seronok sungguh mengutip lokan,","Ibu di rumah sedang menanti;","Banyak cabaran lakukan kebaikan,","Jelaskan niat di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101891,"Kadangkala berasa diri bersalah,","Tutur kata pastikannya sentiasa molek;","Sering bertenang hadapi masalah,","Akan muncul penyelesaian yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101892,"Mereda keributan di luar kota,","Lemas bekerja berhari-hari;","Mengotakan apa yang dikata,","Itulah kemuliaan nilai diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101893,"Berpagi-pagi ke Indera Puri,","Terlihat berenang sekumpulan todak;","Ada masanya turuti bisikan sanubari,","Meski orang di sekeliling mengatakan tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101894,"Buah nangka di atas peti,","Peti berisi buah berangan;","Apabila bercerita hendaklah berhati-hati,","Usah dikongsi orang sebarangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101895,"Berhenti seketika di Pulau Merak,","Setelah letih terbang seharian;","Suasana Eidul Fitri masih bersemarak,","Ambil kesempatan perbaiki perhubungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101896,"Rasa masa masam menelan cuka,","Dimasak asam ikan ketutu;","Lihatlah sesuatu dengan terbuka,","Ada hikmah Setiap yang berlaku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101897,"Kuih tradisi kuih koleh,","Rasanya enak digemari semua;","Singkirkan etos tidak boleh,","Baru pergerakan ke hadapan dapat dilaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101898,"Setelah bermesyuarat perhimpunan pun bubar,","Keputusan dibuat untuk disepakati;","Kelemahan yang ditemui jadikan iktibar,","Supaya pentadbiran lebih berintegriti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101899,"Usah diminta yang bukan-bukan,","Nanti ada perasaan yang terluka;","Segala masalah dapat diselesaikan,","Asalkan dibincang dengan hati terbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101900,"Asap pelita membentuk jelaga,","Pernafasan terganggu selesema menyerang;","Amanah kepimpinan harus dijaga,","Jangan sampai diperlekeh orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101901,"Lama sungguh tidak dikunjungi,","Kampung Setanggi yang banyak cerita;","Biar bergelar dan berkedudukan tinggi,","Ular menyusur akar tak hilang bisanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101902,"Makan gula sakitlah gigi,","Jagalah gigi semenjak muda;","Ke mana jua kita pergi,","Pergilah bersama seluruh jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101903,"Gulai dikacau dengan senduk,","Supaya keenakan dapat dirasa;","Rumah tangga harmoni mari dibentuk,","Usah ikutkan perasaan membara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101904,"Di jalanraya hendaklah peka,","Kemalangan berlaku tanpa diundang;","Hari esok tidak boleh dijangka,","Lakukan yang terbaik tiadalah yang dibimbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101905,"Kabut alam disebabkan kebakaran,","Baiklah berpindah ke tempat selamat;","Lakukan sesuatu dengan penuh kesedaran,","Agar segala tingkah menjadi manfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101906,"Tahun 2020 menjadi wawasan,","Negara maju rakyat berbudi pekerti;","Kesibukan usah dijadikan alasan,","Untuk membentuk kehidupan berkualiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101907,"Menuai padi di sawah Nyonya;","Sawah terbentang di pinggir kali,","Sahabat sejati sedikit bilangannya,","Sahabat masa berada ramai sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101908,"Nelayan ke laut menangkap ikan,","Ikan terperangkap ke dalam pukat;","Suasana raya terus dimanfaatkan,","Menziarahi kerabat dan juga sahabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101909,"Masa yang gembira sewaktu makan,","Lauk lemak pucuk paku pakis;","Kembali ke desa sentiasa membahagiakan,","Menelusuri keindahan meski kian terhakis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101910,"Kalau pergi ke Inderapuri,","Jangan lupa bawa bekalan;","Ulang tahun kelahiran terus dirai,","Zahirkan kesyukuran dengan ketaatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101911,"Setiap manusia ada kekurangan,","Ada yang betul ada yang salah;","Hari baru dimulai kegembiraan,","Positifkan pemikiran hadapi masalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101912,"Mari berkunjung majlis di istana,","Jaga perlakuan jagalah pertuturan;","Hari raya disambut dengan sederhana,","Dirai memanifestasi rasa kesyukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101913,"Keselamatan persekitaran usah dikhuatiri,","Polis DiRaja memelihara kesejahteraan;","Menjelang Aidil Fitri yang kita rai,","Teruskan ibadah tanda kesyukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101914,"Menjahit baju dengan teliti,","Baju merah warnanya garang;","Lakukan sesuatu bermula dihati,","Tanpa mengharap puji-pujian orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101915,"Memang misteri di alam ruhi,","Belajar daripada yang arif sahaja;","Dirgahayu sahabat yang dikasihi,","Semoga hidupmu diberkati sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101916,"Negeri Kelantan negeri berbudaya,","Budi bahasa dan nilai tradisi;","Sakit dan sukar yang sering melanda,","Memberi ruang menginsafi diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101917,"Memang enak buah pulasan,","Meskipun kulitnya tidak berseri;","Memang sukar membuat keputusan,","Walaupun terpaksa melukakan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101918,"Tempat dituju belok ke kiri,","Berhati-hati usahlah leka;","Bila berjanji usah dimungkiri,","Sukar diubati hati yang terluka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101919,"Tidak hilang masamnya cuka,","Kesannya hingga darah pun naik;","Biar pun hati parah terluka,","Sabar dan tabah itu yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101920,"Enak sungguh buah berangan,","Mari dikupas diatas titi;","Perubahan adalah satu perjuangan,","Sukar diterima jika tidak dimengerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101921,"Alangkah enak sibuah duku,","Kulitnya kuning enak rasanya;","Kalau diikut hati yang rindu,","Ingin ku terbang ke hujung dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101922,"Bersolat tarawikh rakaatnya lapan,","Bacaan imam sungguh mempesona;","Cabaran diperlukan dalam kehidupan,","Menjadikan hidup lebih bermakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101923,"Di dalam hutan banyaklah rotan,","Rotan dianyam menjadi raga;","Bulan Ramadhan bulan keberkatan,","Eratkan perhubungan ahli keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101924,"Pelbagai jenis kayu kayan,","Ada yang pendek ada yang panjang;","Permasalahan hanyalah satu ujian,","Menguji kematangan hadapi dengan tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101925,"Cari ketenangan di dalam hutan,","Unggas bersahutan suara beralun,","Penghayatan Al-Quran satu tuntutan,","Generasi terdidik membina tamadun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101926,"Lama kelamaan menjadi biasa,","Harus dijaga siang dan malam;","Nuzul Quran diraikan semua,","Kitab suci merahmati seluruh alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101927,"Purnama menerangi kegelapan malam,","Suasana girang mengubati lara;","Senyuman manifestasi jiwa di dalam,","Dengannya membuat orang lain gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101928,"Musim panas kering banyak kebakaran,","Jerebu muncul berkali-kali;","Kedewasaan dibina dengan pemikiran,","Bukan emosi yang tidak terkendali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101929,"Pergi ke masjid membawa punjut,","Untuk diisi dengan bekalan;","Meskipun usia sudah pun lanjut,","Menuntut ilmu masih jadi kewajiban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101930,"Pergi ke masjid membawa punjut,","Untuk diisi dengan bekalan;","Meskipun usia sudah pun lanjut,","Menuntut ilmu masih jadi kewajiban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101931,"Sesekali bersarapan dengan mihun,","Dimakan dengan ikan kekek,","Dalam usianya 49 tahun,","UKM tersanjung antara yang terbaik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101932,"Memotong sayur dengan belati,","Belati dibeli sudahlah lama;","Karya ditulis daripada hati,","Untuk dikongsi renungan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101933,"Belayar jauh perahu nelayan,","Pendayung dihayun sepenuh hati;","Berlapang dadalah hadapi ujian,","Semoga tenang hidup pun diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101934,"Bunga kantan bunga kemangi,","Ditanam orang di tanah yang rata;","Pemimpin yang tulus sentiasa disanjungi,","Apa yang dikata itulah yang dikota.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101935,"Sentiasa bersyukur kepada Ilahi,","Sentiasa menganugerahkan rezeki melimpah;","Peluang bekerja sudah diperolehi,","Sempurnakan ia dengan penuh amanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101936,"Panjang sekali siular sawa,","Kepada mangsa dia melingkari;","Kebahagiaan dirasa di dalam jiwa,","Tanpa dipaksa mekar sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101937,"Mula dirasakan menjelang raya,","Berpuasa seminggu belum pun diredah;","Walaupun sibuk dengan kerjaya,","Sesekali rehatkan minda dengan beruzlah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101938,"Sedia mendengar tanpa bosan,","Anak kecil bercerita sangatlah lucu;","Kehidupan adalah perjalanan berterusan,","Tetapkan destinasi jelas ingin dituju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101939,"Cahaya pagi di Kuala Rompin,","Pulau Tioman jauh di sana;","Demokrasi mengangkat seorang pemimpin,","Beri peluang sokong segala usaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101940,"Jangan dimakan sebarangan ikan,","Enak sekali siikan kitang;","Perancangan teliti harus dilakukan,","Hadapi cabaran tahun mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101941,"Bunga melur bunga melati,","Tumbuh melata di kaki Daik;","Orang berilmu harus lebih berintegriti,","Tunjukkan contoh teladan yang baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101942,"Makan pagi dengan roti,","Duduk berteman di bentangan kain;","Ramadhan mulia sentiasa dinanti,","Mendidik diri memanfaat orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101943,"Ikan kaci ikan tenggiri,","Daun pegaga dibuat ulam;","Sahabat sejati sukar dicari,","Bagai batu hitam di malam kelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101944,"Dinding diperbuat kayu jati,","Cantik dipandang tidak terkata;","Persahabatan kita persahabatan sejati,","Dekat dihati walau jauh dimata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101945,"Mari membeli sebatang joran,","Memancing ikan di sungai yang dekat;","Orang kenali kita hanya luaran,","Tidak mengenal secara hakikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101946,"Menimba air dengan baldi,","Dingin airnya sungguhlah nyaman;","Negeri Kedah Jelapang Padi,","Negeri berjasa kepada kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101947,"Kisah duka dikongsi berbahagi,","Supaya terangkat beban kedukaan;","Orang berjasa sewajarnya dihargai,","Menjadi contoh segala kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101948,"Membayar hutang secara ansuran,","Tidak terbeban belanja keluarga;","Bila bertindak ikutlah peraturan,","Biarlah melakukan apa yang dibicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101949,"Kalau dipraktik menjadi mahir,","Kalau ditinggal mudah terlupa;","Kasih dan sayang harus dizahir,","Meskipun tanpa berkata-kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101950,"Bahasa yang indah bahasa bermadah,","Perhimpunan cendekia acara tahunan;","Kejayaan dicapai bukanlah mudah,","Maka hargai pengorbanan yang ditunjukkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101951,"Dari Palembang belayar ke Rokan,","Teruskan perjalanan ke Pahang Tua;","Demi silaturrahim kita usahakan,","Meskipun jauh ditempuh jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101952,"Bisa sengatan gigitan lipan,","Kesakitan mencengkam sampai ke isi;","Banyak cabaran pada masa depan,","Tanggungjawab kita bina generasi berinovasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101953,"Buah anggur buah nangka,","Hidup dalam iklim yang berlainan;","Bertolak ansur dan berbaik sangka,","Formula hubungan kasih berpanjangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101954,"Perjalanan jauh ke Bandar Miri,","Bandar berkembang menjadi metropolitan;","Usahlah bangga dengan kebolehan sendiri,","Restu ibu bapa menjamin keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101955,"Milik orang usah diangkut,","Tanpa kebenaran tuan empunya;","Pemimpin bukan membentuk pengikut,","Membina pemimpin itu tanggungjawabnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101956,"Terbang layang-layang dari kertas,","Angin mengusik seperti menggoda;","Janganlah angkuh bila di atas,","Usah lupa hidup seperti roda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101957,"Ketenangan jiwa bukanlah kurnia,","Marilah mencari tidak berakhir;","Usah dikejar harta dunia,","Hanyalah ibarat setitik air.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101958,"Kehidupan sejahtera bukan alam impian,","Perumahan indah di Tasik Kesuma;","Cabaran hari ini terhadap keharmonian,","Semua pihak pertahankan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101959,"Ditimpa hujan baju pun basah,","Dijemur semula bawah matahari;","Apabila kehilangan jiwa pun resah,","Sewaktu ada tidak pula dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101960,"Rasa kesakitan tersalah urat,","Mengeluar keringat dengan bersukan;","Mencari penyelesaian dengan bermesyuarat,","Usah terjerumus kepada pertengkaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101961,"Daripada seorang menjadi berdua,","Menimang anak ditahun hadapan;","Permuafakatan dicapai sehati sejiwa,","Masanya melaksana segala ketetapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101962,"Usah bicara hanya berbasa basi,","Kalau tidak perlu jangan bertanya;","Pemimpinlah menerajui organisasi,","Nilai integriti menjadi terasnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101963,"Kegelapan malam diterangi kandil,","Persekitaran indah dilimpahi cahaya;","Dalam memilih hendaklah adil,","Yang berkelayakan sahaja akan berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101964,"Ikan gemang ikan kerisi,","Isinya enak sengatnya berbisa;","Yang hilang usah ditangisi,","Di hati abadi walau hilang di mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101965,"Majlis meriah dihadiri ramai,","Jamuan disediakan masakan kari;","Ke Tanjung Surat bakti disemai,","Memperkasa komuniti dapat berdikari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101966,"Membakar ikan menggunakan arang,","Sotong, udang, kerang dan lokan;","Manfaatkan kedudukan menolong orang,","Permudahkan urusan usah disukarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101967,"Janganlah hanya menunggu arahan,","Lakukanlah amanah walaupun seorang,","Usahlah mudah timbul kemarahan,","Boleh menjejas kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101968,"Menelusuri keindahan di Sungai Papan,","Sambil mengail ikan sembilang;","Melakar haluan melangkah ke hadapan,","Menjadi landasan membina universiti terbilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101969,"Kalau lapar pergilah makan,","Masak lemak ikan tenggiri;","Usah diikut runtunan perasaan,","Nanti merosak hati sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101970,"Manis sungguh buah longan,","Bentuknya cantik padat berisi;","Hindari rasuah satu perjuangan,","Iltizam semua seluruh organisasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101971,"Angin kencang melanda desa,","Pokok tumbang menimpa kenderaan;","Berusaha keras hidup selesa,","Sifat malas rugi dihari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101972,"Biar pun sukar sanggup diredah,","Kerana kasih semangat bersatu;","Pulang ke desa waktu terindah,","Di situ tempat tumpah darahku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101973,"Biar lambat asal selamat,","Slogan biasa pegangan semua;","Dalam memilih perlulah cermat,","Memilih pemimpin perkara utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101974,"Sama cantik samalah padan,","Pasangan pengantin kita restui;","Segala yang baik jadikan teladan,","Sempadan yang buruk harus diperhalusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101975,"Pelari handalan dari Kenya,","Berlatih bersungguh setiap hari;","Segala permasalahan ada puncanya,","Duduk merenung refleksi diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101976,"Anak kecil harus dilayan,","Dengan sabar didik dari hati;","Hidup ini penuh dengan ujian,","Kejayaan muncul setelah gagal berkali-kali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101977,"Bila berjalan usahlah mendada,","Bumi dipijak dimiliki Tuhan;","Paparkan emosi berpada-pada,","Di situ menguji tahap kematangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101978,"Nabi berpesan kawallah amarah,","Jiwa pun tenang tiadalah lara;","Rasa cemburu adalah fitrah,","Kerana kasih sayang yang membara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101979,"Usah diabai walaupun mimpi,","Ada alamat yang diutara;","Keikhlasan orang boleh dirasai,","Walaupun tiada dirasa mesra.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101980,"Menarik nafas panjang menghela,","Hembuslah dengan perlahan-lahan;","Yang tertindas haruslah dibela,","Yang menindas dibawa ke muka pengadilan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101981,"Sorong papan tarik papan,","Buah pala di atas para,","Lakukan kebaikan misi kehidupan,","Tuhan membalasnya tanpa disangka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101982,"Perut berbunyi waktu makan,","Sampai masa kelaparan terasa;","Sejarah bangsa harus dipertahankan,","Jadikan landasan kejayaan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101983,"Ada masalah kongsikan dengan kawan,","Supaya tidak mencengkam peribadi;","Kepada yang berjasa beri penghargaan,","Jadilah bangsa yang mengenang budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101984,"Lidah kepedasan tergigit cabai,","Berpeluh kepala berlompatan kutu;","kesempitan orang usahlah diabai,","Kedudukan ada manfaatkan untuk membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101985,"Bermain-main dengan pelaga,","Ketika bermain makan keropok;","Rai ulang tahun perkahwinan ke-63,","Luar biasa kasih sayang yang dipupuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101986,"Banyak hidangan di Warung Bunian,","Pengunjung datang beramai-ramai;","Biarlah hidup dengan keharmonian,","Keindahan perhubungan aman dan damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101987,"Jalur awan bergaris-garis,","Jadikan pemandangan indah belaka;","Kalau ada hati yang terguris,","Mungkin kerana buruk yang disangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101988,"Batang buluh dibilah-bilah,","Dibuat bakul oleh Cik Nyonya;","Bila berkata berhati-hatilah,","Agar perkataan kita ada manfaatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101989,"Kalaulah ada hati yang terluka,","Dengan segera cubalah diubati;","Tanggungjawab baharu diterima terbuka,","Cabaran baharu mematangkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101990,"Badan letih tidur pun lena,","Tidak disedari nafas berdengkuran;","Hormati ilmuwan hadiri kuliahnya,","Dalam bahasa kebangsaan ilmu disampaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101991,"Jika ada kuasa di tangan,","Bukan diguna membentuk masyhur;","Keupayaan berfikir bina kematangan,","Usia hanyalah salah satu faktor.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101992,"Kata pujangga sungguh menginspirasi,","Menjadi panduan yang ingin belajar;","Kehidupan ini perlulah diisi,","Dunia dan akhirat keseimbangan yang wajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101993,"Ikan berenang berkejar-kejaran,","Tidak disedari ada buaya;","Masa lalu penuh pengajaran,","Jadikan iktibar untuk hidup berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101994,"Berenang-renang ikan pelaga,","Di celah-celah bunga kiambang;","Kasih dan sayang amat berharga,","Dengan keikhlasan subur berkembang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101995,"Kelemahan diri amat ketara,","Kurangkan bercakap lebihkan refleksi;","Memaknai universiti tajuk bicara,","Bongkar khazanah yang masih tersembunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101996,"Memasang perangkap dalam belukar,","Dapat puyuh dimasak tumis;","Mencari kejujuran yang makin sukar,","Berwaspada dengan janji yang manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101997,"Hari baharu hari Selasa,","Jadual yang padat bukan menghalang;","Usahlah mudah melupakan jasa,","Kelak keberkatan akan menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101998,"Sungguh dingin air telaga,","Terasa segar di waktu petang;","Kesihatan diri sangat berharga,","Hargai ia sebelum menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (101999,"Enak rasanya buah pulasan,","Kulit manggis rasanya kelat;","Berbudi bahasa menjadi landasan,","Berkasih sayang menjadi matlamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102000,"Jika dilayan dendam membara,","Kelak hidupmu menjadi kosong;","Walau ke pedalaman jauh kembara,","Jiwa prihatin menjadi pendorong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102001,"Kejayaan orang jadikan pemangkin,","Masa depan direncana sedang menanti;","Usah diharap kepada yang lain,","Ciptalah sendiri kegembiraan peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102002,"Bunga selasih kembang bertumpuk,","Pucuk dipetik dibuat ulaman;","Kasih dan sayang terus dipupuk,","Perkara remeh usah diperbesarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102003,"Ular ditemui usah dipalu,","Biarlah hidup secara alami;","Setiap orang ada masa lalu,","Semoga kesilapan telah diinsafi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102004,"Baju kemeja keras berkanji,","Amalan dahulu dimana-mana;","Usahlah mudah membuat janji,","Kalau ragu untuk menepatinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102005,"Mencari penawar hingga ke Kedah,","Untuk mengubati batu karang;","Jadi pemimpin bukanlah mudah,","Tingkah peribadi diperhati orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102006,"Bertambah enak santan yang pekat,","Ada khasiatnya minyak kelapa;","Kehidupan kita memanglah singkat,","Berbuatlah baik kepada sesiapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102007,"Setelah seharian cuba berlapar,","Usahlah berlengah pergilah balik;","Supaya kekeliruan dapat dihindar,","Berterus terang adalah yang terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102008,"Bangun pagi memberus gigi,","Di atas meja terhidang sarapan;","Melaka bersejarah dikunjungi lagi,","Pelajari yang lalu untuk terus ke depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102009,"Baju disemat jarum peniti,","Untuk melurus kedutan kain;","Pertemuan singkat penuh bererti,","Mengukuh kasih sayang sedia terjalin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102010,"Menimba air daripada telaga,","Telaga lama airnya keruh;","Jika terpisah usahlah curiga,","Ikatan kasih sayang menjadi kukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102011,"Terasa kedinginan hujan lebat,","Berselimut tebal kehangatan dijana;","Marilah doakan kesejahteraan sahabat,","Semoga dikembalikan kesihatan sempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102012,"Jadikan kebiasaan berjalan berlari,","Supaya tubuh badan menjadi sihat;","Sahabat yang ada harus dihargai,","Ikhlas persahabatan tanpa bersyarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102013,"Helang terbang membelah udara,","Mencari mangsa kesana kemari;","Syukuri sahaja segala yang ada,","Kebahagiaan menjelma di sanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102014,"Bersahut-sahutan suara ungka,","Jadi meriah suasana di hutan;","Janganlah ada rasa berprasangka,","Hanya merosak hubungan persahabatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102015,"Tiada tempat hendak dirujuk,","Persoalan bersarang di dalam hati;","Bukanlah senang hendak dipujuk,","Merajuk berpanjangan berhari-hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102016,"Berjalan-jalan hingga ke Lendu,","Berhenti seketika menjamah laksa;","Bila berjauhan terus merindu,","Terusik sedikit amat terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102017,"Sungguhlah indah Gunung Tahan,","Hutan menghijau merungkai lara;","Segala yang ada milik Tuhan,","Bila-bila masa sahaja diambil semula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102018,"Buah bacang buah kuini,","Mangga harum manis yang terkemuka;","Berkasih sayang dari hati nurani,","Kebahagiaan hakiki yang terus diteroka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102019,"Bunga mawar bunga melati,","Dipetik sekuntum hiasi jambangan;","Daripada berkata yang mengguris hati,","Lebih baik diam perbaiki kekurangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102020,"Bekerja keras bergaji lumayan,","Rezeki yang halal yang dicita;","Integriti harus menjadi keutamaan,","Tanpanya tumbanglah institusi kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102021,"Begitu taajub mulut pun ternganga,","Pastikan tidak dimasuki tetamu;","Yang pergi diiringi doa,","Apatah lagi menerokai ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102022,"Jika diminta berilah ruang,","Supaya hubungan jadi harmoni;","Usah diprasangka kebaikan orang,","Kelak rugi diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102023,"Malam dingin tidur pun lena,","Sambil berselimut terasa nyaman;","Segala urusan dilaksana sempurna,","Ketelitian syarat untuk kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102024,"Tuntutan keterlaluan usah dilayan,","Sumber yang ada uruslah berhati-hati;","Tujuan mesyuarat mencari penyelesaian,","Adab dan etika mestilah dihayati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102025,"Lambat meniti sikura-kura,","Titian dibuat daripada akar;","Pertemuan dinanti dengan gembira,","Kasih sayang dipupuk teruslah mekar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102026,"Makanan dicari di dalam pekan,","Tempatnya bersih hidangan berkualiti;","Perselisihan faham harus diselesaikan,","Kesucian disuburkan di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102027,"Buah nangka buah tarap,","Boleh dibeli di Pasar Tani;","Pemikiran pemimpin sentiasa diharap,","Kecemerlangan lalu harus diteladani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102028,"Pulang bekerja di hari Selasa,","Rehatkan fikiran di Pulau Bali;","Hati orang dijaga sentiasa,","Hati sendiri siapa pula yang peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102029,"Berhening cipta di dalam gua,","Mencari sepi elak hingar bingar;","Biar pun lama tidak bersua,","Dalam sukma tidak pernah pudar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102030,"Cuaca panas minumlah cincau,","Disediakan oleh gadis remaja;","Saudara di Johor isteri di Riau,","Kalau dah jodoh bertaut jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102031,"Melapang fikiran di dalam hutan,","Tempat berteduh di pohon ara;","Perselisihan rasa usah berlarutan,","Bertoleransi mereda duka dan lara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102032,"Biarpun bintang tidak kelihatan,","Panduan perjalanan boleh dicari;","Iri hati dengki usah jadi ikutan,","Kelak rosak binasa diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102033,"Tidur malam dengan begitu lena,","Setelah keletihan seharian berjalan;","Raikan perayaan tahun baharu Cina,","Tradisi kita teruslah berkekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102034,"Undangan kenduri sudah dipenuhi,","Sebelum pulang bersalaman dahulu;","Syukuri nikmat yang Allah beri,","Dia anugerah hari yang baharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102035,"Makanan digemari adalah laksa,","Kuahnya daripada ikan tenggiri;","Kesekian kali pulang desa,","Tidak kan jemu di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102036,"Berhati-hati jangan terpijak,","Kehadiran makhluk halus dapat dirasa;","Biar pun jauh jarak dijejak,","Undangan ikhlas dipenuhi jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102037,"Bunga merah dihinggap lebah,","Flora dan fauna di dalam hutan;","Ada perkara tak perlu dihebah,","Demi kemaslahatan elak kemudharatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102038,"Pantang dan larang nenek moyang,","Menjadi pegangan berpada-pada;","Mari perkukuh kasih dan sayang,","Kebahagiaan dicipta selama-lamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102039,"Pantang dan larang nenek moyang,","Menjadi pegangan berpada-pada;","Mari perkukuh kasih dan sayang,","Kebahagiaan dicipta selama-lamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102040,"Pulau Langkawi Permata Kedah,","Pantai indah belantara menghijau;","Usah diabai orang bermadah,","Jadi panduan jiwa yang kacau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102041,"Cenderawasih tinggi di angkasa,","Mata memandang bahagia sanubari;","Kasih ibu bapa adalah syurga,","Usah disesali di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102042,"Membeli perabot di Pekan Tuaran,","Tidaklah jauh dari Manggatal;","Hidup ini banyak cabaran,","Hanya ujian bina hati yang kental.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102043,"Janganlah takut kepada hantu,","Walaupun keluar diwaktu malam;","Andainya kita inginkan sesuatu,","Mulakan usaha setelah berazam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102044,"Majlis perkahwinan bertemu kerabat,","Setelah selesai pergi berkelah;","Berhati-hati memilih sahabat,","Nawaitu manusia sukar ditelah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102045,"Setelah ditinggalkan rasa gelisah,","Bagaikan diri tiada yang punya;","Sungai dan kehidupan tidak terpisah,","Justeru marilah pelihara kelestariannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102046,"Lagenda Cini berkaitan naga,","Lagenda Ledang tentang Sang Puteri;","Kebahagiaan sendiri usah diperaga,","Mungkin ada yang merasa iri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102047,"Menempuh semak kaki terluka,","Terasa letih berhenti makan;","Terimalah teguran dengan terbuka,","Kebaikan organisasi kita utamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102048,"Berenang, berlari dan juga memanah,","Permainan sunnah kita semua teruja;","Tanggungjawab terpikul adalah amanah,","Walau hujung minggu tetap bekerja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102049,"Amalan mengambil madu lebah,","Untuk memelihara dimensi kesihatan;","Mengaudit program adalah muhasabah,","Mempertingkat segala yang kita lakukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102050,"Menjana ilmu mendekati ilmuwan,","Rajin berusaha rajin mencari;","Kesukaran dihadapi adalah ujian,","Jadikannya motivasi mengangkat diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102051,"Memang enak tempoyak patin,","Di Jalan Semarak tempat perniagaan;","Nasib orang kita berprihatin,","Bantu yang kurang dengan kemampuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102052,"Dinding berlubang diganti papan,","Papan dibeli di kedai nyonya;","Kasih sayang landasan kehidupan,","Berbahagia kita selama-lamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102053,"Campak pakaian ke atas atap,","Amalan dahulu menjadi khurafat;","Pengurusan institusi harus dimantap,","Baru lah keputusan dibuat tepat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102054,"Menempuh perjalanan jauh ke Kangar,","Bila letih berhenti di perhentian;","Pemimpin yang baik sering mendengar,","Keluhan orang berikan perhatian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102055,"Burung kedidi di atas dahan,","Melihat rezeki di keliling sisi;","Bersedialah hadapi sebarang perubahan,","Asalkan untuk kebaikan organisasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102056,"Dipagi hari berkongsi cerita,","Dalam perjalanan ke pekan Rompin;","Meditasi mengakrab diri kepada Pencipta,","Ke jalan redha Dia memimpin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102057,"Musuh negara ketagihan dadah,","Pelbagai kempen sudah dilaksana;","Ziarah yang sakit suatu ibadah,","Doakan semoga kembali sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102058,"Rajin berusaha menjadi kaya,","Segala kekusutan terungkai simpul;","Perkara baik ada yang dirahsia,","Elak mudharat yang mungkin timbul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102059,"Pasang pelita asap berjelaga,","Dalam samar bermain guli;","Hati orang sentiasa dijaga,","Hati sendiri siapa yang peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102060,"Menangkap ikan di Kuala Ibai,","Tolong carikan ikan kelisa;","Kasih sayang usah diabai,","Terus suburkan mekar sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102061,"Beriadah pagi di Bukit Unggul,","Persediaan mendaki Gunung Daik;","Integriti nilai yang unggul,","Jadikan pegangan pemimpin yang baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102062,"Semakin menjadi-jadi bila dilayan,","Anak kecil sememang ingin dimanja;","Rayuan simiskin adalah ujian,","Tuhan turunkan rahmat pada hamba pilihanNya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102063,"Menziarahi keluarga di Taman Perling,","Tanjung Puteri tempat bersuka ria;","Berhati-hati orang di sekeliling,","Tidak semua berhati bersih dan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102064,"Karenah anak harus dilayan,","Untuk membina budi pekerti;","Sentiasalah berusaha mencari kebahagiaan,","Dengan cara sendiri yang diyakini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102065,"Nasi Arab rasanya lain,","Rempah ratus dari Samarkhan;","Usah diharap peranan orang lain,","Fikirkan apa yang kita sumbangkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102066,"Beriadah pagi di Bukit Rupa,","Bawa bekalan sambal dan lempeng;","Biarpun kepentingan diri tidak dilupa,","Kepentingan orang usah dikesamping.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102067,"Segala masalah usah diketepi,","Selesaikan satu persatu walaupun lambat;","Banyak ujian yang terus dihadapi,","Harus tabah dan kuatkan semangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102068,"Murid menangis di dalam kelas,","Bermulalah perjalanan alam persekolahan;","Hari pertama bekerja 2019,","Azam terpahat bakti dibuktikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102069,"Udang nipah udang kara,","Dimasak kari menghiasi hidangan;","Kenangan 2018 indah dan lara,","Jadikan teladan jadikan sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102070,"Ada bukit mari turuni,","Ada lurah yang sangat mencabar;","Hati terguris harus disantuni,","Keharmonian hubungan usah dicalar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102071,"Pendidikan membentuk tata kelakuan,","Hidup membawa visi dan misi;","Jati diri harus terus diperkukuhkan,","Pengaruh mudharat sukar menembusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102072,"Suara sayup di dalam hutan,","Bersahut-sahutan dipohon buluh;","Pulang meraikan majlis perkahwinan,","Silaturrahim diperkukuh persahabatan diperteguh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102073,"Usah menyesal setelah memberi,","Pasti ada ganjaran kebaikan;","Kejayaan orang harus dirai,","Hasad dan iri harus dihindarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102074,"Lebih mulia hidup berdikari,","Yang diusaha berlandaskan ilmu;","Kebahagiaan sendiri harus dicari,","Tiada yang tahu apa yang kita perlu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102075,"Mari banyakkan memakan ikan,","Pergi beriadah hingga berpeluh;","Peraturan baharu harus diperkenalkan,","Seandainya berasas landasan yang kukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102076,"Bila dah kenal barulah mesra,","Setelah mesra terbibit percintaan;","Hari Natal diraikan bersama,","Perkukuh persahabatan menerusi perayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102077,"Buluh perindu tumbuh merimbun,","Ditebang sebatang dibuat lemang;","Kalimah yang baik bagaikan pohon,","Akarnya kukuh pohonnya rendang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102078,"Mengangkat barang di atas bahu,","Untuk dihantar ke Tanjung Karang;","Gembirakan diri sendiri kita yang tahu,","Selama ini hanya melebihkan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102079,"Bagai kaduk naik junjung,","Peribahasa tak lapuk dek masa;","Pemimpin berwibawa sentiasa disanjung,","Kepentingan institusi jadi keutamaannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102080,"Bila bermain janganlah leka,","Selepas bermain pergilah mandi;","Berpantun, berpuisi dan berseloka,","Budayakan ketinggian bangsa berperibadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102081,"Berhati-hati jangan terluka,","Menahan darah gunakan kain;","Baik pekerti ramai yang suka,","Kesusahan diri turut diprihatin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102082,"Usahlah mudah patah semangat,","Ujian yang datang jangan dinafi;","Setiap keputusan yang perlu dibuat,","Maklumat yang soheh mesti diperolehi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102083,"Burung berterbangan di atas lautan,","Sambil mengintai rezeki menanti;","Meskipun diri menghadapi kesakitan,","Hanyalah secebis ujian dihadapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102084,"Hidupkan budaya gemar bersukan,","Kesihatan yang baik tingkat produktiviti;","Kesabaran dan ikhtiar harus diteruskan,","Doa yang dikasihi sentiasa mengiringi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102085,"Pergi beriadah berpeluh dahi,","Lemak dibakar tingkatkan kesihatan;","Kesakitan dialami ujian Ilahi,","Menguji iman, takwa dan penyerahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102086,"Rakan taulan mengajak makan,","Ikan udang kerang dan ketam;","Para pesara kita raikan,","Jasa mereka harus dirakam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102087,"Tidak ramai yang boleh dipercayai,","Badannya besar, hatinya kerdil;","Penat lelah pekerja harus dihargai,","Bayaran setimpal yang lebih adil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102088,"Pergi menyendiri di dalam gua,","Kosongkan fikiran daripada petualang;","Berdoalah untuk kebaikan semua,","Jiwa tenang langkah pun panjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102089,"Berhati-hati dengan binatang berbisa,","Seringkali ada walau tidak kelihatan;","Orang berbudi kita berbahasa,","Balas kemuliaan dengan kemuliaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102090,"Usah keliru biar lebih mengerti,","Janganlah segan untuk bertanya;","Perteguh keperibadian dan berhati-hati,","Tidak semua disekeliling berhati mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102091,"Burung terbang dipipiskan lada,","Kalau dapat dijadikan hidangan;","Lupakan yang lalu, hargai yang ada,","Lihat ke hadapan dengan penuh perancangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102092,"Kalau ketawa usahlah berdekah,","Nanti hilang wibawa keperibadian;","Mari raikan hari yang penuh berkah,","Biar terakam dalam sejarah kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102093,"Apabila keletihan ambillah cuti,","Pergilah melancong janganlah lama;","Walaupun kita kurang berpuas hati,","Hormati keputusan yang dipersetujui bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102094,"Marilah belajar ilmu laduni,","Hati yang bersih mestilah disedia;","Penghalang perubahan harus ditangani,","Membantut kemajuan dalam acuan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102095,"Waktu pagi masa mengarang,","Suasana tenang lapang fikiran;","Usah bersangka buruk kepada orang,","Berilah peluang memapar kepimpinan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102096,"Kalau jahil usah mentakrif,","Nanti orang dalam kekeliruan;","Belajarlah daripada yang lebih arif,","Perlengkap diri dengan ilmu pengetahuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102097,"Kalau bersalah mohon dimaafi,","Maafkan sahaja tidak perlu ditanya;","Apa yang datang tidak diketahui,","Lakukan yang dirancang sebaik-baiknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102098,"Harga barangan akan terus naik,","Maka berhemah dalam perbelanjaan;","Atas keyakinan sesuatu yang terbaik,","Bertawakal dan berserah dengan ketakwaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102099,"Jiwa resah kerana barang yang hilang,","Ada kemungkinan telah dicuri;","Pemimpin berwibawa sentiasa dikenang,","Penggantinya pula harus dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102100,"Dalam hidup lakukan kebaikan,","Banyak cabaran perlu hati yang tabah;","Hendak berperantauan biar dilepaskan,","Mencari pengalaman ilmu pun bertambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102101,"Sambil menonton mengunyah kacang,","Kacang Menglembu sudah terkenal;","Dalam kehidupan kita merancang,","Dengan berstrategi teruskan dengan cekal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102102,"Pergi melancong ke Inderapuri,","Pemandangan indah hati terharu;","Pagi yang muncul harus disyukuri,","Titik permulaan kehidupan yang baharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102103,"Harta karun tersimpan di peti,","Tidak dibuka oleh yang punya;","Keindahan hidup bermula di hati,","Didiklah hati dengan sempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102104,"Memberi kebaikan janganlah sudah,","Daripada hati budayakanlah memberi;","Mencari kebenaran bukanlah mudah,","Sesuatu berharga memang sukar dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102105,"Ikan tenggiri ikan gelama,","Juadah tengahari tersedia hidangan;","Biarpun tidak sentiasa bersama,","Pertemuan singkat sudah membahagiakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102106,"Berilah kebaikan kepada keluarga,","Ada yang susah jangan diketepi;","Dengan melawat bertemu warga,","Baru difahami masalah yang dihadapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102107,"Tidaklah jauh pergi Jementah,","Bawa bekalan air dan kek;","Biarpun diberi kuasa memerintah,","Sempurnakan dengan berhemah dan baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102108,"Kalau keliru marilah bertanya,","Jangan biarkan diri kesesatan;","Kebebasan perlulah ada batasannya,","Nilai budaya janganlah diabaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102109,"Usah biar harga barang naik,","Nanti kehidupan bertambah berat;","Rasulullah junjungan teladan terbaik,","Tunjuk jalan bahagia dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102110,"Laksanakan tanggungjawab dengan taat,","Biar matlamat capai destinasi;","Segala sumber harus dimanfaat,","Untuk kejayaan diri dan organisasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102111,"Api pelita menghasilkan jelaga,","Sangat mengganggu sistem pernafasan;","Kesihatan diri perlulah dijaga,","Segala tanggungjawab sempurna dilaksanakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102112,"Di pagi hari pergi ke perigi,","Menyegar badan bermandi-mandian;","Bagai matahari yang terbit pagi,","Menyinari kehidupan menyemarak perjuangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102113,"Ke Tanjung Puteri destinasi dituju,","Dengan berkereta sepanjang hari;","Sahabat lama ingin diketemu,","Mengimbau kenangan menyegar memori.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102114,"Dalam perjalanan bekalan dibawa,","Bila kelelahan marilah berhenti;","Pengorbanan dilaksana dengan jiwa,","Untuk ikatan yang lebih bererti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102115,"Pergi mengembara bawalah peta,","Agar tidak tersasar nanti;","Saat bahagia harus dicipta,","Hargai sepenuhnya segala yang dimiliki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102116,"Di pagi hari pergi ke perigi,","Menyegar badan bermandi-mandian;","Bagai matahari yang terbit pagi,","Menyinari kehidupan menyemarak kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102117,"Arahan yang betul perlu dipatuhi,","Elak kemudharatan yang tidak diduga;","Hargai anugerah yang ditentukan Ilahi,","Semoga kesyukuran subur dijiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102118,"Elok dimata SiGunung Daik,","Dikatakan orang bercabang tiga;","Usah diperkira berbuat baik,","disuatu hari nanti dibalas juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102119,"Baktinya tinggi tidak terlawan,","Itulah sanjungan budayawan Pak Ngah;","Anugerah selayaknya buat negarawan,","Bakti dan jasa dirakam sejarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102120,"Hati gembira di hari perayaan,","Makanan beraneka terhidang semua;","Hari gembira buat para graduan,","Perjuangan bertahun diraikan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102121,"Layar kapal berkeluk-keluk,","Sauh tersangkut di batu karang;","Jaga lah diri tingkah yang elok,","Sopan dan santun dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102122,"Buah yang manis sibuah nangka,","Buah cempedak jadi tandingan;","Orang berilmu usah diprasangka,","Pelita penunjuk jalan ada ditangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102123,"Tahun baharu semua berazam,","Kegagalan lalu ingin diganti;","Usaha yang gigih dimulai iltizam,","Pasti berjaya hasil yang berkualiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102124,"Pertahanan negara semua dikerah,","Ruang menyusup musuh durjana;","Keindahan semalam menjadi sejarah,","Terpahat dihati selama-lamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102125,"Tidaklah mudah batu ditikam,","Hairan melihat orang yang melakukan;","Peristiwa bersejarah harus dirakam,","Untuk dikenang dimasa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102126,"Bunyi meriah mengetuk kuali,","Masakan istimewa pelanggan biasa;","Segala yang berlaku usah disesali,","Pasti ada pengajaran yang berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102127,"Hang Jebat pahlawan yang gagah,","Tidak gentar semasa dipertempuran;","Bertemu sahabat lama adalah anugerah,","Hargailah selagi diberi kesempatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102128,"Kereta kecil bernama kancil,","Kereta lama bernama Saga;","Jasa sahabat usah di perkecil,","Sumbangannya berguna kepada negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102129,"Keadaan berdebu aktiviti di kuari,","Ganggu pernafasan sesak didada;","Sesuatu yang berharga sentiasa dicari,","Sukar didapati namun tetap ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102130,"Sebelum melintas lihat kanan dan kiri,","Bagi elakkan dari kemalangan;","Pemimpin berwibawa sukar dicari,","Nawaitu yang betul menjadi landasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102131,"Bunga mawar bunga melati,","Baunya harum resah pun hilang;","Pensyarah muda harapan universiti,","Meneruskan tradisi keilmuan gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102132,"Banyak sudah pengeluaran beras,","Penyelidikan oleh ahli akademik;","Kasih sayang harus menjadi teras,","Segala perhubungan menjadi dinamik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102133,"Lakukan segalanya untuk negara,","Elakkan daripada salah dan silap;","Hubungan kekeluargaan teruslah dipelihara,","Dengan tunaikan segala tanggungjawab.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102134,"Pagi-pagi memakan roti,","Roti dimakan bersama rakan;","Usah mudah kecilkan hati,","Hanya menggugat hubungan kemanusiaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102135,"Mulakan perjalanan pergi ke Perdu,","Singgah berehat di pekan Sigli;","Kepada yang dikasihi sentiasa dirindu,","Ada waktu kan bertemu lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102136,"Sungai Sarawak berliku-liku,","Perahu penambang datang dan pergi;","Janalah ilmu dengan berguru,","Sebarkan ilmu tidak kan rugi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102137,"Dimasak pedas tiram dan lokan,","Terasa lidah seperti dipanah;","Ikatan suci perlu dilakukan,","Untuk mengelak maksiat dan fitnah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102138,"Potensi merbahaya harus dihindari,","Risiko dikaji dan harus disisih;","Kepura-puraan akhirnya diketahui,","Kerana hati yang tidak bersih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102139,"Sesuatu yang indah hati berkenan,","Ketenangan hati itulah yang dicari;","Setiap hari adalah perjalanan,","Tentukan destinasi yang sudah pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102140,"Kesedihan kongsikan bersama rakan,","Ladeni bersungguh hindari resah;","Berat hati untuk meninggalkan,","Kerana tahu ada hati sedang gelisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102141,"Waktu senja berdiri di titi,","cahaya mentari semakin kelayuan,","Alumni berjasa harapan universiti,","Ilmu dijana harus dimanfaatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102142,"Hindari tingkah untuk menganiaya,","Hanya mencetus rosaknya hati;","Ke Banda Aceh destinasi seterusnya,","Pemikiran intelektual mari dikongsi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102143,"Marilah mencari udang kara,","Di pohon bakau di celah celahan;","Biarlah dirahsiakan sesetengah perkara,","Untuk kebaikan elak kemudharatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102144,"Padang memutih dengan biri-biri,","Tidak diketahui siapa yang punya;","Kemenangan sememangnya kita mencari,","Kekalahan juga besar pengajarannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102145,"Usah berprasangka kepada rakan,","Berganding bahu turun dan naik;","Perkara yang baik harus disegerakan,","Allah tentukan buruk dan baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102146,"Ramai orang makan di warung,","Tempat kegemaran apek dan nyonya;","Usahawan muda harus didorong,","Walau sekecil mana pun perniagaannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102147,"Usahlah menjadi seorang pemarah,","Hanya melukakan banyaknya hati;","Sebenarnya kita berhutang sejarah,","Kepada generasi pelopor universiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102148,"Enak sekali gulai serati,","Berat badan perlulah dijaga,","Generasi muda perlu didekati,","Kongsikan aspirasi hasrat membara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102149,"Pangkalan kapal di hujung tanjung,","Tempat bermula titik perantauan;","Kepada yang berjasa harus disanjung,","Dengan gelaran yang bersesuaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102150,"Terbang tinggi burung tempua,","Setelah lelah singgah sekali;","Pilih pemimpin yang berwibawa,","Berintegriti tinggi mematuhi janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102151,"Makan soto dengan begedil,","Lebih enak dengan sup kaki;","Lakukan tanggungjawab dengan adil,","Hindari penganiayaan dan hasad dengki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102152,"Biar pun seri berpuas hati,","Perlawanan kedua menanti di Terengganu;","Segala permasalahan harus diamati,","Tidaklah nanti menjadi keliru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102153,"Lama berpisah mari berkunjung,","Bawa hadiah buah berangan;","Dengan semangat perjuangan dijunjung,","Sokongan moral menjadi dorongan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102154,"Dalam hidup ramaikanlah kawan,","Walau keikhlasan sukar diramal;","Segala kesenangan adalah ujian,","Bersyukurlah dengan pembuktian amal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102155,"Lagu didendang gendang dipalu,","Para hadirin tertunailah hasrat;","Menilai semula perancangan lalu,","Mengukuhkan usaha mencapai matlamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102156,"Dalam pemikiran utamakan umat,","Keutamaan sendiri tidakkan teraniaya;","Selamat jalan sahabat terhormat,","Semoga hasrat tercapai jayanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102157,"Yang akan pergi kita merai,","Yang datang diterima sentiasa;","Segala pengorbanan harus dihargai,","Kerana merekalah hidup kita selesa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102158,"Jika berselisih usahlah berkelahi,","Nanti ada yang rasa teraniaya;","Kasih sayang terbina kerana Ilahi,","Hadiah diberi dengan setulusnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102159,"Sungguh berdebar di dalam dada,","Tunggu pengadil meniup wisel;","Dengan usaha berlipat ganda,","Kejayaan diharap pasti berhasil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102160,"Pisang nangka dibuat lepas,","Juadah petang bersama keladi;","Dengan bahasa kita mendapat,","Pandai mengikat dengan budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102161,"Hari ini Ahad yang indah,","Kicauan burung riang menyanyi;","Usah diturut hati yang gundah,","Bertemulah sahabat pujuklah hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102162,"Burung hantu meredah malam,","Untuk mendapat siapakah mangsa;","Apabila bertemu ucapkan salam,","Semoga rahmat melingkungi kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102163,"Beban dibawa dengan menjunjung,","Keringat mengalir nafas dihela;","Orang berjasa haruslah disanjung,","Itulah adat budaya yang mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102164,"Memang tersiksa urat dan saraf,","Kerja berterusan tanpa berhenti;","Keupayaan diri akan diiktiraf,","Tanpa perlu menonjolkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102165,"Usahlah hanya memikir kekayaan,","Ketenangan jiwa yang lebih utama;","Segala dirancang mengejar kejayaan,","Allah jualah yang menentukannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102166,"Bila berduka usah menyendiri,","Carilah teman untuk bercerita;","Apa pun kekurangan yang disedari,","Syukur dan redha menenangkan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102167,"Belikan saya sebungkus halwa,","Mulut pahit dicarikan manisan;","Kebahagiaan tercipta daripada jiwa,","Gembirakan rang lain mengangkat kesusahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102168,"Seri Mersing sungguh jelita,","Gadis desa yang jadi pingitan;","Iltizam berbakti tersemat didada,","Kepada universiti janji disemarakkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102169,"Tidaklah mudah berbicara setia,","Hanya kekuatan dan jati diri;","Walimahtulurus majlis yang mulia,","Undangannya wajar sekali dipenuhi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102170,"Isu dibicarakan orang Penan,","Masa depan mereka usah diketepi;","Sebagai orang tua mainkan peranan,","Elakkanlah menjadi batu api.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102171,"Enak sekali menghirup kuah,","Laksa Sarawak menjadi pilihan;","Rezeki yang datang melimpah ruah,","Golongan memerlukan berilah perhatian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102172,"Gembira sungguh memancing ikan,","Di tengah laut nelayan beraksi;","Segala kekusutan perlu diselesaikan,","Demi kepentingan kelangsungan organisasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102173,"Pergi berkelah di pedalaman Sungkai,","Barang bekalan sama-sama digalas;","Segala kekusutan telah dirungkai,","Asalkan asas perbincangan adalah ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102174,"Riang gembira burung belatuk,","Alam di hutan segar terasa;","Pemimpin rakyat perlu dibentuk,","Ditegur dinasihat sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102175,"Perjalanan sukar ke Kalabakan,","Namun suasana penuh gembira;","Hari Malaysia kita rayakan,","Perkukuh kebersamaan hormati yang berbeza.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102176,"Daun kunyit halus dihiris,","Taburkan di atas ikan gelama;","Biar pun hati sering terguris,","Tanggungjawab asasi tetap dilaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102177,"Bila lapar pergilah makan,","Lauk ikan ditambah udang;","Orang bersalah harus dibicarakan,","Tiada pilih kasih tegakkan undang-undang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102178,"Mencari lokan di pinggir belukar,","Harus bergerak dengan perlahan;","Universiti penyelidikan gagasan yang besar,","Harus didukung penyelidik berketrampilan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102179,"Padi huma harum baunya,","Warnanya menguning dipancar mentari;","Janganlah malu untuk bertanya,","Menjadi maju teruslah mempelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102180,"Burung berkicau riang sekali,","Awan mendung berarak perlahan;","Awal Muharram kini datang lagi,","Masa merenung pelbagai cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102181,"Meneroka alam di dalam hutan,","Masih kelihatan kehidupan primitif;","Isikan hari dengan kegembiraan,","Bermula dengan pemikiran positif.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102182,"Berita gembira hati teruja,","Berita duka ditanggung berat;","Selagi hidup terus bekerja,","Itulah bekal perjalanan ke akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102183,"Harum baunya buah tarap,","Dijual orang di Tamu pagi;","Kepada Ilahi tempat mengharap,","Usaha bersungguh usah berhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102184,"Hari Jumaat mari muliakan,","Pergi ke masjid memakai jubah;","Setiap hari fikirkan pembaharuan,","Cara bekerja perlu turut berubah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102185,"Terasa pedas tergigit cabai,","Hingga menikam ke dalam tulang;","Peluang terbentang usah diabai,","Asal patuhi peraturan undang-undang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102186,"Hilangkan mengantuk memakan kuaci,","Memandu kereta pulang ke desa;","Mari mulakan dengan niat suci,","Hari baru untuk menabur jasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102187,"Menyakiti hati bukan amalan,","Hilangkan perasaan dengki dan iri;","Menyantuni pelajar baharu menggembirakan,","Berkongsi pengalaman dan aspirasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102188,"Pohon rendang dipinggir kali,","Bunganya rimbun bertangkai-tangkai;","Mesyuarat yang panjang usah dikesali,","Persoalan yang buntu kini terungkai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102189,"Pemikiran sendiri usah dipasung,","Berita yang diterima masih diragui;","Dengan harapan yang tinggi menggunung,","Kampus didatangi untuk terokai ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102190,"Setelah berusaha menjadi kurus,","Perlu dimbangi kesihatan diri;","Banyak sekali undangan walimahtulurus,","Perlu dihargai dengan menghadiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102191,"Sungguh nyaman air perigi,","Musim kemarau tetap berair;","Hari Kemerdekaan perlu dihargai,","Perjuangan mengisi tidak kan berakhir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102192,"Sudah lama tidak bersua,","Tentulah dihati penuh kerinduan;","Kemerdekaan sebenar Kemerdekaan jiwa,","Teguhkan semangat hadapi cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102193,"Kandungan protein dalam kekacang,","Sihat sejahtera sayangi nyawa;","Perancangan pembangunan harus berjangka panjang;","Konsep kelestarian jadi landasannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102194,"Makan malam di bawah bangsal,","Bangsal dibina untuk kenamaan;","Bersama kelompok pimpinan Orang Asal,","Mencetus keinsafan menawar pembelaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102195,"Banyak khasiat produk ikan,","Mencarinya mudah tidaklah sukar;","Carilah sahabat yang sentiasa menenangkan,","Hindari orang yang mengajak mungkar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102196,"Usah mencipta sebarang kebakaran,","Tebal jerebu yang sungguh menyiksa;","Terimalah hari baharu dengan kesyukuran,","Lakukan kebaikan kerana itu fitrah manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102197,"Setiap tugasan perlu ditekuni,","Barulah hasilnya boleh dibangga;","Masyarakat desa universiti santuni,","Kerana kewujudannya untuk mereka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102198,"Pedas juga rasanya cabai,","Enak dimakan bersama nasi;","Aduan golongan biasa jangan diabai,","Anggap itu sayangkan organisasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102199,"Sungguh enak gulai di kawah,","Ditambah pula juadah rendang;","Semasa menikmati juadah yang mewah,","Kesusahan orang harus dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102200,"Berhenti melihat kiri dan kanan,","Jalanraya sibuk setiap masa;","Eidul Adha semarak pengorbanan,","Menyantuni fakir miskin lebih utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102201,"Suburkan kemesraan bertegur sapa,","Suasana girang kebahagiaan dilestari;","Walaupun kita bukan kroni sesiapa,","Berusaha menggembleng kudrat sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102202,"Sungguh manis buah pulasan,","Musimnya datang terus dinanti,","Tinggalkan Sarawak dengan kepuasan,","Mi kolok dan laksa sudah dinikmati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102203,"Laksa Sarawak enak sekali,","Makanan tradisi di Bumi Kenyalang;","Usah dihina pemimpin sendiri,","Bina semula negara bukan kepalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102204,"Membakar sate dengan arang,","Baunya harum mencengkam kalbu;","Usah dicari kekurangan orang,","Yang kurang Itulah perlu dibantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102205,"Syarahan berkenaan perniagaan sosial,","Membasmi kemiskinan matlamat utama;","Harus dikenalpasti dimensi kritikal,","Mencari penyelesaian usahlah lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102206,"Banyak sekali kambing biri-biri,","Meragut rumput padang kehijauan;","Syukur Alhamdulillah nikmat diberi,","Berpada-pada dengan lazat makanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102207,"Kesalahan ternyata kerana suapan,","Disoal hakim diam membatu;","Kesabaran nilai asas kehidupan,","Menjadi landasan menghukum sesuatu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102208,"Sakit terasa ditusuk peniti,","Mata yang tua hilang kejelasan;","Pengalaman orang harus diteliti,","Mana yang baik jadikan teladan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102209,"Berjalan malam di pinggir hutan,","Terasa dikejar jembalang dan hantu;","Bila telefon mengalami kerosakan,","Hubungan komunikasi sangat terganggu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102210,"Pergi melancong memandu kereta,","Bawalah teman usah bersendiri;","Sahabat adalah cerminan kita,","Lambang peribadi dan harga diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102211,"Perjalanan Jauh Hingga ke Labis,","Hendak membeli sepikul cabai;","Tugas dalam hidup tidak kan habis,","Kerehatan diri jangan diabai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102212,"Biar pun lembut batang keladi,","Dipotong-potong dijadikan lauk;","Mari diikutkan resmi padi,","Semakin berisi semakinlah tunduk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102213,"Air Asam pencecah kerang,","Dibakar juga Ikan tenggiri;","Dalam sentiasa jaga hati orang,","Sesekali penuhi hak diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102214,"Janganlah ketawa berdekah-dekah,","Takut terhenti nafas berhembus;","Hari ini sungguh rasa bertuah,","Bertemu Semula teman-teman sekampus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102215,"Asap menjulang hingga ke awan,","Penduduk bersama melawan api;","Kedudukan dan jawatan bukan kebanggaan,","Amanah dipikul berat sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102216,"Pergi ke kali mencuci kain,","Tempat mandian anak teruna;","Usah biarkan kesukaran orang lain,","Ini juga ujian buat kita semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102217,"Surah yang Hujung kini disudahi,","Pengajaran diterap menjadi mulia;","Perginya Jiran menghadap Ilahi,","Nama yang baik kerana amal di dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102218,"Tidak lagi kelihatan bulan,","Penghujung Syawal kita sedari;","Masa terbaik pererat hubungan,","Kunjung mengunjung sempena Eidul Fitri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102219,"Hari Raya disambut meriah,","Sanak Saudara Datang berkunjung;","Meskipun cabaran harus diredah,","Nilai Murni perlu dijunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102220,"Memang sungguh berlangkah kanan,","Enak hidangan pulut dibakar;","Orang berilmu harus berperanan,","Untuk membina masyarakat yang sedar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102221,"Sungguh Indah Warna pelangi,","Lebih kelihatan menjelang Siang;","Eidul Fitri masih terus dirai,","Menyatakan syukur pertingkat Kasih sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102222,"Ahli falsafah Profesor Samian,","Pandangannya bernas juga berprihatin;","Tunjukkan sedikit rasa kebersamaan,","Cukup memotivasi warga yang dipimpin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102223,"Bukanlah mudah memberi kepuasan,","Melayan pelanggan petang dan pagi;","Kecantikan paras hanyalah hiasan,","Keindahan budilah yang kekal abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102224,"Makan berselera berlauk gulai,","Sayur nangka berserta ikan;","Para pesara aset bernilai,","Pengalaman lalu wajar dimanfaatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102225,"Kalau bercerita tidak kan habis,","Kisah menarik, seram dan ngeri;","Hari ini memandu menuju Labis,","Merai perkahwinan pembantu sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102226,"Masak lemak masak kari,","Semuanya perlu dicampur santan;","Meskipun kita masih merai EidulFitri,","Kebaikan Ramadhan teruslah diamalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102227,"Banyak gunanya kain perca,","Dijahit elok dibuat kain;","Kegembiraan diri boleh dicipta,","Dengan menggembirakan orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102228,"Sungguh enak berulam cabai,","Walaupun pahit sayur peria;","Kesihatan fizikal jangan diabai,","Meski bermula sihat mental dan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102229,"Berhari raya siang dan malam,","Mengerat Silaturrahim budaya kita;","Buang yang keruh jelaskan yang kelam,","Hidup bermasyarakat semua berbahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102230,"Rendang ketupat lontong dan lodeh,","Hidangan utama di Hari Raya;","Kekurangan orang janganlah dilondeh,","Kelak terbuka segala keaiban kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102231,"Kisah lama janganlah dilupai,","Ada juga teladan dan pengajaran;","Undangan rumah terbuka harus dirai,","Silaturrahim pasti dapat diperkukuhkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102232,"Setelah bekerja pergilah bersukan,","Untuk kesihatan jiwa pun tenang;","Syukur dan redha asas kebahagiaan,","Setelah melepaskan kesukaran orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102233,"Sungguh berkhasiat daun pegaga,","Untuk kesihatan ulam dimakan;","Kerahsiaan Mesyuarat harus dijaga,","Tidaklah nanti disalahtafsirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102234,"Untuk mendapatnya mari membida,","Dengan kelebihan luas pengalaman;","Usah dihina bahasa ibunda,","Daripadanya Jiwa Bangsa dipaparkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102235,"Ada kehidupan masih primitif,","Melihat yang asing menjadi malu;","Isu Bahasa sentiasa sensitif,","Kendali dengan matang berasas ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102236,"Sungguh enak juadah bersarapan,","Nasi dagang berlaukkan tuna;","Setiap hari menjanjikan harapan,","Kita sendiri yang perlu mewarnainya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102237,"Usahlah ketawa berdekah-dekah,","Hilanglah nanti kewibawaan diri;","Tidaklah rugi kita bersedekah,","Tuhan janjikan bertambahlah rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102238,"Lihatlah keindahan cahaya mentari,","Segarkan sukma pagi dan petang;","Manfaatkan kemuliaan Eidul Fitri,","Menebar cinta dan kasih sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102239,"Rayakan Eidul Fitri pasang pelita,","Hantar juadah kepada Norhayati;","Mulakan hari dengan bercinta,","Cinta yang suci lagi diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102240,"Asallah benang darilah kapas,","Kain dibentang ditengah hari;","Usah dikenang musibah yang lepas,","Kalau dikenang merosakkan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102241,"Dalam strategi sediakan kerangka,","Setiap persaingan tidak kan kalah;","Masa depan sukar dijangka,","Berusaha, berkeyakinan dan bertawakallah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102242,"Berhidung belang suka mengurat,","Hidup sia-sia seperti hampas;","Hubungan sejati tanpa bersyarat,","Bermula dengan hati yang ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102243,"Mari berkelah di Tanjung Keling,","Pengkalan nelayan pergi dan pulang;","Jagalah perasaan orang sekeliling,","Kehidupan sejahtera hati pun tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102244,"Suara indah alunan Sang Puteri,","Memikat hati pendengar budiman;","Ziarah menziarahi tradisi EidulFitri,","Yang tua yang sakit jadi keutamaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102245,"Terlalu asyik dengan telefon tangan,","Hingga tak sedar tetamu datang;","Saat meriah menjelang lebaran,","Pertingkat ukhuwah prasangka dibuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102246,"Daun Sireh disusun rapi,","Dalam tepak bertingkat-tingkat;","Ramadhan semakin menghilang diri,","Jiwa terdidik ketaatan diperkuat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102247,"Banyak gunanya pokok dedalu,","Ada kesakitan boleh dirawati;","Usah dikenang yang telah berlalu,","Masa hadapan Sentiasa menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102248,"Sahabat kenalan bernama Ehsan,","Sahabat yang lama bernama Mukhlas;","Marilah kita raikan semua insan,","Mulakanlah dengan hati yang ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102249,"Adik yang manis siapa yang punya,","Ingin memiliki halangan melintang;","Setiap yang berlaku ada hikmahnya,","Meskipun pahit kebaikan mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102250,"Berlari anak di atas balapan,","Semua masalah hingga terleka;","Pagi yang hening menyegar harapan,","Tuhan mendengar rintihan dan duka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102251,"Menjelang raya pelbagai fesyen busana,","Dengan rekaan dan gaya terkini;","Setiap Hari ada rahsianya,","Peranan kitalah melakar dan mewarnai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102252,"Sebagai menghargai sumbangan buruh,","Ditetapkan hari cuti seluruh negara;","Lakukan segala yang Tuhan suruh,","Tinggalkan Segala yang dilarangNya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102253,"Sungguh jauh pergi berkelana,","Pemandangan pelbagai tidak dilupai;","Hidup ini kan Lebih bermakna,","Kehadiran orang lain kita hargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102254,"Membakar sate dengan arang,","Boleh dirasa lebih enaknya,","Kalau tak dibuka aib orang,","Tuhan sembunyikan aib anda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102255,"Hendak protein makanlah ikan,","Ikan dibeli di kedai nyonya;","Hari baru penuh menjanjikan,","Kita sendiri menentukan arahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102256,"Jasa pahlawan dilupa jangan,","Tanpa pengorbanan negara kan punah;","Cerita kejayaan menjadi dorongan,","Cerita kegagalan penuh dengan hikmah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102257,"Resmi lebah mencari bunga,","Enggan dekati segala yang jijik;","Hidup berkualiti mesti dibina,","Perlu dirancang secara strategik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102258,"Hidup setaman mesti bermuafakat,","Tegur menegur amalan menyeluruh;","Berbuka Puasa Bersama masyarakat,","Silaturrahim dipererat persahabatan diperkukuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102259,"Sangat bermakna suasana aman,","Segala perancangan dapat dijelma;","Bukit Frazer dingin terasa nyaman,","Harus dipelihara warisan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102260,"Negeri jauh di seberang samudera,","Pernah wujud kerajaan Perlak;","Ke Bukit Fraser hari ini kembara,","Urusan rasmi tidak dielak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102261,"Akhlak mulia patuhi agama,","Disukai Masyarakat segenap negeri;","Apabila datang seruan berderma,","Rebutlah ini Anugerah Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102262,"Pandu laju tanpa pertimbangan,","Menyebabkan keadaan jadi merbahaya;","Bulan Ramadhan banyak dugaan,","Orang yang insaf orang berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102263,"Berwarna kuning bunga kenanga,","Buat hiasan sanggul sang puteri;","Lakukan tanggungjawab dengan sebaiknya,","Landasan kejayaan berteras integriti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102264,"Menarik sungguh bunga nan merah,","Mari dipetik buat suntingan;","Usah diikut nafsu amarah,","Hanya meninggalkan kesal berpanjangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102265,"Sungguh berkhasiat madu kelulut,","Di batang kayu tempat menjana;","Ilmu duniawi terus dituntut,","Ilmu ukhrawi diperkukuh sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102266,"Buah mangga buah kuini,","Makan bersama sehingga puas;","Walaupun berpuasa ibadah dijalani,","Tanggungjawab kerja terus digalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102267,"Wajah mempesona pada sang puteri,","Hingga terdiam tidak terkata;","Mencari bahagia cara sendiri,","Menggembirakan orang kebahagiaan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102268,"Pohon meranti di dalam hutan,","Bunyi siamang berkali-kali;","Betapa tinggi nilai persahabatan,","Permata yang hilang ditemui kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102269,"Selesai masalah hati pun lega,","Bolehlah ke padang Pergi bermain;","Mengharap sesuatu tanpa berusaha,","Hanyalah seperti angan Mat Jenin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102270,"Meriah perkelahan di Rantau Abang,","Terasa penat hampir termuntah;","Berilah dorongan kepada yang tumbang,","Berilah semangat kepada patah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102271,"Usah duduk hanya berangan,","Lembut bayu menyapa pipi;","Banyak berdoa peroleh ketenangan,","Mohon permasalahan mudah dihadapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102272,"Melambai-lambai sipohon ru,","Di tepi pantai tertambat rakit;","Pagi-pagi pulang Ke Johor Bahru,","Menziarahi adik yang sedang sakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102273,"Harungi ujian semangat digagahi,","Hasil didapat boleh diramal;","Ramadhan mulia anugerah Ilahi,","Kuatkan Iman tingkatkan amal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102274,"Kolam yang luas ternakan ikan,","Usaha yang gigih menjana laba;","Lakukan kebaikan dengan keikhlasan,","Ganjaran keuntungan tiba tanpa disangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102275,"Mari berprihatin dengan kesusahan,","Dialami Masyarakat dimana sahaja;","Apakah keuntungan dengan permusuhan,","Hanya keresahan yang tiada sudahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102276,"Anak keriangan dalam pelukan,","Kepulangan ayah yang dinanti;","Sahabat sejati mendorong kebaikan,","Itulah nilai Persahabatan sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102277,"Di sebelah timur matahari terbit,","Memberi Sinar untuk kehidupan;","Hidup kita sentiasa diaudit,","Audit kekalkan Universiti penyelidikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102278,"Pelbagai idea dan juga saranan,","Untuk menyelesai persoalan yang ada;","Isu perpaduan masih terus dibicarakan,","Peranan Universiti untuk menggubal formulanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102279,"Ada daun dijadikan ubat,","Dihias untuk sirih junjung;","Rasa gembira meraikan sahabat,","Dari Bandung mereka berkunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102280,"Sungguh mekar bunga yang kembang,","Tersenyum manis tuan yang punya;","Udara segar jiwa pun tenang,","Nikmat Tuhan tak ternilai harganya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102281,"Bintang malam indah bergemerlapan,","Tidaklah malam gelap gelita;","Hadirlah hari ini dengan harapan,","Semoga yang diimpikan menjadi nyata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102282,"Buah ranum menghiasi pohon,","Mari dipilih dengan teliti;","UKM berusia 47 tahun,","Desakan rakyat menjelma realiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102283,"Mencuci mata di pasar Karat,","Barang antik, kraf dan pakaian;","Sakit bermakna memberi isyarat,","Tubuh dan minda perlu kerehatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102284,"Dalam sungai berenanglah ikan,","Jangan Leka ada buaya;","Hari Guru kita raikan,","Mengenang jasa jadikan budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102285,"Ikan seluang ikan tenggiri,","Enak lagi ikan pelata;","Apabila sakit melanda diri,","Dekat kepada Tuhan amatlah nyata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102286,"Pergi ke pasar membawa raga,","Hendak membeli ikan belanak;","Kasih ibu membawa ke syurga,","Santuni dan hormatinya tanggungjawab anak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102287,"Segala kelebihan usah dipersia,","Terutama salurkan kepada yang berhak;","Hari ini harus kembali ke Malaysia,","Tanggungjawab besar dilupa tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102288,"Sejarah luka tragedi Tun Teja,","Berpunca kerosakan di dalam hati;","Ceriakan suasana di tempat kerja,","Barulah produktiviti dapat dipasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102289,"Jika melancong marilah berkumpulan,","Terutama sesiapa yang kurang berani;","Pembangunan rumah anak yatim terus berjalan,","Moga Allah mempermudah urusan murni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102290,"Sungguh Indah pemandangan Brastagi,","Tidak terasa Jauh perjalanan;","Tanah Aceh dikunjungi lagi,","Misi Murni perlu disempurnakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102291,"Nafsu makan sukar ditahan,","Asam pedas ikan ketutu;","Nikmati segala anugerah Tuhan,","Asalkan dalam sempadan yang ditentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102292,"Pokok dedalu yang sangat pemalu,","Walau dipenuhi dengan berduri;","Setiap detik yang telah berlalu,","Ada hikmah yang boleh dipelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102293,"Laut berkilau cahaya mentari,","Pagi-Pagi ayam berkokok;","Larian Kanser hari ini disertai,","Kesedaran masyarakat perlu dipupuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102294,"Pagi-pagi memggoreng bertih,","Diikuti pula menggoreng bawang;","Di dalam hati menangis merintih,","Batu Putih pula dimiliki orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102295,"Mencari kedamaian pulang ke desa,","Untuk tenangkan rindu di hati;","Tegur menegur jadikan budaya,","Itulah makna persahabatan sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102296,"Burung merbok di Kampung Serendah,","Bernyanyi riang sambil menari;","Semakin berilmu semakin merendah,","Itulah resmi padi yang harus dipelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102297,"Dari benang menjadi kain,","Kain dipakai gadis bestari;","Sampaikan nasihat kepada orang lain,","Mesej sebenar untuk diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102298,"Musim buah di Cangkat Jering,","Langsat, rambutan dan juga pulasan;","Menziarahi sahabat yang gering,","Mengukuh persahabatan dan keikhlasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102299,"Nelayan ke laut menangkap Ikan,","Tiba musim si ikan langgai;","Sungguh terharu saat meninggalkan,","Segala kenangan dan sahabat handai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102300,"Indah sungguh dipagi hari,","Burung berkicau angin menderu;","Hari ini lebih berseri,","Terpisah lama kini bertemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102301,"Pergi belayar jauh ke Lampung,","Kapal berhayun dilanda badai;","Gembira sungguh pulang ke kampung,","Bertemu saudara sahabat handai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102302,"Sentiasa mengingati yang dikasihi,","Apabila berpisah begitu lama;","Hari yang Padat ditempuhi,","Semoga menjadi ibadah yang diterima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102303,"Lakukan kerja janganlah culas,","Barulah kita memperoleh pahala;","Hormati orang lain dengan ikhlas,","Itulah cerminan keperibadian kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102304,"Mari banyak memakan Ikan,","Protein kaya Usah dipantang;","Hari ini sungguh mendebarkan,","Pertahan belanjawan tahun mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102305,"Mari perkukuh ilmu fardu ain,","Supaya kefahaman lebihlah nyata;","Usah dipersalah kepada orang lain,","Segala yang berlaku ada hikmahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102306,"Di hujung pedang tergadai nyawa,","Drama dipersembah secara tulen;","Manusia terbaik di atas dunia,","Sentiasa berusaha memanfaat orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102307,"Mari diganti dengan papan,","Supaya kuat menjadi titi;","Rancang teliti untuk masa depan,","Supaya Kehidupan Tidak dikesali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102308,"Bersarapan pagi dengan manisan,","Barulah Berjalan dengan bertenaga;","Andainya kebaikan ingin balasan,","Bukannya kebaktian tetapi berniaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102309,"Mari ke Seberang dengan berakit,","Digembleng semua para pemuda;","Bercuti menziarahi Keluarga yang sakit,","Pererat silaturrahim keutamaan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102310,"Semangat kuat terus membara,","Dari Berjalan Ingin berlari;","Sama ada sedih Atau gembira,","Pasti ada hikmah yang harus dipelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102311,"Habis santan tinggal lah hampas,","Banyak gunanya sedang menanti;","Jangan dikenang barang yang Lepas,","Yang hilang pastinya berganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102312,"Pucuk mempelam pucuk dedalu,","Tidak kan sama pucuk melati;","Walaupun semalam sudah berlalu,","Pastinya banyak hikmah yang diperolehi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102313,"Ada kebaikan bersifat luaran,","Daya tahannya merosot nanti;","Bina keyakinan meredah cabaran,","Kejayaan menanti sudahlah pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102314,"Hendak maju sentiasa lah berfikir,","Agar mendapat banyak idea;","Menyantuni masyarakat yang terpinggir,","Dengan Pendidikan hidup terbela.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102315,"Wanita jelita memimpin kuda,","Wajahnya manis Dengan senyuman;","Hargai lah segala yang kita ada,","Pasti Hidup terasa nyaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102316,"Sungguh gembira bertemu rakan,","Cerita lama bergelak ketawa;","Majlis meriah pengantin diraikan,","Bagaikan pinang dibelah dua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102317,"Kalau tuan pergi ke Pekan,","Singgah sebentar di Seri Menanti;","Ikatan silaturrahim mari perkukuhkan,","Bagaikan rambut disimpul mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102318,"Daripada benang dibuat kain,","Siap pakaian yang tinggal perca;","Bahagia sekali gembirakan orang lain,","Melepaskan kesempitan yang ditanggungnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102319,"Dihadiahkan tali leher berwarna merah,","Untuk dipakai di majlis rasmi;","Amalkan pengajaran daripada hijrah,","Hilang keburukan muncul kemuliaan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102320,"Jangan diteruskan hati yang gundah,","Yang pergi tetap kan pergi;","Hidup ini sememangnya Indah,","Dengan syarat saling menghormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102321,"Menyusuri sungai Dengan berperahu,","Hendak menuju Ke bandar Muar;","Masa hadapan siapa yang tahu,","Teruslah berharap, berusaha dan berdoa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102322,"Mengail ikan di Pulau Meranti,","Mencandat sotong di Pulau Perhentian;","Dalam kesibukan Tak pernah berhenti,","Kesihatan harus diberi perhatian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102323,"Kalau mencari sibuah Petai,","Dalam Hutan banyak bergantungan;","Menyantuni orang Asli di Kg Melai,","Potensi mereka perlu dikembangkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102324,"Heboh cerita di sana sini,","Gempar seketika seluruh pekan;","Hari Ini bermusafir ke Tasik Chini,","Esok PM rasmikan Pusat penyelidikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102325,"Perempuan Lebih Ramai daripada lelaki,","Fenomena IPT di seluruh negara;","Jangan dikesali yang Tak dapat dimiliki,","Syukuri nikmat yang Tak terkira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102326,"Hutan rimba dipenuhi Kayu kayan,","Habitat fauna bersuka ria;","Setiap kita memiliki impian,","Tanpa usaha tiadalah jayanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102327,"Dahulu namanya Pantai Cinta Berahi,","Kini Bernama Pantai Cahaya Bulan;","Seorang Jiran dijemput menghadap Ilahi,","Saat kematian memang rahsia Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102328,"Dari anjung Tinggi dapat dilihat,","Pemandangan indah sawah dan bendang;","Walaupun Hari Ini terasa tidak sihat,","Syukur kerana usia diperpanjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102329,"Sahabat lama bernama Zainal,","Asal usul nya dari Lambor;","Di Penang tiada Yang Lebih terkenal,","Selain nasi kandar dan pasembur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102330,"Untuk menyeberang hanyalah berenang,","Kerana ada orang bakar jambatan;","Pagi ini berkelana ke Pulau Pinang,","Semoga dipereratkan ikatan persaudaraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102331,"Sungguh merdu suara biduan,","Makan dan minum terus dijaga;","Ajaran agama jadikan panduan,","Pergaulan berlangsung penuh susila.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102332,"Gunung Daik Indah pemandangan,","Kayuh perahu sepenuh Tenaga;","Perkara yang baik jadi kenangan,","Buat teladan untuk semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102333,"Kuih muih di dalam bakul,","Untuk dijual seluruh desa;","Para hadirin mendengar Dengan ta'kul,","Tan Sri Zeti yang sedang berbicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102334,"Memahami psikologi kumpulan khalayak,","Barulah berkesan ceramah diberi;","Nabi berpesan usahlah riak,","Siapa menganugerah kesempurnaan diri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102335,"Bagaikan lagu dengan irama,","Saling bergantung dengan harmoni;","Hari ini berhimpun sahabat yang lama,","Silaturrahim yang kukuh semakin dirasai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102336,"Lembu mengejar sibaju merah,","Selamatkan diri lintang pukang;","Selamat menyambut Tahun Baharu Hijrah,","Amalan tahun Ini yang lebih cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102337,"Setiap ketika asyik terkenang,","Peristiwa lalu masih berluka;","Tiada putus asa harus dipegang,","Kehidupan bahagia di Hari muka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102338,"Biar pun Hari semakin kelam,","Anak-anak bermain masih di padang;","Hari Ini lebih baik daripada semalam,","Itulah azam orang cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102339,"Pergi memburu kijang dan Rusa,","Untuk dipersembah pada raja negeri;","Persahabatan abadi sangat terasa,","Pendaftar Universiti duduk sama berdiskusi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102340,"Membakar Ikan gunakan Arang,","Walaupun lambat setia menunggu;","Sentiasa lah doakan kebaikan orang,","Kelak kebaikan datang kepadamu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102341,"Pergi Ke pasar membeli Ikan,","Timbangan dibuat menggunakan Kati;","Semuanya boleh sahaja dilakukan,","Asal berkeazaman serta berkreativiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102342,"Guru yang baik dipanggil sifu,","Ajarannya berkesan walaupun garang;","Biar pun keburukan orang kita tahu,","Jangan sekali2 diberitahu orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102343,"Tokoh ternama Dato Senu,","Orang politik banyak bicara;","Hari Ini berada di Trengganu,","Bertanya khabar saudara mara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102344,"Di Pantai Indah Mencari lokan,","Walaupun sukar hati gembira;","Cuti Sabtu Tak dipersia-siakan,","Bengkel jabatan mengukuh cara bekerja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102345,"Lemak manis ikan belanak,","Dimasak singgang buat hidangan;","Kucup Dan peluk lah anak-anak,","Semoga kukuh ikatan kekeluargaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102346,"Siput sedut dimasak lada,","Enak dimakan di tengahari;","Harus dimengerti aspirasi pemuda,","Namun memerlukan didikan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102347,"Duduk termenung di pinggir Kali,","Tempat biasa dikunjung datang;","Kenangan lalu subur kembali,","Bertemu semula sahabat yang hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102348,"Seperti anak kecil mendapat kaki,","Ke sana ke mari tidak keruan;","Hindari sama sekali hasad dan dengki,","Umpama api membakar segala kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102349,"Gembira sungguh ke kampung halaman,","Walaupun berubah pembangunan pesat;","Hadis Nabi menjadi pedoman,","Supaya diperjalanan tidak kan sesat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102350,"Banyak barangan di Pasar Karat,","Barangan lama turun temurun;","Indahnya hubungan tanpa syarat,","Kekal Mesra bertahun-tahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102351,"Dato Haron Din tokoh tersohor,","Kembali ke rahmatullah hari semalam;","Gembira dapat pulang ke Johor,","Erat kekeluargaan perteguhkan salam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102352,"Buah mangga Buah kuini,","Rasanya manis menambat rasa;","Hari ini acara bertemu alumni,","Mengikat jiwa agar terus berjasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102353,"Pelari handalan dari Kenya,","Kekuatan fizikal dan juga latihan;","Setiap permasalahan ada penyelesaiannya,","Usaha Dan tawakal itulah asuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102354,"Lakukan kerja janganlah leceh,","Supaya tidak meninggalkan beban;","Untuk tiga hari berada di Aceh,","Program Promosi dan Ibadah korban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102355,"Malam Raya terasa sunyi,","Mengenang perginya ibu dan bapa;","EidulAdha adalah Untuk menghargai,","Pengorbanan Nabi Ibrahim tiada bandingannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102356,"Sungguh jauh negara Balkan,","Pelbagai bangsa dan juga budaya;","Kini takbir sudah dilaungkan,","Tanda EidulAdha sudah bermula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102357,"Ke tempat jauh ingin berkelana,","Luas pandangan pertingkat pengalaman;","Jawatan dikejar hanyalah bencana,","Seandainya kepentingan peribadi terletak di hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102358,"Pergi ke Aceh untuk berkorban,","Anak yatim miskin perlu dikelola;","Nikmat masa semakin berkurangan,","Kesibukan bekerja diaturkan semula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102359,"Enak sungguh air kelapa,","Diminum pula Waktu kehausan;","Sifat Manusia sering terlupa,","Apabila disibukkan pelbagai urusan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102360,"Sungguh bersantun anak Pak Jusoh,","Berbeza pula anak Pak Atan;","Hati yang ikhlas sentiasa diasuh,","Akhlak mulia terlihat pada perbuatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102361,"Harga barang teruslah naik,","Sayur, ayam, daging dan ikan;","Media Sosial sangatlah baik,","Hanya yang bermanfaat mari sebarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102362,"Pengantin lelaki pakai tengkolok,","Gaun panjang pengantin perempuan;","Hari Ini hadiri Pangkor Dialogue,","Peserta berbincang tentang pembangunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102363,"Membeli Belah di kedai Hassan,","Untuk dibuat tepung pelita;","Apa dilakukan hendak lah berehsan,","Seperti diingin diperlaku kepada kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102364,"Enak rasanya ikan senohong,","Di tambah Lagi juadah lokan;","Nabi bersabda janganlah suka berbohong,","Pastinya menjerumus kepada keburukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102365,"Banyak hasil di batu karang,","Dimasak sambal tumis enak sekali;","Usahlah gemar mengata orang,","Seringlah lihat diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102366,"Negara jauh namanya Tahiti,","Ingin juga pergi cari pengalaman;","Hari ini gembira sungguh di hati,","Sahabat naik pangkat kerana berkelayakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102367,"Usia yang ada usah dileka,","Kelak rugi menyesal Tak sudah;","Hari Ini hari merdeka,","Martabat diri usah dipunah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102368,"Riang hati sehingga bergetar,","Cerita lucu tidak terkira;","Bersama mahasiswa baharu di dalam DECTAR,","Di ambang sambutan kemerdekaan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102369,"Usah kelelahan menuntut ilmu,","Pastinya padat dengan manfaat;","Nabi berpesan jagalah lidahmu,","Pasti selamat dunia dan akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102370,"Menjana kekayaan mengusahakan gaharu,","Bahan Utama produk termasyhur;","Meraikan kedatangan mahasiswa baharu,","Ke Kampus UKM lembah ilmu yang subur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102371,"Bersarapan pagi di Warung Mak Siti,","Makan hulut dengan sirap berguna;","Walaupun hari ini harus bercuti,","Laksana tugas di Kuantan pula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102372,"Bertemu teman terpisah lama,","Banyak bercerita kisah yang lalu;","Perancangan dibuat manfaat diutama,","Kepentingan Ramai mesti didahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102373,"Penerbangan petang meluncur laju,","Penumpang penuh padat sekali;","Kesekian kali ke Kota Kinabalu,","Penuh kenangan terpahat di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102374,"Di dada laut marilah berkolek,","Dengan bekal jala jemala;","Manfaat diri tak kan datang bergolek,","Melainkan berusaha sepenuh jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102375,"Ikan gelama ikan kembung,","Makan tengahari jadikan hidangan;","Janganlah jadi manusia yang sombong,","Segala yang ada hanyalah pinjaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102376,"Cerita pahlawan zaman berzaman,","Kepada watan mereka berbakti;","Mulakan hari dengan keazaman,","Tinggalkan jasa terpahat di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102377,"Anak kecil dipujuk pengasuh,","Hendak permainan orang lain punya;","Generasi muda dididik diasuh,","Pasukan beruniform Salah satu pendekatannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102378,"Bandar diraja namanya Pekan,","Pemandangan Indah suasana tenang;","Semangat patriotik perlu disuburkan,","Walau apapun cabaran mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102379,"Pembangunan pesat berkembang di Pagoh,","Banyak universiti dibina disana;","Perancangan yang baik usah di tangguh,","Susun strategi langkah dilaksana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102380,"Berkelana sampai ke negara Kenya,","Lebih menarik pengalaman di Vanuatu;","Doakanlah sahabatmu Tanpa pengetahuannya,","Lebih makbul doa seperti itu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102381,"Solat Subuh dua rakaat,","Tanda mengabdi kepada Ilahi;","Insan terpuji paling bermanfaat,","Kepada orang lain sentiasa peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102382,"Sungguh berbisa sengatan lipan,","Kesakitannya mencengkam tidak terperi;","Teruskan perjalanan dalam kehidupan,","Liku cabaran mematangkan peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102383,"Hujah sia-sia usah dilawan,","Pertengkaran tidak perlu dicipta;","Pandangan hidup yang benar jadi Panduan,","Akhlak yang benar diterjemahkan sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102384,"Mi rebus terlazat Di Warung Pak Latif,","Jadi tumpuan para pelancong;","Hidup lah dengan fikiran positif,","Jalan yang benar tidak terlencung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102385,"Sahabat lama namanya Kamaruzaman,","Penasihat Undang2 kerjayanya kini;","Menutup Kursus Siasatan Dalaman,","Untuk anggota Keselamatan seluruh universiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102386,"Hari Jumaat penghulu hari,","Dipenuhi dengan amal Ibadah;","Kejayaan orang janganlah diiri,","Perbaiki diri sedia berubah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102387,"Sudah tiba musim Buah-buahan,","Saat gembira yang dinanti-nanti;","Kematian adalah satu kenyataan,","Sediakan persediaan Tanpa berhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102388,"Buah cempedak Buah nangka,","Banyak ditanam di Indragiri;","Amat menghargai pertemuan tak disangka,","Pasti ada hikmah tersendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102389,"Tayangan filem sudah pun tamat,","Terkenang-kenang masuk ke hati;","Kalau hidup tanpa matlamat,","Seperti hidup tetapi mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102390,"Sahabat lama bernama Khair,","Walaupun terpisah masih di ingatan;","Setelah menyantuni masyarakat terpinggir,","Kini urusan Pejabat perlu diuruskan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102391,"Makan nasi berlauk Ikan,","Ikan dimasak goreng bercili;","Kampung Chuweh kini ditinggalkan,","Dengan azam untuk datang Lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102392,"Laluan bot sering melencung,","Bot disewa beramai-ramai;","Ramai pelancong datang berkunjung,","Kampung Chuweh yang tenang dan permai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102393,"Di laut penuh dengan tenggiri,","Ikan di Tasik banyaklah tulang;","Pertama kali mengalami sendiri,","Indahnya Tasik Banding yang tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102394,"Perjalanan jauh menuju destinasi,","Untuk berbakti sesama insan;","Ke Hutan Belum lama dirindui,","Hanya Hari ini kaki dijejakkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102395,"Buah mangga penuh berserat,","Amal mengambilnya untuk Kesihatan;","Kehidupan abadi adalah di akhirat,","Kebaikan di dunia menjadi bekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102396,"Sungguh damai suasana desa,","Burung berkicau udara pun nyaman;","Jiwa bahagia amat dirasa,","Menggembirakan orang yang sedang kesulitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102397,"Pantai bersih di Batu Feringgi,","Tempat bertemu tenang suasana;","Hari yang baru harus dihargai,","Penuh hikmah yang amat berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102398,"Riang-riang terus menyanyi,","Menghibur hati yang sedang rawan;","Aidil Fitri terus dirai,","Mengeratkan ikatan tali persahabatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102399,"Bunga mawar bunga kemboja,","Tetap dicari bunga yang wangi;","Walaupun suasana Raya masih dirasa,","Ibu bapa mertua tetap dikunjungi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102400,"Malam-malam duduk termenung,","Bintang bersinar indah bergemerlapan;","Badan penat Hati pula murung,","Masalah remeh diperbesar-besarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102401,"Pagi-pagi menelusuri titi,","Untuk mencari juadah sarapan;","Hari ini raya peringkat universiti,","Usaha pereratkan rasa kekeluargaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102402,"Sungguh berbakat Jamali Sadat,","Membuat orang senyum ketawa;","Hari ini jadual sangatlah padat,","Urusan kerja tiada hentinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102403,"Pulut dan ubi dibuat tapai,","Jamuan Raya dijadikan hidangan;","Segala impian boleh dicapai,","Andai berusaha dengan perancangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102404,"Besar sekali nilai pengorbanan,","Hingga terkesan di dalam Hati;","Walaupun semuanya telah ditakdirkan,","Dengan berusaha kejayaan sedang menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102405,"Biar jauh pergi berkelana,","Mencari ilmu Timur dan Barat;","Hidup Ini lebih bermakna,","Andai kita memberi manfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102406,"Menumbuk cili di dalam lesung,","Untuk dibuat sambal belaian;","EidulFitri masih lagi berlangsung,","Untuk mengeratkan tali persaudaraan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102407,"Tidak sempat hendak bermanja,","Masa yang ada penuh terisi;","Walaupun Sabtu tetap bekerja,","Ehwal hospital perlu ditangani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102408,"Hari Raya masih meriah,","Suasana tradisi terus diamal;","Menghadapi hidup usahlah gundah,","Kecemerlangan masa depan harus diramal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102409,"Bagaikan penukul dengan pemahat,","Sekali dipukul sekali terbenam;","Pemikiran cerdas Jasmani yang sihat,","Kuatkan semangat kentalkan azam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102410,"Setiap minit detik berharga,","Masa dibuang tak kan kembali;","Makan dan minum perlu dijaga,","Kesihatan sempurna tak boleh dijual beli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102411,"Mencari Nordin orang yang berjasa,","Kepada UKM 1970 telah ditubuhkan;","Orang beraya kita berpuasa,","Sihat rohani dan tubuh badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102412,"Pagi-pagi melangkah ke masjid,","Solat Subuh beri ketenangan;","Bersihkan hati ubati yang sakit,","Bersangka baik peringkatkan hubungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102413,"Rama-Rama menari dengan riang,","Bunga mekar segar mewang;","Aura AidilFitri mengisi ruang,","Mempererat silaturrahim yang sering dilupai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102414,"Hulurkan sedekah janganlah kikir,","Tanda bersyukur kepada Tuhan;","Teks ucapan saya sedang difikir,","Untuk dipersembahkan Isnin harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102415,"Hari raya sungguh meriah,","Kuih kegemaran termasuk kuih Koleh;","Yang menemani kita sedekah jariah,","Ilmu bermanfaat dan anak yang soleh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102416,"Asal tapai gunakan ragi,","Barulah menjadi enak dimakan;","Banyak cabaran usia begini,","Teguhkan Iman hadapi ujian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102417,"Buah tiin buah zaiton,","Enak rasanya penuh berkhasiat;","Biar menunggu bertahun-tahun,","Sabar berusaha meraih manfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102418,"Langit redup alam mendung,","berlarian anak di tengah padang;","golongan miskin harus dilindung,","hulur bantuan untuknya berjuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102419,"Lebuhraya sesak penuh kenderaan,","kota dituju untuk berkelana;","memandu kereta dengan penuh kesedihan,","tempat kenangan sudah pun sirna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102420,"Pekan kecil dipenuhi pekerja,","dari pelbagai negara orang;","mencari-cari dimana lokasi desa,","kini hanyalah tanah yang lapang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102421,"Desa indah sudah berubah,","pembangunan dimantera tiadalah tentangan;","sanak saudara semua diziarah,","yang tua dan uzur harus diutamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102422,"Perjalanan pulang amat membahagiakan,","namun ada kesedihan di dalam hati;","perayaan dirai eratkan perpaduan,","ikhlaskan jiwa hidup harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102423,"Raya kedua masih di sini,","Ibu bapa mertua harus disayang;","Ramadhan tinggalkan kesan di hati,","Di EidulFitri silaturrahim terbentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102424,"Madrasah Ramadhan teguh di hati,","jiwa terdidik dengan ketundukan;","dampak ibadah semoga abadi,","menjadi bekal membentuk keperibadian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102425,"Hari lebaran tanda kesyukuran,","ujian terhadap Iman dan taqwa;","silaturrahim terjalin penuh keikhlasan,","ampun dan maaf cetusan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102426,"Riadah berjadual sihat tubuh badan,","penyakit dihindari fikiran pun tenang;","walaupun hari ini penghujung Ramadhan,","amalan kebaikan terus berulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102427,"Sungguh terhibur dengan FM Klasik,","Lagu lama dilupa jangan;","kebaikan ditinggalkan jangan diusik,","Ke alam akhirat itulah bekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102428,"Buah epal buah mangga,","berbeza tetapi tetap berkhasiat;","Ramadhan telah berada di penghujungnya,","semoga terbentuk pendidikan yang bermanfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102429,"Sukar perjalanan ke Kalabakan,","tempatnya jauh tetapi indah;","hati yang gundah terus ditenangkan,","kepada Allah tempat berpasrah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102430,"Kukuhkan persaudaraan eratkan ikatan,","semoga hidup Aman dan damai;","bulan Ramadhan subur dengan keberkatan,","kebaikan berpanjangan terus tersemai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102431,"Sungguh hebat anugerah Sang Pencipta,","tanda bersyukur marilah solat;","mulakan hari baru dengan cinta,","Sebarkan bakti hidup yang bermanfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102432,"Dugaan yang datang bukan kesukaran,","kerana terlupa sering terpedaya;","marilah serentak membaca AlQuran,","kejayaan di dunia dan akhirat jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102433,"Sungguh berkhasiat sayur petola,","dimasak sup lebih berkhasiat;","walaupun kini tiba musim bola,","Ibadah Ramadan jangan sampai tergeliat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102434,"Bangun pagi menanak nasi,","Juadah sahur untuk sekeluarga;","Hari ini masyarakat berspekulasi,","Rombakan kabinet kesan kepada negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102435,"Bergurau senda anak lah kera,","melompat dari dahan kedahan;","Hari ini kembara hingga ke Jitra,","menziarah ahli masyarakat dalam kemiskinan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102436,"Mari pergi ke Tasik Bera,","Ikan dipancing dapat lah toman;","Mengapa anak kecil sanggup didera,","Tentu pelakunya tidak lagi siuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102437,"Semakin hari semakin pelupa,","cerdaskan fikiran dengan pembacaan;","seringkalilah hubungi ibu bapa,","semoga kita anak-anak tak abaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102438,"Di padang hijau gembala biri-biri,","tenang jiwa memandang kejauhan;","di bulan Ramadhan banyakkan memberi,","fakir dan miskin tentu amat memerlukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102439,"Malam hari pasang pelita,","pelita dipasang menerangi halaman;","AlQuran diturunkan untuk alam semesta,","sebagai petunjuk disepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102440,"Angin berhembus diwaktu pagi,","terasa segar hati pun lapang;","ajaran agama perlu dihayati,","martabat umat pasti terjulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102441,"Waktu Berbuka semuanya ceria,","Juadah terhidang lazat sekali;","Kita lah yang mencipta bahagia,","Bukan sekadar iradat yang dinanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102442,"Rasa rindu pergi ke Masjidilharam,","Mengerjakan haji bila termampu;","Program selesai dilarut malam,","Golongan miskin dan yatim sungguh terharu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102443,"Aras Air laut menjadi tohor,","Apabila surut dipagi hari;","Hari ini program Saya di Johor,","Golongan miskin dan yatim akan disantuni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102444,"Biar mekar mawar ditaman,","Tumbuh juga pohon keladi;","Dunia ini hanya lah pinjaman,","Akhirat jua yang kekal abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102445,"Kenali jauhari kenali Nilam,","Ke dalam batin meresap dihati;","Bangun lah sujud disepertiga malam,","Saat doa dimakbulkan Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102446,"Burung merbah membuat sarang,","Sarang disusun didahan berduri;","Jangan kita suka menghina orang,","Jatuh lah nanti martabat sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102447,"Awan mendung hari pun kelam,","Tidak kelihatan cahaya mentari;","Hari ini harus lebih baik dari semalam,","Esok pula lebih baik dari hari ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102448,"Pergi ke sungai memasang pukat,","Harap mendapat ikan Belida;","Kerja sempurna dengan muafakat,","Hasil yang baik pasti berganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102449,"Gunung jauh kelihatan berbalam,","Indah pemandangan diwaktu senja;","Teringat sesuatu berbuka semalam,","Orang miskin apakah juadahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102450,"Tiada sesuatu yang tidak mungkin,","Itulah hakikat dalam kehidupan;","Ramadhan ramai membantu golongan miskin,","Sewajarnya jadi amalan berterusan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102451,"Bagai minyak penuh yang ditatang,","Biar terjaga jangan tertumpah;","Setiap pagi yang datang,","Pasti membawa rahmat yang melimpah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102452,"Ramai pengunjung ruang pun padat,","Sehingga sesak untuk pernafasan;","Bulan Ramadhan penuhi beribadat,","Bukan menjadi pesta makanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102453,"Sama cantik samalah padan,","ramai orang Datang mendekati;","Hari ini bermulalah Ramadhan,","amalan baik semoga diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102454,"Laut surut air pun tohor,","duduk dipondok melayan rindu;","Hari ini semalaman saya di Johor,","bersama anak dan juga cucu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102455,"Kehidupan sejahtera dengan keamanan,","Sayangi negara semat dijiwa;","Skuasy bersama Canselor acara tahunan,","Pagi ini berlangsung perlawanannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102456,"Pagi tenang hati pun lapang,","awan berarak angin berhembus;","Bakti dicurah usah dikenang,","biarlah dari hati yang tulus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102457,"Jauh sekali pergi berkelana,","Jangan sampai jauh tersesat;","Jawatan dan pengaruh jangan disalahguna,","Kepada masyarakat mesti bermanfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102458,"Membeli sagu di pasar Putatan,","Dibuat kuih amalan tradisi;","Bersukan untuk jaga Kesihatan,","Dimasa tua tidak lah dikesali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102459,"Basuh diterjemahkan menjadi 'wash',","Berhati-hati pakaian pelbagai 'color';","Semalam petang berlatih skuasy,","Sabtu ini lawan 'team' Tuanku Canselor.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102460,"Menongkat langit Istilah bidalan,","Untuk manusia yang lupa diri;","Banyak dugaan dalam perjalanan,","Biar berteman dengan yang dipercayai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102461,"Menari-nari sirama-rama,","Hinggap riang dibunga mawar;","Kesihatan diri tidak selamanya,","Jika sakit cari lah penawar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102462,"Usah dibuang kain yang buruk,","Bila perlu ada gunanya;","Hari ini adalah memori esok,","Hiasi ia yang terbaik belaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102463,"Menumpu perhatian mendapatkan aura,","Dari alam sumber yang banyak;","Senyum dan ketawa seadanya,","Kesihatan berpanjangan berterusan kelak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102464,"Dingin sungguh dipagi hari,","Setelah bumi disirami hujan;","Jangan ada sebarang iri,","Hanya merosak jiwa dan perasaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102465,"Pagi hari pergi bekerja,","Dengan niat untuk berbakti;","Lakukan kebaikan dengan teruja,","Hasil yang baik lahir dari hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102466,"Ingin menyanjung budi kebaikan,","Walaupun yang diberi amat sedikit;","Tabung Penubuhan UKM pernah diwujudkan,","Kini dihargai penderma walaupun seringgit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102467,"Pagi yang tenang jiwa tak ribut,","Hari terang mentari bersinar;","46 tahun perayaan disambut,","Semangat sayang universiti terus disegar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102468,"Bila gatal sedap menggaru,","Hilang keresahan timbul kelegaan;","Sentiasa dinanti hari yang baharu,","Perkataan yang baik dalam sapaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102469,"Amat comel sianak manja,","Suci wajah bersih menawan;","Suara sahabat yang riang sentiasa,","Berkumandang jelas di TV9.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102470,"Dimana kan ku cari ganti,","Lagu lama yang amat merdu;","Nasib yang baik usahlah dinanti,","Jikalau seharian duduk mendungu;", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102471,"Berlari-lari anak sang kancil,","Kesana sini mencari makan;","Amal kebaikan walaupun kecil,","Terus lakukan jangan ditinggalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102472,"Semangat berbakti terus membara,","Untuk masyarakat yang masih ketinggalan;","Hari ini sangat bermakna,","UKM 46 tahun diraikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102473,"Mari melancong ke desa Permai,","Sawah padi luas terbentang;","Walaupun meriah ditengah ramai,","Guru di pedalaman siapa yang pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102474,"Makan nasi dipagi hari,","Bekalan tenaga untuk bekerja;","Lima kilometer paksa berlari,","Kempen kesihatan kehidupan sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102475,"Anak ayam di atas titi,","untuk menyeberang ke sebelah sana;","perlawanan bola dimenangi JDT,","semangat kesukanan terus dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102476,"Kelip-kelip terbang kemari,","Di tepi sungai tempat habitat;","Usah dikira apa yang diberi,","Itulah bekal sampai akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102477,"Tempat indah bernama Krabi,","bawa keluarga bercuti bersama;","bersahur adalah sunnah Nabi,","walaupun hanya sebiji kurma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102478,"Ruang yang luas duduk pun selesa,","Tempat ramai orang berhimpun;","Jangan ragu taja berbuka puasa,","Pahala sama tak dikurangi sedikit pun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102479,"Burung dara, burung nuri,","Bernyanyi bersama di tepi muara;","Tak kira berapa yg diberi,","Biar keikhlasan hati berbicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102480,"Tangan memberi dengan ikhlas,","Tangan menerima dengan syukur;","Senyum ku beri dia membalas,","Jalan kedepan aku tersungkur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102481,"Tersandung kaki tersilap langkah,","Lutut berdarah sakit nya di hati;","Sucikan hati dengan bersedekah,","Agar hidup sentiasa di berkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102482,"Pokok turi tinggi segalah,","Di hujung taman teguh berdiri;","Ikhlas memberi kerana ALLAH,","Subur iman bertambah rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102483,"Majlis meriah ramai kenalan,","Antara disaji pulut berinti;","Sedekah jariah bukan tontonan,","Biarlah menjadi rahsia di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102484,"Anak bulan nampak sudah,","Bulan indah bersinar cahayanya;","Di bulan mulia kita bersedekah,","Semoga Allah memberi ganjaran-Nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102485,"Memancing ikan di petang hari,","Bersama Tok Ki di Tasik Merah;","Penerima doakan pada pemberi,","Semoga rezeki bertambah murah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102486,"Jalan sendirian ditepi hutan,","Sampai dihulu memetik berangan;","Tiap pemberian penuh keberkatan,","Tak perlu pujian dan sanjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102487,"Cantik rumah indah dihias,","Dari jauh nampak menyerlah;","Tangan memberi penuh ikhlas,","Dipandang mulia disisi Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102488,"Pergi sekolah naik teksi,","Teksi lama pak cik Hamdan;","Rezeki allah dikongsi-kongsi,","Sedekah, derma di bulan Ramadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102489,"Mati seekor burung terkukur,","Kerana tercedera di kaki;","Yang menerima mesti bersyukur,","Yang memberi ikhlas dari hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102490,"Sungguh indah alunan irama,","Dialunkan orang dari Mukah;","Manis sungguh senyuman Anisma,","Senyuman juga satu sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102491,"Anak dara duduk di titi,","Mengenang kekasih yang kini jauh;","Memberi menerima seikhlas hati,","Silaturrahim terikat teguh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102492,"Rasa gembira tidak terperi,","Menerima kain buat basahan;","Iklaskan jiwa bila memberi,","Hormat menghormati sesama insan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102493,"Turun sampan naik feri,","Berpeti - peti membawa beras;","Tangan kanan bila memberi,","Didalam hati mesti lah ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102494,"Kambing berbiri jauh tersisih,","Kaki dihulur sehinga sahur;","Tangan memberi mengucap terima kasih,","Tangan menghulur biarlah luhur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102495,"Galah dihulur kait delima,","Karung digalas bawa ke laman;","Sedekah dihulur syukur terima,","Petanda ikhlas insan budiman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102496,"Dosa pahala di hujung jari,","Berita diterima tanpa disukat;","Amalkan sedekah setiap hari,","Biar sedikit tetapi berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102497,"Masak tumis sambal berlada,","Untuk hidangan masa berbuka;","Sentiasa bersyukur dengan yang ada,","Hati tenang tiada prasangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102498,"Terpecah lampu terkena batu,","Ketika ke kanan laju mendesah;","Orang yang mampu sila bantu,","Ringankan bebanan orang susah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102499,"Halau biri biri ke tepi bukit,","Timun ditanam di tanah liat;","Walau diberi hanya sedikit,","Namun dikenang ke akhir hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102500,"Ikan tenggiri ikan gelama,","Dibuat masam enak tak terkira;","Yang memberi dan menerima,","Biar ikhlas tak banyak bersuara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102501,"Hasta ukuran luasnya tebing,","Titi runtuh patah tiangnya;","Harta bagaikan kala jengking,","Pasti terbunuh kerana bisanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102502,"Cermat serama bila berlari,","Merbah di bukit menghalang pula;","Nikmat bersama kita syukuri,","Usah dungkit hilang pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102503,"Pemimpin berkuasa pasti berbangga,","Rakyat diziarah bertemu berjumpa;","Lumrah berpuasa lapar dahaga,","Zakat fitrah jangan terlupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102504,"Tempua terbang jauh, bertaburan,","Hinggap berteduh penat didahan;","Bukan harta jadi ukuran,","Iman dan taqwa jadi suruhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102505,"Bumi Mekah disinari bulan,","Sang mentari menyinari Kaabah;","Hidup bersedekah jadikan amalan,","Menginsafi diri pahala ditambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102506,"Pagi hari pergi ke sawah,","Luas terbentang ciptaan tuhan;","Sedekah itu amal jariah,","Memberinya ikhlas tanpa paksaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102507,"Pak uda mengait buah kelapa,","Hendak disangai membuat kerisik;","Sedikit menghulur besar pahala,","Ganjaran akhirat tiada berlapik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102508,"Tepi kelas tanam halia,","Buah ceri dalam almari;","Sifat ikhlas amalan mulia,","Kanan memberi sorokkan yang kiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102509,"Cantik berseri songket lama,","Tenunan ditekat benang sekerat;","Budaya memberi adat menerima,","Kunci masyarakat bertambah erat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102510,"Di dahan sekah hinggap merpati,","Terlihat lelah jauh terbangnya;","Bila sedekah ikhlas di hati,","Ganjaran Allah besar nilainya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102511,"Duduk berangan seorang diri,","Duduk sendiri dalam perahu;","Kalau tangan kanan memberi,","Tangan kiri janganlah tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102512,"Selepas guna kuali digantung,","Baru lepas masak penganan;","Perbuatan baik tetap dihitung,","Hanya membuang duri dijalanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102513,"Nangka seulas dibuat pengat,","Harum bau nya bila dihidang;","Walau setitik ikhlas keringat,","Berbakti jua tetap dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102514,"Manis berseri nangka seulas,","Dimakan sama si burung terkukur;","Puasa semua jangan malas,","Setahun sekali kita bersyukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102515,"Mendengar irama lagu cinta,","Rasa merana ditanggung badan;","Memberi menerima budaya kita,","Lebih bermakna di bulan Ramadhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102516,"Cantik kenari sudahlah tentu,","Menambah seri taman lindungan;","Ikhlas memberi juga membantu,","Berdiam diri sembunyi tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102517,"Daun palas daun cekur,","Bawa ke daik jalan sepadan;","Beri ikhlas terima bersyukur,","Amalan terbaik bulan Ramadhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102518,"Pergi kebaruh naik perahu,","Kembang harum bunga melati;","Kanan memberi kiri tak tahu,","Itu sifat mukmin sejati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102519,"Rendang ayam ketupat palas,","Enak dimakan dara pingitan;","Kalau memberi haruslah ikhlas,","Takut nanti jadi umpatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102520,"Sungguh cantik sirama rama,","Cantik lagi emas suasa;","Ikhlas memberi ikhlas menerima,","Rezeki yang ada sama dirasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102521,"Bersinar mentari terasa malas,","Penat ditahan duduk di batu;","Kalau memberi biarlah ikhlas,","Kepada Tuhan memohon restu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102522,"Di tepi jalan pisang setandan,","Disisir lima simpan di peti;","Ganda amalan bulan Ramadan,","Bersedekah derma seikhlas hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102523,"Menjahit atap daun rumbia,","Terpancar cahaya di celah dinding:","Jangan mengharap reda manusia,","Mencari keredaaNYA itu yang penting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102524,"Batuan karang laluan lebah,","Terbang melata iring rerama;","Tiada kurang malah bertambah,","Rezeki kita kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102525,"Sejadah dibentang siap beralas,","Terus mengaji jangan malas;","Sudah memberi dengan ikhlas,","Biar Allah saja yang membalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102526,"Gulai pajri si kuih bangkit,","Ayam serama si burung tekukur;","Yang memberi jangan mengungkit,","Yang menerima ucapkan syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102527,"Kail ikan guna joran,","Mata pancing tersangkut sampah;","Bertukar juadah sesama jiran,","Waktu berbuka rezeki melimpah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102528,"Hiasan kelas bunga dahlia,","Ukiran batu dilukis cermat;","Sedekah ikhlas amalan mulia,","Saling membantu sesama umat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102529,"Hinggap nuri dipohon pulasan,","Cantik terserlah kicau berbalas;","Usah memberi harap balasan,","Cukup Allah menilai ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102530,"Cantik berseri dadanan dihias,","Indah bercahaya bak bidadari;","Andai memberi biarlah ikhlas,","Syukur hadir nun disanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102531,"Tangan kanan yang memberi,","Tangan kiri disorokkan;","Ikhlas sembunyi di dalam diri,","Reda illahi nan dihajatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102532,"Niat memberi biarlah rahsia,","Budi bahasa ikhlas dalam hati;","Bertuah hidup di malaysia,","Negara aman rakyat murah hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102533,"Dimakan olih siburung tekukur,","Tekukur dijerat olih Pak Dolah;","Banyakkan sedekah jgn takbur,","Semoga amalan diberkati Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102534,"Pulau Pandan jauh ke tengah,","Gunung Tahan megah di persada;","Hadirnya Ramadhan penuh barakah,","Setiap amalan pahala berganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102535,"Terbang rerama di dedaun lebat,","Di celahan duri tersangkut melekat;","Syukur menerima rezeki didapat,","Rezeki pemberi bertambah berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102536,"Buah kurma dari Mekah,","Hendak disimpan dalam peti;","Bulan Ramadhan banyakkan bersedekah,","Amalan mulia lagi diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102537,"Atur sambungan merentas jeti,","Sebelum malam papan diukur;","Hulur sumbangan seikhlas hati,","Senyum bersulam ucapan syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102538,"Dimakan sama burung tekukur,","Berkongsi rakan burung kenari;","Tangan terima mengucap syukur,","Allah gandakan rezeki pemberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102539,"Bulan ramadhan bulan mulia,","Bulan berkat beribu pahala;","Bersedekah amalan mulia,","Moga beroleh berlipat pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102540,"Kias berkias bersama-sama,","Gelak ketawa mengilai-ngilai;","Ikhlas memberi ikhlas juga diterima,","Selebihnya Allah juga yang menilai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102541,"Tertusuk duri kala meniti,","Bawa serama tuju pesara;","Kalau memberi setulus hati,","Yang menerima pasti gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102542,"Manis bersari nangka seulas,","Buang empulur dimakan lembu;","Bila memberi biarlah ikhlas,","Kanan menghulur kiri tak tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102543,"Harum melati dihinggap kenari,","Pohonnya tinggi di hujung desa;","Ikhlaskan hati tatkala memberi,","Cukup bersaksi Yang Maha Esa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102544,"Cantik berjalur warna kelabu,","Kebaya nyonya indah terserlah;","Sedekah dihulur sekadar termampu,","Walau hanya sebesar zarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102545,"Nangka seulas dibuat pengat,","Kesedapan terasa lahir Alhamdulillah;","Sedekah ikhlas selalu diingat,","Semoga sentiasa diberkati Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102546,"Lemah dan lembut tekukur menari,","Takut tersungkur geraknya perlahan;","Penghargaan disebut pada pemberi,","Menzahirkan syukur kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102547,"Seri Rama mitos handalan,","Dipuja namanya Raja ternama;","Memberi menerima aturan kehidupan,","Khazanah dunia kita kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102548,"Manis bersari nangka seulas,","Dimakan sama burung tekukur;","Wajah berseri tanda ikhlas,","Langkah diayun tidaklah tersungkur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102549,"Manis masam sibuah markisa,","Colek dengan kicap pekat;","Orang memberi kita merasa,","Jiran sepakat membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102550,"Longan dan ceri bawa ke Daik,","Gaul delima kelapa kukur,","Tangan memberi tentulah baik,","Kiri terima ucaplah syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102551,"Pohon berangan si pokok leban,","Daunnya layu jarang berbuah;","Hulurkan tangan ringankan beban,","Saling membantu pererat ukhuwah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102552,"Tepung pelita tampak merekah,","Enak terperi menambat hati;","Ikhlas mulia amalan sedekah,","Dilimpah rezeki moga dirahmati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102553,"Manis bersari nangka seulas,","Dimakan sama si burung tekukur;","Tangan memberi dengan ikhlas,","Tangan menerima dengan syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102554,"Buah limau di dalam dulang,","Dulang berisi cendawan butang;","Harimau mati tinggalkan belang,","Orang mati selalu tinggalkan hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102555,"Minyak sawit dalam peti,","Tampi padi di tepi bukit;","Banyak duit tak bawa mati,","Tapi rasa mati jika tak berduit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102556,"Tingkap kayu papan bersegi,","Sampan sakat di Pulau Angsa;","Indah tampan kerana budi,","Tinggi bangsa kerana bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102557,"Teritip tiram tergantung,","Limau manis banyak bijinya;","Pilih-pilih muda sekampung,","Hitam manis baik budinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102558,"Merah itu saga yang lorek itu kendi,","Kata bermadah indah bahasanya;","Mulianya hati sanjungan budi,","Tetap dikenang selama-lamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102559,"Pagi pagi meraut lidi,","Duduk bersimpuh di atas upih;","Ramai orang menanam budi,","Wang dan harta juga yg di pilih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102560,"Bekas pecah merendam padi,","Daun diramas merendang kerang;","Puas sudah menanam budi,","Namun emas juga dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102561,"Bagaimana nak tanam selasih,","Tanamlah mari di rumpun keladi;","Bagaimana hati nak kasih,","Kasih bercampur denganlah budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102562,"Tentulah ringgit dipandang orang,","Duit sedikit lagikan dikenang;","Hutang budi hilang melayang,","Bahasa indah orang tak pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102563,"Buku tersusun di atas meja,","Nampak kemas ringan tangan;","Kekayaan hanya sementara saja,","Budi bahasa jadi sanjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102564,"Biar sutera cantik dipandang,","Kasa jua tetap dicari;","Budi bahasa tetap dipandang,","Ringgit juga tetap dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102565,"Burung serindit terbang melayang,","Hinggap sebentar di ranting mati;","Bukan ringgit di pandang orang,","Amalan dan pahala di bawa mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102566,"Bunga Mentari kembang cantik,","Jadi hiasan di halaman rumah;","Berakhlak tinggi berbudi baik,","Lebih murni jika peramah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102567,"Serindit hinggap di pohon mati,","Setelah penat jauhnya terbang;","Baik budi tinggi pekerti,","Sepanjang masa di kenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102568,"Anak dara pulang ke desa,","Bawa sebakul bunga hiasan;","Indah budi indahnya bahasa,","Bersopan santun budaya warisan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102569,"Sekawan serindit dari seberang,","Hinggap dibendang memakan padi;","Bukan ringgit dipandang orang,","Tapi memandang bahasa dan budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102570,"Terbang serindit tuju seberang,","Singgah di padang di rumpun keladi;","Bukannya ringgit dipandang orang,","Tapi memandang bahasa dan budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102571,"Burung serindit terbang melayang,","Terhenti hinggap diranting mati;","Pamirkan senyum manis dipandang,","Kerana ini menyejukkan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102572,"Burung serindit terbang berkawan,","Hinggap sebentar didahan mati;","Akhlak baik ramai kawan,","Begitu mudah menabur budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102573,"Burung serindit terbang melayang,","Terhenti hinggap di ranting mati;","Punya wang ringgit ramai yang sayang,","Miskin kayap tiada yang perhati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102574,"Jangan dijolok sarang tempua,","Jatuh nanti bertabur sekeliling;","Walaupun tidak kepada semua,","Terkadang ringgit jua dijeling.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102575,"Menghalau beruk bermain di pantai,","Ombak kuat beralun di lautan;","Kalau buruk kain dipakai,","Deme buat layan tak layan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102576,"Mendaki bukit membawa barang,","Barang dibawa dari Beranang;","Bukan ringgit dipandang orang,","Hati yang mulia lagi dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102577,"Burung serindit diranting mati,","Terbang melayang niat tak henti;","Mengenang duit tujuan di hati,","Risau menginjak maruah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102578,"Merah bebenor epal masak,","Ayor kuning di dalam gelas;","Malas belajor kenape pulak,","Bior rajin jangan pemalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102579,"Orang kota datang bertamu,","Ke rumah desa Kampung Sialang;","Belajarlah kita menuntut ilmu,","Demi masa hadapan gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102580,"Mengikat janji sewaktu bertemu,","Pertalian erat kukuh selamanya;","Manakan rugi menuntut ilmu,","Pengetahuan luas serlah cerdiknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102581,"Sorong papan tarik papan,","Buah petai dalam perahu;","Suruh makan laju jer kau makan,","Dah makan belajar, yg tak tahu jadi tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102582,"Ikan sepat dari serkat,","Makan seorang sambil berangan;","Ilmu di dapat dikongsi berkat,","Di sayang org pahala berpanjangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102583,"Mangga ditut dipangkal dahan,","Berbuah lagi tidakkan jemu;","Ilmu dituntut satu kefardhuan,","Jahilkan pergi hikmah bertemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102584,"Tuturnya santun buatku terleka,","Terasa berat menganggu fikiran;","Belajar berpantun suka-suka,","Ikutlah syarat patuhi peraturan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102585,"Bersampan perahu kerumah nyonya,","Sayang cindai tepi perigi;","Kalau tak tahu kita bertanya,","Sudah pandai berlajarlah lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102586,"Beli cuka jauh ke Banjar,","Terus ke Genting tempat saudara;","Terima suka ditunjuk ajar,","Jangan melenting meninggi suara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102587,"Budak berkejar ke arah perahu,","Berebut anggur hijau merah;","Baru belajar dah berlagak tahu,","Bila ditegur melenting marah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102588,"Pokok temu tumbuh sejajar,","Ditanah liat desa sempadan;","Jangan jemu untuk belajar,","Selagi hayat dikandung badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102589,"Pukul bedok di subuh hari,","Mentari bersinar tinggi se jengkal;","Mencari ilmu setiap hari,","Agar menjadi insan berakal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102590,"Sepasang pekaka terbang berkejar,","Ke arah barat macam berkelahi;","Suka-suka mahu belajar,","Peraturan syarat mesti dipatuhi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102591,"Tanam sejajar setinggi bahu,","Mawar cantik berbau wangi;","Tidak belajar mana tahu,","Insan cerdik orang sayangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102592,"Pergi kehutan menjerat rusa,","Jerat memakan diri sendiri;","Hormati guru setiap masa,","Supaya berkat ilmu dipelajari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102593,"Pokok selasih ditepi pagar,","Sayang tiada siapa yang punya;","Semua perkara harus belajar,","Supaya tahu buruk baiknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102594,"Menjemur jamu didalam bijana,","Letak dianjung jatuh tergolek;","Tuntutlah ilmu walau dimana,","Dunia dijunjung akhirat dikelek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102595,"Seri Langkat nama diberi,","Tempat bertemu sang pemburu;","Asahkan bakat tonjolkan diri,","Hendak berilmu carilah guru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102596,"Lilau perahu celah seburu,","Tebing sejajar curam pinggiran;","Kalau tak tahu carilah guru,","Rajin belajar tajam fikiran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102597,"Ayam katik terkejar-kejar,","Lari sejajar tuju perahu;","Mari adik mari belajar,","Tidak belajar mana tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102598,"Burung pekaka tenggek di pagar,","Anak pepatung terbang ke tasik;","Suka suka kita belajar,","Belajar pantun di sajian klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102599,"Rasa berdebar mengolah sukun,","Dini meluru menyimpan kendi;","Kala belajar mestilah tekun,","Santuni guru sopan berbudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102600,"Pulau sejajar telah ditemu,","Langgas senada sepakat dikir;","Kalau belajar usahlah jemu,","Mencergas minda ligat berfikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102601,"Padi semaian diguni lusuh,","Dijemur takut dimakan bena;","Dari buaian sudah diasuh,","Tuntutlah ilmu walau kenegara China.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102602,"Penat mengejar sakit lutut,","Berebut cempedak manis isinya;","Pergi belajar memang dituntut,","Pandai tidak bukan soalnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102603,"Anak ikan suka berlumba,","Selalu dibuat waktu pagi;","Dalam belajar kita mencuba,","Andai gagal usahalah lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102604,"Kudup kuini sebelah titi,","Mekar sekata alih ke desa;","Suka dan duka silih berganti,","Mengajar kita lebih dewasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102605,"Bermain sojar tiasa lesi,","Tersimbah tauhu susu dirasa;","Rajin belajar masa terisi,","Menambah tahu isu semasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102606,"Gadis banjar dayung perahu,","Berbaju batik mahu ke mana;","Sudah belajar semua tahu,","Hendak kritik biar membina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102607,"Buah pinang bawa belayar,","Buat dagang di pulau seberang;","Susah senang kenalah belajar,","Dah besar tak susahkan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102608,"Ambil tebu ditepi rumah,","Cabut ubi bersama akar;","Sedikit ilmu jika berhikmah,","Garam selaut jadi tawar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102609,"Tumbuh sejajar pohon keranji,","Di sebelahnya rapat pohon pulasan;","Suka belajar amalan terpuji,","Janganlah cepat merasa bosan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102610,"Ingin belajar bahasa banjar,","Tata cara mesti sopan;","Wajib ada minat belajar,","Tidak rugi dimasa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102611,"Buah cempedak di luor pagor,","Ambil guni tolong balutkan;","Rajin tekun mahu belajor,","Malas belajor kita ketinggalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102612,"Makan laksa diluar pagar,","Makan mee dalam bilik;","Kalau kita suka belajar,","Benda susah tak pelik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102613,"Nyior bantut di hujung sana,","Tidaklah gayat dipetik tandan;","Ilmu dituntut walau di mana,","Selagi hayat dikandung badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102614,"Membeli minyak tak perlu berkejar,","Dijual merata di serata kedai;","Semakin banyak kita belajar,","Tahulah kita yang kita tak pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102615,"Pilihan Jamu produk Banjar,","Ramuan kaya mutu dan rasa;","Beroleh ilmu perlu belajar,","Baru berjaya nusa dan bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102616,"Waktu pagi mengambil temu,","Cuaca redup hala ke barat;","Tidak rugi menuntut ilmu,","Untuk bekalan dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102617,"Cuka-cuka beli dibandar,","Makanan sihat lagi berkat;","Ilmu didapat harus disibar,","Hidup berkat dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102618,"Nangka-nangka usah runtun,","Bimbang keluang datang kejar;","Suka-suka karang pantun,","Ada ruang untuk siar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102619,"Tumbuh sejajar pohon gaharu,","Hinggap merpati di dahan langsat;","Kita belajar perlu berguru,","Tidak nanti akan tersesat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102620,"Jangan jemu Untuk bertanya,","Ilmu dipunya manfaatkan berguna;","Menuntut ilmu wajib hukumnya,","Sekalipun jauhnya ke negeri cina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102621,"Budak lasak nyalakan api,","Bersama rakan halau serangga;","Belajar masak tengok resepi,","Enak dimakan seisi keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102622,"Tanam kemboja rapat rapat,","Agar tempat menjadi redup;","Banyak belajar ilmu pun dapat,","Ilmu jadi bekalan hidup.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102623,"Berkicau burung didahan rendang,","Cerah cuaca disinari mentari;","Ilmu dicari maruah disandang,","Bekalan hidup kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102624,"Teliti-teliti membeli durian,","Takut terkena buah yang muda;","Hati-hati terima pujian,","Riak tak ke mana biarlah berpada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102625,"Berlari lari si anak kaldai,","Jatuh seekor dalam perigi;","Jangan buat berlagak pandai,","Cari tok guru belajarlah lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102626,"Tidak bertemu sedih terharu,","Terganggu emosi dimasa sukar;","Tuntutlah ilmu bersama guru,","Tiada aplikasi dapat ditukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102627,"Sopan bersantun gadis Melaka,","Manis orangnya pandai bermadah;","Belajar berpantun suka-suka,","Pastikan bahasanya sentiasa indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102628,"Rai tetamu makan bersama,","Riang di sisi lupa segala;","Menuntut limu tuntutan agama,","Boleh dikongsi berladang pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102629,"Cuka-cuka beli di bandar,","Simpan makan dalam perahu;","Suka-suka mahu belajar,","Tidak belajar mana tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102630,"Memikat burung membawa jebak,","Hibur hati jiwa yang lara;","Bersama kita perangi wabak,","Covid berkubur negara sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102631,"Lebarnya kain trepan,","Bila ditarik jangan sampai rabak;","Mari bantu barisan hadapan,","Berusaha kita hapuskan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102632,"Pergi menjala naik sampan,","Muatan penuh perahu perlahan;","Hormati pertugas di barisan hadapan,","Bekerjasama ikut segala arahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102633,"Kekal kita patuhi arahan dan amaran,","Supaya bahaya tidak merebak;","Hargailah mereka yang sentiasa berkorban,","Bersama kita hapuskan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102634,"Suaranya lemah meluah sesuatu,","Kenangan syawal mercun sepapan;","Berkurung di rumah kita membantu,","Wabak dikawal di barisan hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102635,"Bawa mencebak tanah ditambak,","Susun bata beralaskan papan;","Semoga wabak berhenti merebak,","Doakan sihat barisan hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102636,"Sulaman serban berbelang jingga,","Tekap pertama sebelah kanan;","Bersama korban jiwa dan raga,","Terpisah lama bukan bebanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102637,"Masak pulasan bawa kepekan,","Duku berbuah dibatu pahat;","Akur arahan Demi kebaikan,","Duduk dirumah kekal sihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102638,"Ke arah tambak menjeling menolih,","Anak tenok berlari ke paya;","Selagi wabak belum pulih,","Tidak seronok berpuasa beraya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102639,"Pauh dilayang pohonnya tumbang,","Pokok berangan berhampiran reban;","Keluarga disayang wabak dikekang,","Hulurkan tangan ringankan beban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102640,"Kisah lama ada sejarah,","Duduk beramai di dalam khemah;","Jangan merayap tak tentu arah,","Elak penyakit dari terkena orang rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102641,"Kain rabak baju jarang,","Sangkut di dahan junjung petola;","Semoga wabak covid beransur kurang,","Dengan keizinan dari Allah Taala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102642,"Sepiring nangka menu sarapan,","Nyonya menyua semangkuk aiskrim;","Simpati mereka di baris hadapan,","Hanya doa mampu dikirim.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102643,"Kemerah-merahan pokok di pamah,","Andai dipacak di depan sabak;","Patuh arahan duduk di rumah,","Landai kemuncak jangkitan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102644,"Perling di huma satu di batu,","Jatuh menepi separas tambak;","Saling bersama bantu membantu,","Patuh PKP benteras wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102645,"Sungguh sayu rasanya hati,","Dikenang-kenang menjadi sebak;","Bersama kita bersatu hati,","Bantu membantu hapuskan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102646,"Di hujung dahan hinggap si burung,","Sambar temeras di dalam lopak;","Ikut arahan perintah berkurung,","Bantu benteras sebaran wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102647,"Cantik bergaya jejaka dara,","Di balik pintu berdiri gagah;","Wabak bahaya menyerang negara,","Kita bersatu menghalang mencegah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102648,"Panglima daik memegang tombak,","Kepada raja hendak disembah;","Mengatur langkah mencegah wabak,","Barisan hadapan yang berhati tabah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102649,"Mekar sejambak bunga semalu,","Ungu seluruh nampaknya megah;","Jangkitan wabak pasti berlalu,","Amal yang disuruh jauhi yang ditegah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102650,"Indahnya purnama bulan yang satu,","Menyinari ombak berkilau bercahaya;","Kita bersama bantu-membantu,","Memerangi wabak yang sungguh bahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102651,"Mawar indah mawar berduri,","Petik setangkai harum semerbak;","Jangan kita pentingkan diri,","Bantu lah mereka membasmi wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102652,"Bunga mawar wangi semerbak,","Cantik sekali penyeri pagi;","Andai wabak terus merebak,","Kehidupan indah tiada lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102653,"Bunga harum kembang sekuntum,","Sayang kembang tidak lama;","Perangi wabak bersatu kaum,","Paling utama perlu bekerjasama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102654,"Pergi berlapan memburu keluang,","Singgah di jalan menahan jebak;","Barisan hadapan cekal berjuang,","Demi putuskan rantaian wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102655,"Pegang tombak acah kera,","Jalan kedepan bawa paha;","Jangkitan wabak pintas segera,","Barusan hadapan kuatkan usaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102656,"Sarat sampan membawa mengkuang,","Teratak Normah jadi singgahan;","Barisan hadapan teguh berjuang,","Kita di rumah patuhi arahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102657,"Setangan rakan diberi suri,","Sambil sapukan cangkir berubat;","Jangan pentingkan diri sendiri,","Ambil tindakan fikir akibat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102658,"Berhari kini jadi siulan,","Namun mereka terus mara;","Barisan hadapan penuh kecekalan,","Menggadai diri demi Negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102659,"Sepak raga melambung ke perigi,","Riuh rendah tawa gelak;","Jasa mereka disanjung tinggi,","Moga Allah balas disyurga kelak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102660,"Sawah merata di tanah jawa,","Tepian perigi rimbun celagi;","Seia sekata sehati sejiwa,","Jayakan strategi wabak kan pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102661,"Bisa merebak dipatuk ular,","Tedong tersorok di balik papan;","Tangani wabak terus menular,","Kurang beban barisan hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102662,"Pergi kesawah digigit lintah,","Jari pula tertusuk duri;","Marilah kita ikot perintah,","Arahan kerajaan jangan ingkari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102663,"Secermat antah menyatu corak,","Pulut di makan tanpa dipinta;","Hormat perintah kawal bergerak,","Demi kebaikan rakyat jelata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102664,"Bantu membantu jangan lah malas,","Baru digelar rakyat berhemah;","Barisan hadapan menjalankan tugas,","Tugas kita bantu duduk dirumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102665,"Habis rebah serumpun lada,","Ditimpa dahan tepian rumah;","Jangan menambah jangkitan yang ada,","Ikut arahan duduk di rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102666,"Di balik batu siburung tukang,","Hinggap di papan merenung tambak;","Kita membantu dari belakang,","Barisan hadapan memerangi wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102667,"Sidai baju jngan selerak,","Biar teratur dan rapi;","Jom leraikan segala wabak,","Setiap arahan bersama hadapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102668,"Senduduk menjalar di atas ban,","Peria subur di tanah pamah;","Barisan hadapan sanggup berkorban,","Berkorban kita duduk di rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102669,"Memang cantik bunga tajaan,","Tersusun rapi ikut sajambak;","Duduk rumak bantu kerajaan,","Semoga terputus rantaian wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102670,"Semboyan angkatan berbaris tegak,","Tiupan berkumandang semangat berdetak;","Barisan hadapan menangkis wabak,","Barisan belakang doa ditebak.", 4, 6);
