INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102755,"Sunyi senyap kampung dan kota,","Duduk di rumah rasa selesa;","Berkat ramadan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102756,"Walaupun covid tak nampak mata,","Bersihkan diri patuh arahan sentiasa;","Berkat ramadan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102757,"Cantas tandan biar rata,","Susun padat bawa kedesa;","Berkat ramadhan didepan mata,","Banyakkan ibadat jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102758,"Banting tulang kumpul harta,","Harta dikongsi berkat sentiasa;","Berkat ramadhan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102759,"Kalau Tuan pergi ke kota,","Belikan saya seekor angsa;","Berkat Ramadhan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102760,"Daun pandan tumbuh melata,","Tumbuh rendah subur sentiasa;","Berkat Ramadan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102761,"Cermat cermat bila berkata,","Tersilap kata diri binasa;","Berkat ramadan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102762,"Pisang bertandan bawa ke kota,","Sudah memunggah berjamu laksa;","Berkat ramadan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102763,"Daun pandan tumbuh merata,","Dipetik sudah bawa ke desa;","Berkat ramadan di depan mata,","Banyakkan ibadah jauhkan dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102764,"Kerbau membajak sawah di hulu,","Budak gagaukan bermacam ikan;","Orang yang bijak menyesal dahulu,","Dapat elakkan kerugian keburukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102765,"Resah di bangsal kawan menanti,","Hujan basahi dada bajunya;","Usah menyesal berawan hati,","Takdir Ilahi ada hikmahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102766,"Jalan krikil mesti ditajak,","Tajak dibangsal simpan sehari;","Masak berfikir tindakan bijak,","Tidak kesal kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102767,"Benang berasal dari kapas,","Diborong biduan di majlis nikah;","Usah menyesal kejadian lepas,","Jadikan panduan terus melangkah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102768,"Jerat tempua ke dalam sangkar,","Sayu mata pandang ke baran;","Hindar semua perkara mungkar,","Berhijrah kita ke arah kebenaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102769,"Pergi ke hutan memburu landak,","Sejenis binatang badan berduri;","Berfikir dahulu sebelum bertindak,","Jangan menyesal di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102770,"Gigi sebaris putih melepak,","Menjauhi hanyir ikan patin;","Dikaji habis sebelum bertindak,","Tindakan zahir serta yang batin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102771,"Sudah resmi orang yang bijak,","Orang lama dikenang disanjung;","Di mana bumi kita berpijak,","Di situ langit patut kita junjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102772,"Pergi kekota naik perahu,","Sayang pedati ditengah kota;","Usah berkata kalau tak tahu,","Takut nanti membawa dusta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102773,"Badan letih terasa lesu,","Baik berehat semoga segar;","Jangan diikut tuntutan nafsu,","Setiap perbuatan dahuluilah sabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102774,"Indah jambangan di tepi pagar,","Diintai di tingkap terlindung bangsal;","Dibuat larangan berani dilanggar,","Dah kena tangkap baru menyesal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102775,"Bertemankan rakan mencari makan,","Burung bersiulan merentas desa;","Fikirkan kebaikan renungkan keburukan,","Elakkan penyesalan yang mungkin dirasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102776,"Cahaya sekata teriknya sinar,","Di hujung bangsal terbias indah;","Menitip berita biarlah benar,","Bimbang sesal tiada sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102777,"Terang menebaih semak di bendang,","Bendang padi tempat bermain;","Barang yang lepaih jangan dikenang,","Sudah menjadi hak orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102778,"Sinar cahaya ternampak indah,","Hati melara tidak keruan;","Rasa percaya hilang sudah,","Bila bicara sekadar gurauan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102779,"Jalan berbatu ke Inderagiri,","Hendak mendaki gunungnya daik;","Hujan batu di negeri sendiri,","Patut disyukuri dengan terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102780,"Bunga mawar indah berkembang,","Musim hujan berbunga lebat;","Andai tindakan mengarah sumbang,","Jangan berlengah untuk bertaubat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102781,"Burung merbah terbang rendah,","Hinggap pula diranting mati;","Fikiran janganlah berubah rubah,","Teguhkan pendirian dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102782,"Kopi secakir sekeping roti,","buat sarapan untuk adinda;","akal memikir pelita hati,","suluh kebaikan jalan direda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102783,"Tangkap beruk di tepi sawah,","Makan durian sampai terlena;","Sudah terantuk baru tengadah,","Sesal kemudian tidak berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102784,"Bermandi manda di hulu,","Airnye keroh dikocak bebudak;","Buat sesuatu fikir dahulu,","Jika betul barulah bertindak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102785,"Kala merenda nanar menyerang,","Berpeluh sakan rata menggetus;","Gemala minda menyinar terang,","Menyuluh akan kata diputus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102786,"Jarangkan lada buangkan kekwa,","Takir setentang mengguna tajak;","Lapangkan dada tenangkan jiwa,","Berfikir matang sempurna bijak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102787,"Lekir di hulu nanti dicari,","Keliru busu redah jalannya;","Berfikir dulu amati diri,","Terburu nafsu padah akhirnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102788,"Mempelam muda tiada luak,","Semasa mudik bekalan roti;","Harus waspada hindari riak,","Tutur yang baik terkesan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102789,"Datang peria berbaju lembab,","Berdayung kolek ke Inderagiri;","Perkerti mulia hidup beradab,","Tambah molek merendah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102790,"Hening subuh di pagi jumaat,","Kumpul bersama kluarga sendiri;","Hujan rahmat membawa berkat,","Ramadhan barakah kita syukuri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102791,"Kain di beli dari jawa,","Ambil sehelai terus dilipat;","Senyum diukir iklaskan jiwa,","Menghormati insan pahala pun dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102792,"Dari Segamat pergi ke Johor,","Memang sengaja terud ke Tumpat;","Solat Jumaat diganti zohor,","Sama saja pahala didapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102793,"Pokok semalu di pokok mengkudu,","Tumbuhnya satu di batu merkah;","Senyum selalu semanis madu,","Senyuman itu satu sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102794,"Hutan di hulu nampak si gajah,","Sayang di hilir terlihat kelinci;","Senyum selalu pengindah wajah,","Keikhlasan lahir dari hati suci.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102795,"Dari Lipis ke batu kikir,","Singgah sebentar ke pekan Pontian;","Manis manis senyum terukir,","Kenangan jumaat Ramadhan 2020 jadi ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102796,"Bawa di raga si buah sawa,","Kutip merata kebun di gunung;","Senyum ketawa pengubat jiwa,","Cerialah kita jangan bermenung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102797,"Jumaat pagi penuh indah,","Ramadhan ke 8 kita jalani;","Senyuman di beri satu sedekah,","Buat semua teman di pagi ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102798,"Pakma sekuntum dipijak gajah,","lunyai merekah terlihat jelas;","semanis senyum seindah wajah,","senyumlah sedekah paling ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102799,"Indah di taman di pagi hari,","Mekar melati berbagai jenis;","Madah senyuman ikhlas diberi,","Menguntum di hati terasa manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102800,"Aneka jenis ulam-ulaman,","Ketupat manis sebagai penganan;","Tambahkan manis dalam senyuman,","Kurangkan manis dalam makanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102801,"Dari lipis ke batu kikir,","Rumah cik Anum di terusan;","Bulan puasa banyak kan zikir,","Berbuat baik jangan lah bosan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102802,"Jalani Ramadhan turuti syariat,","Tiap amalan Allahkan lihat;","Berkat sedekah hari Jumaat,","Melimpah rezeki badan pun sihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102803,"Terbang rendah burung merpati,","Lalu hinggap di atas beranda;","Senyum diberi seikhlas hati,","Petua sihat awet muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102804,"Daun pandan daun palas,","Kacang merah di buat inti;","Senyum sedekah tak minta di balas,","Amalkan lah seikhlas hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102805,"Bulan terang menyinari taman,","Ternampak ungka meloncat melompat;","Ceriakan orang dengan senyuman,","Ramai yang suka pahala pun dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102806,"Lurus lurus pokok kelapa,","Gugur buah nya dilaman nyonya;","Hitam sikit tidak mengapa,","Asal saja manis senyumannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102807,"Delima di laman sudah merekah,","Dibawa ke kedai dibeli kawan;","Kerana seyuman dara sedekah,","Hatiku tergadai jasadku tertawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102808,"Penat sangat menanam halia,","Terasa segar mengutip petola;","Hari jumaat hari mulia,","Bersama kita menambah pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102809,"Limau merekah dikupas dimakan,","Bersama ciku sedap rasanya;","Sedekah senyuman dia berikan,","Hati jiwaku tergadai akhirnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102810,"Daun pandan hujung merekah,","Simpul cermat usah di sayat;","Jumaat Ramadhan afdal sedekah,","Syukuri nikmat sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102811,"Kenjah beria lada di para,","Berdentum takir tega dilangkah;","Wajah ceria tanda gembira,","Senyum terukir juga sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102812,"Hari ini hari jumaat,","Jangan kita kerumah hamdan;","Buat pahala jadi manfaat,","Supaya berkat dibulan Ramadhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102813,"Sekumpul syelum beri manfaat,","tengalan sudah jenuh melukah;","tersimpul senyum hari Jumaat,","amalan mudah penuh barakah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102814,"Menuai rindu di hari pekerja,","Sayugia kelapan kita berpuasa;","Barisan hadapan tak duduk sahaja,","Kerah keringat membantu manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102815,"Kepasar pagi membeli halia,","Beli bersama perencah laksa;","Sedekah dihulur dibulan mulia,","Pahala dikumpul berakah sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102816,"Usah mengintai di balik tabir,","Hati berbunga di ulit kasih;","Senyuman manis terukir dibibir,","Tanda ikhlas hati nan bersih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102817,"Sulaman baldu indah terserlah,","Tersergam indah di mahligai seri;","Senyuman itu satu ibadah,","Pahala di tambah wajah berseri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102818,"Ikan berenang menuju kuala,","Laju berlalu bermacam jenis;","Sungguh senang mendapat pahala,","Ukirkan selalu senyuman yang manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102819,"Ke Tasik Belum pergi menjala,","Hujan turun seperti ramalan;","Layang senyum diganjar pahala,","Sedekah mudah jadikan amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102820,"Kucing menggigit lidi penyapu,","Terjatuh hidangan bertaburan sudu;","Bersedekah ringgit mungkin tak mampu,","Gantikan dengan senyuman bermadu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102821,"Bulan ramadan pahala diraih,","Doa diangkat penghapus dosa;","Rakyat muafakat negara dikasih,","Senyuman terukir setia berjasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102822,"Pergi ke taman membeli gambir,","Ketawa berdekah sambil meludah;","Ukir senyuman manis di bibir,","Amal sedekah yang amat mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102823,"Jeling kenari di lorong itu,","kuih segala ditelan undan;","saling memberi bertolong bantu,","raih pahala bulan Ramadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102824,"Dihidang manggis waktu berbuka,","Bersama dadih dan agar-agar;","Senyuman manis penawar duka,","Jiwa yang sedih kembali segar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102825,"Lebat berbunga si buah pala,","Berbunga juga rambutan pulasan;","Berbuat baik mendapat pahala,","Pasti lakukan dengan keikhlasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102826,"Kucing menggigit ketika dilangkah,","Di atas tangga di rumah lama;","Janganlah ringgit sahaja disedekah,","Doa dan tenaga hulurkan sama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102827,"Telur merekah jelas diganti,","di pekan sari hari Selasa;","hulur sedekah seikhlas hati,","nikmat memberi indah terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102828,"Tiupan angin lautan tioman,","Seguni pisang bawa belayar;","Senyuman manis terselah iman,","Awet muda tak perlu bayar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102829,"Sedang kita ramadhan kelapan,","Menyambut jua hari pekerja;","Megah dijulang barisan hadapan,","Yang bercuti dihargai juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102830,"Hari minggu pergi ke pekan,","Beli pakaian di pasar karat;","Selamat bertugas di bulan Ramadhan,","Sambil bekerja teruskan beribadat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102831,"Cantik orangnya tersenyum merekah,","Ketika melawat ke Pulau Pandan;","Besar pahalanya kalau bersedekah,","Di hari Jumaat di bulan Ramadhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102832,"Kalau tuan membeli halia,","Beli juga daun kari;","Bulan Ramadhan bulan mulia,","Hari jumaat penghulu segala hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102833,"Minum secangkir jusnya rambutan,","sunyi-syunyian rindu pun bercantum;","senyum diukir tak kelihatan,","puji-pujian buatku senyum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102834,"Gadis sunti berkebaya baldu,","Waktu gerimis memetik mawar;","Bila hati berombak rindu,","Senyuman manis jadi penawar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102835,"Batu Kikir pekan indah,","Siang pari pisau tumpul;","Senyum terukir sedekah mudah,","Jumaat hari pahala dikumpul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102836,"Sungguh harmonis hidup setaman,","teman berteman tidak menyisih;","kurangkan manis dalam minuman,","manis senyuman pengikat kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102837,"Daun pandan dari Rokan,","Buah kurma dari Mekah;","Banyakkan amalan dibulan Ramadhan,","Senyuman juga satu sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102838,"Hang Lekir bicara beralas,","Hadapan puteri terasa kelu;","Senyum diukir sepenuh ikhlas,","Kanan memberi kiri tak tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102839,"Tanam tebu ditepi bibir,","Di muara sungai memasang lukah;","Senyum manis lembut terukir,","Ikhlaskan senyuman dalam sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102840,"Penuh ikan didalam lukah,","Sungguh berat raga pulasan;","Ikhlaskan hati dalam bersedekah,","Tidak mengharap sebarang balasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102841,"Buah gugur baik dipungut,","Nanti dimakan burung terkukur;","Hidup jangan banyak merungut,","Banyak bersedekah tanda bersyukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102842,"Ada wanita di balik tabir,","Gelak berdekah nampak kepala;","Senyum kita manis di bibir,","Rupanya sedekah diberi pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102843,"Kain serupa berwarna merah,","Dipakai menantu naik kereta;","Usah dilupa zakat fitrah,","Ummah bersatu membersih harta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102844,"Dari Lipis ke Batu Kikir,","Makan cendol dalam gelas;","Manis senyuman terukir dibibir,","Senyum diberi penuh ikhlas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102845,"Musim haji pergi ke Mekah,","Pergi ke sana bersama jiran;","Hari Jumaat banyakkan sedekah,","Doa dan salam tanpa bayaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102846,"Tanaman manggis rapat ke jeti,","Tumbuhnya rendah dari kelapa;","Senyuman manis menambat hati,","Hilanglah sudah duka nestapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102847,"Halaman rumah nampak berseri,","Walau pun penat tidak rugi;","Senyuman manis ikhlas di beri,","Solat dhuha membawa rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102848,"Kalau berjalan ke Batu Kikir,","Damai rasanya diwaktu petang;","Walau sedikit senyum diukir,","Rami yang terpandang terasa senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102849,"Terlajak tepung kuih merekah,","Tersilap adunan pening kepala;","Amalan terbaik kita bersedekah,","Ganjaran Allah besar pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102850,"Sirih bergambir coklat dikulum,","Buah manggis masak merekah;","Lebarkan bibir lemparkan senyum,","Senyuman manis umpama merekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102851,"Dahan yang rekah menimpa halia,","Ranting merata di tanah merekah;","Jumaat barakah hari mulia,","Senyumlah kita sebagai sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102852,"Dari Lipis ke Batu Kikir,","Sungai diredah memasang lukah;","Dibulan Ramadhan banyakkan berzikir,","Semoga kehidupan dipenuhi barakah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102853,"Di atas sejadah tangan disilang,","Takbir diangkat kiblat diarah;","Jumaat indah tiada terbilang,","Ibadah ditingkat Allah permurah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102854,"Daun pandan di racik-racik,","Teruna dara riang gembira;","Senyuman manis di lihat menarik,","Sedekah ikhlas dengan sempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102855,"Anak muda mencari ikan,","Ikan ditangkap menggunakan lukah;","Bukanlah harta dijadikan ukuran,","Senyuman yang ikhlas sedekah termudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102856,"Merah darah kaki terluka,","Kerana tersepak kayu yang patah;","Allah anugerah rezeki berganda,","Ikhlas berinfaq beroleh barakah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102857,"Sudah merekah ketupat palas,","Kerana salah membuat sukatan;","Mari bersedekah tak mengharap balas,","Serahkan Allah membuat penilaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102858,"Ke Batu Kikir membawa lukah,","Jalan selesa tiada tersekat;","Senyum diukir satu sedekah,","Pahala puasa jumaat berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102859,"Susah hidup miskin fakir,","Kerja tiada penat lelah;","Sedekah dihulur senyum diukir,","Semoga amalan diberkati Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102860,"Terbang burung dipokok sena,","Hinggap seekor diranting rekah;","Senyuman diukir penuh makna,","Jumaat ramadhan paling barokah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102861,"Jumaat 1 mei hari pekerja,","Jatuh pula pada ramadhan mulia;","Ikhlas dalam membuat kerja,","Pekerja ikhlas dipandang mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102862,"Dari Lipis ke Batu Kikir,","Sungai diredah memasang lukah;","Manis manis senyuman diukir,","Sedekah mudah Jumaat barakah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102863,"Terbang merata hingga ke huma,","Pulang ke hutan setelah malam;","Alam semesta dijaga bersama,","Pelengkap tuntutan Bahasa Alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102864,"Kata nan manis usah tergoda,","Berhati hati diusia mekar;","Hidup usah mendabik dada,","Tak tercelar ular menyusur akar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102865,"Tidak terkata wajahnya bengis,","Memang ternyata di depan mata;","Bukit merata penyu menangis,","Angkara kita alam derita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102866,"Rusa betina berbelang kaki,","Mati terkena jerat sembat;","Orang yang muda kita sanjungi,","Orang yang tua kita hormat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102867,"Cerita silam Andalusia,","Allah menguji tidak perhati;","Semesta alam penuh rahsia,","Mesti dikaji dengan teliti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102868,"Kueh pelita didalam talam,","Hasil cipta dari cik puan;","Indah nya nyata di dalam alam,","Kuat kan semangat serta iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102869,"Kambing gurun berkacamata,","Kambing kurap tidur tak lena;","Wujudnya alam bukti yang nyata,","Adanya Allah yang Maha Pencipta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102870,"Terbang sekawan burung kedidi,","Hinggap pula dipokok senduduk;","Ikutlah resmi padi,","Makin berisi makin menunduk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102871,"Alunkan lagu penuh puisi,","Irama syahdu lemah gemalai;","Kata tersurat penuh diisi,","Nasihat bernas janganlah lalai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102872,"Sapu kaya sama herba,","Letak galah atas bata;","Alam maya berbagai pancaroba,","Mohon allah selesai sengketa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102873,"Gelap gelemat tidak selamat,","Terbang merbah pergi ke lembah;","Alam yang nikmat tandanya rahmat,","Kena musibah perlu muhasabah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102874,"Kantan lengkuas tanam setentang,","Tumbuh sebelah pokoknya turi;","Lautan luas alam terbentang,","Kurniaan Allah kita syukuri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102875,"Di balik pagar ada kuda,","Kuda memanjat kena renjangan;","Buat baik berpada pada,","Buat jahat sekali jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102876,"Cuaca kelam kala petang,","Dengan tinta menulis madah;","Merenung alam ilham datang,","Maka tercipta pantun yang indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102877,"Puteri bersiram dipinggir kolah,","Hembusan angin sungguh nyaman;","Jadikan ilham ciptaan Allah,","Hadapi kehidupan perkukuhkan iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102878,"Sejarah Melaka kota istana,","Jadi tumpuan juga idaman;","Indah alam ciptaan yg esa,","Syukur selalu jadi amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102879,"Bawa bekal di tepi sawah,","Duduk bersimpuh menjamah hidangan;","Amal kan sikap rajin bersedekah,","Menolong yang susah ringankan bebanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102880,"Hendak pergi ke pulau Daik,","Tempat jauh di penuhi hutan;","Muliakan peribadi berkata baik,","Supaya yang lain menjadi ikutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102881,"Kalau tak sudi lupakan saja,","Padamkan gambar kirimkan salam;","Apa terjadi tidak sengaja,","Ambil iktibar pedoman alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102882,"Niat mendaki gunung Daik,","Singgah sebentar di Bandar Melaka;","Orang baik budi pun baik,","Ke mana pergi orang pun suka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102883,"Cantik jelita jadi kesayangan,","Menabur budi menyorok-nyorok;","Alam semesta dimusnah jangan,","Takut terjadi bencana buruk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102884,"Dari kota ke gunung Daik,","Dalam sangkar dibawa undan;","Insaf diri dalam membuat baik,","Semesti ikut pemeritah punya arahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102885,"Pokok pegaga tumbuh melata,","Hidup subur di tanah pamah;","Belajarlah dari sekeliling kita,","Semoga kita mendapat hikmah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102886,"Kalau tuan pergi ke Pekan,","Kirimkan saya sedikit tepung;","Kesana kemari sudah dibolehkan,","Emak tanya bila nak balik kampung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102887,"Ke belukar mencari rotan,","Buat perabut juga anyaman;","Alam semesta bukan sekadar pandangan,","Tapi pedoman baik buat kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102888,"Kalau sudah menanam talas,","Jangan buang di tanah darat;","Yang jahat pasti terbalas,","Sengsara di dunia dan di akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102889,"Hendak berkata terketar-ketar,","Sakit mulut makan kanji;","Siang malam sentiasa berputar,","Tepati masa bila berjanji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102890,"Kepasar selasa bawa kurma,","Simpang bujur jalan merata;","Kehidupan selesa impian semua,","Ikhtiar jujur amalan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102891,"Panas terasa pergi berendam,","Bersesak di kolam padat sepenuh;","Kerakusan manusia memugar alam,","Rosak alam petanda mengeluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102892,"Panas terasa pergi berendam,","Bersesak di kolam padat sepenuh;","Kerakusan manusia memugar alam,","Rosak alam petanda mengeluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102893,"Turun kebendang berhari-hari,","Terbang bebas si burung camar;","Jadilah seperti hangat mentari,","Walau di cemuh tetap bersinar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102894,"Pisang emas dipotong bersikat,","Diperam bersama buah mempelam;","Nilai alam penuh berkat,","Jagai ia hormati alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102895,"Pergi kedai membeli ikan,","Ayam juga jadi pilihan;","Jaga diri utamakan kebersihan,","Jadikan ia amalan seharian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102896,"Selindung balam bawah jerami,","Leka bersujud mengutip lada;","Bahasa alam cuba fahami,","Sebalik ujud ada petanda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102897,"Batang rumbia tempat menyidai,","Bilah rotan kelapa setandan;","Pentas dunia gelagatnya pelbagai,","Baik jadikan ikutan buruk jadi sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102898,"Jalur gemilang sedang berkibar,","Angin berhembus terasa nyaman;","Jadikan pedoman ambil iktibar,","Semoga diri kekal beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102899,"Kala malam langit membiru,","Cuaca redup memayung desa;","Dengan alam kalau berguru,","Pasti hidup aman sentosa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102900,"Dari kota ke Gunung Daik,","Bersama si dia mengutip halia;","Alam semesta pedoman terbaik,","Kita manusia ciptaan termulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102901,"Bunga-bunga cantik menawan,","Mekar berkembang di waktu pagi;","Teladan baik jadi panduan,","Yang buruk mohon dijauhi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102902,"Indah berkalam seni olahan,","Syahdu pujangga susunan kata;","Anugerah alam kurniaan Tuhan,","Sama menjaga warisan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102903,"Membaca cerita Lubalang Daik,","Jiwa meruntun bercampur gundah;","Alam semesta pedoman terbaik,","Mencipta pantun cantik dan indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102904,"Anak lipan didalam talam,","Lipan bara bisa di depan;","Belajar kehidupan dari alam,","Agar sejahtera masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102905,"Meredah sungai memukat ikan,","Hati hati dengan sang buaya;","Indahnya alam ibarat insan,","Jagalah ia selagi terdaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102906,"Naik kereta menuju kota.","Lama di sana bertukar semi;","Alam semesta rahmat Pencipta,","Indah sempurna usah zalimi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102907,"Pipit kicauan terbang si Balam,","Hinggap di dahan burung Ketitir;","Rimba kehijauan indahnya alam,","Ciptaan Tuhan bagi yang berfikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102908,"Alam sekitar pedoman terbaik,","Jadikan iktibar juga tauladan;","Hidup di dunia berbuat baik,","Bila tiada kelak kan jadi kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102909,"Alam semesta pedoman terbaik,","Jadikan iktibar juga teladan;","Hati yang baik akhlak pun baik,","Akhlak yang baik menjadi ikutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102910,"Indah sungguh tarian Melayu,","Menjadi ikutan dara jelita;","Hidup ini harus bersatu,","Agar kita sentiasa redha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102911,"Ketitir balam di pinggir kota,","Kicau berserta burung tekukur;","Seisi alam indah dicipta,","Pada Pencipta lafazkan syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102912,"Gunung daik bercabang tiga,","Nampak berbalam dari seberang;","Buat lah baik berpada pada,","Jangan sampai dibenci orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102913,"Cantik sekata bunga hiasan,","Dihias serumpun bunga melati;","Sindiran kata bahasa kiasan,","Teguran santun menjaga hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102914,"Pipit bersiul di hujung taman,","Terpegun sang lembu atas permatang;","Hidup bergaul beribu pengalaman,","Pedoman dan ilmu membina matang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102915,"Pergi ke bendang sebagai ruji,","Cuaca kelam samar pandangan;","Dugaan bertandang sekadar menguji,","Kejadian alam sebagai bandingan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102916,"Kisah semalam jadi kenangan,","Bila di kenang hiba terasa;","Quran dan sunnah jadi pegangan,","Pedoman hidup sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102917,"Radio Klasik alunan berzaman,","Berlagu seiring denyutan nadi;","Andai hidup tiada berpedoman,","Ibarat kapal patah kemudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102918,"Lemah terkulai dek ucap sindiran,","Di kesatnya kata Allah menguji;","Jangan menilai indahnya luaran,","Cari permata disudut dijanji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102919,"Indah pemandangan di gunung Daik,","Nampak dari celah dahan;","Hargai alam perkara yang baik,","Moga sentiasa diberkati Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102920,"Gunung daik bercabang tiga,","Cabang yang mana jalan berliku;","Renungan baik untuk kita,","Barang cantik dikenang selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102921,"Selawat salam tadarus Quran,","Syahdu didengar tuntutan Agama;","Indahnya Alam ciptaan Tuhan,","Melindunginya amanah bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102922,"Anak raja pergi memanah,","Panah sikancil terkena kaki;","Baik berbudi kepada tanah,","Hasilnya dapat itulah rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102923,"Gunung Daik sebalik mentari,","Cantik dilihat di ufuk Barat;","Berbuatlah baik jahat hindari,","Pasti selamat dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102924,"Indah bersopan tutur dan kata,","Sederhana dandan bila kebandar;","Universiti kehidupan didunia nyata,","Baik diteladan yg buruk dihindar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102925,"Hidang juadah dalam talam,","Ada kuih buatan Nyonya;","Ambillah iktibar dari alam,","Buruk baik ada petunjuknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102926,"Alam semesta pedoman terbaik,","Jadikan iktibar juga pedoman;","Hidup didunia buat terbaik,","Moga moga tenang dan aman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102927,"Dari Kota ke Gunung Daik,","Dalam sangkar dibawa undan;","Alam semesta pedoman terbaik,","Jadikan Iktibar juga teladan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102928,"Hari selasa pergi ke pekan,","Pandu sendiri jalan bersama;","Berbagai fasa kita laksanakan,","Sikap diri paling utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102929,"Tidak terperi ayu memikat,","Bersirih bujur cantik dan mekar;","Kawalan kendiri wajar dipertingkat,","SOP dianjur jangan diingkar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102930,"Tangkap ikan anak tenggiri,","Banyak ketuka di punggah nelayan;","Jaga kebersihan, jarakkan diri,","Pelitup muka dilupa jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102931,"Serah kunci penghulu berbunyi,","Peduli belaka restu agama;","Rumah suci dahulu bersunyi,","Kembali terbuka pintu utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102932,"Terbang merata hingga ke daik,","Di pohon pedada riang bernyanyi;","Sentiasa kita bersangka baik,","Pastinya ada hikmah tersembunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102933,"Disebut jangan dikenang jangan,","Yang pergi biarkan pergi;","Patuhi arahan jangan ambil ringan,","SOP juga perjarakan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102934,"Di bawah dahan mematuk antah,","Ayam berlari berebut-rebutan;","Ikutlah arahan patuhi perintah,","Mengawal diri daripada jangkitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102935,"Burung merak berjalan perlahan,","Terkena getah melekat di jari;","Jaga jarak jaga kebersihan,","Patuhi perintah kawallah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102936,"Burung nuri terbang lalu,","Menuju bangsal dikelilingi duri;","Kawallah diri berjaga selalu,","Taklah menyesal di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102937,"Terbang Garuda membelah awan,","Berlegar terus serata buana;","Teguh waspada kita lawan,","Segera hapus covid durjana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102938,"Makan ulam bersambal kuini,","Rasa kenyang duduk tersandar;","Kena patuhi fasa terkini,","Biar wabak cepat terhindar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102939,"Usah bertingkah pasal tajaan,","Sikap negatif mesti dibantah;","Berbagai langkah dilaksana kerajaan,","Rakyat positif ikut perintah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102940,"Rasa ketenangan mengingat Tuhan,","Akur yang diduga persiapkan diri;","Fasa pemulihan adalah tempoh bertenang,","Waspada dan curiga kekal di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102941,"Ikan diperap cara bertingkah,","Aroma masakan harum semerbak;","Sama sederap kita melangkah,","Menuju pemulihan jangkitan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102942,"Buah manggis buah rambutan,","Buah mangga dalam baldi,","Masuk premis suhu dibacakan,","Jangan lupa bawa pen sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102943,"Merah-merah biji saga,","Cantik berkilat warna terpancar;","Sama-sama kita jaga,","Perintah SOP pasti lancar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102944,"Enak dimakan sibuah berangan,","Dimakan bersama sibuah durian;","Kawalan kendiri dilupa jangan,","Jadikan SOP rutin harian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102945,"Sahutan bertumpu bergema suara,","Laungan yang stabil berkerut dahi;","Yang pasti penentu hanya dua perkara,","Tindakan yang diambil dan ketentuan Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102946,"Selisih kereta potong di tepi,","Kerana ngeri dipandu cermat;","Bersusah kita jalani PKP,","Kendali diri langkah selamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102947,"Padi bukit sudah disemai,","Menyemai sehari tumbuhnya berbulan;","Tempat sempit suasana ramai,","Mari hindari harus tinggalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102948,"Kasa basahan telah dibuang,","Memakai sarung membuat kerja;","Fasa pemulihan memberi ruang,","Tidak terkurung di rumah saja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102949,"Pergi ke seberang menaiki rakit,","Hanya untuk mengambil rambutan;","PKP pemulihan bukanlah tiket,","Untuk kita lupa daratan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102950,"Terbang bertiga menuju kota,","Dari hutan mencari makan;","Kita jaga diri kita,","Demi keselamatan demi kebaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102951,"Padi jelantah tuai di huma,","Bersama besan berpeluh dahi;","Patuh perintah kita bersama,","Langkah berkesan kita patuhi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102952,"Petik-petik si buah berangan,","Kumpul sama didalam dulang;","Kawalan kendiri dilupa jangan,","Agar COVID lekas menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102953,"Polis Doktor Askar Jururawat,","Barisan Depan Tak Kenal Penat;","Covid Dihindar Itulah Matlamat,","Moga Tuhan Tunaikan Hajat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102954,"Gendang gendut tali kecapi,","Jatuhnya kuah ke nasi juga;","Biar mengikut kawalan kendiri,","Supaya mudah urusan terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102955,"Dusun ditebas sepanjang pagi,","Di tengah hari berhenti makan;","Biarpun bebas semasa PKPP,","Kawalan kendiri kita tingkatkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102956,"Dahan patah penuh berduri,","Tertikam kekaki sukar diramal;","Arahan PKP kita patuhi,","Doa bersama kehidupan normal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102957,"Fasa pemulihan mengambil alih,","Marilah terus tepati arahan;","Semoga dunia kembali pulih,","Dengan asbab patuhi kawalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102958,"Nampak merata burung di langit,","Sekawan camar terbang rendah;","Lupus gelora wabak penyakit,","Jangan dicemar ingkar perintah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102959,"Laluan nuri berat ke kanan,","Di atas paya baru bertambak;","Kawallah diri peka saranan,","Moga berjaya putuskan wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102960,"Kalau jalan ke pekan Tuaran,","Jangan lupa membeli ragi;","Norma baru rutin harian,","Kelak tidak ditinggal lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102961,"Indah berseri kemuning cina,","Berbunga lebat di hujung dahan;","Rakyat bestari selalu cakna,","Peka maklumat patuh arahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102962,"Jalan selari ke rumah Salmah,","Berdua sufian terpijak lopak;","Kawalan kendiri cara berhemah,","Putus Rantaian Mencegah wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102963,"Makan nasi ulam pegaga,","Habis makan air diminum;","Jaga diri jaga keluarga,","PKPP sudah diumum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102964,"Kail tenggiri berpeluh dahi,","Dicega ombak perahu redah;","Kawal kendiri kita patuhi,","Semoga wabak berakhir sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102965,"Sakit hati masih terluka,","Ubatkan hati alunankan sendiri;","Guna sentiasa penutup muka,","Terhindar wabak jaga kendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102966,"Berdayung sampan ketengah muara,","Hendak memukat ditengah lautan;","Barisan hadapan wira negara,","Pertahan rakyat menjaga kedaulatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102967,"Musim cuti banyak kenduri,","Hadirin datang tidak menyepi;","PKP pemulihan jaga kita jaga diri,","Sikap amalan patuhi SOP.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102968,"Jalan-jalan ke Kuala Saga,","Singgah minum secangkir kopi;","Putuskan rantaian wabak berbahaya,","Kawal keluarga dan diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102969,"Kampungku nun tempat dilahir,","Tidak jauh jalannya lurus;","PKP pasti akan berakhir,","Moga dijauh dihapus terus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102970,"Buah langsat kuning mencelah,","Senduduk tidak berbunga lagi;","Elakkan alasan, hindari helah,","Kawalan kendiri sentiasa dihayati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102971,"Pergi Perak pandu sendiri,","Hendak mencari buah durian;","Anjurkan majlis telah diberi,","Jagalah diri demi kesihatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102972,"Jalan perlahan berhati hati,","Lurah lembab nanti gelincir;","Patuh arahan dengan teliti,","Susah senang sendiri fikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102973,"Terbang merata hingga ke daik;","Di pohon pedada riang bernyanyi:","Sentiasa kita bersangka baik,","Pastinya ada hikmah tersembunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102974,"Lubalang Daik mencari rakan,","Datang berakit dayung bergalah;","Amalan baik jangan abaikan,","Jauh penyakit keredaan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102975,"Ke tambak johor berduyun-duyun,","Hendak mencari emas suasa;","Meskipun wabak semakin menurun,","Kawalan kendiri waspada sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102976,"Jika merantau biar sampai,","Agar tercapai tempat dituju;","Kesihatan diri jangan diabai,","Janganlah kesihatan diri selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102977,"Pergi kehutan membawa raga,","Sambil mencari daun palas;","PKPP kendiri perlu dijaga,","Putuskan rantaian wabak COVID19.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102978,"Alunan muzik merdu kedengaran,","Mengusik kalbu irama dendangan;","Terimakasih MKN atas ingatan,","Segala arahan kerajaan kami ikutkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102979,"Berperi merdu burung kenari,","Sahut sahutan burung tempua;","Jangan ingkar saranan diberi,","Demi bahagia kita semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102980,"Berjalan berlari dipagi hari,","Amalan baik untuk kesihatan;","Kawalan kendiri mesti dipatuhi,","Bersama kita menuju kemenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102981,"Musim tengkujuh air melimpah,","Kawasan rendah jadi sasaran;","Disebalik musibah kandung hikmah,","Ambil iktibar jadikan pengajaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102982,"Setiap pemimpin insan terpilih,","Bukan usia batas pengukur;","Wabak menular semakin pulih,","Waspada sentiasa tanda bersyukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102983,"Kalau pergi ke kuala kedah,","Belikan hamba pucuk pegaga;","Pkpp jangan ambil mudah,","Jaga diri jaga keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102984,"Dari tambun pergi kegemas,","Singgah diselayang mengambil nyonya;","Harta bertimbun segunung emas,","Kasih sayang tiada nilainya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102985,"Setulus ikhlas salam dihulur,","Senyuman terukir bertambah cantik;","Saling membantu bertolak ansur,","Urusan kerja berjalan baik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102986,"Pergi ke kota dengan si nona,","Beli ceri dan buah keranji;","Limpahan harta tidak bermakna,","Dapatnya dari cara yang keji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102987,"Dilubuk hati setulus ikhlas,","Kata dicipta tak berbelah bagi;","Manis diberi tuba kau balas,","Tidak ku rela di dusta lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102988,"Bunga teluki puspa angsana,","Digubah dara letak di tepi;","Mewah rezeki hidup sempurna,","Sirna gembira ratapi sepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102989,"Mari dilurut pucuk meranti,","Serak taburan si daun pandan;","Usah diturut rasanya hati,","Silap caturan merana badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102990,"Beras segantang sudah disukat,","Lalu disimpan di atas para;","Andai seluruh masyarakat muafakat,","Negara damai rakyat sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102991,"Suara azan dari madrasah,","Dalam paya anak berudu;","Makan tak kenyang mandi tak basah,","Hati lara menanggung rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102992,"Tanah diramas berhati-hati,","Semai di sawah sepokok jawa;","Bersangkar emas tidak bererti,","Hidup bermewah tidak bertakwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102993,"Pergi ke pantai bermain kayak,","Letih berdayung rehatlah sekarang;","Apa guna harta yang banyak,","Susah menderma berkatnya kurang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102994,"Hinggap di sigai burung kenari,","Di anjung limas bentuknya nenas;","Terasa bagai menelan duri,","Cawannya emas airnya panas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102995,"Begini rasa meminum kopi,","Pahit-pahit ku pinum jua;","Begini rasa membalas budi,","Jauh sakit ku datang juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102996,"Sampan terbelah tersepit jari,","Serap belahan dipupuk ketumbar;","Andai masalah menghimpit diri,","Tetaplah tahan untuk bersabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102997,"Madu secangkir dirasa sudah,","Tumpah terpalit didulang tembaga;","Senyum terukir tatkala gundah,","Walau pahit ditelan juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102998,"Mentari pagi bersinar lagi,","Sambil minum teh Sarbat;","Assalamualaikum dan selamat pagi,","Buat Anisma dan semua sahabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (102999,"Buah keranji daun mengkudu,","Di bawa juga untuk jualan;","Kalau berjanji setia dipadu,","Jiwa dan raga daku serahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103000,"Ikan duri masak kari,","Ulam pengaga di lupa jangan;","Sakit hati tk di hargai,","Hidup bagai orang buangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103001,"Tanah disukat kurang sekaki,","hendak dibanci elak muslihat;","mencari berkat sumber rezeki,","biarlah suci bukan subahat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103002,"Berhujan panas mencari palas,","Sambil berjalan membawa guri;","Di gelas emas minum beralas,","Air ditelan bercampur duri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103003,"Di ufuk timur berkelip bintang,","Pudar kelip nya di sinar mentari;","Ku sangka panas sampai ke petang,","Rupanya hujan di tengah hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103004,"Air secangkir dituang ditadah,","Untuk dinikmati setelah berbuka;","Di bibir terukir senyuman indah,","Di lubuk hati bergelumang duka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103005,"Tepi jendela bersila panggung,","Nyaman disapa udara bersih;","Derita jiwa rela ditanggung,","Andai ia menyambung kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103006,"Masak ranum buah ceri,","Musim tengkujuh menanam nenas;","Air diminum terasa duri,","Tonggeng gelojoh rupanya panas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103007,"Baca akhbar rakan menyapa,","Bawa lada dimakan merbah;","Indah khabar dari rupa,","Terus waspada elak musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103008,"Selamat bertugas giliran pagi,","Teman berteman erat sekali;","Cantik dimata cantik dihati,","Hubungan ukhuwah subur mewangi.", 4, 6);
