INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103100,"Tersohor kari di kedai mamu,","Dibeli mertua si gulai serati;","Pejarakkan diri kalau bertemu,","Itu petua menyekat dijangkiti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103101,"Sarang kerengga di hujung dahan,","Pohon yang tinggi boleh di lihat;","Duduk di rumah patuhkan arahan,","Hindari wabak dari melarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103102,"Ikan siakap lekat di pukat,","Anak kelah masuk di sangkar;","Demikian sikap segelintir masyarakat,","Makin ditegah seronok diingkar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103103,"Setiap pagi mawar di jirus,","Tumbuh mekar tepi sawah;","Duduk di rumah putuskan virus,","Demi sekeluarga ikatkan ukhwah.", 4, 6);
