INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103110,"Ikat seluar simpul mati,","Seluar basahan sangkut di dahan;","Usah keluar sesuka hati,","Patuhi arahan ikut panduan.", 4, 6);
