INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103113,"Jalan-jalan bersama Cik Limah,","Terus terpandang pohon meranti;","Jika kita duduk di rumah,","Virus jangkitan boleh dihenti.", 4, 6);
