INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103264,"Pagi hari pergi berniaga,","Kayuh basikal menuju kepekan;","Duduk berkurung bersama keluarga,","Maklumat dan hiburan DJ siarkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103265,"Rehat sebentar tepi empangan,","Sepasang merpati celahan lalang;","Lagu diputar menggamit kenangan,","Lara hati beransur hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103266,"Nasi dijamah ulam pegaga,","Sayur keladi tinggal sedikit;","Berehat di rumah bersama keluarga,","Elakkan diri dijangkiti penyakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103267,"Dj radio sungguh peramah,","Siarkan lagu untuk semua insan;","Walau pun berkurung dalam rumah,","Berkemas memasak tidak bosan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103268,"Patah dahan kayu mereput,","Timpa jatuh pohon semalu;","Perintah kawalan mesti diikut,","Semoga virus cepat berlalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103269,"Awan berpintal terapung-apung,","Tepian pantai tidak berombak;","Kalau tuan pulang ke kampung,","Pastikan dulu diri bebas wabak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103270,"Batu cincin delima merah,","Di jari manis elok tersemat;","Elok jangan marah,","Perintah duk rumah semoga selamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103271,"Ayam di tanah sedang berlaga,","Itik pula makan rambutan;","Duduk di rumah jaga keluarga,","Sedikit pergerakan memungkinkan jangkitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103272,"Duduk bertenggek atas dahan,","Sambil makan buah Melaka;","Anak-anak kurniaan tuhan,","Merekalah penentu syurga neraka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103273,"Burung ketitir burung merbuk,","Duduk bertenggek atas dahan;","Walaupun kita hari-hari sibuk,","Namun keluarga jangan diabaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103274,"Bercucuk tanam dan membela ikan,","Hobi sihat sambil beriadah;","Duduk lah dirumah seperti diarahkan,","Virus menyerang sesal tak sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103275,"Perintah kawalan mesti diikut,","Jangan duduk tepi tasik;","Musibah yang datang pasti takut,","Di rumah dengar radio klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103276,"Berarak awan cerah mentari,","Tiada tanda hari kan hujan;","Sahut seruan Perdana Menteri,","Jauhi wabak kawal pergerakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103277,"Terbang rendah burung merbah,","Hinggap pula dihujung dahan;","Walaupun semua kurung dalam rumah,","Banyakkan amalan kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103278,"Tepian telaga bunga jambangan,","Kelibat unggas bersiul mesra;","Halwa telinga mencuit kenangan,","Selamat bertugas seceria suara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103279,"Awan cerah hingga ke pekan,","Tinggi nian berarak mega;","Tuan di rumah kami hiburkan,","Sayangi diri jaga keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103280,"Pergi memancing membawa joran,","Sayang nya tak dapat ikan;","Adik beradik kita adalah jiran,","Persahabatan mulia tidak menghampakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103281,"Pohon cenderai dahannya patah,","Jatuh merintang di tengah jalan;","Jangan bercerai berpecah belah,","Wujud silaturrahim sesama jiran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103282,"Sayang saudara kasihkan jiran,","Beri hadiah bagi cenderahati;","Dalam memelihara jiran dan persaudaraan,","Pelihara hak lidah dan juga hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103283,"Parang berkarat dalam simpanan,","Dilampas tajam di tengahari;","Berjiran ibarat kedua belahnya tangan,","Saling membersihkan sesama sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103284,"Bawa manggis dalam timba,","Sama halia letak ditaman;","Jiran menanggis kuta berhiba,","Amalan mulia orang beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103285,"Terkena jelaga ketika memasaknya,","Lauk berkuah berrserta nasi;","Jiran tetangga baik orangnya,","Musim buah selalu berkongsi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103286,"Ayam denak terbang berkeliaran,","Di Pasir Besar hutannya dekat;","Membela anak kecil jiran,","Dah besar menjadi anak angkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103287,"Ambil joran umpan dilekat,","Air tenang di hujung desa;","Hidup berjiran elok muafakat,","Susah senang sama dirasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103288,"JULIA ramah pandai bercerita,","Bawa joran rapi diikat;","40 rumah keliling kita,","Hidup berjiran membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103289,"Pergi berakit membawa tangguk,","Sungai takat banyak talapia;","Jiran sakit wajib di jenguk,","Hidup bermasyarakat sesama manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103290,"Biar terputus tali diikatan,","Kayu dibelah mengintung bara;","Jangan memutuskan hubungan kejiranan,","Hamba Allah semuanya bersaudara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103291,"Bunga di taman merah menyala,","Tempat meletak lampu pelita;","Manis senyuman ibarat bergula,","Jiran sebelah saudara kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103292,"Lama hidup banyak pengalaman,","Kisah lama sering terkenang;","Hidup berjiran perlu bersefahaman,","Agar jiwa terana tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103293,"Terbangnya cepat dari utara,","Di pohon Ara hinggap berlima;","Jiran setempat jiran bersaudara,","Tanpa mengira bangsa dan agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103294,"Burung merpati dipokok kelapa,","Sayang terikat si ayam serama;","Hormat menghormati saling menyapa,","Ukhuwah muafakat dibina bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103295,"Jinaknya haiwan dibilang akur,","Dijaga baik diberi nama;","Jiran tetangga bak penghibur,","Tatkala suka dan duka tetap bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103296,"Buat joran mengail ikan,","Umpan di makan dapat gelama;","Sayangi jiran rakan taulan,","Susah senang muafakat bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103297,"Air telaga di petang hari,","Masin terasa banyak berbuih;","Jiran tetangga kanan dan kiri,","Di bulan puasa bertukar kuih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103298,"Santan digaul ramuannya pekat,","Tepung diuli buat penganan;","Keutamaan bergaul dalam masyarakat,","Kebaikan budi erat kejiranan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103299,"Kayu patah diganti buluh,","Tongkat pokok agar tak tumbang;","Pada yg susah usah mengeluh,","Kita membantu biar seimbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103300,"Bertegur sapa jadikan amalan,","Tak kira siang atau malam;","Suburkan kasih sayang sesama jiran,","Ibart manisnya gula masinnya garam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103301,"Si dara bersimpuh menyulam tekat,","Buat teruna jadi bekalan;","Jiran yang baik membawa berkat,","Hidup bermuafakat jadikan amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103302,"Berkasih sayang sehidup semati,","Bercinta jangan sambil lewa;","Jiran yang baik sentiasa di hati,","Tawa dan tangisnya sebati di jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103303,"Terbitnya awan nanti kelebat,","Sinaran naik kepulan bersih;","Hadirnya kawan jadi sahabat,","Jiran yang baik jangan disisih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103304,"Terbang tinggi si burung angsa,","Berhenti terbang disengat tabuan;","Hidup kita berbilang bangsa,","Kekalkan hubungan eratkan perpaduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103305,"Pancing kelah sungai Beruas,","Umpan tapa dari kota;","Jiran sebelah janda berhias,","Pantang jumpa mengenyit mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103306,"Membaca quran bersama teman,","Asuhan qari dari serian;","Bertemu jiran lemparkan senyuman,","Moga menjadi amalan harian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103307,"Jangan mudah berputus asa,","Hingga termenung berduka lara;","Jiran berbudi kita berbahasa,","Barulah hidup aman sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103308,"Redup hari jemuran diangkat,","Mari sesama kain dilipat;","Hidup berjiran hidup berpakat,","Rezki dikongsi membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103309,"Tanam pinang rapat-rapat,","Biar senang puyuh berlari;","Jiran tetangga saudara terdekat,","Bertanya khabarlah setiap hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103310,"Tidur malam bertilam awan,","Petang hari bersembang dirumah jiran;","Burung terbang berkawan kawan,","Saudara terdekat kita itu lah jiran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103311,"Pagi hari makan capati,","Minum pula air halia;","Sesama jiran hormat menghormati,","Itulah dia amalan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103312,"Melentok joran disambar nyonya,","Umpan roti dimakan labi-labi;","Memuliakan jiran besar pahalanya,","Sejarah terbukti di zaman nabi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103313,"Ayam didik berkokok perlahan,","Kakinya terikat tali belati;","Adik-beradik duduk berjauhan,","Jiran terdekat jadi pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103314,"Memetik lada sepasang dara,","Dijual diniaga di pasar sabtu;","Bila ada kenduri-kendara,","Jiran tetangga menolong membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103315,"Mengikat tali dibatang joran,","Joran diguna mengail ikan;","Ambil peduli terhadap jiran,","Hidup harmoni kita amalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103316,"Itik angsa berenang bersama,","Dalam telaga itik serati;","Berlainan bangsa berlain agama,","Jiran tetangga sentiasa dihormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103317,"Di dalam raga tepung pelita,","Mengutip pinang di pinggir kali;","Jiran tetangga saudara kita,","Susah dan senang ambil peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103318,"Kalau masam tentulah asam,","Pahit pula bagai hempedu;","Aku hulur ikan pekasam,","Jiran ku balas dengan budu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103319,"Indah banjaran bercabang tiga,","Hijau melata pepohon rendang;","Adab berjiran harus dijaga,","Tundukan mata mencuri pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103320,"Ulam pegaga sayurnya bendi,","Hantar sepiring gulainya tongsan;","Jiran tetangga terhimpun budi,","Doa diiring jadi balasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103321,"Terlompat lompat sianak merak,","Mati seekor tertelan kapur;","Jiran tetangga tempat berborak,","Usah lupa tanak nasi di dapur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103322,"Sajian enak si buah ara,","Kuih lama pembuka selera;","Jiran tetangga bak saudara,","Hidup makmur dan sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103323,"Dara bersimpuh menyulam tekat,","Buat teruna bawa bekalan;","Jiran muafakat membawa berkat,","Jadikan lah ia sebagai amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103324,"Indah ukiran bukannya tekat,","Ambil satu bawa ke Cini;","Hidup berjiran kekal sepakat,","Saling membantu hidup harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103325,"Batu geliga siap diikat,","Cantik berseri buat hantaran;","Bila tetangga saling sepakat,","Pasti harmoni hidup berjiran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103326,"Gadis manis duduk di tangga,","Bila ditegur tersenyum malu;","Kita hidup bersama jiran tetangga,","Saling bantu membantulah selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103327,"Anak serati terkena badik,","Turun berenang di tepi huma;","Jiran seperti adik beradik,","Susah senang tetap bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103328,"Warna cerah cincin suasa,","Dipakai indah gadis pingitan;","Sungguh meriah bulan puasa,","Bertukar juadah sesama jiran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103329,"Biar di desa mahupun di kota,","Amalkan kerja berganding bahu;","Jangan kerana kemewahan kita,","Jiran berlapar buat tak tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103330,"Kain tenun kain pelikat,","Corak berbunga sangat bermutu;","Jiran tertangga saudara sepakat,","Susah senang sama membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103331,"Anak itik si anak angsa,","Berenang-renang di air tenang;","Berjiran tak kira agama dan bangsa,","Hidup muafakat susah dan senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103332,"Redup banjaran subur gelugur,","Terap, berangan lebatnya ara;","Hidup berjiran tegur menegur,","Akrab hubungan bagai saudara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103333,"Naik rakit di musim bah,","Kayuh tersalah hanyut ke Pedu;","Kalau sakit ditimpa musibah,","Jiran tetanggalah tempat mengadu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103334,"Hidup berjiran amalkan muafakat,","Tolong menolong jadi amali;","Kalau jiran sudah dekat,","Ibarat bau gulai dalam kuali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103335,"Hendak ke kota mencari sutera,","Beli juga pisang sesikat;","Bulat kata aman sejahtera,","Jiran tetangga saling muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103336,"Duduk bersantai di pantai punggur,","Menikmati keindahan saujana pandangan;","Sesama jiran tegur menegur,","Ziarah menziarahi jadikan amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103337,"Kuning kuning si baju kuning,","Sungguh segak bunga dibahu;","Sakit demam sakit pening,","Tak salah ambil lah tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103338,"Indah halaman dik bunga Gelam,","Bendul tangga dihinggap tempua;","Manis senyuman bertukar salam,","Jiran tetangga adik beradik kedua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103339,"Kain dewangga halus sutera,","Ramai berkenan susah dicari;","Jiran tertangga ibarat saudara,","Dicubit kanan terasa kiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103340,"Memikat tempua di Pulau Tiga,","Pulang bersampan ke hulu muara;","Muafakat kata sesama tetangga,","Nyaman harmoni hati pun gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103341,"Pergi Tuaran empat jejaka,","Leka perhati dua sejoli;","Dengan jiran berbaik sangka,","Iri hati jangan sesekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103342,"Tahan pukat dan juga belat,","Air yang tenang banyak ikannya;","Jiran sepakat membawa berkat,","Susah senang biar bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103343,"Terburu disauk si ikan sepat,","Berenang melentuk sebelah sampan;","Terbau lauknya jiran terdekat,","Dihulur semangkok mengelak kempunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103344,"Digigi air memasang belat,","Dapat seekor ikan bentutu;","Jiran muafakat membawa berkat,","Hidup harmoni saling membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103345,"Songket biru indah ditekat,","Dua sejoli sehidup semati;","Hidup berjiran amalkan muafakat,","Negara makmur tetap dihormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103346,"Indah sulaman bercorak alam,","Kain dewangga halus sutera;","Manis senyuman berjabat salam,","Jiran tetangga ibarat saudara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103347,"Pergi memancing si ikan belanak,","Janganlah lupa bawa bekalan;","Bila ku ingat zaman kanak-kanak,","Sehingga kini masih berkekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103348,"Sungguh manis limau bali,","Manis lagi buah rambutan;","Bermain sereemban dan lompat tali,","Permainan tradisional tetap diingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103349,"Sungguh manis limau bali,","Manis lagi buah rambutan;","Bermain sereemban dan lompat tali,","Permainan tradisional tetap diingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103350,"Dijolok-jolok belalang tanah,","Di tepi sawah berduri semambu;","Bermain pondok-pondok di belakang rumah,","Ada juga yang jadi ayah dan ibu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103351,"Layang layang bertali tangsi,","Main guli di bawah tangga;","Gajah tak ponah makan nasi,","Tapi badan gedempol juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103352,"Memilih kain berasak-asak,","Di pasar kuala ramai pembeli;","Kakak bermain masak-masak,","Adik pula bermain guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103353,"Duduk bersimpuh di tepi bangku,","Sambil bermain si batu congkak;","Itulah kenangan zaman kecil ku,","Bermain bersama adik dan kakak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103354,"Sayur kacang ikan kerisi,","Buat santapan berulam peria;","Galah panjang mainan tradisi,","Bersama teman bersuka ria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103355,"Menggoreng puyu untuk diratah,","Telah dijala dapat sekarung;","Senapang kayu berikat getah,","Peluru pula bijinya terung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103356,"Kura-kura mendengus keluh,","Kepala melekap bawah titi;","Aku kira sampai sepuluh,","Siapa tertangkap dia ganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103357,"Air kelapa buah limau,","Jadi makanan di Solok Duku;","Pukul berapa Tok Rimau,","Itulah permainan kesukaanku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103358,"Pagi hari bermain lelayang,","Petang hati bermain guli;","Dikau tetap ku sayang,","Namun diriku tak dipeduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103359,"Terbang helang berpusing pusing,","Jauh tinggi nun diawan;","Dalam banyak banyak gasing,","Kayu jambu tak boleh lawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103360,"Dari Sentul tuju Segambut,","Bawa bertih habis dijaja;","Seronok betul main selambut,","Tarik upih hingga ke senja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103361,"Ada kodok di tepi reban,","Petik maman dibawa pulang;","Main pondok batu seremban,","Rindu teman alang-kepalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103362,"Laman terbentang tepi berkerikil,","Tersedia untuk lubang digali;","Waktu petang rakan memanggil,","Berkumpul untuk bermain guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103363,"Meniti budak di atas titi,","Takutnya rebah jalannya laju;","Mata ditutup sebelah teliti,","Dalam bulatan guli dituju.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103364,"Berkias madah beralun lunak,","Wajah ceria saudara saudari;","Sungguh indah zaman kanak-kanak,","Riang gembira sepanjang hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103365,"Bermain guli dilaman rumah,","Warna gulinya cantik menawan;","Senyum diberi bersifat ramah,","Murah rezeki ramai lah kawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103366,"Isikan air di dalam baldi,","Bawa kehutan mencari nira;","Seronok dulu main konda kondi,","Berlari-lari sambil mengira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103367,"Ditebang rebah pohon bidara,","Kayunya dibuat membakar ikan;","Pluck terbang dibaling ke udara,","Jatuh berdentum jadi keseronokkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103368,"Paya banyak si ikan keli,","Tangkap seekor untuk dimakan;","Punyalah seronok bermain guli,","Perut lapar lupa nak makan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103369,"Ambil akar mengikat rusa,","Rusa tejerat tepian onak;","Gelanggang pendekar warisan bangsa,","Terpisah tidak di zaman kanak-kanak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103370,"Tika sang kancil lain dikurung,","Pakat berlari memanjat busut;","Leka si kecil main tempurung,","Diikat tali dibuat kasut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103371,"Pergi ke kedai membeli kotak,","Hendak di isi buah berangan;","Teringat aku zaman budak budak,","Itu semua tinggal kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103372,"Tinggi sungguh pohon berangan,","Tempat berteduh sepasang rusa;","Cerita yang dulu tinggal kenangan,","Budak-budak sekarang tak boleh merasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103373,"Tun teja dari Lampong,","Naik perahu pulang ke desa;","Rasanya seronok duduk di kampong,","Bermain berenang perkara biasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103374,"Budak-budak bermain gasing,","Bermain saling pangkah memangkah;","Kalau susah janganlah bising,","Berusaha ikhtiar selesaikan masalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103375,"Layang-layang terbang tinggi,","Diterbangkan ramai tepi paya;","Hidup kita ada untung rugi,","Hanya yang tabah pasti berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103376,"Kicau burung di pagi hari,","Matahari bersinar terang;","Zaman kanak kanak riang berlari,","Hingga kini tetap terkenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103377,"Pegi mengail tepi sungai,","Bawak seekor ikan keli;","Jangan kita buat perangai,","Lebih baik main guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103378,"Bersiar siar di bandar seremban,","Mencari cacing mesti digali;","Kakak bermain batu seremban,","Bersama abang bermain guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103379,"Pergi memancing mencari cacing,","Mencari cacing bersama Suli;","Zaman kanak-kanak bermain gasing,","Bermain gasing dan juga guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103380,"Naik basikal berpusing,","Bila jatuh jangan bertekak;","Budak lelaki main gasing,","Budak perempuan main congkak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103381,"Kepungan sudah ada sang kancil,","Jangan diletak kayu, keluli;","Kenangan indah ketika kecil,","Bermain congkak dan tuju guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103382,"Cantik kain baju kebaya,","Kain ela dipotong labuh;","Mari bermain sepak raga,","Timbang bola badan berpeluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103383,"Biskut kelapa terasa lemau,","Dihidang nyonya kacukan banjar;","Pukul berapa datuk harimau,","Habis bertanya datuk pun kejar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103384,"Cincin warisan berbatu permata,","Tampak berseri diukir ukir;","Baling tin,Seremban permainan kita,","Warisan tradisi semakin terpinggir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103385,"Jalak lenteng lakonan seniman,","Anak desa inginkan puteri;","Bermain teng teng di tengah laman,","Dikejar angsa bertempiaran lari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103386,"Seri Siantan suka berlenggang,","Membawa kelapa jalan perlahan;","Gelung rotan dipusing di pinggang,","Berlawan siapa yang lama tahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103387,"Tarian zapin sudah diisi,","Jangan diisi tarian celaru;","Kuda kepang permainan tradisi,","Gagahkah lagi di zaman yang baru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103388,"Bermain congkak di pesta tahan,","Menang berganda hadiah idaman;","Janganlah bongkak di bumi Tuhan,","Apa yang ada semuanya pinjaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103389,"Mencari umbut digigitlah lintah,","Di tepi bendang tepian titi;","Bermain selambut bijilah getah,","Sangatlah girang sukanya hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103390,"Beli laksa di pekan Danok,","Laksa disorok dalam cerana;","Membesar di desa cukup seronok,","Main sorok-sorok hingga terlena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103391,"Bunyi rebana sudah bergema,","Cepatlah adik nanti terlambat;","Wau, gasing permainan lama,","Bagaimana mungkin mahu dimartabat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103392,"Jalan berliku pargi ke Tangkak,","Beli sekilo si udang galah;","Zaman kecilku suka bermain congkak,","Jadi hero tak pernah kalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103393,"Tuju ke reban baiki bumbung,","dibubuh redai tempa sehala;","batu seremban tinggi dilambung,","jatuh berderai timpa kepala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103394,"Isi pulut dalam bakul,","Buat tapai untuk diperam;","Tali dililit gasing dipukul,","Gasing pecah menangis geram.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103395,"Terasa lapang berehat di depan,","Bukit Berapit cantik tempatnya;","Dibuat senapang daripada papan,","Terung pipit menjadi pelurunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103396,"Jangan nyaring menyapa orang,","Bunyinya kasar tidaklah enak;","Bermain tingting dan galah panjang,","Teringat pula zaman kanak-kanak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103397,"Panjat bukit sambil merangkak,","Lepas makan perut sengkak;","Terkenang kenangan bermain congkak,","Bermain bersama arwah kakak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103398,"Beli pahat jala serawan,","Di pinggir kali pokok beban;","Waktu rehat bersama kawan,","Main guli batu seremban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103399,"Telang merukah ke lain pusing,","kaduk merempuh lentur serangkak;","silang memangkah bermain gasing,","duduk bersimpuh mengatur congkak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103400,"Lari sang kancil diserkup kain,","Ke rumpun bemban menuju kali;","Di masa kecil ralit bermain,","Jemu seremban melontar guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103401,"Kanak-kanak bermain galah,","Galah diambil dari paya;","Hidup jangan mudah mengalah,","Selalulah berusaha hingga berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103402,"Ambilkan galah tolonglah tunjuk,","Galah yang lain untuk si bujang;","Jikalau kalah aku merajuk,","Kenangan bermain hai galah panjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103403,"Jalan pusing kampung hainan,","Nasi ditanak jangan berangan;","Congkak gasing tradisi mainan,","Zaman kanak kanak jadi pertandingan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103404,"Nasi lemak rasanya enak,","Sambal tumis pedas sekali;","Rasa seronok zaman kanak-kanak,","Bermain congkak bermain guli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103405,"Pagi pagi memetik petai,","Penat memetik pakai galah;","Zaman dulu belajar tak pandai,","Banyak ponteng nak ke sekolah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103406,"Laluan denai sangat sempit,","Cari pula jalan lurus;","Tembak guna terung pipit,","Seronok berlari badan kurus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103407,"Pecah cawan menjadi serpih,","Jangan dibiar ia bersepah;","Leka berkawan menarik upih,","Tidak sedar senja dah merah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103408,"Ke pohon buluh mencari rebung,","Di sengat tebuan lari bertempiaran;","Pinggang di gelek bermain relung,","Adik menangis tiada hiraukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103409,"Berdagang ke Champa Daeng Melewar,","Gulai belanak dibekal kakak;","Rindu menyapa apa penawar,","Zaman kanak-kanak bermain congkak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103410,"Bergema bising ikan di lukah,","Tersangkut batang pokok seroja;","Bermain gasing pangkah memangkah,","Leka sepetang sehingga senja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103411,"Jalan berliku juga beronak,","Duri terpijak tidaklah asing;","Zaman riang kanak-kanak,","Bermain congkak bermain gasing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103412,"Langit nampak mendung,","Mentari entah di mana;","Hutang banyak dikendung,","Menipu sini sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103413,"Ayah asah keri,","Asah tepi laman;","Kalau cari isteri,","Cari yang beriman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103414,"Apa dalam baju,","Sekuntum bunga idaman;","Jangan pandu laju,","Nanti kena saman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103415,"Minum dalam cawan,","Sarapan lepas subuh;","Biar ramai kawan,","Jangan ramai musuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103416,"Sedap kuih talam,","Buat putri ayu;","Jauh kirim salam,","Dekat pujuk rayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103417,"Makan buah kuini,","Potong biar tebal;","Sarapan pagi ini,","Lempeng cicah sambal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103418,"Minum guna cawan,","Sarapan nasi dagang;","Bila dah tertawan,","Baik berterus terang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103419,"Pantun tiga kerat,","Nampak macam mudah;","Badan makin berat,","Kena rajin riadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103420,"Petik pucuk manis,","Sayang sudah layu;","Mimpi jumpa anis,","Tengah mancing puyu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103421,"Naik gunung daik,","Nampak bayang bayang;","Kalau hati baik,","Ramai orang sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103422,"Anak lembu merah,","Tambat di pokok asam;","Adik kena marah,","Tarik muka masam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103423,"Naik kapal selam,","Pergi rumah penghulu;","Jauh berkirim salam,","Berjumpa saya kelu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103424,"Berjalan bersama nyonya,","Melintas di atas titi;","Apa lah ubatnya,","Jika rindu di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103425,"Angan beli baldi,","Singgah dekat kedai;","Jangan termakan budi,","Nanti diri tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103426,"Pisang dua tandan,","Masak atas peti;","Sayang semua kawan,","Dikenang sampai mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103427,"Bangau tengah bendang,","Tepi pokok pauh;","Angau sudah berdendang,","Rindu orang jauh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103428,"Ayam tak patuk,","Itik tak sudu;","Mata dah mengantuk,","Jom kita beradu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103429,"Pergi Teluk Wanjah,","Sarapan makan roti;","Biar buruk wajah,","Jangan buruk pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103430,"Petang pergi pekan,","Jumpa gadis ayu;","Mari kita daulatkan,","Khazanah pantun Melayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103431,"Menjolok buah pauh,","Kaki tertusuk duri;","Mahu pergi jauh,","Berdagang membawa diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103432,"Bunga di tepi tasik,","Buah di tepi kolam;","Berteman Radio Klasik,","Pagi sampai malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103433,"Angin bertiup kencang,","Kini bertukar perlahan;","Kita hanya merancang,","Jodoh ketentuan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103434,"Cempedak luor pagor,","Minta tolong jolok;","Teman baru belajor,","Minta tolong tunjok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103435,"Pulut dalam gantang,","Beras dalam cupak;","Mulut jangan lantang,","Nanti kena penyepak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103436,"Pergi pasar malam,","Beli buah laici;","Jangan suka berdendam,","Nanti orang benci.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103437,"Pipis sebakul lada,","Sambil makan ciku;","Renung wajah bonda,","Sejuk damai hatiku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103438,"Duduk tepi tasik,","Bekal dengan manisan;","Jangan suka berbisik,","Nanti orang perasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103439,"Malam minum jamu,","Ramuan buah peria;","Kita tuntut ilmu,","Hingga hujung usia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103440,"Makan kuih talam,","Minum air segelas;","Saya bagi salam,","Mengapa tak balas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103441,"Itik pulang petang,","Sambil berlari-lari;","Mengaku masih bujang,","Tapi dah beristeri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103442,"Makan petai jawa,","Sambil kuak lentang;","Tenang fikiran jiwa,","Tak banyak hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103443,"Burung kenari cantik,","Hinggap atas pokok;","Dara berkain batik,","Teruna segak bersongkok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103444,"Kalau makan kari,","Makan tepi tasik;","Bangun pagi hari,","Dengar radio klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103445,"Lari dikejar angsa,","Bilang sampai tiga;","Usah buang masa,","Rugi kita juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103446,"Buai laju-laju,","Sampai pokok sena;","Kena jampi tuju,","Tidur tidak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103447,"Bangun gosok gigi,","Rambut kena sikat;","Senaman setiap pagi,","Badan jadi sihat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103448,"Makan roti bakar,","Sedap planta terasa;","Semua apa kabar,","Semoga sihat sentiasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103449,"Kaki pakai selipar,","Cuci air perigi;","Perut sudah lapar,","Jom sarapan pagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103450,"Keindahan asli desa,","Menjadi daya tarikan;","Santun berbudi bahasa,","Senyum sentiasa diukirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103451,"Kerat biji kiambang,","Taruh dalam sudu;","Dekat rasa bimbang,","Jauh rasa rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103452,"Riuh bunyi kereta,","Tempat jual roti;","Biar jauh dimata,","Tapi dekat dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103453,"Sedap buah betik,","Bolih cuba rasa;","Harap rupa cantik,","Tiada budi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103454,"Sedap rasa bubur,","Makan tepi tasik;","Jom kita berhibur,","Dengar Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103455,"Tebang pokok akasia,","Timpa daun kesum;","Kita satu Malaysia,","Berbagai suku kaum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103456,"Banyak pokok semalu,","Tumbuh tepi perigi;","Pagi senyum selalu,","Hingga nampak gigi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103457,"Pergi pasang tajur,","Bekal kuih koci;","Hati tidak jujur,","Semua orang benci.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103458,"Makan buah jeruk,","Masam rasa cuka;","Jangan berperangai buruk,","Orang tak suka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103459,"Pagi hari pergi ke sawah,","Waktu petang baru nak pulang;","Nampak dimata hidup mewah,","Tapi hutang keliling pinggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103460,"Pahit rasa hempedu,","Masam buah kepayang;","Bila jauh rindu,","Dekat makin sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103461,"Tebas akar seruntun,","Tebang pokok kuini;","Masuk satu pantun,","Rancakkan laman ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103462,"Pergi pulau Daik,","Untuk cari lokan;","Radio klasik terbaik,","Irama merentas zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103463,"Pauh hidu sakan,","Lupa hendak dibeli;","Jauh rindu rakan,","Jumpa tidak peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103464,"Panjat gunung daik,","Burung dalam sarang;","Kalau nak buat baik,","Pedulikan cakap orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103465,"Ambo tumpang lalu","Mahu jemur cindai","Belajar pantun dulu","Baru cepat pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103466,"Kayu jati bertarah,","Dari Kampung Merua;","Jangan marah marah,","Nanti cepat tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103467,"Kapal turun sauh,","Singgah dekat hulu;","Rindu bila jauh,","Jumpa rasa malu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103468,"Memang tak cantik dilihat,","Pakaian lusuh kotor;","Kalau rasa taksihat,","Cepatlah jumpa doktor.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103469,"Bina empang daik,","Tahan air sungai;","Kalau adik baik,","Mak bagi rantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103470,"Rendah sarang tempua,","Tepi pokok semalu;","Selamat pagi semua,","Moga ceria selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103471,"Tinggi pohon rumbia,","Ceria burung kedidi;","Selamat pagi Malaysia,","Sihat dan dirahmati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103472,"Buai laju laju,","Bawah dahan mati;","Dalam kocek baju,","Gambar cinta hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103473,"Buat kuih talam,","Makan pagi hari;","Jauh kirim salam,","Dekat mengapa lari?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103474,"Berbatik kita pergi ke parti,","Parti keraian hari jadi;","Cantik di mata cantik di hati,","Jadi pakaian hiasi peribadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103475,"Kain batik kain pelikat,","Berbagai warna jadi buruan;","Wajah cantik pandai memikat,","Hati yang mana tidak keruan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103476,"Berkain batik Si Gadis Sulu,","Naik pedati pulang ke desa;","Berwajah cantik santun pemalu,","Mencair hati teruna perkasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103477,"Batik bercorak sibunga palas,","Dijemur orang di atas pagar;","Orang berbudi tak harap dibalas,","Kalau berhutang wajib dibayar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103478,"Jangan diperap si kainnya batik,","Sidai dijemuran atas tangga;","Jangan diharap air setitik,","Jika ditelan tak hilang dahaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103479,"Bahtera terjongket dipukul gelora,","Bahtera ditambat di Bagan Semporna;","Kain songket pakaian nak dara,","Kain pelikat sarungan teruna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103480,"Berkain batik ke Pulau Tioman,","Membawa pala dan juga lokan;","Sutera Batik menjadi idaman,","Belacu pula jangan dilupakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103481,"Kain sutera indah nan bernilai,","Samping songket berselang bayang;","Anak dididik mesra dibelai,","Moga menjadi insan penyayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103482,"Kain batik kain pelikat,","Pakaian warisan orang Melayu;","Budi baik orang terpikat,","Wajah manis orang nya ayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103483,"Cantik indah sulaman batik,","Buat dari tenunan Jawa;","Jika mempunyai wajah cantik,","Pasti menjadi idaman jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103484,"Kain buruk atas papan,","Dibuat sapu si airmata;","Biar lah buruk wajah mu tuan,","Kasih mendalam tidak terkata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103485,"Kain buruk di atas bata,","Kain sutera buat tenunan;","Biar buruk di pandang mata,","Hatiku kasih pada mu tuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103486,"Kain belacu buat basahan,","Tenunan songket dipuja orang;","Rindunye hati tiada tertahan,","Pada anak dirantau orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103487,"Bulan bercahaya di Tanjung Jara,","Ke bintang di depan mata melerek;","Kurung kebaya pakaian dara,","Nampak bersopan cantik menarik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103488,"Batik dijaja dipekan sehari,","Kain sari dijual sama;","Cantik bahasa sopannya diri,","Jadi sebutan orang selama-lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103489,"Belacu tetap jadi pilihan,","Boleh dipakai dibuat basahan;","Begitu besar budi mu tuan,","Beralih masa tetap berkekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103490,"Bersulam indah emas sutera,","Rasa teruja hati di dalam;","Biar jauh rindu di damba,","Dalam doa berkirim salam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103491,"Kain batik dari Sumatera,","Buat basahan setiap hari;","Alang kan cantik kain sutera,","Bila di pakai macam bidadari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103492,"Kain baldu mahal harganya,","Di simpan orang di dalam peti;","Kalau dibuat kebaya nyonya,","Cantik di mata cantik di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103493,"Singgah bahtera riak cetusan,","tidaklah enak didengar orang;","Kain sutera corak lukisan,","Sumbangsih anak dari seberang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103494,"Sutera emas cindai sebabak,","Muram melarat pagi rentapnya;","Bahtera cemas dibadai ombak,","Karam di darat lagi lemasnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103495,"Tepak sireh berbenang emas,","Cembul tembaga disusun rapi;","Adat berkasih terasa cemas,","Bila curiga merusuk hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103496,"Pokok bidara sipohon kemboja,","Kerap dibaja oleh si nyonya;","Walau sutera kerap dipuja,","Belacu juga banyak jasanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103497,"Pakai baju bunga  bunga,","Pergi ke kedai mak Siti;","Pakai sampin terpinga- pinga,","Di ikat rapi sepenuh hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103498,"Daun pegaga tumbuh melata,","Bunga mawar mekar didahan;","Sungguh cantik di pandang mata,","Batik sutera jadi pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103499,"Kain sutera baju kebaya,","Baju tidur baju kelawar;","Hendak bertemu apakan daya,","Hanya suara menjadi penawar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103500,"Kain cindai ditenun bersulam,","Benang selembar jahitan berawan;","Datang siang menggantikan malam,","Semoga panas dinaungi awan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103501,"Kain cindai ditenun bersulam,","Benang selembar jahitan berawan;","Datang siang menggantikan malam,","Semoga panas dinaungi awan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103502,"Kain batik si kain sarung,","Lalu dicetak ditengah laman;","Wajah cantik mengapa sombong,","Itu semua sekadar pinjaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103503,"Pergi ke pekan berkain batik,","Ingin mencari sutera karisma;","Telah keudara sajian klasik,","Bertemankan sicantik molek dj anisma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103504,"Batik dipakai zaman dulu,","Pergi bawak kerumah nyonya;","Cantik dipakai hari minggu,","Kain belacu murah harganya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103505,"Sinar bulan sangatlah indah,","Mendayu pungguk di pohon ara;","Semasa berkenalan memberi hadiah,","Sepersalinan baju kain sutera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103506,"Tidur nyenyak di awan biru,","Burung jentayu berangan membayang;","Biar pun banyak pakaian baru,","Baju Melayu tetap kusayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103507,"Di senja kala menjolok setangkai,","Buah delima sungguh lebat;","Sekali-sekala bila memakai,","Baju panglima terasa hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103508,"Tenun Pahang warisan budaya,","Buatan dari tirai besi,","Songket sutera sangat berharga,","Batik juga pilihan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103509,"Songket tenunan si anak dara,","Dibuat samping teruna idaman;","Walau berkelana ke luar negara,","Hati tertinggal di kampung laman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103510,"Mudik perahu selagi berdaya,","Lubuk sebarau letaknya di hulu;","Kain songket pakaian budaya,","Seni tenunan sejak dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103511,"Ziana zain Penyanyi pujaan,","Cantik berseri bila berkebaya;","Pakaian melayu jangan dilupakan,","Berbaju telok belanga tampak bergaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103512,"Bersampin songket berbaju melayu,","Sutera cina dibuat cheongsam;","Bersatu padu tidak mudah layu,","Teguh agama beradat resam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103513,"Halus tenunan songket Terengganu,","Pakai bersama selendang sutera;","Hidup di dunia tuntutlah ilmu,","Buat bekalan dihari tua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103514,"Timur barat selatan utara,","Di lembah sana tempat teratai;","Memang mahal kain sutera,","Alah membeli menang memakai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103515,"Jauh belayar naik bahtera,","Dipukul ribut bahteta bergoyang;","Indah sungguh kain sutera,","Kain pelikat juga dipakai orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103516,"Sampan kolek menuju seberang,","Pendayung patah sampan tak sampai;","Biar sutera dipandang orang,","Belacu pula tetap dipakai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103517,"Berbaju batik kebaya nyonya,","Dipakai orang zaman dahulu;","Apa khabar sahabatku semuanya,","Moga sihat sihat dan ceria selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103518,"Bahtera berlayar menuju utara,","Pulau Angsa tempat singghan;","Betapa mahal kain sutera,","Belacu juga buat basahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103519,"Becok murai diatas dahan,","Mencari kawan terbang bersama;","Kebaya nyonya berlenggang di jalan,","Hati terpikat anak dara siapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103520,"Di dalam bendang subur kemahang,","Tempat bergabung si ikan tilan;","Kain sebidang tenunan Pahang,","Gadis kelubung lawa berjalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103521,"Buah teratai dijual bertangkai,","Singgah Batu Gajah untuk bersantai;","Bior buruk kain kita pakai,","Boleh dibuat pengelapnya lantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103522,"Jalan-jalan ke Ulu Tiram,","Hati teringin nak beli jeruk;","Kalau hujan biarlah malam,","Bila dingin ada yang peluk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103523,"Gajah berlari di Kuala Gandah,","Sambil makan buah kana;","Usah dilayan hati yang gundah,","Kelak jiwa jadi merana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103524,"Dari Klang ke Pulau Pinang,","Cari rezeki diusia muda;","Hati teringat airmata bergenang,","Kenangkan pengorbanan ayah dan bonda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103525,"Anak wak-wak ditangkap diikat,","Dibuat mainan ditimang dilambung;","Di Miri, Sarawak hatiku terpikat,","Dengan keturunan Puteri Santubong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103526,"Angin sepoi di waktu petang,","Elok berjalan ambil udara;","Hatiku rindu malam dan siang,","Bilalah bulan jatuh ke riba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103527,"Di Batu Gajah singgah bertamu,","Sempat berkelah di Pantai Segari;","Menjadi adat orang berilmu,","Ibarat lilin membakarnya diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103528,"Sungguh cantik Pulau Tioman,","Lautnya jernih pantainya indah;","Ucapan selamat pagi buat semua teman,","Semoga hari ini semuanya indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103529,"Waktu petang berkeliaran kerbau,","Ditarik paksa masuk kandangnya;","Jika sampai di Lubuk Merbau,","Nikmatilah laksa yang enak rasanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103530,"Batu Gajah sebuah pekan,","Sungai Kinta merentang panjang;","Jika hidup tiada menyenangkan,","Jangan kita menyusahkan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103531,"Bandar bersejarah kota Melaka,","Dekat pelabuhan ada perahu;","Jangan marah berpaling muka,","Baik simpan orang tak tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103532,"Dari Rajang hendak ke Bintulu,","Singgah sebentar di Tanjung Manis;","Bila terkenang kesah yang lalu,","Resah hiba hati menangis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103533,"Pusu Tiga kampung saya,","Tempat aman dan damai;","Saya terkenal tapi tidak kaya,","Allah sayang kita bukan dinilai oleh orang ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103534,"Pulau Pinang pulau Mutiara,","Kenangan berdua hati terusik;","Moga tenang sihat sejahtera,","Buat semua kerabat klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103535,"Kalau pergi ke Kuala Kedah,","Makan laksa di Telok Kechai;","Bercinta sudah bertunang sudah,","Tunggu pinangan bilakan sampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103536,"Air Pak Abas di Alor Gajah,","Bacang, Semabok nama diberikan;","Asam pedas Melaka terkenal lah sudah,","Belacan, cencalok jadi pujaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103537,"Hatiku rabak koyak berlubang,","Dijahitkan dengan benang sutera;","Beralun ombak di Rantau Abang,","Menggamit kenangan dibelai asmara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103538,"Dari Johor pergi utara,","ggah di Melaka beli berangan;","Apakah ubat hati yang lara,","Pastinya radio klasik mengamit kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103539,"Berkayak berakit laluan berjeram,","Sungai Tembeling situ tempatnya;","Bersusah bersakit hidupnya resam,","Usaha di gembeling mampu mengubahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103540,"Teluk Cempedak airnya biru,","Taman Gelora tempat rekreasi;","Seribu tahun tetap ku tunggu,","Tidak akan berubah hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103541,"Kota Lama diberi nama,","Tempat berteduh tempat riadah;","Biar hidup berkerjasama,","Agar kerja menjadi mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103542,"Cinta berpaut dipantai merdeka,","Ke Tanjung dawai membeli belah;","Andai hidup asyik berleka,","Rezeki tk kan datang dngan mudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103543,"Kapal Dagang dari seberang,","Sarat muatan kayu meranti;","Dari jauh tuan/puan datang,","Orang Melaka sedia menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103544,"Pergi Berkelah di Pantai Melawi,","Singgah sebentar di pekan Jelawat;","Sematkan hati semangat pertiwi,","Biar perpaduan jadi hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103545,"Dari Temerloh pergi ke Rasah,","Jangan lupa membeli tali;","Apa di keluh apa di kesah,","Orang yang tidak ambil peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103546,"Pohon pauh orang tebangkan,","Menimpa beranda tercabut paku;","Permatang Pauh takkan kulupakan,","Kerana Zaleda mencuri hatiku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103547,"Negeri Pahang banyak hutan,","Bandar Temerloh nama diberi;","Patin tempoyak jadi sebutan,","Mertua lalu tak sedar diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103548,"Pulau Pandan sederhana besar,","Sungguh cantiknya dipandang mata;","Di Bukit Chandan Kuala Kangsar,","Di situ tempatnya kujatuh cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103549,"Di Kuala Kurau mengail semilang,","Bila dapat masak berlada;","Hilang kemarau rindu menghilang,","Bila dapat isteri nan muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103550,"Sayang Sarawak sungainya sempit,","Buah kepayang lambung-lambungan;","Hendak kubawak perahuku sempit,","Tinggal lah sayang tinggal junjungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103551,"Johor Lama kota yang penting,","Ramai pedagang berniaga di situ;","Hidup umpama aur dan tebing,","Bekerjasama saling membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103552,"Tanjung Katung airnya biru,","Tempat mandi nak dara jelita;","Hasrat tergantung kerana cemburu,","Si dia tak sudi menjalin cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103553,"Dulu Teluk Anson kini Teluk Intan,","Menara condong mercu tandanya;","Kaseh sayang tak boleh dipisahkan,","Ibarat lagu dengan iramanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103554,"Banyak gua di Langkawi indah,","Jadi kenangan sepanjang hayat;","Sentiasa berdoa kepada Allah,","Semuga di kabulkan segala hajat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103555,"Beli selasih di Cangkat Duku,","Untuk dibawa ke Gelang Patah;","Apa lah malang nasib diri ku,","Sampai ke tua menoreh getah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103556,"Makan Pinang di tepi tasik,","Sambil melihat ikan berenang;","Tanjung kembang tempat yang menarik,","Di situlah akan berlangsungnya pesta layang-layang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103557,"Layah geroda mara jentayu,","terbang selesa tuju Belaga;","madah bunda bahasa Melayu,","keagungan bangsa nilai berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103558,"Indah sungguh Pantai Merdeka,","Riuh rendah penggunjung datang;","Jangan dilayan hati yang duka,","Ayuh bersantai di angin petang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103559,"Seri Mersing lagu melayu","Dinyanyikan oleh biduan lama;","Hatiku runsing bertambah sayu,","Kerana tak boleh nak hidup bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103560,"Cemara pantai cantik teratur,","Teluk Bidara nama pantainya;","Leka bersantai bermain catur,","Tentulah dara benci padanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103561,"Gunung Kinabalu dekat Kundasang,","Sungguh indah bila dipandang;","Siang malam terbayang-bayang,","Teringat adinda bilakah datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103562,"Kalau tuan ke Tanjung Kenari,","Kirim kan saya si buah manggis;","Belum berjalan sudah berlari,","Bila jatuh kenapa menangis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103563,"Mari berkelah ke Pulau Segari,","Sambil berkelah mencari kerrang;","Andai hidup meninggi diri,","Kelak nanti dibenci orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103564,"Dari Melaka pergi ke Jabur,","Singgah di taman tepian tasik;","Rindu siang bawa berhibur,","Berteman lagu di Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103565,"Pulau Pangkor indah berseri,","Tempat kita bermandi-manda;","Kalau ingin wajah berseri,","Hati yg baik semat di dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103566,"Pulau Pisang benua selatan,","Di pantai Pontian nampak terlihat;","Sebarkan salam eratkan persaudaraan,","Hubungan mersa hidup muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103567,"Tiupan angin dingin terasa,","Pohon nyiur melambai lambai;","Rindu di hati dapat dirasa,","Bila melihat ombak di pantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103568,"Pergi bercuiti ke Kuala Kemaman,","Singgah sebentar di Tanah Merah,","Mulakan pagi dengan senyuman,","Semoga rezeki bertambah murah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103569,"Tidur mengereng sambil beradu,","Diserang selesema angkara langsat;","Pisang goreng penawar rindu,","Saujana Utama gerai berpusat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103570,"Berombak Sipadan ke pantai memintal,","Lembayong senja membuai kenangan;","Rindu tuan alas berbantal,","Rindu hamba beralas lengang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103571,"Teluk Senangin pandang tak jemu,","Berulang kali singgah dan mandi;","Rasa teringin hendak bertemu,","Sayang sekali dia tak sudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103572,"Mulakan senyuman di awal pagi,","Hindarkan gundah dan gulana;","Marilah singgah dikota Tinggi,","Banyak sejarah ada dimana-mana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103573,"Ke Pekan Sari membeli-belah,","Tanjong Tuan tempat di tuju;","Rasa rindu hamba menjengah,","Mengapa tuan buat tak tahu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103574,"Dari Batu Pahat nak ke Kuala Lumpur,","Tengah jalan orang berpusu-pusu;","Malam hari tak dapat tidur,","Asyik merindu senyum manismu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103575,"Tajuk Johor namanya lagu,","Johor Bahru ibu negeri;","Andai tidur teringat ibu,","Tanda rindu tiada bertepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103576,"Tanjung Kling terkenal sudah,","Mandi Safar ketika dulu;","Hati sedih terasa gunda,","Mengenang masa terus berlalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103577,"Jika singgah ke Kuala Terengganu,","Boleh melihat penyu bertelur;","Buat baik janganlah jemu,","Berkat lagi jika selalu jujur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103578,"Ikan buntal tiada bersengat,","Timbul tenggelam hanyut ke tepi;","Tepuk bantal panggil semangat,","Semangat datang ddalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103579,"Pulau Tioman cantik menawan,","Tempat berkelah di hujung minggu;","Hati merindu tidak tertahan,","Aku di sini setia menuggu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103580,"Pergi bercuti di Kuala Perlis,","Sambil mkan mangga harum manis;","Aduhai si buah hati ku berada di negeri Perlis,","Orangnya hitam manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103581,"Tanjung Bidara pantainya indah,","Tepi laluan dara jelita;","Jiwa gelora bertambah gundah,","Bila tuan jauh di mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103582,"Pergi ke pekan membeli batik,","Kain pun cantik warna pun cerah;","Setiasalah kita berbuat baik,","Banyak sahabat rezeki bertambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103583,"Tanjung Malim lokasi indah,","Bangun pagi sejuk menggigil;","Jika kelapangan marilah singgah,","Mandi-manda di Sungai Bil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103584,"Bermandi-manda di Telok Bayu,","Bersuka ria anak-anak muda;","Hatiku dilanda rindu padamu,","Mengapa sepi tanpa berita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103585,"Negeri Sembilan ada Sri Menanti,","Sarawak pula ada nama Miri;","Janji cintanya sehidup semati,","Tetapi mati aja cari pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103586,"Pantai lido tempat berkelah,","Santai bersama keluarga dan taulan;","Usah kita berkeluh kesah,","Kehidupan pasti ada rintangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103587,"Pergi ke pasar membeli kentang,","Pagi hening adik berbuai;","Sungai Besar sawah terbentang,","Padi menguning sedang dituai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103588,"Pulau tioman tempat berehat,","Ke mana-mana senang berjalan;","Andai hidup mengejar akhirat,","Buang keburukan tingkatkan amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103589,"Pantai lido airnya biru,","Tempat mandi nak dara jelita;","Sedang sekampung lagi dirindu,","Inikan pula hai jauh dimata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103590,"Tanjung Aru tempat bertemu,","Anging bertiup di pohon sena;","Apa ubat hati merindu,","Siang malam tidur tak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103591,"Tangan menepuk berulang-ulang,","Mengikut rentak tariannya tari;","Jangan menepuk ayor di dulang,","Kelak terpercik ke muka sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103592,"Di atas pohon burung bersarang,","Burung terbang di ruang angkasa;","Dalam kita ada terlarang,","Jangan sesekali membuat dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103593,"Burung terbang di ruang angkasa,","Jatuh ke laut lalu tenggelam;","Jangan sesekali membuat dosa,","Wahai umat beragama islam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103594,"Dari mana hendak ke mana,","Dapatnya sagu dipecahkan ruyung;","Jangan ditiru si ayam betina,","Bertelur sebiji riuhnya se kampung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103595,"Burung kenari bersiul bernyanyi,","Angin berbisik bertiup ke taman;","Bila diri terasa sunyi,","Radio Klasik jadi peneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103596,"Angin berbisik bertiup ke taman,","Bunyinya bersantun menjalin muhibah;","Radio Klasik jadi peneman,","Dapat berpantun bersama juruhebah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103597,"Bunyinya bersantun menjalin muhibah,","Disaksikan awan putih berseri;","Dapat berpantun bersama juruhebah,","Juga kawan-kawan yang bijak bistari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103598,"Entah mengapa padiku ini,","Makin kulurut basah-basahan;","Entah mengapa hatiku ini,","Makin kuturut tiada kesudahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103599,"Makin kulurut basah-basahan,","Kucampak ke pangkal pohon jati;","Makin kuturut tiada kesudahan,","Hendak kutinggal tak sampai hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103600,"Asal padi menjadi beras,","Dari beras menjadi nasi;","Usah dikenang perkara yg lepas,","Kerana sudah menjadi basi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103601,"Pergi ke ladang rumput ditajak,","Sirih ditupang naik ke junjung;","Di mana bumi kita berpijak,","Di situ langit kita junjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103602,"Buah cempedak tumbuhnya rendang,","Ambil galah hendak menjolok;","Apa didengor riuhnya enggang,","Kita makan buah belolok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103603,"Padi melukut tumpah ditambak,","Cantik menguntum si bunga teratai;","Kalau takut dihempas ombak,","Jangan berumah di tepinya pantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103604,"Lautan tenang dipukul ribut,","Tersangkut dedahan dipangkal mengkudu;","Hati terkenang mulut tersebut,","Tak tertahan rasa rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103605,"Berarak arak mendung kelabu.","Berarak pergi hujan tk jadi;","Teragak agak buat sesuatu,","Buat rapi pasti menjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103606,"Sayur bayam asin-asin,","Sayur kailan tepi berangan;","Jangan jadi macam mat jenin,","Hidup dalam angan-angan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103607,"Sayur kailan tepi berangan,","Dalam taman bunga melati;","Hidup dalam angan angan,","Semua kerja tak menjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103608,"Cincin bertunang bertahta swasa,","Buat dara gadis impian;","Bersopan santun berbudi bahasa,","Jejaka menjeling jadi kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103609,"Penuh berisi buah delima,","Sama dibawa buah berangan;","Terhias peribadi berabad lama,","Pesanan moyang dilupa jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103610,"Sama dibawa buah berangan,","Buah tangan dari sempadan;","Pesanan moyang dilupa jangan,","Jadikan pedoman menharungi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103611,"Apa guna menanam ubi,","Nenas datang dari seberang;","Puas sudah ku beri budi,","Emas juga dipilih orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103612,"Puas sudah ku beri budi,","Emas juga dipilih orang;","Ikutlah rasmi nya padi,","Walau tunduk isi tak kurang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103613,"Kain baru kain cukin,","Bawa mari kesat dulang;","Sudah buruk lagi miskin,","Mana peduli pada orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103614,"Sama dibawa buah berangan,","Dalam kapal sarat papan;","Pesan dan nasihat dilupa jangan,","Sudah budaya beradab sopan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103615,"Penuh berisi buah delima,","Masak ranum di atas peti;","Jangan ditinggal rukun yg lima,","Sematkan selalu di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103616,"Harum mekar kembang setaman,","Mekar pula dihujung laman;","Lebihkan manis dalam senyuman,","Kurangkan manis dalam minuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103617,"Buah delima merah warna isinya,","Mari dimakan bersama dinda;","Hidup di dunia hanya sementara,","Budi baik sentiasa dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103618,"Mari dimakan bersama dinda,","Gelak ketawa sambil bercanda;","Budi baik sentiasa dijaga,","Hormat menghormati sesama kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103619,"Sempaya kota rimbun meredup,","Daun diruntun terkulai mati;","Budaya kita amalan hidup,","Bersopan santun baik pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103620,"Daun diruntun terkulai mati,","Tanam halia sisi kelisa;","Bersopan santun baik pekerti,","Sifat mulia warga berbangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103621,"Cantik ayu anak Mak Siti,","Sudah dipinang jejaka budiman;","Sungguh sayu rasa di hati,","Bila terkenang kampung halaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103622,"Sudah dipinang jejaka budiman,","Orangnya gagah mulia berada;","Bila terkenang kampung halaman,","Terbayang wajah ayahanda bonda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103623,"Orangnya gagah mulia berada,","Tinggal di kota disayangi jiran;","Terbayang wajah ayahanda bonda,","Air mata jatuh berguguran.", 4, 6);
