INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103695,"Sarang tumpangan burung merpati,","Selasih ada ditepi sawah;","Sekarang Kekanda baru mengerti,","Kasih Adinda sudah berubah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103696,"Dari Melaka pergi ke Pahang,","Singgah di Johor beli berangan;","Kami mengucap selamat datang","Apa yang kurang dikeji jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103697,"Makan sirih berpinang tidak,","Di makan oleh puteri jauhari;","Walau kaseh berbalas tidak,","Pada mu lah juga tertambat hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103698,"Bunga tanjung putih berseri,","Kembang jelita harum baunya;","Gagah sungguh pohonmu berdiri,","Tegap rimbun gagah perkasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103699,"Mengharap sinar menelan kabus,","Ubat jiwa penawar luka;","Dapatkah hati ini menebus,","Dosa insan sejenis hamba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103700,"Kusangka aur di pinggir tebing,","Kiranya tebu di pinggir bibir;","Kusangka jujur pancaran batin,","Rupanya palsu penghias zahir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103701,"Turun ke sawah menanam padi,","Hendak dijual ke pekan lama;","Jangan selalu menanggung budi,","Keraplah kali jadi bencana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103702,"Tanya sama pokok,","Apa sebab goyang.","Nanti jawab pokok,","Angin yang goncang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103703,"Sopan santun anak melayu,","Senyum terukir bila disapa;","Dalam pantun ku kirim rindu,","Rindu mengalir didalam kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103704,"Lama sudah tidak ke ladang,","Habislah padi dililit kangkong;","Lama sudah tidak kupandang,","Hati meragu si dengan jantung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103705,"Kain buruk jatuh ke papan,","Patah galah dahan meranti;","Biar pun buruk wajah mu tuan,","Asalkan pandai mengambil hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103706,"Harum baumu si bungalah tanjung,","Harumnya sampai melintasi gunung;","Tuan kuharap umpamakan payung,","Hujan dan panas tempat berlindung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103707,"Selat teduh lautan tenang","Banyak labuh perahu Aceh","Jangan kesal jangan kenang","Walau hati rasa pedih mengapa bersedih", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103708,"Sirih dikapur berserta pinang,","Untuklah makanan bujang kelana;","Orang tak ada jangan dikenang,","Betalah datang membawa bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103709,"Anak Cina menjual tahu,","Tahu dijual bertempat-tempat;","Kalau cik adik ingin nak tahu,","Itulah dia renung makrifat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103710,"Sepetang di pinggir bukit,","Anak dagang di perantauan;","Terkenang adik bukan, bukannya sedikit,","Nasi terhidang tidak tertelan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103711,"Ku kira hati jiwa Nurani,","Suci seindah wajah terbayang;","Ku kira puji seikhlas budi,","Ku lupa lidah tidak bertulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103712,"Ku sangka aur di pingir tebing,","Kiranya tebu di pinggir bibir;","Ku sangka jujur pancaran batin,","Rupanya palsu penghias zahir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103713,"Anak punai anak merbah,","Terbang turun buat sarang;","Anak sungai pun berubah,","Inikan pula hati orang mengapa di kenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103714,"Harum jelita si bunga suci,","Idaman dara gadis rupawan;","Sangat sombong nya bunga menari,","Melenggang lengguk di hujung dahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103715,"Burung merpati terbang seribu,","Hinggap seekor di tengah laman;","Hendak mati di hujung kuku,","Hendak berkubur di tapak tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103716,"Apalah guna ke bendang,","Padi seberang takkan menjadi;","Apalah diharap sayang,","Kasihnya orang, orang tak sudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103717,"Kalau pergi Kuala Kubu,","Tulis nama atas batu;","Saban hari ku menanti waktu,","Pulanglah segera kepada ku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103718,"Diriku insan biasa,","Wajah tampan aku tak punya;","Harta juga aku tiada,","Cuma lah aku menyintai sepenuh jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103719,"Tenang-tenang air di laut;","Hai sampan kolek mudik ke tanjung:","Hati terkenang mulut menyebut,","Budi yang baik saya nak junjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103720,"Cindailah mana tidak berkias,","Jalinnya lagu rentak beribu;","Bagailah mana hendak berhias,","Cermin ku retak seribu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103721,"Tanam pinang rapatlah rapat,","Biar senang puyuh berlari;","Kalau kupinang pinang tak dapat,","Kupujuk-pujuk kubawak lari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103722,"Dari Melaka ke Pahang,","Singgah di Johor beli berangan;","Kami mengucap selamat datang,","Apa yang kurang dicaci jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103723,"Gugur bunga kembang bermadu,","Hilang seri wajah jelita;","Pada siapa hendak mengadu,","Kekasih hati jauh dimata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103724,"Kalau tuan mudik kehulu,","Carikan saya bunga kemboja;","Kalau tuan mati dahulu,","Nanti saya di pintu syurga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103725,"Nyiur gading puncak mahligai,","Buat ketupat berisi inti;","Hancur daging tulang berkecai,","Kasih sayang sampai ke mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103726,"Jangan tergoda bintang di awan,","Kalau terbit nya disiang hari;","Jinak merpati makan ditangan,","Jangan dikurung disangkar hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103727,"Anak nelayan naik perahu,","Sedang berdayung hujan gerimis;","Hancur hati orang tak tahu,","Mulut tertawa hati menangis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103728,"Berenang renang di muara,","Hati hati dilambung ombak;","Dah berzaman asyik bercinta,","Entahkan jadi entahkan tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103729,"Pohon selasih cabang kebendang,","Bunga berangan berputik dirawa;","Radio klasik sering berkumandang,","Membuat kenangan mengusik jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103730,"Sambil berdoa kepada Tuhan,","Mentari bersinar diwaktu pagi;","Seribu langkah tetap bertahan,","Demi Kesihatan tak berbelah bagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103731,"Hari Jumaat seminggu sekali,","Jangan lupa solat Jumaat;","Sihatkan diri setiap hari,","Diri sihat keluarga manfaat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103732,"Perah Nyior santan mewangi,","Kuntum bunga sikembang pagi;","Cerah bebenor sinaran mentari,","Assalammualaikum semua selamat pagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103733,"Tengok bunga mekar di taman,","Tunggu mentari meninggi hari;","Mulut ternganga udara nyaman,","Waktu pagi bersenam berlari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103734,"Ombak bertalu memukul pantai,","Indah nya suasana dikala senja;","Kenapa tuan perlu bersantai,","Jendela dirumah masih terbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103735,"Pergi ke kedai di waktu pagi,","Kedai terletak di hujung kampung;","Bila dah tua hilang gigi,","Bila nak cakap mulut melopong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103736,"Jatuh terjerumus ke dalam lumpur,","Membawa jamu di dalam gayung;","Bertungkus lumus memasak di dapur,","Ramai tetamu datang berkunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103737,"Warna merah sibiji saga,","Tumbuh subur hujung halaman;","Selesai dulu urusan keluarga,","Kekebun kita melihat tanaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103738,"Tanah sebidang dibahagi enam,","Tanah Tok Penghulu dari Kemasik;","Sebelum ke ladang bercucuk tanam,","Berpantun dahulu di Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103739,"Jalan jalan cari makan,","Singgah sebentar ditepi tasik;","Jom kita bersarapan,","Sambil layan sajian klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103740,"Udang sama udang,","Kepah juga di tepi pantai;","Bujang sama bujang,","Hari minggu kita bersantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103741,"Kaki kebas lenguh di tangan,","Jalan terketar melangkahi papan;","Sambil menebas merumput di laman,","Berhenti sebentar menyantuni sarapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103742,"Mawar, kenanga, kekwa, kiambang,","Mata memerhati bersalut khayalan;","Merenung bunga mekar berkembang,","Sambil menikmati burung bersiulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103743,"Ambil air dari perigi,","Minum kopi besarapan kentang;","Inginku ucap Selamat pagi,","Semoga hari cerah sampai kepetang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103744,"Di dalam telaga bunga teratai,","Dibuang ke tasik tumbuh subur;","Bersama keluarga berehat santai,","Radio Klasik berkumandang menghibur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103745,"Berjalan-jalan ditepian tasik,","Amat nyaman tetapi sepi;","Santai mendengar sajian klasik,","Sambil melayan secawan kopi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103746,"Di atas meja tersedia bubur,","Pergi ke tasik buat bekalan;","Kami bekerja sambil berhibur,","Radio Klasik menjadi pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103747,"Jalan Jalan di kotaraya,","Ramai peniaga disini sana;","Santai pagi minum bersama,","Radio klasik jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103748,"Pergi ke hilir melihat sampan,","Sampan diikat ditepi tasik;","Moh kawan-kawan kita sarapan,","Sambil mendengar Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103749,"Udang siput ketam dan kupang,","Bawa balik bersama umpan;","Orang pencen waktunya lapang,","Membelai tanaman selepas sarapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103750,"Cantik mekar bunga teratai,","Mekar kelompok dihujung taman;","Sambil mengemas sambil santai,","Radio Klasik setia meneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103751,"Berakit rakit menuju ke seberang,","Bawa bersama jala dan joran;","Seawal pagi hingga ke petang,","Tugas mengawal rutin harian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103752,"Buah kelapa atas beranda,","Masak sebiji gulai ikan;","Selalu berniaga rehatkan minda,","Seminggu sekali jom ke pekan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103753,"Anak kerbau mati tertambat,","Anak itik tertelan kentang;","Selamat pagi wahai sahabat,","Semoga ceria hingga ke petang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103754,"Di hujung desa petai bersusun,","Singgah berhenti bertanya lagi;","Hari Selasa santai di dusun,","Sambil nikmati serinya pagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103755,"Rimbun kelisa rebah diruntun,","Buah keranji letak di para;","Budi bahasa sopan dan santun,","Tingkah terpuji menyerlah aura.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103756,"Sedap sungguh kuih talam,","Rambut ikal di pintal-pintal;","Hari ini kak Ana ke Shah Alam,","Ziarah kak Aya di hospital.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103757,"Ayam masak santan berlada,","Beri sedikit pada besan;","Bangun pagi air tiada,","Tergendala habis segala urusan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103758,"Daun keladi tumbuh kemetot,","Buat sayuran gulai ikan;","Bangun pagi meregang otot,","Ubat jangan lupa dimakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103759,"Mengelilingi tasik gadis bertiga,","Tersenyum melerek rapat bergandingan;","Radio Klasik teman berniaga,","Menjadi penarik mengganda keuntungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103760,"Dari pagi ke senja,","Dari bulan ke tahun;","Daripada duduk-duduk sahaja,","Baik kita pergi ke kebun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103761,"Bakor setanggi hidupkan api,","Kukor nyior buat kerisik;","Sarapan pagi se cangkir kopi,","Sambil mendengor Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103762,"Ramai berpesta di sana sini,","Riuh rendah di tengah laman;","Ke pekan selasa di pagi ini,","Untuk membeli ulam-ulaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103763,"Tenang tenang air di paya,","Rumpun keladi tumbuh di tepian;","Saban hari menghadapi kerjaya,","Kerana ia sebahagian penghidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103764,"Anak imam bapa pun imam,","Rumah berpagar ditepi tasik;","Nak demam tak jadi demam,","Bila mendengar Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103765,"Pergi ke Ipoh membeli papan,","Berhenti diUlu Yam rehatkan diri;","Pagi2 jom kita sarapan.","Capati, kari ayam buatan sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103766,"Basuh baju tepi telaga,","Air disimpan dalam tempayan;","Berniaga niaga juga,","Radio klasik, tetap dilayan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103767,"Minum pagi secawan kopi,","Hidang pula nasi lemak;","Apa khabar kawan sejati,","Sayang tak luak kasih melimpah ruak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103768,"Haram tanaman pokok popi,","Jangan di tanam tepi halaman;","Cuti berehat minum kopi,","Radio Klasik jadi peneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103769,"Surung papan tarik papan,","Hedak dibawa ditepi taman;","Ditepi meja menjamah sarapan,","Radio klasik jadi peneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103770,"Cili ditanam tinggi se inci,","Membeli papan diPekan Sik;","Setelah bersenam Dan bertaici,","Kita sarapan berteman Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103771,"Nyior dikukor menggulai kari,","Hendak menjamu Encik Zaki;","Mengagah tekukor di pagi hari,","Kucing menggesel di celah kaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103772,"Dari Chini tuju Mempaga,","Rehat di taman di tepi tasik;","Hari ini cuti niaga,","Santai berteman Sajian Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103773,"Meriah malam pengantin berinai,","Berarak dengan menaiki rakit;","Tak kurang baiknya anai-anai,","Sikit-sikit lama-lama jadi bukit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103774,"Pak Ali pergi menjaja,","Menjual kuih pau sekaya;","Contohi semut membuat kerja,","Walaupun lambat akhirnya berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103775,"Seperti kelekatu masuk ke api,","Tiada tahu mati akhirnya;","Ibarat asmaradana cinta diimpi,","Tiada menghirau akan padahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103776,"Jangan kedekut berbuat pahala,","Tanpa pahala akhirat tersiksa;","Mati semut kerana gula,","Mati manusia kerana kuasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103777,"Kelihatan bukit di sebalik awan,","Cuaca cerah berolah raga;","Pepijat mengigit keseluruh badan,","Kebersihan rumah harus di jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103778,"Mencari rotan rimba di lembah,","Lebah bersarang di pokok Talang;","Bagai meminang anak labah labah,","Tiada sepadan bukan kepalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103779,"Orang mengamuk kerana bertengkar,","Habis barang semua dihumban;","Marahkan nyamuk kelambu dibakar,","Marahkan manusia silaturrahim diputuskan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103780,"Sekawan lebah menjadi musuh,","Sedang madunya ubat berharga;","Kita berkawan jngan bermusuh,","Setiap tutur harus dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103781,"Pohon jambu banyak kerengga,","Buah mempelam juga kiwi;","Mewah dan kaya jangan dibangga,","Sekadar sandaran hidup duniawi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103782,"Hinggap kumbang dekat pelita,","Tunggul buruk sarangnya kelulut;","Enak membawang menyampai berita,","Pandai pandailah menjaga mulut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103783,"Sebatang sungai deras airnya,","Semut meniti diatas dahan;","Baik budi tinggi pekertinya,","Usaha itu tangga kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103784,"Si anak dara sinis mengadu,","Tangkap di kamar lipan berbisa;","Lunak bicara manis bermadu,","Jerangkap samar mengumpan mangsa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103785,"Hinggap merbah di pohon mengkudu,","Melepas lelah sambil berehat;","Mulut lebah membawa madu,","Bontotnya pula membawa sengat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103786,"Hendak sihat makanlah madu,","Sakitnya disengat serangga lebah;","Nak tahu bagaimana hebatnya bermadu,","Cuba dibawa pulang ke rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103787,"Burung kelicap menghisap madu,","Kelicap mengelak diganggu merbah;","Apa digeruni jika bermadu,","Kalau dah retak menantinya belah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103788,"Semut kerengga bersarang rendah,","Semut merah mati diracuni;","Hebat khasiat si madu lebah,","Hidup bermadu memang digeruni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103789,"Anai-anai membina busut,","Memakan kayu sehingga rongga;","Rusuh teramat kepala kusut;","Berhati sayu duduk di tangga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103790,"Orang tak betul dikatakan gila,","Tiada terurus badan kelesa;","Matinya semut dik kerana gula,","Matinya manusia dah sampai masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103791,"Tepi perigi sarang tebuan,","Di pokok beruas menghadap mentari;","Hebat dan tinggi kasihmu tuan,","Tiada terbalas jasa yang diberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103792,"Lebah bukan sebarang lebah,","Lebah bersarang dipohon tualang;","Kasih sejati takkan berubah,","Biar apa pun datang menghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103793,"Pintu dibuka si anak gadis,","Ternampak peti berisi jala;","Semut suka makanan yang manis,","Akhirnya mati kerana gula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103794,"Belalang kunyit terbang di padang,","Melompat-lompat jika ditangkap;","Hidupan serangga cantik dipandang,","Indahnya alam menjadi pelengkap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103795,"Merayap kerengga di tanah rata,","Lelabah bersarang di celah batu;","Usah curiga dalam bercinta,","Dulu sekarang hanya kau satu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103796,"Intung api hendak memanggang,","Hendak memanggang siburung kedidi;","Jangan seperti serangga pianggang,","Baunya busuk merosakkan padi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103797,"Pergi ke bendang jalan bersama,","Membawa bakul berisi kuini;","Cantik warnamu si rama rama,","Terbang berkawan ke sana sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103798,"Encik Yaakob membeli lada,","Di bandar Labuan tiada cencalok;","Kalau tak cukup ilmu didada,","Sarang tebuan jangan dijolok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103799,"Si rama-rama warnanya cantik,","Terbang hinggap di sarang merpati;","Bunga di taman sudah di petik,","Menjadi hiasan di mahligai nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103800,"Masuk ke hutan banyak semut,","Dalam gua membuat sarang;","Budi yg baik disanjung disebut,","Itulah resam menjadi orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103801,"Burung merbah terbang ke seberang,","Sayu hatinya membawa diri;","Penat lebah membuat sarang,","Habis madunya orang dah curi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103802,"Bawa bekal di dalam raga,","Sambil bawa atas rakit;","Sekawan lebah sangat berharga,","Kawanan lalat pula bawa penyakit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103803,"Majlis berinai penuh sesak,","Di rumah di tanah orang bersorakan;","Anai-anai makhlok perosak,","Mesti dimusnah mesti dihapuskan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103804,"Tepi permatang pepohon mengkudu,","Bertiup bayu di pagi hari;","Kumbang datang menghisap madu,","Bunga layu tidak berseri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103805,"Mawar merah di datangi kumbang,","Kumbang cantik hiasi taman;","Janganlah anda asyik bersembang,","Lalu tegurlah jiran setaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103806,"Bakar colok di tengah halaman,","Telor di sangkak jatuh ke tanah;","Jangan dijolok sarangnya tebuan,","Lain membengkak lain yang menanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103807,"Belalang makan di tepi tasik,","Terbang pepatung keliling desa;","Bersama meriahkan Pantun DKlasik,","Martabatkan pantun budaya bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103808,"Sungguh jauhnya kampung terpencil,","Tempat berkelah ramai pengunjungnya;","Lipas hanya makhlok yang kecil,","Ramai pulalah yang takutkannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103809,"Bersarapan memakan roti canai,","Masakan mamak di kedai seberang;","Kecil-kecil badan si anai-anai,","Tiang yang tegak boleh tumbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103810,"Bunga kantan lembah anai-anai,","Di pinggir kolam pohonnya rendah;","Mahalnya intan sudah dicanai,","Gua yang kelam sanggup diredah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103811,"Terbang sendirian si burung punai,","Membawa diri mencari sangkar;","Jangan kerana marahkan anai-anai,","Sebuah rumah habis dibakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103812,"Hinggap pepatung di dahan sekah,","mata beria kelihatan semut;","jangan dihitung banyak sedekah,","kelak sia-sia bagaikan lumut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103813,"Lantai berlumut jalan perlahan,","Pakai sepatu kain diangkat;","Laksana semut dalam kumpulan,","Saling bersatu sama sepakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103814,"Gunung Berlumut didaki dilawati,","Banjarannya teguh walau dah lama;","Resmi semut eloklah diikuti,","Rajin sungguh bekerjasama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103815,"Sekawan lebah membuat sarang,","Pokok tinggi sarang dibuat;","Biar diri serba berkurang,","Amal ibadat imannya kuat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103816,"Syair digubah oleh pujangga,","Bahasa terjaga sungguh mengasyikkan;","Madu lebah menambahkan tenaga,","Madu tiga eloklah dielakkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103817,"Terbang rerama indah dilihat,","Berwarna warni cantik rupanya;","Iman dan ibadat perlu dipahat,","Bila upahnya jangan ditanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103818,"Pergi ke hutan tebang meranti,","Membuak busa kayunya tumpat;","Bagai gigitan si semut api,","Demikian bisa lidah pengumpat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103819,"Semut melata penuh di bohor,","Terbang sekawan lebah berbisa;","Radio klasik pernghibur tersohor,","Halwa telinga sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103820,"Sambil bertugas mulut menyanyi,","Waktu pagi hatinya riang;","Cengkerik unggas riuh berbunyi,","Ditambah lagi suara reriang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103821,"Cuaca sejuk tidur berselimut,","Waktu subuh azan bergema;","Mari ikut rasmi semut,","Tolong menolong bekerjasama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103822,"Singgah sebentar di kedai Ah Huan,","Singgah membeli rempah kari;","Jangan di jolok sarang tebuan,","Tak menang kaki berlari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103823,"Kelip-kelip sikunang kunang,","Menghiasi di malam hari;","Orang jauh jangan dikenang,","Kelak merana lah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103824,"Turun sawah berbaju lusuh,","Bawa bekal di dalam raga;","Nak berbakti tak payah disuruh,","Dapat pahala dibawa ke syurga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103825,"Banyak kerengga di pohon mempelam,","Dari dahan merayap ke tepi kolam;","Bila bersua ucap lah salam,","Eratkan silaturahim sesama islam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103826,"Bunga digubah majlis bertunang,","Majlis di desa ramai yang datang;","Pintar lelabah menyirat benang,","Perangkap mangsa sarang direntang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103827,"Membeli akhbar di pekan Lumut,","Labu sayong dibawa ke daik;","Jadikan iktibar hebatnya semut,","Bergotong royong muafakat terbaik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103828,"Rasa melekit madu kelulut,","Kelulut hitam tapi berbisa;","Penyakit datang dari mulut,","Kerana mulut badan binasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103829,"Terbang tinggi si burung punai,","Mati seekor tersangkut pukat;","Ikutlah rasmi si anai anai,","Biar kecil hidup muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103830,"Suara merdu burung terkukur,","Di dahan ara berkicau ria;","Malaysia maju aman dan makmur,","Rakyat sejahtera hidup bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103831,"Waktu senja membakar sampah,","Sampah dibakar di perdu peria;","Walaupun kecil serangga lebah,","Tetap berbakti kepada manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103832,"Hujan lebat air bah,","Habis tenggelam perigi telaga;","Ikutilah contoh semut dan lebah,","Berusaha bersama menyara keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103833,"Terbang kumbang mencari madu,","Singgah di bunga mentari terik;","Kepada Allah tempat mengadu,","Jauhi daripada perbuatan syirik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103834,"Turun ke sawah berbaju lusuh,","Bawa bekal di dalam raga;","Sekawan lebah menjadi musuh,","Sedangkan madunya ubat berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103835,"Ikan haruan di bawah jerami;","Adik mengejar jalan bertuntun:","Minta bantuan tolonglah kami,","Menunjuk ajar mengarang pantun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103836,"Sawah nan luas menguning padi;","Tidak dijaga musuh menyerang:","Sudah puas kutabur budi,","Harta juga dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103837,"Di Tanjung Karang pasirnya putih,","Tiada pohon buat berlindung;","Jangan meradang jangan merintih,","Kepada tuhan kita berpayung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103838,"Sudah terlepas burung kedidi,","Di pangkal kuini tersepit kepala;","Minggu lepas menuai padi,","Minggu ini mengemping pula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103839,"Duduk termenung di tepi bendang,","Rindu padanya tiada terkira;","Ku hiburkan hati sambil berdendang,","Mengilangkan segala duka dan lara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103840,"Banyak saya menabur padi,","Padi habis dimakan memerang;","Banyak saya menabur budi,","Budi tidak dikenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103841,"Leka memandang si baju belang,","Di pagi hari duduk di tangga,","Ke sawah bendang membanting tulang,","Rezeki dicari buat keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103842,"Pohon padi berbuah lebat,","Padi malinja masak sekata;","Pantun DKlasik bersama martabat,","Warisan bangsa budaya kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103843,"Membeli kain berkodi-kodi,","Selepas makan diangkut pulang;","Seronok bermain di sawah padi,","Menangkap ikan puyu sembilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103844,"Tanam padi tumbuh lalang,","Gunung daik bercabang tiga;","Jangan terlupa jalan pulang,","Sudah berkelana kembali juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103845,"Tinggi rendah padi malinja,","Buahnya padat batang dikerat;","Umur sudah semakin senja,","Buat ibadat bekalan akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103846,"Kalau tuan pergi ke bentong,","Kirimkan saya asam sebalang;","Kalau sudah tidak beruntong,","Padi sebendang tumbuh lalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103847,"Pagi hari pergi kebendang,","Kaki tercucuk duri semalu;","Bagaimana hendak berdendang,","Sedang lagu sumbang selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103848,"Tinggi tinggi terbang helang,","Jauh diawan nampak berbalam;","Ku tanam padi tumbuh nya lalang,","Ngeri sungguh mimpi semalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103849,"Hijau pandangan di sawah padi,","Senja menjelma beburung pulang;","Kalau hidup selalu berbudi,","Nama disebut berulang ulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103850,"Sawah padi terbentang luas,","Ikan sepat dibawah titi;","Budi tuan tidak terbalas,","Moga tuan sentiasa dirahmati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103851,"Kunang-kunang terbang di laman,","Dari malam tadi banyak yang datang;","Asyik terkenang kampung halaman,","Sawah padi yang luas terbentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103852,"Tanah pamah curam ke bawah,","Tempat melastik itik angsa;","Di depan rumah di belakang sawah,","Sungguh cantik pemandangan desa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103853,"Padi menguning habis di tuai,","Kumpul jerami lalu di bakar;","Terasa hati di buai buai,","Kenangan lalu indah terlakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103854,"Tanah pamah curam ke bawah,","Tempat melastik itik angsa;","Di depan rumah di belakang sawah,","Sungguh cantik pemandangan desa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103855,"Padi menguning habis di tuai,","Kumpul jerami lalu di bakar;","Terasa hati di buai buai,","Kenangan lalu indah terlakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103856,"Nasi kawah tambahkan sedikit,","Sungguh enaknya orang menjamu;","Tuan di sawah saya di bukit,","Di mana agaknya hendak bertemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103857,"Tanam padi tumbuh lalang,","Gunung daik betcabang tigs;","Jangan berharap budi dikenang,","Bila berjasa akan dikenang juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103858,"Di tepi sawah padi ditampi,","Padi ditampi di dalam dulang;","Orang tak sudi teman ke tepi,","Dik sedar diri kita menumpang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103859,"Sawah padi kuninglah-emas,","Padi masak tangkai terjatuh;","Wabak terjadi janganlah cemas,","Jangan bersesak sentuh-bersentuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103860,"Indah sungguh negeri Kedah,","Ada banjaran juga bendang;","Hidup usah terlalu megah,","Nanti buruk dikenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103861,"Sudah lama tidak ke bendang,","Menyusuri titi melintasi kolam;","Sudah lama tidak kupandang,","Rindu di hati semakin mendalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103862,"Apa diharap pada jerami,","Habis padi jerami dibuang;","Apa diharap kepada kami,","Muka buruk budi kurang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103863,"Buah berembang dipatuk kedidi,","Akak Tini ceria dititi;","Usah dibimbang orang tak sudi,","Saya disini sedia menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103864,"Indah menguning padi malinja,","Tentu tak sama sipadi bukit;","Buruk orang jangan dijaja,","Buruk kita pun bukan sedikit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103865,"Terbentang luas sawah padi,","Padi huma padi meranti;","Kalau sudi katakan sudi,","Tidak hamba ternanti nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103866,"Dari Sekinchan ke Tg Karang,","Saujana pandangan sawah padi;","Andainya rupa tiada yang pandang,","Serlahkan diri amalan berbudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103867,"Ke rembulan rendah pungguk memandang,","Hatinya mengimbau godaan angau;","Sungguh indah pemandangan di bendang,","Sekawan kerbau bertemankan bangau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103868,"Indahnya purnama menyinari desa,","Di pohon cemperai pungguk memandang;","Setelah lama memendam rasa,","Cinta terburai di sawah bendang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103869,"Apa di tuai si padi huma,","Nasi ditanak retak belanga;","Lena dibuai berkasih mesra,","Hati bertauk berbunga-bunga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103870,"Indahnya irama lagu cinta,","Bagaikan menjadi ikrar sumpah;","Biar pun lama hidup di kota,","Ke sawah padi kasihku tertumpah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103871,"Kalau tuan turun kesawah,","Pakai baju biar selesa;","Kalau berkelah tepi sawah,","Nasi sepinggan tidak terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103872,"Luas hamparan padi sendiri,","Cantik menguning dihujung laman;","Biarlah ku bawa diri,","Jika tiada yang sudi berteman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103873,"Padi secupak tuai dihulu,","Tanam selasih ditengah laman;","Dari sejak semula dahulu,","Menanggung rindu tidak tertahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103874,"Waktu petang selepas asar,","Burung kedidi di atas kaldai;","Jemputlah datang ke Sungai Besar,","Melihat padi sedang dituai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103875,"Meraut lidi tepian jebak,","Bawa muatan bersama teman;","Selaut budi harum semerbak,","Jadi ingatan sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103876,"Gadis manis menampi padi,","Padi ditampi di pinggir anjung;","Puas sudah kutabur budi,","Emas ringgit juga disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103877,"Ada bendang di tanah rendah,","Di tengah bendang dibuat titi;","Sayang kasi layulah sudah,","Di mana lagi kucari ganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103878,"Petik kemuning di waktu petang,","Di dalam guni letak senduduk;","Hijau menguning sawah terbentang,","Girang petani padi merunduk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103879,"Hasil bermula namanya padi,","Selepas beras bertukar nasi;","Jaga lah diri jagalah hati budi,","Sempurna lah sepanjang hidup ini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103880,"Sawah tali air rumpun buluh betong,","Kampungku yang ceria lagi berkat;","Bulatnya air kerana pembentung,","Bulat manusia kerna muafakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103881,"Kedah negeri jelapang padi,","Luas terbentang damai memandang;","Pada tanah kita berbudi,","Hasil nya menjadi hidup pun senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103882,"Jerami kering d gulung rapi,","Bergulungan di tengah bendang;","Riuh rendah menjadi sepi,","Semua hilang tidak dapat di pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103883,"Limau bali di tepi bendang,","Kail haruan di atas titi;","Kalau dia tak sudi memandang,","Cari yang lain buat pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103884,"Luas sawah saujana memandang,","Tumbuh menghijau ditepi kali;","Senyum diukir tetamu bertandang,","Hidangkan juadah enak sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103885,"Cantik kehijauan padi rembia,","Duduk berduaan jerami di banting;","Ramai kawan rasa gembira,","Ikatan kekeluargaan amat penting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103886,"Sawah padi menghijau terbentang,","Nampak luas disulami jerebu;","Kisah silam tetap dikenang,","Berbunga mekar dalam kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103887,"Kanak-kanak berlari-lari,","Atas permatang ditepi bendang;","Hidup biarlah berbudi,","Sampai mati tetap dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103888,"Sungguh lebat padi dibendang,","Dituai tuai senang dibawa;","Buruk dan baik elok dipandang,","Tidak hati berasa kecewa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103889,"Cuaca redup menuai padi,","Padi dituai dibawa keseberang;","Biar hidup menanam budi,","Bila mati dikenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103890,"Hari hujan bendang tenggelam,","Ban pecah perlu ditanggam;","Banyak udang banyaklah garam,","Banyak orang berbagailah ragam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103891,"Padi disemai jerami segengam,","Ikan disalai di atas batu;","Kawan ramai pelbagai ragam,","Boleh dinilai satu persatu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103892,"Tepian pagar si pohon kundang,","Layu meranti hala ke randu;","Sawah lebar saujana memandang,","Sayu hati dilamun rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103893,"Jerami di sawah dibakar rentung,","Untainya padi jemur di alang;","Malangnya badan tiada beruntung,","Bertanam padi yang tumbuhnya lalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103894,"Turun ke bendang rumput ditarah,","Hujan gerimis di sawah padi;","Bior menangis airmata darah,","Apa yang terjadi telah terjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103895,"Kala gerimis sawah diredih,","Menghijau padi benih disebar;","Usah menangis meratap sedih,","Apa terjadi ambil iktibar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103896,"Padi diketam kemudian dipukul,","Tunggul padi dibiarkan kering;","Yang berat sama kita pikul,","Yang ringan sama kita jinjing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103897,"Membawa bekal ketupat palas,","Bekerja di bendang hingga ke petang;","Walaupun kasih tidak berbalas,","Baik hati mu tetap dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103898,"Pagi hari pergi ke bendang,","Membawa raga berisi sagu;","Dari jauh hamba mencuri pandang,","Ku curah kasih dalam lagu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103899,"Pagi Hari pergi ke bendang,","Membawa raga berisi sagu;","Dari jauh hamba mencuri pandang,","Ku Curah kasih dalam lagu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103900,"Indah pandangan padi menguning,","Terbentang luas saujana mata;","Bersolat subuh dipagi hening,","Semoga Allah rahmati kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103901,"Harum baunya bunga melati,","Berwarna warni subur di taman;","Bila merindu si buah hati,","Radio klasik jadi peneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103902,"Terbang kenari bersama teman,","Menuju arah ke pohon jati;","Mawar berduri menyeri laman,","Warna yang merah berkenan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103903,"Harum sungguh bunga kenanga,","Sayang tumbuhnya ditengah rimba;","Banyak bunga antara bunga,","Bunga pajak usah dicuba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103904,"Putih putih si bunga kobis,","Sedap dimakan dicicah budu;","Tangan letih air dah habis,","Bunga ku siram di tasik madu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103905,"Bunga anggerik bercorak batik,","Jadi hiasan dirumah kakak;","Jika yang molek dipandang cantik,","Yang kacak pula tentunya segak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103906,"Burung di sangkar bersiul perlahan,","Menjadi penawar di kala senja;","Sungguh sukar nak buat pilihan,","Antara mawar dengan seroja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103907,"Mencari penawar jauh di sana,","Dukunnya dara gadis yang cantik;","Cantiknya mawar beraneka warna,","Berebut perwira mahu memetik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103908,"Harum baunya mawar di laman,","Sunting sekuntum buat si dara;","Bersihkan jiwa teguhkan iman,","Ukirkan senyum biarpun lara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103909,"Benih ditabur di Tasik Bera,","Airnya dalam luas memanjang;","Bumikan subur hidup sejahtera,","Rahmatan alam menjadi tunjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103910,"Bunga mawar ditapak tangan,","Tangkai melatai dalam genggaman;","Jangan leka selalu berangan,","Kalau ingin miliki kekasih idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103911,"Gugur sekuntum si bunga melur,","Dikutip orang lalu disimpan;","Sentiasalah kita berbudi luhur,","Agar kekal jadi ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103912,"Indah cantik bunga anggerik,","Buat hiasan di hadapan laman;","Hidup jangan mudah serik,","Jika penuh dengan cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103913,"Pergi ke desa memetik melati,","Banyak rerama terbang di dusun;","Hilangkan rasa resah di hati,","Mari bersama kita berpantun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103914,"Bakar setangi api pelita,","Hangatnya haba bawa ke ambang;","Harum mewangi mawar di kota,","Anggerik di rimba segar berkembang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103915,"Mekar kudup si bunga tanjung,","Jual di pekan oleh si nona;","Jika hidup minta disanjung,","Segala kebaikan tidak bermakna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103916,"Bunga angerik jadi kegemaran,","Bunga kantan dibuat laksa;","Hidup penuh dengan cabaran,","Jangan cepat berputus asa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103917,"Pandang jauh mata kabur,","Bila dekat senyum sendiri;","Layu kemboja atas kubur,","Tiada siapa ambil peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103918,"Harum bau nya bunga kenanga,","Harum sampai dari seberang;","Hati hati memetik bunga,","Takut bunga dihalaman orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103919,"Bersama cik kiah pergi undangan,","Malam khamis buat kenduri;","Bunga mawar bunga larangan,","Nampak cantik tapi berduri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103920,"Itik bertelur ayam perhati,","Riang menari si rama rama;","Bunga melur gubahan hati,","Cantik berseri memikat sukma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103921,"Harum wangi bunga melati,","Harum lagi bunga tanjung;","Masa muda menurut hati,","Sudah tua badan menanggung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103922,"Tepi anjung letak kemangi,","Waktu pagi dibentang tikar;","Bunga tanjung harum mewangi,","Pohonnya tinggi dipetik sukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103923,"Kenangan silam menghambat diri,","Di pinggir kali menunggu dinda;","Siang malam hamba mencari,","Bunga bakawali di manakah berada?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103924,"Bau nye harum di pinggir rimba,","Di situ kembang si bunga kasturi;","Mandi bunga sudah ku cuba,","Mengapa wajah ta juga berseri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103925,"Indah berseri bunga ditaman,","Bunga dahlia mari dipetik;","Mulakan hari dengan senyuman,","Moga ceria wajah yang cantik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103926,"Di atas ranting semut meniti,","Hendak ke paya hutan seberang;","Hendak menyunting bunga melati,","Sayang kembangnya di laman orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103927,"Gugur layu kuntum melati,","Mekar wangi bunga sahara;","Mengikat janji bersimpul mati,","Walau tertanam jasad di pusara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103928,"Mengeliling bangku kumbang menari,","Ke kuntum kemboja terbang menyerang;","Sedih hatiku ditinggal sendiri,","Bunga kupuja dipetik orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103929,"Bawa baja ketanah seberang,","Pohon selasih di tanah melayu,;","Bunga ku puja dipetik orang,","Kuntum kasih terkulai layu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103930,"Menumbuk ringi lalu dirasa,","Seorang diri sepi di kali;","Harum mewangi anggerik desa,","Cantik berseri tak dipeduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103931,"Sayang kiambang celahan kemangi,","Didalam taman c bunga kemunting;","Tika kembang harum mewangi,","Banyak tangan ingin mnyunting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103932,"Gugur kemboja dipusara hati,","Kembang mawar dihujung ranting;","Kaseh umpama seputih melati,","Panas setahun tidak akan kering.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103933,"Bunga mawar bunga melati,","Harum mewangi indah ditaman;","Orang sabar dan baik hati,","Sudah pasti disukai teman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103934,"Rona bercampur awan gemawan,","Tepian pantai persis berapi;","Tumbuh di lumpur mekar menawan,","Bunga teratai jauh menepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103935,"Jangan ditawar harga dah mati,","Sentiasa bersedia janji dikota;","Bunga mawar gambaran hati,","Buat si dia yang tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103936,"Kali ke lima ayam bertelor,","Di celah tunggul eram serata;","Harum aroma kuntuman melor,","Menghias sanggul dara jelita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103937,"Bunga mawar kembang setaman,","Dipetik pula sigadis pemalu;","Apa khabar teman-teman,","Moga Sehat bahagia selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103938,"Bunga matahari indah ditaman,","Tanam sebaris dipadang kota;","Hiasi hari dengan senyuman,","Cantik di hati cantik dimata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103939,"Bunga melati bunga kenanga,","Gugur sekuntum ke hujung muara;","Masa hidup tak dapat bunga,","Ku nanti kemboja ditaburan pusara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103940,"Harum mewangi bunga melati,","Hidup subur dalam taman;","Berpantun kita hiburkan hati,","Warisan sejak zaman berzaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103941,"Bunga mawar di atas kepala,","Sering diguna para penari;","Dalam hidup memcari pahala,","Menjadi bekalan kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103942,"Putih berseri bunga kenanga,","Elok tersusun di atas papan;","Gadis ibarat sekuntum bunga,","Jagalah adab jagalah sopan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103943,"Berdesir dedaun di pohon meranti,","Embun nan pasrah lurut ke bumi;","Bunga pahar karangan hati,","Pengikat kata asmara bersemi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103944,"Bunga tulip indah menguntum,","Dipetik anak buat suntingan;","Apa diharap bunga sekuntum,","Masih banyak yang berkembangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103945,"Cantik mekar bunga teratai,","Hidup di air akar tak jejak;","Apakan daya hajat tak sampai,","Kasih ku beri orang tak hendak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103946,"Harum semerbak bunga melur,","Kupetik riang di pagi hari;","Hati resah bawalah berhibur,","Nanti merana memakan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103947,"Nampak kejora di hujung padang,","Pokok pinang si pokok sena;","Kemboja di pusara sejuk dipandang,","Semoga tenang penghuninya di sana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103948,"Banyak luruh buah kelor,","Santan kara di buat kari;","Cantik sungguh si bunga melor,","Di rambut si dara nampak berseri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103949,"Bunga teratai kembang di kolam,","Puyu berenang di celah akar;","Jauh jauh berkirim salam.","Ikatan ukhwah teguh menjalar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103950,"Bunga melati di atas dahan,","Bunga melur di dalam cepa;","Rindu hati tidak tertahan,","Bila kita dapat berjumpa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103951,"Bunga kemboja di Bandar Pontian,","Bunga Raya di Tanjung Bidara;","Sering-sering mengingati kematian,","Bijak merancang kehidupan sementara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103952,"Bunga kuning kembang bercelah,","Senuduk pula berbunga pagi;","Sudah dapat gading bertuah,","Tanduk tadak berguna lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103953,"Harini cuaca panas membara,","Bunga matahari harum semerbak;","Paktam bukan main gembira,","Pagi-pagi dah berjoget lambak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103954,"Bertiup bayu kala senja,","Selat utara tidak bertepi;","Gugur layu bunga kemboja,","Atas pusara diam menyepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103955,"Anak gembala meniup seruling,","Meniup seruling di waktu pagi;","Biru merah putih dan kuning,","Jalur gemilang berkibar tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103956,"Hijau bendang ingatan melayang,","Putih cawan berisi inti;","Sekali pandang wajah terbayang,","Senyum puan menusuk hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103957,"Langit membiru indah dipandang,","Suria pagi memancarkan sinarnya;","Lalui hari dengan hati yang tenang,","Syukuri nikmat limpah kurnianya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103958,"Pagi hening pergi ke hutan,","Tidak berteman berjalan leka;","Putih kuning dara pingitan,","Jadi idaman ramai jejaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103959,"Cahaya putih di ufuk timur,","Tanda fajar telah menyinsing;","Usaha letih jangan berundur,","Banyakkan belajar terus bersaing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103960,"Pergi kedai membeli mancis,","Mancis dibeli berwarna merah;","Rupa x cantik asalkan manis,","Buat cik abang tak tentu arah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103961,"Langkah di hayun mata melirik,","Taktau kemana arah tujuan;","Semua warna tetap menarik,","Mengikut tema setiap jemputan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103962,"Kepala sakit terasa dungu,","Banyak masalah datang bertamu;","Warna kegemaran pastinya ungu,","Pantang ternampak tak pernah jemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103963,"Tanam bunga di dalam taman,","Mari di siram waktu petang;","Bukit menghijau terasa aman,","Laut yang biru hati pun tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103964,"Bunga raya warnanya merah,","Cantik indah tak jemu di pandang;","Simpan di hati rasa nak marah,","Perkara buruk jangan di undang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103965,"Padi menghijau di luar kota,","Mawar merah di atas para ;","Awas pada pandangan mata ,","Dri mata hati berbicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103966,"Terbang tinggi burung merpati,","Turun makan di tepi jalan;","Warna merah tanda berheti,","Bila dah hijau bolih jalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103967,"Kurik kundi merahnya saga,","Timbang emas untuk dijual;","Baik budi harus dijaga,","Biar kemas sering dibual.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103968,"Naik pedati pergi kebendang,","Padi dimakan pipit sekawan;","Merah bukan bermakna garang,","Hatiku lembut seputih awan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103969,"Putih putih bunga melati,","Banyak terdapat didalam taman;","Cantik dimata cantik dihati,","Ingin kusunting menjadi teman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103970,"Pergi niaga di Pasar Payang,","Ke Bukit Genting bekal dibawa,","Wajah adinda ku bayang-bayang,","Putih kuning dambaan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103971,"Kain langsir indah berona,","Hulu keris indah terukir;","Berbeda kulit pelbagai warna,","Namun merah darah yang mengalir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103972,"Indah sungguh bunga melati,","Bunga kebangsaan si bunga raya;","Sopan santun budi pekerti,","Inilah warna sesuatu budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103973,"Bunga kertas berwarna jingga,","Megar berseri di sudut halaman;","Bila cinta telah berbunga,","Semaikan kasih dengan kejujuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103974,"Awan larat corak berbiku,","Corak selendang kuntum kenanga;","Merah jingga warna hatiku,","Bertemu pandang cinta berbunga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103975,"Ayamlah katik mematuk betik,","Burung berbintik di pohon angsana;","Berkain batik mata melirik,","Aduhlah cantik anggun pesona.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103976,"Berkayuh cik Abang pergi ke pasar,","Tersenyum cik senah bibir nya merah;","Damba kasih semakin menjalar,","Duduk termenung semakin resah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103977,"Padi menguning cantik berseri,","Hasil keringat para pesawah;","Maha Agung akan memberi,","Sentiasalah ingat kepada Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103978,"Kilauan emas indah permata,","Memukau mata memikat hati;","Ros merah tanda mata,","Pemberian dari kekasih hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103979,"Lauk ketam pucuk paku,","Makan berkawan di rumah bonda;","Sungguh pun hitam warna kulit ku,","Tetap menawan ramai tergoda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103980,"Badan letih hati keliru,","Tiada nikmat selera pun hilang;","Merah putih kuning dan biru,","Warna keramat jalur gemilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103981,"Jingga Merah, Awan Kelabu,","Senja Tiba, Unggas Pun Pulang;","Bicara Marah, Menghiris Kalbu,","Bersayu Hiba, Hatinya Walang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103982,"Indah nian bunga melati,","Aura dibawa memberi sakti;","Putih bersih cinta dihati,","Untuk dia si pemikat hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103983,"Turun ke sawah membawa tajau,","Untuk menyerkap si katak puru;","Cemburu kuat berona hijau,","Tukar biru moga hilang cemburu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103984,"Darah ditubuh mengalir merah,","Mengalir cedera bila terluka;","China suka bebenor warna merah,","Anggapan bertuah budaya mereka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103985,"Kering sudah tasik dan danau,","Ramai orang pergi berburu;","Lain pulak dengan warna hijau,","Orang kata kuat cemburu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103986,"Meredah hutan luka berdarah,","Membawa bakul mencari buah;","Penduduk china memilih merah,","Konon merah membawa tuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103987,"Meredah padang hutan dan paya,","Sambil memikul padi berguni;","Warna merah tanda bahaya,","Merah juga lambang berani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103988,"Dari mana hendak ke mana,","Meredah padang hutan dan paya;","Dalam banyak pilihan warna,","Warna merah tanda bahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103989,"Anak dara berbaju batik,","Si teruna pula bersongkok hitam;","Ape guna berwajah cantik,","Jika hati tidak tenteram.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103990,"Pagi hening ke Bandar Bintangor,","Kerana perniagaan keluarga tercinta;","Merah kuning bendera Selangor,","Warna kebanggaan rakyat jelata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103991,"Setepak sirih berbenang emas,","Cembul tembaga baru berganti;","Adat berkasih terasa cemas,","Bila curiga merasuk hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103992,"Merah biru kuning dan putih,","Lambang bagi bendera Malaysia;","Usaha kuat janganlah letih,","Hidupmu pasti akan ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103993,"Hitam hitam si tampuk manggis,","Kemuning gugur bunganya;","Hitam hitam ku pandang manis,","Putih kuning apa gunanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103994,"Hidup subur pohon selasih,","Hijau merimbun ditepi bendang;","Sudah kutabur budi dan kasih,","Emas jua dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103995,"Padi menguning luas terbentang,","Hijau kuning indah menawan;","Budi bahasamu eluk d pandang,","Keimanan mu buat ku tertawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103996,"Warna biru warna kesukaan ku,","Selalu dipakai sejak dahulu;","Radio Klasik sentiasa dihati ku,","Menggamit kenangan masa lalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103997,"Masak bening campur ikan,","Letak di salai ikan kerisi;","Merah kuning jadi kesukaan,","Bila memakai nampak berseri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103998,"Pokok nangka pokok ru,","Mana nak sama pokok sena;","Warna hijau kusangka biru,","Rupa nya aku dah rabun warna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (103999,"Burung balam terbang di awan,","Terbang terus ke arah lagun;","Pakai hitam lebih menawan,","Kelihatan kurus lagi anggun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104000,"Biru jernih air di tasik,","Nampak cantik btu di dasaran;","Setiap hari htiku berbisik,","Dengarlah radio klasik untuk hiburn.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104001,"Masak nasi berbau hangit,","Nasi dikacau berhati hati;","Tujuh warna pelangi di langit,","Namun Hijau idaman di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104002,"Banyak baju bolih dipakai,","Baju hitam tudung putih;","Apa jawapan jangan pertikai,","Badan segar jadi letih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104003,"Aneka warna sejalur pelangi,","Merentas langit aduh indahnya;","Rukun hidup saling menyayangi,","Hitam putih berserah pada-Nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104004,"Nyamuk dipukul terpercik darah,","Darah merah di tapak tangan;","Berusahalah kita menahan marah,","Supaya jiwa dalam ketenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104005,"Pohon zaitun tepian angsana,","Rebah sehala menimpa sabut;","Pantun digarap biar sempurna,","Bahasa yg indah sedap disebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104006,"Pohon kekabu tinggi mengawan,","Jalan haluan teratak moyang;","Rona kelabu nampak gerawan,","Warna pujaan orang tersayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104007,"Hijau kuning warna nya lembut,","Di pakai mari gadis desa;","Kalau menjeling tambah terpaut,","Berkobar di hati menusuk rasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104008,"Kilauan senja di ufuk barat,","Warna lembayung indah terserlah;","Bebanan hidup semakin berat,","Kepada Allah lh kita berserah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104009,"Petik selasih di pagi hari,","Bertiup bayu gugur kekabu;","Putih bersih wajah berseri,","Sungguh ayu menawan kalbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104010,"Bunga ros warnanya merah,","Cantik berseri di taman larangan;","Cinta dituju ke satu arah,","Kini hanya tinggal kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104011,"Kaki berdarah tertikam duri,","Sakit terasa bisa sangat;","Warna merah tanda berani,","Bila di pakai rasa semangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104012,"Cerek dan kendi buatan gangsa,","Barangan antik letak merata;","Elok berbudi santun bahasa,","Berwajah cantik anggun jelita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104013,"Bunga Raya cantik merah,","Ditanam buat hiasan laman;","Cabaran jangan cepat mengalah,","Hadapi dengan penuh kesabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104014,"Awan larat corak berbiku,","Corak selendang kuntum kenanga;","Merah jingga warna hatiku,","Bertemu pandang cinta berbunga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104015,"Burung bangau terbang rendah,","Turun di paya mencari ikan;","Hidup bertawakal pada Allah,","Perihal rezeki tidak dirisaukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104016,"Gunung Angsi di kampung lama,","Orang mendaki dari arah kanan;","Kita berkongsi mentari yang sama,","Namun rezeki tetap berlainan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104017,"Capai berus gosok gigi,","Berus gigi bahannya plastik;","Assalammualaikum dan selamat pagi,","Buat semua sahabat radio klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104018,"Subuh pagi pergi ke Trolak,","Singgah di Sungkai membeli kari;","Rezeki jangan kita tolak,","Musuh jangan pulak dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104019,"Dipadang kota bersuka ria,","Minum air bergelas gelas;","Hidup manusia diatas dunia,","Hendak rajin jangan pemalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104020,"Subuh terjaga sebelum pagi,","Bangun mencari lampu suluh;","Mengurus keluarga tidak bergaji,","Khidmat diberi tidak mengeluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104021,"Burung nuri terbang di angkasa,","Beralih arah menuju ke pekan;","Rezeki dicari di kota di desa,","Berkat dan murah kita pohonkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104022,"Minum pagi dengan tokki,","Lepas mengendong beras seguni;","Pagi keluar mencari rezeki,","Nak bagi makan anak dan bini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104023,"Melangkah pulang setelah mendaki,","Bukit Puteri dikenang diingat;","Membanting tulang mencari rezeki,","Di bawah matahari bersinar hangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104024,"Langit petang awannya merah,","Mencuit kenangan ketika bersamanya;","Rezeki datang di pagi nan cerah,","Bersyukur dengan nikmat kurniaannya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104025,"Pantun selamat pagi,","Tepi perigi si pisang kaki;","Buahnya lebat bergugus pangsa,","Keluar pagi cari rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104026,"Kajang Pak Malau kajang berlipat,","Kajang hamba mengkuang layu;","Selamat pagi semua sahabat,","Muga sehat sejahtera selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104027,"Che hamidah naik kenari,","Singgah di pasar beli kerisik;","Sungguh meriah di pagi hari,","Dok dengar di radio klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104028,"Buluh tinggi dibuat galah,","Lalu di titi buluh digalas;","Mulakan pagi dengan bismillah,","Ikhlaskan hati sempurnakan tugas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104029,"Makan buah hati-hati,","Takut-takut tertanggal gigi;","Senyum-senyum senang hati,","Assalamualaikum selamat pagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104030,"Menjelang malam bakar mercun,","Singa menari di tiang besi;","Ucapan salam dengan berpantun,","Smbil mencari sesuap nasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104031,"Makan nasi bercampur kuah,","Makan bersama dengan mak esah;","Bila rezeki melimpah ruah,","Jangan lupa mereka yang susah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104032,"Harum baunya si gulai kari,","Makan nasi lauk berkuah;","Memohon doa di pagi hari,","Semoga rezeki melimpah ruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104033,"Jangan dipanjat pohon Meranti,","Hiasan laman bunga senduduk;","Zikir munajat hidupkan hati,","Bertambah iman diri tawaduk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104034,"Ingin kupergi menjelajah dunia,","Gunung didaki pulau di dekati;","Mentari pagi membawa kurnia,","Semoga rezeki murah diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104035,"Di sebalik gunung terbit mentari,","Kelak jatuh dihari petang;","Elakkan bermenung dipagi hari,","Nanti jodoh lambat datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104036,"Assalamualaikum kata di beri,","Permulaan kata bila berjumpa;","Sarapan pagi jangan hindari,","Lebih cergas tidak akan hampa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104037,"Pohon celagi tepi perigi,","Terpatah dahan menimpa sentang:","Berkais pagi makannya pagi,","Kadang bertahan hingga ke petang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104038,"Gunung Daik jauh pandangan,","Indah dilihat diwaktu petang;","Asyik termenung serta berangan,","Tidaklah yg secupak itu jadi segantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104039,"Di pagi hari memakai kerudung,","Kerudung bersulam permata tergantung;","Mencari rezeki macam lipas kudung,","Ingat mati sedar dik untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104040,"Kuih bingka letak terdedah,","Selalu alpa menggosok gigi;","Asyik leka mengemas rumah,","Jangan lupa bersarapan pagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104041,"Ke mana terbang burung terkukur,","Mencari tempat untuk singgah;","Jangan lupa untuk bersyukur,","Setiap rahmat daripada Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104042,"Bibir merah baju pun merah,","Gadis kota bergaya di desa;","Mentari cerah rezeki pun murah,","Bersyukurlah kita kepada Yang Esa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104043,"Berjalan kaki ke Gunung Sempak,","Bawa delima seraga cekur;","Dapat rezeki amalkan infak,","Sedikit cuma tanda bersyukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104044,"Burung kenari di pohon jati,","Bersiul lagi bagaikan bermadah;","Rezeki dicari bersungguh hati,","Bersama pagi yang sungguh indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104045,"Pagi hari mandi di perigi,","Membawa raga ke Kuala Dulang;","Kais pagi makan pagi,","Lewat senja baru ku pulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104046,"Pulas telinga sianak jantan,","Pagi menunggu lambatnya sampai;","Se ulas nangka sepangsa durian,","Entahkan jadi rezeki si tupai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104047,"Tun nila berpurnama berjalan kaki,","Mencari suri di sisi bersama;","Assalamualaikum selamat pagi,","Dengarlah klasik pasti hati berbunga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104048,"Indah lagun di tanah tinggi,","Sebelah hulu serumpun buluh;","Cuba bangun di awal pagi,","Mandi dahulu sebelum subuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104049,"Pagi-pagi memasang lukah,","Lukah dipasang di tanjung jati;","Cari rezeki yang barakah,","Semoga hidup kita diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104050,"Keluar ke pasar berjalan kaki,","Walau panas tidak mengalah;","Saat keluar mencari rezeki,","Niatkan kerja kerana Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104051,"Kayu sugi pencuci keramat,","Di ambil pak Khir dikebunnya nyonya;","Satu lagi ucapan selamat,","Pantun terakhir sejahtera hendaknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104052,"Padi bukit sipadi huma,","Mari ditampi dalam talam;","Rezeki berkat kongsi bersama,","Seiring doa siang malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104053,"Di tepi kolam bermain sembat,","Bermata tiga kail dicara;","Assalamualaikum sahabat,","Selamat pagi salam sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104054,"Waktu pagi makan mempelam,","Sambil berjalan di tepi tasik;","Selamat pagi dan Assalammualaikum,","Buat rakan taulan sajian klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104055,"Jangan di panjat jangan di daki,","Kalau gunung terlalu tinggi;","Assalamualaikum selamat pagi,","Semoga dipermudahkan mencari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104056,"Arca tanjak dibuat cermat,","Indah terukir nampak sekata;","Rezeki tidak salah alamat,","Andai ditakdir miliknya kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104057,"Tiap pagi teruskan langkah,","Hendak mandi dikolah;","Mencari rezeki biarlah berkah,","Jangan lupa amalkan bersedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104058,"Tepi perigi si kebun kesum,","Cantik bidara sarangnya tempua;","Selamat pagi assalamualaikum,","Salam sejahtera rakan semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104059,"Daun gelenggang dibuat jamu,","Di adun perlahan memang ketara;","Di gelanggang pantun kita bertemu,","Saling berkenalan beramah mesra.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104060,"Tepi perigi bunga kemboja,","Pokok bidara di rumpun halia;","Selamat pagi selamat bekerja,","Semoga sejahtera khamis mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104061,"Assalamualaikum selamat pagi,","Itu lah yang sering diucapkan;","Pagi2 sudah dapat rezeki,","Itu tanda rezeki allah murahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104062,"Jahit manik tercucuk jarum,","Jarum mengait baju buah hati;","Selamat pagi Assalamualaikum,","Semoga hidup sentiasa diberkati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104063,"Jinak seligi melantun-lantun,","Pemancing muda senyum terukir;","Setiap pagi menukang pantun,","Aktifkan minda pantas berfikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104064,"Rasa penat berjalan kaki,","Jenuh berjalan di tepi bendang;","Setiap pagi mencari rezeki,","Untuk kebahagiaan orang tersayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104065,"Gunung didaki puncak didekati,","Terbang kekunang berasa selesa;","Semoga rezeki sentiasa diberkati,","Bahagia tenang sepanjang masa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104066,"Banyak gunung banyak lah jurang,","Berhati hati di kala berjalan;","Dalam kehidupan umpama berjuang,","Mengais rezeki itu lah tuntutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104067,"Berbaju kurung gadis jelita,","Tudung bersegi bersarung kaki;","Umpama burung terbang merata,","Seawal pagi kutip rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104068,"Burung tekukur hinggap di dahan,","Mencari makan mematuk-matuk;","Bersyukur kita kepada Tuhan,","Masih menghirup pagi yang sejuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104069,"Kalau pegi kepekan sari,","Bawak bersama bakul rotan;","Selamat pagi kepada isteri,","Selamat hari lahir saya doakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104070,"Pokok celagi si pohon merkisa,","Subur di Bandung bersama kekabu;","Selamat pagi ucapan biasa,","Namun terkandung doa seribu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104071,"Naik pedati jual keria,","Penuh kesabaran membawa jualan;","Raikan pagi penuh ceria,","Panjat kesyukuran tunaikan kewajipan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104072,"Pergi ke pekan bersama teman,","Janganlah lupa membeli benang;","Pagi hari amalkan senaman,","Badan cergas jiwa pun tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104073,"Pokok gelam dibelit seruntun;","Tumbuh merata di tepi tasik:","Ku ucap salam sejuta pantun,","Sasaran kita di Radio Klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104074,"Pagi hari jemur di ampaian,","Petang hari sudah di lipatan;","Dulu pernah jadi impian,","Walau kini sekadar kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104075,"Pulang berdua memasang lukah,","Di telaga sawah padi melata;","Tak lupa doa memohon barakah,","Semoga Allah murahkan rezeki kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104076,"Dalam semak ada duri,","Burung tempua bersarang rendah;","Assalamualaikum selamat pagi,","Mulakan aktiviti dengan bismillah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104077,"Empat buntil beras dikandar,","Pokok betik belakang rumah;","Moty Hotel inap bandar,","Hotelnya cantik warganya ramah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104078,"Pokok betik belakang rumah,","Dipotong rencong buah kuini;","Hotelnya cantik warganya ramah,","Mari pelancong inap di sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104079,"Karangan wira berbenang perak,","Seloka berpantun di desa Rumbia;","Pelancongan negara rancak semarak,","Tinggi tamadun peradaban dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104080,"Dara kecil berkain kasa,","Karangan wira berbenang perak;","Usaha kecil ampuhkan bangsa,","Pelancongan negara rancak semarak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104081,"Mula kelongsong jadi rerama,","Terbang rama ke pohon buluh;","Dari kosong menjadi lima,","Selepas lima jadi sepuluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104082,"Dalam dulang cincin permata,","Cincin permata jadi idaman;","Jangan dibuang sampah merata,","Jika merata rosak halaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104083,"Asyik melarik renang bergaya,","Lemah gemalai menari kepang;","Pelaga si unik ikan Malaya,","Tempala sempilai dialah cupang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104084,"Lemah gemalai menari kepang,","Dalam balang tepi beranda;","Tempala sempilai dialah cupang,","Alang-alang ketika ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104085,"Dalam balang tepi beranda,","Esok lusa biar berpinak;","Alang-alang ketika ada,","Nanti-nanti membiak anak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104086,"Matahari mengomel cantik belantik,","Beria-ia dia mengomel;","Mahsuri comel Mahsuri cantik,","Sihatnya dia berbadan mongel.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104087,"Beria-ia dia mengomel,","Gembar-gembur kisah berbagi;","Sihatnya dia berbadan mongel,","Bulunya subur tak gugur lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104088,"Gambar-gembur kisah berbagi,","Cerita ria bermain-main;","Bulunya subur tak gugur lagi,","Sebab ceria dapat bermain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104089,"Cerita ria bermain-main,","Sukanya awan sambil bercekak","Sebab ceria dapat bermain,","Setia berkawan dengan si kakak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104090,"Sukanya awan sambil bercekak,","Berarak-arak menyentuh langit;","Setia berkawan dengan si kakak,","Suka berborak selagi tak lengit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104091,"Berarak-arak menyentuh langit,","Molek sua mereka selisih;","Suka berborak selagi tak lengit,","Mesra berdua menjalin kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104092,"Kampung Kerinting di Gua Musang,","Kabusnya tebal sejuk sekali;","Jangan melenting jika dilesang,","Akal berbekal sabar berkali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104093,"Dari Penjuring ke Sungai Ruan,","Banyak berjaja si nangka madu;","Ilmu dijaring dengan ilmuwan,","Bagai dibaja tanah sepadu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104094,"Benta dan Lipis satu laluan,","Jalan lama ke Gua Musang;","Tak menipis tak hilang haluan,","Selagi berlama iman terpasang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104095,"Susup lari masuk semak,","Ada ular susur akar;","Didik diri singkirkan tamak,","Elak tular menjadi mekar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104096,"Ada ular susur akar,","Tunggu mangsa masuk jerat;","Elak tular menjadi mekar,","Esok lusa tidak melarat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104097,"Tunggu mangsa masuk jerat,","Hilang nyawa badan musnah;","Esok lusa tidak melarat,","Tenanglah jiwa hidup bertanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104098,"Hilang nyawa badan musnah,","Baru ular telan mangsa;","Tenanglah jiwa hidup bertanah,","Elok gelar citra wangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104099,"Kuih kelempung popia berlada,","Martabak Keramat lemang dibelah;","Balik kampung jumpa bonda,","Sebelum tamat cuti sekolah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104100,"Ada pala bersaki baki,","Ada sekati asam jawa;","Raih pahala tambah rezeki,","Pergi bercuti bonda pelawa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104101,"Kuih bingka masih berbahang,","Teh Almonds menu kudapan;","Dari Melaka pergi ke Pahang,","Cameron Highlands tempat sarapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104102,"Ikan kekisi diasam celagi,","Dikuah sudah dihidang amah;","TAC lokasi sarapan pagi,","Pelbagai juadah sedap dijamah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104103,"Naik rakit kayuh rakit,","Ikut arah jangan berbalas;","Naik bukit turun bukit,","Singgah merarau di Sungai Palas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104104,"Batu akik warna putih,","Dicicin tunang untuk si tunang;","Makan kek minum teh,","Perut kenyang hati pun senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104105,"Kerlip bintang berkurun-kurun,","Tidak berbolak cantik berdamping;","Waktu petang hujan pun turun,","Kami bertolak tuju ke Taiping.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104106,"Ayam didik hilang ke mana,","Bawah dapur balik talam;","Jumpa adik mastautin di sana,","Tumpang tidur hanya semalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104107,"Bara lipan si kayu balak,","Besar kasar seperti ditulis;","Selepas sarapan kami bertolak,","Ke Padang Besar negeri Perlis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104108,"Cantik alis si gadis pintar ,","Dara sunti mewah rezeki;","Dari Perlis ke Alor Setar,","Nikmati cuti masih berbaki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104109,"Tangkas pendekar bingkas menyilang,","Gagah berani Putera Gelam;","Alor Setar kota gemilang,","Maka di sini kami bermalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104110,"Terbang lagi burung kenari,","Terpipis jerami bertimbun derum;","Awal pagi siapkan diri,","Destinasi kami ke Royal Belum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104111,"Kuih talam ditatang-tatang,","Dodol ditanding jualan puan;","Melihat alam tasik terbentang,","Tasik Banding beralih tumpuan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104112,"Cucuh api sumbu pelita,","Molek terang gelap berketi;","Ada kisah bakal dicipta,","Sebelum pulang tamatkan cuti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104113,"Tinggi-tinggi pokok rambutan,","Tinggi lagi pokok kelapa;","Hati-hati pergi ke hutan,","Tentulah suami tidak dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104114,"Burung puyuh terbang rendah,","Susup lari masuk semak;","Walaupun kuyup berhujan gundah,","Didiklah diri singkirkan tamak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104115,"Tinggi tinggi pokok rambutan","Tinggi lagi pokok kelapa","Wangi setanggi jadi sebutan","Usah dilupa setia nya dupa!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104116,"Tinggi tinggi pokok rambutan,","Tinggi lagi pokok kelapa;","Pergi beli nyoq pandan,","Minta lebih isi kelapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104117,"Tinggi-tinggi pokok rambutan,","Tinggi lagi pokok kelapa;","Buahnya lesap di pinggir hutan,","Tinggal hanya sabut kelapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104118,"Tinggi-tinggi pokok rambutan","Tinggi lagi pokok kelapa","Hati-hati duhai watan","Bintang bersegi jangan dilupa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104119,"Tinggi-tinggi pokok rambutan,","Tinggi lagi pokok kelapa;","Diingat lagi duhai watan,","Sayang berbagi janganlah papa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104120,"Tinggi-tinggi pokok rambutan,","Tinggi lagi pokok kelapa;","Setanggi wangi dalam lipatan,","Tidak dilupa kesetiaan dupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104121,"Beri merah daunnya hijau,","Boleh dimakan dibuat ubat;","Hati nan pasrah jangan ditinjau,","Rindu sepekan redanya lambat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104122,"Kampung Batu Padang Temu,","Pokok kari subur ditanam;","Darjah satu menuntut ilmu,","Hujung hari darjah enam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104123,"Daun kari petik bertanam,","Jadi perisa pauk si Ratu;","Hujung hari darjah enam,","Esok lusa tingkatan satu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104124,"Ada taju berlapis enam,","Patah diusah berjahit benang;","Kelas Maju darjah enam,","Banyak kisah dikenang-kenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104125,"Pukul tabuh bertalu sudah,","Ada watikah dikehendaki:","Sihatlah tubuh untuk ibadah,","Buat sedekah mewahkan rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104126,"Ada selasih ada legundi,","Dalam nyiru kami asingkan;","Terima kasih kalungan budi,","Jasa guru kami kenangkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104127,"Ikan tamban di Inderagiri,","Selar papan di Selat Melaka;","Sepekan saban menunggu hari,","Selaut harapan berkat belaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104128,"Meranti nyatuh kayu di hutan ,","Di hutan Pahang Darul Makmur;","Pasti patuh perintah Tuhan,","Molek dihalang maksiat berlumur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104129,"Ada cerana sambut bersambut,","Latah tersasul Mak inang Jeli;","Pasti bermakna hari disambut,","Maulidur Rasul kesekian kali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104130,"Burung puyuh terbang rendah,","Susup lari masuk semak;","Walaupun kuyup berhujan gundah,","Didiklah diri singkirkan tamak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104131,"Kalau kayu hendak ditarah,","Keratlah cabang dengan daunnya;","Kalau ilmu hendak bertambah,","Dekati orang dengan pantunnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104132,"Apalah guna daun kayu,","Untuk tempat orang berteduh;","Apalah guna pantun Melayu,","Untuk tempat mencari suluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104133,"Tuai padi antara masak,","Esok jangan layu-layuan;","Intailah kami antara nampak,","Esok jangan rindu-rinduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104134,"Minta daun diberi daun,","Dalam daun buah bidara;","Minta pantun diberi pantun,","Dalam pantun ada bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104135,"Pijat-pijat batang keduduk,","Keduduk seman bunga tiada;","Jika bijak pelihara wuduk,","Pasti Iman tebal di dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104136,"Raja selasih letak di pinggan,","Letak juga bunga melati;","Kalau bersih mencuci tangan,","Nescaya juga sucinya hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104137,"Elak selisih pisau berlaga,","Bimbang juga terluka jari;","Mahu bersih jiwa raga,","Elok jaga kebersihan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104138,"Elok buluh diukir-ukir,","Dilorek bunga raya;","Ada akal berfikir-fikir,","Diguna jadi kaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104139,"Daki Bukit Pasir,","Tengok mentari jelma;","Bijak baca tafsir,","Bahagia nanti menjelma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104140,"Tepuk amai-amai,","Lagu Kota Selangi;","Busuk yang ramai,","Daripada yang wangi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104141,"Kukur tumbuh berjajar,","Putih deret selari;","Berguru kepalang ajar,","Nanti gigit jari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104142,"Semambu buat ubat,","Lumur asam celagi;","Hujan turun lebat,","Rezeki datang lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104143,"Pandai baca rumi,","Jawi boleh tahan;","Tujuh petala bumi,","Terabdi rahsia Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104144,"Cendawan titi tumbuh melata,","Putih kilat tidak melampai;","Elok teliti apa dikata,","Ada niat hendak disampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104145,"Asam garam cuca mantera,","Tabur merata atas bentala;","Tidaklah karam belayar bahtera,","Setuju kata angguk kepala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104146,"Ringgi-ringgi di pohon kayu,","Taruk jerami ancak dipingit;","Betapa tinggi akal Melayu,","Laksana bumi sampai ke langit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104147,"Ambil ragi buat meramu,","Jadi raksa menyalut bebat;","Tidaklah rugi mencari ilmu,","Esok lusa menjadi hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104148,"Gesek rebab jangan ditandak,","Lagu cerita kisah Temagi;","Maka sebab molek bertindak,","Bukan kita siapa lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104149,"Tukang pagar dengan tukul,","Paku berderai jatuh di tangga;","Kerang segar ada di bakul,","Dalam kedai Taman Tenaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104150,"Jangan kalih purnama mengambang,","Pungguk merayu berselang-seli;","Dipilih-pilih cukup timbangan,","Seluk saku mampu dibeli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104151,"Tumbuh liar pohon berangan,","Lereng bukit di hutan Jelai;","Sambil bayar mula berangan,","Apa menu hendak digulai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104152,"Sulam lerang bunga selasih,","Kudup pecah lima kembangan;","Basuh kerang biar bersih,","Siapkan rencah untuk hidangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104153,"Tanah nanas jangan berlumpur,","Salah kira kelaknya sesak:","Kuali panas sudah di dapur,","Kerang sira siap dimasak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104154,"Melaka Pindah ke Tanjung Ara,","Arah bukan ke pekan Pilah;","Siaplah sudah si kerang sira,","Jemput makan baca bismillah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104155,"Hikmat kain hitam,","Renda corak bira;","Baca sampai katam,","Ayuh kita bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104156,"Renda corak bira,","Dara suka gelang;","Ayuh kita bicara,","Simpan sebelum hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104157,"Jahit dengan cermat,","Kebarung untuk tamu;","Baca sampai tamat,","Dapat banyak ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104158,"Ibu masak mihun,","Serbat kelapa muda;","Sudah 20 tahun,","Masih rasa muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104159,"Sirap dalam gelas,","Elok kurang gula;","Kerja biar ikhlas,","Dapat banyak pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104160,"Ayam pura-pura,","Konon sakit kaki;","Elok jangan berkira,","Nanti mewah rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104161,"Indah Pantai Kemasik,","Cantik jadi lakaran;","Jika hati terusik,","Sahut sebagai cabaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104162,"Sampai hujung kota,","Hujan turun lebat;","Manusia itu lata,","Allah Maha Hebat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104163,"Alor molek berturap,","Bikin boleh tahan;","Elok bergantung harap,","Hanya kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104164,"Ular atas permatang,","Katak tidak selamat;","Pasti rezeki datang,","Tidak salah alamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104165,"Semalu tepi perigi,","Tumbuh pokok lada;","Gigih usaha lagi,","Tambah ilmu di dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104166,"Serbat kelapa muda,","Elok kacau dulu;","Ilmu ada di minda,","Kena amal selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104167,"Daki gunung Tahan,","Jumpa pokok rumbia;","Dapat reda Tuhan,","Pasti hidup bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104168,"Padi Mahsuri Padi Malinja,","Selepas diangin simpan di kepuk;","Silakan Abang Rahim bersolat kita,","Dalam doa beroleh petunjuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104169,"Mari menari rentak sambar.","Jgn menari diatas perahu.","Kejam mata bawa bersabar.","Buat buat tak nampak macam tak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104170,"Tuai padi mari ditampi,","Jangan terlepas ayam serama;","Lena tidur dijengah mimpi,","Mimpi bertemu Si Arab Penderma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104171,"Mengalir air terhalang ditebat,","Meliuk-lintuk renang belida;","Dua tokoh di gelanggang debat,","Debat beradab memperkaya minda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104172,"Meliuk-lintuk renang belida,","Sesekali sekilas lincahnya sepat;","Debat beradab memperkaya minda,","Saling menghormati pelbagai pendapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104173,"Sesekali sekilas lincahnya sepat,","Sayang belida tersangkut di joran;","Saling menghormati pelbagai pendapat,","Matlamat akhir menemui kebenaran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104174,"Sayang belida tersangkut di joran,","Tali berbelit dicari punca;","Matlamat akhir menemui kebenaran,","Memenangkan keadilan timbangan neraca", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104175,"Pohon enau mayangnya hitam,","Cantas pelepah diambilnya nira;","Bertukar putih rambut yang hitam,","Menyuluh lorong ke arah pusara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104176,"Akar melilit akar memponang,","Memanjat tualang mengambil madu;","Semakin diingat semakin terkenang,","Asyik mengenang mengundang rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104177,"Terbang tinggi si burung bangau,","Ke gigi air katak melompat;","Bukan angau sebarang angau,","Angau hamba angau bertempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104178,"Pergi ke sawah dicalar benta,","Membasuh kaki duduk di titi;","Mari berpantun bermain kata,","Jangan berduka hiburkan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104179,"Merah ranum buah pulasan,","Isi durian dimasak pengat;","Hati mengenang kasih berjauhan,","Dia tak rindu daku yang ingat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104180,"Merah ranum buah pulasan","Isi durian di masak pengat","Kasih yg jauh itu ujian","Kesetiaan itu jd pengikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104181,"Jeram dingin jeram bertingkat,","Tempat rangmuda bertemu cumbu;","Kesetiaan kata menjadi pengikat,","Kenal dah lama belum bertemu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104182,"Titip dan pesan silih bertamu,","Ditarik sauh kapal belayar;","Sekadar berserah sukar bertemu,","Tanpa usaha juga ikhtiar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104183,"Buah cempedak si buah nangka,","Berbuah lebat di luar pagar;","Harga minyak naik telah dijangka,","Kiranya turun negarakan gempar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104184,"Merata rumpun rumput semalu,","Rumpun bonglai si rama-rama","Menanti embun menitis malu,","Bagai mempelai malam pertama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104185,"Tanjung sepi cengkerang merata,","Berulang kerap jejak diimbau,","Mencari mimpi dalam gelita,","Merangkul harap mendakap igau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104186,"Dedak bukan umpan pun bukan,","Gula-gula mungkin pilihan raya;","Subsidi ditarik harga minyak dinaikkan,","Seumpama BR1M diambil semula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104187,"Dedak bukan umpan pun bukan,","Gula-gula mungkin pilihan raya;","Kiranya pemberian penuh keikhlasan,","Subsidi dihapuskan siapa percaya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104188,"Merata rumpun rumput semalu,","Rumpun bonglai si rama-rama","Menanti embun menitis malu,","Bagai mempelai malam pertama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104189,"Tanjung sepi cengkerang merata,","Berulang kerap jejak diimbau,","Mencari mimpi dalam gelita,","Merangkul harap mendakap igau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104190,"Apa guna orang bertenun,","Untuk membuat pakaian adat;","Apa guna orang berpantun,","Untuk mengingat petuah amanat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104191,"Apa guna orang bertenun,","Untuk membuat kain dan baju;","Apa guna orang berpantun,","Untuk mengangkat tuah Melayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104192,"Apa guna orang bertenun,","Untuk membuat pakaian budak;","Apa guna orang berpantun,","Untuk mengajar hukum dan syarak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104193,"Apa guna orang bertenun,","Untuk membuat kain cindai;","Apa guna orang berpantun,","Untuk membaiki laku perangai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104194,"Apa guna orang bertenun,","Untuk membuat pakaian nikah;","Apa guna orang berpantun,","Untuk menyampaikan petuah amanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104195,"Apa guna orang bertenun,","Untuk membuat kain pelekat;","Apa guna orang berpantun,","Untuk mengkaji adat istiadat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104196,"Apa guna orang bertenun,","Untuk membuat kain selerang;","Apa guna orang berpantun,","Untuk mengisi mana yang kurang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104197,"Apa guna orang bertenun,","Untuk membuat kain dan baju;","Apa guna orang berpantun,","Untuk menimba berbagai ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104198,"Kalau orang melabuh pukat,","Carilah pancang kayu berdaun;","Kalau kurang mengetahui adat,","Carilah orang tahu berpantun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104199,"Dara bertamu ke pekan Rasah","Kerana menjemput datang nya tunang","Andai tidur mu diamuk resah","Mungkin selimutmu sudah pun hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104200,"Awal pagi telah ke pasar,","Mencari lauk buat juadah;","Hilang selimut tidakku gusar,","Hilang pelikat ku lagi gundah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104201,"Singah kelangkat ditahun sudah","Jumpa puteri menarik kerbau","Hilang pelekat tak perlu gundah","Mungkin dicuri kerana berbau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104202,"Subur dipaya pepohon turi","Tumbuhnya dekat tepi perigi","Pastilah tuannya datang mencuri","Ditukar pelikat yang berbau wangi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104203,"Ambil setangkai si daun kari,","Tanam di pasu di tepi tangga;","Sewangi cinta si empunya diri,","Sehidup semati berkongsi cerita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104204,"Bau setanggi harum di tangga","Diruang tamu duduk sidia","Cinta nan wangi mekar berbunga","Semoga hidup mu dikelilingi bahgia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104205,"Tajam hangat batu kerikil,","Terjengket-jengket teriring tawa;","Terima kasih doa ternukil,","Bahagia kasih di dalam jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104206,"Bawah sepatu dipenuhi lulur","Jalan mendada bawah jambatan","Hanya itu yang mampu dihulur","Sebagai tanda jalinan persahabatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104207,"Pasang jamung pasang pelita,","Senja bertandang Maghrib menghampir;","Terbantut hasrat bicara dusta,","Menjalin cinta kalam dimungkir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104208,"Siakap senohong","Gelama ikan duri","Bercakap memang bengong","Boleh pula jadi menteri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104209,"Mari rewang ke rumah Mak Timah,","Mari memasak si air gula;","Hari cuti mengemas rumah,","Hari bekerja berselerak semula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104210,"Mari masak air gula","Gula datang dari seberang","Sonang yo che'gu kalau tak nak bersepah semula","Cari lah orang rumah sorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104211,"Menjerat puyuh di tepi belukar,","Jenuh mengelak pohon berduri;","Mencari orang rumah tidaklah sukar,","Yang rajin berkemas sukar dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104212,"Hari raya berbaju baru","Baju di beli kat Putrajaya","Kalau che'gu nak cari yang baru","Cari lah calon yang tak berkerjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104213,"Masuk ke hutan mari berburu,","Gulai pelanduk dicampur nangka;","Yang tidak bekerja mudah cemburu,","Semua pergerakan disalah sangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104214,"Hari ni gulai kangkong","Makan dengan sambal belacan ulam rajo","Che'gu cari yang satu kampong","Sonang satu kapal satu bendero samo rajo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104215,"Jambu mawar ambil seraga,","Buah tangan ke rumah nyonya;","Calon sekampung bermasalah juga,","Mudah balik ke rumah keluarganya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104216,"Beli ikan nk buat masak kari","Rempah di beli kek kodai pktuo","Kalau susah sangat che'gu nk cari","Alamat membujang lah sampai ke tuo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104217,"Buka poso makan bahulu","Nak poso jangan lupo sahor","Che'gu sey stop dulu","Nak solat zohor", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104218,"Anak punai meniti titi","Katak mati tepi perigi","Bayangan didalam mimpi","Semakin parah ko Awi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104219,"Menyanyi nak dara suaranya merdu,","Menatap terbang si rama-rama;","Jiwa kuparah kian merindu,","Wajah dirindu berbayang jelma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104220,"Masam sungguh si buah kundang,","Masam lagi si asam Jawa;","Seberat-berat mata memandang,","Berat bebanan tanggungan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104221,"Pisau takik pisau belati,","Pisau bukan mengupas pelam;","Rindu sendiri simpan di hati,","Rindu bercinta ke serata alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104222,"Merimbun halia tepian huma","Sudah di belah tidak berbaki","Rindu manusia tak kekal lama","Rindu Allah rindu hakiki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104223,"Hari minggu pergi berkelah,","Berendam mandi ke tengah hari;","Rindu hakiki rindukan Allah,","Setelah insan mengenal diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104224,"Dahan jati patah sendiri","Pohonnya redup tepian kuini","Sematkan dihati siapa diri","Baru hidup bertambah harmoni", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104225,"Luka di kaki selirat benta,","Di atas permatang berulang lalu;","Mengenal diri Agungkan Pencipta,","Hidup redha syukur selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104226,"Budak kecil bermain dadu","Dadu terjatuh kedalam parit","Baik dbuang rasa rindu","Lagi dsimpan bertambah perit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104227,"Buluh aur si buluh madu,","Pucuk berjuntai jatuh ke paya;","Alangkah indah mainan rindu,","Titipan kasih buat manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104228,"Kelip-kelip si api-api,","Tampak dari permatang;","Hati dilanda sepi,","Pabila rindu bertandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104229,"Hati-hati menyapu sampah,","Jangan sampai terpijak duri;","Hiasi diri berperibadi indah,","Buang segala hasad dan iri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104230,"Rendang tumbuhnya si ati-ati,","Di tepi kolam di rumpun sena;","Dendam kesumat berumah di hati,","Sepanjang malam tidur tak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104231,"Bunga sekuntum bunga melati","Putih berseri harum baunya","Assalamualaikum dan selamat pagi","Semoga hari ini sehat dan bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104232,"Kembang kelopak si bunga raya,","Selit di rambut tampak berahi;","Tiap detik terhias bahagia,","Tertitip syukur ke hadrat IlLahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104233,"Rembang petang menjelang senja,","Mentari terbenam alangkah indah;","Pusaka bangsa merata dijaja,","Angkara dendam digadai maruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104234,"Meracik buluh pisau belati,","Setelah meracik pisau direndam;","Menyimpan amarah di dalam hati","Menyimpan dendam dalam diam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104235,"Pisau ku tumpul tolong asahkan","Tajamnya jangan terlalu tajam","Jangan disimpan amarah mu tuan","Dendam disimpan jangan berdendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104236,"Awan mendung gelap gelita,","Nenek tua mencari kelkati;","Amarah dendam mainan kata,","Memujuk hati insaf mengerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104237,"Tinggi sungguh pohon meranti,","Berselang pohon si asam jawa;","Kasih dan sayang rahmat Ilahi,","Menyubur syukur di lubuk jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104238,"Ambil parang mari ke hutan,","Mengayam palas sarung ketupat;","Dagang lalu meriah ditanakkan,","Anak sendiri dibiarkan merempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104239,"Buah pulasan si buah berangan,","Pokok ciku berbuah rendah;","Lena tidurku berbantalkan lengan,","Kamarku sempit mimpinya indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104240,"Hutan belukar jangan diredah","Nanti tersesat pulang tiada","Tamu yang datang sekadar singgah","Habis bicara pulang lah dia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104241,"Tok ketua di balai penanggah,","Dijamu orang di air nira;","Tiada salahnya sekadar singgah,","Pulang mengaut khazanah negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104242,"Air nira sedap lagi air kelapa","Penghilang dahaga terik mentari","Benar juga seperti dikata","Lumayan suapan untuk Kabinet Menteri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104243,"Riuh ternakan memakan dedak,","Pagi petang dan tengah hari;","Entah disuap entahkan tidak,","Baca dan fikir rumuskan sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104244,"Budak-budak menarik seludang,","Dara sunti bermain guli;","Biar dijeling asal memandang,","Bicara di hati siapa peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104245,"Dari 'Beat It' bertukar ke 'Bidin',","Gaya tarian menarik hati;","Ayuh ke dapur menggulai sardin,","Menu hidangan tatkala cuti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104246,"Gaya tarian menarik hati,","Tika bermain pondok-pondok;","Menu hidangan tatkala cuti,","Kuahnya boleh dicecah cekodok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104247,"Biji kundang rasanya pahit,","Mari meneguk si air nira;","Telunjuk lurus kelingking berkait,","Manis di mulut berpura-pura.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104248,"Padi di kepuk mari ditampi,","Padi di sawah diserang bena;","Lena tidur diulet mimpi,","Rindu berbisik di dalam lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104249,"Cantik berjalur bulu kecapi","Diruang tamu turun berlaga","Terlena tidur diulet mimpi","Mimpi bertemu bidadari syurga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104250,"Mari bertandang ke rumah nyonya,","Buah tangan seraga anggur;","Semuanya mudah redha kuncinya,","Yakinkan hikmah Allah mengatur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104251,"Isteri redha menempah syurga","Pada mulanya rasa teraniaya","Yang main peranan ketua keluarga","Pincang dia pincanglah semuanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104252,"Merah hitam si biji saga,","Cantik bersinar disinar mentari;","Suami tercinta kuncinya syurga,","Pengorbanan suami buat isteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104253,"Berpintal pintal akar jerami","Panjang bagai seutas tali","Jika berpihak semuanya ke suami","Bagaimana pula nasib sang isteri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104254,"Putih berseri jiwa dan raga","Bersama sidia kesana sini","Setia isteri suami dijaga","Rumahtangga bahgia kekal harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104255,"Manis sungguh si nangka madu,","Nangka diperam di atas para;","Resah jiwa diamuk rindu,","Rindu tak tahu kepada siapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104256,"Manis sungguh si nangka madu,","Nangka diperam di atas para;","Resah jiwa diamuk rindu,","Rindu tak tahu kepada siapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104257,"Cik tipah berbaju kebaya","Cantik bagai puteri kayangan","Dah rindu tak tahu ke siapa","Dah kompom rindu bayangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104258,"Terbang berkejaran si burung balam,","Terbang riang ke sana ke mari;","Rindu bertandang tatkala malam,","Tak mungkin bayangan tanpa mentari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104259,"Buluh perindu alangkah merdu,","Seruling teruna memikat si dara;","Indah terbayang hidup bermadu,","Beralih rasa merubah selera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104260,"Makan nasi gulai peria,","Makan berhidang duduk di pangkin;","Sekadar anganan mengundang ceria,","Bicara di mulut di hati tak mungkin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104261,"Buluh perindu letaknya tinggi","Tetap jauh dari angkasa.","Jangan angan terlalu tinggi","Kesian si isteri memendam rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104262,"Kalau nak rasa hidup bermadu","Kena la cari si anak dara","Kalau di hati dah rasa rindu","Teruskan saja kalau berdaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104263,"Mari menimba ikan di paya,","Ikan keli haruan dan sepat;","Rindu di hati berasa terdaya,","Akad yang dulu tak kering dakwat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104264,"Mencari ikan di dalam paya","Dapat seekor si ikan haruan","Kalau rindu sudah tiada berdaya","Buat la cinta jadi taruhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104265,"Terbang burung hinggap di dahan,","Dahannya tinggi licin dipanjat;","Cinta bukan barang taruhan,","Kekal bertakhta sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104266,"Jangan dicari madu lebah berbisa","Bisanya pedih tak boleh diubati","Jangan diingat bermadu indah dirasa","Namun sakitnya membawa ke mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104267,"Mekar kelopak semerbak wangi,","Kuntuman mawar si bunga kekwa;","Qada dan qadar wajib diimani,","Redha dan syukur mengindahkan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104268,"Anak hindu memakai purdah","Mata belolok nampaklah dagu","Hidup bermadu tidaklah mudah","Hanya seronok mendengar lagu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104269,"Kuih pelita manis berlemak","Hasil tangan si dara jelita","Berjimat cermat dan jangan tamak","Simpanan untuk dihari raya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104270,"Bilo nok rayo kita buek wajid","Masak wajid di rumah pak Ali","Bila nok buka puasa den poei mosjid","Tak perlu gi pasar ramadan tak perlu memboli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104271,"Secubit nasi sekadar menjamah,","Nasi dagang di pasar payang;","Berbuka di masjid bersolat jemaah,","Menjalin ukhuwah berkasih sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104272,"Lalu permatang menuju ke bendang,","Menghijau padi di kiri dan kanan;","Salah bertanya malu bertandang,","Umpama diri debu jalanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104273,"Melambai mesra pelepah kelapa,","Diraut pelepah lidi diikat;","Getar jiwa cinta menyapa,","Sedangkan diri kasih mengikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104274,"Jamuan kenduri dimasak kari,","Tetamu pulang dititipkan jajan;","Jangan dilerai pengikat diri,","Guruh berdentum mungkin tak hujan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104275,"Siput melekat diatas batu","Anak kerenga mati terkapai","Tali mengikat barulah satu","Sedangkan tiga masih melempai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104276,"Pukullah tabuh si kulit kerbau,","Kerbau kampung badannya sasa;","Seorang termimpi tiga mengigau,","Sekadar berpantun bermain bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104277,"Ambil kayu lalu dibelah,","Dinyalakan api merebus ketupat;","Demikian hidup hati berbelah,","Lain dihajat lain yang dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104278,"Dodol dimasak untuk lebaran,","Pujaan hati dibenak minda,","Itulah takdir suratan Tuhan,","Ada hikmah atas segalanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104279,"Mari berkelah dirikan khemah,","Di tepi pantai bermain pasir;","Takdir suratan tersembunyi hikmah,","Kelemahan insan lewat mentafsir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104280,"Masak lemak ikan tenggiri,","Untuk berbuka hari ini,","Ukur baju badan sendiri,","Mampu ke tidak nak banyak bini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104281,"Masak lemak ikan tenggiri,","Mangga muda dihiris dikerat;","Ukur baju menghitung diri,","Seorang isteri sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104282,"Susun batu si batu-bata,","Pagar tembok sempadan jiran;","Berbuka puasa bagaikan pesta,","Selera dilayan tanpa sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104283,"Berbuka makan asam laksa,","Laksa dijual oleh pak Atan,","Layan Nafsu berbuka puasa,","Bila membazir jadi gang Syaitan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104284,"Buat ulam pucuk pegaga,","Masak pedas sirap secangkir;","Melayan selera biarlah berpada,","Insafi diri ingatkan yang fakir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104285,"Segak dan tampan berbadan sasa,","Hasil main gym setiap hari,","Cari sebenar pengertian puasa,","Pasti dapat menginsafi diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104286,"Masak sahur dinihari,","Minum teh serbuk halia;","Mengerti puasa menginsafi diri,","Puasa membentuk peribadi mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104287,"Perigi buta kontang sentiasa","Tempat mainan si awang kenit","Banyak berhelah menambah dosa","Poket bocor tapi nafsu melangit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104288,"Seruling buluh ditiup merdu,","Duduk meniup di tepi sawah;","Tiada indahnya hidup bermadu,","Setiap detik mencipta helah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104289,"Asam gelugur dijemur kering,","Diperah air si asam jawa;","Nafsu membazir sering seiring,","Moga Ramadhan mendidik jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104290,"Jawatan disandang sebagai nazir,","Ramailah yang menghormati,","Perancangan awal tak sure elak bazir,","Sampai masanya nanti NAFSU dituruti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104291,"Jawatan disandang sebagai nazir,","Ramailah yang menghormati,","Perancangan awal tak sure elak bazir,","Sampai masanya nanti NAFSU dituruti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104292,"Lebat hujan menjelang senja,","Resah di hati mungkinkah banjir;","Perancangan awal bijak berbelanja,","Mengawal diri elakkan membazir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104293,"Berhati hati meniti titi,","Langkah diatur dan dikawal,","Fikir juadah untuk petang nanti,","Itu namanya perancangan awal.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104294,"Merah sungguh masak pulasan,","Manis buahnya bagaikan nira;","Menu pelbagai kreatifnya insan,","Aneka menu melayan selera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104295,"Sidai baju dilaman rumah,","Sayangnya mentari cahaya suram,","Itu dah lumrah dan juga fitrah,","Menu tak sudah jadinya ke malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104296,"Subur menjalar akar dedalu,","Melilit dahan si pohon nangka;","Baru seketika sahur berlalu,","Telah berbincang menu berbuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104297,"Mana anak hendak percaya","Masak apa semua disapu","Anak dah tahu kepakaran ayahnya","Disekolah guru didapur pula sifu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104298,"Tambah wangi lemaknya nasi,","Masukkan gumpalan si daun pandan;","Tumpahnya kuah pasti ke nasi,","Ayah umpama contoh teladan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104299,"Kata bulan ramadhan tiada hantu,","Tapi manusia yang jadi Hantu,","Tak hairan perangai anak begitu,","Kerana bapanya juga dulu sebegitu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104300,"Nyaring berbunyi si itik angsa,","Lehernya panjang makan menyudu;","Anak manjaku meragam berpuasa,","Asyik bertanya menu dan waktu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104301,"Periuk antik berlejuk-lekuk,","Diambil dari atas para;","Hasrat anak ditumpang semangkuk,","Keluarga bahagia hasrat si bapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104302,"Dalam periuk nasi ditanak,","Periuk antik berlekuk lekuk,","Bukan sahaja permintaan anak,","Isteri juga tumpang semangkuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104303,"Mangkuk tingkat empat perenggan,","Buah tangan ke rumah nyonya;","Andai permintaan anak dilayan,","Satu bazar ingin dibelinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104304,"Bakar sampah asap berkepul,","Dibawa angin menusuk ke mata;","Meriah berbuka keluarga berkumpul,","Biar sedikit semua merasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104305,"Sayup terdengar sayunya ungka,","Sambil bergayutan dahan ke dahan;","Bersama bonda di dapur pusaka,","Badak berendam menu pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104306,"Tinggi kelapa berselang rambutan,","Mendongak terlihat sarang tempua;","Untung rugi bukannya alasan,","Dijenguk rindu titipkan doa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104307,"Lalu titi tepian bonda","Sambil ditangan memakan ciku","Untungnya awak masih berbonda","Tercuit dihati mengundang rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104308,"Ke Kuala Perlis makan kerang,","Kerang dicicah cili berkicap;","Dalam gelap tiada yang terang,","Dalam terang banyak yang gelap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104309,"Awal pagi mandi di perigi,","Di hujung kampung si pohon ara;","Apa disesal cinta yang pergi,","Cinta pengganti mengintai mesra.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104310,"Ayer perigi sangat la sejuk,","Mandi pula di hujung kampung,","Kalau muka dah mcm tembok,","Sampai kesudah cinta tak datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104311,"Akhir besi akan berkarat,","Sama juga pisau belati;","Cinta tulus tanpa syarat,","Subur di jiwa mekar di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104312,"Jahit kebaya bersulam tangan,","Renda-renda berwarna merah;","Saf tarawikh kian berkurangan,","Bazar Ramadhan kian meriah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104313,"Luruh ke tanah pelepah kelapa,","Ke unggun api mari dipunggah;","Rindu gerangan singgah menyapa,","Seimbas lalu tersilau wajah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104314,"Goreng ikan campur pegaga,","Cili dan bawang buat penumis;","Puasa menggoda juga menduga,","Air paip pun berasa manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104315,"Tempoyak goreng sajian tok wan","Buat santapan kak leha janda","Begitu sekali fikiran tuan","Nafsu ditentang berkat pahala berganda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104316,"Pasang pelita menjelang senja,","Berjalan ke masjid bersama teman;","Mengimbau kenangan tatkala remaja,","Pelantar lonjakan keteguhan iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104317,"Batas halia dikais kais","Di rata rata mengikut suka","Itulah dia permainan iblis","Mengajak kita masuk neraka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104318,"Lincah di muara anak seluang;","Sepat benua celahan benta;","Kasih tersisih sayang terbuang,","Ceria di wajah di hati derita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104319,"Dalam sampan buah keranji,","Ambil sebiji rasa bersama;","Derita itu Tuhan menguji,","Sakit sedikit penghapus dosa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104320,"Di pagi hari menanam selasih,","Selasih ditanam ditepi pintu,","Usah di ikut hati yang sedih,","Kita merancang Allah menentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104321,"Paya bakau si tanah rawa,","Rimbun pokok benteng penampan;","Jangan bersedih jangan kecewa,","Redha dan syukur takdir-Nya Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104322,"Rendang ketapang singgah berlindung,","Cantik berbunga semarak api;","Hadir pelangi mengusir mendung,","Hadirmu dinda mengusir sepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104323,"Tajam sungguh duri semalu,","Tercucuk jari alangkah pedih;","Sering teringat hatiku pilu,","Sering mengenang jiwaku sedih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104324,"Putih bersih si ulas manggis,","Jangan disimpan di dalam peti;","Biarpun hitam dipandang manis,","Manis senyum santun pekerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104325,"Kain batik disusun bertingkat,","Kain pelikat dalam almari;","Tulus berdoa memohon hasrat,","Yakin suratan takdir IlLahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104326,"Burung tiung si burung merbah,","Hinggap di pohon asam jawa;","Takdir tertulis takkan berubah,","Syukur dan redha kedamaian jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104327,"Putih berkepul awan-gemawan,","Tampak dari Tanah Jawa;","Jangan dilayan hati nan rawan,","Kelak membarah derita jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104328,"Bakar lemang kacau penganan,","Ubi kayu dibuat bingka;","Ingin lena tidur di lengan,","Membelai kasih Subuh menyapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104329,"Tinggi-tinggi si pokok sena,","Tebang rumbia diambil sagu;","Rembulan indah hilang ke mana,","Pungguk setia hiba berlagu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104330,"Tumbuh berjejer rumpun selasih,","Bunga raya mekarnya merah;","Duka di hati cinta tersisih,","Salam dihulur dilayan amarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104331,"Pulai condong sekah mempelam,","Dahan dipotong getah mengalir;","Salam Maghrib memasuki malam,","Merehatkan zahir batin berzikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104332,"Riang ria bercanda ketawa,","Tidak terasa luka tercalar;","Sunder dan Gopal sahabat sejiwa,","Radha mencelah cinta tercabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104333,"Jahit tangan bersulam di tepi,","Kurung Kedah lengan ke siku;","Menonton SANGAM mengundang mimpi,","Cerita cinta duka berliku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104334,"Wang Tepus Wang Kelian,","Pasar pagi setiap Sabtu;","Tertitip ucapan daripada kalian,","Esok kujawab satu persatu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104335,"Jauh berpergi dirantau orang","Mencari rezeki berserta ilmu","Jiwa mendesak, hati melarang","Aku menanti jawap dari mu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104336,"Guni pasir buat penampan,","Pasang jerat pasang perangkap;","Tentunya sukar memberi jawaban,","Sedang soalan belum terungkap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104337,"Mempelam rendang jangan ditebang,","Tempat berteduh berehat diri;","Terang malam rembulan mengambang,","Bertemu adinda di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104338,"Tumbuh keladi di tepi paya,","Mekar membiak kian merimbun;","Rumah terbuka rumah budaya,","Terzahir budi termaklum santun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104339,"Tanam serai di penjuru halaman,","Bawa ke pasar di bakul rotan;","Pimpinan bangsa hilang pedoman,","Berpatah arang berkerat rotan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104340,"Hujung minggu pergi berburu,","Pergi berburu ke Tanah Rata;","Jangan dientengkan jasanya guru,","Tanpa guru bagaimanakah kita?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104341,"Terluka hati mainan cinta,","Berlidah dusta berjanji mungkir,","Berserah hamba kepada Yang Esa,","Agar dipandu ke sebaik takdir!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104342,"Kuih bingka tepung pelita,","Terhidang di meja minuman secangkir;","Terluka hati mainan cinta,","Berlidah dusta berjanji mungkir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104343,"Ikhlas mendidik barakah ilmu,","Pahala mengalir tiada batasan,","Asalkan Ilmu yang Allah restu,","Batil dan haq diberi penjelasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104344,"Wanita anggun menjual jamu,","Lencun berpeluh dek kepanasan;","Ikhlas mendidik baraqah ilmu,","Pahala mengalir tiada batasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104345,"Sampan kolek perahu panjang,","Berlabuh perahu nakhoda ke kota;","Tika jauh bicara berkajang,","Pabila semuka kehilangan kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104346,"Debur ombak di tengah segara,","Sedang di pantai berpuput bayu;","Tutur di bibir benci membara,","Bisikan hati gelora rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104347,"Ulam raja si pucuk pegaga,","Ikan goreng dicicah budu;","Ranum senja didakap mega,","Tunduk syahdu di sejadah rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104348,"Dara jelita berdagang jamu,","Air seteguk menghilang kehausan;","Harta guru buku dan ilmu,","Tertitip ganjaran tanpa batasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104349,"Indah alam direnung tak jemu,","Indah sempurna cacat tiada;","Jadilah guru pendidik berilmu,","Memperkaya minda memperteguh jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104350,"Tanah berpasir ditanam maman,","Maman penyedap si daging rendang;","Jadilah diri suami budiman,","Jangan menengking mudah meradang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104351,"Ke pantai berselut mencari lokan,","Kumpulkan lokan ke dalam raga;","Sayang anak jangan dimanjakan,","Sayang isteri diperhambakan tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104352,"Burung balam burung tekukur,","Diumpan diangguk diberi makan;","Isteri baik menunduk akur,","Menengking bicara elok pisahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104353,"Dayung sampan arah ke kuala,","Tambat sampan lalu berjalan;","Kasihkan anak berkorban segala,","Kasihkan isteri berbatas sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104354,"Sampan kolek dayung ke muara,","Lalu ditambat ke tiang jeti;","Rindu menggila cinta bergelora,","Damaikan jiwa istigfar di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104355,"Tinggi terbangnya si burung helang,","Menjunam terbang terlihat mangsa;","Rindu di hati bilakan hilang,","Kian diingat rindu menggila.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104356,"Buah ranum jangan diperam,","Mari menggoreng buah berangan;","Inginku lena mata tak pejam,","Ingin kupejam diziarah kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104357,"Terbang pepatung hinggap merata,","Di tepi kolam si ati-ati;","Dalam gelita mengintai cinta,","Cinta mesra di lubuk hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104358,"Merah ranum buah pulasan,","Rimbun merendang ke rumah sebelah;","Biar sombong di mata insan,","Ramah mesra di penglihatan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104359,"Kapal belayar berbunyi serombong,","Arah belayar ke terbit mentari;","Sedang ramah dituduh sombong,","Demikian cermin mencermin diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104360,"Bukan bayu meniup lalang,","Taufan lalu ributnya kencang;","Tika senja waktunya pulang,","Melepas lelah berehat panjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104361,"Andai datang membawa badai","Pulangnya pula membawa bayu","Jangan ditafsir usah diandai","Baru berputik terkulai layu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104362,"Anak manja merajuk dijentik,","Bancuhkan susu diberi minum;","Elok terkulai baru berputik,","Jangan busuk ketika ranum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104363,"Menanti malam indah rembulan,","Menjelma rembulan ketika senja","Meraikan Syawal penuh sebulan,","Ukhuwah dan kemaafan subur di jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104364,"Rumah limas di penjuru kampung,","Menyiram bunga air perigi;","Syawal ceria tiba ke penghujung,","Tertitip takdir ketemu lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104365,"Indah mekarnya kuntum melati,","Akhirnya layu ke tanahnya gugur;","Rindu hakiki ranum di hati,","Sejak azali kini menyubur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104366,"Pergi seberang lalu di titi,","Mangkuk tingkat lima pangsa;","Berpantun daku menghibur hati,","Semarakkan bahasa budaya bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104367,"Dara pingitan pandai bersolek,","Tertib laku rajin bak dayang;","Yang bulat tak datang menggolek,","Yang pipih tak datang melayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104368,"Lepas ikatan perahu di dayung,","Tika pagi dipuput bayu;","Andai tidak dipecahkan ruyung,","Mana mungkin mendapat sagu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104369,"Hutan gunung tampak berbalam,","Ilham melukis anak remaja;","Siang telah menyapa malam,","Usia telah menyapa senja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104370,"Kucing mengiau dimarah jangan,","Bimbang lari ke dalam kelambu;","Teringin tidur berbantal lengan,","Menatap cicak di siling bercumbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104371,"Penglipur lara membaca hikayat;","Cerita hiba air mata berderai","Bergaduh kucing bertambah zuriat,","Bergaduh manusia mengapa bercerai?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104372,"Pohon mangga pohon mempelam,","Berselang tumbuh pohon durian;","Senja merangkak ke ranjang malam,","Usia merangkak ke ruang kematian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104373,"Bunga di taman aneka warna,","Susun berbaris ke arah tangga;","Mendakap sepi di ranjang lena,","Membelai mimpi di dalam jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104374,"Hati-hati menyusun bata,","Retak bata terluka jari;","Harta benda mainan mata,","Kasih sayang hiasan nurani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104375,"Merah bara di puntung hitam,","Padam pelita letak ke tepi,","Mengeluh lara gelita malam,","Menanti cinta mengulet mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104376,"Merah bara di puntung hitam,","Padam pelita letak ke tepi,","Mengeluh lara gelita malam,","Menanti cinta mengulet mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104377,"Di hujung kampung si pohon ara,","Berjalan lalu jangan ditegur;","Senyuman bonda merawat lara,","Bicara bonda hati terhibur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104378,"Mengandar benih untuk disemai,","Benih secekak diikat-ikat;","Pulang ke kampung jiwaku damai,","Mendakap bonda memohon berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104379,"Mari ke dusun mengutip manggis,","Menggoreng jagung dibuat bertih;","Zahir ketawa batin menangis,","Hati terluka jiwa merintih,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104380,"Zahir ketawa menangisnya batin.","Hati terluka merintihnya jiwa,","Dengan Allah berserah dan yakin,","Ubat mujarab bagi segala duka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104381,"Bumi mana tak ditimpa hujan,","Lautan mana tak pernah bergelora,","Suka dan duka merupakan mainan,","Selagi hidup di alam fana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104382,"Kita hamba dijadikan Tuhan,","Hidup didunia penuh ujian,","Siapa antaranya yang penuh iman,","Hamba2 inilah didalam kemenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104383,"Masak pedas ubi keladi,","Bawa bekal ke rumah nyonya;","Kepada Allah kita mengabdi,","Ikhtiar berserah pada takdir-Nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104384,"Indah lembayung di pantai Mersing,","Debur ombak mengocak sepi;","Tinggal sebumbung hati terasing,","Merawat hati mahligai mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104385,"Pucuk labu si pucuk cekur,","Ambil keladi tumbuh di paya;","Ceria bonda lincah di dapur,","Gembira anak balik beraya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104386,"Pedih jari digigit kerengga,","Sapukan minyak pedihnya kebah;","Bicara jujur orang curiga,","Bicara dusta jiwa gelisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104387,"Pelepah nyiur saling membelai,","Angin bertiup mengusap manja;","Hidup bersama jiwa bercerai,","Menatap anak takdir-Mu kuredha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104388,"Kelapa parut mari diperah,","Santan bergula buat serawa;","Sakit yang terasa indah,","Redha penawar rasa kecewa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104389,"Buah berangan isinya pahit,","Jangan d makan nti sendawa,","Mngharap kasihnya org memng sakit,","Jangn mencuba sebelum kecewa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104390,"Baju kebaya berkain sarung,","Dipakai anggun ke majlis kenduri;","Cincin tidak sendiri tersarung,","Tanpa penggali liang tak jadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104391,"Jalan-jalan ke Kuala berang ,","Singgah sekejap di muara kiri,","Tk pernah mngharap kasihnya orang ,","Sentiasa sedar kekurangan diri..", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104392,"Petik kecapi makan berangan,","Berdendang sayu menjelang petang;","Kasih orang diharap jangan,","Bagai melukut di tepi gantang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104393,"Wangi semerbak daunan pandan,","Perasa pengat ditambah gula;","Cinta Allah tanpa sempadan,","Cinta manusia bersyarat segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104394,"Kelip-kelip di Kuala Sepetang,","Cahaya bagai semarak api","Sedang melukut di tepi gantang,","Padi hampa terbuang ditampi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104395,"Tinggi bersarang burung tempua,","Merata-rata sarang merpati;","Lebuh raya milik semua,","Berkongsi sabar saling menghormati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104396,"Berkawan burung terbang ke sarang,","Setelah seharian bertebaran","Sabar menanti senja menjelang,","Pengumuman tarikh beraya dizahirkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104397,"Ulasnya sendat seulas nangka,","Halwa seperiuk tetamu dihidang,","Sanggahan adat hakikat terlupa,","Kitab dirujuk sekilas pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104398,"Halwa seperiuk tetamu dihidang,","Tutur bicara mesra kenalan,","Kitab dirujuk sekilas pandang,","Teruna dara mangsa kejahilan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104399,"Lebat berbuah kelapa pandan,","Kelapa diparut dibuat inti,","'Selamat Hari Raya' kepadamu Tuan,","Syawal hadapan bertemu lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104400,"Ku kirim salam dri kejauhan,","Utkmu sahabat yang budiman,","Salam Syawal untukmu tuan,","Raikankanlah dengan penuh ksyukuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104401,"Hati-hati meneguk ketum,","Aduan awam polis mengepung;","Hujan lebat guruh berdentum,","Ke manakah air lari berkampung?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104402,"Taktibau pulang petang,","Ambil kain dari ampai;","Yang ditunggu tak datang,","Yang lain pula sampai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104403,"Daging salai dicampur rebung,","Menu hidangan makan sekeluarga;","Ayuh kita pulang berkampung,","Beramai-ramai kita beraya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104404,"Gelombang dan badai di pentas lautan,","Taufan dan bayu saling bertemu;","Ilmu dan imaginasi ilham titipan,","Tinggi luas makamnya ilmu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104405,"Hendak ke sumur lewat permatang,","Berhati-hati melangkah perlahan;","Ketupat lemang rendang terhidang,","Meriah beraya raikan taulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104406,"Elok-elok meniti titi,","Jangan patah buluh pemegang,","Sahur terakhir Ramadhan ini,","Moga ketemu Ramadhan mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104407,"Masak cempedak gugurnya lumat,","Ulasnya sendat seulas nangka,","Takdir tak mungkin tersalah alamat,","Sanggahan adat hakikat terlupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104408,"Dibina pondok menumpang singgah,","Masak cempedak gugurnya lumat,","Cinta berjodoh takdir menyanggah,","Takdir tak mungkin tersalah alamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104409,"Berbunga manggis batasan rimba,","Dibina pondok menumpang singgah,","Hati menangis dirundung hiba,","Cinta berjodoh takdir menyanggah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104410,"Mekar cantik bunga kemunting,","Berbunga manggis batasan rimba,","Cinta berputik tak sempat disunting,","Hati menangis dirundung hiba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104411,"Pelepah ijuk mereput terendam,","Mekar cantik bunga kemunting,","Anak merajuk derita dipendam,","Cinta berputik tak sempat disunting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104412,"Burung enggang hujung belukar,","Pelepah ijuk mereput terendam,","Adat dipegang dirunding sukar,","Anak merajuk derita dipendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104413,"Terbang pulang senja menjelang,","Burung enggang hujung belukar,","Cinta hilang kasih terhalang,","Adat dipegang dirunding sukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104414,"Pulang ke sarang burung taktibau,","Terbang pulang senja menjelang,","Duduk seorang kenangan diimbau,","Cinta hilang kasih terhalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104415,"Merbah batu di dahan sekah,","Dahan mereput buahnya lerai,","Diseru bersatu terpisah langkah,","Kuasa direbut maruah tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104416,"Ayunan lenggang siulan bujang,","Membeli benang penuh seraga","Sorak pejuang sejarah berulang,","Merebut menang buat keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104417,"Hujung minggu pergi berburu,","Pergi berburu ke Tanah Rata;","Jangan dientengkan jasanya guru,","Tanpa guru bagaimanakah kita?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104418,"Tumbuh keladi di tepi paya,","Mekar membiak kian merimbun;","Rumah terbuka rumah budaya,","Terzahir budi termaklum santun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104419,"Tanam serai di penjuru halaman,","Bawa ke pasar di bakul rotan;","Pimpinan bangsa hilang pedoman,","Berpatah arang berkerat rotan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104420,"Kupas kelapa diambil sabut,","Kelapa tumbuh di tepi perigi;","Salam tak berjawab, tegur tak bersambut,","Relalah daku mengundur diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104421,"Menjelang senja pasang pelita,","Terpadam api sumbunya basah;","Andai bicara berbunga dusta,","Hidup derita jiwa gelisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104422,"Harum manis pohonnya rendah,","Rendang berbuah manis sekali;","Cuti beraya selesai sudah,","Kembali bertugas mencari rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104423,"Dicucuh api meriam buluh,","Bunyi bergema bingit telinga;","Andai diuji jangan mengeluh,","Berserahlah diri kepada-Nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104424,"Batik Jawa kebaya nyonya,","Bersulam bunga labuci merah;","Duit raya terbudaya akhirnya,","Terhakis makna ketulusan berziarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104425,"Duduk bersembang ceritanya lucu,","Sambil makan kuih keria;","Beraya di kampung menantu dan cucu,","Bonda gembira nenek ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104426,"Pagi pagi mngemas rumah,","Juadah raya sedia mnnti,","Taulan dtg kusambut ramah,","Agar pulangnya nnti gembira pasti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104427,"Usah direngkuh dahan bidara,","Terkena tebar kasau bersilang,","Berdusta sungguh cinta negara,","Bendera dikibar terbalik di tiang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104428,"Lebat berbuah kelapa pandan,","Kelapa diparut dibuat inti;","Ceritera lalu imbauan teladan,","Menyelak harapan di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104429,"Cantik corak tirai di pintu,","Lipat beropol ikat ke dinding;","Pada kenangan hatiku rindu,","Akad nikah duduk bersanding.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104430,"Dingin terasa air di kali,","Membasah tekak musafir lalu;","Salah kita diungkitnya berkali,","Salah dia dinafikan bertalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104431,"Ayam jantan tajam bertaji,","Di pangkal pohon bunga raya;","Si ayah mengira hari gaji,","Si anak menghitung duit beraya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104432,"Merah kelopak si bunga raya,","Berselang tumbuh si ati-ati;","Si anak menghitung duit beraya,","Si ayah menjeling penuh bererti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104433,"Duduk mencangkung tepi permatang;","Melihat pepatung juga rerama;","Yang ditunggu belum datang,","Yang dimimpi belum menjelma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104434,"Benderang mentari unggas menyanyi,","Riang siulan bertali-temali;","Teduh hujan muncul pelangi,","Terukir senyum cinta bersemi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104435,"Ayu nak dara pulang berlenggang,","Bersanggul rapi di celah telinga;","Bagai melukut di tepi gantang,","Hadir tidak membawa makna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104436,"Pokok meranti juga akasia,","Tumbuh di hutan bertatatingkat;","Demikian tercatat resam manusia,","Memilih darjat rupa dan pangkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104437,"Bersahutan bunyi burung ketitir,","Bagai menyapa sinar mentari;","Sekadar kata di hujung bibir,","Sekadar titipan di hujung jari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104438,"Padi beras padi pulut,","Cendol pulut campur durian;","Pelihara lidah jagalah mulut,","Terlepas kata menyesal jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104439,"Pagi2 menanam bayam,","Bayam ditanam tepi perigi,","Hamba ucapkan Selamat malam","Dipanjang umur berjumpa lagi..", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104440,"Ketupat palas dan nasi himpit,","Makan rendang dan kuah kacang;","Orang bahagia tidur berhimpit,","Kita ngelamun lena seorang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104441,"Busana sedondon berhari raya,","Bersampin pelikat ikat ke tepi;","Merah menyala tampak bergaya,","Sekadar memiliki di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104442,"Sembang santai di hujung beranda;","Sambil dijamu makan penganan;","Pesan ayah terpahat di minda,","Menambah boleh membuang jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104443,"Oh! Sekarang baru ku tahu,","Tuan Zawawi seorang guru","Dalam pantun diselit ilmu","Itulah sesungguhnya guru bermutu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104444,"Tinggi bersarang burung tempua,","Di hujung pelepah kelapa;","Menuju syurga impian semua,","Redha Allah kunci pembuka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104445,"Sebaik kejadian manusia dicipta,","Hamba & Khalifah dihadapan Allah,","Marilah beramal menuju kesyurga,","Dijauhi Larangan dilaksana Perentah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104446,"Ambil air dari telaga,","Berjalan pulang dari sawah;","Sebaik kejadian manusia dicipta,","Hamba dan Khalifah di hadapan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104447,"Ambil air dari telaga,","Berjalan pulang dari sawah,","Dengan Allah kita hanyalah hamba,","Walau dinobat sebagai Khalifah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104448,"Teratai tepi kolam","Ati-ati pelbagai rupa","Kemari tanpa salam","Pasti tak jumpa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104449,"Teratai tumbuh tepian kolam,","Ati-ati berdaun jingga,","Andai sungguh kutitip salam,","Hasrat di hati tertunai juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104450,"Pengantin muda ke pasar tamu,","Indah busana tenunan tangan,","Orang muda kasih merindu,","Sukar lena mengimbau kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104451,"Merdu nyanyi unggas di dahan,","Nyelinap puyuh celah rumpunan,","Biarlah sunyi dalam keriuhan,","Jangan riuh dalam kesepian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104452,"Ambil nyiru di pondok sawah,","Pasang pelita hari dah senja,","Usah ditiru perangai Si Tuah,","Akur membuta ingkar tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104453,"Padi segemal beras sesukat,","Periuk remuk letak di lokar,","Usah diamal perangai Si Jebat,","Marahkan nyamuk kelambu dibakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104454,"Terperap di bilik nasi berkerak,","Beras ditanak pulut dikukus,","Teladani cerdik bijak Tun Perak,","Tepung tak serak rambut tak putus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104455,"Sarung songket susun berlipat,","Kebaya labuh tersarung sopan;","Hidup berumah mati bertempat,","Amal secebis jadi taruhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104456,"Sarung songket susun berlipat,","Kebaya labuh bersarong sopan,","Ikhlas beramal pahala mendapat,","Dari Allah dikurnia ganjaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104457,"Ambil air dari telaga,","Berjalan pulang dari sawah;","Andai derhaka kepada bonda,","Hidup sengsara biarpun mewah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104458,"Mempelam rendang jangan ditebang,","Tempat berteduh berehat diri;","Terang malam rembulan mengambang,","Bertemu adinda di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104459,"Nasi bekal mangkuk bertingkat,","Buat bekalan tika berjaja;","Meningkat usia kematian mendekat,","Sedangkan jiwa serasa remaja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104460,"Susun pasu di atas bata,","Mari disusun di hujung halaman;","Hendak berperi kehilangan kata,","Hendak bicara kehilangan teman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104461,"Mekar teratai di paya rawa,","Selut diredah tertusuk duri,","Mari dilerai derita di jiwa,","Kenangan indah simpan sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104462,"Pasang khemah mempelai diarak,","Hiasan renda digantung- gantung,","Empunya rumah berebut sorak,","Tetamu gembira berebut untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104463,"Indah teratai penyeri kolam,","Ati-ati menghias tebingan,","Andai berkecai dibadai alam,","Cinta di hati terlepas jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104464,"Pasang pelita letak di tangga,","Hujan lebat menjelang senja,","Air mata terapi derita,","Ibarat ubat pencuci luka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104465,"Teratai penyeri kolam","Ati-ati pelbagai warna","Tuan dicari serata alam","Mandi tak basah tidur tak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104466,"kini teratai layu sudah","hidup sendirian tanpa berteman","diri dikecewa tk sudah2","hanya airmata sedia meneman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104467,"Teratai indah penyeri kolam,","Ati-ati mereput di batang,","Usah diredah serata alam,","Seru di hati pasti kudatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104468,"Terperap di bilik nasi berkerak,","Beras ditanak pulut dikukus,","Teladani cerdik bijak Tun Perak,","Tepung tak serak rambut tak putus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104469,"Emas suasa hias kalungan,","Leher jinjang dara jelita,","Budi bahasa jadi gurauan,","Gelar dijulang harta bertakhta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104470,"Petai belalang ulaman budak,","Tumbuh subur tepi tebingan,","Kasihkan orang berbalas tidak,","Impian lebur menjadi kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104471,"Buat penyapu diraut pelepah,","Nyiru penampi susun teratur,","Padam lampu melabuh lelah,","Mengukir mimpi berdakap tidur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104472,"Tumbuh selasih tidak berduri,","Daun halus kembang merimbun,","Salam kasih sehangat mentari,","Salam tulus sejernih embun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104473,"Ungu muda warnanya baju,","Baju Melayu cekak musang;","Detik berpisah hatiku sayu,","Dirasakan malam amatlah panjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104474,"Itik bemban itik serati,","Itik angsa lehernya panjang;","Redup wajah tangis di hati,","Cinta dimungkir janji berkajang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104475,"Cantik senyuman sentiasa bersugi,","Sesudah berbuka juga bersahur;","Muslimin muslimah ke Tanah Suci,","Moga beroleh Haji Mabrur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104476,"Gulai pedas sambal mangga,","Mangga dihiris lalu direndam;","Andai anak ditegur tetangga,","Hargai segera elakkan dendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104477,"Beras baharu mari ditanak,","Nasi dimakan berulam pegaga;","Seorang ibu melahirkan anak,","Jiran tetangga turut menjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104478,"Mengangin padi tikar ke tepi,","Padi bernas dan padi hampa;","Lena malam dijamu mimpi,","Mimpi bertemu entah siapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104479,"Alangkah dingin air perigi,","Bagai menusuk tulang-temulang;","Orang tamak mungkin tak rugi,","Beroleh untung berganda ulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104480,"Penternak ayam menaburkan dedak,","Jagung dan nasik makanan biasa,","Untung rugi mainan budak,","Untung bosa kepada yang berkuasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104481,"Di padang pasir batu kerikil,","Panas hangat menusuk kulit;","Sementara hidup janganlah bakhil,","Hidup sempit sentiasa sulit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104482,"Membiru langit tanpa rembulan,","Indah germerlapan kerdipan bintang;","Setia bercinta bukan khayalan,","Sekadar usia menjadi penghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104483,"Indah lembayung di dada langit,","Terbang burung pulang ke sarang;","Merebut cinta alangkah sengit,","Setelah miliki janganlah curang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104484,"Pahit ubat makan ditelan,","Tekak mual serasa loya;","Gersang tanah disirami hujan,","Gersang jiwa disirami cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104485,"Pelepah kelapa jatuh bertimpa,","Ambil galah buah dipetik;","Sebatang kara memanggang derita,","Rindukan bahagia cuma sedetik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104486,"Hujan di hulu belum reda,","Keruh air di muara;","Kasih dan cinta berpada-pada,","Kelak kecewa jiwa sengsara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104487,"Pergi ke hutan memburu denak,","Bawa bekal di dalam raga;","Garang harimau boleh dijinak,","Garang manusia sukar diduga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104488,"Pelepah kelapa beralun gemalai,","Batang di takik persis tangga,","Sebelum roh dan jasad bercerai,","Kepada Allah dan insan ditabur jasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104489,"Pelepah kelapa beralun gemalai,","Batang ditakik persis tangga;","Roh dan jasad kelak bercerai,","Amal dan bakti terpahat nama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104490,"Pelepah kelapa beralun gemalai,","Batang ditakik persis tangga,","Apabila urusan dunia sudah selesai,","Kembalilah kita kepada Yang Esa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104491,"Pelepah kelapa beralun gemalai,","Batang ditakik persis tangga;","Urusan dunia sukar selesai,","Hasrat manusia datang bertimpa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104492,"Hujan menderu gerimis pagi,","Daun bergeser bagai menyapa;","Biar pedih tertusuk hati,","Pujuk nurani utuhkan jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104493,"Ambilkan nyiru mari menampi,","Pijak lesung kaki tersepit;","Malam dini kian sepi,","Suara berbisik bagai menjerit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104494,"Pokok jati dan pokok sentang,","Di tanam tepi lebuh raya;","Gulai sardin campur kentang,","Makan malam lebih selera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104495,"Lepaskan tali dayung perahu;","Dayung perahu arah muara;","Hati menangis orang tak tahu,","Wajah ceria canda jenaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104496,"Ambil nyiru di pondok sawah,","Pasang pelita hari dah senja,","Usah ditiru perangai Si Tuah,","Akur membuta ingkar tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104497,"Padi segemal beras sesukat,","Periuk remuk letak di lokar,","Usah diamal perangai Si Jebat,","Marahkan nyamuk kelambu dibakar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104498,"Angin malam menyapa mesra,","Menyelinap lalu celah jeriji;","Sepi malam jiwa terdera,","Menanti kan hadirnya bidadari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104499,"Pergi ke hutan mencari palas,","Daun dihimpun ke dalam guni;","Bibir bagai limau seulas,","Mesra senyuman menyapa nurani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104500,"Indah pelangi di dada langit,","Bagai tersangkut di ranting akasia;","Senyum terukir mata mengenyit,","Tersirat makna penuh rahsia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104501,"Jarang tempua bersarang rendah,","Di pohon nyiur pelepah yang tinggi;","Puas kupujuk tidak diendah,","Mengundur diri melangkah pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104502,"Indah menghijau saujana bendang,","Tampak dari perdu bidara;","Biar buruk wajah dipandang,","Asalkan elok tutur bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104503,"Dara jelita jalan berlenggang,","Disapa mesra manis jelingan;","Aku pipit tak setara enggang,","Bagai mentimun dengan durian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104504,"Sekacip pinang sekapur sirih,","Salam beradat warisan budaya;","Hatiku duka jiwaku pedih,","Jujur bicaraku masih diperdaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104505,"Gelita malam pasang lantera,","Penuhkan minyak barang secangkir;","Benarkah perihal empunya ceritera,","Sengsaralah hidup mimpi yang mungkir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104506,"Elok-elok berjalan di titi,","Di belakang sekawan itik angsa;","Unggul di minda cemerlang di hati,","Citra warga negara bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104507,"Pelepah kelapa beralun gemalai,","Batang ditakik persis tangga;","Biar tubuh luluh berkecai,","Untuk anak kurempuh juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104508,"Gelap cuaca meredup lindungan,","Pohon berbunga gugur melayang,","Doa dibaca penyambung hubungan,","Tuhan empunya kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104509,"Tumbuh selasih tidak berduri,","Daun halus kembang merimbun,","Salam kasih sehangat mentari,","Salam tulus sejernih embun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104510,"Pohon perah pohon cenerai,","Pucuk ara ranting berangan,","Kasih berbalah cinta terlerai,","Dendam membara hangus kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104511,"Burung bersarang di pohon sena,","Di periuk kera sembunyi katak,","Kasihnya orang kerana berguna,","Kasih saudara berganti tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104512,"Di periuk kera sembunyi katak,","Sedangkan ungka mencari hama,","Kasih saudara berganti tidak,","Bermasam muka sebentar cuma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104513,"Sedangkan ungka mencari hama,","Di dahan perah di pohon meranti,","Bermasam muka seketika cuma,","Warisan darah tak mungkin terhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104514,"Di dahan perah di pohon meranti,","Berdentum guruh paluan tabuh,","Warisan darah tak mungkin berhenti,","Melainkan roh meninggalkan tubuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104515,"Berdentum guruh paluan tabuh,","Gemuruh dentuman gema di hutan,","Melainkan roh meninggalkan tubuh,","Kekal kenangan dalam ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104516,"Gemuruh dentuman gema di hutan,","Gelap cuaca meredup lindungan,","Kekal kenangan dalam ingatan,","Doa dibaca penyambung hubungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104517,"Gelap cuaca meredup lindungan,","Pohon berbunga gugur melayang,","Doa dibaca penyambung hubungan,","Tuhan empunya kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104518,"Jeruk kedondong si asam jawa,","Jeruk kundang jeruk mempelam;","Jangan ramaikan rakan ketawa,","Di luar benderang di hati kelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104519,"Lumpur selut di paya rawa,","Berdarah luka dicarik benta;","Orang ketawa kita ketawa,","Sedangkan orang mentertawakan kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104520,"Gulai kari dicampur pudina","Nak sedap lagi letak daun kari","Sdh menjadi resam dunia","Rakan menangis sukar dicari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104521,"Indah senja di Pantai Remis,","Tampak dari chalet bertangga;","Jangan dicari rakan menangis,","Menangis tak mungkin membasuh duka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104522,"Mari mengail ikan di bendang,","Jangan lupa membawa cangkul;","Andai berat mata memandang,","Berat lagi bahu memikul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104523,"Nyaring berlagu si reriang,","Terdengar dari kampung seberang;","Hidup umpama layang-layang,","Tali teraju di tangan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104524,"Terkadang jering rasanya kelat,","Makan berulam mengundang sendawa;","Kusyuk tawadduk kita bersolat,","Berserah diri mengabdi jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104525,"Dahan rendah jangan direngkuh,","Biar teduh hujung halaman;","Tika berkuasa janganlah angkuh,","Bukannya milik sekadar pinjaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104526,"Semerbak mewangi daunan pandan,","Nasi lemak menjadi sedap;","Peribadi mulia menjadi teladan,","Umpama cahaya di dalam gelap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104527,"Baju kotor mari diberus,","Seluar dan juga singlet;","Mulia bangsa terajunya lurus,","Terhina bangsa teraju berbelit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104528,"Lari puyuh ke dalam belukar,","Tika nyaring berlompatan kera;","Bermula salam cintapun mekar,","Rindu meranum jiwa gelora.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104529,"Elok-elok akar diramu,","Air ramuan diminum pagi;","Syukur Ya Allah akan rahmat-Mu,","Mengizinkan daku bernafas lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104530,"Jangan dipagar nyiur yang condong,","Andai ditebang buahnya luruh;","Hidup di dunia janganlah sombong,","Air jernihpun bisa keruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104531,"Beramal ikhlas berteraskan Allah,","Moga-moga pahala menyubur;","Teguhkan prinsip jangan mengalah,","Terus melawan jangan berundur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104532,"Betapapun kaya hidup bergantung,","Berbahgialah kaya hidup bersyukur,","Amal ikhlas dengan Allah bergantung,","Mudahan pahala menyubur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104533,"Insaflah diri sebelum diusung,","Dari mahligai kelubang kubur,","Datang keinsafan masih beruntung,","Bodoh sombong roh tersungkur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104534,"Insaflah diri sebelum diusung,","Dari mahligai kelubang kubur,","Datang keinsafan masih beruntung,","Bodoh sombong roh tersungkur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104535,"Ilmu dicurigai imannya kosong,","Segala amal hancur lebur;","Insaflah diri sebelum diusung,","Dari mahligai ke lubang kubur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104536,"Andai dihati subur menyombong,","Pasti melenting bila ditegur,","Ilmu dicurigai imannya kosong,","Segala amal hancur dan lebur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104537,"Jangan dipagar kelapa condong,","Ditiup angin pelepahnya gugur;","Andai di hati subur menyombong,","Pasti melenting pabila ditegur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104538,"Kelapa condong dipagar jangan,","Berbuah lebat mudah dipetik;","Bersantai duduk imbau kenangan,","Menyarung cincin ke jari lentik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104539,"Hujan reda berpuput bayu,","Kelkatu disambar dek layang-layang;","Mengimbau kasih hatiku sayu,","Detik terindah masih terbayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104540,"Ulam genuak si buah jentik,","Masamnya mangga si buah amra;","Terus berjuang terus mendidik,","Keruh di muara gigih di segara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104541,"Ulam genuak sibuah jentik,","Masamnya mangga dan buah amra,","Bukanlah salah alam mendidik,","Keruh dikuala perbuatan manusia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104542,"Ulam genuak si buah jentik,","Masamnya mangga dan buah amra;","Di mana salahnya alam mendidik,","Di hulu jernih keruh di muara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104543,"Dara jelita pulang berlenggang,","Berkain batik baju kebaya;","Mata menjeling berbalas pandang,","Melamar meminang sambil beraya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104544,"Air kelapa minum secawan,","Ambil buahnya di rumah nyonya;","Syukur teramat doamu Tuan,","Moga kita berkongsi manfaatnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104545,"Jazakallahu khair kpd mu tuan,","Pagi-pagi menerima ucapan,","Kepada Allah dikembali pujian,","Agar darinya dikurnia ganjaran..", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104546,"Ambil sagu batang rumbia,","Ambil buluh dibuat galah,","Hamba bersyukur disenangi manusia,","Namun mengharap keredhaan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104547,"Air kelapa diminum secawan,","Tolong kaitkan kelapa pandan;","Andai manusia cintakan Tuan,","Alamat sentiasa diri merawan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104548,"Nasib Melayu apa hendak dikata,","Cerah gelap dimasa mendatang?","Jika hamba dibenar meneka,","Sedikit kedepan banyak kebelakang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104549,"Jika hamba dibenar meneka,","Sedikit kedepan banyak kebelakang,","Apabila ilmu tak cukup dikepala,","Diberi amanah pemimpin kecundang!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104550,"Kain songket corak bersilang,","Baju Melayu sedondon kelabu;","Hilanglah aura bangsa terbilang,","Tutur bicara pemimpin celaru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104551,"Dipukul telur dibuat bahulu,","Inti kelapa badak berendam;","Terlerai kasih dek curiga bertalu,","Jujur dan tulus lebur berdendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104552,"Keria, donat dua saudara","Hidangan petang suami tercinta","Iblis syaitan punya angkara","Teguh iman, pasrah pada yg esa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104553,"Gugur daunan lalu mereput,","Musim kemarau tiada air;","Kasih sekadar embun di rumput,","Dijengah mentari hilang mencair.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104554,"embun bertakung di daun keladi","Keladi gajah dihutan paya","Apakan daya kasih tak menjadi","Orang disana sudah berpunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104555,"Terbang bebas si layang-layang,","Terbang rendah si burung balam;","Kasih umpama bayang-bayang,","Hilang ke mana di gelita malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104556,"Merak anggun sang rimba ratu","Menari rapi ikut irama","Bayang tetap setia bersatu","Malam menjelma beradu sama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104557,"Ambil sagu batang rumbia,","Ambil buluh dibuat galah;","Sepanjang usia berhati mulia,","Moga memperoleh redha Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104558,"Menenun songket di Pulau Duyung,","Ditenun gadis ayu jelita;","Jangan dihina negarawan ulung,","Jelata meluat silang sengketa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104559,"Pulau Duyung nama diberi","Perawan jelita diminda teruna","Begitulah keletah Bapak-Bapak Menteri","Dulu dipuja sekarang dihina.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104560,"Menenun songket di Pulau Duyung,","Indah coraknya tenun berseni;","Jangan dihina negarawan ulung,","Kiranya benar berhujah berani.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104561,"Duyung jelita berenang sendiri","Ekor melentok indah sekali","Bercakap meraung gilakan undi","Hakikatnya bacul menyorok belakang bini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104562,"Angin menderu terpadam pelita,","Sedang juadah telah terhidang;","Termungkir janji maaf dipinta,","Maaf tertunai dusta berulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104563,"Angin menderu terpadam pelita,","Sedang Juadah telah terhidang,","Nasib Melayu apa hendak dikata,","Cerah gelap dimasa mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104564,"Nasib Melayu apa hendak dikata,","Cerah gelap dimasa mendatang?","Jika hamba dibenar meneka,","Sedikit kedepan banyak kebelakang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104565,"Jika hamba dibenar meneka,","Sedikit kedepan banyak kebelakang,","Apabila ilmu tak cukup dikepala,","Diberi amanah pemimpin kecundang!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104566,"Kain songket corak bersilang,","Baju Melayu sedondon kelabu;","Hilanglah aura bangsa terbilang,","Tutur bicara pemimpin celaru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104567,"Angin menderu terpadam pelita,","Sedang Juadah telah terhidang,","Nasib Melayu apa hendak dikata,","Cerah gelap dimasa mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104568,"Mujarab sungguh pucuk gelenggang,","Rebus daunnya jangan dikukus","Jantan berkokok di dalam gelanggang,","Tika di luar tunduk menikus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104569,"Tumbuh di bukit periuk kera,","Terdedah malam embun mengisi;","Sopan anak bertutur bicara,","Tumpahlah kuah ke dalam nasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104570,"Rimbun dahan tempat berlindung,","Hangat membahang mentari terik;","Tak pernah salah ibu mengandung,","Sekadar anak tersalah didik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104571,"Tumpang berteduh mentari terik,","Di celah dahan burung mengeram;","Bukan guru tersalah didik,","Sekadar murid tersalah faham.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104572,"Cantiknya bunga jadi hiasan,","Susunan bunga bagai dikarang;","Dara jelita melayan sopan,","Menuang air penutup dipegang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104573,"Di dalam cangkir air minuman,","Tuang ke cawan buat tetamu;","Mesra menyapa manis senyuman,","Hilang di mata di hati merindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104574,"Rumah kampung atap rumbia,","Lantai dan dinding buluh berbelah;","Manis senyum wajah ceria,","Merajuk kasih pujuk mengalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104575,"Rumah kampung atap rumbia,","Rantai dan dinding buluh berbelah;","Sayang bangsaku hilang ceria,","Pimpinan kaya dalih dan helah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104576,"Beratap rumbia rumah di kampung,","Lantai bersusun berbelah buluh;","Sedar diri nasib diuntung,","Mengalir jernih hilanglah keruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104577,"Diikat buluh menupang tangga,","Dibasuh kaki air tempayan;","Bahagia hidup berumah tangga,","Umpat keji jangan dilayan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104578,"Diikat buluh menupang tangga,","Air tempayan membasuh kaki;","Rukun bahagia berhimpun keluarga,","Jangan dihirau hasad dan dengki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104579,"Belah buluh buat gelegar,","Gelegar diikat dengan rotan;","Duduk mengeluh hati berdebar;","Rindu diutus lewat titipan.", 4, 6);
