INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104586,"Bersahutan di hutan berlagu ungka,","Berselang bunyi si burung undan;","Litar Sepang menganyam duka,","Politik dendam tanpa sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104587,"Buatlah tapai dicampur ragi,","Di serambi rumah berlagu hikayat;","Pemimpin membazir negara rugi,","Kaya berhutang menanggungnya rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104588,"Merah mekar harum berbunga,","Hinggap unggas menghisap madu;","Proton terlepas bangsa ternganga,","Kepada siapa hendak mengadu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104589,"Seluruh benua orang mengeji,","Orang memuji menumpang laba,","Sampai bila mahu mengerti?","Sedarkah rakyat hidup sengsara?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104590,"Tajam sungguh mata gergaji,","Jangan bergurau jangan mencuba;","Seluruh benua orang mengeji,","Orang memuji menumpang laba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104591,"Air sungai mengalir deras,","Petanda lebat hujan di hulu;","Angkuhnya tegar hatinya keras,","Tetap membisu ditanya bertalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104592,"Lebat sungguh hujan di hulu,","Sungai melimpah hanyutlah titi;","Jangan hidup bagai dedalu,","Menumpang hidup sehingga mati.", 4, 6);
