INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104637,"Sungai melimpah hanyutlah titi,","Merunduk padi bendang berlumpur;","Hutang budi dibawa mati,","Hutang janji sumpah terhambur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104638,"Lebat sungguh hujan di hulu,","Sungai melimpah hanyutlah titi;","Sebelum bicara fikir dahulu,","Janji terucap tersemat di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104639,"Lebat sungguh hujan di hulu,","Sungai melimpah hanyutlah titi;","Jangan hidup bagai dedalu,","Menumpang hidup sehingga mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104640,"Air sungai mengalir deras,","Petanda lebat hujan di hulu;","Angkuhnya tegar hatinya keras,","Tetap membisu ditanya bertalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104641,"Tajam sungguh mata gergaji,","Jangan bergurau jangan mencuba;","Seluruh benua orang mengeji,","Orang memuji menumpang laba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104642,"Seluruh benua orang mengeji,","Orang memuji menumpang laba,","Sampai bila mahu mengerti?","Sedarkah rakyat hidup sengsara?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104643,"Merah mekar harum berbunga,","Hinggap unggas menghisap madu;","Proton terlepas bangsa ternganga,","Kepada siapa hendak mengadu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104644,"Buatlah tapai dicampur ragi,","Di serambi rumah berlagu hikayat;","Pemimpin membazir negara rugi,","Kaya berhutang menanggungnya rakyat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104645,"Bersahutan di hutan berlagu ungka,","Berselang bunyi si burung undan;","Litar Sepang menganyam duka,","Politik dendam tanpa sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104646,"Duduk di pangkin sudut halaman,","Bintang di langit bintang kejora;","Di dalam hati mewangi taman,","Di dalam taman mimpi gelora.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104647,"Meluruh daun si pokok sena,","Pucuk kaduk dibawa pulang;","Wajah dirindu hilang ke mana,","Indah kenangan kian menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104648,"Merah sungguh warna pulasan,","Patah ranting daunpun layu;","Menunggu giliran sangatlah bosan,","Menanti waktu perlahan berlalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104649,"Terbentang alam tiada bertepi,","Langit dan laut bagai bercumbu;","Tika rindu kian menyepi,","Cinta mekar dirundung sayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104650,"Rimbun mengkuang tajam berduri,","Tumbuh meliar di selut paya;","Jangan suburkan sombong naluri,","Hidup ceria aman bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104651,"Pagi Jumaat ke Pasar Kemboja,","Menawar harga pelbagai karenah;","Salam Maghrib seiring senja,","Bentang sejadah solat berjemaah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104652,"Rendang berbuah Durian Belanda,","Makan sedikit sekadar menjamah;","Hujan lebat banjir melanda,","Waktu terbaik membasuh rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104653,"Rendang berbuah Durian Belanda,","Buah yang masak makan berkongsi;","Hujan lebat banjir melanda,","Seronok anak-anak tolong mencuci.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104654,"Panjangnya leher di itik angsa,","Cantik bulunya si itik serati;","Manis di mulut ingat sentiasa,","Sedang dusta bersarang di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104655,"Berpuput bayu ombak berdebur,","Melambai-lambai pelepah kelapa;","Pergi Langkawi santai berlibur,","Melepas lelah kerja bertimpa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104656,"Pulang kesarang terbit mentari,","Sedang embun masih diranting,","Hamba disini tetap menanti,","Biar jasadku kering melenting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104657,"Sedang embun masih diranting,","Menitik kerumput dipuput bayu,","Walau jasadku kering melenting,","Pasti dikau kembali kepangkuan ku!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104658,"Puteh puteh warnanya kapas,","Dari kapas kain ditenun,","Hamba tunggu pantun dibalas,","Walaupun ia seribu tahun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104659,"Air telaga si air paip,","Tampak jernih di dalam gelas;","Biar mendagu daku menaip,","Pantun Dato tetap kubalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104660,"Menjelang senja terbang kelawar,","Pulang ke sarang tebit mentari;","Tidak terniat menyambut hambar,","Ruang santai sukar dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104661,"Pulang ke sarang terbit mentari,","Sedang embun masih di ranting;","Ruang santai sukar dicari,","Didera tugas di saat genting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104662,"Sedang embun masih di ranting,","Menitik ke rumput dipuput bayu;","Didera tugas di saat genting,","Tugasan wajib tak boleh dirayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104663,"Pantun dijual pantun dijawab,","Jika dilayan hingga ke pagi,","Mata ngantuk dah menguab-nguab,","Kemudian hari kan ketemu lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104664,"Tanah sawah mari dibaja,","Setelah dibajak reput lerai;","Demikian aura emas dipuja,","Bicara mesra tiada bernilai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104665,"Nasi minyak si nasi kandar,","Buat bekalan ke rumah nyonya;","Cinta mekar terasakan pudar,","Bicara hambar hilang mesranya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104666,"Hujan gerimis gelapnya awan,","Mendung berarak langit pun terang;","Jikalau harta dunia jadi sukatan,","Tidak ternilai kasih dan sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104667,"Petir menyambar silang menyilang,","Langit gelap menyorokkan mentari;","Jangan dibuka aibnya orang,","Nanti terbuka aib sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104668,"Kuatnya guruh petir bersahutan,","Kilat memancar cahaya berbalas;","Buat seluruh teman dan taulan,","Salam kasih tulus dan ikhlas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104669,"Anak seladang di tepi bendang;","Anak lipan di atas papan:","Gerimis mengundang tanpa diundang,","Menyimbah harapan tiada jawapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104670,"Majlis batal meresahkan tetamu,","Tetamu mengadu sebab dijemput;","Awan dipintal kutulis namamu,","Ku kirim rindu di bayu berpuput.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104671,"Puncak bukit di selaput awan,","Awan putih berkepul kepul;","Dendam cinta masih di simpan,","Agar tidak menjadi simpul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104672,"Selepas hujan ada pelangi,","Selepas kabus ada mentari;","Moga hidup mu sentiasa disenangi,","Dirahmati Allah setiap hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104673,"Bertanya diri rasa berkenan;","Mulut menyebut hatinya tawar:","Kasih kau beri hanya mainan,","Umpama kabut serangkai mawar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104674,"Cuaca gelap semakin redup,","Masakan boleh kembali terang;","Budi bahasa amalan hidup,","Baru lah kekal dikenang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104675,"Langit cerah tiada mendung,","Bayu bertiup terasa nyaman;","Jangan suka berhati sandung,","Kelak nanti tiada beteman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104676,"Langit mendung hujan gerimis,","Berlari anak masuk kerumah;","Wajah kacak manis berkumis,","Lebih hebat jika peramah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104677,"Awan berarak di mega mendung,","Gerimis turun laksana dupa;","Bertapa azab ibu mengandung,","Jasa ibu jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104678,"Sejuk pagi diselubungi kabus,","Terbit mentari menyilau pandangan;","Jalinkan persahabatan biarlah tulus,","Barulah ia kekal berpanjangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104679,"Terang bukan sebarang terang,","Terangnya nampak apa di zahir;","Sayang bukan sebarang sayang,","Janjinya sampai nafas yang akhir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104680,"Awan mendung angin berlalu,","Terasa nyaman hembusan bayu;","Bunga berkembang di puja selalu,","Mengapa di buang bila layu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104681,"Berbaju merah ligat bertandak;","Irama bingit kuat bernada:","Air kucurah hujannya tidak,","Guruh di langit bukan petanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104682,"Cuaca kelam burung beterbangan,","Gerimis bertandang guruh bergema;","Hujan semalam mengimbau kenangan,","Ingatan manis datang menjelma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104683,"Bertiup bayu bagai berbisik,","Guruh di langit gegak gempita;","Ajaibnya Sajian Radio Klasik,","Selamanya hati terpaut cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104684,"Langit nan merah pancaran rembulan,","Seluruh pelusuk mendadapat biasan;","Hati ku gudah terusik fikiran,","Hanya dikau yg satu sentiasa dlm ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104685,"Hadirnya pagi sinar mentari,","Suram senja dilambai malam;","Kirimnya doa pengganti diri,","Angin berlalu sampaikan salam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104686,"Terang cuaca di pagi hari,","Hujan gerimis di waktu petang;","Puas sudah daku mencari,","Tapi si dia tak jua datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104687,"Awan memutih indah pelangi,","Hujan semalam lebat sekali;","Kasih dihati harum mewangi,","Sayangnya tidak di ambil peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104688,"Mendung meniti di lembah curang,","dihidang langit sendu tersayat;","kekasih hati milik orang,","jari digigit sepanjang hayat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104689,"Berjalan seorang ketika petang,","Di lorong berliku berjurang dalam;","Langit terang diserikan bintang,","Namun hatiku suram dan kelam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104690,"Sejuk mendayu lambaian angina,","Menghempas lembut pokok kelapa;","Andai bahagia yang kita ingin,","Amalan solat jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104691,"Putih gebu awan menghampar,","Tambah nyaman bayu bertiup;","Perut bercerak terasa lapar,","Tak ada nasi kuah pun ku hirup.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104692,"Bukan salah ibu mengandung,","Perut digendong kemana sahaja;","Batu Gajah pagi ni cuaca mendung,","Menunggu mentari menyinarkan cahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104693,"Lambaian senja singgah sebentar,","Malam hampiri indah gemintang;","Untaian cinta sudah dihantar.","Mengapa dilupai indah kasih sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104694,"Indahnya syair cantiknya hujah,","Gurindam lama iramanya syahdu;","Di dalam air terbayang wajah,","Di bulan purnama kutitipkan rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104695,"Panas cuaca bertopi lebar,","Benang bersulam sidaun purun;","Puas sudah ku pendam sabar,","Dari bulan bertukar ketahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104696,"Gelisah tedung dihambat balam,","Kuku balam sungguh tajamnya;","Langit mendung kusangka malam,","Kalau malam di mana bintangnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104697,"Guruh petir bertalu-talu,","Membuat hati rasa curiga;","Sebelum kerja sarapan dahulu,","Baru badan sihat bertenaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104698,"Bulan mengambang di malam minggu,","Menyuluh atap desa dan pekan;","Usah dibimbang usah diragu,","Janji tetap akan kutunaikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104699,"Pikul tukun bersama jerami,","Letak berjemur di tepi bara;","Hujan turun membasahi bumi,","Pokok subur petani gembira.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104700,"Pagi hari sarapan lepat,","Rasanya manis banyak gula;","Hujan turun sungguh lebat,","Tempat sahabat bagaimana pula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104701,"Malam yang indah tenang dan damai,","Indah purnama di pagar bintang;","Hilanglah gundah berteman ramai,","Cari yang pertama kasih mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104702,"Terbangnya camar di langit biru,","Terbang sekawan diwaktu redup;","Sepi di kamar di tahun baru,","Hati nan rawan menghadap hidup.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104703,"Redup cuaca menggamit sayu,","Gerimis tiba jatuh di kali;","Kasih yang tumbuh janganlah layu,","Kusiram kubaja subur kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104704,"Mentari terbenam berwarna merah,","Diufuk barat nampak bayangan;","Cinta sudah beralih arah,","Disebut jangan dikenangan pun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104705,"Mengintai intai di balik tabir,","Menanti hujan turun tak puas;","Mengapa perlu menanam di bibir,","Kan ladangnya terbentang luas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104706,"Cuaca dingin berhujan batu,","Mencari berlindung beria ia;","Eh, hamba tidaklah begitu,","Pada yang satu hamba setia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104707,"Guruh sudah hujan pun sudah,","Air laut tenang bak cermin;","Merisik sudah bertunang pun sudah,","Cuma menanti nak naik pelamin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104708,"Hujan gerimis di pagi hari,","Gugurlah manggis banyak di perdu;","Tersenyum manis wajah berseri,","Hati menangis bawa beradu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104709,"Hujan gerimis membentuk pelangi,","Nampak indah diufuk timur;","Cintaku ini megar mewangi,","Hidup damai bertambah makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104710,"Indah nian purnama terang,","Angin bayu sepoi-sepoi bahasa;","Datangnya cinta membawa girang,","Manis jiwa lebih terasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104711,"Di atas sabut si ulat jengkal,","Berhati-hati abang menepis;","Hujan ribut dapat kusangkal,","Gerimis di hati gagal kutepis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104712,"Sejuk terasa bayu di laut,","Sejuknya sampai hingga ke tulang;","Budi mu mesra hati terpaut,","Rupanya sudah dimilik orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104713,"Langit petang mendung bertapa,","Pelangi menyinar hujung beranda;","Pagi menyapa bertegur sapa,","Assalammualaikum walau dimana berada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104714,"Fajar dingin di selangi guruh,","Hujan pagi petanda resah;","Belajarlah rajin bersungguh sungguh,","Kelak nanti tidaklah susah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104715,"Awan biru bertukar kelabu,","Hujan lebat waktu tengahari;","Sayang guru sayangi ibu,","Nasihat keramat bijak memberi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104716,"Cantiknya awan bertingkat-tingkat,","Satu persatu nampak berseri;","Kami sekawan sama sepakat,","Saling membantu menghitung hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104717,"Sekawan angsa berenang di kolam,","Tidak jemu mata memandang;","Mendung cuaca menyelubungi alam,","Semendung kasihmu semakin menghilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104718,"Titisan embun di pagi hari,","Namun hujan tak jua bertandang;","Kalaulah diikutkan sakit hati,","Wajahmu puan tak sudi ku pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104719,"Bayu berbisik nada perlahan,","Kepulan awan indah dipandang;","Radio Klasik tetap pilihan,","Biar puluhan siaran kumandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104720,"Apalah erti senja menjelang,","Andai rembulan masih gerhana;","Apalah erti kasih bertandang,","Andai hati masih merana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104721,"Langit kirmizi di ufuk barat,","Berpuput lembut bayu senja;","Kasih di hati semakin sarat,","Mengapa layu cinta dipuja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104722,"Mekar di taman di rumah nyonya,","Disiram selalu pagi dan petang;","Rupaku tiada harta tak punya,","Sedangkan dia minyak ditatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104723,"Menjelang senja terbang kelawar,","Pulang ke sarang terbit mentari,","Biarpun sambutan mu hambar,","Hamba disini tetap menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104724,"Terpaut di hati sukar disunting,","Mekar di taman di rumah nyonya;","Segan memandang segan menjeling,","Rupaku tiada harta tak punya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104725,"Terpaut hati sukar di sunting,","Kesal tidak pintar merayu,","Apa lagi di pandang dijeling ,","Tinggallah tangisan mendayu-dayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104726,"Menitik ke rumput di pupuk bayu,","Terpaut hati sukar di sunting,","Kesal tidak merayu merayu,","Apa lagi di pandang dijeling", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104727,"Sedang embun masih di ranting,","Menitik ke rumput dipuput bayu;","Terpaut hati sukar disunting,","Mekar sendiri sehingga layu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104728,"Pulang ke sarang terbit mentari,","Sedang embun masih di ranting;","Mata menyapa angkuh berduri,","Terpaut hati sukar disunting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104729,"Menjelang senja terbang kelawar,","Pulang ke sarang terbit mentari;","Ibarat indah sekuntum mawar,","Mata menyapa angkuh berduri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104730,"Mencari rotan mari ke bukit,","Pokok tumbang mari dialih;","Kumbang dan bunga bukan sedikit,","Sekadar hati cerewet memilih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104731,"Pucuk gelugur dihiris halus,","Masak pedas berdaging salai;","Bersikap jujur berjiwa tulus,","Jasad dihina prinsip tak lerai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104732,"Masak pedas cendawan kukur,","Campur serai dan terung telunjuk;","Nikmati hidup sentiasa bersyukur,","Setiap detik diberi petunjuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104733,"Masak pedas semilang salai,","Dicampur pucuk asam gelugur,","Nikmati hidup jangan terlalai,","Tersalah langkah kita tersungkur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104734,"Rautlah lidi buat penyapu,","Penyapu jangan sandar ke katil;","Andai cerdik jangan menipu,","Janganlah sombong kiranya jahil.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104735,"Tunggul jerami mari dibakar,","Asap tebal berkepul-kepulan;","Cinta bersemi mulanya mekar,","Sayang terputus di tengah jalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104736,"Mari membakar tunggul jerami,","Melayang terbang si layang-layang;","Mesra bicara cinta bersemi,","Semakin hari semakin sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104737,"Mari membakar tunggul jerami,","Asap berkepul terhalang pandangan;","Bersembang mesra cinta bersemi,","Kini sekadar tinggal kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104738,"Mari dibakar tunggul jerami,","Mula membajak untuk bersawah;","Sikap dirinya minta difahami,","Sikap kita tak diambil kisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104739,"Indah mekar bunga melati,","Semerbak harum si bunga melur;","Indah kenangan semat di hati,","Biar cinta telahpun lebur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104740,"Dari rebung buluh dilentor,","Buluh ditebang dijadikan hiasan,","Kau kata kumbang bukan seekor,","Begitu juga bunga di taman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104741,"Kelip-kelip si kunang-kunang,","Terang cahaya tika gelita;","Rindu merimbun hati mengenang,","Indah naluri dirangkul cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104742,"Dahan sekah dek angin kencang,","Dahan jatuh di paya berlumpur;","Elok muafakat dibawa berbincang,","Kasih sayang kan mekar subur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104743,"Angin kencang sekahlah dahan,","Dahan jatuh di rumpun lalang;","Cinta kasih anugerah Tuhan,","Tiada insan dapat menghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104744,"Angin kencang sekahlah dahan,","Dahan jatuh ke dalam paya;","Kasih sayang bukan taruhan,","Tersemat utuh di lubuk jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104745,"Kasih sayang bukan taruhan,","Tersemat utuh dilubuk jiwa,","Tak salah mencari sicantik rupawan,","Namun terbaik yang beriman didada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104746,"Kasih sayang bukan taruhan,","Tersemat utuh di lubuk jiwa;","Cantik rupawan banyak pilihan,","Utuh beriman sukar diteka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104747,"Indah senja bauran warna,","Langit dan laut tampak bercumbu;","Sesal kemudian tidak berguna,","Asyik dikenang mengundang rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104748,"Lemak pedas semilang salai,","Campur dengan hirisan kundang;","Andai hidup selalu lalai,","Diri hina dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104749,"Pucuk gelugur dihiris halus,","Masak pedas berdaging salai,","Bersikaplah jujur biarpun kurus,","Asalkan iman tidak tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104750,"Pucuk gelugur dihiris halus,","Masak pedas berdaging salai;","Saling menasihat berjiwa tulus,","Hidup terbimbing terelak lalai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104751,"Pagi berkabut mendung gelita,","Terbit mentari tinggi menggalah;","Hati bicara lidah berdusta,","Luhur jiwa redha mengalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104752,"Pucuk kunyit si daun kari,","Merah kantan kelopak berbunga;","Wang dan ringgit rakus dicari,","Kasih sayang terlerai akhirnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104753,"Mencari rezeki tuk anak bini,","Agar keluarga menjadi happy,","Itulah lumrah kehidupan kini,","Hingga kasih sayang ditolak tepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104754,"Hidup seperti air sungai yang mengalir,","Mengalir ia mengikut rentak bumi,","Seperti kehidupan yang menyusur takdir,","Takdir yang ditentukan oleh Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104755,"Hujan di hulu sungai dilimpahi,","Mengalir di sungai juga ke paya;","Menyusur takdir ketentuan IlLahi,","Syukur dan redha hidup bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104756,"Duduk santai di hujung anjung,","Orang kampung riuh menampi;","Bahasa orang dikelek dijunjung,","Bahasa negara ditolak ke tepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104757,"Kelip-kelip si kunang-kunang,","Terang cahaya tika gelita;","Cinta silam jangan dikenang,","Andai dikenang jiwa derita.", 4, 6);
