INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104760,"Kelip-kelip si kunang-kunang,","Terang cahaya tika gelita;","Jodoh tertitip dilamar bertunang,","Indah kahwin kerana cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104761,"Tanam serai barang serumpun,","Subur biak rimbun seladang;","Bahagia kasih hidup rukun,","Miskin harta bukan penghalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104762,"Sayup berbalam terbangnya helang,","Terbang hinggap di dahan meranti;","Jangan mengigau mimpi berulang,","Umpama berkasih bercerai mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104763,"Ayuh berlumba mari ke litar,","Air mineral jangan dilupa;","Hidup umpama roda berputar,","Berlalu derita bahagia menyapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104764,"Sepat benua si ikan puyu,","Duduk mengail di perdu berangan;","Tutur tulus lidah merayu,","Malang bertepuk sebelah tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104765,"Ambil talam dijemur ragi,","Menampi memilih biji selasih;","Tak mungkin daku merayu lagi,","Kian kurayu kian tersisih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104766,"Cantik nuri di dalam sangkar,","Sangkar bertutup kain baldu;","Mesra berlagu di kuningan mekar,","Melodi indah mengimbau rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104767,"Tertusuk duri sakit berdenyut,","Rimbun semalu di perdu pinang;","Mimpi berdayung perahuku hanyut,","Terjaga lena air mata berlinang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104768,"Cantik berlorek ikan belida,","Duduk mengail di perdu redup;","Sebak di jiwa menangis tiada,","Derita kecewa mainan hidup.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104769,"Terbang riang si rama-rama,","Sedang pengail duduk di beting;","Bicara mesra seketika cuma,","Ibarat unggas hinggap di ranting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104770,"Dara mengintai di balik tabir,","Malu menatap renung kekasih;","Janji berkasih mainan bibir,","Didakap mewah kasih tersisih.", 4, 6);
