INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104893,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Rajin membayar tidak berkira,","Alah kadar jangan berhutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104894,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Biarpun diam tidak bersuara,","Jangan dilupa lunaskan hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104895,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Belajarlah anak-anakku sampai menara,","Insyaallah kecemerlangan di hadapan mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104896,"Jauh belayar hingga kemuara,","Tidak sedar hari dah petang;","Hutang berbayar hati gembira,","Jika dah mati tidak tinggalkan hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104897,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Walaupun kita bebas bersuara,","Mesti nak jaga pantang dan larang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104898,"Jauh berlayar hingga muara,","Tidak sedar hari dah petang;","Akan ku tempuh duka dan lara,","Kemenangan pasti akan mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104899,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Setelah penat kita berusaha,","Jangan disia peluang mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104900,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Jangan dibiar org lain juara,","Usaha kejar berundur pantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104901,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Berusaha dan berjuang jadi juara,","Ambil kesempatan apa jua semua peluang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104902,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Hindari sikap suka berpura-pura,","Moga perjalanan hidup sentiasa tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104903,"Anak seluang si anak sepat,","Jemur di para pinggirlah paya;","Rebut peluang selagi dapat,","Semangat membara ke mercu jaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104904,"Jauh belayar hingga ke muara,","Tidak sedar hari dah petang;","Buat baik sepanjang masa,","Selagi masih ada peluang.", 4, 6);
