INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104910,"Jauh belayar hingga ke muara,","Tidak sedar hari dah petang;","Hidup jangan suka berpura-pura,","Kelak diri dibenci orang.", 4, 6);
