INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104918,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Macam loyar memujuk bersuara,","Kata-kata diputar orang ditentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104919,"Jauh belayar hingga ke muara,","Tidak sedar hari dah petang;","Lupakanlah duka dan lara,","Buka lembaran baru akan datang.", 4, 6);
