INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104926,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Jangan dibiar tidak bersuara,","Jom layan radio klasik hingga ke petang.", 4, 6);
