INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104928,"Jauh belayar hingga ke muara,","Tak sedar hari dah petang;","Adek yang jauh selalu merana,","Buat abang tidur tak senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104929,"Jauh belayar hingga ke muara,","Tidak sedar hari dah petang;","Berlagak macam orang kaya,","Akhirnya pecah juga tembelang.", 4, 6);
