INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104934,"Jauh berlayar hingga muara,","Tidak sedar hari dah petang;","Bila pencen dipanggil pesara,","Semoga selesai semua hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104935,"Jauh berlayar hingga muara,","Tidak sedar hari dah petang;","Walau sudah bergelar pesara,","Khidmat kita jangan dihalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104936,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Janji nak bayar hanya pura-pura,","Lalu beredar meninggalkan hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104937,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Hutang manusia telah ku bayar,","Dunia akhirat insyaallah tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104938,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Jika hasrat ingin ke syurga,","Pastikan dijelaskan semua hutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104939,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Hasrat hati ingin ke syurga,","Namun pahala masih kontang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104940,"Tepok dada tanya selera,","Diri terasa masih bernyawa;","Dulu muda kita dah pun tua,","Itu antara klasik namanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104941,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Orang berbudi tidak mengira,","Asalkan jangan buruk sangka pada orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104942,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Bila senang ramai saudara,","Bila susah semua tak pandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104943,"Jauh belajar hingga muara,","Tidak sedar hari dah petang;","Buat baik berpada-pada,","Menjadi bekal hari mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104944,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Sedarlah wahai diri kita dah tua,","Amal ibadat jangan kita lupa kan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104945,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Berbaloi terbayar jadi juara,","Bukan sekadar cakap jer lantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104946,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Rajin bersedekah rezeki yang ada,","Balasan pahala luas terbentang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104947,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Jika bertambah rezeki yang ada,","Bantu yang lain sedekah disumbang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104948,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Jika kita rajin berusaha,","Rezeki pastikan mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104949,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Hendaklah kita rajin berusaha,","Menggunakan setiap masa yang terluang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104950,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Giat belajar tatkala muda,","Waktu dah beredar kan terus hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104951,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Rezeki dah ada di depan mata,","Esok lusa mungkin tiada peluang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104952,"Jauh berlayar hingga muara,","Tidak sedar hari dah petang;","Buat baik berpada-pada,","Dahula ayan ibu tercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104953,"Jauh belayar hingga muara,","Tidak sedar hari dah petang;","Ambil kesempatan selagi ada,","Jangan sampai diri mu malang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104954,"Jauh berlayar hingga muara,","Tidak sedar hari petang;","Berusaha hingga berjaya,","Ambil kesempatan hingga cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104955,"Jauh belayar hingga ke muara,","Tidak sedar hari dah petang;","Hidup dah aman dalam negara,","Janganlah hendakNya musibah mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104956,"Pergi Turki musim salji","Pergi bercuti seorang diri","Kalau tak pasti jangan berjanji","Nanti terluka diri sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104957,"Berbalam balam cahaya suasana,","Budak budak bertepuk ketampi.","Kalau jodoh tak kan kemana ,","Pasti ketemu di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104958,"Burung Merak burungMerpati","Berwarna indah cantik sekali","Pasti yg waras berhati-hati","klau berjodoh tak perlu berjanji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104959,"Ke Rompin memungut landak,","Sawah penuh padi huma.","Hnya dipimpin diikat tidak,","Org dipimpin sudah berpunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104960,"Datang ke Rompin, menempah landak","Duduk di pangkin, menyusun huma","Hanya di pimpin, di ikat tidak","Mana mungkin, bertahan lama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104961,"Kalau menanam padi pulut,","Lupa jangan padi beras,","Jangan suka berjanji dimulut,","Kalaulah tidak dihati yang waras", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104962,"Jemur mengkuang, di akar temu","Tepian batu, lereng berdahan","Jangan di buang, anugerah ilmu","Kerana itu, rahmat dari tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104963,"Musafir singgah surau lama,","Duduk seraya zikir tasbih,","Kalau sudah cinta agama,","Seribu daya tak seribu dalih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104964,"Lagu baru lagu lama","Layang-layang bertali benang","Usah diungkit kisah lama","Memang sedih jika dikenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104965,"Hinggap ketitir di pokok kenaf","Akibat alpa tercerut tali","Walaupun bibir menyebut maaf","Untuk melupa sukar sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104966,"Hang Tuah pahlawan Melaka,","Pantas bersilat gagah perkasa,","Andai hati telah terluka,","Seumur hidup pedihnya terasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104967,"Di atas bangku ayam berkokok,","Di dalam sabut telornya bertanda;","Bagaikan paku lekat di pokok,","Walau dicabut bekasnya ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104968,"Banyak panji perkara panji,","Panji patah malam berhujan,","Banyak janji diantara janji,","Inilah janji Allah dalam Al Quran,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104969,"Gunung kerunai gunung kenderung","Puncaknya tinggi mencapai awan","Aku tahu penyebab hatimu murung","Kerena lama kau tak dapat belaian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104970,"Terbang burung di pagi hari,","Hinggap di laman di pokok peria;","Terasa murung saat bersendiri,","Carilah teman pengubat ceria.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104971,"Cantik sungguh Si-Puteri Santubong","Dcemburui oleh Puteri Si-Jinjang","Jangan Diikut Hati yang Murung","Mari Bersama kita Berdendang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104972,"Kebaya labuh benang sutra,","Dipakai celana sianak dara","Ubat rawan rindu ubatnya,","Pulihkan rindu ubatnya setia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104973,"Haiwan terbang bernama busung","Sesat jalan malu bertanya","Kalau anda berasa murung","Bentang sejadah mohon padanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104974,"Baju kebaya baju kebarung","Angkat hidangan bawa kedapur","Bertandang sepi mengapa bermurung","Carilah teman bawa berhibur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104975,"Kepuncak gunung bawalah sarung","Dipuncak dingin berlapis awan","Gerangan apa hingga bermurung","Biar kucari obatnya rawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104976,"Sedap sekali rasa durian,","Buahnya kuning amatlah pekat,","Penawar rawan di dalam quran,","Peninggalan Baginda penuh mukjizat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104977,"Malam sepi mendaki gunung,","Sekadar mencari si bunga berteh.","Sedih pilu hatimu sedih,","Jom kita sama keluar ngeteh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104978,"Buah setapi penuh sekarung","Juga semangkok buah keranji","Di malam sepi hati mu murung","Ambil wuduk bawa mengaji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104979,"Letak selasih di dalam kuali,","Letak di almari buah remnia;","Andainya kasih tidak di peduli,","Berhijrah mencari ke hujung dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104980,"Di waktu rembang melihat tuntung,","Di waktu redup menyusur perlahan;","Jangan di timbang nasibnya untung,","Suratan hidup ketentuan tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104981,"Pukul gendang kulit biawak,","Bunyinya tidak berdentum lagi,","Ke mana kasih hendakku bawak,","Sedikit tidak beruntung lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104982,"Bunga lempoyang tepian titi,","Mekarnya tiga dipetik dara,","Kasih sayang tulus di hati,","Rumahtangga aman sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104983,"Benih ku tanam buah berangan,","Tapi yg tumbuh pohon selasih","Ikhlas dan jujur jadi pegangan,","Tanda jujur dlm berkasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104984,"Malam gelap sunyi sepi","Terang bulan bintang ceria","Hati ikhlas luhur & suci","Disenangi orang rasa bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104985,"Lereng semak parit ditepi,","Janji dipadu usah diengkar.","Bila kasih mekar dihati,","Kuntum kasih menjadi segar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104986,"Lereng berakar, parit di tepi","Rumput merambat, tepian palang","Biar mekar, di dalam hati","Kasih bersahabat, tak akan hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104987,"Hikayat dahulu banyak rekaan,","Duduk tersandar dipohon korma,","Kasih dan sayang bukan paksaan,","Hati yang luhur perlu bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104988,"Tidak semua kaca berkilau","pecah terjatuh tidak semesti","Bila bicara banyak berkalau","dahan merapuh akhirnya mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104989,"Apa terjadi sudah terjadi","Ikutkan hati tak dk percaya lagi","Walaupun dijelas berkali kali","Terasa phobia takut disakiti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104990,"Burung merpati terbangnya tinggi","Burung belatuk terbangnya rendah","Berhati hati sebelum terjadi","Usah terhantuk baru tengadah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104991,"Berjalan-jalan diwaktu petang","Sambil minum duduk disampan","Biar banyak sesalan yang datang","Buat pengajaran dimasa hadapan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104992,"Cantik belatuk berjambul merah","hujung titian hinggap di sana","Sudah terhantuk baru terngadah","sesal kemudian tidak berguna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104993,"Anak ayam anak angsa","Berderet-deretan jalan di titi","Sekali terhantuk kesan terasa","Berhati-hati sudahlah pasti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104994,"Meredah laut, lagaknya angkuh","Mata rambang, ukiran andoh","Hajat berpaut, di dahan kukuh","Anggaplah bersembang, kita berjodoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104995,"Cincin perak, berupih inang","Tali kelasi, tepian papan","Air beriak, nampak tenang","Kenapa sangsi, mencampak umpan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104996,"Bila awasi susah ternampak","Hendak di baiki takut melukut","Biar sangsi umpan dicampak","Semoga rezeki kail tersangkut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104997,"Daun keladi tidak berbaki","Habis diratah sianak angsa","Seandai sudi usah berteki","Hati meresah hilang selesa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104998,"Biar batu, jadi petunjuk","Tepian berangan, bersusun ampai","Kalau itu, syarat memujuk","Khuatir tangan, yang tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (104999,"Rindu pungguk pada rembulan","dengar tangis mendayu-dayu","Syarat dipujuk bukan di tangan","di jari manis tempat merayu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105000,"Tepian tasik, berangin bayu","Seekor bangau, berenang menyudu","Kalau asyik, dengan merayu","Khuatir angau, menjadi rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105001,"Angin bayu diwaktu pagi","tiba bersama rintihan hujan","Asyik merayu sangka sudi","rupanya cuma satu mainan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105002,"Rumput bersisa, tepi kailan","Buah berembang, tepian keladi","Jangan terasa, hati terkilan","Duduk bersembang juga, di kira sudi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105003,"Sedari jauh sudah jelajah","Tiba bukan sekadar sembang","Hati merusuh ingin terjah","Rasa terkilan ingin bersarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105004,"Sayur bayam, di tudung saji","Siput kerang, memanjat perahu","Tuah ayam, nampak di taji","Tuah orang, kira tak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105005,"Buat batas tanamlah tebu,","Selang batas tanam pegaga;","Jemari runcing menjadi gebu,","Selera menggoda di teratak bonda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105006,"Duduk di banir pohon mempelam,","Kicau unggas hinggap di dahan;","Sendirian duduk merenung kelam,","Menanti ilham titipan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105007,"Mari ke sungai mengail belida,","Berjalan pulang mandi di jeram;","Guru dan murid terpisah tiada,","Ibarat gulai perlukan garam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105008,"Langit mendung menyambar kilat,","Angin menderu beralih arah;","Bentang sejadah arah ke kiblat,","Bersimpuh sujud menghadap Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105009,"Cantik mawar pohon berduri,","Tertusuk jari lalu berdarah;","Di hadapan hakim guru berdiri,","Bicara hakim tinta sejarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105010,"Tebang rumbia terbang kelkatu,","Batang rumbia mari dicincang;","Terkurang didik waris membantu,","Salah bertindak bawa berbincang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105011,"Dari sawah lembayung petang,","Baju berselut mari direndam;","Guru mendidik waris menentang,","Lilin bangsa ditiup padam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105012,"Kupas kelapa diambil sabut,","Air kelapa dalam bejana;","Pimpinan bangsa minda serabut,","Semua dirancang serba tak kena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105013,"Ke seberang mendayung perahu,","Muara berlumpur mencari lokan;","Pabila ditanya semua tak tahu,","Bangsa terpinggir seteru disalahkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105014,"Bergolek perahu dibawa arus","Ditarik ke tepi diguna akar","Buat kerja biarlah telus","Jika tidak busuk tersebar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105015,"Pepatah Cina ada berkata,","Ikan buruk bermula dikepala,","Sudah lama reputnya ia,","Namun kekal diatas 'takhta'", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105016,"Gelang emas pending suasa,","Berlian di jari letak diriba;","Biarpun reput kekal berkuasa,","Didokong jejuak mengumpul laba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105017,"Di tepi tangga tanam setawar,","Tumbuh rendang mencecah tanah;","Adab bangsa semakin pudar,","Sejak pimpinan menyumpah seranah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105018,"Malam dingin berselimut gebar,","Seluruh badan sehingga ke wajah;","Andai busuk telah tersebar,","Jenuh berdebat mencari hujah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105019,"Sayang tempua bersarang rendah,","Burung belatuk di dahan berangan;","Rindu bertahun terlerai sudah,","Bicara mesra mengutip kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105020,"Sayang tempua bersarang rendah,","Burung belatuk di dahan berangan;","Rindu bertahun terlerai sudah,","Bicara mesra mengutip kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105021,"Halus songket tenunan tangan,","Untuk sampin raja sehari,","Bicara sesaat dalam kenangan,","Rasa terngiang sehingga kini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105022,"Halus songket tenunan tangan,","Dibuat sampin dek anak teruna;","Duduk sendirian mula berangan,","Termimpi-mimpi tika terlena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105023,"Ambil pisau kupaslah pauh,","Buah pauh di tapak tangan;","Buat teman berjalan jauh,","Dilindungi Allah dalam perjalanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105024,"Mari dihiris si buah pauh,","Buat rencah gulai semilang;","Tatkala dekat berasa jauh,","Tika berjauhan terasa hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105025,"Ikan keli si ikan semilang,","Ikan sepat si ikan gelama;","Ke manakah gerangan dikau menghilang,","Sehingga kini tak kunjung jelma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105026,"Gemalai beralun pelepah kelapa,","Bercanda gurau dibelai pawana;","Cuaca mendung renyai menyapa,","Hilang mentari rembulan ke mana?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105027,"Sepat benua di sawah bendang,","Digoreng garing pedas berlada;","Biarlah hina orang memandang,","Asalkan kita hidup bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105028,"Pagi hari ke pasar tamu,","Mencari kundur juga peria;","Terpisah waktu kembali bertemu,","Hati teruja jiwa bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105029,"Gugur pelam menimpa perdu,","Lebat berbuah pohon bidara;","Lama terpisah diterpa rindu,","Tika bertemu terkelu bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105030,"Dara jelita berjual jamu,","Panas mentari dipakai topi;","Inginku hadir dalam lenamu,","Berkongsi bahagia di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105031,"Bunga kemboja bunga kenanga,","Tanam di pasu dibentuk seni;","Tidurku lena mimpi berbunga,","Wajahku rindu menjelma kini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105032,"Asam kandis si jeruk cermai,","Tumbuh rendang tepi perigi;","Andai hadirku mengocak damai,","Rela daku mengundur pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105033,"Cili masak mari dipetik,","Lemak pedas cendawan kukur;","Senyum manis berwajah cantik,","Bersuara manja tika bertutur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105034,"Lemak pedas telor itik,","Dicampur dengan mangga muda;","Hati terpikat dek wajah cantik,","Renungan mata jiwa tergoda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105035,"Digulai pedas pucuk pegaga,","Digoreng pula ikan sepat;","Tidur malamku kerap terjaga,","Cinta jauh terasa dekat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105036,"Selit di pinggang pisau belati,","Buat peneman masuk ke hutan;","Fikir baik tak resah di hati,","Redha takdir titipan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105037,"Baju kelawar di bawa lena","Baju batek di majlis kenduri","Buat penawar resah dijiwa","Jangan di pikiak yang menyusahkan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105038,"Buah cempedak buah nangka","Goreng buat sarapan pagi","Maniskan senyuman jangan bersangka","Pasti aman hati ke hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105039,"Masak manis si buah nangka,","Manis lagi buah rambutan;","Demikian tanda orang tak suka,","Diberinya dalih dengan alasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105040,"Tebang hutan dibuat kota,","Menara tinggi empat persegi;","Berpantun seloka berpencak kata,","Menyirat makna dan akal budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105041,"Retak seribu cermin di dinding,","Jiran sebelah asyik bertukang;","Melihat ceria pengantin bersanding,","Meronta jiwa ingin menumpang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105042,"Pisang nangka si pisang embun,","Tumbuh berumpun di tepi sawah;","Hati resah rindu merimbun,","Orang dirindu buat tak kesah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105043,"Di taman bunga terbang rerama,","Menitis embun rumputpun basah;","Hilang sedetik rasa nan lama,","Rasa kasih jiwa gelisah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105044,"Beratap rumbia dindingnya papan,","Bersimpuh manja di tikar anyaman;","Resah hati menanti titipan,","Gelora jiwa menatap senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105045,"Letak pelita atas para,","Ambil sal atas peti;","Setiap kata ditimbang tara,","Tidak sesal kemudian nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105046,"Gulai keladi pedas terasa","Rebus nangka buat serawa","Hidup berbudi dirikan bangsa","Jadi pusaka subur di jiwa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105047,"Terbang rerama jauh berbalam","Hinggap di batang pangkal dahan","Hidup umpama kembara malam","Kerdip bintang penunjuk jalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105048,"Periuk kera di bawah titi","Batang berduri selirat akar","Hemah bicara santun pekerti","Kawan dicari musuh dihindar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105049,"Batang berduri selirat akar","Air ditebat membiak belida","Kawan dicari musuh dihindar","Doa sahabat ternilai tiada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105050,"Air ditebat membiak belida","Benih segemal penuh seraga","Doa sahabat ternilai tiada","Pemberat amal jalan ke syurga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105051,"Selut di lopak dari permatang","Mentari redup samar bayangan","Diberi secupak dibalas segantang","Mulia hidup rezeki berpanjangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105052,"Kutip jambu penuh seraga","Gugur kelapa menimpa kasturi","Hari ibu disambut sekeluarga","Hai bapa disambut sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105053,"Merayap puyuh unggas bernyanyi","Nyaring reriang di banir mengkudu","Dalam riuh terasa sunyi","Dalam riang diterpa rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105054,"Baju yang basah diatur disidai","Panas cuaca kering jua","Bina rumah jauh dari pantai","Sayang pantai merusuk juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105055,"Biar rantai,terikat raga","Tepi tersembunyi,tanjung jampi","Biarpun pantai,berubah juga","Muara sunyi,tetap menyepi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105056,"Wangi sungguh bunga sejambak,","Rasanya manis seperti gula","Pulau kaku dihempas ombak,","Namun pantai terhakis juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105057,"Mawar merah pohon berduri,","Tersalah petik terluka tangan;","Siang berhujah dengan mentari,","Malam merayu pada rembulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105058,"Di tebing sungai pokok berangan,","Anak remaja riuh berenang;","Senyum di bibir tajam renungan,","Gayamu manja hati terkenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105059,"Keris Melayu selit di pinggang,","Di celah sampin di rusuk kiri;","Cinta kasih tak boleh di pegang,","Tersemat utuh di sanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105060,"Cendawan kukur tumbuh di batang,","Dikutip digulai direndang maman;","Indah malam dek kerdip bintang,","Indah wajah dek manis senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105061,"Kelapa mawar pohonnya rendah,","Di tepi sawah dekat kuala;","Indah bercuti berakhir sudah,","Musim sekolah telah bermula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105062,"Berumpun buluh tumbuh di tebing,","Di likuan sungai hampir ke muara;","Tidur tak lena resah berbaring,","Menanti titipan kata nan mesra", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105063,"Alas duduk tikar mengkuang,","Mari dibentang hujung beranda;","Kasih lama jangan dibuang,","Cinta baru mekar di dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105064,"Ikan kerapu si ikan gelama,","Nelayan menjala pantainya dalam;","Biar berjarak rasa bersama,","Bicara mesra cinta tersulam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105065,"Teratak bonda berlantai papan,","Buluh gelegar dibuat pelantar;","Manja bicara berbalas titipan,","Digoda jelita jiwa bergetar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105066,"Bunga sejambak, tepian bidai","Tersusun bangku, di tepi pasu","Biar berombak, laut membadai","Pulau kaku, tetap membisu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105067,"Kemeja batik berbunga di tengah,","Klasik dipandang dari hadapan;","Tahun baru kian menjengah,","Menyapa dengan salam harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105068,"Dingin malam belaian bayu,","Membunga embun di rumpun semalu;","2017 melambai sayu,","2018 menjengah malu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105069,"Enak hidangan berulam cekur,","Pencuci mulut si kuih tumpi;","Mimpi malam mainan tidur,","Mimpi siang mainan sepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105070,"Ambil belati simpan di peti,","Simpan peti belakang tabir;","Indah pekerti tadbiran hati,","Mulianya hati basuhan zikir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105071,"Menyeberang jalan mari dituntun,","Pekan sehari ramainya orang;","Orang berpantun dia berpantun,","Dibibir merdu di hati curang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105072,"Pergi mengail ikan belida,","Digigit pacat titiskan garam;","Getar rindu gelora di dada,","Terasa lena mata tak pejam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105073,"Awan mendung mula berarak,","Saling bersahut burung taktibau;","Andai bangau menjadi merak,","Jangan lupa belakang kerbau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105074,"Sulur labu tumbuh melilit,","Labu tumbuh pangkal kelapa;","Tidur malam berbaju kulit,","Mesra berdakap lelah menyapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105075,"Acar mentah ditambah cuka,","Acar buah ditambah madu;","Biarlah wajah berparut luka,","Asal hatinya bahagia merindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105076,"Bunga api di malam gelita,","Indah langit tidak terperi;","Terlanjur kata jiwa derita,","Ceria canda dah hilang seri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105077,"Rumpun selasih akar terendam,","Kaduk menjalar di para-para;","Sayang kasih berbalas dendam,","Rindu tercalar mainan bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105078,"Kemeja batik berbunga di tengah,","Klasik dipandang dari hadapan;","Tahun baru kian menjengah,","Menyapa dengan salam harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105079,"Ke Kulim keluarga ikut bersama,","Berjalan-jalan lihat sekitar;","Hilang sedetik berasa lama,","Menanti ketemu hati bergetar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105080,"Buluh titi rotan berduri,","Ambil parang tebang rumbia;","Luka di hati bebat sendiri,","Harap orang sedang bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105081,"Tumbuk cili sehingga lumat,","Bilis berlada celur bayam;","Sunyi sepi malam Jumaat,","Menyemai cinta dalam diam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105082,"Ikan kekisi si pucuk paku,","Makan di lopak tengah permatang;","Pemandu teksi biadapnya laku,","Rezeki secupak rugi segantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105083,"Gulainya basi terlebih santan,","Pekatnya pati kacau bersudu;","Naik teksi untuk ke pekan,","Hasrat di hati sekadar membantu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105084,"Dara tersedu di belakang bidai,","Rambut disisir elusan jemari;","Angkuh pemandu teksi tersadai,","Mencurah pasir ke periuk sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105085,"Datuk panglima orangnya gagah,","Duduk santai mengunyah sirih,","Tiada duka tiada marah,","Kuat usaha tiada merintih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105086,"Duduk jejaka jiwanya gundah,","Sedang puteri berduka lara;","Hati terluka dendam tak sudah,","Rugi diri bangsa sengsara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105087,"Kelapa dikupas diambil sabut,","Buah dibelah airnya ditadah,","Nafsu amarah mulut menyebut,","Luka berdarah dendam tak sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105088,"Dagang sentri mengenderai pedati,","Bercanda cerita perihal negeri;","Mimpinya ngeri realiti tak pasti,","Bersorak gempita di kubu sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105089,"Titip bicara bisikan naluri,","Musnah biadat dari sanubari;","Masihkah membara angkuh di hati,","Sesudah mendapat mimpi realiti?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105090,"Kertas selembar tuliskan puisi,","Titip bicara bisikan naluri;","Apa khabar pemandu teksi,","Masihkah membara angkuh di hati?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105091,"Gugur berserakan bunga selasih","Di pagi hari banyaknya luruh","Menumpang bukan nak berkongsi kasih","Sekadar mencari tempat berteduh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105092,"Letak keladi bawahan redup,","Di dalam perahu di bawa selasih;","Sudah menjadi resamnya hidup,","Tiada yang mahu berkongsi kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105093,"Cenderawasih terbangnya tinggi,","Di tanah lapang melingkar sawa;","Bila kasih hendak berbahagi,","Yang menumpang pasti kecewa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105094,"Ke tempat berkelah berperahu tambang","Emak di huma menyemai lada","Pandai-pandailah hidup menumpang","Belajar terima apa yang ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105095,"Pesisir laut tempatnya lapang,","Mengukir minda menghias pemandangan.","Ada juga tempat menumpang ,","Dari hidup jadi gelandangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105096,"Hinggap tekukur di hujung dahan","Berhampiran titi batang pinang","Kena bersyukur dengan keadaan","Barulah hati damai tenang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105097,"Merah perati benang berlembar,","Dipegang budak mengikat bijan,","Bisikan di hati banyak bersabar,","Mendung tidak bermakna hujan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105098,"Tinggi sungguh Gunung Ledang,","Puteri nya cantik menjadi rebutan,","Belum hatiku rasa senang,","Ingin tahu jawapan kepada soalan,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105099,"Tinggi sungguh Gunung Ledang,","Puteri nya cantik menjadi rebutan,","Kekasih yang jauh jangan dikenang,","Tuan datang kami alu-alukan,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105100,"Tengah hari memasak lokan,","Makan bersama dengan nasi,","Adat silaturahim kita eratkan,","Tersimpul sudah sebuah janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105101,"Bangun pagi minum teh pekat,","Makan nasi berlauk ikan,","Berada di sini ingin mendekat,","Salam kenali saya berikan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105102,"Lipat gebar di katil tunggal,","Kantuk terasa bangun terpaksa;","Kuasa bubar jawatan tanggal,","Masih berasa diri berkuasa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105103,"Lipat gebar di katil tunggal,","Tertinggal sehelai di tali penyidai,","Kuasa bubar jawatan tanggal,","Maruahnya murah dah lama tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105104,"Jika ada dulang disimpan,","Jangan dibiar jatuh terhempas;","Jika ada kuasa di tangan,","Jangan biar orang merampas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105105,"Gagah sasa pendekar budiman,","Gagah semangat pencak di tanah;","Tika kuasa dalam genggaman,","Janga khianat menjaga amanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105106,"Jika ada dulang disimpan,","Jangan dibiar ia terbempas,","Mudah melanggar perentah Tuhan,","Harta hilang kuasa terlepas!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105107,"Jika ada dulang disimpan,","Jangan dibiar ia terhempas;","Dusta dalih wang diumpan,","Hutang ditimbun maruah dirampas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105108,"Dusta dalih wang diumpan,","Hutang dihimpun maruah digadai,","Perintah Tuhan diambil ringan,","Jiwanya gundah tenangnya berkecai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105109,"Dara jelita menjeling manja","Teruna desa berkain pelikat","Amanat negara harus dijaga","Bukannya merompak harta rakyat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105110,"Cikgu menjual","Murid membeli","Cikgu soal","Murid jawab berkali-kali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105111,"Sorong tarik mata gergaji,","Habuk dedak habuk papan;","Orang politik murah berjanji,","Tertunai tidak kaya alasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105112,"Naik ke darat dari menjala,","Lubuk paya dipasang lukah;","Guru ibarat dian menyala,","Tebar cahaya pahala melimpah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105113,"Daun kering gugur ke tanah,","Anak burung di ranting kayu;","Hilang parut luka bernanah,","Cinta lerai diimbau sayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105114,"Selerak kera tabuh dipalu,","Berlagu ungka di tengah rimba;","Hati rindu diungkap malu,","Bagai perigi mencari timba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105115,"Masak sayur pucuk cemperai,","Buat hidangan tengah hari;","Cinta hancur kasih berderai,","Ke mana gerangan membawa diri?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105116,"Pinggir belukar tumbuh cemperai,","Mari dicelur air secangkir;","Tersimpul tali bisa terlerai,","Tersimpul kata tidak termungkir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105117,"Pinggir belukar tumbuh cemperai","Di celur bolih makan beramai","Tersimpul tali bisa terlerai","Tersimpul kata kita berdamai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105118,"Lemak pedas pucuk pegaga,","Pisang karok tepi belukar;","Hidup diduga hidup menduga,","Tertitip takdir insan diuji sukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105119,"Tika jujurku disoal sahabat,","Daku berdiam kudrat kukumpul,","Ada rahsia hasrat tersekat,","Ada terbelit kuat tersimpul.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105120,"Tinggi rendang pohon bidara,","Rendang berbuah pohon cermai;","Di luar pintu dilambai bendera,","Di dalam hati melambai damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105121,"Buah pulasan betik papaya,","Upin dan Ipin dikejar kera;","Ber'Barisan' kereta di lebuh raya,","Memilih pemimpin 'Harapan' negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105122,"Duduk di titi menanti senja,","Basah celana mari diperah;","Bahagia hati selera gelora,","Tidur lena mimpi tak sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105123,"Budak-budak mandi bonda,","Bocompogho highuk mempikuk;","Tolampau bogek kono boda,","Lobam botih koting meghengkuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105124,"Merah warna bijian saga,","Kunang-kunang si kelip-kelip;","Mewah hidup jangan peraga,","Takdir datang mata berkelip.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105125,"Sambal belacan celur bayam,","Goreng sotong bersalut tepung;","Elok ditiru perangai ayam,","Bertelor sebiji riuh sekampung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105126,"Kala senja masih di titi,","Hendak mandi tertinggal timba;","Terpapar di mata tercuit di hati,","Didik jiwa berlapang dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105127,"Buah tangan ke rumah nyonya,","Bawa pinang bawa kelkati;","Kerdil hamba gelar tak punya,","Tidak terlintas riak di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105128,"Ribut dah reda hujan berhenti,","Buat serawa pisang sesikat;","Kasih bonda tiada berganti,","Taruhan nyawa doanya berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105129,"Gulai lomak nasi ditanak,","Dialeh loka disapu ghemah;","Handa omak mendidik anak,","Lambek bongka digoga ae ghumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105130,"Mencaghak matoaghi kopalang paneh,","Bolonjap dek poloh molokit bodan;","Sojak kocik banyak tokomeneh,","Habih bighat solughuh bodan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105131,"Pulas-pulas tangkai berangan,","Dipetik jangan buah muda;","Tua renta diperlekeh jangan,","Muda tak usah mendabik dada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105132,"Wangi semerbak mekar melati,","Buat ramuan minyak hikmat;","Tidak pernah lupa di hati,","Di St. Columba awal berkhidmat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105133,"Mari ke sungai kita berenang","Dekat pondok anjing mengejar","Tempat jatuh lagi dikenang","Ini kan pula tempat mengajar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105134,"Ke sungai mari berenang,","Nasi lemak dibekalkan Ain;","Tempat jatuh lagi dikenang,","Inikan pula tempat bermain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105135,"Keli haruan di selut paya,","Ikan puyu ikan berduri;","Gelap alam terang cahaya,","Ibarat lilin membakar diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105136,"Bekal ulam tika berlibur,","Jeram tingkat di kaki bukit;","Kekal bergantung maruah lebur,","Diberi dihulur segala diungkit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105137,"Pucuk putat si pucuk cekur,","Bekal ulam tika berlibur;","Beralih pimpin negara hancur,","Kekal bergantung maruah lebur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105138,"Debu sawang sukar dipunggut,","Takut ada kutu dan hama,","Bukan hati nak merungut,","Cuma terkilan menunggu lama,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105139,"Usah dipalu gantungan genta,","Debu dan sawang sukar dipungut;","Kira demikian perihal cerita,","Redha dan redah tanpa merungut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105140,"Terbang riang si burung bayan,","Hari panas berjemur kain.,","Aduhai hati, seorng tak terlayan,","Apatah lagi nak cari lain,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105141,"Terbang kelkatu di waktu petang,","Layang-layang di kayu jati;","Andai menunggu tiada datang,","Cari lain buat pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105142,"Ayam laga makan di tangan,","Bulu jalur balung berbelang;","Rajuk hati dipujuk jangan,","Rindu kasih rajuknya hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105143,"Elok cincin sarung ke jari,","Batu delima diikat suasa;","Jangan dikejar cinta nan lari,","Lelah jiwa mengikut rasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105144,"Gelang emas melingkar di tangan,","Cincin suasa di jari manis;","Cinta lepas dikenang jangan,","Andai dikenang didera tangis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105145,"Sungai Korok MADA berpaya,","Terpijak selut kaki terbenam;","Untungkah pesawah di pilihan raya,","Menuai sehari lima tahun menanam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105146,"Ambil kelkati pinang dikelar,","Dialas talam diletak tepak;","Luntur pekerti budi tercalar,","Jasa silam diendah tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105147,"Terbang pipit disambar helang","Hinggap sebentar di sawah bendang","Bagaimana rajuk nak hilang","Orang ditunggu tak kunjung datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105148,"Cincin suasa di jari tangan,","Pending lama raja masyhur;","Cinta bangsa khianat jangan,","Cinta agama bicara luhur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105149,"Emas suasa pending pusaka,","Sampin songket susun selisih;","Khianati bangsa janji dusta,","Beri diungkit merayu dipilih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105150,"Keris pendek pending pusaka,","Selit di pinggang teruna terbilang;","Adat dikelek adab dijaga,","Janji dipegang maruah dijulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105151,"Comel lincah si anak kera,","Dihulur kacang lalu menari;","Mimpi ngeri anak didera,","Tiada kasih gersang naluri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105152,"Ikan puyu di paya benta,","Asyik mengail hingga senja;","Bertemu bibir mekar cerita,","Imbau kenangan tika remaja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105153,"Ambil tukul dan juga pahat,","Cangkul gergaji juga penggali;","Gerhana bulan boleh dilihat,","Gerhana di hati siapa peduli.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105154,"Senja hari pasanglah pelita,","Pelita disimpan di atas para;","Bibir bertemu terpejam mata,","Hati berdebar terkelu bicara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105155,"Cantik bercorak ikan belida,","Disentap kail sangkut di benta;","Kasih sayang syarat tiada,","Saling percaya tanpa dusta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105156,"Buah cempedak lebat di pangkal,","Ambil sebiji ke rumah nyonya;","Malam gelita jangan menakal,","Terlihat tidak terasa getarnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105157,"Merah oren limau mandarin,","Disusun elok di raga kayu;","Andai diimbau cinta kelmarin,","Hati berdebar terasa sayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105158,"Dara jelita atas permatang,","Kebaya ungu selendang sangkut;","Pucuk dicita ulam mendatang,","Senyum dibalas cinta disambut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105159,"Penuh sekepuk buah berangan,","Tandan pisang susun ke tepi;","Cinta bertepuk sebelah tangan,","Kasih gersang kian menyepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105160,"Bayu malam daun berdesir,","Debur ombak terlerai sepi;","Cinta resah kasih terusir,","Rindu pendam di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105161,"Kuning masak si buah langsat,","Merah ranum buah pulasan;","Berjalan tanpa peta sesat,","Berdua tanpa kata bosan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105162,"Tinggi gunung tampak berbalam,","Cerun landai ke ujung tanjung;","Tika jauh berkirim salam,","Tika dekat saling mengunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105163,"Terbentang laut tidak bertepi,","Bertemu langit di ujung mata;","Ucap berdegar berapi-api,","Tutur bicara menjaja dusta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105164,"Ujung tanjung di teluk sepi,","Dingin tubuh diusap bayu;","Cinta semalam bagai mimpi,","Kala dikenang hatiku sayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105165,"Di dada langit terbit pelangi,","Tiada mendung kelam gelumat;","Salam kasih mekar mewangi,","Tanpa amarah dendam kesumat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105166,"Emas perak tembaga suasa,","Cincin sebentuk di jari manis;","Diri tersisih sedih terasa,","Andai diingat ingin menangis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105167,"Riak debur malam kenangan,","Putih pasir dan utuh batuan;","Biar bertabur hati di awangan,","Semua kuusir dikau pilihan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105168,"Kerdip bintang di langit biru,","Sipu rembulan di celah awan;","Pemimpin berdebat kian keliru,","Kuasa dan harta dalam igauan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105169,"Pemimpin berdebat kian keliru,","Kuasa dan harta dalam igauan,","Banyak khianat banyak menipu,","Asalkan kuasa dimilik berkekalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105170,"Emas perak intan terpilih,","Kain songket sulam berkait;","Khianat menipu dusta berdalih,","Telunjuk lurus kelingking berkait.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105171,"Digulai pedas semilang salai,","Mangga muda dihiris halus;","Kasih berbalas rindu terkulai,","Debar di dada cintanya tulus.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105172,"Redup anjung hening dan dingin,","Pohon kelapa tumbuh bersilang;","Hidup disanjung serba teringin,","Dipuji teralpa wibawa pun hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105173,"Mari minum air kelapa,","Kulit kelapa buang ke tepi;","Salam kasih rindu menyapa,","Sehening tanjung di teluk sepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105174,"Bermain wau atas permatang,","Sesekali berlari terkadang berjalan;","Andai daku seumpama bintang,","Sudikah dikau menjadi rembulan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105175,"Ramah debur menyapa pantai,","Sampan kecil dialun ombak;","Kasih berkubur sedih mengintai,","Rasa terpencil melayan sebak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105176,"Kuning oren limau mandarin,","Mari dikupas tebal kulitnya;","Diputar waktu detik kelmarin,","Cinta terlepas orang empunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105177,"Ngelamun dara nasinya hangit,","Garam gulai tidak berasa;","Beraja helang di dada langit ,","Ayam di kepok menjadi mangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105178,"Di bukit sepi tumbuh mempelam,","Di cerun bukit pohon tembusu;","Tika jauh berkirim salam,","Tika dekat diam membisu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105179,"Sambut raya bakar bahulu,","Enak rasanya dapor berasap,","Dah dekat lidah pun kelu,","Rupanya jari ligat balas whatssap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105180,"Pucuk maman dimasak rendang,","Ayam kampung goreng berlada;","Hina miskin tidak dipandang,","Ramah disapa kaya berada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105181,"Pucuk maman dimasak rendang,","Ayam kampung goreng berlada;","Hina miskin tidak dipandang,","Ramah disapa kaya berada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105182,"Dingin sungai mengalir deras,","Di tebing tumbuh si pohon ara;","Jantung pisang digulai pedas,","Sepat goreng membuka selera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105183,"Memetik cermai juga keranji,","Buah bidara dan nangka madu;","Musim menyemai ringgit dan janji,","Lembut bicara manis bermadu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105184,"Pohon kelapa di ujung pelimbahan;","Rimbun pandan tumbuh berumpun,","Hidup terikat dek takdir Tuhan,","Terungkap syukur dalam keampunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105185,"Pelepah nyiur si daun kelapa,","Burung tempua buat sarang;","Dingin pagi mentari menyapa,","Moga cuaca cerah ke petang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105186,"Daun kesum si daun ulam,","Mari di beli di pasar pagi;","Waalaikummussalam,","Juga selamat pagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105187,"Kembang mawar merah warna,","Harum bau menusuk hidung;","Dingin malam dakap tak lena,","Tangan melingkar kaki tersadung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105188,"Anggun lenggang dara jelita,","Senyum manis hias rupa;","Lena bergenang si air mata","Tawa memendam tiap nestapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105189,"Rendang buah pohon bidara,","Ujung kampung pohon beringin","Lurus lidah tulus bicara,","Bukan lalang ditiup angin", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105190,"Tampak berbalam tinjau-meninjau,","Padi huma ditampi-tampi;","Tidur malam terjaga mengigau,","Kekasih lama di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105191,"Ramah berbunyi burung tekukur,","Hinggap mari di dahan angsana;","Baru baring sudah berdengkur,","Orang sebelah sukar nak lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105192,"Gugur mangga gugur mempelam","Penuh sebakul sisih ke tepi;","Andai diimbau cerita silam,","Air mata bergenang di pipi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105193,"Jinak merpati makan di tangan,","Ayam laga balungnya merah;","Dendam hati dituruti jangan,","Hindari jiwa dari amarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105194,"Tobang pokok pangkeh dahan,","Pokok tumbang monimpo kodai,","Amarah hati tak boleh ditahan,","Dikobaan negaro kito nak tergadai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105195,"Biji mangga enak dikulum,","Setelah dikupas dimakan buah;","Ada amarah dibenarkan hukum,","Perihal mungkar keji maruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105196,"Ambilkan asam limau kasturi,","Ikan disiang dikelar-kelar;","Hidup umpama pagar berduri,","Diterjah jatuh dipanjat calar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105197,"Ambil kanvas lakarkan arca,","Di kaki tangga depan pergola;","Hidup umpama cermin kaca,","Dipamer semua terlihat segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105198,"Tinggi rendang pokok angsana,","Bertupang dahan pokok kapas;","Gelita malam bulan gerhana,","Kasih hanyut ke laut lepas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105199,"Enak dimakan si buah melaka,","Nira enau dibuat gula;","Cerita orang palsu belaka,","Sendiri menipu benar segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105200,"Hijau daun dimakan ulat,","Lalu layu gugur ke tanah;","Suara rakyat kuasa berdaulat,","Pilih pemimpin punya amanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105201,"Badak berendam inti kelapa,","Daun pandan kerat di rumpun;","Siang menegur malam menyapa,","Bila kan reda rindu terhimpun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105202,"Pucuk kaduk si daun palas,","Sisip di pinggang pisau belati;","Bibir cantik limau seulas,","Lirik mata menawan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105203,"Sahut bersahut unggas berkicau,","Itik bemban dan itik angsa;","Bersua sedetik rindu meracau,","Cinta kasih mendera rasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105204,"Daging busuk jangan dibuang,","Dihiris digulai mari dimakan;","Sesak nafas mencari ruang,","Malu muka mencari jalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105205,"Kelapa parut mari diramas,","Buat santan tepung pelita;","Walau di mana hujan emas,","Bawa balik ke negeri kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105206,"Pucuk pauh dahan mempelam,","Buah nangka bau cempedak;","Malang si bisu bermimpi malam,","Terasa ada terkata tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105207,"Buat batas ditanam maman,","Dipetik dijual ke seberang;","Hidup umpama tikar anyaman,","Digulung dibentang sesuka orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105208,"Merah warna buah pulasan,","Masak ranum di hujung dahan;","Kerdil kita di mata insan,","Moga besar di mata Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105209,"Pagi hari terbit mentari,","Riang siulan unggas;","Ke sana ke mari menteri,","Hampir PRU semua bergegas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105210,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Perniagaan rezeki penuh berkat,","Berderma berzakat setelah untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105211,"Kelajuan di lebuhraya jangan diduga,","Bior lambat asalkan selamat;","Kalau nak maju dalam berniaga,","Contohi tokeh 99 Speedmart.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105212,"Ulam raja bunga kantan,","Cicah sambal makan berselera;","Berniaga untuk tambah pendapatan,","Satu ibadah amalan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105213,"Bunga mawar bunga kemboja,","Dibeli untuk buat perhiasan;","Usah bertawar tika bekerja,","Rajin usaha bawa kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105214,"Kalau mahu jadi juara,","Jangan lupa senyum padanya;","Saya berniaga jual suara,","Untuk menghibur pendengar semuanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105215,"Di atas tangga duduk berbeka,","Setelah puas menuju sawah;","Bidang niaga cuba teroka,","Rezeki luas hidup pun mewah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105216,"Gadis manis siram tanaman,","Makan pula si buah rambutan;","Niaga satu bidang pekerjaan,","Dimana saja asal ada keberkatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105217,"Bila gelap pasang lampu,","Lampu lama sudah berkarat;","Jikalau berniaga jangan menipu,","Nanti hidup tidak berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105218,"Indahnya si bunga Cempaka,","Baunya harum cantik berseri;","Berkahnya hidup berniaga,","Beramal sambil mencari rezeki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105219,"Untung bukan sebarang untung,","Untung berniaga berlipat-lipat;","Jangan asyik nak kira untung,","Bayar zakat baru untung berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105220,"Tepi jalan sekawan balam,","Ayam jantan tuju telaga;","Berniaga sambilan di pasar malam,","Jana pendapatan untuk keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105221,"Berjimat cermat memang baik,","Kalau berbelanja jangan boros;","Lebihan belanja bawak balik,","Jangan ditabor tanda boros.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105222,"Ramainya haijin antara haijin,","Haijin juga dari institut;","Banyak rajin perkara rajin,","Rajin berniaga amat dituntut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105223,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Untung perniagaan kantung pun berat,","Tunaikan haji berkat menabung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105224,"Makan nasi ulam pegaga,","Jemput juga jiran terdekat;","Banyak untung dalam berniaga,","Jangan lupa membayar zakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105225,"Anak sembilang ditapak tangan,","Buat santapan ditengah hari;","Usaha cemerlang doa dipohonkan,","Ceburi perniagaan memantapkan ekonomi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105226,"Anak tupai menari-nari,","Jatuh tersungkur mengejar rama-rama;","Berjimat untuk kemudian hari,","Hidup selesa kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105227,"Turun kesawah perlu peka,","Takut terpijak duri gelama;","Masa berniaga usahlah leka,","Solat lima waktu perlu diutama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105228,"Cari pegaga dihujung tanjung,","Buat santapan ditengah hari;","Orang berniaga patut disanjung,","Dengan ikhlas iya berbudi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105229,"Kalau tuan pergi ke darat,","Singgah mari di hujung tanjung;","Berniaga jujur barulah rezeki berkat,","Dunia akhirat mendapat untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105230,"Rasa manis buah naga,","Isu merah sedap dimakan;","Rezeki itu dalam berniaga,","Sunah Rasulullah perlu diamalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105231,"Bulan lelong sembarang lelong,","Lelong murah bandar Melaka;","Bukan untung sembarang untung,","Jujur berniaga amalan mulia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105232,"Kalau tuan pergi ke darat,","Jalan mari ke hujung tanjung;","Berniaga itu juga satu ibadat,","Jadi usah hanya mengejar untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105233,"Makan nasi ulam pegaga,","Duduk di dapur rumah Mak Bidah;","Kalau kita rajin berniaga,","Rezeki kita bertambah-tambah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105234,"Kalau puan pergi ke darat,","Sambil membawa bekalan dijunjung;","Selagi masih kuat kudrat,","Semangat berniaga amat disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105235,"Buah naga, buah peria,","Mari disimpan di atas peti;","Rajin berniaga amalan mulia,","Mendapat rezeki senangnya hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105236,"Jalan mari ke hujung tanjung,","Setengah depa jalan disekat;","Bila berniaga sudah untung,","Jangan lupa bayar zakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105237,"Pergi ke kedai membeli kangkung,","Pergi dengan anak Pak Mahat;","Rajin berniaga banyak untung,","Jangan lupa membayar zakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105238,"Sedap sungguh kopi cap raga,","Masukkan air panas kacau dan minum;","Bermula kecil-kecilan kita berniaga,","Bila dah berjaya hati gembira wajah tersenyum.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105239,"Pak Mamat turun menjala,","Dapat sebakul ikan Tilan;","Jangan malu nak berniaga,","Walau sekadar penjaja jalanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105240,"Pagi-pagi buka warung,","Jualan nasi beraneka kuih;","Ramai orang datang berduyung,","Alhamdulillah rezeki melimpah ruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105241,"Hadir tetamu mari dijamu,","Tertumpah air basah celana;","Sering terbayang cantik wajahmu,","Ingin bertemu di dalam lena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105242,"Turun ke air mendayung sampan,","Ke seberang bawa berangan;","Tinggi gunung tinggi harapan,","Burung terbang hinggap di tangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105243,"Hari senja pasang pelita,","Letak pelita di tiap bucu;","Rang muda bermain cinta,","Rang tua bermain cucu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105244,"Tajam terasa duri mengkuang,","Mari diraut menganyam tikar;","Daging busuk jangan dibuang,","Kasih jangan silih bertukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105245,"Tumbuk cili dibuat sambal,","Ikan goreng dicicah budu;","Kian lama kasih menebal,","Jauh di mata di hati rindu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105246,"Digoreng garing buah berangan,","Buat jualan di pasar Sabtu;","Indahnya muda dalam kenangan,","Terasa ingin memutar waktu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105247,"Batik sedondon hitam celana,","Songkok hitam di atas dahi;","Gelita malam bulan gerhana,","Terang hati cahaya IlLahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105248,"Hitam sedondon baju celana,","Sampin songket bunga perang;","Rindu bulan bulan gerhana,","Rindu kasih dah milik orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105249,"Bunga senduduk rembang mentari,","Angin berdesir menjelang petang,","Bukan duduk bimbang menanti,","Duduk mentafsir takdir mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105250,"Pohon mempelam di laman nyonya,","Di banir angsana burung tekukur,","Mimpi semalam hasrat berbunga,","Di hujung lena kelopaknya gugur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105251,"Intung api tepian telaga,","Kertas kajang terbakar berasap;","Berhati hati bila berniaga,","Rugi memanjang untungnya lesap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105252,"Burung merpati terbang bertiga,","Bersama anak menuju ke hutan;","Berhati-hati kalau nak berniaga,","Ramai orang yang ambil kesempatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105253,"Ramai datang bergotong-royong,","Sambil bawa sedikit juadah;","Jangan berniaga nak cari untung,","Berniagalah sambil sedekah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105254,"Gantung raga tangkai diikat,","Tepian batu cuaca kelam;","Untung berniaga keluar zakat,","Dapat membantu sesama Islam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105255,"Kari terung gulai cendawan,","Kelapa parut pulut kelamai;","Mencari untung dalam perniagaan,","Harga patut pelanggan pun ramai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105256,"Bukit Saga arah barat,","Bawa kantong berisi surat;","Kalau niaga ikut syariat,","Pasti untung dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105257,"Rotan dipotong diikat-ikat,","Diguna untuk buat titi;","Untung yang sedikit tetapi berkat,","Penjual dan pembeli berpuas hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105258,"Titi diguna setiap hari,","Untuk mengambil air di perigi;","Perniagaan maju tanda diberkati,","Berniagalah apa saja pasti tak rugi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105259,"Pokok mempelam pokok mempelas,","Rotan dipotong diikat-ikat;","Berjual beli biarlah ikhlas,","Untung yang sedikit tetapi berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105260,"Sumur dikiri dikanan telaga,","Dihujung sawah memasag lukah;","Jujur dan ikhlas adab berniaga,","Untung sedikit tetapi berakah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105261,"Radio Klasik menggamit Kenangan,","Irama pula merentas zaman;","Memang berniaga mencari keuntungan,","Namun keikhlasan menjadi amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105262,"Mari petik daun pengaga,","Pengaga tanam tepi kolah;","Hidup senang dari berniaga,","Moga sentiasa diberkati Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105263,"Beli sepatu di kedai Atan,","Sepatu buluh ciptaan nyonya;","Berniaga itu amalan ikutan,","9/10 rezeki dariNya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105264,"Tergantung-gantung banyak lampu,","Menarik mata merenung melekat;","Mencari untung jangan menipu,","Berniagalah kita supaya berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105265,"Ikan diduga cara nelayan,","Ikan dipikat unjam kelapa;","Gigih berniaga hasil lumayan,","Pastikan zakat jangan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105266,"Murni peribadi penuh keikhlasan,","Sayang menyayangi akrab persahabatan;","Berniaga berjaja dengan kewarasan,","Dunia akhirat menjadi sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105267,"Buah kuini pucuk pegaga,","Ikan keli dikerat kerat;","Sungguh murni khidmat peniaga,","Berjual beli untuk masyarakat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105268,"Padang senget tempat bersilat,","Duduk bersimpuh sambil berpakat;","Simpan ringgit wajib berzakat,","Duit disimpan jadinya berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105269,"Kaki sakit terpijak kelapa,","Duduk di banir daun di kerat;","Biar sedikit tidak mengapa,","Sentiasa mengalir membawa berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105270,"Junjung raga menjual ubi,","Dicicah kerutub ikan haruan;","Amalan berniaga di zaman nabi,","Ada termaktub dalam al qur'an.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105271,"Dahan nangka condong ke penjuru,","Ditarik lentur robeklah tabir,","Bertatap muka menunduk malu,","Kata diatur tersekat di bibir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105272,"Kebaya merah benang tersangkut,","Simpul rombak diikat santai,","Beralih arah kasih mengikut,","Sesetia ombak kepada pantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105273,"Kebaya pendek pakai bersantai","Mari berkunjung ke rumah datuk","Setiakah ombak kepada pantai.","Merubah tanjung menjadi teluk?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105274,"Buah beluluk jatuh dikaut,","Buah cempedak si buah nangka,","Tanjung dan teluk masih di laut,","Terputus tidak setia mereka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105275,"Sanggul rambut sepit bersilang,","Kebaya labuh tekat bersulam,","Nama disebut budi dijulang,","Jasad luluh ingatan tak padam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105276,"Sulur keladi paya berselut,","Ikan semilang renang ke hilir,","Orang berbudi sering disebut,","Jasad hilang pahala mengalir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105277,"Di paya rawa berumpun benta,","Rumpun dibentur memercik ke mata,","Andai di jiwa angkuh bertakhta,","Tindak tutur undang sengketa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105278,"Guruh petir bertingkah di langit,","Panahan kilat pohon berdesir,","Payah getir bertempur sengit,","Bulat muafakat penjajah diusir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105279,"Bukan amarah dendam membara","Sedang dendam bukan kesumat","Mengusir penjajah dengan bicara","Derita digenggam membakar semangat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105280,"Andai berbalas cinta terungkap,","tak mungkin pungguk rindukan rembulan,","Andai takut diri ditangkap,","Jangan la tunggu berbulan-bulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105281,"Sarung pelikat basahan embun,","Rendang pauh sarang kerengga,","Andai dekat rindu merimbun,","Tika jauh dijenguk curiga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105282,"Pisang emas dibawa belayar","Pisang tanduk dimakan kera","Carilah ubat segala penawar","Untuk pulihkan hati yang lara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105283,"Pisang emas dibawa belayar,","Pisang tanduk dimakan kera,","Bagai diramas jiwa ditawar,","Jalan tunduk tampung derita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105284,"Lumpur selut di rimbun padi,","Pohon semalu tengah permatang,","Tutur di mulut gambaran budi,","Santun pemalu maruah ditatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105285,"Teratak condong ke sawah jiran,","Permatang selut di kaki tangga,","Tika dikendong habis berciciran,","Sembah melutut derita warga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105286,"Padi berjajar serumpun empat,","Teratak condong ke sawah jiran,","Andai dikejar tidak mendapat,","Yang dikendong jangan berciciran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105287,"Andai sirih pulang ke ganggang,","Andai pinang pulang ke tampuk,","Berpenat jerih janji dipegang,","Jiwa berdendang hati bertepuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105288,"Tumbuh kepaya di rendang ara,","Di ranting ara burung menyanyi","Di buana maya menyapa mesra,","Terhibur lara terubat sunyi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105289,"Tumbuh kepaya di rendang ara,","Di ranting ara burung menyanyi","Di buana maya menyapa mesra,","Di depan muka menyepi sunyi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105290,"Kalau tuan pergi ke darat,","Jalan mari hujung tanjung;","Simpan ringgit nanti berkarat,","Bawa berniaga cari untung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105291,"Pucuk dicita ulam pudina,","Manis serawa juadah terhidang,","Alam gelita bulan gerhana,","Sepi jiwa cinta terhalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105292,"Petang remang terbang kelkatu,","Hilir muara ikan belida,","Terlihat udang sebalik batu,","Sopan bicara mesra menggoda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105293,"Sopan bicara mesra menggoda,","Lembutlah gigi daripada lidah,","Berbuat baik berpada-pada,","Jangan tertipu dengan lembutnya lidah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105294,"Hilir ke muara si ikan belida,","Waktu pagi pemandangan indah,","Sopan bicara mesra menggoda,","Lembutlah gigi daripada lidah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105295,"Usah dijolok sarang tebuan,","Bisa sengatan maut menanti,","Rupa paras jadi tumpuan,","Alamat lambat kahwin la nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105296,"Tajam golok bawa ke hutan,","Tebal hutan pohon meranti,","Usah dijolok sarang tebuan,","Bisa sengatan maut menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105297,"Bunga melati si bunga kekwa,","Mekar pagi embun melata,","Biar hati didakap kecewa,","Asal janji kata dikota.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105298,"Kukus pulut di waktu petang,","Angkat kuali beralas kain,","Kita melukut di tepi gantang,","Tersisih diri dek orang lain.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105299,"Bangun sahur sebelum siang,","Serasa susah nasi ditelan,","Bagai aur ditarik songsang,","Jiwa resah hati terkilan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105300,"Senja kala hujan gerimis,","Pasang pelita terlihat terang,","Bagai gembala diberi keris,","Memberi cinta tersalah orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105301,"Padi menguning padi terbit,","Gatal benta rayap bak hama","Bulu kening si bulan sabit,","Wajah jelita bulan purnama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105302,"Pohon terap tumbuh merata,","Angin semilir bertiup perlahan,","Jangan diharap janji jelita,","Andai mungkir sejuta alasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105303,"Cekak musang baju Melayu,","Sampin putih berjalur hitam,","Kasih sayang jodoh diseru,","Hati merintih dalam diam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105304,"Cekak musang baju melayu,","Sampin putih berjalur hitam,","Sungguh indah tradisi melayu,","Salah dan silap tidak berdendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105305,"Sungguh gembira senang hati,","Dipagi syawal melihat surat,","Terkadang lidah menikam hati,","Mohon diampun dunia akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105306,"Juadah berbuka ketupat pulut,","Dihiris dibelah dicampur kuah,","Tertikam luka sembuh berparut,","Seluruh salah dimaaf sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105307,"Daunan nyiur cumbuan angin,","Indah pantai mainan camar;","Khianat ditegur amanah diingin,","Akal pandai jangan dicemar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105308,"Air sirap si pandan wangi,","Kurma kismis asam halwa,","Jangan diharap indah pelangi,","Hilang gerimis mata kecewa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105309,"Lebah madu bunga setaman,","Angin semilir hangat mentari,","Ingin mengadu tiada berteman,","Mata berair disapu sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105310,"Camar putih terbang melayang,","Berpuput angin di laut teduh,","Gerangan kasih cinta terhalang,","Kamar dingin badan berpeluh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105311,"Dibasuh bersih beras ditanak,","Pucuk gelugur si paku tanjung,","Peluk kasih dimanja anak,","Salah ditegur betul disanjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105312,"Butang ketip kebaya si dara,","Cantik jahitan di lincah tangan,","Potret dititip menyapa mesra,","Imbau ingatan kutip kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105313,"Pucuk pauh delima batu,","Anak semilang di tapak tangan,","Biarpun jauh beribu batu,","Hilang di mata di hati jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105314,"Buah langsat kuning mencelah,","Senduduk tidak berbunga lagi,","Sudah dapat gading bertuah,","Tanduk tidak berguna lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105315,"Andai kata tersalah ungkap,","Andai gurau tersalah tempat,","Andai hasrat tersalah harap,","Andai terbuang jangan tersisih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105316,"Andai kantuk bantal disorong,","Andai lapar jamahlah hidangan","Andai gelisah marilah sembang","Andai marah segera maafkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105317,"Rimbun ara dilitupi kabus,","Tanjung kuala tampak seberang,","Lagi bara kugenggam hangus,","Inikan pula si hitam arang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105318,"Topi kemas tepi bertaut","Jahitan mula di indrapura","Kopi panas lagi kau tiup","Inikan pula panasnya bara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105319,"Berbalam gunung tampak berbayang,","Pohon berbunga awan beralih,","Mata merenung fikiran melayang,","Lain ditanya dijawab berdalih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105320,"Kajang pak malau kajang berlipat","Kajang tuan hamba mengkuang lalu","Bertanya khabar sekiranya sempat","Berkirim salam di angin nan lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105321,"Pasang pelita hari dah senja,","Tepi teratak ke rumah nyonya,","Dititip kata di hujung tawa,","Disoal tak jawab diam ditanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105322,"Kebaya nyonya kilauan nilam,","Simpuh berkalih capal di tanah,","Usah ditanya derita semalam,","Lukanya pedih parut bernanah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105323,"Dahan terap hinggapnya punai,","Ranting maranti burung bersarang,","Usah diharap janji tertunai,","Detik dinanti kecewa bertandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105324,"Kelopak bunga ada lebah,","Lebah terbang arah ke sarang,","Dalam belanga gulai berkuah,","Susun di dulang agih ke orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105325,"Tumbuh kundur di rumpun lalang,","Burung tekukur si burung balam,","Elok undur andai terhalang,","Biar tersungkur dari tersembam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105326,"Andai dipaku papan rapuh,","Paku karat papannya lerai,","Patah kuku berganti paruh,","Akhir kudrat jasad bercerai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105327,"Andai sirih pulang ke gagang,","Tika pinang pulang ke tampuk,","Andai memilih lelaki bujang,","Jauh terkenang dekat bertepuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105328,"Indah rerama semak belukar,","Terbang dikejar dara jelita,","Guru umpama lilin terbakar,","Lalu pelajar menjadi apa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105329,"Cincin suasa si dara Laila,","Sarung selesa di jari Uda,","Tika puasa godaan menggila,","Air paip serasa krim soda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105330,"Dibunga angsana hinggap rerama.","Cantik warna menawan hati","Berbuka ku makan tiga kurma","Air suam dah cukup sebati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105331,"Buah merugai masak kari,","Gulai nangka si daun puding,","Air pelbagai 'warsa' dibeli,","Saat berbuka sandar ke dinding.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105332,"Cincin suasa sarung di jari,","Wangi selasih hiasan kamar,","Berkat puasa didiklah diri,","Subur kasih qalbu nan cemar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105333,"Andai warak pinta doanya,","Andai cerdik pinta ilmunya,","Andai cantik pinta rahsianya,","Andai sihat pinta petuanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105334,"Andai kaya pinta dermanya,","Andai miskin pinta tabahnya,","Andai tenang pinta zikirnya,","Andai resah pinta sebabnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105335,"Songket putih dibuat bengkung,","Ratu Jawa selempang pelikat,","Bercerai kasih jiwa menanggung,","Bercerai nyawa hidup bertekad.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105336,"Burung merpati turun ke tanah,","Makan padi di tapak tangan,","Merawat hati cintanya musnah,","Huluran budi dalam kenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105337,"Petik kecapi talinya kendur,","Irama merdu jiwa meronta:","Andai mimpi mainan tidur,","Hati rindu belaian cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105338,"Pecah derai cermin di bingkai,","Rumpun serai buang kaca:","Buah lerai putus di tangkai,","Kasih lerai putus dek kata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105339,"Ikan di tebat ikan bersisik,","Lampam jawa jerat di bubu:","Redup mata cinta berbisik,","Getar jiwa kasih dicumbu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105340,"Rumput palas akar rerambut","Hias dinding corak di gubah","Andai cinta telah di sambut","Tunggu apa lagi, jom pi nikah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105341,"Rumpun palas akar rerambut","Hias dinding corak digubah,","Kasih berbalas cinta bersambut,","Impi bersanding cita terindah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105342,"Usah direndam beras penganan,","Santan kotak kuali di lokar,","Andai dendam jadi mainan,","Lerai kelopak baru mekar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105343,"Ikan tilan hilir ke muara,","Senja remang malam menanti,","Andailah bulan bisa bicara,","Pungguk terbang cari pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105344,"Ikan sebarau dari seberang,","Burung camar ikan tenggiri,","Usah dihirau cacian orang,","Andai benar tegak berdiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105345,"Emas suasa jarum peniti,","Semat di bahu milik kekasih,","Cemar bahasa luruh pekerti,","Bangsa malu adab tersisih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105346,"Kuih talam si pulut inti,","Kuih bingka di dalam kotak,","Tika malam si bisu bermimpi,","Terasa ada terkata tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105347,"Muara keruh hulunya bah,","Selut di tebing hakis lumat,","Jelata heboh perihal hibah,","Tuduh banding jahil maklumat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105348,"Ranting selisih dahan direngkuh,","Pohon bidara di tepi halaman,","Awal berkasih usah angkuh,","Lembut bicara hati berkenan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105349,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Berganding timba dengan si tali,","Timba tidak mencarinya perigi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105350,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Rela diuji sebagai lelaki sejati,","Bagi wanita sebagai serikandi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105351,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Hidup kita lawannya mati,","Tiada yang akan kekal abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105352,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian penentu jatinya diri,","Akan diuji selagi tak mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105353,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian selalunya menguji di hati,","Selalu diuji tanda Allah Mengasihi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105354,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian menentukan kemampuannya diri,","Setiap yang datang mesti dihadapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105355,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian menentukan barangan berkualiti,","Berhati takut terbeli barangan kodi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105356,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ketentuan hidup pastinya mati,","Moga damai di negeri abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105357,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian datang silih berganti,","Buruk dan baik kadang terjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105358,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Setiap musibah kasihnya Ilahi,","Sabar dan redha sematkan dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105359,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Lenyaplah riang duka menanti,","Reda ujian walau senadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105360,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Lenyaplah riang kelamlah nanti,","Apa yang kurang takdir Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105361,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Walaupun dugaan silih berganti,","Kejayaan tetap muncul pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105362,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian cobaan menduga setiap hari,","Kudrat dan Irodat dariNya Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105363,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Kasih dan sayang Allah berkati,","Reda sebarang ujian terjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105364,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Mati itu memang sudah pasti,","Sementara hidup taburlah budi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105365,"Poei kodai naik lori balak,","Singgah sebentar ke stesen minyak;","Memang benar tu mama yatt,","Semua nya ada di uji allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105366,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Usah dikesal takdir Ilahi,","Ujian datang hikmah menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105367,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Dugaan datang silih berganti,","Dalam rupa bagaimana tidak ketahui.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105368,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Setiap ujian dari Ilahi,","Kita tabah juga reda di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105369,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Siang untuk mencari rezeki,","Bila malam mengabdikan diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105370,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Begitulah alam semula jadi,","Tiap-tiap yang hidup akan mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105371,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Hidup penuh ranjau dan duri,","Allah menguji ketabahan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105372,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Sabar dan redha tawaqal dihati,","Iman dan taqwa bekalan yang abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105373,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Redha ku terima ketentuan Illahi,","Sentiasa pasrah muhasabah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105374,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Lumrahnya hidup cabaran menanti,","Selagi berdenyut jantung dan nadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105375,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Yang pergi tetap pergi,","Yang hilang akan berganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105376,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Segala yang datang kehendak Ilahi,","Tiada siapa boleh atasi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105377,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Redha selalu takdir Ilahi,","Pasti hidup tenang disanubari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105378,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Suka dan duka silih berganti,","Sabar dan redho ujian ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105379,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Yang datang dan pergi,","Hidup kita tiada abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105380,"Langit mendung awan kelabu,","Siang malam menghantui diriku;","Bulan December hatiku pilu,","Redha segala apa yang berlaku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105381,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Cobaan kata Ajis Sudin dan Ramli,","Kini hanya tinggal kenangan abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105382,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Perginye dia ketentuan illahi,","Biarlah terus menjdi kenangan abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105383,"Al kesah riwayat Pak Pandir,","Di cerita orang waktu pagi;","Jangan terbiasa menyalahkan takdir,","Bencana menimpa muhasabahlah diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105384,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Setiap bencana yang telah terjadi,","Mungkin ada hikmahnya yang menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105385,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian yang datang sudah pasti,","Kesabaran dihati harus disemadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105386,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Hati yang walang usah diratapi,","Ujian Allah redha dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105387,"Luput siang malam berganti,","Cahaya terang gelap menanti;","Biarlah ujian Allah bersilih ganti,","Kita kuat dan tabah apanya segala ketentuanNya nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105388,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Suka dan duka berselang seli,","Reda dan akur ketentuan Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105389,"Luputlah siang malam berganti,","Cahaya terang gelap menanti;","Kasihnya ibu tiada pengganti,","Tenanglah ibu menghadap ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105390,"Liputi siang malam berganti,","Cahaya terang gelap berganti;","Sunguh malang diriku ini,","Kasih hatiku lari pergi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105391,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Terasa sayu pilu di hati,","Mendengar berita perginya insan seni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105392,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Di wajah riang memujuk hati,","Redha sebarang apa terjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105393,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Ujian tiba tanpa henti,","Terima sahaja takdir Ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105394,"Luput siang malam berganti,","Cahaya terang gelap menjadi;","Bermacam dugaan Allah menguji,","Pasrah redha takdir ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105395,"Tenangnya air di Kali,","Setenang hati ku ini;","Petang ini hujan lebat sekali,","Moga lepas berhenti hujan awan kan cerah kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105396,"Hujan rahmat membasahi bumi,","Kadang mendung hujan tak jadi;","Ku serah diri ku pada Mu Ilahi,","Sentiasa dibawah payung lindungan Ya Rabbi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105397,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Sediakan bekal sebelum mati,","Disanalah yang kekal abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105398,"Luput siang malam berganti,","Cahaya terang gelap menjadi;","Bila panas hujan di nanti,","Di musim tengkujuh ni waspadalah diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105399,"Luputlah siang malam berganti,","Cahaya terang gelap menjadi;","Di dalam bayang kenali hati,","Atasi kurang dengan abadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105400,"Luput siang malam berganti,","Cahaya terang gelap menjadi;","Dugaan datang berulang-kali,","Sabar tenang tabah hadapi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105401,"Terang bulan bagai diukir,","Tiupan angin ayunan buaian;","Susah senang senyuman dibibir,","Tabah tempuhi kehidupan hari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105402,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Usah ditelan fikiran fakir,","Menjadi fasiq dalam sangkaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105403,"Umpan cacing mengail ikan,","Kail diipacak di tepian sampan;","Positif negatif perlu difikirkan,","Saling melengkapi di dalam kehidupan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105404,"Di sebalik ranting terang rembulan,","Cahaya menerangi seluruhnya alam;","Sikap positif menjadi bualan,","Negatif ibarat kegelapannya malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105405,"Galah di pintu menimpa bangku,","Tandanya lama biar sandarnya;","Allah penentu apa berlaku,","Reda terima besar ganjar-Nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105406,"Bila terantuk baru terngadah,","Perbahasa melayu boleh diguna;","Sudah terkena tanggunglah padah,","Sesal kemudian tidak berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105407,"Pedasnya lada tidak terperi,","Petai sepapan di pohon hulu;","Ubah minda siapkan diri,","Maju hadapan lupakan yang lalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105408,"Makan nasi di musim hujan,","Nasi dimakan beralaskan rotan;","Hidup mesti diteruskan,","Walau badai datang tak disangkakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105409,"Tanpa semua siapalah kita,","Pangkat bukan pandangannya taraf;","Jika positif dan negatif tiada,","Tiadakan lengkap sebuah geraf", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105410,"Banyak kelkatu di pokok papaya,","Menuju sekiat terbang ke sana;","Fikir itu pelita jaya,","Nanti berbuat jadi sempurna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105411,"Encik Latif memakan bubur,","Bubur dimakan di pekan Trolak;","Positif berlambangkan tanda campur,","Negatif pula bermaksudnya tolak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105412,"Seri pagi bernyanyi ketitir,","Sayang tempua hingap di dahan;","Telah puas aku berfikir,","Sudah begitu ketentuan Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105413,"Pak Mat tempeh jual rojak,","Minah karen duduk di tembok;","Mengapa ramai yang suka merajuk,","Silaturrahim perlu dipupuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105414,"Pohon dedalu di atas daik,","Pohon pedada di hujung taman;","Kita perlu bersangka baik,","Kunci kepada kekuatan iman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105415,"Burung Nuri terbang tinggi,","Hinggap di dahan mati;","Hidup ini mesti berhati-hati,","Agar tidak menyesal nanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105416,"Bersama bonda membawa beras,","Jumpa mertua membeli benang;","Waktu muda bekerja keras,","Bila tua hidup senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105417,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Jika berjanji usah mungkir,","Padah nanti terima balasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105418,"Dentum guruh bingit guntur,","Dahan sekah tanah rekah,","Andai buluh tersalah lentur,","Rebung patah ruas pecah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105419,"Hutang lupa saling mengingatkan,","Hutang salah saling memaafkan,","Hutang jahil kita belajar,","Hutang pandai kita mengajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105420,"Cahaya terang sinar mentari,","Ranting berduri lekat di songket,","Di padang orang kita berlari,","Di padang sendiri kita berjengket.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105421,"Daun gugur luruh ke tanah,","Buah dicicah sambal colek,","Andai ditegur usah melatah","Di cermin pecah usah bersolek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105422,"Gelita malam cahaya pelita,","Lena terlelap dek keasyikan,","Bagai nilam batu permata,","Indah gemerlap jadi sebutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105423,"Masak gulai ikan seludu,","Ampai bersama si kacang bendi ,","kalau jauh terasa rindu ,","Tataplah gambar bawak ke mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105424,"Berbisik lembut pada rembulan","Mngapa kah masih tercegat di situ","Dh lama pungguk rindu kan bulan","Sampai kapan akan bersatu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105425,"Lelah berjalan air diteguk,","Rimbun meranti buat berteduh,","Andai bulan dirindui pungguk,","Usah menanti di dahan rapuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105426,"Bunga kertas indah warna,","Rimbun bunga musim kemarau,","Otak cerdas akal di guna,","Andai ditanya diambil hirau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105427,"Pohon duku rendang ke hulu,","Pohon pelam pohon angsana:","Baca buku peroleh ilmu,","Tatap alam peroleh makna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105428,"Terbang rendah kicau melata,","Rumpun padi haruan di paya:","Bicara indah mainan kata,","Umpama budi dihiasi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105429,"Dalam daun ada bidara,","Dahan bidara usah dikerat,","Dalam pantun ada bicara,","Dalam bicara makna tersirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105430,"Minta daun diberi daun","Dalam daun ada bidara","Minta pantun diberi pantun","Dalam pantun ada bicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105431,"Ambil timba mari ke paya,","Tajam benta lukalah jari,","Sesat hamba dalam cahaya,","Tutup mata bukalah hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105432,"Buluh rakit ikat bertindih,","Akar berduri lekat di batu,","Ditarik sakit ditolak pedih,","Akal bestari jadi penentu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105433,"Ambil benang jahit perca,","Buat tabir gantung di dapur,","Hadir tenang dalam gelora,","Redha takdir dalam syukur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105434,"Ikan bekot di atas lumut .","Nunggu serangga buat makanan.","Jangan takut perut nya gemok.","Itu lah anugerah dari lah tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105435,"Dentum guruh bingit guntur,","Dahan sekah tanah rekah,","Andai buluh tersalah lentur,","Rebung patah ruas pecah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105436,"Berputus kasih tidak terlupa kan.","Hati merana bagai terluka.","Selagi masih berselera makan.","Makan saja apa di suka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105437,"Bagai mana Hati tak Remuk","Karena janji -janji tidak di ingat","Bagaimana perut tak gemuk","Karena makan banyak dan berkeringat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105438,"Gelita malam cahaya pelita,","Lena terlelap dek keasyikan,","Bagai nilam batu permata,","Indah gemerlap jadi sebutan.", 4, 6);
