INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105442,"Terbang rendah kicau melata,","Rumpun padi haruan di paya:","Bicara indah mainan kata,","Umpama budi dihiasi bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105443,"Pohon duku rendang ke hulu,","Pohon pelam pohon angsana:","Baca buku peroleh ilmu,","Tatap alam peroleh makna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105444,"Rumpun buluh ujung bendang,","Tepinya paya tebing rata,","Usah disuluh malam berbintang,","Terang cahaya diam segala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105445,"Rumpun buluh ujung bendang,","Tepinya paya tebing rata,","Usah disuluh sikaki tempang,","Terang mata kaki tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105446,"Rumpun buluh ujung bendang,","Tepinya paya tebing rata,","Usah disuluh di waktu petang,","Sudah ilmunya nampak nyata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105447,"Rumpun buluh ujung bendang,","Tepinya paya tebing rata,","Usah disuluh malam berderang,","Terang cahaya silau di mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105448,"Tebang sebatang tebu serumpun,","Dikerat-kerat bagi ke jiran,","Manja ditatang hidup setahun,","Ibarat menjerat belut di lumpuran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105449,"Kain sarung tenunan lama,","Awan larat bunga kerawang,","Keris bersarung tersangkut lama,","Mata berkarat hulu bersawang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105450,"Simpan alu lesungnya pecah,","Ambil dedak timbang di kati,","Bujur lalu melintang patah,","Undur tidak terhalang mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105451,"Bagai Hikayat Melayu Melaka","Jebat Ditewas Tuah yang Tua","Tiada ucapan melintang patah","Titian roboh semua tergadah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105452,"Dendam berahi buka hikayat,","Remang senja teduh kelapa,","Kucing kelahi tambah zuriat,","Kelahi manusia cerai mengapa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105453,"Anak rusa dirumah Pak Malai","Itik kelahi rebut ikan gelama","Bile manusia gaduh jadi cerai berai","Tandanye cetek ilmu agama di dada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105454,"Usah dilentur buluh lurus,","Pisau tapik cantas duri,","Terluah tutur bicara tulus,","Kias berlapik sukar mengerti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105455,"Tanam padi benih seberang,","Dituai mari tinggal jerami,","Buat budi di tempat orang,","Diri dicari cinta bersemi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105456,"Tanam padi benih seberang,","Dituai mari tinggal jerami,","Buat budi di tempat orang,","Diri dicari cinta bersemi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105457,"Emas permata milik sahaya,","Dalam laci disuluh pelita,","Dalam gelita terbit cahaya,","Dalam benci bertunas cinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105458,"Tampak indah bunga rendang,","Pohon sena dahan mati,","Tika rendah tempua bersarang,","Ada makna hasrat di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105459,"Gegak gempita angin menderu,","Petir kilat guruh bergegar,","Tika dusta di tiap penjuru,","Lebur hasrat di pintu pagar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105460,"Mari menampi padi di bendang,","Celur cemperai dihiris binjai,","Ingin bermimpi igau bertandang,","Lena terlerai mimpi berkecai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105461,"Sayu tekukur di tepi paya,","Burung merpati kaki terjerat,","Indah tutur rakan bahagia,","Luhur hati amalan berkat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105462,"Gugur pauh dahan di gegar,","Getah melekat kain terlipat,","Orang menjauh kita beredar,","Orang mendekat kita merapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105463,"Petai sepapan pucuk gelugur,","Ulas nangka dibuat pengat,","Berlidah sopan tika menegur,","Berbaik sangka saling mengingat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105464,"Burung pegar di rumpun padi,","Anak semilang terenang-renang:","Cinta mekar dek baik budi,","Di mata hilang di hati terkenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105465,"Buluh joran di hutan meranti,","Buluh hasta dibuat galah,","Andai teguran terasa di hati,","Mungkin kata susun tersalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105466,"Akar sulur menjalar di batang,","Calar betis di sekah dahan:","Budi dihulur jasa dikenang,","Darah setitis nyawa taruhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105467,"Goreng gelama dicelur sayur,","Tepung pelita iringan kuih:","Hidup umpama warna berbaur,","Rencam cerita bertindan tindih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105468,"Ranting cemperai usah direnggut,","Putik menjulur lingkar di titi:","Tubuh bercerai hati tersangkut,","Kasih luhur tiada pengganti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105469,"Teluk belanga baju idaman,","Samping kembang tenunan puteri:","Mekar bunga wangi setaman,","Datang kumbang menyapa seri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105470,"Rendang senduduk di sepi kubur,","Pohon saga teduhan senja:","Sebelum duduk usah berlunjur,","Wajib dijaga maruah keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105471,"Pohon menjulang nampak berbalam,","Ranting merapat tiupan bayu:","Sekejap siang hari dah malam,","Terasa cepat detik berlalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105472,"Ambil bekal bawa ke laut,","Ambil kikir bawa belayar:","Bijak akal boleh diikut ,","Lemah fikir boleh diajar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105473,"Ikan belida renang ke baruh,","Burung pipit rumpun keledek:","Andai dada mendabik angkuh,","Hidup sempit langkah pendek.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105474,"Mangga cicah di atas pangkin,","Tampak merbah buat sarang:","Telor pecah di kepala pemimpin,","Hanyir wajah dibenci orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105475,"Tepung pelita pulut sekaya,","Keras kerak hitam belanga:","Berinfak harta andai kaya,","Andai wara' hulurlah doa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105476,"Pucuk kasturi ranting berduri,","Tumbuh rimbun sudut halaman:","Wajah berseri santun pekerti,","Rindu terhimpun dara idaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105477,"Dara bersimpuh di depan pintu","Kerudung tersangkut di pohon bidara","Cinta disimpul bersulam rindu","Kasih terpaut memendam rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105478,"Nira cair si gula kabung,","Rendang maman buat tetamu:","Bulat air kerana pembetung,","Jiran taman dijadikan seteru?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105479,"Padam pelita minyak dituang,","Letak penganan atas talam:","Terlerai cinta kasih terbuang,","Indah kenangan usah dipadam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105480,"Pilih dipilih madu lebah,","Pokok merbau pokok terap:","Angin beralih ruang berubah,","Lama diimbau baru ditatap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105481,"Piring retak dibuang jangan,","Nasi ditanak gulai di kuali:","Andai teratak syurga idaman,","Bahagia anak kejam dibuli?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105482,"Sirih berlipat pinang diracik,","Letak kelkati di dalam puan:","Usah mengumpat tika berbisik,","Damailah hati kekal keruan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105483,"Bayu laut di tanjung sepi,","Camar putih terbang berlegar:","Hati terpaut wajah berseri,","Senyum kasih di bibir mekar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105484,"Sirih berlipat kaduk berjunjung,","Racik pinang dengan kelkati:","Jangan diumpat usah disanjung,","Andai dikenang sakit di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105485,"Pucuk labu dimasak lemak,","Jantung pisang ulam ulaman:","Hati diserbu rindukan emak,","Kurung sepasang jadi peneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105486,"Sirih junjung buat hantaran,","Pinang racik disusun serong:","Usah disanjung tokoh siaran,","Tingkah licik si bodoh sombong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105487,"Anak belida di pinggir kali,","Ikan berduri tepi permatang:","Antara tanda orang benci,","Jemput kenduri orang tak datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105488,"Ikan kelisa di selut tebat,","Pohon bidara tepi surau:","Andai berasa diri hebat,","Kelak bicara orang tak hirau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105489,"Hinggap balam dahan angsana,","Burung camar putih warna:","Kelam malam bulan gerhana,","Gelita kamar tuan ke mana?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105490,"Ranting kayu daun cemperai,","Akar berduri di dahan perah:","Usah dirayu kasih terlerai,","Luka diri semakin parah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105491,"Sulur sayur celahan serai,","Merah lada digiling lumat:","Kasih dihulur impian terlerai,","Titip cinta tersalah alamat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105492,"Pasir pantai perahu nelayan,","Putih bersih tanjung muara:","Duduk santai bunda melayan,","Suapan kasih tiada tara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105493,"Gulai maman ulam cekur,","Goreng peria kacang kelisa:","Air tangan bonda di dapur,","Sesuai selera usia bersisa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105494,"Pokok di kebun akar nya reput,","Baja ditabur pijarlah tangan:","Menitis embun segar rerumput,","Tanah subur rimbun pohonan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105495,"Sungai mengalir lesu ke muara,","Pasir mendap tepi tebingan:","Hidup bergilir ceria sengsara,","Indah diharap kecewa jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105496,"Bagai pahat dengan penukul,","Sekali diketuk sekali bergerak","Sekali jahat umpat sebakul,","Nista sekepuk meriah sorak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105497,"Telor dieram meliar mata,","Burung bayan dahan berangan:","Langit suram hitam gelita,","Air tempayan dibuang jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105498,"Dahan mempelam sekah ke titi,","Dingin embun gerimis di paya;","Siang dan malam silih berganti,","Rindu merimbun ke hujung usia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105499,"Indah busana ditenun jelita,","Kelopak melati corak kerawang;","Menunggu lena malam gelita,","Cinta hati ke mana menghilang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105500,"Ditumbuk lumat daun selasih,","Dibuang akar tumbuh bersilang:","Rasa hormat di hati kasih,","Ular di akar bisa tak hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105501,"Ikan belida di rumpun benta,","Keruh di baruh air mengalir;","Tiada kanda memilih cinta,","Akur seluruh suratan takdir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105502,"Ombak berdebur gelombang surut,","Sampan hanyut tidak berdayung;","Dendam lebur amarah berparut,","Pedih berdenyut diri menanggung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105503,"Ikan gelama di muara sungai,","Sampan nelayan tambat di jeti;","Cinta lama kasih tak lerai,","Rindu dilayan simpan di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105504,"Pedasnya kepalang si biji lada,","Masam cuka asalnya nira,","Siapalah hamba di hatinya dinda,","Raid Tiara atau Zizan Mira?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105505,"Madu lebah si madu kelulut,","Menjerat tekukur di perdu leban;","Kasih menyembah rindu melutut,","Amarah lebur dek manis senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105506,"Ayam salai dimasak pedas,","Terung telunjuk buat campuran;","Kasih lerai tidak berbalas,","Lelah memujuk dibuat tak heran.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105507,"Dinding gelegar rautan buluh,","Ambil galah parang disarung;","Kokok berdegar langitpun runtuh,","Sayang mengalah sebelum bertarung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105508,"Kebaya jarang warna sedondon,","Selendang merah ayu berlenggang;","Dari Marang terbang ke London,","Pencaknya alah di luar gelanggang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105509,"Ayam kampung mari disalai,","Dimasak lemak cili padi;","Andai dihitung kokok berderai,","Bertelor di semak menetas tak jadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105510,"Parang bersarung ikat ke pinggang,","Masuk belukar mencari beluluk;","Hidup diharung janji tak lekang,","Betapapun sukar rasa dijeruk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105511,"Ikan semilang di paya lumpur,","Anak itik di air lalu;","Lama hilang bukan berkubur,","Jumpa sedetik tanya bertalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105512,"Usah digusar bunyi tekukur,","Bunyi kecapi di tengah sawah;","Dari pasar terus ke dapur,","Mencari resepi buat perencah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105513,"Batu Kikir jambatan bosi,","Lagu dinyanyi waktu petang;","Selalu berfikir tidaklah rugi,","Bila bertindak untung yang datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105514,"Turun ke kali mengalih sampan,","Hendak mendayung ke Pulau Merua;","Positif negatif pasangan kehidupan,","Bateri tak berfungsi tanpa kedua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105515,"Pagi hari makan ketupat,","Makan dengan gulai berlada;","Apa yang dituai itulah yang dapat,","Berbakti buatlah baik selagi ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105516,"Terang bulan bagai diukir,","Tupai mengusik bunga duruan;","Nasihatkan anak-anak jangan main banjir,","Biar sesal dahulu jangan sesal kamudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105517,"Buat tapai guna ragi,","Agar dapat buat bekalan;","Musahabah diri tidakan rugi,","Bersangka baik jadikan amalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105518,"Masak lemak pucuk meranti,","Di campur pegaga enak rasanya;","Fikir itu pelita hati,","Dengan berfikir menyelamatkan segalanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105519,"Gunung daik bercabang tiga,","Hancur badan di kandung tanah;","Amalkan perinsip berjaga-jaga,","Hidup bahagia hingga ke Jannah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105520,"Kikir batu serata titi,","Mengelap meja berkerut dahi;","Fikir itu pelita hati,","Mengelak hampa menjerut diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105521,"Pergi menjaja keluar kota,","Jual kelapa dan juga durian;","Berfikir bertindak biar sekata,","Supaya tidak menyesal kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105522,"Dari desa pergi ke kota,","Singgah di pasar beli pedate;","Walau apa orang nak kata,","Kita tetap tenang di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105523,"Jelingan mata tanda perkenalan,","Ketika berkelah di Simpang Empat;","Gantung cita-cita tinggi di bulan,","Berusaha kejarlah sampai dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105524,"Tupai mengusik bunga durian,","Sudahlah gugur sebelum berbuah;","Beruntung kita kurniaan Tuhan,","Bumi Malaysia sangat bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105525,"Kancing suasa permata intan,","Diambil kakak jadikan butang;","Kurang harta bukan alasan,","Untuk tidak mencapai bintang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105526,"Sambil mengarang corak diukir,","Di belakang tabir dibalik ruang;","Kata-kata orang direnung difikir,","Yang baik ambil yang buruk buang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105527,"Anak-anak bermain guli,","Beramai-ramai meriah sekali;","Hidup di dunia berhati-hati,","Jangan sampai merana diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105528,"Sungguh cantik lukisan diukir,","Tempat pameran hendak dibawa;","Hidup dengan amalan berzikir,","Agar mengsuci rohani jiwa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105529,"Pucuk labu daun meranti,","Masak lemak resepi mak Jah;","Akal itu pelita hati,","Jangan dipadamkan dek nafsu amarah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105530,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Jangan terlalu banyak berfiikir,","Hingga merosak jiwa dan badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105531,"Hati terpikat cantiknya suasa,","Hiasannya pula indah dipandang;","Minda sihat jiwa perkasa,","Hidup selesa hati pun tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105532,"Segala perbuatan harus difikir,","Agar tak kesal dihari kemudian;","Kalau kita selalu berzikir,","Pasti ingat pada tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105533,"Cantik si bunga menjadi pujaan,","Hiasan taman menambah kecantikan;","Idea positif mengarah kejayaan,","Perbuatan negatif menjurus keburukan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105534,"Indahnya nama diberi,","Buat insan yang tersayang;","Dekatkan diri pada ilahi,","Jauhkan perkara yang terlarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105535,"Terang bulan bagai diukir,","Cahaya bersinar kembang melati;","Jika kurang orang berpikir,","Malap cahaya pelita hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105536,"Semut kerengga di pokok teja,","Ulat bulu di pokok durian;","Tekun berusaha bersemangat waja,","Susah dahulu senang kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105537,"Pergi mandi di kuala sungai,","Jangan berenang terlalu jauh;","Ubah sikap ubah perangai,","Baiki diri dengan bersungguh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105538,"Tupai mengusik bunga durian,","Jatuh berserak di atas pasir;","Tidak menyesal hari kemudian,","Berbuat baik tak payah fikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105539,"Beli ikan dari Pekan,","Beli pula ikan cencaru;","Berfikirlah dulu sebelum lakukan,","Moga hidup tidak bercelaru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105540,"Duduk di kali sambil bernazam,","Anggun bersila tangan digaru;","Usah kesali kenangan silam,","Bina semula iltizam baru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105541,"Rimbun sungguh pohonan kopi,","Tinggi lagi si pohon nangka;","Hidup kita saling melengkapi,","Bersama amalkan berbaik sangka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105542,"Buah kuini diperhati ditinjau,","Nampak penyengat mengejar keluang;","Kehidupan ini dipenuhi ranjau,","Kuatkan semangat teruslah berjuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105543,"Sungguh tirus lalang sebatang,","Bersimpang siur bayu menyiku;","Jalan lurus luas terbentang,","Kenapa disusur jalan berliku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105544,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Apa pun nak buat perlu berfikir,","Agar hasilnya tidak menghampakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105545,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Kebaikan dicari sama difikir;","Sertakan doa diberikan dorongan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105546,"Kapal juragan sedang berlabuh,","Air dangkal untuk laluan;","Kerja jangan suka bertangguh,","Usaha cekal bawa kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105547,"Terang bulan di sebalik awan,","Punggok merindu sedu dan sedan;","Perlu positif demi kebaikan,","Pandang negatif sebagai sempadan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105548,"Ambil air dengan Che Syukri,","Sambil tersenyum dengan Che Hasnah;","Hidup ini mesti pandai berdikari,","Agar nanti tidak mendatangkan masalah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105549,"Terang bulan bagai di diri,","Tupai mengusik durian;","Rajin-rajinlah muhasabah diri,","Agar tidak menyesal di hari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105550,"Di pokok kapas sikunang-kunang,","Cahayanya biru dilihat si Baha;","Gagal yang lepas jangan dikenang,","Rancang yang baru terus usaha.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105551,"Dusun Pak Latif penuh durian,","Lebat bunganya lekat di batang;","Perkara negatif bukan halangan,","Teladanikan ianya elakkan berulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105552,"Terang bulan dipagar bintang,","Waktu malam terang cahaya;","Kalau berfikir biarlah matang,","Supaya rumah tangga aman bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105553,"Ikan gonjeng nyaman digoreng,","Kalau digoreng bau lemaknya;","Orang dara jangan dikeyceng,","Kalau dikeyceng marah emaknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105554,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Sesuatu pekerjaan perlu difikir,","Jangan menyesal dihari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105555,"Tajam pahat cantik diukir,","Alur berliku jajar selari,","Sebelum dibuat perlu difikir,","Agar tidak menyesal kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105556,"Dulang berisi si apam balik,","Untuk hidangan calon menantu;","Hilangkan sangsi fikirkan yang baik,","Sepanjang hidup bahagia selalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105557,"Tupai mengusik bunga durian,","Luruh di mana tidak keruan;","Tidak menyesal hari kemudian,","Jatuh kelana tiada kesampaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105558,"Tupai mengusik bunga durian,","Kera menangis minta makan;","Hidup kita mesti diteruskan,","Demi untuk masa hadapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105559,"Tersayat kulit lalu berdarah,","Rasalah negeri tidak terukir;","Sememang sulit menahan marah,","Pujuklah diri habiskan fikir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105560,"Terang bulan bagai diukir,","Tupai mengusik bunga durian;","Sebarang perbuatan habiskan fikir,","Tidak menyesal hari kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105561,"Rendang ke tepi buah markisa,","Empat penjuru si ati-ati,","Gerangan mimpi lena tersisa,","Gelisah rindu rimbun di hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105562,"Pucuk paku goreng berlada,","Sambal tumis dicampur petai;","Andai diaku rupa tiada,","Dicukur kumis terserlah otai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105563,"Pucuk paku gulai bersantan,","Ikan kembung goreng berlada;","Selebar kuku budi huluran,","Tidak terhitung darjat pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105564,"Burung terkukur si burung balam,","Burung merbah hinggap di dahan;","Hidup bersyukur siang dan malam,","Sujud sembah kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105565,"Mengail tilan diumpan dedak,","Dedak lumat bijian saga;","Jauh berjalan mencari jejak,","Jumpa alamat orang tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105566,"Burung wak-wak di rumpun padi,","Burung tempua bersarang rendah;","Hidup di dunia hidup berbudi,","Berbekal doa menghadap Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105567,"Daghi Soghomban lalu ko Bongek,","Bawak bogheh ko ghumah bigheh;","Ditogo molawan, dipujuk bogek,","Kojo tak ponugheh peghangai tak senegheh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105568,"Memutih embun di daun ati-ati,","Keladi di paya di tebingnya palas;","Budi merimbun lekat di hati,","Sepanjang usia tidak terbalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105569,"Terung asam dibuat gulai,","Digulai dengan ikan merah;","Kasih berdendam cinta bercerai,","Tinggal kenangan menyesal tak sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105570,"Usah dilentur si buluh dara,","Ruasnya rekah miangnya bisa;","Indah tutur benar bicara,","Sopan tingkah budaya bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105571,"Mengail udang di teluk dalam,","Di bakau berakar duduk mengumpan;","Senja mengundang gelita malam,","Siang menghantar indah harapan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105572,"Ambil sabut nyalakan bara,","Daun dilulur perdu cempedak;","Salam disambut ramah bicara,","Kasih dihulur berjawab tidak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105573,"Usah menampi di tepi tangga,","Nyala pelita padam digagau;","Baru bermimpi udah terjaga,","Impikan cinta di dalam igau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105574,"Anyam ketupat di malam rewang,","Daun basah pulut direndam;","Usah mengumpat usah membawang,","Jiwa gelisah amarah dendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105575,"Cenderawasih terbang melayang,","Burung serindit di pokok palas;","Selaut kasih segunung sayang,","Budi sedikit tidak terbalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105576,"Kelapa dikukur dibuang sabut,","Sambal cicah di bawah khemah;","Jenguk ke dapur kepala serabut,","Tongkang pecah di dalam rumah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105577,"Burung kuang terbang ke sawah,","Senja redup pulangnya tidak;","Andai menuang jangan melimpah,","Andai menghirup jangan tersedak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105578,"Ikat kerbau di batang mempelam,","Dengusnya bengis tajam renungan;","Andai diimbau derita silam,","Hati menangis pecah empangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105579,"Tiang pagar jauh disorot,","Tanah adat usah dipawah;","Andai longgar jangan terlorot,","Andai sendat jangan lelah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105580,"Pohon berangan di tengah rimba,","Pohon senduduk tumbuh keliling;","Huluran tangan mengharap cinta,","Hati menunduk takdir berpaling.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105581,"Kelapa tumbang tepi permatang,","Pasang pelita jalan bertunjit;","Senja rembang azan kumandang,","Bersiap kita imarahkan masjid", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105582,"Pisau tapik meraut palas,","Tumbuh lalang tepi telaga;","Kata berlapik hasrat beralas,","Andai terhalang malu terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105583,"Usah menampi padi hampa,","Lelah mengikut tiupan angin;","Lewat mimpi tidak berjumpa,","Kita melukut di nasi dingin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105584,"Ikan tenggiri tidak bersisik,","Kacang buncis buat campuran;","Dini hari gelita berbisik,","Embun menitis menyapa daunan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105585,"Usah ditutuh dahan keranji,","Burung hinggap terbang lari;","Andai rapuh cinta dijanji,","Usah gundah takdir menguji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105586,"Ke pasar pagi membeli udang,","Sayur pegaga ulam-ulaman;","Kasih pergi rindu bertandang,","Tidur terjaga hilang peneman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105587,"Dianyam atap untuk teratak,","Terluka jari pelepah berduri;","Usah ditatap cermin nan retak,","Sukar mencari hakikat diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105588,"Ikan semilang di lumpur paya","Onak dan duri perlu ku tempuh","Tak pernah hilang teman sebaya","Ada jer teman kat Ipoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105589,"Ikan semilang di lumpur paya,","Telapak tangan tercucuk duri;","Ke mana hilang teman sebaya,","Hati terkenang ke mana dicari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105590,"Gulai peria ulam pegaga,","Ikan semilang dimasak kari;","Kelmarin ceria mesra bicara,","Ke mana hilang lelah mencari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105591,"Tanam selasih bawah teduhan,","Pohon cermai di telaga mandian;","Busana bersih dengan wangian,","Melangkah damai ke saf pengabdian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105592,"Terbang melayang si burung bangau,","Melepas keringat di paya keladi;","Andai terbayang bukanlah angau,","Kadar mengingat mulianya budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105593,"Malam gelita di tanjung sepi,","Semalu tajam menusuk celana;","Hati meronta dikunjung mimpi,","Mata pejam enggan terlena.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105594,"Rendang keranji tumbuh merata,","Pohon pelam si pohon sena;","Tika janji dibelit dusta,","Cinta sekelam bulan gerhana.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105595,"Pasir memutih ke ujung teluk,","Ujung teluk airnya keruh;","Entah kasih dijenguk rajuk,","Kian dipujuk kian menjauh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105596,"Indah alam belaian bayu,","Hijau segar nyanyian unggas;","Gelita malam cinta merayu,","Kasih mekar minta dibalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105597,"Bila malam jer cinta","Bila malam je nak merayu","Kalau dah begitu deritanya jiwa","Eloklah cari buah hati yg baru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105598,"Jeram tingkat air mengalir,","Ikan sebarau di akar kayu;","Kata diikat janjinya mungkir,","Tidak dihirau pujuk dan rayu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105599,"Tinggi gunung usapan awan,","Jeram tingkat air mengalir;","Kian direnung hatiku rawan,","Kata diikat janjinya mungkir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105600,"Tumbuh rotan melilit batang,","Rotan tumbuh di lereng bukit;","Kutitip pesan cincin di dulang,","Luka sembuh jangan diungkit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105601,"Tumbuh rotan melilit batang,","Rotan tumbuh di lereng bukit;","Kutitip pesan cincin di dulang,","Luka sembuh jangan diungkit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105602,"Dayung sampan ombak bergulung,","Lepas dayungan ditebar jala;","Bijak bukan dek sijil segulung,","Maruah bukan dek ijazah berjela.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105603,"Ranting berduri mencucuk tangan,","Tatkala direngkuh memetik mekar,","Dimuliakan diri bakti lumayan,","Leburkan angkuh jujur dipugar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105604,"Siakap senohong,","Gelama ikan duri;","Bercakap bohong,","Dilantik jadi menteri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105605,"Selesai meraut duduk termenung,","Melihat kelkatu terbang senja;","Terpisah dek laut terhalang dek gunung,","Bahasa penyatu umat manusia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105606,"Ambil pelepah diraut lidi,","Anak angsa di bawah tangga;","Cantik wajah elok peribadi,","Indah bahasa cerminan bangsa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105607,"Sawah luas menghijau di lembah,","Menyeberang titi mengail sepat;","Andai tewas pemimpin berhujah,","Emosi pasti mengambil tempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105608,"Sawah luas menghijau di lembah,","Menyeberang titi mengail sepat;","Andai tewas pemimpin berhujah,","Emosi pasti mengambil tempat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105609,"Berbalam pulau dilindung kabut,","Tanjung berteluk kapal berlabuh;","Pergi dihalau datang bersambut,","Benih yang elok dilempar tumbuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105610,"Burung belatuk di batang berangan,","Burung tempua menganyam sarang;","Rajin angguk melawan jangan,","Kelak ketua menjadi sayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105611,"Pokok palas di celah senduduk,","Di pinggir sawah belukar semak;","Tika di atas bawahlah menunduk,","Tika di bawah belajar mendongak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105612,"Tudung saji di dinding teratak,","Aneka warna susun teratur;","Andai dipuji jangan melonjak,","Tika dihina jangan tersungkur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105613,"Tuai padi antara masak","Esok jangan layu layuan","Intai kami antara nampak","Esok jangan hilang hilangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105614,"Pucuk bersulur dedalu melekap;","Dahan cemperai tempat berpaut,","Tepak dihulur hasrat terungkap,","Jangan dilerai kasih bertaut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105615,"Terbang rendah burung serindit,","Pohon berangan di tengah rimba;","Menatap indah rembulan di langit,","Menanti gerangan jatuh ke riba,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105616,"Kughung meghah coghak bobungo,","Poei somayang pelekat bolang;","Kok maghah tak nampak tolingo,","Kok ghiyang sonyum momanjang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105617,"Gulai kundur berulam cekur,","Susun di talam beralas baldu;","Kelak tidur jangan berdengkur,","Sepi malam hilang syahdu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105618,"Sedang menimba digigit agas,","Di paya rawa menangkap keli;","Indah rimba nyanyian unggas,","Indah jiwa nyanyian sepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105619,"Dingin gerimis di gelita malam,","Guruh petir di kelam gelumat;","Usah mengemis kasih pendendam,","Manis bibir di hati khianat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105620,"Dingin gerimis di gelita malam,","Guruh petir di kelam gelumat;","Usah mengemis kasih pendendam,","Manis bibir di hati khianat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105621,"Burung enggang di dahan tualang,","Lebah penyengat di ranting ara;","Kasih renggang cinta terhalang,","Dendam kesumat empunya angkara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105622,"Petai sepapan ulam-ulaman,","Ikan merah dimasak berkuah;","Titip harapan dalam kenangan,","Usah pasrah tebus maruah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105623,"Mawar sekuntum ranting berduri,","Wangi semerbak kelopak mekar,","Kasih nan harum kubawa pergi,","Tangis menyebak sendu gementar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105624,"Daunan tingkap kerawang palas,","Langsir berkedut cahaya pelita;","Rindu terungkap tidak berbalas,","Manis di mulut di hati berdusta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105625,"Malam rahmat indah anugerah,","Gelita kelam ghairah bergetar;","Salam Jumaat penuh baroqah,","Tawakal dalam gigih ikhtiar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105626,"Terluka tangan duri mengkuang,","Rendam selasih tangan dituam;","Kuasa di tangan angkuh dibuang,","Orang kan kasih di dalam diam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105627,"Dara jelita jalan berlenggang,","Di permatang arah ke baruh;","Usah dicita hakmilik orang,","Melirik pandang nyawa dipertaruh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105628,"Bulan mengambang padam lantera,","Pungguk di dahan terbang kelawar;","Biar gelombang membadai bahtera,","Tenang juragan mengemudi layar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105629,"Andai terang padam pelita,","Usah berlari jalan ke titi;","Anak orang anak kita,","Anak sendiri tersisih ke tepi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105630,"Pergi kekedai jumpa cik nyonya,","Cik nyonya pula belum berpunya,","Boleh cik abang saya bertanya,","Insan d maksud siapa gamaknya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105631,"Angin desa menghantar hiba,","Menderu arus di sepi malam;","Manis bahasa bersalut tuba,","Di luar lurus rencung di dalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105632,"Rumpun selasih ditimpa dahan,","Patah galah patah sandaran;","Sepi tersisih di tengah keriuhan,","Sendiri beralah mencari ketenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105633,"Batik bercorak perca berjurai,","Tajam peniti dicucuk berulang;","Debur ombak mencumbui pantai,","Debar di hati mencumbui bayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105634,"Akar kayu membelit batang,","Unggas berlagu di ranting kayu:","Hati sayu rindu bertandang,","Kasih keliru jiwaku ragu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105635,"Dara jelita ke rumah nyonya,","Tepung pelita buat bingkisan;","Kaya harta tak mungkin punya,","Kukaya kata dan mewah perasan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105636,"Limau purut daunnya wangi,","Tumbuh rimbun sisi halaman:","Jangan diturut hati nan sunyi,","Jiwa meruntun carilah teman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105637,"Akar kayu membelit batang,","Unggas berlagu di ranting kayu:","Hati sayu rindu bertandang,","Kasih keliru jiwaku ragu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105638,"Pelepah ijuk gugur ke tanah,","Kaki tersepit pelepah berduri:","Andai dipujuk tidak mengalah,","Biarlah nasib insaf sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105639,"Angin berpuput debu di relau,","Paku berkarat cucuk sakit:","Terlontar ke laut jadi pulau,","Terlontar ke darat jadi bukit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105640,"Cincin suasa mari ditempa,","Pakai bertamu hias pualam;","Di depan mata cari tak jumpa,","Takdir bertemu di gelita malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105641,"Duduk membawang coghito sabun,","Tunggu maggi masak sokawah;","Penjaga gawang baco peghabun,","Palang tinggi nampak ghondah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105642,"Waktu sonjo pasangkan polito,","Takdo polito golap gumin mcm si buto,","Sosak napeh dibueknya kito,","Sib baik penyepak penalti dighabunkan mato.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105643,"Tiang pagar rebah ke paya,","Durian belanda dahannya sekah;","Bangkok digegar dek Harimau Malaya,","Debar di dada gelora ke sudah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105644,"Nangka madu rendang ke tepi,","Dahannya rendah menyentuh bumi;","Lerai rindu di dalam mimpi,","Angannya indah ke subuh hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105645,"Burung belatuk di dahan sena,","Nangka madu rendang ke tepi:","Andai mengantuk marilah lena,","Lerai rindu di dalam mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105646,"Indah busana ditenun jelita,","Kelopak melati corak kerawang;","Menunggu lena malam gelita,","Cinta hati ke mana menghilang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105647,"Merah bata dibakar hangit,","Pinggan ayan hangat di tangan,","Gegak gempita guruh di langit,","Air tempayan dibuang jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105648,"Pohon mempelam di laman nyonya,","Di banir angsana burung merbah,","Mimpi semalam hasrat berbunga,","Di hujung lena kakinya rebah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105649,"Bunga senduduk rembang mentari,","Angin berdesir menjelang petang,","Bukan duduk bimbang menanti,","Duduk mentafsir takdir mendatang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105650,"Patah tumbuh hilang berganti,","Pasang surut lumrah duniawi,","Fikir memikir dr mata turun ke hati,","Ini lah dia pendidik nusa dan bangsa Zawawi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105651,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Ku kira puji seikhlas budi,","Rupanya lidah tidak bertulang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105652,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Pertama kasih kedua budi,","Ibu bapa paling disayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105653,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Kerana harta lenyapnya budi,","Hancur jahanam tidak dipandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105654,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Puas sudah menanam budi,","Kekayaan juga dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105655,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Jangan jemu menanam budi,","Ibarat pepatah pujangga didendang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105656,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Pusaka asli adalah budi,","Sehingga mati akan dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105657,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Rindu kerana terhutang budi,","Jauh dikenang dekat disayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105658,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Sentiasa amalkan hidup berbudi,","Kemana pergi senang berdagang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105659,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Hidup kalau suka berbudi,","Bila dah tiada jasamu dikenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105660,"Marilah kita menanam padi,","Padi ditanam di dalam bendang;","Tidak terkata indahnya budi,","Jauh terbenam tidak terpandang.", 4, 6);
