INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105751,"Pohon bidara pohon kekabu,","Disulam sukun serta papaya;","Digenggam bara menjadi abu,","Gigih dan tekun pasti berjaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105752,"Pengira canggih mengganti sempoa,","Dihitung sukat tepat selamat;","Usaha gigih beriring doa,","Hasilnya berkat mendapat rahmat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105753,"Kurau di tampin dibayar dukun,","Gelama cuma sampai di Broga;","Kalau rajin belajar berpantun,","Lama-lama pandai juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105754,"Bersalah diri maaf bersalam,","Tutur tersasul bukan mainan,","Bagai mentari menerangi alam,","Wasiat Rasul menjadi pedoman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105755,"Ikan belida tersangkut di pukat,","Raga penjaja tersangkut di kawat,","Sering bercanda lelah terawat,","Semua kerja seronok dibuat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105756,"Ikan kelisa si ikan jelawat,","Ikan belida tersangkut di pukat,","Ceria sentiasa nakal bertempat,","Sering bercanda lelah terawat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105757,"Adik beradik saling memakai,","Bersalah diri maaf bersalam,","Andai cerdik berkongsi pandai,","Bagai mentari menerangi alam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105758,"Jangan bakhil mewah dunia,","Adik beradik saling memakai,","Andai jahil sering bertanya,","Andai cerdik berkongsi pandai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105759,"Terbang terkukur jelita menampi,","Dulang di papan susun di tanah,","Mengigau tidur terganggu mimpi,","Indah harapan terlerai musnah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105760,"Atap tiris papan sekeping,","Retak tabuh lama terendam,","Andai keris pamer di dinding,","Tiada tubuh luka tertikam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105761,"Puncak gunung embun membasah,","Sekah dahan ujung permatang,","Tatkala kurenung tampak gelisah,","Tika berjauhan rindu bertandang.", 4, 6);
