INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105763,"Kayu beruti dibuat ampaian,","Kain ditarik ampaian beralih,","Rendang hati kerisek bersantan,","Teh ditarik limpahan buih.", 4, 6);
