INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105765,"Mandi perigi pelepah melindung,","Pelepah berduri disusun sendeng,","Hidangan pagi selera kampung,","Karipap berkari telor bergoreng.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105766,"Di kayu nyatuh buluh gelegar,","Diraut dilentur atap anyaman,","Peribadi utuh wibawa terpapar,","Santun tutur Melayu budiman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105767,"Burung merak berbulu cantik,","Buat hiasan di bingkai suasa,","Beragam corak busana batik,","Tradisi warisan budaya bangsa.", 4, 6);
