INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105769,"Haruman bunga rambut bersanggul,","Kunci almari simpan di para,","Hadir umpama debu di tunggul,","Pujuklah diri merawat lara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105770,"Hilang teman mulut menyebut","Nurani lesu siapa nak hirau","Jiwa rawan kalut serabut","Hati rindu dah naik sasau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105771,"Menelusuri laluan kering hembusan angin,","Lorong berliku melayannya mengomel,","Roti canai dua keping segelas milo dingin,","Sarapan putriku yang manja dan comel.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105772,"Semak halaman meliar rumput,","Bunga di pasu gersang kemarau,","Hilang teman mulut menyebut,","Nurani lesu siapa nak hirau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105773,"Terbang rendah terbang merata,","Burung merpati ke darat ke baruh,","Tajam lidah bisanya kata,","Terluka di hati bilakan sembuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105774,"Daun kari si limau purut,","Gugur melati di tebing kolam,","Terluka jari kan tinggal parut,","Terluka hati kan tinggal dendam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105775,"Saya Suka setiap bicara,","Manis Bicara luahan kata,","Pantun warisan budaya kita,","Jangan ada hati yang terluka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105776,"Di redup angsana duduk menampi,","Kembang melati di sanggul tersemat,","Tatkala terlena berkongsi mimpi,","Jangan khianati sumpah keramat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105777,"Jelitanya dara renungan mata,","Di redup angsana duduk menampi,","Berbeza cara matlamat sekata,","Tatkala terlena berkongsi mimpi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105778,"Lipatan baldu renda berjurai,","Jelitanya dara renungan mata,","Hasrat berpadu dendam terlerai,","Berbeza cara matlamat sekata.", 4, 6);
