INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105780,"Usah dinanti orang seberang","Entahkan hidup entah mati","Biar hidup serba kurang","Namun kaya jiwa dan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105781,"Tenunan songket bukan sembarang,","Dipakai cantik gadis jelita,","Apa diharap kasihnya orang","Malaysia bersih harapan kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105782,"Usah mengharap kasihnya orang","Betapa jujur akan dikhianati","Usah dinanti orang seberang","Entahkan hidup entah mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105783,"Pasang pelita tatkala senja,","Letak di bata arah ke paya,","Kucari cinta dalam gelita,","Terpejam mata silau cahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105784,"Kaki celana dijahit tepi,","Tampak setara sesuai rupa,","Andai lena berhias mimpi,","Mungkin cinta datang menyapa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105785,"Ayam denak di rumpun rumbia,","Senja gelap sinar lintasan,","Melihat anak dewasa ceria,","Bakat digilap membangun insan.", 4, 6);
