INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105788,"Gelang suasa di dalam peti,","Atap tiris berkarat paku,","Andai bersisa kasih di hati,","Simpan secebis untuk diriku", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105789,"Sekawan lebah seludang pinang,","Seludang pinang juraian mayang,","Jangan rebah sebelum kenyang,","Sudah kenyang bertambah lagi.", 4, 6);
