INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105799,"Minum milo diberikan percuma,","Sambil minum meriah bercerita,","Ini bukannya peluang pertama,","Moga bukan yang terakhir juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105800,"Air berkocak mainan ikan,","Hujung halaman si ati-ati,","Kecil teratak bukan ukuran,","Jiwa budiman idaman hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105801,"Puputan bayu di hening pagi,","Kicau merbah hinggap di dahan,","Tiang kayu tingkap bersegi,","Serambi rendah berlantai papan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105802,"Sesat kancil di hutan enau,","Tumbuh cermai jalan ke lembah,","Teratak kecil di pinggir danau,","Jiwa damai sekitaran indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105803,"Tumbuh dedalu lekat di dahan,","Akar merata diusap bayu,","Sedang waktu berputar perlahan,","Menanti cinta datang bertamu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105804,"Buluh titi rotan berduri,","Ambil parang tebang rumbia;","Luka di hati bebat sendiri,","Harap orang sedang bahagia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105805,"Tanjung sepi teluk berliku,","Banir akar jalar-menjalar,","Indah mimpi dibumbu rindu,","Jiwa segar gelora membugar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105806,"Banir akar jalar-menjalar,","Tumbuh dedalu lekat di dahan,","Jiwa segar gelora membugar,","Sedang waktu berputar perlahan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105807,"Cincin suasa mari ditempa,","Pakai bertamu hias pualam;","Di depan mata cari tak jumpa,","Takdir bertemu di gelita malam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105808,"Tumbuk cili sehingga lumat,","Bilis berlada celur bayam;","Sunyi sepi malam Jumaat,","Menyemai cinta dalam diam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105809,"Cinta disemai didalam diam","Dalam kelam kasih mendayung","Apa dikenang cinta yg silam","Disana menanti cinta nan Agung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105810,"Digoreng tilan buat bekalan,","Sarapan pagi karipap kentang,","Rezeki nelayan dihalang hakisan,","Mengais pagi makannya petang.", 4, 6);
