INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105814,"Seberang titi menghijau bendang,","Banjaran gunung biru berbalam,","Gelisahnya hati teringin pulang,","Sendirian menung terkurung di dalam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105815,"Banjaran gunung biru berbalam,","Pohon kelapa pelepah melambai,","Sendirian menung terkurung di dalam,","Mata berkaca badan dirantai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105816,"Asyik mengail dari permatang,","Menangguk belut dan ikan keli,","Kuala sungai ke laut terbentang,","Hanyut ke laut tak mungkin kembali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105817,"Sungai mengalir antara tebing,","Jalan berjengket atas permatang,","Nasib bergilir jangan kepiting,","Berkongsi sedikit usah berhutang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105818,"Dua titi antara tebing,","Lantai buluh tiangnya keras,","Andainya hati bakhil kepiting,","Usah mengeluh orang membalas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105819,"Belah buluh melantai titi,","Sungai mengalir antara tebing,","Usah mengeluh gusar di hati,","Nasib bergilir jangan kepiting.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105820,"Melodi syahdu liriknya lengkap,","Malam kelam pasang pelita,","Ingin mengadu tidak terucap,","Kata terendam dek air mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105821,"Pemetik kecapi di tepi paya,","Melodi syahdu liriknya lengkap,","Terasa sepi berteman cahaya,","Ingin mengadu tidak terucap.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105822,"Terbang rendah burung ke bendang,","Pemetik kecapi di tepi paya,","Mimpikan indah igau bertandang,","Terasa sepi berteman cahaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105823,"Burung kedidi terbang merata,","Murai batu terbang berlegar,","Sayur keladi berulamkan pegaga,","Pucuk paku tumbuhnya meliar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105824,"Burung tekukur di dahan kerdas,","Merayap musang di pohon kekabu,","Cendawan kukur digulai pedas,","Jantung pisang dibuat kerabu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105825,"Bakar bahulu di bulan ramadan,","Letih puasa anak mak Siti;","Bersusah-susah dahulu bersenang-senang kemudian,","Doa dan usaha itu yg pasti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105826,"Senangin ada satu di paya,","Seratus kelisa ikan di kali;","Rajin berusaha tentu berjaya,","Berputus asa jangan sekali.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105827,"Dari kecil sudah belajar semua kitab,","Untuk mendapat ilmu dunia & akhirat;","Semua bukan untuk merasa hebat,","Biarlah kejayaan kita di dunia & akhirat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105828,"Padi bukit padi huma,","Pulang senja pasang pelita;","Susah senang kita bersama,","Bila berjaya bersyukurlah kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105829,"Mengidam kerangan kepah kijing,","Dibeli menantu di Pekan Selama;","Berat ringan dipikul dijinjing,","Kejayaan itu milik bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105830,"Riuhnya murai nyaring berlagu,","Burung kedidi terbang merata,","Pucuk cemperai si pucuk labu,","Sayur keladi berulamkan pegaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105831,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Setiap kejayaan diraih tabah,","Baru kemanisan bertahan lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105832,"Padi bukit dinegeri Sabah,","Padi wangi berasnya;","Biar sedikit selalu menambah,","Lama-lama kan banyak jadinya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105833,"Tuai padi jadi kan beras,","Tanak pulut beras usang;","Kerja keras jangan malas,","Usaha sediki tak jadi segantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105834,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Kegigihan Pak Tani sentiasa tabah,","Nikmatnya rezeki dikongsi diterima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105835,"Berenang-renang diketepian,","Bersenang-senang dihari kemudian;","Bersusah payah nak capai impian,","Harap usaha jangan disia-siakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105836,"Teduhan leban di banir mempelam,","Riuhnya murai nyaring berlagu,","Ayam di reban ikan di kolam,","Pucuk cemperai si pucuk labu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105837,"Kutip terung di penjuru halaman,","Teduhan leban di banir mempelam,","Keliling kampung sumber makanan,","Ayam di reban ikan di kolam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105838,"Burung pipit di rumpun lengkuas,","Kutip terung di penjuru halaman,","Hidupnya sempit dunianya luas,","Keliling kampung sumber makanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105839,"Terbang burung ke pohon kelepung,","Burung pipit di rumpun lengkuas,","Biar terkurung hidup di kampung,","Hidupnya sempit dunianya luas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105840,"Taghik upih polopah pinang,","Bobunyi balam bobunyi taktibau,","Lum bulih nak balik bawak botonang,","Tokughung lam ghumah pueh molilau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105841,"Jatoh ko payo keq tobeng maman,","Taghik upih polopah pinang,","Tokonang yo keq kampong laman","Lum bulih nak balik bawak botonang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105842,"Buah compodak buah bidagho,","Jatoh ko payo keq tobeng maman,","Dighi tocampok nun ko utagho,","Tokonang yo keq kampong laman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105843,"Kain pelikat dibuat sampin,","Batik lepas ikat di pinggang,","Bangsa tergugat kerana pemimpin,","Kuasa terlepas ke tangan orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105844,"Angin menderu damainya alam,","Bagaikan ratib dalam gelita,","Kaedah berguru warisan silam,","Adab tertib didikkan pendeta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105845,"Panas mentari cerah cuaca,","Angin menderu damainya alam,","Disiplinkan diri buku dibaca,","Kaedah berguru warisan silam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105846,"Buat ampaian redup lindungan,","Panas mentari cerah cuaca,","Gendala capaian putus hubungan,","Disiplinkan diri buku dibaca.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105847,"Pagi-pagi makan kurma,","Minum bersama air bali;","Susah payah hidup bersama,","Biar senang rasa dihati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105848,"Kerap mandi dipinggir kali,","Sejuk dingin didinihari;","Harap hati menerima budi,","Taburlah dulu budi sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105849,"Akan kusahut seruan hasratmu,","Ku sematkan kukuh di kalbu;","Ku tunggu kejayaan tanpa jemu,","Selagi hayat sanggup ku tunggu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105850,"Pohon berangan hujung belukar,","Buat ampaian redup lindungan,","Modul di tangan mata di layar,","Gendala capaian putus hubungan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105851,"Apa ditenong apa dibelek,","Adakah hilang tersilap simpan;","Kejayaan tidak datang bergolek,","Diraih dengan usaha dan cobaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105852,"Berhenti di gua membuka bekal,","Makan bersama berdua-duaan;","Berikhtiar, berdoa, dan tawakal,","Itulah rahsia capai kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105853,"Ingin kurasa belaian kasihmu,","Ingin aku sangat rasakan;","Jika kejayaan datang bertamu,","Bersyukur kita kepada Tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105854,"Bangun pagi mesti mandi,","Biar badan jadi sihat;","Kalau rezeki nak menjadi,","Berusaha keras pasti dapat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105855,"Duduk termenung ditepi kali,","Lihat si puyu berenang renang;","Tumpah keringat mencari rezeki,","Usaha ikhlas penuh tenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105856,"Anyaman raga berbunga sarat,","Berisi gamat beralas sekam;","Berjuang hingga kehujung hayat,","Terasa nikmat kejayaan digenggam.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105857,"Juadah pagi kuih penganan,","Diiring dengan si buah berangan;","Kejayaan bukan untuk tontonan,","Rezeki yang lebih hulur sumbangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105858,"Rumpun papaya tepi penjuru,","Pohon berangan hujung belukar,","Bertemu di maya resam berguru,","Modul di tangan mata di layar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105859,"Batas sejajar tanam peria,","Rumpun papaya tepi penjuru,","Resam belajar berubah cara,","Bertemu di maya resam berguru.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105860,"Empat penjuru pagar berduri,","Batas sejajar tanam peria,","Murid dan guru mengurung diri,","Resam belajar berubah cara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105861,"Jiran sebelah pintu terkatup,","Empat penjuru pagar berduri,","Lima sekolah diarahkan tutup,","Murid dan guru mengurung diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105862,"Orang seberang penghuni desa,","Jiran sebelah pintu terkatup,","Angkara seorang tak faham bahasa,","Lima sekolah diarahkan tutup.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105863,"Padi huma padi bukit,","Tumbuh subur sepanjang banjaran;","Usaha tanpa mengenal perit,","Kejayaan diraih penuh kenikmatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105864,"Berdayung sampan menuju pengkalan,","Membawa dagangan untuk jajaan;","Jangan kecundang dik kegagalan,","Usaha itu tangga kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105865,"Yanie Rose tepok tangan,","Dua tangan nampak bersih;","Pantun saya suka berangan,","Ku ucapkan terima kasih.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105866,"Pergi pasar beli rempah,","Jangan lupa beri ikan;","Hidup ini harus bersabar,","Rajin usaha pasti kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105867,"Anak panglima pergi kehulu,","Sarat sampan penuh durian;","Biarlah kita bersakit dahulu,","Pasti bersenang kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105868,"Masak rondang dalam kawah,","Boleh di makan bersamo-samo;","Nak hidup sonang memang susah,","Usaho lobih pasti berjayo.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105869,"Masak lauk ikan tenggiri,","Ambil sedikit buat bekalan;","Berusaha majukan diri,","Di mana ada kemahuan di situ ada jalan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105870,"Padi pulut padi melukut,","Sudah dituai penuh seraga;","Rezeki sedikit jangan kedekut,","Rahmat Allah tidak terduga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105871,"Balik ke pangkal dari penghujong,","Sesat jalan jika tak bertanya;","Kalau tidak dipecahkan ruyong,","Bagaimana nak dapat sagunya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105872,"Padi dibukit elok dibaja,","Baja diambil diseberang jalan;","Hasil sedikit tidak mengapa,","Usahakan lebih untuk kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105873,"Pukul telur buat bahulu,","Enak makan diwaktu petang;","Bersusah payah kita dahulu,","Hasil kongsi dengan tersayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105874,"Tanjung Piai di tepi laut,","Peraturan diakur di pilihan raya;","Hasil dituai kesyukuran disebut,","Keringat yang gugur berbaloi akhirnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105875,"Makan papaya dikunyah lumat,","Rasanya manis kalahkan ciku;","Menuju jaya arah matlamat,","Sabar merintis jalan berliku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105876,"Padi di bukit simpan graha,","Dibuat tapai beras yang perang;","Sakit sedikit kerna usaha,","Jaya dicapai tersenyum girang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105877,"Hujan salju memecah awan,","Beratnya sarat umpama batu;","Untuk menuju pintu kejayaan,","Amalkan solat di awal waktu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105878,"Beras pulut di buat tapai.","Dibuat oleh mak Bedah;","Usaha ikhlas pasti di capai,","Segala kepayahan terasa indah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105879,"Bermain rakit di musim bah,","Sambil menyelok ikan linang;","Berdikit-dikit kita bersusah,","Esok-esok menuai senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105880,"Padi bukit dari Bengkulu,","Siram benih untuk semaian;","Bersakit-sakit kita dahulu,","Bersenang-senang kita kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105881,"Padi bukit dari Sabah,","Hasil tuaian bawa bersama;","Usaha gigih jangan mengalah,","Ada kejayaan kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105882,"Mengail bawal usah mengeluh,","Tepian titi dipancar cahaya;","Usaha terawal bermandi peluh,","Digenggam nanti sekalung jaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105883,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Banyak sedikit hendaklah tabah,","Cari rezeki kita bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105884,"Tak datang jika tiada pelawaan,","Jadi haram jika dimakan;","Dulu tangga pendakian kejayaan,","Sekarang lift suis ditekan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105885,"Dari Melaka pergi ke Segamat,","Singgah sebentar di tepi tanjung;","Kejayaan tiada titik penamat,","Ia satu perjalanan tiada penghujung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105886,"Ingin ku selam lautan hatimu,","Ingin aku terus mencuba;","Mengejar kejayaan tanpa jemu,","Jangan disertakan sifat haloba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105887,"Padi bukit di Negeri Sabah,","Hasil tuai bawa bersama;","Segala kepayahan tetap tabah,","Usaha keringat dikongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105888,"Duduk membaca diatas titi,","Segar disapa angin yang nyaman;","Biarlah apa hasilnya nanti,","Jadikan usaha satu pengalaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105889,"Padi bukit dinegeri Sabah,","Hasil tuai bawa bersama;","Kalau kita selalu bersabar,","Harta yang sikit kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105890,"Kek cokelat dan cek mek molek,","Beralas upih mengganti loyang;","Yang bulat tak datang bergolek,","Yang pipih tak datang melayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105891,"Tiada buloh gunakan rotan,","Rotan pengikat kemas simpulan;","Sesal kemudian tiada pendapatan,","Tidak dicuba tiada kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105892,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Susah payah hadapi dengan tabah,","Moga kejayaan menjadi nyata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105893,"Padi bukit sedap terasa,","Negeri Sabah memangnya kaya;","Jangan sesekali berputus asa,","Resepi hidup untuk berjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105894,"Ikan todak di buat colek,","Di basuh bersih jemur di laman;","Rezeki tidak datang bergolek,","Berusaha gigih tunggak kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105895,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Terima kasih kapada juruhebah,","Selalu menyajikan lagu-lagu kenangan lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105896,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Besar sungguh pengorbanan ibu ayah,","Kesenangan kini dikecapi seisi keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105897,"Padi bukit dinegeri Sabah,","Hasil tuaian dibawa bersama;","Biar bersakit jatuh rebah,","Rezeki diraih kongsi semua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105898,"Gelap di ruang tiada cahaya,","Luar muara angin bertiupan;","Semangat juang bina kerjaya,","Maju sejahtera masa depan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105899,"Padi bukit dinegeri Sabah,","Hasil tuai bawa bersama;","Susah senang Anugerah Allah,","Ujian dari nya kena terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105900,"Enak sungguh buah berangan,","Di bawa orang seberang lautan;","Tekun dan sabar asas kejayaan,","Rajin berusaha penentu kesenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105901,"Buat rakit dari buluh,","Buluh datang dari seberang;","Biar sakit seluruh tubuh,","Kejayaan datang hatipun senang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105902,"Ukiran gong, tepian dadih","Ikan kurau, tepian bersisa","Jangan tersinggong, jangan bersedih","Cuma bergurau, perkara biasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105903,"Kutip cendawan tepi titi","Gugur merata bunga kemboja","Jangan kawan bersusah hati","Berpantun kita bergurau saja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105904,"Usah pangkah hingga terbelah","terbelah susah hendak dikatup","Belum langkah awal bismillah","alhamdulillah langkah penutup", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105905,"Pokok merimbun pokok melati","Di tepi sekolah pokok jati","Apa pun boleh diperolehi","Sekiranya langkah tidak terhenti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105906,"Lereng didaki lurah turuni","bekal berkaki cukup kepetang","Dimana rezeki perlu dicari","tidak rezeki bergolek datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105907,"Kain dijemuran sudah basah","Diperah disitu diletak disana","Selagi ada kekuatan melangkah","Rezeki itu tidak kemana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105908,"Usah goyah saat di sergah","andai berhati rasa keselesaan","Jerit payah tercapai sudah,","hanya menanti kecap kejayaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105909,"Tanjung rasah, kuala kota nian","Batu serdak, tepian nisan","Sudah bersusah payah, mengapai impian","Itu tidak menjadi alasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105910,"Ada sekuntum di dalam gelas","ditiup angin hilang semerbak","Lontar senyum tidak berbalas","zahir kepingin batin menidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105911,"Sungguh sakit terpijak duri semalu,","Sakit lagi jika tertusuk duri landak,","Hendak membalas terasa malu","Entah kan tuan sudi berteman ke tidak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105912,"Di pagi hening bunyi seruling","irama diolah membawa sebak","Hingga juling asyik menjeling","puan sebelah buat tak nampak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105913,"Hari panas menjemur kain","Kain dijemur sama seiring","Tuan memandang mata kelain","Baru ku perasan mata mu juling", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105914,"Lagu nyanyian baba nyonya","lirik beralas di dalam nyanyian","Sinis senyuman manis tanda","cukup dibalas terpukau jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105915,"Makan rambutan terasa manis","Penuh onak duri ranjau","Tuan hanya tersenyum sinis","Mana mungkin hati terpukau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105916,"Air diminum cukup segelas","hilang dahaga penat bermain","Rasa senyum bagai dibalas","kenapa dimata pandang kelain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105917,"Cantik anyaman si daun palas","Hiasan meja di tepi pintu","Manis senyum mu sudah di balas","Tuan saja buat tak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105918,"Bau semerbak jauh kesana","hati berkobar dalam pencarian","Satu jambak berbagai warna","rasanya sukar untuk kepastian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105919,"Cantik permata dari Labuan","Bawa kemari dibungkus kain","Didepan mata tuan tak perasan","Rupanya hati tuan ketempat lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105920,"Daun palas si daun kelapa","anyam di jadi sebuah raga","Tidak dibalas tak mengapa","asalkan di hati nawaitu ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105921,"Harum durian dari anambas,","Ribut hujan di Melaka,","Senyum tuan bukan tak nak dibalas,","Takut tuan tidak terima", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105922,"Sama kenari ingin terbang","kini tiada tempat kesukaan","Bila mencari waktu hilang","itu tanda satu penghargaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105923,"Burung tempua, bersarang lagi","Burung helang, terbang tinggi","Ketika ada, tidak dihargai","Ketika menghilang, baru mencari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105924,"Tertumpah minyak, terkena pukat","Hitam jelaga, mengotori kemboja","Walaupun banyak, tempat memikat","Di sini juga, selesa bermanja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105925,"Bila berkubang di dalam kali","tempat main di tengah padang","Sangka terbang tidak kembali","didahan lain membuat sarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105926,"Bersama kulaan solat dhuha,","Cuaca redup awan berlabuh;","Tiada kejayaan tanpa usaha,","Jika hidup berpeluk tubuh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105927,"Kuih keria dari keledek,","Makan bersama di dalam dulang;","Yang bulat takkan datang bergolek,","Yang pipih takkan datang melayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105928,"Anak kera duduk menggaru,","Bermain jinak bersama tuan;","Hati gembira rasa terharu,","Melihat anak bergelar graduan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105929,"Benang emas jahit di bucu,","Selendang sutera berikan puteri;","Usaha gigih tak kenal jemu,","Sinar kejayaan sedia menanti.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105930,"Memetik labu di kala petang,","Bersama Leha cantik bergaya;","Biar beribu halangan mendatang,","Gandakan usaha pastikan berjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105931,"Dari kucing ke pekan Tuaran,","Bandarnya cantik jadi kebangaan;","Susah dahulu jadi cabaran,","Untuk mencapai nikmat kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105932,"Patah ranting di tengah dahan,","Pokok pinang tumbuhnya rapat;","Setiap kejayaan kurniaan Tuhan,","Cuma hadirnya lambat atau cepat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105933,"Jangan diharap padi seberang,","Padi huma atas bukit;","Hidup jangan susahkan orang,","Usahakan rezeki walau sedikit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105934,"Pagi hari terasa hangat,","Sambil mengcangkul tepi halaman;","Rajin berusaha kuat semangat,","Pasti kejayaan dalam genggaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105935,"Padi bukit di negeri Sabah,","Hasil tuaian bawa bersama;","Biar sakit bersusah payah,","Pahit akan manis akhirnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105936,"Teguh palang, tepian huma","Seekor ikan, terikat jala","Saya menghilang, bukannya lama","Sekarang kan, masih bersama semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105937,"Daun selasih buat pengalas,","hiasan indah dalam jambangan.","Kenapa masih belum dibalas,","selalu sudah dilontar senyuman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105938,"Tinggi-tinggi pokok kelapa","Panjat takut sering berangan","Suami isteri ibu & bapa","Bekalan syurga dilupa jangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105939,"Beli pari langsung dibelah,","Bagikan ke keluarga pak lurah,","Bagi isteri yang sholehah,","Jaminan syurga dari Allah,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105940,"Beli pari langsung dibelah,","Bagikan ke keluarga pak lurah,","Bagi isteri yang sholehah,","Jaminan syurga dari Allah,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105941,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Jaga kesihatan supaya sihat,","Agar bergembira seluruh keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105942,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Kalau terseliuh sendi dan urat,","Sapu dengan minyak geliga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105943,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Bila sihat terkinja terlompat,","Udah sakit minta dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105944,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Makan berfikir jangan tak sempat,","Sihat selama tubuh dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105945,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Cuaca jerebu pandangan tersekat,","Lapik hidung pernafasan dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105946,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Bersenam jangan terlebih lompat,","Kalau sakit siapa nak jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105947,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Rajin rajin makanlah ubat,","Kesihatan penting untuk dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105948,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Bila sakit dirawat cepat,","Agar tidak merisaukan keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105949,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Amalkan gaya hidup sihat,","Tahap kesihatan selalu dijaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105950,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Bersama berfikir putuskan pendapat,","Peranan bersama kebersihan dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105951,"Tiap pertolongan ada makna,","bila berdikari rezeki ditambah","Tanpa sokongan tak kemana,","akhir sendiri terima musibah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105952,"Di sinar suria rehat di anjung,","Cuaca cerah terbang rerama,","Peribadi mulia tetap di sanjung,","Jujur amanah paling utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105953,"Tumbuh di lata pohon berangan","Sebelahnya titi menuju rawa","Dalil dan fakta sekadar sokongan","Kebenaran pasti terserlah jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105954,"Tiba berarak bawa bersama,","ribut dan hujan asyik berlaga;","Busuk tidak tersimpan lama,","waktu ketetapan terhidu juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105955,"Kupu-kupu & rama-rama","Serupa seiras banyak juga bezanya","Jujur amanah paling utama","Kepalsuan terbit pada akhirnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105956,"Burung walit terbang ke kiri,","lama ke kiri berubah kebarat;","Berputar belit pentingkan diri,","leher sendiri akhirnya terjerat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105957,"Rimbun keladi, tepian seberang","Tanah landai, tepian berduri","Biar berbudi, di sanjung orang","Asalkan pandai, membawa diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105958,"Burung kedidi burung kedasih,","Terbang rendah dekat tambatan,","Kerana budi terpaut kasih,","Tersulam indah dalam ingatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105959,"Burung kedidi terbang di hutan,","Turunnya tenang di tanah gambut;","Setitis budi segunung intan,","Kasihnya dikenang mulut menyebut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105960,"Sedap ketupat disediakan ibu,","Lemak rasa kelapa sebutir,","Budi terpahat di dalam kalbu,","Nama sentiasa bermain di bibir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105961,"Senyuman manis sering terukir","Anak dara masih perawan","Nama nya jadi sebutan di bibir","Kerana budi jasad tertawan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105962,"Jalin benang sirat berwarna","Tambah seri letak perada","Makin di kenang munajat doa","Bercambah budi semarak pahala.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105963,"Pokok ijuk di tepi titi","Daun disiat simpan di huma","Janganlah merajuk berjauh hati","Kenangkanlah saat bahagia bersama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105964,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Di saat akhir memang tak sempat,","Kesihatan bersama awalnya dijaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105965,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Jerebu melanda di segenap tempat,","Awasi keluarga kesihatan dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105966,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Minumlah air sebanyak dapat,","Di musim jerebu kesihatan dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105967,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Minumlah air selagi dapat,","Badan sama hendak dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105968,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Kalau tak sihat berubat lah cepat,","Agar segera terasa lega.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105969,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Hidup jgn kuat mengumpat,","Nanti mati tak bau syurga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105970,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Hidup kita biarlah bermuafakat,","Negara aman rakyat sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105971,"Ikan di air melompat - lompat,","Ikan bernama si ikan laga;","Rajin bersenam kita kan sihat,","Semakin berusia kenalah jaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105972,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Tidur malam biarlah tepat,","Moga kesihatan elok terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105973,"Ikan di air melompat-lompat,","Ikan bersama si ikan laga;","Dulu terpinggir kini mendapat,","Kasih di sukma seluruh raga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105974,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Akibat jerebu hidung tersumbat,","Makan ubat barulah lega.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105975,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Kalau kita hendak sihat,","Kesihatan sentiasa perlu dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105976,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Asap hadir begitu cepat,","Demam selesema berjaga-jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105977,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Kalau sakit berubatlah cepat,","Kesihatan wajib selalu dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105978,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Rajin-rajin lah makan ubat,","Supaya kesihatan sentiasa terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105979,"Ikan di air melompat -lompat.","Ikan bernama si ikan laga.","Amalan sihat membawa berkat.","Keharmonian hidup kita sekeluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105980,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Jaga pemakanan badankan sihat,","Kekal bahagia di samping keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105981,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Jerebu mutakhir semua tempat,","Mari bersama kesihatan dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105982,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Amalkan lah gaya hidup yang sihat,","Untuk kebahagiaan seisi keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105983,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Senaman ringan selalu di ingat,","Mudahan sihat siaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105984,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Kini jerebu ramai tak sehat,","Kesihatan diri perlu dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105985,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Bila sakit pergi berubat,","Kesihatan kita perlu dijaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105986,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Kalau sakit makan ubat cepat,","Kalau dah melarat payah nak jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105987,"Ikan di air melompat- lompat,","Ikan bernama si ikan laga;","Makan ubat diwaktu yang tepat,","Sayangi diri kesihatan baik sangat berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105988,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Bangun pagi terasa sihat,","Bila kesihatan sentiasa di jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105989,"Makan lemang si juru mudi","Duduk berdua bernostalgia","Hidup memang tiada abadi","Kini susah, kelak bahagia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105990,"Manusia banyak membuat salah","Diulangi sekali-kali jangan","Jangan mudah menyerah kalah","Hidup ini suatu perjuangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105991,"Ke kuala daik,memetik lada","Buah berangan,tepian padi","Berbuat baik,biarlah berpada","Jadi sanjungan,kerana budi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105992,"Kuncup ditaman bunga kenanga","Kini mati lupa disiram","Hidup jangan berputus asa","Kita musti punya azam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105993,"Kuih bahulu dalam acuan","Air selasih dicampur halwa","Sabar dahulu menempuh ujian","Pastinya kasih bertaut jua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105994,"Emas sepuh tapak kuda,","Pedang samurai suasa berhulu.","Galangnya rapuh, teguh tiada,","Terhapus longlai diangin lalu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105995,"Hilang ampuh, patah cemati","Tetap kukuh, tiang meranti","Biar teguh sampai di hati","Tetap tumbuh kasihnya, tiada galang ganti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105996,"Selasih diisi dalam cawan,","Gula ditambah jadi manisan,","Kasih sayang hanya mainan,","Kita bertemu dalam igauan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105997,"Pikul beras di atas bahu","Tiada kain baju di alas","Kalau anak dara tidak mahu","Cuba cari janda berhias", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105998,"Hidang sireh di dalam puan","Sireh sekapur enak rasanya","Malang sungguh nasibmu tuan","Cubalah petua mandi bunga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (105999,"Ular sawa ular lidi","Anak kuda jatuh titi","Jika dara tidak sudi","Cari janda pilihan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106000,"Anak soleh bernama Rauf","Pergi ke masjid memakai kain","Jika tidak jumpa peneman hidup","Minta bantu ke orang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106001,"Kuala kota, tepian batu","Pohon kari, sebelah mengkudu","Orang bercinta, memang macam tu","Setiap hari, duduk merindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106002,"Kuala kota, tepian batu","Pohon kari, sebelah mengkudu","Orang bercinta, memang macam tu","Setiap hari, duduk merindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106003,"Menjala ikan membawa guni,","Untuk diisi berjenis ikan.","Sekejap di sana sekejap disini,","Mana satu ndakku turutkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106004,"Tepian titi, bertanah landai","Akar serdak, cuma hiasan","Ambik hati, kena lah pandai","Agar tidak, menjadi paksaan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106005,"Biji durian jangan ditelan","Biji kelapa hempas saja ke pinggan","Kalau sembelit sudah sebulan","Andai melepas beku hidung satu kampungan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106006,"Indah sungguh pakaian ratu","Lemah lembut bila berjalan.","Sampai hati kata begitu","Saya sembelit sudah sebulan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106007,"Kaki luka terpijak paku","Sakit amat paku karat","Tak buat mana nak ngaku","Mungkin Pak Kuntong yg terlajak buat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106008,"Air pasang meredah pengkalan","Ribut kencang baru nak reda","Bodohnya berak di tengah jalan","Tandas moden sedia ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106009,"Pandang langit tengok Bulan","Bilang bintang susun bejajar","Kalau dah berhenti ditengah jalan","Nampak sangat tak belajar thaharoh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106010,"Belayar di laut dengan bahtera","Disapa lembut ombak memalu","Didoakan semua sihat sejahtera","Semoga ceria hari berlalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106011,"Kalau sakit bawa lah berubat","Jam lima berita terkini","Waalaikumsalam buatmu sahabat","Sering-sering lah jenguk ke sini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106012,"Pohon ketumbar daunnya jarang","Jarang disiram si air hujan","Kerana tugas,lama tidak bertandang","Alhamdulillah masih diberi kesihatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106013,"Subur delima di kebun suri,","Pohon ketumbar daunnya jarang,","Sudah lama tidak kemari,","Apa khabar saudara sekarang?.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106014,"Pergi ke Peramu di petang redup,","Singgah sekejap di Kuala Jati,","Berhijab kamu semasa hidup,","Sebelum dihijab pabila mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106015,"Buah kueni rasanya sedap,","Berangkat pagi menuju laut,","Barulah diri teringat berhijab,","Saat menghadapi sakratul maut,", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106016,"Hendak daun diberi daun,","Dalam daun ada ubat,","Hendak pantun diberi pantun,","Dalam pantun ada nasihat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106017,"Senyum ketawa suatu nikmat,","walau kadang didalam keresahan.","Semoga taqwa dapat ditingkat,","dada dilapang serap pengetahuan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106018,"Bergayut di tepi dahan,","Tangan kiri menjemur kain.","Senyum tawa Anugerah tuhan,","Gunakan ia sebaik mungkin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106019,"Anak terkukur anak kenari,","turun berehat dibatang kelapa.","Bila bersyukur setiap diberi,","kita mengingat tuhan tak lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106020,"Terketar ketar kerana salah","Usah malu kalau bertanya","Rezeki itu anugerah Allah","Bersyukur lah selalu kita padanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106021,"Jika hendak usah alasan,","Jangan diam membisu kata.","Berjaya tidak setiap ujian,","Saat terpejam manakah kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106022,"Lama menanti, tak kunjung tiba","Kaki lenguh, hendak berjalan","Rezeki dicari, amal ditimba","Moga teguh dalam ujian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106023,"Anak merpati mencari makan","nampak merana tidak terdaya","Lepaskan titi anggaplah ujian","sampai kesana tanda berjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106024,"Dari pagi hingga senja","Mencari rezeki di bumi tuhan","Titi meniti suka dan duka","Uji teruji diri oleh kepayahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106025,"Bila menyengat terasa bisa","tambah pedih tanpa ramuan","Ingat mengingat antara kita","moga masih dalam landasan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106026,"Jauh berkelah ke Pulau Pangkur","Beli seikat buah sawa","Pada Allah kita bersyukur","Meningkat iman dan taqwa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106027,"Cantik pantai air membiru,","Deras ombak menyapu pasir,","Senyum berseri hati berlagu,","Gembiranya tidak dapat ditafsir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106028,"Tanah di hilir berbatu batu","Tepian sungai mengail patin","Kata dibibir memang begitu","Rupanya sudah ada yang lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106029,"Tiada lama tiada baru","Tiadg manis semanis gula","Kekasih hati di awan biru","Takut tersangkut yang lain pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106030,"Tepian selasih, tepi semalu","Akar terdampar, tepian palang","Mengharap kasih, di angin lalu","Khuatir berlapar, tak kenyang kenyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106031,"Tersandar ditepi pagar,","Membalut hati terluka,","Badan yang lemah menjadi segar,","Kata janjimu pengubat duka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106032,"Nyiur gading, puncak mahligai","Asam kepayang, tersangkut titi","Hancur dinding, tulang berkecai","Kasih sayang, sampai ke mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106033,"Kalau sudi beri lah pendapat","Gatal sangat di ketik tungau","Pada dia hati tertambat","Mungkin itu di katakan angau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106034,"Atas titi, penuh bersisa","Cuaca nyaman, sampai ke titi","Kasih di hati, tidak boleh, di paksa","Berilah senyuman, seikhlas hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106035,"Burung helang terbang melayang","Membuat sarang digunung daik","Apa diharap kasihnya orang","Orang tak sudi carilah lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106036,"Pokok manggis banyak cabang","Senang di panjat ambil buah nya","Hati bersih allah pun sayang","Satu jalan menuju syurga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106037,"Tanam selasih tumbuhnya tidak,","Pokok meranti naik meninggi,","Andai kasih dibalas tidak,","Pasrah hati ketentuan ilahi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106038,"Ikan di air melompat lompat,","Ikan bernana si ikan laga;","Badan sihat hidup berkat,","Keluarga bahagia lebih berharga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106039,"Ikan di air melompat lompat,","Ikan bernama si ikan laga;","Kesihatan tak jaga sakit yang dapat,","Jaga lah pemakanan seisi keluarga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106040,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Suasana jerebu merata tempat,","Kesihatan diri perlu dijaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106041,"Ikan di air melompat-lompat,","Ikan bernama si ikan laga;","Jerebu berada di merata tempat,","Ambil langkah berjaga-jaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106042,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Segala bersih impi dicita,","Malaysiaku nusa kebanggaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106043,"Antara kasih, sayang dan cinta,","Mana dulu mana kemudian;","Bukan sekadar melafaz kata,","Biar nyawa galang gantian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106044,"Antara kasih, sayang dan cinta,","Mana dulu mana kemudian;","Usah disisih ungkapan nyata,","Malaysia maju seiring lambaian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106045,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Negara bersih dipandang mata,","Tidak lah malu jadi cacian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106046,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Amalan bersih rakyat jelata,","Negara maju tepati impian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106047,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Malaysia bersih sasaran kita,","Mari bersatu rakyat sekalian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106048,"Antara kasih sayang dan cinta,","Mana dulu mana kemudian;","Sejarah negara telah tercipta,","Negara Malaysia jadi kebangaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106049,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Negara Malaysia bumi tercinta,","Bersatu padu meraih kejayaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106050,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Demi keharmonian Malaysia tercinta,","Perpaduan & keamanan perlu diutamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106051,"Antara kasih sayang dan cinta,","Mana dulu mana kemudian;","Merdeka disambut dengan air mata,","Penuh kesyukuran serta kesetiaan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106052,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Bumi Malaysia negara kita,","Demi pertiwi rela berkorban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106053,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Kasih sayang & cinta pandangan di mata,","Cintai negara menjadi kewajiban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106054,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Demi Malaysia yg ku cinta,","Ku rela berkorban bermati-matian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106055,"Antara kasih sayang dan cinta,","Mana dulu mana kemudian;","Memang benar bagai di kata,","Negara Malaysia yang di utamakan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106056,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Negara bersih negara kita,","Negara dulu diri kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106057,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Tak kisah apa yang dipinta,","Keduanya saling bergantian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106058,"Antara kasih sayang dan cinta,","Mana dulu mana kemudian;","Bendera masih dikibar merata,","Cintai malaysia jadi sebutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106059,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Demi Negara yang tercinta,","Jasad dan nyawa rela berkorban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106060,"Gemerlapan berlian di jari,","Pinangan diterima siteruna pilihan;","Yang pertama sayangi diri sendiri,","Sayangi negara jadi taruhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106061,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Darah tertumpah kasih tercipta,","Cinta negara tiada bertepian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106062,"Antara kasih dengan cinta,","Mana dulu mana kemudian;","Sumbangsih negara tercinta,","Sayang selalu indah nian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106063,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Malaysia yang kita cinta,","Curah bakti rela berkorban untuk semua sekalian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106064,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Demi Malaysia bebas merdeka,","Jiwa raga sanggup ku korban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106065,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Dalam hati tertanam cita-cita,","Demi negara sanggup berkorban.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106066,"Antara kasih sayang & cinta,","Mana dulu mana kemudian;","Bersihkan jiwa hindarkan dusta,","Namamu Malaysia jadi sebutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106067,"Disorong bantal bila mengantuk,","Jangan terngadah sesudah terhantuk;","Malaysia maju masyarakat majmuk,","Jangan bergaduh jangan mengamuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106068,"Lampu cemeni cerminnya bulat;","Kegunaan biasa bagai lantera;","Malaysia ini negara berdaulat,","Makmur sentosa kita pelihara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106069,"Payung terjun tali teraju,","Di pakai tentera segak mengancam;","Malaysia membangun ke arah maju,","Negara sejahtera aman tenteram.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106070,"Berkejar di taman anak bertiga,","Berebut kereta barang permainan;","Ke akhir zaman Malaysia dijaga,","Bersatu kita nikmati keamanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106071,"Sisa terakhir isi juadah,","Di dalam dulang menu berbilang;","Malaysia mengukir sejarah indah,","Nama gemilang warga cemerlang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106072,"Cik Limah duduk ditangga,","Sambil makan buah delima;","Sama-samalah kita majukan negara,","Agar Malaysia terkenal seluruh dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106073,"Makan bihun di Pekan Pagoh,","Kedai desa bawah pohon delima;","56 tahun Malaysiaku teguh,","Sejahtera bangsa pertahankan bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106074,"Dari pekan menuju ke kota,","Kota terletak di sebelah timur;","Mari hentikan segala sengketa,","Semoga Malaysiaku bertambah makmur.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106075,"Halia bara buah mengkudu,","Dijemur orang di atas para;","Marilah kita hidup bersatu padu,","Berganding bahu majukan negara.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106076,"Halia dan serai diketuk-ketuk,","Ikan keli di dalam belanga;","56 tahun Malaysia dibentuk,","Semoga terus gah di mata dunia.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106077,"Pagi hari pergi membajak,","Bawa bekal sayur tauhu;","Pemimpin dipilih pemimpin bijak,","Sungguh benar katamu itu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106078,"Sayang Sarawak lagu diminati,","Perlahan rentak bukannya zapin;","Sabah Sarawak sentiasa di hati,","Mestilah bijak memilih pemimpin.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106079,"Berangan pergi ke Osaka,","Sebab disana tinggal kawan;","Kemakmuran dibagi sama rata,","Sabah dan Sarawak dilupa jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106080,"Berdiri teguh bukan rahsia,","Megah berkibar jalur gemilang;","Tinggi nilai kita Malaysia,","Kedaulatan negara tetap disandang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106081,"Antara batu dan kaca terbit cahaya,","Cahaya datang dari mana tidak kira;","Malaysiaku rasa bahagia,","Rakyat semua sihat dan sejahtera.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106082,"Jahit labuci tercucuk jarum,","Labuci dijahit berpuluh biji;","56 tahun sudah bersatu kaum,","Aman maju teguh berdiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106083,"Bijak pendeta amatlah kagum,","Banyak pengalaman selalu berkongsi;","Jauhi sengketa antara kaum,","Malaysia aman rakyat harmoni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106084,"Anak Cina asalnya Melaka,","Bila berjalan mahu berteman;","Sudah berzaman Malaysia merdeka,","Bersama kita kekalkan keamanan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106085,"Asia ke batu kikir,","Kuda belang naik pedate;","Hari Malaysia sudah terukir,","Tidakkan hilang di sudut hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106086,"Asalnya kudup menjadi buah,","Kitaran biasa pokok papaya;","Aku hidup di bumi bertuah,","Berbagai bangsa serta budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106087,"Pagi hari memetik buah,","Buah rambutan manis rasanya;","Aku hidup di negara bertuah,","Berbagai bangsa berbagai budaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106088,"Kembang teratai tepi perigi,","Sayang tebu habis berbuku;","Kesetiaan tidak berbelah bagi,","Kerana disini tumpah darah ku.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106089,"Pergi berkelah membawa khemah,","Bawa bekalan juga minuman;","Malaysia harmoni penyatu ummah,","Sentiasa dikenang sepanjang zaman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106090,"Ke Batu Kikir mencari anak dara,","Untuk dijadikan penghias taman;","Malaysia aman dan sejahtera,","Mari bersama kita kekalkan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106091,"Bunga ros bunga amnesia,","Dibeli orang dihujung tanjung;","56 tahun lahirnya Malaysia,","Sama menjaga sama menjunjung.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106092,"Pergi ke Miri mencari rumbia,","Ke Lahad Datu membeli penyagat;","Malaysia dikenali seluruh dunia,","Kekal bersatu demi sejagat.", 4, 6);
