INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106179,"Buah pauh atas permatang,","Masak sebiji buah delima;","Hati tersentuh di langit petang,","Ingin kembali bercumbu huma.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106180,"Buah pauh atas permatang,","Masak sebiji buah delima;","Walau jauh tinggal kan orang tersayang,","Cukup masanya kembali jua.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106181,"Buah pauh atas permatang,","Masak sebiji buah delima;","Hujan emas dinegeri orang,","Hujan batu dinegeri kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106182,"Buah pauh atas permatang,","Masak sebijik buah delima;","Jauh merantau ke negeri orang,","Demi keluarga hidup bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106183,"Jauh merantau ke negeri orang,","Demi mencari sesuap nasi;","Rindu abang pada adinda seorang,","Akan ku pulang kembali ke sisi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106184,"Buah pauh atas permatang,","Masak sebiji buah delima;","Dari jauh hamba datang,","Untuk mencari kasih terpisah lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106185,"Buah pauh atas permatang,","Masak sabiji buah delima;","Masih kecil ditimang ditatang,","Bila jauh ujian diterima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106186,"Buah pauh atas permatang,","Masak sebiji buah delima;","Dari jauh hamba datang,","Sudikah radio klasik terima.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106187,"Buah pauh atas permatang,","Masak sebiji buah delima;","Rindu sangat aku nak pulang,","Di perantauan sangat lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106188,"Buah pauh atas permatang,","Masak sebiji buah delima;","Ayah bonda jauh dimata,","Namun adat budaya selalu ku kenang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106189,"Buah pauh atas permatang,","Masak sebiji buah delima;","Dari jauh saya dating,","Moga kasih kekal bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106190,"Buah pauh atas permatang,","Masak sebiji buah delima;","Dari jauh saya dating,","Moga kasih kekal bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106191,"Buah pauh atas permatang,","Masak sebiji buah delima;","Apakah untung anak dagang,","Hari petang hati pun hiba.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106192,"Buah pauh atas permatang,","Masak sebiji buah delima;","Walaupun seronok dinegara orang,","Negara kita tidak dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106193,"Buah pauh atas permatang,","Masak sebiji buah delima;","Merantau jauh pandang panjang,","Kalau berjanji jangan lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106194,"Buah pauh atas permatang,","Masak sebiji buah delima;","Biar pun jauh di tempat orang,","Tetap berjanji..Perak selamanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106195,"Buah pauh atas permatang,","Masak sebiji buah delima;","Walau badai terus mendatang,","Merantaulah kamu mencipta nama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106196,"Buah pauh atas permatang,","Masak sebiji buah delima;","Merantau jauh tidak dihalang,","Cari pengalaman kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106197,"Buah pauh atas pematang,","Masak sebiji buah delima;","Bukan datang sebarang datang,","Sayang sedikit tak tinggal lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106198,"Buah pauh atas permatang,","Masak sebijik buah delima;","Biar jauh di rantau orang,","Jangan di lupa sahabat lama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106199,"Buah pauh atas permatang,","Masak sebiji buah delima;","Merantau jauh ke negeri orang,","Menuntut ilmu saranan agama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106200,"Buah pauh atas permatang,","Masak sebiji buah delima;","Jauh merantau sambil berdagang,","Rezeki melimpah kongsi bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106201,"Buah pauh atas permatang,","Masak sebiji buah delima;","Biarpun jauh di rantau orang,","Negara sendiri tidak ku lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106202,"Buah pauh atas permatang,","Masak sebiji buah delima;","Dari jauh asik memandang,","Dah dekat malu-malu pula.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106203,"Buah pauh atas permatang,","Masak sebijik buah delima;","Walau jauh kita berdagang,","Tempat sendiri takkan dilupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106204,"Buah pauh diatas permatang,","Masak sebiji buah delima;","Hidup jauh dilautan rentang,","Bumi sini dibawa bersama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106205,"Buah pauh atas permatang,","Masak sebiji buah delima;","Walau jauh dirantau orang,","Adat budaya akan di utama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106206,"Buah pauh atas permatang,","Masak sebiji buah delima;","Biar jauh di rantau orang,","Asal usul ku tak pernah di lupa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106207,"Tanam cili di dalam pasu,","Segar berbuah sama merasa;","Jangan terburu menurut nafsu,","Fikirkan dahulu baik buruknya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106208,"Terang bulan bagai diukir,","Abadikan ia dalam kenangan;","Biar berperang dalam berfikir,","Hasilnya nanti pasti kemenangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106209,"Lekat selumbar di kain rabak,","Kain dikembang nampaklah lebar;","Berombak debar memikir wabak,","Surutkan bimbang banyak bersabar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106210,"Orang dahulu memalu nobat,","Nobat dipalu kayu berangan;","Fikir dahulu sebelum dibuat,","Kalau dibuat menyesal jangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106211,"Pergi ke hulu tengok cempedak,","Segera diambil buah delima;","Fikir dahulu sebelum bertindak,","Keputusan diambil adil saksama.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106212,"Mari makan haluwa kundur,","Kundur ditanam oleh nyonya;","Terlajak perahu boleh diundur,","Terlajak kata buruk padahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106213,"Samar semalu bunga melata,","Tepi muara antara jeti;","Fikir dahulu sebelum berkata,","Agar bicara tak lukai hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106214,"Pergi ke hulu membeli cempedak,","Singgah sebentar di Sungai Koyan;","Berfikir dahulu sebelum bertindak,","Agar tidak menyesal kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106215,"Mata berpinar kilau permata,","Hati celaru mungkinkah intan;","Maka benar bagai dikata,","Sesal dahulu itu pendapatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106216,"Duduk seorang terasa pilu,","Sambil termenung menggigit jari;","Sebelum buat tindakan berfikir dulu,","Supaya tidak menyesal di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106217,"Indah berombak di Pulau Rusa,","Mendung tiada santai berkelah;","Akal menidak nafsu berkuasa,","Tetap berada di jalan Allah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106218,"Beras wangi marilah dijamu,","Bekalan kembara buat si teruna;","Awas tangkah awas perbuatanmu,","Sesal kemudian tiadalah berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106219,"Di tepi tasik menebang sagu,","Terluka tangan dibalut bonda;","Melodi klasik pasti ditunggu,","Mengimbau kenangan di usia muda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106220,"Pagi hari pakai selendang,","Selendang rindu alunan gemersik;","Datang mari datang berdendang,","Lagu rindu melodi klasik.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106221,"Kopi secangkir sekeping roti,","Buat sarapan untuk adinda;","Akal memikir pelita hati,","Suluh kebaikan jalan direda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106222,"Jarangkan lada buangkan kekwa,","Takir setentang mengguna tajak;","Lapangkan dada tenangkan jiwa,","Berfikir matang sempurna bijak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106223,"Anak Melayu segak bertanjak,","Handal bersilat mempertahankan diri;","Fikir dahulu sebelum bertindak,","Tiada sesalan di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106224,"Meja dihias cantik di mata,","Berdua Awang makan sarapan;","Walau berkias bait-bait kata,","Buruk dibuang baik diteladan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106225,"Berjalan menuju ke Batu Kikir,","Hendak pergi makan kenduri;","Buat kerja perlu berfikir,","Tidak menyesal diri sendiri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106226,"Sayang-sayang buah kepayang,","Kalau dibuang terasa sayang;","Kepala jangan suka melayang,","Akibat tak baik dipandang orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106227,"Tupai mengusik bunga durian,","Lalu terjatuh menyembah bumi;","Setiap perbuatan harus difikirkan,","Sesal nanti merana diri.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106228,"Bunga di jambangan sungguh indah,","Petik letak di sanggul isteri;","Siap pertimbangan sesal tak sudah,","Nasi tak masak bubur terjadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106229,"Mempelam muda tiada luak,","Semasa mudik bekalan roti;","Harus waspada hindari riak,","Tutur yang baik terkesan hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106230,"Berguni padi darinya bendang,","Bawa ke kota dijual rancak;","Semurni budi harta dipandang,","Rasminya mata pilih yang kacak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106231,"Harga belian tetap yang lama,","Elakkan merempuh jangan berebut;","Hayat insan persis","Luputnya tempoh nyawa dicabut.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106232,"Pohon kenanga dihias hias,","Manis berseri si anak dara;","Petugas negara bekerja keras,","Pastikan penyakit lumpuh sepenuhnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106233,"Cinta bersemi di Tasik Selatan,","Bersemi Cuma di hujung pena;","Sesal dahulu pendapatan,","Sesal kemudian tidak berguna.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106234,"Tupai mengusik bunga durian,","Baru berputik habis sedahan;","Buatlah baik walau perlahan,","Barulah hidup dirahmat tuhan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106235,"Lepas solat kita berzikir,","Habis berzikir ke rumah tuan;","Segala perbuatan harus berfikir,","Supaya tak menyesal kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106236,"Rebah semalu dipijak rata,","Dihempap beluti hanya sebatang;","Fikir dahulu baru berkata,","Tidaklah nanti dikata orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106237,"Sakitnya duri semalu,","Terkena kaki pasti berpaling;","Bila bercakap fikir dahulu,","Jangan menyusahkan orang di sekeliling.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106238,"Burung merbah terbang rendah,","Hinggap pula di ranting mati;","Fikiran janganlah berubah-ubah,","Teguhkan pendirian dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106239,"Bunga melati jatuh merata,","Bunga di rimba ada di kota;","Terpikatnya hati dek kerana mata,","Bisunya hamba cantik tak terkata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106240,"Rasa segar, senaman pagi","Makan jambu sambil berlari","Saya dengar Pak Kuntong sudah beristeri","Cukupkah 'ilmu' nak menambah lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106241,"Bila mata bertentang mata,","Hati gementar tak terduga.","Kalau begitu bagai di kata,","Tunggu hamba di tepi tangga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106242,"Bunga kemboja harum baunya","Buah sentul, enak dimakan","Bercakap saja, apa gunanya","Kalau betul, masuk meminang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106243,"Berburu di hutan daik","Tertembak kaki dan tangan.","Bila suka semuanya baik,","Tiada potongan tak jadi halangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106244,"Hati berdebar menunggu kawan","Terasa hampa, rasa kepenatan","Dalam gambar sungguh menawan","Bila berjumpa, tidak ada potongan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106245,"Kotak rumpun perlu diikat,","Kain kasa ditanduk rusa.","Belum bertemu sudah terpikat,","Bila berjumpa membisu seribu bahasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106246,"Terlihat cantik jambangan melati","Gubahan dikenal diseluruh negara","Berbuat syirik jangan sekali","Akan menyesal selama lamanya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106247,"Rimbun kuini tepian teja,","Sayur kailan di dalam dulang,","Kita ini tetamu senja,","Banyaklah bekalan sebelum pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106248,"Mencari jarum,di tepi taman.","Penat berlegar,rasa keletihan.","Tasbih mengharum,orang beriman.","Wajah segar,senyum kelihatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106249,"Jangan di amal jampi mentera,","Hati gusar hidup tak aman,","Hidup di dunia hanya sementara,","Quran dan hadith jadi pedoman.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106250,"Air perigi air kolah,","Nampak bayang sebelah kaki,","Ingat lagi masa di sekolah,","Guru sayang rakan dengki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106251,"Terbang helang dua sejoli","Terbang berdua ke Sri Menanti","Kasih sayang yang sejati","Membawa bahagia hingga kemati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106252,"Bangau terbang tiada lelah","Terbang menuju pantai Sabah","Kasih sayang semakin melimpah","Jadikan hidupku semakin indah..", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106253,"Layang layang terbang melayang.","Tersangkut layang didahan nangka.","Kalau diri sudah disayang.","Tuah badan baik belaka.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106254,"Pokok cekur dibuang jangan","Campuran herba makan bersama","Redha dan syukur jadikan pegangan","Sinar bahagia pasti menjelma", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106255,"Pergi berkelah di hujung daik","Di pohon berangan nampak tekukur","Pemberian Allah selalu terbaik","Terima dengan rasa bersyukur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106256,"Sampan kolek ke Inderagiri","Sarat muatannya kelapa muda","Rezeki molek Allah yang beri","Syukur padanya apa yang ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106257,"Ukir pintu corak kerawang,","Nampak berseri rumah bonda;","Fikir dahulu minda cemerlang,","Salah tafsir malu berganda.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106258,"Berseri indah wajah di muka,","Basuh muka air telaga;","Nasihat baik untuk kita semua,","Duduk rumah agar kesihatan kita terjaga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106259,"Rasa kematu lintas di titi,","Terasa kesat di tapak kaki;","Berfikir itu pelita hati,","Jauhkan hasad dan dengki.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106260,"Tiada yang kekal melainkan Ilahi,","Kasih dan sayang sama dibugar;","Fikir di akal muktamad di hati,","Elak yang buruk hindar yang mungkar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106261,"Tidak bersepatu di atas titi,","Terpijak paku berdarah kakinya;","Terlajak perahu boleh dihenti,","Terlajak laku buruk padahnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106262,"Gelok diukir rumah penghulu,","Budak perhati bangsal berdian;","Elok berfkir terlebih dahulu,","Tidak nanti menyesal kemudian.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106263,"Burung tempua terbang naik,","Menuju bangsal kebun berduri;","Fikirkan semua buruk dan baik,","Elak menyesal di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106264,"Naik perahu menuju ke hilir,","Cerah cuaca di waktu pagi;","Setiap perbuatan hendaklah difikir,","Elakkan sesalan di kemudian hari.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106265,"Cantik tersemat tampak serasi,","Bertatah permata kerongsang di dada;","Selidik maklumat sebelum kongsi,","Sahih berita menyesal tiada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (106266,"Indah harum bunga di taman,","Nampak berseri bila dipandang;","Berfikir itu cahaya iman,","Penyelamat hidup dari kecundang.", 4, 6);
